(window.webpackJsonp=window.webpackJsonp||[]).push([[154],{507:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1115-thu-phuc-tam-ma"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1115-thu-phuc-tam-ma"}},[n._v("#")]),n._v(" Chương 1115: Thu phục tâm ma")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1115: Thu phục tâm ma"),t("br"),t("br"),n._v("Nói chuyện chính là Ngoan Nhân."),t("br"),t("br"),n._v("“Của ngươi?” Trương Huyền sửng sốt."),t("br"),t("br"),n._v("“Không sai, loại này tế đàn câu hồn, là đem danh sư nguyên thần, hồn phách bắt tới, bổ dưỡng nguyên thần của mình, thủ đoạn cực kỳ ác độc!” Ngoan Nhân thanh âm nói tiếp."),t("br"),t("br"),n._v("“Bổ dưỡng... Ý của ngươi là, cái tên này đầu nhập vào chính là... Ngươi một phần khác?”"),t("br"),t("br"),n._v("Trương Huyền cau mày."),t("br"),t("br"),n._v("“Đoán không sai, có lẽ vậy!” Ngoan Nhân đáp."),t("br"),t("br"),n._v("Năm đó cùng Khổng sư đại chiến, bị chém giết bỏ mình, thi thể nổ vỡ nát."),t("br"),t("br"),n._v("Bất quá, tu vi của hắn sớm đã đạt tới nhỏ máu trùng sinh tình trạng, mặc dù hóa thành bụi bặm, chỉ cần cơ duyên đầy đủ, vẫn như cũ có thể lần nữa phục sinh, thật giống như lúc trước xương ngón tay."),t("br"),t("br"),n._v("Phục sinh sau hắn, không cách nào cảm ứng cái khác tồn tại, đều cảm giác chính mình là Ngoan Nhân, muốn lần nữa khôi phục thực lực, chỉ có thể lần lượt thôn phệ, đem những này vỡ vụn bộ phận, toàn bộ sưu tập hoàn chỉnh."),t("br"),t("br"),n._v("Trước đó một mực không có tin tức, không nghĩ tới, vị này có lẽ tìm phiền toái Tàng Hư, thế mà đầu nhập vào một phần khác."),t("br"),t("br"),n._v("“Chủ nhân, nếu như có thể thôn phệ bộ phận này, thực lực của ta, khẳng định còn có thể tăng nhiều...” Ngoan Nhân tràn đầy kích động."),t("br"),t("br"),n._v("“Tốt, ta nghĩ phương pháp sưu hồn!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Hiện tại cái này Ngoan Nhân, đã hoàn toàn thần phục với hắn, thực lực gia tăng, đối với hắn cũng có ích lợi rất lớn."),t("br"),t("br"),n._v("Cùng Ngoan Nhân trao đổi, chỉ là trong nháy mắt sự tình, chung quanh tâm ma tại Tàng Hư la lên bên dưới, lao qua."),t("br"),t("br"),n._v("Tâm ma công kích, cùng nhân loại khác biệt, dùng không phải chiêu số cùng lực lượng, mà là xâm nhập thức hải, chế tạo đủ loại huyễn tượng, để cho người ta tinh thần sụp đổ, nội tâm mất phòng."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Tâm ma vọt tới, Trương Huyền quả nhiên cảm thấy trong đầu có chút mê muội."),t("br"),t("br"),n._v("Tàng Hư là Nguyên Thần cảnh cường giả đỉnh phong, tâm ma cũng cùng cấp bậc của hắn tương đồng, lại thêm số lượng quá nhiều, liền xem như tâm hắn chí kiên định, một khi toàn bộ vọt tới, cũng khó có thể chống lại."),t("br"),t("br"),n._v("“Chậm đã!”"),t("br"),t("br"),n._v("Rõ một khi bị đối phương xâm nhập trong óc, tất nhiên tinh thần rối loạn, Trương Huyền Thiên Đạo công pháp tại hồn thể bên trong vận chuyển, trừng mắt nhìn: “Các ngươi là Tàng Hư tâm ma, vì sao phải công kích ta?”"),t("br"),t("br"),n._v("Những này tâm ma, đều là chung quanh trận pháp, theo Tàng Hư trên thân dẫn ra, tìm bản thân làm gì?"),t("br"),t("br"),n._v("“Hắc hắc, hắn đã biến thành tâm ma, tại sao có thể có ngươi mỹ vị?”"),t("br"),t("br"),n._v("“Ta biết ngươi muốn xung kích tầng thứ cao hơn, muốn trở thành lợi hại hơn danh sư, chỉ cần nghe theo ta, có thể giúp ngươi rất nhanh thực hiện mục tiêu...”"),t("br"),t("br"),n._v("“Ngươi trên tâm cảnh có sơ hở, tin tưởng lời của ta, có thể để ngươi bổ sung không đủ...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đủ loại mê hoặc thanh âm từ chung quanh tâm ma trong miệng phát ra, để cho người ta trầm mê, rất dễ dàng mất phương hướng bản thân."),t("br"),t("br"),n._v("“Không có cái mới ý...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Vốn những này tâm ma đánh tới, còn cảm thấy có chút mê muội, nghe được những này mê hoặc lời nói, nói hắn công pháp không đúng, trên tâm cảnh có sơ hở, lập tức tỉnh táo vô cùng."),t("br"),t("br"),n._v("Thiên Đạo công pháp, là hắn lớn nhất tự tin và dựa dẫm... Thiếu hụt? Nói đùa cái gì!"),t("br"),t("br"),n._v("Thiên Đạo công pháp, ngươi tới chỉ điểm... Ngươi cho rằng ngươi là ai?"),t("br"),t("br"),n._v("Đường đường tâm ma, trình độ cũng quá thấp..."),t("br"),t("br"),n._v("“Mặc dù không biết các ngươi tâm ma, là thế nào tới, nhưng nhìn hình thái cùng hồn phách có chút tương tự, các ngươi chẳng lẽ vẫn muốn như vậy ngơ ngơ ngác ngác tiếp tục sống, chỉ giấu ở trong góc, không dám quang minh chính đại xuất hiện, chẳng lẽ... Không muốn thực lực mạnh lên, trở nên nổi bật?”"),t("br"),t("br"),n._v("Thanh âm xoay một cái, Trương Huyền trong lời nói mang theo từ tính."),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Tất cả tâm ma ngừng lại, nguyên một đám hai mặt nhìn nhau, vẻ mặt choáng váng."),t("br"),t("br"),n._v("Bọn họ đang tại mê hoặc đối phương, làm sao đối phương mê hoặc bọn họ?"),t("br"),t("br"),n._v("Không nên ngươi rơi vào hôn mê, bị chúng ta thừa lúc vắng mà vào ư? Không nên đối với chúng ta tin tưởng không nghi ngờ, rơi vào trầm luân không cách nào tự kềm chế ư?"),t("br"),t("br"),n._v("Làm sao... Như thế tỉnh táo, còn muốn thực lực chúng ta mạnh lên?"),t("br"),t("br"),n._v("Bất quá... Hắn nói, thật là có chút làm lòng người động!"),t("br"),t("br"),n._v("“Hồn phách tu luyện, muốn phòng ngừa hồn lực ngũ suy, tâm ma tất nhiên cũng là như vậy, ta chỗ này có một bộ pháp quyết, có thể nói cho các ngươi nghe một chút, nghiêm túc tu luyện, có lẽ liền có thể miễn trừ tai hoạ...”"),t("br"),t("br"),n._v("Chính không biết như thế nào cho phải, liền nghe Trương Huyền thanh âm tiếp tục vang lên, lập tức, trên bầu trời tựa như có hoa đóa rơi xuống, chung quanh linh khí xao động, dường như chỉ cần dựa theo hắn nói công pháp tới tu luyện, liền có thể vượt qua cực hạn, càng ngày càng mạnh."),t("br"),t("br"),n._v("“Tâm ma, đáp tâm mà sinh, tâm nếu như tinh khiết, các ngươi tự nhiên không có đất đặt chân, cẩn thận nghĩ một hồi, đây là hạng gì bi ai! Sự hiện hữu của các ngươi, thế mà căn cứ tu luyện giả tới định, vận mệnh không khỏi chính mình chưởng khống... Có phải hay không cảm thấy rất bất lực? Rất vô lực? Có điều, không sao! Chỉ cần dựa theo ta truyền thụ cho công pháp tu luyện, liền có thể nắm giữ vận mệnh của mình, trở thành chủ nhân của mình...”"),t("br"),t("br"),n._v("Thanh âm tiếp tục vang lên."),t("br"),t("br"),n._v("Trước đó còn có chút do dự tâm ma, lúc này, tất cả đều nguyên một đám kích động nắm đấm xiết chặt, vẻ mặt đỏ lên."),t("br"),t("br"),n._v("Phối hợp sư ngôn thiên bẩm cùng ma âm lời nói thực sự quá có mê hoặc tính, liền xem như tâm ma, cũng có chút chống lại không được."),t("br"),t("br"),n._v("“Không được do dự, không được chần chừ, tin tưởng mình, các ngươi khẳng định có thể làm được, không cần sống khổ cực như vậy, tới đi, nghe theo ta, các ngươi sẽ trở nên càng mạnh...”"),t("br"),t("br"),n._v("Trương Huyền xòe bàn tay ra."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Tất cả tâm ma mặt mũi kích động, tất cả đều bay tới, Trương Huyền lật bàn tay một cái, một cái bình ngọc xuất hiện, nhẹ nhàng một chiêu, thu nhập trong đó."),t("br"),t("br"),n._v("“Thả trong này, bọn họ thật sẽ không chạy đến?”"),t("br"),t("br"),n._v("Dựa theo lúc trước hắn kinh nghiệm, chỉ cần không nhận tâm ma mê hoặc, thứ này tự nhiên sẽ tán loạn, có điều, Ngoan Nhân nói cho hắn biết, hoàn toàn có thể đem những người này, biến thành của mình, cũng nói thu lấy phương pháp, lúc này mới sư ngôn thiên bẩm, nói thiên hoa loạn trụy."),t("br"),n._v("“Chỉ cần dùng ngươi theo Hồng Diệp Vương trong tay lấy được tế đàn, niêm phong lại miệng bình, liền không cách nào đi ra, muốn sử dụng thời điểm, mở ra nắp bình, nhất định có thể đưa đến tác dụng không tưởng tượng nổi...”"),t("br"),t("br"),n._v("Ngoan Nhân nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Bàn tay một trảo, lúc trước theo Hồng Diệp Vương trong tay lấy được tế đàn xuất hiện, nhẹ nhàng điểm một cái, một cỗ đặc thù phong ấn bao phủ, to lớn tế đàn chậm rãi thu nhỏ, rơi vào trên bình ngọc phương, thành một cái bình bỏ vào."),t("br"),t("br"),n._v("Nhẹ nhàng hạ xuống, bên trong tâm ma quả nhiên không có động tĩnh nữa, hình như biến mất không thấy hình bóng."),t("br"),t("br"),n._v("Tu vi đạt tới Thánh vực, chân khí đã có một chút huyền diệu biến hóa, tựa như hiện tại biến lớn thu nhỏ, trước đó vẫn còn tương đối phiền phức, mà bây giờ, đã không tính là gì."),t("br"),t("br"),n._v("Lúc trước Tử Dương thú làm Thánh thú, có thể trở nên hơn mười mét lớn nhỏ, liền là dùng loại phương pháp này."),t("br"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("Vốn cho rằng nhiều như vậy tâm ma, chỉ cần công kích trước mắt người thanh niên này, chẳng mấy chốc sẽ trầm luân, luống cuống, nằm mơ đều không có nghĩ đến, thời gian nháy mắt, tâm ma tất cả đều phản bội, bị đối phương thu phục... Tàng Hư toàn thân run rẩy, sợ tới mức sắp điên rồi."),t("br"),t("br"),n._v("“Sưu hồn!”"),t("br"),t("br"),n._v("Không thèm để ý đối phương hoảng sợ, bàn tay một trảo, Trương Huyền vu hồn sư đặc thù sưu hồn thi triển đi ra."),t("br"),t("br"),n._v("Loại thủ đoạn này, đối hồn phách tổn thương cực lớn, nhưng kẻ trước mắt này đầu nhập vào Dị Linh tộc, còn muốn lấy giết bản thân, cho dù lợi hại hơn nữa trừng phạt, cũng không đủ."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Thời gian nháy mắt, đối phương liền bị sưu hồn xong xuôi, Tàng Hư vốn chính là một đạo ý niệm, giờ phút này cũng lại không chịu nổi, trong nháy mắt sụp đổ, biến mất không thấy hình bóng."),t("br"),t("br"),n._v("“Thế mà tại Tề Bắc thành đầm lầy chi địa...”"),t("br"),t("br"),n._v("Sưu hồn xong xuôi, lập tức rõ cái tên này bí pháp là cùng ai học."),t("br"),t("br"),n._v("Vị này Tàng Hư, lúc trước chỉ là cái mao tặc, cùng Đạo Khấu hai người, bốn phía bị người đuổi giết, chạy đến đầm lầy về sau, vốn cho rằng hẳn phải chết không nghi ngờ, lại tại một cái đặc thù hang động, nhận được truyền thừa, còn người thừa kế là ai, lại là người nào, bọn họ cũng không rõ ràng."),t("br"),t("br"),n._v("Chỉ biết là, thường cách một đoạn thời gian, liền muốn dùng nguyên thần tế ra tế đàn, bắt giữ tu luyện giả hồn phách, cung cấp cho đối phương hưởng dụng."),t("br"),t("br"),n._v("Những hồn phách này, lấy danh sư vì tốt."),t("br"),t("br"),n._v("Những năm này trong tay hắn, chết đi danh sư, không biết bao nhiêu, nguyên nhân chính là như vậy, rõ Trương Huyền là danh sư, cũng không cảm thấy sợ hãi, vẫn như cũ tiếp tục động thủ."),t("br"),t("br"),n._v("“Sau này hãy nói đi...”"),t("br"),t("br"),n._v("Đem những ký ức này tiêu hóa, Trương Huyền không tại nhìn, Vu hồn trở lại thân thể, mở mắt lần nữa, chỉ thấy trước mắt tâm ma, đã toàn bộ biến mất, bản thân cũng theo trước đó huyễn cảnh bên trong khôi phục lại, trước mắt vẫn như cũ là cái kia bình thường gian phòng, dạ minh châu quang minh, chói lóa mắt."),t("br"),t("br"),n._v("Tình cảnh vừa nãy, cùng giống như nằm mơ, để hắn có chút hoảng hốt."),t("br"),t("br"),n._v("Cúi đầu nhìn lại, lòng bàn tay quả nhiên có bình ngọc: “Nhìn tới không phải nằm mơ, ta thật đem những này tâm ma thu phục...”"),t("br"),t("br"),n._v("Khóe miệng giật một cái."),t("br"),t("br"),n._v("Vốn đang tưởng rằng giả, nhìn thấy bình ngọc cảm nhận được trong đó bị phong ấn đồ vật, giờ mới hiểu được, đều là thật."),t("br"),t("br"),n._v("“Nhìn một chút tâm cảnh có biến hóa nha...”"),t("br"),t("br"),n._v("Trước đó phong thánh kiếp thời điểm, kinh nghiệm tâm ma, để tâm cảnh của hắn khắc độ chợt tăng một phen, đạt đến 23. 1, lần này nhiều như vậy tâm ma, có thể hay không, hiệu quả càng lớn?"),t("br"),t("br"),n._v("Vội vàng lấy ra khảo nghiệm Văn Lý Thạch, nhìn một cái, khóe miệng giật một cái."),t("br"),t("br"),n._v("“Tại sao như vậy?”"),t("br"),t("br"),n._v("Tâm cảnh còn cùng trước đó đồng dạng, chỉ có 23. 1, không có biến hóa chút nào."),t("br"),t("br"),n._v("Hình như vừa rồi kinh nghiệm những tâm ma này, không có chút nào hiệu quả đồng dạng."),t("br"),t("br"),n._v("“Chủ nhân, những tâm ma này không phải ngươi, cho dù ngươi có thể mê hoặc thành công, đối ngươi tâm cảnh, cũng không có bất kỳ trợ giúp nào... Không tăng trưởng, cũng rất bình thường!” Rõ ý nghĩ của hắn, Ngoan Nhân truyền âm tới."),t("br"),t("br"),n._v("“Tốt a...”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy buồn phiền."),t("br"),t("br"),n._v("Vì sao người khác đều nhiều như vậy tâm ma, hắn không có đâu?"),t("br"),t("br"),n._v("Liền để hắn tăng lên tâm cảnh cơ hội đều không có, quá không công bằng đi!"),t("br"),t("br"),n._v("Thật tịch mịch ah."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Đang tại than thở, chỉ thấy trước mắt mặt tường một hồi lay động, một cái lối đi xuất hiện ở trước mắt, hình như là hắn thông qua được tâm ma kiểm tra, kích phát một loại nào đó cơ quan."),t("br"),t("br"),n._v("Lại không tiếp tục xoắn xuýt, Trương Huyền đứng dậy, nhìn một cái, phát hiện phía sau cửa không có nguy hiểm, lúc này mới chậm rãi đi vào."),t("br"),t("br"),n._v("Đồng dạng là cái phòng kín mít, ở giữa đứng sừng sững lấy một cái bóng loáng thủy tinh cầu."),t("br"),t("br"),n._v("Bàn tay ở phía trên đụng một cái, một đạo quang mang lóe lên, một bóng người bất ngờ xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Là cái lão giả, nhìn không ra tuổi tác, chỉ có thể nhìn ra râu tóc trắng tinh."),t("br"),t("br"),n._v("“Có thể đi tới nơi này, nói rõ thông qua được trước mặt tâm ma khảo hạch, tâm cảnh đã hoàn mỹ không một tì vết, có tư cách tiếp nhận truyền thừa của ta...”"),t("br"),t("br"),n._v("Lão giả vuốt râu: “Tại hạ là Tâm điện người sáng lập, cái này điện đường, vì rèn luyện tâm cảnh mà sinh, ta muốn truyền thụ cho chính là Tâm điện cao nhất bí pháp, !”"),t("br"),t("br"),n._v("“Lưu Ly Luyện Tâm kinh?”"),t("br"),t("br"),n._v("“Bộ này luyện tâm pháp quyết, có thể để cho tâm ma số lượng giảm mạnh, tu luyện tới đại thành tình trạng, có thể tâm như lưu ly, sạch không tỳ vết, không nhận tâm ma hoang mang, nguyên thần củng cố mượt mà, thực lực cũng sẽ càng mạnh!”"),t("br"),t("br"),n._v('Lão giả cười nhìn lại: "Chỉ có có thể gánh vác được tâm ma đại trận thiên tài, mới có tư cách học tập... Ngươi có thể đi vào, nói rõ đã có tư chất, nhưng có hứng thú học tập?'),t("br"),t("br"),n._v("“Không hứng thú!” Trương Huyền nói."),t("br"),t("br"),n._v("“...” Lão giả."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1123"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1125"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);