(window.webpackJsonp=window.webpackJsonp||[]).push([[1541],{1895:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-309-tram-chuong-cung-vang-len"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-309-tram-chuong-cung-vang-len"}},[n._v("#")]),n._v(" Chương 309: Trăm chuông cùng vang lên")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v("“Làm sao lại nhanh như vậy?”"),g("br"),g("br"),n._v("“Cái này vẫn chưa tới nửa nén hương thời gian a? Đã bắt đầu bài giảng, hơn nữa... Giảng xong?”"),g("br"),g("br"),n._v("“Đến cùng nói cái gì?”"),g("br"),g("br"),n._v("Nghe được tiếng chuông, phía ngoài đám người tất cả đều thân thể run lên."),g("br"),g("br"),n._v("Công pháp hải cho ra bí tịch về sau, sẽ có thời gian một nén nhang quan sát, sau đó lại tiến hành giảng giải."),g("br"),g("br"),n._v("Hiện tại mọi người đi vào, tổng cộng cũng liền nửa nén hương tả hữu, Tiên Hiền Chung liền vang lên, chẳng lẽ... Cho một bản mới bí tịch, không cần nhìn liền trực tiếp bắt đầu bài giảng?"),g("br"),g("br"),n._v("Hơn nữa... Lúc này mới bao lâu, liền kể xong rồi?"),g("br"),g("br"),n._v("Bởi vì chỉ có giảng giải xong, mới có thể thu được tiên hiền tán thành, nghe được chuông vang."),g("br"),g("br"),n._v("Người khác đi vào, coi như thu hoạch được tiên hiền tán thành, cũng muốn chí ít tốn hao ba, bốn nén nhang thời gian, dài nhất càng là giảng nửa ngày."),g("br"),g("br"),n._v("Nửa nén hương liền bắt đầu gõ vang..."),g("br"),g("br"),n._v("Đừng nói chưa thấy qua, nghe đều chưa từng nghe qua!"),g("br"),g("br"),n._v("“Giảng quá nhanh, khẳng định nội dung không đầy đặn, ta sợ một tiếng chuông vang liền là cực hạn!” Lăng Tiêu Tiêu sau lưng lão giả nhịn không được lắc đầu, trên mặt lộ ra vẻ tiếc hận."),g("br"),g("br"),n._v("Dục tốc bất đạt, bất kỳ cái gì sự tình đều như thế."),g("br"),g("br"),n._v("Cho dù có bản sự, đối công pháp lý giải cũng nhanh... Nhưng nhìn một chút sách liền bắt đầu bài giảng, quá cầu tốc độ, tất nhiên có bỏ sót chỗ không nhìn thấy, dù sao lại đơn giản công pháp đều hội tụ tiền bối trí tuệ, hơi không cẩn thận, liền có thể ngộ nhập lạc lối, vạn kiếp bất phục."),g("br"),g("br"),n._v("Trên đời người tu luyện, tẩu hỏa nhập ma người rất nhiều, chính là đối công pháp lý giải có sai, mới trong tu luyện xuất hiện sai lầm."),g("br"),g("br"),n._v("Đương nhiên, không hiểu cũng còn đỡ, giảng bài thời gian cũng ngắn, lớp học liền không đầy đặn, rất dễ dàng lọt mất trọng điểm."),g("br"),g("br"),n._v("Không ít khảo hạch danh sư người, cùng đáp lại bài thi đồng dạng, sẽ đem biết đến tri thức toàn bộ nói ra, làm không cẩn thận câu kia liền đụng phải, từ đó đạt được càng lớn tán thành."),g("br"),g("br"),n._v("Ngắn như vậy thời gian kết thúc, lời hướng dẫn đồ vật liền thiếu đi, coi như để Tiên Hiền Chung vang lên, đoán chừng thanh âm cũng sẽ không quá nhiều."),g("br"),g("br"),n._v("“Đúng vậy a, vẫn là tuổi trẻ, quá gấp chút...” Hồng Vân đan sư cũng gật gật đầu."),g("br"),g("br"),n._v("Hắn nhận qua đối phương ân huệ, tự nhiên hi vọng vị này Trương Huyền lão sư, có thể đi càng xa, không nghĩ tới hắn gấp gáp như vậy, chỉ sợ thành tích cũng kém không nhiều như vậy dừng bước."),g("br"),g("br"),n._v("“Ngã một lần khôn hơn một chút, chờ hắn khảo hạch nhị tinh danh sư thời điểm, liền sẽ không như thế lỗ mãng...” Lão giả gật đầu, ngay tại cảm khái, mặt đất lần nữa chấn động, âm thanh lớn vang lên lần nữa."),g("br"),g("br"),n._v("Đương! Đương! Đương!"),g("br"),g("br"),n._v("Liên tiếp ba tiếng oanh minh, chấn mặt đất run rẩy."),g("br"),g("br"),n._v("“Bốn tiếng? Thượng giai? Đã có thể so với Mạc Hoằng Nhất Mạc sư thành tích?”"),g("br"),g("br"),n._v("Lão giả, Hồng Vân đan sư liếc mắt nhìn nhau, suýt chút nữa không có thổ huyết."),g("br"),g("br"),n._v("Vừa nói xong đối phương quá lỗ mãng, thành tích sẽ không quá tốt, một cái liền vang lên bốn tiếng... Hai người chỉ cảm thấy trên mặt nóng bỏng, một trận đau đớn."),g("br"),g("br"),n._v("“Bốn tiếng... Hẳn là cực hạn a?”"),g("br"),g("br"),n._v("Bốn tiếng kết thúc, yên lặng phút chốc, lão giả lúc này mới thở phào nhẹ nhõm, nhìn về phía trước, chính muốn tiếp tục nói cái gì, bên tai lần nữa chấn động."),g("br"),g("br"),n._v("Coong!"),g("br"),g("br"),n._v("Thanh âm vang lên lần nữa."),g("br"),g("br"),n._v("“Năm tiếng triệt để lý giải... Mới ghi chép... Ra đời!”"),g("br"),g("br"),n._v("Tất cả mọi người khuôn mặt trắng bệch, trợn mắt hốc mồm."),g("br"),g("br"),n._v("Vương quốc Danh Sư đường thành lập mấy ngàn năm qua, thành tích tốt nhất không ai qua được Mạc Hoằng Nhất bốn tiếng, mặt khác coi như mạnh hơn cũng nhiều nhất ngang tay... Trước mắt cái này Trương Huyền, chỉ dùng nửa nén hương thời gian, liền vang lên năm tiếng... Đã đem ghi chép đánh vỡ, trở thành đệ nhất nhân."),g("br"),g("br"),n._v("“Thiên tài, chính là thiên tài, cầm lấy bí tịch, liền có thể hiểu được, giảng chuông vang năm âm thanh, triệt để lý giải... Bội phục, bội...”"),g("br"),g("br"),n._v("Cảm khái một tiếng, lão giả còn chưa nói xong, mặt đất lần nữa chấn động."),g("br"),g("br"),n._v("Lại là một tiếng oanh minh."),g("br"),g("br"),n._v("“...”"),g("br"),g("br"),n._v("Mẹ nó!"),g("br"),g("br"),n._v("Có hết hay không?"),g("br"),g("br"),n._v("Lão giả nhanh muốn khóc."),g("br"),g("br"),n._v("Vừa cảm giác đối phương cực hạn đến đây là kết thúc, liền vang một cái, vừa cảm giác đến không cách nào tiến thêm một bước, lại vang một cái... Ngươi là cố ý a?"),g("br"),g("br"),n._v("Người khác đều là kể xong khóa, tiếng vang liên tiếp, chênh lệch thời gian không dài, ngươi cái này ngược lại tốt, đột nhiên vang một cái, đột nhiên vang một cái, là muốn đem người dọa chết tươi sao?"),g("br"),g("br"),n._v("“Năm vang hoàn mỹ...”"),g("br"),g("br"),n._v("Trong lòng của hắn phiền muộn, những người khác thì sắc mặt trắng bệch, tất cả đều hóa đá."),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("“Quan phó đường chủ, đường chủ ở nơi nào, mau phái người đi mời!”"),g("br"),g("br"),n._v("Danh Sư đường chỗ sâu một cái phòng, một vị lão giả sải bước đi tiến đến."),g("br"),g("br"),n._v("“Đường chủ đêm qua sau khi trở về, bắt đầu bế quan, để chúng ta không có đại sự không nên quấy rầy!”"),g("br"),g("br"),n._v("Danh Sư đường Phó đường chủ quan trạch quyền, nghi ngờ nhìn qua: “Chúc trưởng lão, chuyện gì xảy ra, để ngươi như thế bối rối? Đến, nói một chút, nếu thật là đại sự, ta cái này đi tìm đường chủ, để hắn tới!”"),g("br"),g("br"),n._v("“Bế quan?”"),g("br"),g("br"),n._v("Chúc trưởng lão nhướng mày, chần chờ một chút, vẫn là giải thích: “Là có người khảo hạch nhất tinh danh sư!”"),g("br"),g("br"),n._v("“Khảo hạch nhất tinh danh sư?”"),g("br"),g("br"),n._v("Quan phó đường chủ cười một tiếng, kìm lòng không được lắc đầu: “Ngô Toàn không phải tại sao? Để hắn chủ trì liền tốt, loại chuyện nhỏ nhặt này cũng đáng phiền phức đường chủ? Ngươi không biết đường chủ thân thể có ám thương, cần tĩnh dưỡng sao?”"),g("br"),g("br"),n._v("“Ta biết khảo hạch nhất tinh danh sư không đáng kinh động đường chủ! Nhưng... Nếu như ngươi biết khảo hạch người, náo ra nhiều động tĩnh lớn, thi ra cái gì thành tích, chỉ sợ cũng không đã nói như vậy!” Chúc trưởng lão lắc đầu."),g("br"),g("br"),n._v("“Ồ? Còn có thể làm ra nhiều động tĩnh lớn? Chẳng lẽ ra một cái giống như Mạc Hoằng Nhất thiên tài?”"),g("br"),g("br"),n._v("Quan phó đường chủ sững sờ."),g("br"),g("br"),n._v("“Mạc Hoằng Nhất?” Chúc trưởng lão lắc đầu: “Ta là nghe được Tiên Hiền Chung vang lên, mới nhịn không được đi qua nhìn, ba cửa trước cũng không nhìn thấy, không biết cụ thể thành tích, không tốt xen vào. Chỉ biết là Tiên Hiền Chung không ngừng vang lên...”"),g("br"),g("br"),n._v("“Không ngừng vang lên? Chẳng lẽ, vượt qua bốn vang?” Quan phó đường chủ lông mày vung lên."),g("br"),g("br"),n._v("“Đâu chỉ...” Chúc trưởng lão cười khổ."),g("br"),g("br"),n._v("“Bốn vang thượng giai, không chỉ bốn vang, chẳng phải là biểu thị... Đạt đến năm vang? Đây chính là triệt để lý giải, không có bất kỳ cái gì sai lầm...”"),g("br"),n._v("Quan phó đường chủ không còn có trước đó không quan trọng, đứng dậy, sắc mặt nghiêm túc: “Tiên Hiền Chung bên trong ẩn chứa tri thức, là tứ tinh danh sư lưu lại, đạt tới năm vang, đã nói lên giảng giải tri thức, đạt được bọn hắn triệt để tán thành... Ngươi xác định là khảo hạch nhất tinh danh sư?”"),g("br"),g("br"),n._v("“Là khảo hạch nhất tinh danh sư!” Chúc trưởng lão gật đầu: “Hơn nữa... Vang lên không chỉ năm tiếng!”"),g("br"),g("br"),n._v("Không phải Chúc trưởng lão không muốn nói, cố ý lưu lo lắng, mà là liền liền hắn, đều không thể tin được."),g("br"),g("br"),n._v("“Không phải năm tiếng...”"),g("br"),g("br"),n._v("Quan phó đường chủ rốt cuộc kìm nén không được, bờ môi run rẩy: “Chẳng lẽ là... Sáu vang? Năm vang triệt để lý giải, sáu vang hoàn mỹ, nói rõ đối công pháp lý giải, đã cùng tứ tinh danh sư không khác nhau chút nào...”"),g("br"),g("br"),n._v("Lời còn chưa nói hết, Chúc trưởng lão liền dùng để hắn phát điên thanh âm, nói: “Là bảy vang!”"),g("br"),g("br"),n._v("Phù phù!"),g("br"),g("br"),n._v("Quan phó đường chủ đầu gối mềm nhũn, một lần nữa ngồi ở trên chỗ ngồi, sắc mặt trắng bệch: “Chuông vang bảy minh, chư hiền tán thành? Cái này... Làm sao có thể?”"),g("br"),g("br"),n._v("Người người đều biết tiếng chuông vang vọng sáu âm thanh, liền đạt đến hoàn mỹ cấp bậc, nhưng lại không biết, đằng sau còn có bảy vang."),g("br"),g("br"),n._v("Đạt tới loại tình huống này, tương đương với đạt được Tiên Hiền Chung tán thành!"),g("br"),g("br"),n._v("Nói cách khác, đạt được tứ tinh danh sư tán thành, tại công pháp lý giải bên trên, có thể tới ngang hàng luận giao."),g("br"),g("br"),n._v("Một cái khảo hạch nhất tinh danh sư gia hỏa, để tứ tinh danh sư lưu lại ý niệm tán thành, ngang hàng luận giao?"),g("br"),g("br"),n._v("Quan trạch quyền chỉ cảm thấy sắc mặt trắng bệch, trong lòng tràn đầy điên cuồng."),g("br"),g("br"),n._v("Với tư cách Danh Sư đường Phó đường chủ nhiều năm như vậy, trời mới gặp qua không ít!"),g("br"),g("br"),n._v("Nhưng biến thái như vậy, đừng nói chưa thấy qua, nghe đều chưa từng nghe qua!"),g("br"),g("br"),n._v("“Chuông vang bảy minh... Có hay không có thể xin đường chủ đến đây?”"),g("br"),g("br"),n._v("Thấy thân thể của hắn run rẩy, Chúc trưởng lão biết đổi lại ai, nghe được tin tức này, chỉ sợ đều sẽ như thế, lần nữa nhìn tới."),g("br"),g("br"),n._v("“Cái này... Đương nhiên có thể, bất quá, ta trước đi xem một chút, xác nhận một chút...”"),g("br"),g("br"),n._v("Cố nén trong lòng rung động, Quan phó đường chủ thở ra một hơi, tay áo vẫy một cái."),g("br"),g("br"),n._v("“Vậy được rồi, chúng ta đi!”"),g("br"),g("br"),n._v("Chúc trưởng lão gật gật đầu, hai người đi ra ngoài, ngay tại vừa ra khỏi phòng."),g("br"),g("br"),n._v("Coong!"),g("br"),g("br"),n._v("Một cái cự đại vang lên, vang vọng toàn bộ Danh Sư đường."),g("br"),g("br"),n._v("Trước đó thanh âm, chỉ ở khảo hạch danh sư trong đại sảnh lưu chuyển, mà thanh âm này, tựa hồ đem trọn cái Thiên Vũ vương thành đều lắc lư một cái."),g("br"),g("br"),n._v("Rung động dữ dội, giống như là muốn xé rách Danh Sư đường gông cùm xiềng xích, đem thiên địa đều thông suốt mở một đường vết rách."),g("br"),g("br"),n._v("Chấn động to lớn dưới, Quan phó đường chủ, Chúc trưởng lão đồng thời té ngã trên đất, sắc mặt trắng bệch, không thể tin được thanh âm chậm rãi vang lên..."),g("br"),g("br"),n._v("“Là... Trăm chuông cùng vang lên... Chư hiền bái phục?”"),g("br"),g("br"),n._v("“Cái này... Đến cùng xảy ra chuyện gì?”"),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("“Chư hiền bái phục? Có ý tứ gì? Ta làm sao chưa từng nghe nói qua?”"),g("br"),g("br"),n._v("Nghe được tất cả tiếng chuông đồng thời vang lên, thanh âm giống như là muốn xé rách không khí, Lăng Tiêu Tiêu cũng nhịn không được nữa nhìn về phía sau lưng lão giả."),g("br"),g("br"),n._v("Nàng nghe qua một vang, nhị vang, ba vang... Mãi cho đến sáu vang, bảy vang liền chưa từng nghe qua!"),g("br"),g("br"),n._v("Hiện tại trăm chuông cùng vang lên... Càng làm cho nàng đầu óc choáng váng, không hiểu rõ đến cùng chuyện gì xảy ra."),g("br"),g("br"),n._v("“ ý tứ chính là... Coi như tứ tinh danh sư ở chỗ này, cũng sẽ an tâm nghe giảng bài, không dám phản bác... Nói đúng là, vị này Trương Huyền đối công pháp giảng giải, đã vượt qua tứ tinh phạm trù, đạt tới ngũ tinh danh sư, thậm chí cao hơn...”"),g("br"),g("br"),n._v("Lão giả run rẩy."),g("br"),g("br"),n._v("“Cái gì? Vượt qua tứ tinh?”"),g("br"),g("br"),n._v("Lăng Tiêu Tiêu sắc mặt trắng nhợt."),g("br"),g("br"),n._v("Cái kia cái gì cũng đều không hiểu gia hỏa, đối công pháp, võ kỹ lý giải, vượt qua tứ tinh..."),g("br"),g("br"),n._v("Cái này sao có thể?"),g("br"),g("br"),n._v("Nàng chỉ cảm thấy thân thể run rẩy, đều muốn điên rồi."),g("br"),g("br"),n._v("“Đúng vậy a, chỉ bất quá...”"),g("br"),g("br"),n._v("Lão giả nói đến đây ngừng lại, hoảng sợ ánh mắt bên trong, còn mang theo nghi hoặc."),g("br"),g("br"),n._v("“Chẳng qua cái gì?” Lần này không chỉ Lăng Tiêu Tiêu, liền liền một bên Hồng Vân đan sư cũng nhìn qua."),g("br"),g("br"),n._v("“Bất quá... Tình huống bình thường, trăm chuông cùng vang lên, chư hiền tán thành, tiếng chuông muốn liên tục vang vọng một thời gian uống cạn chung trà, làm ăn mừng, làm sao chỉ vang lên một tiếng liền kết thúc?”"),g("br"),g("br"),n._v("“Một thời gian uống cạn chung trà?”"),g("br"),g("br"),n._v("“Đúng vậy a!” Lão giả lắc đầu: “Xem ra chỉ có thể chờ đợi tất cả danh sư đi ra, mới có thể biết đáp án!”"),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v("Công pháp hải gian phòng bên trong."),g("br"),g("br"),n._v("Tất cả chịu trách nhiệm khảo hạch nhất tinh danh sư, tất cả đều nghe như si như say."),g("br"),g("br"),n._v("Trước mắt vị thanh niên này giảng giải Đa Ba Thủy Văn chưởng, quá thấu triệt, dựa theo hắn giảng giải phương pháp tu luyện, không bao lâu, liền có thể người người học được."),g("br"),g("br"),n._v("Chân chính đạt tới người người như rồng."),g("br"),g("br"),n._v("Đương đương đương đương!"),g("br"),g("br"),n._v("Cùng đám người si say đồng dạng, chung quanh chuông lớn, nghe được cao thâm như vậy lý luận, cũng hưng phấn không thôi, đồng thời vang lên, thanh âm trực trùng vân tiêu."),g("br"),g("br"),n._v("“Ngậm miệng, có hết hay không? Một tiếng, hai tiếng, ba tiếng, ta không so đo ngược lại cũng thôi, bây giờ lại cùng một chỗ vang rền, ta khóa còn không có kể xong, mò mẫm kêu to cái rắm!”"),g("br"),g("br"),n._v("Lông mày nhíu một cái, Trương Huyền một tiếng quát lớn."),g("br"),g("br"),n._v("Ô!"),g("br"),g("br"),n._v("Vang lên rất nhiều chuông lớn, lập tức câm xuống dưới."),g("br"),g("br"),n._v("Ngô sư, Mạc Hoằng Nhất, Mạc Vũ, Khương Thần đám người, há to miệng, nguyên một đám mặt mũi tràn đầy phát điên."),g("br"),g("br"),n._v("Convert by: ThấtDạ"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-317"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-319"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);