(window.webpackJsonp=window.webpackJsonp||[]).push([[1548],{1903:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-315-tam-canh-1-0-1"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-315-tam-canh-1-0-1"}},[n._v("#")]),n._v(" Chương 315: Tâm cảnh 1 0.1!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Chúng ta dùng Văn Lý Thạch, gọi là hạ phẩm Văn Lý Thạch, chỉ có thể kiểm trắc 9. 0 trở xuống tâm cảnh, nói cách khác, chỉ có thể khảo thí tam tinh danh sư trở xuống khắc độ, một khi vượt qua... Liền sẽ không chịu nổi, biến thành phế phẩm.”"),t("br"),t("br"),n._v("Khương đường chủ gật gật đầu, chỉ hướng trên đất bột phấn, mở lời giải thích: “Nếu không, bình thường Văn Lý Thạch, cứng rắn vô cùng, không cao hơn phạm vi chịu đựng phá hư trong đó hoa văn, làm sao có thể đụng một cái liền nát?”"),t("br"),t("br"),n._v("“Vừa nói như vậy, ngược lại là có khả năng!”"),t("br"),t("br"),n._v("Đám người lần nữa sửng sốt."),t("br"),t("br"),n._v("Văn Lý Thạch làm ngọc thạch, tính chất cứng rắn, coi như dùng trường kiếm cứng rắn bổ, đều rất khó phá hư, đụng một cái liền biến thành bụi phấn, quả thật có chút quỷ dị."),t("br"),t("br"),n._v("“Đường chủ có ý tứ là nói... Trương sư tâm cảnh khắc độ giá trị, khả năng vượt qua... 9. 0?”"),t("br"),t("br"),n._v("Hiểu đối phương ý tứ, Chúc trưởng lão bờ môi phát khô, thanh âm hơi hơi run rẩy."),t("br"),t("br"),n._v("9. 0 khái niệm gì?"),t("br"),t("br"),n._v("Tam tinh danh sư tiêu chuẩn! Một cái không đủ hai mươi gia hỏa, đạt tới loại này tiêu chuẩn?"),t("br"),t("br"),n._v("Nghe nói như thế, mọi người chung quanh cũng hiểu được, nguyên một đám trợn mắt hốc mồm."),t("br"),t("br"),n._v("Mẹ nó, vừa khảo nghiệm 5. 0, vốn cho rằng đột phá 6. 0 liền ngưu rối tinh rối mù, nghe Khương đường chủ ý tứ, còn không chỉ như thế..."),t("br"),t("br"),n._v("“Muốn xác định có phải hay không, cũng rất đơn giản, ta chỗ này có một viên trung phẩm Văn Lý Thạch, kiểm tra một chút, liền có thể biết thành tích!”"),t("br"),t("br"),n._v("Khương đường chủ sắc mặt nghiêm túc, cổ tay khẽ đảo, một viên ngọc thạch xuất hiện."),t("br"),t("br"),n._v("Cái này mai Văn Lý Thạch rất rõ ràng cùng trước đó sử dụng khác biệt, càng thêm chói lóa mắt, mặt ngoài hoa văn, cũng càng thêm dày đặc, tu vi thấp người, chỉ nhìn lên một cái, liền sẽ cảm thấy đầu não choáng váng, không chịu nổi."),t("br"),t("br"),n._v("Trung phẩm Văn Lý Thạch! Mỗi một mai, đều giá trị liên thành."),t("br"),t("br"),n._v("Toàn bộ Thiên Vũ vương quốc Danh Sư đường, cũng chỉ có một viên."),t("br"),t("br"),n._v("“Trương sư huynh, còn làm phiền ngươi thử lại một chút!”"),t("br"),t("br"),n._v("Đem Văn Lý Thạch đưa tới."),t("br"),t("br"),n._v("Nghe được công pháp, đã xác nhận vị này Trương Huyền khẳng định là Dương sư học sinh, lập tức xưng hô cũng sửa lại, biến thành sư huynh."),t("br"),t("br"),n._v("Đổi lại người khác, Danh Sư đường đường chủ, thế nhưng là liền quốc vương đều có thể không để ý tới siêu cường nhân vật, xưng hô một cái so nhi tử còn nhỏ gia hỏa là sư huynh, tất nhiên thẹn thùng."),t("br"),t("br"),n._v("Nhưng chính mắt thấy “Dương sư” đáng sợ, lại thêm danh sư bên trong cũng có “Tuổi tác không lớn nhỏ, người thành đạt vi sư” thuyết pháp, tiếng thứ nhất kêu đi ra, đằng sau cũng là không cảm thấy khó chịu."),t("br"),t("br"),n._v("[ngantruyen.com ]"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Lên tiếng, Trương Huyền tiếp nhận Văn Lý Thạch, lòng bàn tay hợp lại, tinh thần khẽ động, tâm cảnh lần nữa vận chuyển lên tới."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Lần nữa kêu khẽ, cúi đầu nhìn lại."),t("br"),t("br"),n._v("Cùng mới vừa rồi không có số lượng khác biệt, một đạo hào quang chói sáng từ đó phẩm Văn Lý Thạch bên trên bắn ra, chiếu tứ phương sặc sỡ loá mắt, một hàng con số chậm rãi từ phía trên nổi lên."),t("br"),t("br"),n._v("Thấy rõ ràng phía trên số lượng, đám người tất cả đều ngây người, lập tức vỡ tổ."),t("br"),t("br"),n._v("“Mười, mười... Mười điểm một?”"),t("br"),t("br"),n._v("“Tâm cảnh của hắn khắc độ, là... 1 0.1?”"),t("br"),t("br"),n._v("Vốn cho rằng đối phương là đang khoác lác, không có tâm cảnh khắc độ, kết quả... Đạt đến mười điểm một!"),t("br"),t("br"),n._v("Tâm cảnh 3.0 khảo hạch nhất tinh danh sư, 6. 0 khảo hạch nhị tinh, 1 0.1... Coi như khảo hạch tam tinh cũng đầy đủ!"),t("br"),t("br"),n._v("“Không phải mới vừa... 5.1 sao? Làm sao...”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất lần này là thật muốn khóc."),t("br"),t("br"),n._v("Hắn cảm giác đối phương là đặc biệt tới đánh mặt."),t("br"),t("br"),n._v("Phá hắn lưu lại tất cả ghi chép không nói, càng quan trọng hơn là, vừa chất vấn chức nghiệp không đủ, liền lấy ra huy chương; Vừa nói người ta không có đạt tới Tông sư, ngay tại chỗ đột phá; Mấu chốt nhất là... Vừa nói tâm hắn cảnh khắc độ không đủ, không chỉ đạt đến 6. 0, còn một cái liền khảo hạch tam tinh danh sư tư cách cũng đủ."),t("br"),t("br"),n._v("Còn có thể hay không vui sướng chơi đùa?"),t("br"),t("br"),n._v("Nếu có một cái lỗ, tuyệt đối sẽ lập tức chui vào, lại cũng không muốn sống."),t("br"),t("br"),n._v("Một bên Lục Tầm cũng cảm thấy yết hầu ngòn ngọt, suýt chút nữa một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("Hắn đột phá tâm như chỉ thủy, khắc độ đạt tới 1.8, thật hưng phấn không ngủ cảm giác, tự cho là đúng siêu cấp thiên tài, trời đất bao la có thể tùy ý ngao du, nhìn thấy Trương lão sư cái này tâm cảnh, mới biết được... Hắn đồ chơi kia, cái gì cũng không bằng."),t("br"),t("br"),n._v("Quay đầu nhìn lại, nhịn không được lắc đầu, đối Mạc Hoằng Nhất đầy là đồng tình."),t("br"),t("br"),n._v("Vị này đường đường đệ nhất thiên tài, bị đả kích... Sinh hoạt đều nhanh không thể tự gánh vác."),t("br"),t("br"),n._v("Trương lão sư, ngươi cũng là đủ!"),t("br"),t("br"),n._v("Liền Mạc Hoằng Nhất loại này nhân vật lợi hại, đều bị đả kích thành như vậy, hắn biết mình cùng vị này Trương lão sư chênh lệch đã lớn tựa như hồng câu, tại không có bất kỳ cái gì có thể so cần thiết."),t("br"),t("br"),n._v("“Đến 1 0.1 rồi?”"),t("br"),t("br"),n._v("Đám người nổi điên, Trương Huyền cũng là sững sờ, lập tức nhẹ gật đầu."),t("br"),t("br"),n._v("Xem ra kim sắc trang sách cũng không đem trước khắc độ triệt tiêu, vẫn là trước sau như một ra sức, để cho mình lần nữa tăng lên 5. 0 khắc độ."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Vốn là, đám người liền rất phiền muộn, gặp hắn một mặt hậu tri hậu giác, vừa mới minh bạch bộ dạng, lần nữa thổ huyết."),t("br"),t("br"),n._v("Tâm cảnh khắc độ đạt tới cảnh giới gì, bản thân thế mà không biết... Ngươi tốt đi một chút được hay không?"),t("br"),t("br"),n._v("“Trương sư, tâm cảnh khắc độ rất khó tăng lên, ngươi đây là...”"),t("br"),t("br"),n._v("Đem trong lòng rung động ngăn chặn, Chúc trưởng lão thực sự nhịn không được, hỏi."),t("br"),t("br"),n._v("Tâm cảnh khắc độ cùng tuổi tác đồng dạng, cho dù có biện pháp tốt tu luyện, cũng chỉ có thể làm từng bước, mài nước công phu một chút xíu gia tăng."),t("br"),n._v("Một cái theo 5.1, đạt tới 1 0.1, không khỏi gia tăng cũng quá nhanh chút!"),t("br"),t("br"),n._v("Hơn nữa, tâm cảnh tăng lên bao nhiêu, chính mình cũng không biết..."),t("br"),t("br"),n._v("Ngươi dám lại hồ đồ một chút sao?"),t("br"),t("br"),n._v("Nghe được tra hỏi, những người khác cũng tất cả đều nhìn qua, muốn biết đến cùng chuyện gì xảy ra."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền chần chờ một chút, trên mặt vẻ làm khó, một lát sau, nói: “Mà thôi, nếu như không nói, chắc hẳn các ngươi cũng sẽ không cam lòng. Tâm cảnh của ta khắc độ, cụ thể là bao nhiêu, ta đích xác không biết, bởi vì vừa mới đột phá tâm như chỉ thủy thời điểm, liền bị lão sư phong bế, hơn nữa chuyên môn bàn giao, chỉ có thực lực đạt tới mới có thể giải khai... Vừa rồi sở dĩ gia tăng nhiều như vậy, hẳn là vừa mới đột phá Tông sư, khôi phục một chút!”"),t("br"),t("br"),n._v("Kim sắc trang sách, Thiên Đạo thư viện, không có khả năng nói ra, về phần lí do thoái thác, trước đó liền nghĩ kỹ, giao cho cái kia có lẽ có lão sư là được."),t("br"),t("br"),n._v("“Phong bế tâm cảnh? Còn có loại thuyết pháp này?”"),t("br"),t("br"),n._v("“Nên mạnh bao nhiêu tâm cảnh, mới muốn xuất thủ phong bế?”"),t("br"),t("br"),n._v("Đám người xôn xao."),t("br"),t("br"),n._v("Phong bế tâm cảnh khắc độ, bọn hắn chưa từng nghe qua, nhưng nếu như không phải như vậy, thật không có cách nào giải thích, tâm cảnh khắc độ tại sao lại thoáng cái bạo tăng 5. 0!"),t("br"),t("br"),n._v("Rung động đồng thời cũng giống nhìn quái vật nhìn qua."),t("br"),t("br"),n._v("Đến cùng cao bao nhiêu tâm cảnh, mới cần phong ấn?"),t("br"),t("br"),n._v("Mà hắn vị lão sư kia lại là mấy sao danh sư mới có thể làm đến điểm ấy?"),t("br"),t("br"),n._v("Trước đó, còn có người cảm thấy hắn có phải hay không có lợi hại gì đột phá tâm cảnh phương pháp, nghe được thế mới biết, người ta tâm cảnh cường đại là trời sinh, không cách nào sánh được."),t("br"),t("br"),n._v("“Có ít người xác thực trời sinh tâm cảnh cường đại, nghe nói lúc trước thiên nhân Khổng sư, liền là Tiên Thiên tâm cảnh nhập Thánh, có thể so với cửu tinh danh sư, vừa ra đời liền mười phần bất phàm, cuối cùng mới có thể đi đến thế giới đỉnh phong, để cho người ta bái phục!”"),t("br"),t("br"),n._v("Khương đường chủ mở lời."),t("br"),t("br"),n._v("“Tiên Thiên tâm cảnh nhập Thánh? Có thể so với cửu tinh danh sư?”"),t("br"),t("br"),n._v("Đám người lần nữa giật nảy mình."),t("br"),t("br"),n._v("Mặc dù không biết cửu tinh danh sư cụ thể tiêu chuẩn, nhưng dựa theo 3.0 một cái cấp bậc, cũng muốn đạt tới 27. 0 trở lên mới được."),t("br"),t("br"),n._v("Trời sinh liền tâm cảnh khắc độ đạt tới 27?"),t("br"),t("br"),n._v("“Không chỉ Khổng sư, Hiên Viên phong hào vương quốc Danh Sư đường, cũng có một vị thiên tài, mười bảy tuổi bước vào tâm như chỉ thủy cảnh giới, lần thứ nhất khảo thí, tâm cảnh khắc độ liền có để cho người ta kinh khủng 9. 3, có thể đủ khảo hạch tam tinh danh sư.”"),t("br"),t("br"),n._v("Thân là đường chủ, biết rất nhiều bí mật, Khương Thư tiếp tục nói: “Loại siêu cấp thiên tài này, mặc dù thưa thớt, nhưng cũng từng có tiền lệ! Bất quá, tâm cảnh quá cao, tu vi theo không kịp, rất dễ dàng xuất hiện sai lầm, cuối cùng dẫn đến không cảm nhận được xem xét biến số, khả năng lão sư cũng là giám ở đây, mới đưa tâm cảnh của ngươi khắc độ phong ấn.”"),t("br"),t("br"),n._v("Nói đến đây nhìn về phía Trương Huyền, trong mắt tràn đầy hâm mộ."),t("br"),t("br"),n._v("Khó trách Dương sư loại này nhân vật lợi hại, thu hắn làm học sinh, trời sinh liền có được loại này tâm cảnh khắc độ, tuyệt đối là ngàn dặm mới tìm được một danh sư kỳ tài, chỉ cần không vẫn lạc, thành tựu tuyệt đối không tại tứ tinh danh sư phía dưới."),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("“Cũng đúng, tâm cảnh khó mà tăng lên, thường thường tu luyện mấy chục năm, đều tăng lên không được không phẩy mấy, một điểm mấy, nếu như đều giống như chúng ta, điểm xuất phát thấp như vậy, lại làm sao có thể xuất hiện lục tinh danh sư, thất tinh danh sư? Thậm chí cao hơn?”"),t("br"),t("br"),n._v("“Đúng vậy a, trời đất bao la, cường giả vô số, Thiên Vũ vương quốc mặc dù tại xung quanh mười ba trong nước tính toán lớn nhất, cùng chân chính cường quốc so sánh, vẫn như cũ chỉ là biên thuỳ nơi hẻo lánh.”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe xong Khương đường chủ giải thích, đám người giờ mới hiểu được tới."),t("br"),t("br"),n._v("Trời đất bao la, cường giả vô số, rất nhiều chuyện theo bọn hắn nghĩ, hào không có lý do, trên thực tế chỉ là kiến thức ít mà thôi."),t("br"),t("br"),n._v("Trương Huyền cũng không nghĩ tới, còn có trời sinh tâm cảnh khắc độ liền cường đại như thế, hơi hơi kinh ngạc."),t("br"),t("br"),n._v("Bất quá, đã Khương đường chủ đã thay hắn giải thích, cũng là lười nhác đang tìm lý do, nhìn về phía trước mắt Mạc Hoằng Nhất."),t("br"),t("br"),n._v("“Chức nghiệp, tu vi, tâm cảnh đều đạt đến... Mạc sư, khảo hạch nhị tinh danh sư, còn có cái gì yêu cầu? Nếu không... Cùng một chỗ nói đi, cũng tốt cùng một chỗ chuẩn bị một chút!”"),t("br"),t("br"),n._v("“Không có...”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất chỉ cảm thấy trên mặt nóng bỏng, thân thể lắc một cái, mặt như mướp đắng."),t("br"),t("br"),n._v("Vốn nghĩ ngăn cản đối phương khảo hạch, tốt một tiếng hót lên làm kinh người, kết quả... Kinh người không làm được, ngược lại biến thành mất mặt."),t("br"),t("br"),n._v("“Không có, cái kia... Ta có thể khảo hạch nhị tinh danh sư đi?” Trương Huyền nhãn tình sáng lên."),t("br"),t("br"),n._v("“Có thể...”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất đè nén muốn thổ huyết xúc động."),t("br"),t("br"),n._v("Sớm biết gia hỏa này biến thái như vậy, không có việc gì cùng hắn đòn khiêng cái gì, kết quả lần này tốt, thiên tài thanh danh xuống dốc đến, ở trong mắt người khác biến thành không biết lượng sức..."),t("br"),t("br"),n._v("“Đã phù hợp điều kiện, cái kia nhị vị bắt đầu khảo hạch đi!”"),t("br"),t("br"),n._v("Sợ Trương Huyền tiếp tục hỏi tiếp, Mạc Hoằng Nhất sẽ làm trận buồn bực nhảy lầu, Chúc trưởng lão đánh gãy xấu hổ."),t("br"),t("br"),n._v("“Khảo hạch nhị tinh danh sư, đồng dạng có năm cửa, trong đó phụ sửa sảnh, khôi lỗi điện, công pháp hải, không sai đường cơ hồ tương đồng, chỉ là độ khó gia tăng, tỷ như, phụ sửa sảnh vẫn như cũ khảo hạch luyện đan lời nói, sẽ chỉ điểm luyện chế nhị phẩm đan dược. Thư hoạ sư, cũng sẽ chỉ điểm làm ra tứ cảnh tác phẩm hội họa!”"),t("br"),t("br"),n._v("“Cái này bốn quan tương đồng, chỉ có cửa thứ nhất tín nhiệm phòng, hoàn toàn khác biệt!”"),t("br"),t("br"),n._v("“Cửa thứ nhất tín nhiệm phòng, yêu cầu kiểm trắc học sinh độ tín nhiệm, người học sinh này, có thể giảng dạy mấy năm, có thể tuổi tác không hạn, không ít người có thể đầu cơ trục lợi, đem học sinh từ nhỏ đã mang theo, tự nhiên cùng hắn thân hòa độ cao... Nhưng khảo hạch nhị tinh liền không dễ dàng như vậy.”"),t("br"),t("br"),n._v("“Khảo hạch nhị tinh, sẽ để cho người khảo nghiệm hiện thu học sinh, cấp cho thời gian mười ngày, trong khoảng thời gian này, độ tín nhiệm không cần vượt qua năm mươi, đạt tới bốn mươi coi như thông qua.”"),t("br"),t("br"),n._v("Chúc trưởng lão nói ra khảo hạch nhị tinh danh sư quy củ."),t("br"),t("br"),n._v("(Quyển thứ ba ta làm tên sư kết thúc, quyển kế tiếp hắn là thầy ta, tức sắp mở ra, kính thỉnh chờ mong!)"),t("br"),t("br"),n._v("(Khảo hạch danh sư đại cao trào, lão nhai từ phía trên huyền vương nước liền bắt đầu bố cục, kim sắc trang sách đột nhiên bạo tăng 5. 0 hồn lực khắc độ, Thú đường khảo hạch nhị tinh thuần thú, thu Khương Thư vì học sinh, khảo hạch tam tinh thư hoạ sư... May mắn, viết cùng đại cương nhất trí, không có bất kỳ cái gì chệch hướng, lão nhai cũng coi như hài lòng. Còn Độc điện tình tiết, cái kia là bố cục đằng sau một cái siêu cấp đại cao trào, tạm thời liền không lộ ra.)"),t("br"),t("br"),n._v("(Nhìn qua vô tận đan điền bằng hữu đều biết, nhân vật chính Nhiếp vân cùng đệ đệ Nhiếp đồng tình huynh đệ, lão nhai quyển thứ tư, hắn là thầy ta, cũng muốn triển khai một đoạn thầy trò tình, lão sư cùng học sinh, chân chính tình nghĩa, kính mời mọi người chờ mong! Quyển này kết thúc, không nghỉ ngơi, ban đêm còn có đổi mới, không đáng nguyệt phiếu, phiếu đề cử sao, hắc hắc..)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-323"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-325"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);