(window.webpackJsonp=window.webpackJsonp||[]).push([[1549],{1904:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-316-tin-nhiem-phong-chan-chinh-khao-hach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-316-tin-nhiem-phong-chan-chinh-khao-hach"}},[n._v("#")]),n._v(" Chương 316: Tín nhiệm phòng chân chính khảo hạch")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Hiện thu học sinh?”"),t("br"),t("br"),n._v("“Dạy bảo mười ngày, liền để độ tín nhiệm đạt tới bốn mươi?”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất nhướng mày."),t("br"),t("br"),n._v("Độ tín nhiệm bốn mươi nghe so khảo hạch nhất tinh đơn giản, trên thực tế độ khó gia tăng gấp đôi không thôi."),t("br"),t("br"),n._v("Trước đó độ tín nhiệm năm mươi, không ít người dạy bảo học sinh chí ít ba năm trở lên, hơn nữa từ nhỏ đã bắt đầu ở chỉ điểm, tín nhiệm cũng là tốt hơn thành lập."),t("br"),t("br"),n._v("Thu học sinh, từ khi biết đến khảo hạch, chẳng qua thời gian mười ngày, liền muốn để hắn độ tín nhiệm đạt tới bốn mươi, khó khăn chi đại, khiến cho người khó có thể tưởng tượng."),t("br"),t("br"),n._v("“Tín nhiệm phòng cái này liên quan khảo hạch, có thể đặt ở phía sau cùng, hiện tại các ngươi đã có thể khảo hạch còn lại bốn quan!”"),t("br"),t("br"),n._v("Giới thiệu xong quy tắc, Chúc trưởng lão nhìn về phía Trương Huyền, Mạc Hoằng Nhất hai người."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, nhấc chân liền muốn đi vào, mới đi hai bước liền bị Chúc trưởng lão gọi lại."),t("br"),t("br"),n._v("“Trương sư, ngươi trước không vội...”"),t("br"),t("br"),n._v("Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Ây... Là như thế này, ngươi có thể hay không tại Mạc sư đằng sau thi, ta sợ... Ngươi đi về sau, khôi lỗi xuất hiện chuyện ngoài ý muốn, như thế... Mạc sư liền không có cách nào khảo hạch!” Chúc trưởng lão có chút xấu hổ."),t("br"),t("br"),n._v("Danh Sư đường cửa thứ nhất, gia hỏa này thi qua về sau, có khôi lỗi cửa ải đều hỏng, lần sau lại có thể khiến người ta khảo hạch, còn không biết bao lâu."),t("br"),t("br"),n._v("Chúc trưởng lão đã có chút e ngại, sợ cửa thứ hai quá khứ cũng là một mảnh hỗn độn."),t("br"),t("br"),n._v("Thật muốn như thế, Mạc Hoằng Nhất cũng không cần thi lại."),t("br"),t("br"),n._v("“Ây... Tốt a!”"),t("br"),t("br"),n._v("Trương Huyền tràn đầy thật không tiện."),t("br"),t("br"),n._v("Không nghĩ tới liền khảo hạch một lần, cho người ta lưu lại như vậy một cái ấn tượng xấu."),t("br"),t("br"),n._v("Đương nhiên, nếu như cho hắn biết, tại mọi người trong suy nghĩ, hắn khảo hạch danh sư, quả thực cùng đập phá quán, đá quán đồng dạng, tuyệt đối sẽ càng thêm im lặng."),t("br"),t("br"),n._v("Mạc Hoằng Nhất không hổ là Thiên Vũ vương quốc siêu cấp thiên tài, luân phiên khảo hạch, một canh giờ sau, đi ra, bốn quan toàn bộ thông qua."),t("br"),t("br"),n._v("Thành tích bên trên mặc dù không có Trương Huyền như vậy nghịch thiên, nhưng cũng phá vỡ trước kia không ít ghi chép."),t("br"),t("br"),n._v("Tiếp lấy Trương Huyền bắt đầu khảo hạch."),t("br"),t("br"),n._v("Lại là một hồi náo loạn, tất cả cửa ải đều tàn phế không sai biệt lắm, cái này mới đi ra."),t("br"),t("br"),n._v("Nhìn thấy triệt để tê liệt Danh Sư đường, Khương đường chủ, Chúc trưởng lão, Ngô sư đám người nước mắt ào ào... Mặc dù Danh Sư đường có thể tự động tu bổ, lại cũng cần đại lượng tài chính, tài liệu đầu nhập, nhất tinh, nhị tinh khôi lỗi đều hứng chịu tới tổn thương, còn không biết phải hao phí đại giới cỡ nào, mới có thể hoàn hảo như lúc ban đầu."),t("br"),t("br"),n._v("“Thông qua cái này bốn quan, còn có cửa ải cuối cùng tín nhiệm phòng khảo hạch, cho các ngươi thời gian mười ngày, thu một vị hoặc là nhiều vị học sinh, chỉ cần để hắn độ tín nhiệm vượt qua bốn mươi là được!”"),t("br"),t("br"),n._v("Hai người tất cả đều thông qua, Chúc trưởng lão nói ra cuối cùng một hạng."),t("br"),t("br"),n._v("“Thu một cái học sinh là được?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Không sai!” Chúc trưởng lão vuốt râu."),t("br"),t("br"),n._v("“Mười ngày...” Trương Huyền suy tư một chút: “Ta hiện tại thu một cái... Được hay không?”"),t("br"),t("br"),n._v("Mười ngày khảo hạch nhị tinh danh sư, thực sự quá chậm, không được, liền hiện trường thu một cái, chắc hẳn để hắn độ tín nhiệm vượt qua bốn mươi, cũng không khó."),t("br"),t("br"),n._v("“Đừng vội, thu học sinh còn có yêu cầu!”"),t("br"),t("br"),n._v("Chúc trưởng lão lắc đầu, ngay sau đó nhìn quanh một tuần: “Khảo hạch nhị tinh danh sư, còn có nhiệm vụ muốn Trương sư, Mạc sư đi làm, có thể muốn rời đi Thiên Vũ vương thành, trong thời gian ngắn không thể trở về đến, chư vị mời về đi. Yên tâm, nhiệm vụ này, không dính dáng nguy hiểm, an toàn vô cùng, chỉ bất quá trong thời gian ngắn, không cách nào cùng các ngươi liên hệ mà thôi!”"),t("br"),t("br"),n._v("“Còn có nhiệm vụ?”"),t("br"),t("br"),n._v("“Muốn rời khỏi vương thành? Làm sao trước kia chưa từng nghe qua?”"),t("br"),t("br"),n._v("“Chỉ cần không nguy hiểm là được!”"),t("br"),t("br"),n._v("Không nghĩ tới khảo hạch nhị tinh danh sư phức tạp như vậy, còn có rời đi vương thành nhiệm vụ, mọi người chung quanh tất cả đều sững sờ, muốn còn muốn hỏi rõ ràng, nhưng thấy Chúc trưởng lão không muốn nhiều lời, lại hạ lệnh trục khách, đành phải lắc đầu, lui ra ngoài."),t("br"),t("br"),n._v("Triệu Nhã mấy người cũng nhìn Trương Huyền một chút, đi đại sảnh, rất nhanh trong phòng chỉ còn lại có Trương Huyền, Mạc Hoằng Nhất cùng Khương đường chủ mấy người ba vị nhị tinh danh sư."),t("br"),t("br"),n._v("“Tốt, Chúc trưởng lão đừng thừa nước đục thả câu, nói đi!” Khương đường chủ khoát khoát tay."),t("br"),t("br"),n._v("“Ừm!” Chúc trưởng lão gật gật đầu, nhìn về phía Trương Huyền hai người: “Mới vừa rồi cùng đám người nói, các ngươi phải có ra khỏi thành nhiệm vụ là giả, mục đích là vì phối hợp các ngươi hoàn thành tín nhiệm phòng khảo hạch, phòng ngừa gây nên phiền toái không cần thiết!”"),t("br"),t("br"),n._v("Nhìn nhau, Trương Huyền cùng Mạc Hoằng Nhất đều có chút không hiểu rõ."),t("br"),t("br"),n._v("“Tín nhiệm phòng khảo hạch, cùng ta mới vừa nói đồng dạng, trong mười ngày, để mới thu học sinh, độ tín nhiệm đạt tới bốn mươi. Bất quá, các ngươi không thể lấy thân phận bây giờ thu đồ đệ, nếu không, khẳng định không ít người nguyện ý bái tại môn hạ, độ tín nhiệm cũng rất dễ dàng đạt tới!”"),t("br"),t("br"),n._v("Chúc trưởng lão nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hai người gật đầu."),t("br"),t("br"),n._v("Mặc dù không muốn thừa nhận, đây cũng là thực sự."),t("br"),t("br"),n._v("Hai người bốn quan toàn qua, cơ bản tương đương khóa lại nhị tinh danh sư ghế, lúc này muốn thu học sinh, khẳng định có vô số người chen chúc mà đến, muốn cho một hai người độ tín nhiệm đạt tới bốn mươi, thực sự quá đơn giản."),t("br"),t("br"),n._v("“Không thể lấy thân phận bây giờ, cái kia...”"),t("br"),n._v("Mạc Hoằng Nhất đối khảo hạch nhị tinh danh sư quy củ tựa hồ cũng biết không rõ ràng, nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Danh Sư đường sẽ cho các ngươi hai cái thân phận mới, cũng sẽ giúp các ngươi cải biến một cái bộ dáng, đưa đến Thiên Vũ học viện làm một cái bình thường lão sư. Thời gian mười ngày bên trong, nhờ vào thân phận mới, tuyển nhận học sinh, để hắn độ tín nhiệm đạt tới 40 coi như thông qua. Tại trong lúc này, không thể cho thấy thân phận, không thể liên hệ bất luận kẻ nào, nếu không... Khảo hạch thất bại!”"),t("br"),t("br"),n._v("Chúc trưởng lão thần thái nghiêm túc."),t("br"),t("br"),n._v("“Giấu diếm thân phận, đi làm phổ thông lão sư?”"),t("br"),t("br"),n._v("“Không thể cho thấy thân phận, không thể liên hệ bất luận kẻ nào?”"),t("br"),t("br"),n._v("Trương Huyền, Mạc Hoằng Nhất đều là sững sờ."),t("br"),t("br"),n._v("Hiển nhiên, đều không nghĩ tới khảo hạch nhị tinh danh sư cái này liên quan, quỷ dị như vậy."),t("br"),t("br"),n._v("Bất quá, suy nghĩ kỹ một chút, cũng là hiểu được."),t("br"),t("br"),n._v("Cầm giữ có danh sư thân phận, bản thân liền có thể khiến người ta độ tín nhiệm gia tăng, nếu quả thật lấy bản tôn bộ dáng đi thu học sinh, chỉ sợ không bao lâu, liền có thể khiến người ta khăng khăng một mực."),t("br"),t("br"),n._v("Nhất là Mạc Hoằng Nhất, thân là vương quốc đệ nhất thiên tài, cầm giữ khẳng định rất nhiều, chỉ cần tìm tới mấy cái độ tín nhiệm đạt tới bốn mươi còn không phải dễ như trở bàn tay?"),t("br"),t("br"),n._v("Một khi không cần thân phận, đổi thành một người khác, liền không đồng dạng."),t("br"),t("br"),n._v("Hết thảy lại lần nữa bắt đầu, nếu như vậy cũng có thể làm cho học sinh trong thời gian ngắn độ tín nhiệm đạt tới 40, mới chính thức có thể thể hiện ra dạy học năng lực, không thẹn cho nhị tinh danh sư xưng hào."),t("br"),t("br"),n._v("“Vừa rồi ta cố ý cùng đám người nói, các ngươi muốn ra ngoài làm nhiệm vụ, như vậy học viện coi như thêm ra hai vị lão sư, cũng sẽ không có người hoài nghi!”"),t("br"),t("br"),n._v("Chúc trưởng lão cười giải thích."),t("br"),t("br"),n._v("Hai người hiểu được."),t("br"),t("br"),n._v("Có hắn làm nền, có lẽ không bao lâu, toàn bộ vương thành người, đều biết bọn hắn muốn ra khỏi thành hoàn thành nhiệm vụ, như vậy coi như lẫn vào Thiên Vũ học viện, cũng sẽ không khiến cho gợn sóng."),t("br"),t("br"),n._v("Không có người hoài nghi, trước kia thân phận, tự nhiên cũng liền vô dụng."),t("br"),t("br"),n._v("Hết thảy lại lần nữa bắt đầu."),t("br"),t("br"),n._v("“Tốt, các ngươi hai người thân phận, Danh Sư đường sớm liền chuẩn bị thỏa đáng, có thể trải qua ở bất luận cái gì thẩm tra. Ngày mai buổi sáng, bởi vì mấy vị lão sư rời đi, Thiên Vũ học viện vừa vặn muốn công khai thông báo tuyển dụng lão sư, bổ sung máu mới. Các ngươi quá khứ nhận lời mời... Sớm nói một câu, học viện lão sư, bao quát viện trưởng, cũng cũng không biết các ngươi hai người thân phận, hết thảy chỉ có thể dựa vào bản thân. Mười ngày sau, mang theo tân thu học được về tới đây, chỉ phải tín nhiệm độ thông qua, coi như khảo hạch thành công, trực tiếp có thể ban phát nhị tinh danh sư huy chương, liền Đáp Vấn hội đều không cần kinh nghiệm!”"),t("br"),t("br"),n._v("Chúc trưởng lão nói, đưa tới hai bản thư tịch."),t("br"),t("br"),n._v("Tiếp nhận trong đó một bản, Trương Huyền lật ra nhìn sang."),t("br"),t("br"),n._v("“Liễu Trình, Thiên Vũ vương quốc Tuyền Hải thành giáo sư công hội khảo hạch thành công giáo sư bình thường, hai mươi bốn tuổi, tu vi Thông Huyền cảnh sơ kỳ, từng tại Tuyền Hải học viện dạy thay một năm...”"),t("br"),t("br"),n._v("Phía trên ghi lại là hắn muốn ngụy trang nhân vật, theo xuất sinh đến bây giờ cuộc đời, cơ hồ toàn bộ bao dung ở bên trong, không tính là thiên tài, cũng không tính quá yếu, miễn cưỡng thành vì lão sư, trên người không có quá nhiều hào quang, tự nhiên cũng không có quá nhiều lỗ thủng."),t("br"),t("br"),n._v("Rất mau đem quyển sách lật hết, cái thân phận này toàn bộ rõ ràng trong lòng."),t("br"),t("br"),n._v("Không thể không nói, Danh Sư đường làm được chuẩn bị vẫn là thật chu toàn, vị này Liễu Trình là thật tồn tại, coi như xem kỹ, cũng có thể tra đi ra. Chỉ bất quá, vị này Liễu lão sư hiện tại đi địa phương nào, ai cũng không biết."),t("br"),t("br"),n._v("Hẳn là cùng giáo sư công hội ký kết một loại hiệp nghị nào đó, vì phối hợp khảo hạch cố ý che giấu."),t("br"),t("br"),n._v("Quay đầu hướng Mạc Hoằng Nhất nhìn lại, chỉ gặp hắn cũng vừa đem thư tịch xem hết, chắc hẳn phía trên cũng viết hắn muốn ngụy trang thân phận, cuộc đời."),t("br"),t("br"),n._v("“Tốt, đây là Danh Sư đường mặt nạ da người, các ngươi mang lên, liền lại biến thành muốn ngụy trang cái kia, khảo hạch bắt đầu từ ngày mai tính giờ, trong mười ngày nhất định phải đem học sinh mang tới khảo thí, nếu không, cũng coi như thất bại!”"),t("br"),t("br"),n._v("Chúc trưởng lão lại đưa qua hai trương mặt nạ da người."),t("br"),t("br"),n._v("Trương Huyền tiếp nhận, thả ở trên mặt, lập tức cảm thấy một trận nhẹ nhàng khoan khoái, mặt nạ trong nháy mắt tan vào làn da, sờ không tới cũng nhìn không thấy."),t("br"),t("br"),n._v("Nhìn không thấy mình bây giờ bộ dáng, chẳng qua nhìn về phía Mạc Hoằng Nhất, lập tức phát hiện, đã cùng vừa rồi hoàn toàn khác biệt, giống như là biến thành người khác, thậm chí liền trước đó khí tức, đều không cảm thấy được."),t("br"),t("br"),n._v("Không hổ là Danh Sư đường đồ vật, quả nhiên lợi hại!"),t("br"),t("br"),n._v("“Hôm nay còn có một đêm thời gian chuẩn bị, có cái gì muốn lời nhắn nhủ sự tình, sớm thông báo một chút. Bất quá, không thể nói cho bất luận kẻ nào các ngươi đi địa phương nào, ngụy trang người nào!”"),t("br"),t("br"),n._v("Toàn bộ nói xong, Chúc trưởng lão cuối cùng nói."),t("br"),t("br"),n._v("Hai người gật đầu."),t("br"),t("br"),n._v("Nếu là khảo hạch nhị tinh danh sư cửa ải, tự nhiên không hy vọng bởi vì vì nguyên nhân khác từ đó làm cho bỏ quyền."),t("br"),t("br"),n._v("Đối với loại này khảo hạch, Trương Huyền cũng không phản đối, ngược lại kích động."),t("br"),t("br"),n._v("Vừa thật mong muốn đi Thiên Vũ học viện thu mấy cái học sinh, nghĩ biện pháp thu hoạch được cảm kích, tạo ra kim sắc trang sách, cái khảo hạch này, cũng coi là thỏa mãn tâm nguyện."),t("br"),t("br"),n._v("“Chúc trưởng lão, ta muốn thấy nhìn liên quan tới danh sư thư tịch, không biết ở nơi nào, hiện tại có thể đi sao?”"),t("br"),t("br"),n._v("Tại đi Thiên Vũ học viện trước, hắn muốn nhìn Danh Sư đường thư khố, thuận tiện tìm xem liên quan tới thiên nhân Khổng sư tin tức, cùng võ giả bát trọng Tông Sư cảnh bí tịch."),t("br"),t("br"),n._v("Tại Hồng Liên thành, Tông Sư cảnh bí tịch đã sưu tập không ít, hiện tại càng là đột phá đến cảnh giới này, chỉ cần lại tìm mấy trăm quyển, liền có thể hình thành Thiên Đạo công pháp, thuận lợi tu luyện."),t("br"),t("br"),n._v("Dù sao có mười ngày thời gian, chỉ cần sớm tìm được đầy đủ bí tịch, liền có thể thừa dịp trong khoảng thời gian này, đem tu vi đột phá đến Tông sư đỉnh phong."),t("br"),t("br"),n._v("“Muốn nhìn thư tịch? Tàng thư khố ở bên kia, bất quá, ngươi bây giờ chỉ là nhất tinh danh sư, chỉ có thể quan sát nhất tinh thư khố, còn chưa có tư cách tiến vào nhị tinh thư khố!”"),t("br"),t("br"),n._v("Chúc trưởng lão tiện tay một chỉ."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền lên tiếng, nhấc chân hướng Danh Sư đường nhất tinh thư khố đi tới."),t("br"),t("br"),n._v("(Mới một quyển bắt đầu, cầu phiếu đề cử!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-324"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-326"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);