(window.webpackJsonp=window.webpackJsonp||[]).push([[155],{508:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1116-sua-chua-cong-phap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1116-sua-chua-cong-phap"}},[n._v("#")]),n._v(" Chương 1116: Sửa chữa công pháp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Có tư cách đi vào nơi này, đột phá phía ngoài tâm ma đại trận, cơ bản đều là Chiến sư đường cao tầng, mục đích đúng là vì cái này Lưu Ly Luyện Tâm kinh mà tới... Hỏi có hứng thú hay không, chỉ là thuận miệng nói, cho rằng đối phương tất nhiên sẽ đáp ứng, lão giả nằm mơ đều không có nghĩ đến... Trực tiếp cự tuyệt!"),t("br"),t("br"),n._v("Không hứng thú, ngươi xông trận pháp, chạy đến nơi đây làm gì?"),t("br"),t("br"),n._v("Đùa nghịch ta chơi đâu?"),t("br"),t("br"),n._v("“Đây chính là Tâm điện tuyệt học tối cao, vô thượng bí tịch... Thật không muốn học?” Có chút chần chờ, lão giả hỏi."),t("br"),t("br"),n._v("“Không muốn!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn hiện tại không có tâm ma, mà bộ công pháp này, tu luyện tới đại thành, chỉ có thể giảm bớt tâm ma số lượng, càng học càng kém, trừ phi điên rồi, nếu không, đồ đần mới học!"),t("br"),t("br"),n._v("“Vậy ngươi... Làm gì xông trận?” Lão giả không vui."),t("br"),t("br"),n._v("“Ta tưởng là nơi này có tâm ma, có thể tăng lên tâm cảnh tu vi, tới rèn luyện một chút, kết quả... Tâm ma quá kém, hoàn toàn không có hiệu quả, làm hại ta cao hứng hụt một hồi, nói thật, đang cảm thấy có chút mất hứng!”"),t("br"),t("br"),n._v("Trương Huyền than thở, vẻ mặt cô đơn."),t("br"),t("br"),n._v("Sớm biết là thấp như vậy đẳng cấp đại trận, liền không tiến vào, lãng phí thời gian."),t("br"),t("br"),n._v("Bất quá, cũng không tính đến không, thu phục mấy trăm đầu tâm ma, lại thành công rõ Ngoan Nhân một phần khác vị trí, đối với về sau, cũng có cực lớn trợ giúp."),t("br"),t("br"),n._v("“Quá kém? Mất hứng?”"),t("br"),t("br"),n._v("Kìm nén sắc mặt thấu đỏ, lão giả thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Cái tâm ma này đại trận, là hắn năm đó một lần cơ duyên lấy được, có thể đưa tới tu luyện giả trong cơ thể “Ác”, “Buồn”, “Tổn thương”, “Hoảng sợ” rất nhiều tâm tình tiêu cực, cũng tăng thêm lợi dụng, để cho người ta không tự giác ở trong đó trầm luân."),t("br"),t("br"),n._v("Có thể kháng qua, tâm cảnh đều gia tăng thật lớn, không kháng nổi, thời gian nhất định phía trong, trận pháp cũng sẽ ngừng, sẽ không đả thương đến căn bản."),t("br"),t("br"),n._v("Nghe khảo hạch đơn giản, nhưng tâm ma một khi xuất hiện, muốn vượt qua thực sự quá khó khăn, theo kỳ trước Chiến sư đường đường chủ trăm chẳng qua năm... Liền có thể nhìn ra độ khó, trước mắt thanh niên này ngược lại tốt, nói thẳng quá kém..."),t("br"),t("br"),n._v("Nhìn bộ dáng, vẻ mặt ghét bỏ..."),t("br"),t("br"),n._v("Chê ngươi muội ah, ngươi thật sự là tới khảo hạch?"),t("br"),t("br"),n._v("“Cái tâm ma này đại trận, tại chính thức trong mắt cường giả, quả thực không tính là gì, nhưng... Thực lực của ngươi, chỉ có Thánh vực nhất trọng, hơn nữa đợi như thế thời gian mới phá trận, hẳn là cũng không dễ dàng như vậy đi!”"),t("br"),t("br"),n._v("["),t("br"),n._v("trｕyen cua tui | Net ]  Lão giả mang theo không vui."),t("br"),t("br"),n._v("Cái tên này theo tiến vào trận pháp đến bây giờ, đã trải qua ít nhất hơn mười phút, mặc dù xem như rất nhanh, nhưng cũng tuyệt đối không hắn nói nhẹ nhàng như vậy."),t("br"),t("br"),n._v("Giả trang cái gì lão sói vẫy đuôi?"),t("br"),t("br"),n._v("“Làm sao? Cảm thấy ta ăn nói bừa bãi?”"),t("br"),t("br"),n._v("Thấy hắn biểu lộ, Trương Huyền lắc đầu, than thở một tiếng: “Ngươi trận pháp này, khó coi, quả thực không dùng được! Không tin, ta hiện tại liền nói cho ngươi nghe một chút, trận pháp này mặc dù có thể đưa tới người tâm ma, nhưng cần đi đến trung tâm trận pháp mới có thể hoàn thành, này bằng với cho tu luyện giả tâm lý ra hiệu ngầm, tiếp xuống kinh nghiệm... Đều là giả, chủ quan có loại ý nghĩ này, uy lực tự nhiên muốn yếu đi rất nhiều... Đây là vấn đề thứ nhất!”"),t("br"),t("br"),n._v("“Cái này...” Lão giả sững sờ, nói không ra lời."),t("br"),t("br"),n._v("Cái này đích xác là trận pháp thiếu hụt."),t("br"),t("br"),n._v("Bất quá, tâm ma một khi bị dẫn ra, cả người liền sẽ rơi vào hỗn loạn, cho dù chủ quan có ý nghĩ này, cũng rất khó hoài nghi."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, là không có người sẽ để ý, trước mắt vị này nhưng một ngụm nói ra... Chẳng lẽ, thật không phải khoác lác, mà là thật cảm thấy trận pháp quá kém?"),t("br"),t("br"),n._v("“Trận pháp sinh ra tâm ma, thủ đoạn công kích quá duy nhất, sẽ chỉ tìm công pháp, trên việc tu luyện sơ hở, tiến hành mê hoặc, ngộ nhỡ vượt quan người, đối tu luyện công pháp, nắm giữ tuyệt đối tự tin, uy lực khẳng định còn có thể yếu đi rất nhiều... Đây là vấn đề thứ hai!”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Lão giả lần nữa cứng ngắc."),t("br"),t("br"),n._v("Tâm ma đại trận bản thân liền khó mà bố trí, xuất hiện thiếu hụt cũng rất bình thường, khuyết điểm không che lấp được ưu điểm, cái tên này bị tâm ma vây quanh, còn có rảnh rỗi tìm thiếu sót..."),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("“Đại trận này, trận pháp dấu vết quá nồng, tâm ma quá có vẻ mặt hóa, như vậy xấu xí, ảnh hưởng cảm nhận, còn có người nào tâm tình cùng hắn chơi liều? Khả năng hận không thể tại chỗ đem hắn đánh nổ đi! Hẳn là để nó biến hóa thành người mình quen, hiệu quả liền sẽ càng tốt hơn... Đây là vấn đề thứ ba!”"),t("br"),t("br"),n._v("“... Đây là vấn đề thứ tư!”"),t("br"),t("br"),n._v("“... Đây là vấn đề thứ năm!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“... Đây là cái thứ mười vấn đề, một cái không lớn trận pháp, xuất hiện nhiều như vậy thiếu hụt cùng sơ hở, chẳng lẽ không phải quá kém?”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Trương Huyền bĩu môi."),t("br"),t("br"),n._v("Trận pháp có thể đưa tới người tâm ma, để cho người ta phòng bất chính phòng, nhưng mà, thiếu hụt cũng rất lớn."),t("br"),t("br"),n._v("Vấn đề lớn nhất chính là đại nhập cảm không tốt!"),t("br"),t("br"),n._v("Lấy ma quỷ khuôn mặt xuất hiện, chẳng phải lập tức để người ta biết ngươi chính là tâm ma?"),t("br"),t("br"),n._v("Khách hàng thể nghiệm cảm giác quá kém, ảnh hưởng nghiêm trọng cảm quan."),t("br"),t("br"),n._v("Chân chính lợi hại tâm ma, lại biến thành trong lòng ngươi tiếc nuối hoặc là thiếu hụt, lấy để ngươi không cách nào phân biệt phương thức xuất hiện... Đây mới gọi là đáng sợ."),t("br"),t("br"),n._v("Tỷ như, những này tâm ma, nếu không phải xấu như vậy, mà là biến thành Lạc Nhược Hi bộ dạng, bản thân làm không cẩn thận cũng sẽ rơi vào trong đó, khó mà tự kềm chế."),t("br"),t("br"),n._v("“Cái này, cái này...”"),t("br"),t("br"),n._v("Lão giả vẻ mặt trắng bệch, vốn là có chút phai mờ ý niệm, lay động cũng liền càng lớn, tựa như lúc nào cũng sẽ sụp đổ."),t("br"),n._v(" Hắn là trận pháp bố trí người, tự nhiên rõ trận pháp thiếu hụt cùng sơ hở, đối phương nói mười đầu, thế mà một cái đều không có sai!"),t("br"),t("br"),n._v("Nói cách khác, đối phương không chỉ thông qua được đại trận, còn đem trận pháp bản chất nhìn rõ rõ ràng ràng."),t("br"),t("br"),n._v("Khó trách đã trải qua hơn mười phút... Nhìn tới, đối phương không phải đối kháng tâm ma, mà là tìm trận pháp thiếu hụt cùng vấn đề."),t("br"),t("br"),n._v("Ngắn như vậy thời gian, tìm ra nhiều như vậy... Nhãn lực quá tốt đi mất!"),t("br"),t("br"),n._v("“Nhưng có... Phương pháp giải quyết?”"),t("br"),t("br"),n._v("Không dám tiếp tục xem thường, lão giả thận trọng hỏi."),t("br"),t("br"),n._v("“Muốn giải quyết triệt để, rất khó, liền xem như ta cũng rất không có khả năng làm được, nhưng mà, để hắn hơi tăng lên chút uy lực vẫn là có thể... Chỉ cần trong đó tăng thêm một cái huyễn trận, để đi vào người tham gia khảo hạch, còn không có phát hiện vấn đề, liền rơi vào huyễn cảnh, sau đó lại dẫn phát tâm ma, uy lực liền sẽ gia tăng không ít...”"),t("br"),t("br"),n._v("Suy nghĩ một chút, Trương Huyền ngón tay trên không trung một điểm, chân khí huyễn hóa thành một cái hình vẽ, cùng trước đó gian phòng giống nhau như đúc."),t("br"),t("br"),n._v("Lần nữa một điểm, gian phòng bốn phía lập tức xuất hiện mấy trăm cái điểm sáng."),t("br"),t("br"),n._v("Thư họa sư hư không thành giống thủ đoạn."),t("br"),t("br"),n._v("“Tại đây chút điểm sáng chỗ bố trí một cái huyễn trận, hai đại trận pháp dung hợp, uy lực liền không đồng dạng...” Trương Huyền giải thích."),t("br"),t("br"),n._v("Hắn đối với trận pháp lý giải sớm đã đạt đến thất tinh đỉnh phong, thậm chí có thể so với bát tinh sơ kỳ, lại đã trải qua Khưu Ngô cung, dung hợp trận pháp mặc dù phức tạp, đối với hắn mà nói, không tính quá khó."),t("br"),t("br"),n._v("Huống chi, chỉ là tăng lên cái tâm ma này đại trận một bộ phận uy lực, lại không tăng lên quá nhiều."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Có thể bố trí ra tâm ma đại trận, vị lão giả này đối với trận pháp lý giải, cũng cực kỳ cao thâm, nhìn một hồi, thân thể chấn động, tràn đầy không thể tưởng tượng nổi."),t("br"),t("br"),n._v("Trước mắt cái này bày trận chi pháp, mặc dù thiên mã hành không, không bám vào một khuôn mẫu, nhưng vừa vặn có thể bù đắp tâm ma đại trận rất nhiều sơ hở, để uy lực của nó gia tăng thật lớn."),t("br"),t("br"),n._v("“Thụ giáo...”"),t("br"),t("br"),n._v("Bái một cái, lão giả vui lòng phục tùng."),t("br"),t("br"),n._v("Trước đó hoài nghi, không những tan thành mây khói, còn phục sát đất."),t("br"),t("br"),n._v("Trước mắt thanh niên này, tuổi tác không lớn, đối với trận pháp lý giải, nhưng so với hắn còn cao minh hơn, mấu chốt nhất là, tâm cảnh mạnh mẽ, tâm ma đại trận đều không thể ảnh hưởng, thật không nghĩ tới, hậu thế bên trong, thế mà xuất hiện lợi hại như thế thiên tài."),t("br"),t("br"),n._v("“Không cần khách khí!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Hắn chẳng qua là cảm thấy đại trận này không ra hồn, thuận miệng nói, đối phương thừa nhận, cũng không cần phải đúng lý không khiến người ta."),t("br"),t("br"),n._v("“Đây là Lưu Ly Luyện Tâm kinh công pháp bí tịch, cho dù ngươi không hứng thú, đã thông qua được, dựa theo ước định cũng phải cấp ngươi...” Lão giả ngón tay một điểm, trước mắt ánh sáng lóe lên, một cái công pháp bí tịch, xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Nhìn một cái, Trương Huyền ghi vào trong óc, lập tức nhíu nhíu mày: “Bộ công pháp này, đối tâm cảnh yêu cầu cực cao ah!”"),t("br"),t("br"),n._v("Trước mắt luyện tâm pháp quyết, đối tâm cảnh yêu cầu cực cao, chỉ có đạt tới nhất định trình độ mới có thể tu luyện, nếu không, không những không luyện được, còn có thể dẫn tới tâm ma, cuối cùng dẫn đến tu vi giảm lớn, thậm chí còn có thể tinh thần rối loạn, biến thành tên điên."),t("br"),t("br"),n._v("Có thể có cao như vậy tâm cảnh người, bản thân liền ít đi, khó trách truyền thụ cái công pháp còn cần khảo hạch."),t("br"),t("br"),n._v("Muốn học tập, chỉ có thể thực lực đầy đủ, nếu không, tiết lộ ra ngoài, không những không thể để cho để tiến bộ, còn có thể hại người."),t("br"),t("br"),n._v("“Rõ!” Lão giả gật đầu."),t("br"),t("br"),n._v("“Yêu cầu cao như vậy tâm cảnh công pháp, rất khó phổ cập, như vậy đi, ta giúp ngươi sửa chữa một chút, để người bình thường cũng có thể thuận lợi tu luyện, ngươi xem coi thế nào?”"),t("br"),t("br"),n._v("Trầm tư một chút, Trương Huyền nói."),t("br"),t("br"),n._v("Đổi lại những công pháp khác, liền xem như hắn cũng bất lực, mà cái này khác biệt."),t("br"),t("br"),n._v("Cái gọi là lưu ly luyện tâm quyết, thật ra thì liền là đem nguyên thần cùng linh hồn tu luyện như lưu ly, óng ánh long lanh, cùng Khải Linh sư ôn nhuận nguyên thần có hiệu quả như nhau hiệu quả."),t("br"),t("br"),n._v("Trầm tư thời điểm, đem cái này pháp quyết cùng Khải Linh sư rất nhiều công pháp dung hợp lại cùng nhau, mặc dù không có hình thành Thiên Đạo công pháp, nhưng cũng phá vỡ, chỉ cần cường giả mới có thể tu luyện giới hạn."),t("br"),t("br"),n._v("Xem như đền bù cái này lớn nhất thiếu hụt."),t("br"),t("br"),n._v("“Người bình thường cũng có thể luyện?” Lão giả cứng đờ."),t("br"),t("br"),n._v("Năm đó vì nhận được môn công pháp này, hắn chịu nhiều đau khổ, mục đích chỉ là vì để Chiến sư, tăng lên sức chiến đấu."),t("br"),t("br"),n._v("Đáng tiếc, công pháp quá mức rườm rà, chỉ có thể để tâm cảnh cực cao người tu luyện, vẫn cảm thấy vô cùng tiếc nuối, nhưng cũng không có cách nào."),t("br"),t("br"),n._v("Nếu quả như thật người bình thường đều có thể tu luyện, như vậy toàn bộ Chiến sư đường thực lực, khẳng định sẽ lần nữa tăng lên một đoạn dài."),t("br"),t("br"),n._v("Nhân tộc an toàn, cũng tất nhiên thật to bảo hộ."),t("br"),t("br"),n._v("“Bộ công pháp này, lớn nhất thiếu hụt liền là trong quá trình tu luyện, sẽ xuất hiện đủ loại mê hoặc, định lực hơi kém, không có khả năng ngăn cản đi qua, từ đó làm cho, cánh cửa nhất định phải đề cao, thật ra thì điểm ấy, muốn giải quyết, cũng rất dễ dàng, vừa mới ta xem một lần, đã sửa chữa xong xuôi.”"),t("br"),t("br"),n._v("Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("“Sửa chữa xong xuôi? Ngươi chỉ nhìn một cái liền sửa chữa kết thúc?”"),t("br"),t("br"),n._v("Còn tưởng rằng đối phương nói sửa chữa, khả năng cần mấy năm, thậm chí mấy chục năm, trăm năm thời gian, không nghĩ tới, theo bản thân thả ra công pháp đến bây giờ, không đến nửa phút, đối phương không chỉ nhìn xong, còn sửa chữa kết thúc... Lão giả toàn thân rung động, khắp khuôn mặt là khó tin."),t("br"),t("br"),n._v("Vì có thể để cho người bình thường tu luyện, khi còn sống, hắn cũng hao tốn vô số tâm huyết, đều không có thành công... Trước mắt vị này chỉ nhìn lướt qua, đổi lại người khác liền công pháp nội dung đều không có thấy rõ ràng, cũng đã sửa chữa kết thúc..."),t("br"),t("br"),n._v("Liền xem như lúc trước Khổng sư, cũng chưa chắc có loại năng lực này đi."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền nhìn lại, khuôn mặt tràn đầy hòa ái: “Làm sao? Ngươi nếu muốn nhìn, ta hiện tại liền có thể viết một phần cho ngươi!”"),t("br"),t("br"),n._v("PS: Gửi cho bạn bè trường phong một quyển sách 《 mạc hậu 》. Chuyện xưa giới thiệu vắn tắt: Hắn kêu Lục Hi Ngôn, là đại danh đỉnh đỉnh “Phán Quan”, một bản Sinh Tử bộ, một nhánh câu hồn bút, “76” số uông giả Hán gian hồn phi phách tán, phân đất nguyên chợp mắt, cũng phải mở một con mắt."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1124"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1126"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);