(window.webpackJsonp=window.webpackJsonp||[]).push([[1550],{1905:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-317-cung-kinh-bach-thiem-【-hai-hop-mot-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-317-cung-kinh-bach-thiem-【-hai-hop-mot-】"}},[n._v("#")]),n._v(" Chương 317: Cung kính Bạch Thiềm 【 hai hợp một 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nhất tinh tàng thư khố, rộng rãi bao la, không biết trữ giấu bao nhiêu bản thư tịch, hàm cái không ít chức nghiệp cùng chỉ điểm bên trên kỹ xảo."),t("br"),t("br"),n._v("Tiến vào bên trong, Trương Huyền nhìn thoáng qua, cũng không chịu được chấn kinh."),t("br"),t("br"),n._v("Cái này tàng thư khố so trước đó vương quốc tàng thư khố càng thêm hùng vĩ, thư tịch càng nhiều, tri thức mặt cũng là càng rộng, nhân văn địa lý, man thú đan dược, bí cảnh bảo vật... Không chỗ không có, không chỗ không chứa."),t("br"),t("br"),n._v("“Nhiều như vậy sách, nhất định phải tại buổi sáng ngày mai trước đó xem hết...”"),t("br"),t("br"),n._v("Thở ra một hơi."),t("br"),t("br"),n._v("Lúc trước vương quốc tàng thư khố, hắn nhưng là nhìn ròng rã năm ngày năm đêm, bất quá, ngay lúc đó đọc sách thủ pháp quá chậm, hiện tại tốc độ coi như nhanh hơn không ít, muốn nửa ngày một đêm công phu toàn bộ xem hết, cũng là một kiện công trình vĩ đại."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Biết không có quá nhiều thời gian để hắn chần chờ, thân thể nhoáng một cái, lần nữa chạy ra."),t("br"),t("br"),n._v("Tu vi đạt tới Tông Sư cảnh, lại thêm tâm cảnh khắc độ gia tăng, không giống như trước, chạy một hồi liền không chịu nổi, có thể kiên trì càng lâu, tốc độ cũng càng nhanh."),t("br"),t("br"),n._v("Hoa lạp lạp lạp!"),t("br"),t("br"),n._v("Từng dãy thư tịch, được thu vào trong óc, thong thả tại tri thức trong hải dương, Trương Huyền quên đi thời gian."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bạch Thiềm y sư nội tâm là sụp đổ."),t("br"),t("br"),n._v("Khoảng cách Danh Sư đường rất gần phủ đệ tìm được, nhưng giá cả cũng quý dọa người, cơ hồ đem hắn đời này tiền kiếm được đều dán tiến vào."),t("br"),t("br"),n._v("Bất quá, cùng mạng sống so, những này vật ngoài thân không tính là gì."),t("br"),t("br"),n._v("“Trương Huyền thiếu gia tại Danh Sư đường, chẳng lẽ hắn cũng muốn khảo hạch danh sư?”"),t("br"),t("br"),n._v("Tìm xong phủ đệ, hắn cùng chịu trách nhiệm giám sát Tôn Cường hai người nhanh chân hướng Danh Sư đường phương hướng đi tới."),t("br"),t("br"),n._v("“Ừm, thiếu gia là lão gia học sinh, tự nhiên muốn khảo hạch danh sư...” Tôn Cường gật đầu: “Chúng ta nhanh lên, có lẽ còn có thể nhìn thấy thiếu gia biểu hiện ra tài hoa, một tiếng hót lên làm kinh người!”"),t("br"),t("br"),n._v("“Một tiếng hót lên làm kinh người?”"),t("br"),t("br"),n._v("Bạch Thiềm y sư lắc đầu: “Danh sư không phải dễ dàng như vậy thi, nhất là năm cái cửa ải, một cửa so một cửa khó, nhà các ngươi thiếu gia, mặc dù cũng rất lợi hại, nhưng ta cảm thấy vẫn là trẻ hơn một chút, thuận lợi thông qua có lẽ có thể, một tiếng hót lên làm kinh người, chỉ sợ cũng khó khăn! Dù sao, phía trên có cái Mạc Hoằng Nhất đè ép, tất cả ghi chép đều là hắn!”"),t("br"),t("br"),n._v("“Mạc Hoằng Nhất?” Tôn Cường nhìn qua."),t("br"),t("br"),n._v("Hôm qua vừa đến Thiên Vũ vương quốc, liền gặp được gia hỏa này, sau đó liền là tìm phủ đệ, cơ bản không chút nghỉ ngơi, đối với vị này siêu cấp đại thiên tài danh hào, chưa từng nghe qua."),t("br"),t("br"),n._v("“Đúng vậy a, Mạc Hoằng Nhất là chúng ta Thiên Vũ vương quốc đệ nhất thiên tài, nhất là tại khảo hạch danh sư thời điểm, liền phá kỷ lục, mệnh danh từ ngàn năm nay đệ nhất nhân!”"),t("br"),t("br"),n._v("Bạch Thiềm y sư ánh mắt lộ ra vẻ sùng bái: “Tâm cảnh khắc độ càng là đạt đến 6. 0 trở lên, cái thế vô song! Khương đường chủ từng nói rõ, muốn vượt qua hắn, trong vòng trăm năm chỉ sợ không người có thể làm đến.”"),t("br"),t("br"),n._v("“Tâm cảnh khắc độ 6. 0? Ngàn năm đệ nhất nhân?” Tôn Cường líu lưỡi."),t("br"),t("br"),n._v("Không nghĩ tới, vương quốc còn có như vậy một vị cao thủ."),t("br"),t("br"),n._v("Hai người vừa nói vừa đi về phía trước, rất mau tới đến Danh Sư đường trước cửa, chính muốn đi vào, chỉ thấy một tên hộ vệ đi tới."),t("br"),t("br"),n._v("“Bạch y sư...”"),t("br"),t("br"),n._v("Nhìn thấy hai người, hộ vệ nhãn tình sáng lên, gấp vội vàng nghênh đón."),t("br"),t("br"),n._v("“Diêu đội trưởng, ngươi cũng tại đây!” Bạch Thiềm y sư nhẹ gật đầu."),t("br"),t("br"),n._v("Chính là tối hôm qua vị kia Diêu đội trưởng."),t("br"),t("br"),n._v("Lúc này Diêu đội trưởng, không có tối hôm qua khí thế, trên mặt vẫn như cũ tràn đầy sưng đỏ, bất quá, ánh mắt lộ ra khó mà ngăn chặn vẻ khiếp sợ."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Đi tới trước mặt, đầu gối mềm nhũn quỳ rạp xuống đất: “Đa tạ Bạch y sư ân cứu mạng!”"),t("br"),t("br"),t("span",[n._v("  Truyện C"),t("br"),n._v("ủa Tuichấm Net"),t("br")]),n._v(" “Ân cứu mạng?” Bạch Thiềm y sư như hòa thượng sờ mãi không thấy tóc: “Diêu đội trưởng làm cái gì vậy, cớ gì nói ra lời ấy...”"),t("br"),t("br"),n._v("Bản thân lúc nào cứu hắn rồi?"),t("br"),t("br"),n._v("“Nếu không phải ngươi khuyên can, ta khả năng sẽ còn phạm hồ đồ, không biết trời cao đất rộng, tìm cơ hội trả thù Trương sư, là ngươi, để cho ta may mắn thoát khỏi tại khó...”"),t("br"),t("br"),n._v("Diêu đội trưởng cảm khái."),t("br"),t("br"),n._v("Làm công chúa hộ vệ, Trương Huyền khảo hạch danh sư quá trình, hắn tất cả đều xem ở trong mắt, dọa đến suýt chút nữa không có chết rồi."),t("br"),t("br"),n._v("Đối công chúa có nửa sư tình nghĩa, Khương đường chủ sư huynh, có một cái vượt qua tam tinh lợi hại lão sư, tức sẽ thành nhị tinh danh sư..."),t("br"),t("br"),n._v("Bất kỳ một cái nào thân phận, bối cảnh, đều không phải là hắn một cái nho nhỏ hộ vệ có thể đắc tội."),t("br"),t("br"),n._v("Nếu không phải hôm qua Bạch Thiềm y sư khuyên can, thật ngu đột xuất đi tìm người ta phiền phức, chỉ sợ bị tại chỗ chụp chết, đều không có người thay hắn tiếc hận."),t("br"),t("br"),n._v("Quá dọa người!"),t("br"),t("br"),n._v("“Trương sư?”"),t("br"),t("br"),n._v("Bạch Thiềm vẫn là không biết rõ: “Ngươi nói là... Trương Huyền thiếu gia?”"),t("br"),t("br"),n._v("“Đúng vậy a, Trương sư quá lợi hại, khảo hạch nhất tinh danh sư phá vỡ Mạc Hoằng Nhất tất cả ghi chép, thậm chí khảo hạch nhị tinh danh sư cũng đem tất cả ghi chép đánh vỡ, ta vậy mà có mắt không tròng, muốn muốn trả thù loại thiên tài này, thật sự là muốn chết rồi...”"),t("br"),t("br"),n._v("“Đánh vỡ Mạc Hoằng Nhất ghi chép? Đến cùng xảy ra chuyện gì?” Bạch Thiềm y sư mộng."),t("br"),t("br"),n._v("“Ngươi không biết? A, ngươi vừa rồi không tại Danh Sư đường, không biết rất bình thường, là như vậy, ta hôm nay phụng mệnh bảo hộ công chúa...”"),t("br"),t("br"),n._v("Diêu đội trưởng liền tranh thủ bản thân nhìn thấy nói một lần."),t("br"),t("br"),n._v("“Độ tín nhiệm 85, phụ tu sảnh 130, khôi lỗi điện một chiêu bại địch... Hiện trường đột phá Tông sư, khảo hạch nhị tinh danh sư bốn quan thông qua, Khương đường chủ sư huynh...”"),t("br"),t("br"),n._v("Nghe xong đối phương giảng thuật, Bạch Thiềm y sư con mắt xích hồng, thân thể run rẩy, nhanh muốn khóc."),t("br"),t("br"),n._v("Mình rốt cuộc đắc tội một cái dạng gì quái vật?"),t("br"),t("br"),n._v("Vừa mới nói muốn muốn phá mất Mạc Hoằng Nhất ghi chép rất khó, kết quả, không chỉ phá mất, còn cường thế đánh mặt..."),t("br"),t("br"),n._v("Vốn còn nghĩ, chỉ cần đối phương đem hắn kịch độc trong cơ thể giải hết, liền lập tức trở mặt, dựa vào của hắn nhân mạch, đem tiểu tử này hảo hảo thu thập một chầu, nghe được thế mới biết, cùng đối phương so sánh, cái kia chút nhân mạch, chó má không phải, lông cũng không tính là một cái."),t("br"),t("br"),n._v("Nhị tinh danh sư, tại tăng thêm thân là Khương đường chủ sư huynh, chỉ sợ ra lệnh một tiếng, coi như quốc vương bệ hạ, cũng muốn tuân theo không dám vi phạm."),t("br"),t("br"),n._v("Buồn cười hắn còn sắc đảm bao thiên đùa giỡn người ta học sinh..."),t("br"),t("br"),n._v("Thật sự là ông cụ thắt cổ... Chán sống!"),t("br"),t("br"),n._v("“Trương sư... Hiện tại người đâu?”"),t("br"),t("br"),n._v("Vẻ mặt cầu xin, nhịn không được hỏi."),t("br"),t("br"),n._v("“Danh Sư đường cho hắn nhiệm vụ gì, khả năng trong thời gian ngắn muốn rời khỏi vương thành!” Diêu đội trưởng suy nghĩ một chút, liền ôm quyền: “Lòng cảm kích, ghi ở trong lòng, về sau mời ngươi ăn cơm, ta còn có việc, cũng không muốn nói nhiều!”"),t("br"),t("br"),n._v("Nói xong, quay người rời đi."),t("br"),t("br"),n._v("“Ta liền nói thiếu gia khảo hạch khẳng định sẽ một tiếng hót lên làm kinh người, sẽ không sai đi!” Tôn Cường đầu lâu nâng lên."),t("br"),t("br"),n._v("“Vâng, vâng!”"),t("br"),t("br"),n._v("Bạch Thiềm y sư nghĩ tới điều gì, nhãn tình sáng lên: “Trương sư đã có nhiệm vụ muốn làm, học sinh của hắn có phải hay không cũng ở nơi đây, vừa dễ dàng tiếp vào phủ đệ... Hảo hảo hầu hạ!”"),t("br"),t("br"),n._v("Nghe được vị này Trương Huyền tất cả sự tích, không còn vi phạm tâm tư, trước đó bởi vì đùa giỡn Trương sư học sinh, từ đó sinh ra vết rách, có lẽ có thể thừa cơ tu bổ một cái."),t("br"),t("br"),n._v("“Ách, tốt a!”"),t("br"),t("br"),n._v("Tôn Cường gật gật đầu, hai người đi vào Danh Sư đường."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Lưu sư, Trương sư để ta nói cho các ngươi biết, trước tìm địa phương nghỉ ngơi, thay chiếu cố mấy cái học sinh, hắn khảo hạch nhị tinh danh sư khả năng còn phải cần một khoảng thời gian!”"),t("br"),t("br"),n._v("Danh Sư đường bên trong, Lưu Lăng trước mặt, một cái học đồ đi tới."),t("br"),t("br"),n._v("“Đa tạ thông truyền!”"),t("br"),t("br"),n._v("Học đồ sau khi đi, Lưu Lăng nhìn về phía đám người: “Đã sư huynh còn phải cần một khoảng thời gian, ta xem chúng ta vẫn là trước tìm địa phương ở lại đi!”"),t("br"),t("br"),n._v("“Tốt, chỉ là... Ở nơi nào? Thiên Vũ vương thành chúng ta lại không quen, vạn nhất ở quá lệch, lão sư trở về tìm không thấy làm cái gì?” Triệu Nhã nhịn không được nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lưu Lăng cũng mày nhăn lại: “Thực sự không được, lại ở trước đó địa phương...”"),t("br"),t("br"),n._v("Nói còn chưa dứt lời, liền nghe đến một người gọi tiếng vang lên."),t("br"),t("br"),n._v("“Lưu sư, chư vị thiếu gia, tiểu thư, ta đã chuẩn bị xong phủ đệ, mời đi theo ta đi...”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy hôm qua đùa giỡn Triệu Nhã, như là Thiên Vương lão tử Bạch Thiềm y sư, một mặt cung kính, nô bộc đồng dạng, khắp khuôn mặt là khiêm tốn ý cười, tựa hồ sợ chọc bọn hắn không vui."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("“Đây là tình huống gì?”"),t("br"),t("br"),n._v("Lưu Lăng đám người đồng thời ngây người."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("“Có người liên tiếp phá mất Mạc Hoằng Nhất ghi chép?”"),t("br"),t("br"),n._v("“Khương đường chủ sư huynh? Lão sư hắn là một vị vượt qua tam tinh danh sư?”"),t("br"),t("br"),n._v("“Khảo hạch nhị tinh danh sư thành công?”"),t("br"),t("br"),n._v("Danh Sư đường chuyện phát sinh, như vòi rồng, xoay tròn mà ra, trong khoảnh khắc liền truyền tới vô số đại gia tộc tộc trưởng trong tai."),t("br"),t("br"),n._v("Danh sư, nước căn bản."),t("br"),t("br"),n._v("Nhị tinh danh sư càng là Thiên Vũ vương quốc đỉnh phong nhất nhân vật, khảo hạch thành công, hơn nữa phá vỡ thiên tài Mạc Hoằng Nhất tất cả ghi chép, tin tức quá mức kinh người, muốn che giấu, đều không che giấu được."),t("br"),t("br"),n._v("“Vương quốc cảnh nội lại còn có một nhân vật như vậy? Người tới, tuyên chỉ, phong vị này Trương sư vì Huyền Vương, có thể tự do xuất nhập hoàng cung, không cần triệu kiến...”"),t("br"),t("br"),n._v("Trong vương cung cao lớn nhất trên bảo tọa, một người trung niên xem hết trong tay tấu, ánh mắt lóe lên, lập tức tuyên bố."),t("br"),t("br"),n._v("Bất quá, nói còn chưa dứt lời, gấp vội khoát khoát tay: “Trước không vội, những điều kiện này quá mức dung tục, ngươi phái người thời khắc trông coi Danh Sư đường, một khi Trương sư xuất hiện, lập tức nói cho ta biết, ta muốn đích thân bái kiến!”"),t("br"),t("br"),n._v("“Tự mình bái kiến? Bệ hạ, cái này...”"),t("br"),t("br"),n._v("Nghe được quyết định của hắn, tất cả đại thần tất cả đều sững sờ."),t("br"),t("br"),n._v("Bất kể nói thế nào, đều là nhất đẳng vương quốc bệ hạ, tự mình bái kiến một cái không đủ hai mươi thanh niên... Có phải hay không có chút nhỏ nói thành to?"),t("br"),t("br"),n._v("“Không cần nói, ý ta đã quyết!”"),t("br"),t("br"),n._v("Trung niên nhân khẽ nói."),t("br"),t("br"),n._v("Vị này Trương Huyền mặc dù nhìn hiện tại chỉ là nhị tinh danh sư sơ kỳ, thậm chí còn có quan hệ kẹt không có khảo hạch xong, nhưng bằng mượn tiềm lực, trở thành tam tinh, thậm chí cao hơn, chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("Loại người này, đi đến bất kỳ địa phương nào đều sẽ là xưng bá một phương siêu cường tồn tại, coi như hắn là quốc vương, cũng không dám buông lỏng."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Đám người đành phải gật đầu."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Thành tích khá như vậy, khảo hạch nhị tinh danh sư, tuyệt đối là ván đã đóng thuyền, nhanh lên chuẩn bị cho ta một phần hậu lễ, chờ vị này Trương sư vừa xuất hiện, liền cho ta đưa qua...”"),t("br"),t("br"),n._v("Vương thành một tòa rộng lớn phủ đệ, một người trung niên cũng biết tin tức, vỗ bàn một cái, làm ra quyết định: “Không, ta muốn đích thân đưa qua!”"),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Trước mắt hộ vệ gật gật đầu."),t("br"),t("br"),n._v("“Đúng rồi, thiếu gia tìm tới không?”"),t("br"),t("br"),n._v("Phân phó xong, trung niên nhân nhìn qua."),t("br"),n._v("“Hồi bẩm lão gia, thiếu gia tìm được, bị người dập ở ngoài thành trên cây, trần như nhộng, nhận hết nhục nhã...” Hộ vệ chần chờ một chút, ôm quyền bẩm báo."),t("br"),t("br"),n._v("“Cái gì? Dám đem nhi tử ta cởi y phục xuống, treo ở trên cây? Đến cùng là ai? Muốn chết...”"),t("br"),t("br"),n._v("Sắc mặt tái xanh, trung niên nhân cả người tựa như sắp thiêu đốt."),t("br"),t("br"),n._v("Đường đường vương thành một trong tam đại gia tộc, gia chủ con trai trưởng bị người như vậy, quả thực chẳng khác nào không nhìn bọn hắn Quý gia uy nghiêm, ngang nhiên khiêu khích."),t("br"),t("br"),n._v("“Thiếu gia không nói... Thuộc hạ không biết!”"),t("br"),t("br"),n._v("Thấy gia chủ nổi giận, hộ vệ giật nảy mình, liền vội vàng khom người."),t("br"),t("br"),n._v("“Đem hắn mang cho ta tiến đến!”"),t("br"),t("br"),n._v("Trung niên nhân bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Đúng!” Hộ vệ quay người đi ra ngoài, chỉ chốc lát, một thanh niên mặt mũi tràn đầy sưng đỏ đi đến."),t("br"),t("br"),n._v("Nếu như Trương Huyền tại cái này, khẳng định có thể nhận ra, chính là cùng hắn tỷ thí thư hoạ nhất tinh thư hoạ sư, Quý Mặc công tử."),t("br"),t("br"),n._v("Lúc này Quý Mặc đâu còn có nửa điểm phong lưu phóng khoáng bộ dáng, toàn thân sưng đỏ, như là đầu heo, choàng một bộ y phục, nói là từ đâu ăn xin vừa trở về, cũng tuyệt đối có người tin tưởng."),t("br"),t("br"),n._v("“Cha, ngươi nhất định phải báo thù cho ta...”"),t("br"),t("br"),n._v("Vừa vào cửa, Quý Mặc công tử lập tức lệ rơi đầy mặt, quỳ rạp xuống đất, lên tiếng khóc lớn."),t("br"),t("br"),n._v("“Yên tâm đi, dám khiêu khích ta Quý gia uy nghiêm, để cho nhi tử ta chịu nhục, ta chẳng cần biết hắn là ai, liền xem như lão thiên gia, cũng muốn đâm thủng một mảnh!” Nhìn thấy nhi tử thảm trạng, trung niên nhân rít lên một tiếng, khí tức trên thân điên cuồng phát ra, như là nước sông, cuồn cuộn không thôi."),t("br"),t("br"),n._v("“Đa tạ cha thay ta làm chủ!”"),t("br"),t("br"),n._v("Nghe được phụ thân lời nói, Quý Mặc công tử hốc mắt đỏ lên."),t("br"),t("br"),n._v("“Nói, là ai đưa ngươi đánh thành như vậy? Ta hiện tại liền dẫn người tới, đem hắn chẻ thành nhân côn, báo thù cho ngươi!”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, trung niên nhân triển lộ ra tam đại gia tộc gia chủ uy nghiêm và khí chất."),t("br"),t("br"),n._v("“Là của ta mấy cái kia thuộc hạ, a, không, chủ mưu không phải bọn hắn, là một cái vừa mới khảo hạch thành công thư hoạ sư, là hắn không biết dùng thủ đoạn gì, để thuộc hạ của ta phản bội, đem ta treo ở trên cây, mất mặt xấu hổ, ta muốn hắn chết...”"),t("br"),t("br"),n._v("Quý Mặc công tử mặt mũi tràn đầy điên cuồng."),t("br"),t("br"),n._v("“Thư hoạ sư? Hừ, một cái hạ cửu lưu chức nghiệp, cũng dám tìm chúng ta Quý gia phiền phức, ta hiện tại liền đi tìm bọn họ hội trưởng, để hắn đem gia hỏa này giao ra!” Trung niên nhân hai mắt như điện, lộ ra hùng hồn Haki: “Chúng ta Quý gia khiêm tốn nhiều năm như vậy, cũng nên mạnh mẽ lên, làm cho tất cả mọi người tất cả xem một chút nội tình!”"),t("br"),t("br"),n._v("“Không sai...” Nghe được phụ thân lời nói, Quý Mặc công tử chỉ cảm thấy toàn thân nhiệt huyết sôi trào, hưng phấn đỏ ngầu cả mắt."),t("br"),t("br"),n._v("Quý gia, bao nhiêu năm không có xuất thủ, không ít người đã quên bọn hắn tồn tại."),t("br"),t("br"),n._v("Hiện tại có lẽ để bọn hắn biết, Thiên Vũ vương thành, Quý gia vẫn như cũ là vô thượng cự đầu, thư họa sư công hội coi như lợi hại, cũng bất quá hạ cửu lưu chức nghiệp một trong, còn có thể đem bọn hắn thế nào?"),t("br"),t("br"),n._v("“Đúng rồi, hại ngươi người tên gọi là gì?”"),t("br"),t("br"),n._v("Trung niên nhân nhìn qua."),t("br"),t("br"),n._v("“Hồi bẩm cha, gia hỏa này gọi Trương Huyền, vừa mới khảo hạch thư hoạ sư thành công, còn tự xưng cái gì thiên tài!” Quý Mặc công tử tròng mắt đỏ hoe: “Chờ đem hắn bắt tới, ta phải thật tốt chà đạp, để hắn muốn sống không được muốn chết không xong...”"),t("br"),t("br"),n._v("Ngay tại mặt mũi tràn đầy dữ tợn gào thét, đột nhiên thấy gian phòng an tĩnh lại, không có thanh âm, nhịn không được ngẩng đầu nhìn lại, chỉ thấy mới vừa rồi còn hăng hái cha tận mắt trừng mắt nhìn qua."),t("br"),t("br"),n._v("“Cha, thế nào...”"),t("br"),t("br"),n._v("Tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Nói còn chưa dứt lời, liền thấy một cái cự đại bàn chân bay tới, trung niên nhân rít lên một tiếng."),t("br"),t("br"),n._v("“Làm sao em gái ngươi, ngươi cái phá sản đồ chơi, làm sao không chết ở bên ngoài...”"),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Quý Mặc công tử còn không có kịp phản ứng, lập tức bị đạp bay rớt ra ngoài."),t("br"),t("br"),n._v("“Người tới, đem cái này nghiệt súc cho ta trói tốt, chờ lấy đưa cho Trương sư xin lỗi!”"),t("br"),t("br"),n._v("Trung niên nhân bờ môi run rẩy."),t("br"),t("br"),n._v("“Xin lỗi?”"),t("br"),t("br"),n._v("Quý Mặc công tử khóe miệng co giật, trước mắt biến thành màu đen, nhịn không được khóc."),t("br"),t("br"),n._v("Vừa rồi không còn nói muốn báo thù cho ta sao? Không còn nói muốn hiển lộ rõ ràng Quý gia uy nghiêm sao?"),t("br"),t("br"),n._v("Một cước đem ta đá bay, xin lỗi cái quỷ gì?"),t("br"),t("br"),n._v("Ai có thể nói cho ta biết... Đây rốt cuộc chuyện gì xảy ra?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đi qua nửa ngày thêm một đêm chạy, rốt cục đem Danh Sư đường nhất tinh thư khố bên trong tất cả tàng thư đều thu vào thư viện."),t("br"),t("br"),n._v("Lúc này Trương Huyền, cũng khắp khuôn mặt là rã rời."),t("br"),t("br"),n._v("Cường độ cao vận dụng tinh thần, dù là tâm cảnh khắc độ đạt đến 1 0.1, vẫn như cũ có chút không kiên trì nổi."),t("br"),t("br"),n._v("Ngồi tại nguyên chỗ khôi phục nửa canh giờ, cái này mới hoàn toàn làm dịu tới."),t("br"),t("br"),n._v("Tinh thần nội thị, tại trong thư viện liếc nhìn mà qua, nhịn không được nhướng mày."),t("br"),t("br"),n._v("“Thế mà không có liên quan tới Khổng sư giới thiệu, không có Tông Sư cảnh tu luyện bí tịch...”"),t("br"),t("br"),n._v("Đến tàng thư khố đọc sách, liền là muốn tìm tìm liên quan tới thiên nhân Khổng sư sự tích, thuận tiện tìm tới Tông Sư cảnh tu luyện bí tịch, kết quả... Hai thứ này, một cái đều không có."),t("br"),t("br"),n._v("“Hẳn là nhất tinh danh sư, còn không có tư cách tiếp xúc Khổng sư loại cấp bậc này nhân vật. Còn công pháp, nhất tinh cơ bản đều tại Thông Huyền cảnh, nửa bước Tông sư, cho dù có Tông Sư cảnh công pháp, cũng vô dụng...”"),t("br"),t("br"),n._v("Nghĩ một lát, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Xem ra hắn còn là nghĩ nhiều."),t("br"),t("br"),n._v("Nhất tinh danh sư, làm danh sư cái nghề nghiệp này tầng dưới chót nhất, là không có tư cách tiếp xúc rất nhiều bí mật, nhất là thiên nhân Khổng sư, mệnh danh vạn thế chi sư, danh sư đứng đầu, loại người này sự tích, làm sao có thể tùy tiện thả ở loại địa phương này, để cho người ta tuỳ tiện đọc qua?"),t("br"),t("br"),n._v("Mặc dù có, chỉ sợ cũng là nghe nhiều nên thuộc sự tình, không có khả năng ghi lại Tiên Thiên thai độc."),t("br"),t("br"),n._v("“Được rồi, mặc dù không có Tông Sư cảnh công pháp, Thông Huyền cảnh, nửa bước Tông Sư cảnh bí tịch, công pháp ngược lại là có không ít, có thể sửa sang một chút, cho Triệu Nhã đám người tu luyện...”"),t("br"),t("br"),n._v("Tông Sư cảnh công pháp không có, còn lại ngược lại là có không ít, mỗi một cái cấp bậc đều chứa đựng chí ít mấy ngàn bản khác biệt bí tịch, chỉ cần cho ra thời gian nhất định để hắn chỉnh lý, tất nhiên có thể một lần nữa làm ra một bộ thích hợp Triệu Nhã đám người tu luyện pháp quyết."),t("br"),t("br"),n._v("Xem hết công pháp, đem ánh mắt tập trung ở danh sư loại thư tịch bên trong."),t("br"),t("br"),n._v("Nhìn một hồi, rốt cuộc minh bạch tới."),t("br"),t("br"),n._v("Khó trách danh sư có thể trở thành tất cả chức nghiệp đứng đầu, hoàn toàn chính xác không tầm thường."),t("br"),t("br"),n._v("Một chút nhìn ra người khác thiếu hụt, công pháp nhược điểm, cũng không phải là trời sinh nhãn lực, mà là ban ngày kế ngày tôi luyện cùng cảm ngộ."),t("br"),t("br"),n._v("Căn cứ thư tịch bên trên ghi chép, phán định người khác tu luyện công pháp loại hình có hay không vấn đề phương pháp, liền có vài chục vạn loại nhiều, cùng y sư xem bệnh đồng dạng, nhất định phải nắm giữ những này, mới có thể phát giác đừng người không thể phát giác sự tình, từ đó giải quyết vấn đề, đạt được người khác tôn trọng."),t("br"),t("br"),n._v("“Này mười ngày vừa vặn cũng học tập cho giỏi một cái...”"),t("br"),t("br"),n._v("Ngụy trang danh sư, tuỳ tiện cho người khác chỉ điểm, hắn dựa vào là Thiên Đạo thư viện, đối chân chính danh sư kỹ năng, biết cũng không nhiều, vừa vặn này mười ngày khảo hạch, thừa dịp cơ nhiều học một chút, bổ sung một cái tri thức, để hắn vị này nhị tinh danh sư cũng có thể xứng đôi chút."),t("br"),t("br"),n._v("Quét xong liên quan tới danh sư thư tịch, lại đem hắn đều quét một lần."),t("br"),t("br"),n._v("Danh Sư đường bên trong thư tịch, bao dung chủng loại tuy nhiều, lại cũng chỉ tập trung ở mấy cái thường gặp chức nghiệp bên trong, giống độc sư dạng này chức nghiệp, liền không có quá nhiều miêu tả."),t("br"),t("br"),n._v("Xem ra Danh Sư đường cũng không phải vạn năng, danh sư mặc dù có Minh Lý cảnh tâm cảnh, đọc sách, ký ức đều so bình thường người tu luyện nhanh, cũng không có khả năng một người đem thiên hạ tất cả chức nghiệp đều học xong."),t("br"),t("br"),n._v("“Những nghề nghiệp khác không nhiều, nhưng nơi này liên quan tới luyện khí sư thư tịch cũng không phải ít!”"),t("br"),t("br"),n._v("Con mắt dừng ở một đống thư tịch bên trên."),t("br"),t("br"),n._v("Là liên quan tới luyện khí quyển sách, lít nha lít nhít đủ có mấy vạn bản nhiều."),t("br"),t("br"),n._v("Đối với luyện khí, hắn có thể nói một chữ cũng không biết."),t("br"),t("br"),n._v("Thiên Huyền vương quốc không có luyện khí sư, cũng không có đem đối ứng công hội, tự nhiên cũng không có thư tịch."),t("br"),t("br"),n._v("“Binh khí chia làm phàm, linh, thánh, vương bốn đẳng cấp...”"),t("br"),t("br"),n._v("Tiện tay lật ra một bản, phía trên kỹ càng miêu tả."),t("br"),t("br"),n._v("“Mỗi một cái cấp bậc, đều đối ứng hạ, trung, thượng, tuyệt tứ phẩm! Bình thường võ giả cửu trọng sử dụng đều là phàm phẩm, Linh cấp binh khí, chỉ có tứ tinh trở lên luyện khí sư mới có thể rèn đúc...”"),t("br"),t("br"),n._v("Nhẹ nhàng đọc qua, rất mau nhìn xong."),t("br"),t("br"),n._v("“Không nghĩ tới binh khí còn có nhiều như vậy phân cấp...”"),t("br"),t("br"),n._v("Trước đó nhìn thấy Danh Sư đường khôi lỗi hết sức lợi hại, liền lòng tràn đầy nghi hoặc, bây giờ mới biết, thứ này thuộc về linh binh."),t("br"),t("br"),n._v("Cái gọi là linh binh, liền là trong đó có linh tính binh khí."),t("br"),t("br"),n._v("Tỷ như, trường kiếm có được kiếm linh, liền có thể trở thành linh binh."),t("br"),t("br"),n._v("Loại binh khí này càng thêm cường đại, viễn siêu phàm binh."),t("br"),t("br"),n._v("Chỉ bất quá, quá mức trân quý, toàn bộ Thiên Vũ vương quốc, đều chưa hẳn có thể có mấy món."),t("br"),t("br"),n._v("“Kỳ thật không chỉ binh khí chia làm bốn loại, ngay cả công pháp, bí tịch cũng có phân cấp!”"),t("br"),t("br"),n._v("Xem hết binh khí, nhớ tới vừa mới nhìn đến công pháp và bí tịch."),t("br"),t("br"),n._v("Những vật này cũng đồng dạng chia làm phàm, linh, thánh, vương bốn đẳng cấp, trong đó lại phân làm hạ, trung, thượng ba cái tiểu cấp bậc."),t("br"),t("br"),n._v("Giống trước đó Hồng Thiên học viện tất cả học sinh tu luyện Hồng Thiên cửu trọng quyết, bất quá là Phàm cấp hạ phẩm công pháp mà thôi."),t("br"),t("br"),n._v("Thiên Huyền vương thất tu luyện công bí tịch, cũng bất quá Phàm cấp trung phẩm."),t("br"),t("br"),n._v("Loại công pháp này, cấp bậc thấp, tu luyện chậm, ngưng tụ ra chân khí độ tinh thuần cũng là kém, được cho cấp thấp nhất."),t("br"),t("br"),n._v("Thiên Huyền vương quốc bởi vì không có lại thêm cao cấp bậc công pháp, cho nên những này đẳng cấp phân cũng không tỉ mỉ, tại Danh Sư đường nhìn thấy thư tịch ghi chép, mới tính hiểu được."),t("br"),t("br"),n._v("“Không biết Thiên Đạo công pháp là... Cấp bậc gì!”"),t("br"),t("br"),n._v("Biết trước kia nhìn qua công pháp đẳng cấp đều rất thấp, trong đầu lóe lên, nhớ tới hắn tu luyện Thiên Đạo công pháp."),t("br"),t("br"),n._v("Bộ công pháp này là Thiên Đạo thư viện mượn nhờ vô số bản công pháp ngưng tụ mà thành, tổng kết trong đó tất cả chính xác phương pháp tu luyện, vô luận tốc độ tu luyện vẫn là hiệu quả, đều viễn siêu phổ thông công pháp."),t("br"),t("br"),n._v("Mặc dù không biết cấp bậc, nhưng chỉ sợ không thua Thánh cấp, thậm chí cấp bậc cao hơn Vương cấp!"),t("br"),t("br"),n._v("Bởi vì, cũng chỉ có lợi hại như thế công pháp, mới có thể để cho hắn Thiên Đạo chân khí, tinh thuần vô cùng, bất kỳ cái gì kinh mạch bế tắc, đều có thể nhẹ nhõm khơi thông."),t("br"),t("br"),n._v("“Tốt, sách đều thu vào thư viện, có thể từ từ xem, hiện tại thời gian cũng không sớm, nên đi Thiên Vũ học viện!”"),t("br"),t("br"),n._v("Xuyên thấu qua cửa sổ nhìn thoáng qua bên ngoài, một đêm trôi qua, Thái Dương đã sớm dâng lên, duỗi lưng một cái, Trương Huyền sải bước đi ra ngoài."),t("br"),t("br"),n._v("Quyển sách nhiều như vậy, có thể từ từ xem, hiện tại trọng yếu nhất chính là hoàn thành nhị tinh danh sư cái cuối cùng khảo hạch."),t("br"),t("br"),n._v("“Trương sư, Thiên Vũ vương quốc tuyển dụng hội lập tức liền muốn bắt đầu, chúng ta vẫn là mau mau đi!”"),t("br"),t("br"),n._v("Vừa đi ra tàng thư khố, Mạc Hoằng Nhất liền tiến lên đón."),t("br"),t("br"),n._v("Lúc này Mạc Hoằng Nhất, đã hoàn toàn biến thành một người khác, nhị mười sáu mười bảy tuổi bộ dáng, một mặt xám xanh, cùng lúc trước anh tuấn, bộ dáng lãnh khốc, hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết hôm nay vừa vặn Thiên Vũ vương quốc thông báo tuyển dụng, bỏ qua cơ hội này, còn muốn trà trộn vào đến liền khó khăn, Trương Huyền lúc này gật gật đầu."),t("br"),t("br"),n._v("Cùng sau lưng hắn, từ cửa sau đi ra Danh Sư đường, dọc theo đường đi đi về phía trước gần nửa canh giờ, liền thấy một cái cự đại sân trường xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Xây dựa lưng vào núi, nước sông vờn quanh, rộng lớn, khí phái! Vô số học sinh ra ra vào vào, sách tiếng vang vang, cho người ta một loại yên tĩnh, cảm giác không linh."),t("br"),t("br"),n._v("Cùng cái này sân trường so sánh, trước đó Hồng Thiên học viện liền cùng nông thôn trường học, không đáng giá nhắc tới."),t("br"),t("br"),n._v("Đi tới rộng lớn trước cổng chính, hai người ngừng lại."),t("br"),t("br"),n._v("Thiên Vũ học viện, đến!"),t("br"),t("br"),n._v("(Hôm nay ba canh, ra tay trước cái hai hợp một, sáu ngàn chữ, buổi chiều còn có. Chúc lão nhai các nữ bạn đọc, ngày lễ khoái hoạt!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-325"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-327"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);