(window.webpackJsonp=window.webpackJsonp||[]).push([[1551],{1906:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-318-đia-nguc-hinh-thuc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-318-đia-nguc-hinh-thuc"}},[n._v("#")]),n._v(" Chương 318: Địa Ngục hình thức")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Bình thường học viện đều là vừa khai giảng thông báo tuyển dụng lão sư, lúc này nhận người, chẳng lẽ là Danh Sư đường cố ý làm?”"),i("br"),i("br"),n._v("Đi vào sân trường, nhìn thấy vô số đông học sinh, giấu trong lòng mộng tưởng, an nhàn học tập, Trương Huyền cảm thấy thư thái một hồi, nhịn không được hỏi."),i("br"),i("br"),n._v("Bình thường học viện, đều là học kỳ sơ hoặc là học kỳ mạt tuyển nhận lão sư, học sinh nhập học về sau, mới có thể tự chủ lựa chọn."),i("br"),i("br"),n._v("Hiện tại thời gian này, khai giảng đã hai tháng, chính là học kỳ bên trong, vô duyên vô cớ tuyển nhận lão sư, học sinh làm cái gì? Chẳng lẽ để các lão sư khác lui khóa?"),i("br"),i("br"),n._v("Cái này rõ ràng không quá hiện thực."),i("br"),i("br"),n._v("Chỉ sợ cũng chỉ có danh sư đường mới có như thế năng lực."),i("br"),i("br"),n._v("“Không phải!”"),i("br"),i("br"),n._v("Mạc Hoằng Nhất lắc đầu: “Là học viện mấy vị lão sư không biết bởi vì nguyên nhân gì rời đi, lớp học của bọn họ không đi. Thông báo tuyển dụng mới lão sư là vì quản lý những này lớp, không làm trễ nải bình thường giảng bài.”"),i("br"),i("br"),n._v("“Lớp không đi? Ngươi ý tứ... Một khi nhận lời mời bên trên, liền sẽ trực tiếp chỉ huy trực ban? Mà không phải toàn trường phạm vi bên trong chân tuyển học sinh?”"),i("br"),i("br"),n._v("Trương Huyền sững sờ."),i("br"),i("br"),n._v("Trực tiếp chỉ huy trực ban, cùng để học sinh tự chủ lựa chọn là hai chuyện khác nhau."),i("br"),i("br"),n._v("Chỉ huy trực ban chẳng khác nào học sinh cái gì đều đã sắp xếp xong xuôi, muốn đổi cũng không có cách nào đổi, mà cái sau, có thể tự do lựa chọn, lại càng dễ chiêu đến ưa thích bản thân lớp học học viên."),i("br"),i("br"),n._v("“Chân tuyển học sinh? Nào có chuyện tốt như vậy, cũng không phải học kỳ sơ, làm sao có thể có học sinh để ngươi lựa chọn? Lại nói, chúng ta bây giờ cái thân phận này, không có danh khí gì, cho dù có tân sinh, cũng sẽ không lựa chọn ah!”"),i("br"),i("br"),n._v("Mạc Hoằng Nhất trợn trắng mắt."),i("br"),i("br"),n._v("Biết siêu bất quá trước mắt quái thai này, hắn cũng không có trước đó như vậy căm thù, thái độ hòa hoãn rất nhiều."),i("br"),i("br"),n._v("Đường đường thiên tài nếu như ngay cả hòa hoãn tâm cảnh cũng không biết, chỉ sợ sớm không biết chết bao nhiêu lần."),i("br"),i("br"),n._v("“Như thế...”"),i("br"),i("br"),n._v("Trương Huyền vò đầu."),i("br"),i("br"),n._v("Hắn hiện tại ngụy trang vị này Liễu Trình, không có có danh tiếng, cũng không có lợi hại gì tư lịch, liền xem như khai giảng thời gian, tuyển nhận mới học sinh, đoán chừng cũng không có người nguyện ý báo hắn ban."),i("br"),i("br"),n._v("Đương nhiên, muốn nói điểm xuất phát, so lúc trước hắn tại Hồng Thiên học viện cao hơn."),i("br"),i("br"),n._v("Khi đó, nhưng còn đeo học viện kém cỏi nhất lão sư thanh danh, đồng dạng thu mấy cái nghịch thiên học viên."),i("br"),i("br"),n._v("“Thiên Vũ học viện học sinh là thế nào phân niên cấp?” Suy nghĩ một chút, Trương Huyền hỏi tiếp."),i("br"),i("br"),n._v("“Ngươi không biết?” Mạc Hoằng Nhất nhìn quái vật nhìn qua."),i("br"),i("br"),n._v("Có đôi khi hắn cảm thấy kẻ trước mắt này yêu nghiệt không tưởng nổi, để cho người ta tuyệt vọng, lại có lúc đợi cảm thấy đơn thuần cái gì cũng đều không hiểu, giống như vừa tới thế giới này."),i("br"),i("br"),n._v("Thiên Vũ học viện làm vương quốc đệ nhất học phủ, là cái lão sư cũng biết, ngươi đều trở thành danh sư, sẽ không rõ ràng?"),i("br"),i("br"),n._v("“Thiên Vũ học viện, tuyển nhận học sinh, cần tu vi đạt tới võ giả tam trọng chân khí cảnh! Chia làm bốn cái niên cấp, đối ứng Chân Khí, Bì Cốt, Đỉnh Lực cùng Ích Huyệt bốn loại cảnh giới. Đạt tới Ích Huyệt cảnh, liền có thể xin tốt nghiệp.”"),i("br"),i("br"),n._v("Chần chờ một chút, Mạc Hoằng Nhất giải thích một phen: “Làm vì lão sư, hàng năm đều có thể tuyển nhận học sinh, bởi vậy, bình thường lão sư môn hạ, bốn cái niên cấp học sinh cơ hồ đều có, cao thấp không đều.”"),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Kỳ thật những này quy định cùng Hồng Thiên học viện không sai biệt lắm, hắn xuyên qua trước tiền thân, nếu không phải dạy học sinh tẩu hỏa nhập ma, cũng là có lão sinh."),i("br"),i("br"),n._v("“Đến!”"),i("br"),i("br"),n._v("Chính đang suy tư, liền nghe Mạc Hoằng Nhất lời nói, ngẩng đầu nhìn lên, chính là Thiên Vũ học viện trưởng lão viện."),i("br"),i("br"),n._v("Thông báo tuyển dụng mới lão sư, cần trưởng lão đồng ý mới được."),i("br"),i("br"),n._v("Viện tử chính giữa, ngồi một loạt lão giả, chừng hơn mười vị, tu vi kém nhất cũng đều đạt đến nửa bước Tông sư, cường đại càng là Tông Sư cảnh đỉnh phong."),i("br"),i("br"),n._v("“Ở giữa nhất cái kia là viện trưởng Tạ Tất, Thiên Vũ vương quốc bài danh mười vị trí đầu siêu cường nhân vật, nhất tinh danh sư, tại trong vương quốc có rất lớn danh khí. Liền xem như ta, tới chiến đấu, đều không có nắm chắc tất thắng!”"),i("br"),i("br"),n._v("Mạc Hoằng Nhất lặng lẽ một chỉ, truyền âm tới."),i("br"),i("br"),n._v("Thuận ngón tay của hắn nhìn lại, một cái lão giả râu bạc trắng đang ngồi ở nhất vị trí trung tâm, hai mắt trừng trừng tựa như hùng sư, mặc dù tuổi tác không nhỏ, lại không có chút nào khí tức suy bại, ngược lại toàn thân tinh lực sung mãn, để lộ ra khí thế cường đại."),i("br"),i("br"),n._v("Tông sư cường giả đỉnh phong!"),i("br"),i("br"),n._v("Hôm qua Mạc Vũ công chúa Đáp Vấn hội thời điểm, không có nhìn người nọ, xem ra có lẽ cũng không có đi qua."),i("br"),i("br"),n._v("Lão giả râu bạc trắng phía trước, là một loạt người, chỉnh tề xếp thành đội ngũ, chừng trên trăm."),i("br"),i("br"),n._v("“Hai vị nếu như cũng là khảo hạch lão sư, xin ở chỗ này điền tư liệu, sau đó lại đi qua xếp hàng!”"),i("br"),i("br"),n._v("Mới vừa vào cửa, một cái học sinh ngay tại một bên chào hỏi."),i("br"),i("br"),n._v("Hai người đi tới, nhận một cái bảng biểu."),i("br"),i("br"),n._v("Nội dung rất đơn giản, liền là tính danh, giới tính cùng tu vi, dạy thay kinh lịch."),i("br"),i("br"),n._v("Hai người ngụy trang người đều có tư lịch nhưng tra, rất nhanh điền hoàn tất."),i("br"),i("br"),n._v("“Ta muốn hỏi một chút, những người này đều là đến nhận lời mời lão sư?”"),i("br"),i("br"),n._v("Đem lấp xong bảng biểu đưa tới, Trương Huyền một chỉ xếp hàng hơn trăm người."),i("br"),i("br"),n._v("“Đúng!” Học sinh gật gật đầu."),i("br"),i("br"),n._v("“Nhiều như vậy?” Trương Huyền nhịn không được hỏi: “Cái kia... Học viện chúng ta hết thảy tuyển nhận mấy cái lão sư?”"),i("br"),i("br"),n._v("Hàng trăm người tới nhận lời mời, nếu như tuyển nhận nhiều còn tốt, thu ít, chỉ sợ còn muốn cạnh tranh một cái."),i("br"),i("br"),n._v("“A, hết thảy tuyển nhận năm cái, hiện tại có ba cái nhận lời mời thông qua, những người này ở đây cạnh tranh còn lại hai cái danh ngạch!” Học sinh nói."),i("br"),i("br"),n._v("“Còn lại hai cái?”"),i("br"),i("br"),n._v("Trương Huyền cùng Mạc Hoằng Nhất liếc mắt nhìn nhau."),i("br"),i("br"),n._v("Trên trăm cái lão sư cạnh tranh hai cái danh ngạch..."),i("br"),n._v(" Danh Sư đường không phải để chúng ta thu học sinh, khảo hạch độ tín nhiệm sao? Làm sao ngược lại vừa đến, biến thành nhận lời mời giáo sư rồi?"),i("br"),i("br"),n._v("Vạn nhất nếu là nhận lời mời không lên, há không trực tiếp phán định thất bại?"),i("br"),i("br"),n._v("“Đúng!” Học sinh gật đầu."),i("br"),i("br"),n._v("“Những lão sư này bất chính đứng xếp hàng sao? Làm sao đã có ba cái nhận lời mời thông qua được? Thông báo tuyển dụng lão sư nhưng có yêu cầu gì?” Trương Huyền tiếp tục hỏi."),i("br"),i("br"),n._v("Còn có người xếp hàng, liền có ba cái thành công?"),i("br"),i("br"),n._v("Không nên tất cả mọi người xem một lần, sau đó lại cẩn thận lựa chọn sao?"),i("br"),i("br"),n._v("“A, viện trưởng nhận lời mời, đầu tiên là nhìn sơ yếu lý lịch, sau khi xem xong, cảm thấy phù hợp liền trực tiếp mướn. Phía trước ba cái, đều là nửa bước Tông sư cường giả, làm một cái bình thường lão sư tự nhiên thành thạo điêu luyện!”"),i("br"),i("br"),n._v("Học sinh cười nói."),i("br"),i("br"),n._v("“Nửa bước Tông sư?”"),i("br"),i("br"),n._v("Trương Huyền cùng Mạc Hoằng Nhất cười khổ."),i("br"),i("br"),n._v("Loại thực lực này, tại Thiên Vũ học viện đều có thể làm trưởng lão, làm lão sư hoàn toàn chính xác rất đơn giản."),i("br"),i("br"),n._v("Mà hắn ngụy trang cái này Liễu Trình, chỉ có Thông Huyền cảnh sơ kỳ, tương đối yếu đi rất nhiều."),i("br"),i("br"),n._v("Nửa bước Tông sư cùng Thông Huyền cảnh sơ kỳ đồng thời nhận lời mời, người ngu đi nữa cũng chọn cái trước."),i("br"),i("br"),n._v("Mạc Hoằng Nhất ngụy trang người kia, cũng chỉ có Thông Huyền cảnh sơ kỳ, hai người một nửa cân một cái tám lượng, không kém nhiều."),i("br"),i("br"),n._v("Vốn cho rằng đi tới liền sẽ trở thành học viện lão sư, sau đó dạy bảo học sinh, đơn giản đến cực điểm, không nghĩ tới trực tiếp cho cái cực hạn hình thức, kết thúc không thành, ngươi liền phó bản đều không gặp được."),i("br"),i("br"),n._v("Một vị là đường đường Thiên Vũ vương quốc đệ nhất thiên tài, một vị là Danh Sư đường liên tục đánh phá kỉ lục siêu cường tân tinh..."),i("br"),i("br"),n._v("Vì hoàn thành nhị tinh danh sư khảo hạch, lại để cho cùng một đám phổ thông lão sư cạnh tranh... Ngẫm lại cũng là say."),i("br"),i("br"),n._v("“Tốt, hai vị xin xếp hàng đi, nếu như phù hợp, tự nhiên sẽ được lưu lại!” Học sinh chào hỏi."),i("br"),i("br"),n._v("Trương Huyền hai người lên tiếng, theo sát ở sau lưng mọi người."),i("br"),i("br"),n._v("“Các ngươi cũng là đến nhận lời mời? Thông Huyền cảnh sơ kỳ, ta nhìn vẫn là trở về đi, nơi này kém nhất đều đạt đến trung kỳ, hậu kỳ!”"),i("br"),i("br"),n._v("Vừa đi qua, phía trước một thanh niên liền một mặt khinh miệt nhìn qua, đồng thời triển lộ tu vi của mình, Thông Huyền cảnh hậu kỳ!"),i("br"),i("br"),n._v("Hai người vì ngụy trang cần, đều đem tu vi của mình áp chế ở Thông Huyền sơ kỳ, để cho người ta có thể một chút nhìn ra."),i("br"),i("br"),n._v("Bằng không, Trương Huyền Thiên Đạo chân khí tinh thuần như vậy, liền Khương đường chủ đều không thể phát hiện, gia hỏa này lại làm sao có thể biết."),i("br"),i("br"),n._v("“Lão sư, sẽ giảng bài mới là bản lĩnh, không biết giảng bài, tu vi lại cao hơn cũng vô dụng!”"),i("br"),i("br"),n._v("Mạc Hoằng Nhất hừ một tiếng."),i("br"),i("br"),n._v("Làm vì siêu cấp thiên tài, từ nhỏ đến lớn đều bị người ngưỡng mộ, ngoại trừ tại cái này Trương Huyền trước mặt thua thiệt qua, lúc nào bị người khinh thị qua, gia hỏa này vừa lên đến liền nói lời này, để hắn tràn đầy lửa giận."),i("br"),i("br"),n._v("“Tuổi không lớn lắm, hỏa khí còn không nhỏ!”"),i("br"),i("br"),n._v("Thanh niên lạnh hừ một tiếng: “Nói như vậy cũng là vì các ngươi tốt, bằng không thì ta sợ các ngươi đợi uổng công một ngày, cuối cùng không thu hoạch được gì!”"),i("br"),i("br"),n._v("Nói xong quay đầu đi, không tiếp tục để ý."),i("br"),i("br"),n._v("Thấy bị đối phương khinh thị, Mạc Hoằng Nhất sắc mặt đều tái rồi, Trương Huyền mỉm cười."),i("br"),i("br"),n._v("Có thể đem đường đường đệ nhất thiên tài tức thành như vậy, gia hỏa này cũng là nhân tài."),i("br"),i("br"),n._v("Đang muốn nói lên hai câu, liền nghe đến ngay phía trước một cái lão giả tiếng âm vang lên."),i("br"),i("br"),n._v("“Lão phu Tạ Tất, cảm tạ chư vị lão sư đến đây nhận lời mời!”"),i("br"),i("br"),n._v("Nghe được viện trưởng mở lời, tất cả mọi người đều an tĩnh lại, đồng loạt nhìn về phía trước."),i("br"),i("br"),n._v("“Học viện lão sư thiếu hụt sự tình, chắc hẳn chư vị đều nghe nói, đã các ngươi đến đây nhận lời mời, hẳn là cũng biết lớp tình huống.”"),i("br"),i("br"),n._v("Tạ Tất viện trưởng nhìn quanh một tuần: “Bất quá, ta vẫn là sớm nói một chút hai cái này lớp. Là Hồ Nghĩa cùng Bạch Phương hai vị lão sư lưu lại, bởi vậy, hai cái này ban học sinh, đều tương đối ưu tú, ta cũng hy vọng có thể thông báo tuyển dụng cái ưu tú hơn lão sư, không đến mức lãng phí thiên phú của bọn hắn.”"),i("br"),i("br"),n._v("“Hồ Nghĩa, Bạch Phương?”"),i("br"),i("br"),n._v("Mạc Hoằng Nhất nghe được danh tự, nhướng mày."),i("br"),i("br"),n._v("“Ngươi biết?” Trương Huyền nhìn qua."),i("br"),i("br"),n._v("“Bọn hắn là Thiên Vũ học viện nổi danh minh tinh giáo sư, dạy học chất lượng rất tốt, hơn nữa... Vẫn là Khương đường chủ học đồ!” Mạc Hoằng Nhất truyền âm."),i("br"),i("br"),n._v("“Khương đường chủ học đồ?”"),i("br"),i("br"),n._v("Có thể trở thành nhị tinh đỉnh phong danh sư học đồ, cái này hay vị lão sư trình độ hoàn toàn chính xác rất cao."),i("br"),i("br"),n._v("“Đúng vậy a, nghe nói lớp học của bọn họ, là toàn bộ Thiên Vũ vương quốc ưu tú nhất lớp một trong, chiêu thu học sinh, cũng đều là số một số hai, xem ra nhiệm vụ của chúng ta không có đơn giản như vậy!”"),i("br"),i("br"),n._v("Mạc Hoằng Nhất xoa xoa mi tâm."),i("br"),i("br"),n._v("Học sinh ưu tú, bình thường đều sẽ tự cho mình siêu phàm, đối lão sư thuộc về quản liền sẽ tương ứng yếu bớt."),i("br"),i("br"),n._v("Thật giống như ngươi chỉ dạy một thiên tài cùng một cái tầm thường, đồng dạng theo chân khí cảnh, dạy đến Đỉnh Lực cảnh, người sau tất nhiên mang ơn, cái trước ngược lại sẽ cảm thấy ngươi là dung sư, làm trễ nải hắn."),i("br"),i("br"),n._v("Cho phú hào một ngàn khối tiền, cùng tên ăn mày một ngàn khối tiền, hoàn toàn là hai chuyện khác nhau."),i("br"),i("br"),n._v("Đưa than sưởi ấm trong ngày tuyết rơi ân nghĩa nặng, mùa hè đem áo bông đưa qua, tự nhiên rơi không đến hảo cảm."),i("br"),i("br"),n._v("Hai cái này lớp, là học viện ưu tú nhất lớp một trong, học sinh cũng là đỉnh tiêm, nếu như so với bọn hắn còn thiên tài, ngược lại là có thể chấn nhiếp ở... Nhưng bằng mượn hiện tại cái này hư thân phận giả, muốn muốn trấn trụ, cũng lại đạt được độ tín nhiệm, quá khó khăn..."),i("br"),i("br"),n._v("Đường đường thiên tài, làm sao có thể cam tâm tình nguyện nhận hai cái bình thường người làm lão sư?"),i("br"),i("br"),n._v("Ma đản, vốn cho rằng chạy tới thu học sinh, nhẹ nhõm thêm vui sướng, thời gian mười ngày rất nhiều sung túc, không nghĩ tới không riêng gì gặp cực hạn hình thức, quả thực liền là địa ngục hình thức."),i("br"),i("br"),n._v("Khảo hạch nhị tinh danh sư, quả nhiên... Không tưởng tượng dễ dàng như vậy."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-326"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-328"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);