(window.webpackJsonp=window.webpackJsonp||[]).push([[1552],{1907:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-319-ghet-hoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-319-ghet-hoc"}},[n._v("#")]),n._v(" Chương 319: Ghét học?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ta biết các vị ở tại đây, đều có phong phú dạy học kinh nghiệm, đều là công hội thi đậu tư chất chính thức lão sư, chỉ bằng vào sơ yếu lý lịch, tùy tiện hỏi hơn mấy câu, lựa chọn ai, không tuyển chọn ai, đều để người khó mà tin phục.”"),t("br"),t("br"),n._v("Tạ Tất viện trưởng nhìn quanh một tuần, tiếp tục nói: “Đã như vậy, ta cứ dựa theo giáo sư cân nhắc tiêu chuẩn tới chọn.”"),t("br"),t("br"),n._v("“Giáo sư cân nhắc tiêu chuẩn?”"),t("br"),t("br"),n._v("Đám người tất cả đều nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Không sai! Nhà giáo, truyền đạo học nghề giải thích nghi hoặc vậy! Lão sư nhất bản chức công việc vẫn là dạy bảo học sinh, để hắn đi đến chính xác con đường, thu hoạch được tốt hơn phát triển! Ta chỗ này có hai cái học sinh, đều có chút vấn đề, ai có thể giải quyết, học viện liền sẽ trúng tuyển ai. Đồng dạng có thể giải quyết tình huống dưới, ai làm càng tốt hơn, ai thứ tự liền gần phía trước!”"),t("br"),t("br"),n._v("Tạ Tất viện trưởng nói."),t("br"),t("br"),n._v("“Lão sư chức trách liền là dạy học, dạy học không tốt, hết thảy đều vô dụng!”"),t("br"),t("br"),n._v("“Tu cho lại cao hơn giảng không ra, cũng là uổng công!”"),t("br"),t("br"),n._v("Nghe được tuyển bạt tiêu chuẩn, đám người đồng thời gật đầu."),t("br"),t("br"),n._v("“Loại này khảo hạch cũng là phù hợp quy củ!”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất trên mặt cũng lộ ra vẻ hài lòng."),t("br"),t("br"),n._v("Thuật nghiệp hữu chuyên công, không có giảng bài kinh nghiệm, không mang qua học sinh, không có dạy học lý luận, coi như thực lực mạnh hơn, lớp học hiệu quả cũng chưa chắc mạnh cỡ nào!"),t("br"),t("br"),n._v("Không ít đại học vấn gia chạy đi học, nói chuyện trời đất, một tiết khóa xuống tới, kết quả... Học sinh chó má không biết, cái gì đều không có hiểu rõ."),t("br"),t("br"),n._v("“Đem bọn hắn mang vào đi!”"),t("br"),t("br"),n._v("Nương theo Tạ viện trưởng một tiếng la lên, hai cái mười lăm, mười sáu tuổi học viên bị dẫn vào."),t("br"),t("br"),n._v("Một nam một nữ."),t("br"),t("br"),n._v("Nữ hài trên mặt lãnh ngạo chi sắc, nam hài thì ánh mắt lấp lóe, như là ưng kiêu."),t("br"),t("br"),n._v("Hai vị học sinh đi tới đám người trước mặt, trong mắt cũng không vẻ sợ hãi, tựa hồ biết mình định vị, yên tĩnh đến cực điểm."),t("br"),t("br"),n._v("“Liền là hai vị này học sinh, các ngươi ai trước đến xem?” Tạ Tất nhìn quanh đám người."),t("br"),t("br"),n._v("“Ta!”"),t("br"),t("br"),n._v("Trương Huyền, Mạc Hoằng Nhất trước mặt cái kia Thông Huyền cảnh hậu kỳ thanh niên, hừ một tiếng, một mặt cao ngạo đi ra."),t("br"),t("br"),n._v("“Không sai, lão sư liền muốn có loại này dũng cảm nếm thử thái độ, sợ đầu sợ đuôi, lo trước lo sau, chỉ sợ cũng dạy không ra học sinh tốt!”"),t("br"),t("br"),n._v("Tạ Tất hài lòng gật đầu."),t("br"),t("br"),n._v("Nghe nói như thế, đám người một trận hối hận."),t("br"),t("br"),n._v("Sớm biết liền lên đi, gia hỏa này chẳng những ra danh tiếng, còn chiếm được Tạ viện trưởng hảo cảm, làm không cẩn thận, liền khảo hạch thành công."),t("br"),t("br"),n._v("Nghe được tán dương, thanh niên trong mắt tinh quang lóe lên, mấy bước đi tới hai cái học sinh trước mặt, xuôi theo lấy bọn hắn dạo qua một vòng, mở lời phân phó: “Riêng phần mình đánh một bộ võ kỹ nhìn xem!”"),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Nữ hài trong mắt lóe lên một đạo vẻ mặt không sao cả, đương bắt đầu trước."),t("br"),t("br"),n._v("Nàng đánh võ kỹ trung quy trung củ, lực lượng cùng linh tính đều mười phần, cho thấy lương thiên phú tốt, rất nhanh một bộ quyền pháp đánh xong, nam hài đi vào giữa sân."),t("br"),t("br"),n._v("Chiêu số của hắn không có cái trước như vậy linh động, càng thêm trực tiếp, nhanh chóng, mang theo lực lượng hùng hồn, thậm chí ẩn ẩn để lộ ra sát phạt khí tức."),t("br"),t("br"),n._v("Hai người thi triển võ kỹ, các lão sư khác cũng ở một bên quan sát, các có chút suy nghĩ."),t("br"),t("br"),n._v("Rất nhanh, võ kỹ biểu hiện ra xong, hai người lần nữa ôm quyền đứng thẳng."),t("br"),t("br"),n._v("“Tốt, vị lão sư này có thể bình điểm!”"),t("br"),t("br"),n._v("Tạ Tất vẫy tay một cái."),t("br"),t("br"),n._v("“Tốt!” Thanh niên lên tiếng, hai tay chắp sau lưng, rất có chỉ điểm giang sơn chi ý: “Cô gái này đánh ra bộ võ kỹ này vì , mây trôi nước chảy, lực lượng linh động. Chiêu số mặc dù tinh mỹ, hơn nữa tu luyện cơ sở cũng rất vững chắc, chỉ bất quá... Chân khí của nàng không đủ, du tẩu kinh mạch thời điểm, biểu lộ ra khá là phí sức, nếu như ta không nhìn lầm, hắn có lẽ vừa đột phá Chân Khí cảnh không lâu, đối chân khí vận dụng còn không thế nào quen thuộc!”"),t("br"),t("br"),n._v("Nói xong nhìn về phía nữ hài, mang theo hỏi thăm chi ý: “Không biết ta nói chính xác hay không?”"),t("br"),t("br"),n._v("“Ta ba tháng trước mới đột phá Chân Khí cảnh!” Nữ hài gật gật đầu."),t("br"),t("br"),n._v("“Chiêu số mặc dù tu luyện tốt, nhưng cũng tiếc chân khí khống chế không đủ, lại xinh đẹp, lại tinh chuẩn, cũng đều là chủ nghĩa hình thức. Nếu như là đệ tử của ta, ta sẽ muốn cầu nàng dốc lòng tu luyện, trước nghĩ biện pháp ngưng tụ chân khí lại nói, chỉ có chân khí đạt tới hùng hậu cảnh giới, mới có thể đem chiêu số phát huy thấu triệt!”"),t("br"),t("br"),n._v("Thanh niên cười cười: “Thân thể của nàng tú lệ đoan trang, khí tức tinh thuần, ta sẽ đề nghị tu luyện học viện , cùng khí chất của nàng tương xứng, tu luyện cũng có thể đắc tâm ứng thủ!”"),t("br"),t("br"),n._v("“Tại hạ tu luyện chính là Ngọc Tố công!” Nghe được hắn, nữ hài ánh mắt lộ ra một tia ngạc nhiên, nhịn không được nói."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Một mặt đắc ý, chỉ điểm xong nữ hài, thanh niên nhìn về phía cách đó không xa nam hài: “Lực lượng của ngươi cương mãnh hữu lực, nói rõ thích hợp tu luyện lực lượng hình công pháp và võ kỹ, ta cảm thấy  tương đối thích hợp ngươi!”"),t("br"),t("br"),n._v("“Hồi bẩm lão sư, tại hạ tu luyện chính là Trúc Dương công!”"),t("br"),t("br"),n._v("Nam hài nhẹ gật đầu."),t("br"),t("br"),n._v("“Tốt, chỉ điểm của ta xong...”"),t("br"),t("br"),n._v("Hất lên ống tay áo, thanh niên mặt mũi tràn đầy đắc ý đi trở về đám người, đi tới Trương Huyền hai người trước mặt, còn cố ý giơ lên cái cằm."),t("br"),t("br"),n._v("“Những này ta cũng đã nhìn ra, có lẽ sớm đi lên nói!”"),t("br"),t("br"),n._v("“Ai nói không phải, để hắn đoạt tiên cơ, lại đi qua nói, chẳng khác nào vẽ rắn thêm chân, ngược lại không đẹp!”"),t("br"),t("br"),n._v("“Thật sự là đáng giận...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được thanh niên lời nói, đám người tất cả đều lộ ra hối hận chi ý."),t("br"),t("br"),n._v("Có thể khảo hạch chính thức giáo sư, những này tự nhiên cũng đều có thể nhìn ra, bất quá, người khác đã nói qua, ngươi lại đến đi nói, khó tránh khỏi có đạo văn chi ngại, cũng thu hoạch được không được hảo cảm."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Cùng phản ứng của mọi người khác biệt, Tạ viện trưởng trên mặt không có quá nhiều biểu tình biến hóa, ngược lại lộ ra vẻ thất vọng, tiếp lấy nhìn quanh một tuần: “Còn có người có cái gì bổ sung sao?”"),t("br"),n._v(" “Ta đến!”"),t("br"),t("br"),n._v("Chần chờ phút chốc, lại đi ra một mình."),t("br"),t("br"),n._v("Cái này là cái trung niên người, chừng bốn mươi tuổi."),t("br"),t("br"),n._v("“Là Thiên Tuyền học viện Đỗ Tầm lão sư!”"),t("br"),t("br"),n._v("“Nghe nói là minh tinh giáo sư, hơn nữa đã bị một vị danh sư thu làm học đồ!”"),t("br"),t("br"),n._v("“Đúng vậy a, ta nghe qua hắn khóa, rất lợi hại...”"),t("br"),t("br"),n._v("“Xem ra, danh ngạch muốn lại phải thiếu một cái!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy trung niên nhân đi tới, tất cả mọi người xôn xao, nhịn không được trên mặt khẩn trương."),t("br"),t("br"),n._v("Vừa rồi vị này trong đám người không thế nào hiển sơn lộ thủy, vừa đi ra, lập tức có không ít người nhận ra được."),t("br"),t("br"),n._v("Có thể trở thành minh tinh giáo sư cùng danh sư học đồ, đều có năng lực hơn người."),t("br"),t("br"),n._v("Đến đây nhận lời mời người bên trong, cơ bản đều là phổ thông lão sư, cùng loại cấp bậc này nhân vật, vẫn là có khoảng cách."),t("br"),t("br"),n._v("“Vừa rồi hai người bọn họ thi triển võ kỹ, ta cũng nhìn, nữ sinh lực lượng nhẹ nhàng, vừa rồi vị lão sư kia nói không sai, thích hợp tu luyện Ngọc Tố công, bất quá... Hắn không để ý đến một điểm. Nữ sinh này mới vừa tiến vào Chân Khí cảnh ba tháng, liền có thể tu luyện ra lợi hại như thế võ kỹ, đem chân khí cùng thân thể kết hợp tốt như vậy... Đủ có nói rõ thiên phú, vượt qua thường nhân! Thuộc về... Thiên tài!”"),t("br"),t("br"),n._v("Đỗ Tầm lão sư đi tới hai người trước mặt, nhìn kỹ một hồi, kỹ càng lời bình."),t("br"),t("br"),n._v("“Ngọc Tố công, chỉ là nữ tử trong tu luyện, tương đối phổ biến công pháp, nếu như thiên phú hơi kém, tu luyện không có vấn đề quá lớn, nhưng thiên phú tốt cũng có chút lãng phí, ta cảm thấy nếu như đổi tu , lại thêm là thích hợp!”"),t("br"),t("br"),n._v("“Đến mức vị này nam sinh, lực lượng cương mãnh, khí thế hùng hậu, lực lượng hình  tương đối phù hợp. Bộ công pháp này đập bóng, có thể tốt hơn phát huy ưu thế của hắn!”"),t("br"),t("br"),n._v("Nói đến đây, Đỗ Tầm lão sư liền ôm quyền: “Ta nói xong!”"),t("br"),t("br"),n._v("“Rất tốt!”"),t("br"),t("br"),n._v("Nghe được hắn, Tạ Tất hài lòng nhẹ gật đầu."),t("br"),t("br"),n._v("Vừa rồi thanh niên lúc nói, hắn lộ ra thất vọng, vị này Đỗ Tầm mở lời, thế mà khen một câu, đủ thấy cả hai đã có khác biệt."),t("br"),t("br"),n._v("“Đáng giận!”"),t("br"),t("br"),n._v("Nhìn thấy nét mặt của hắn biến hóa, thanh niên nguyên bản đắc ý biểu lộ, trầm xuống, nắm đấm xiết chặt."),t("br"),t("br"),n._v("Người trong nghề vừa ra tay liền biết có hay không, hắn nói mặc dù cũng sai lầm gì, nhưng cùng vị này Đỗ Tầm so sánh, liền hơi có vẻ Trung Dong, hoàn toàn chính xác, dùng phương pháp của hắn hiệu quả, khẳng định phải tốt hơn bản thân."),t("br"),t("br"),n._v("“Tốt, còn có ai muốn bổ sung sao?” Tạ Tất lần nữa nhìn tới."),t("br"),t("br"),n._v("Lại có mấy người đi lên phía trước, bất quá, nói đều cùng phía trước hai người không sai biệt lắm, không có quá nhiều ý mới."),t("br"),t("br"),n._v("“Nếu như không có bổ sung, danh ngạch liền là phía trước nhất hai vị lão sư!”"),t("br"),t("br"),n._v("Mang theo thất vọng, Tạ Tất tiếp tục nói."),t("br"),t("br"),n._v("“Ta tới đi!”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất đi lên phía trước."),t("br"),t("br"),n._v("Thấy đều nói nhiều như vậy, còn có người tiến lên, tất cả mọi người đem ánh mắt tập trung tới, bất quá, khi thấy rõ tu vi, nguyên một đám đồng thời lắc đầu."),t("br"),t("br"),n._v("Không ít Thông Huyền cảnh hậu kỳ, đỉnh phong mới nói, không có quá nhiều sáng tạo cái mới, một cái nho nhỏ sơ kỳ mà thôi, lại làm sao có thể có cao hơn kiến thức!"),t("br"),t("br"),n._v("Ngay cả Tạ Tất cùng tất cả trưởng lão cũng không có ôm hy vọng quá lớn."),t("br"),t("br"),n._v("Tu vi mặc dù không có nghĩa là giảng bài, nhưng cũng có thể nói rõ một vài vấn đề."),t("br"),t("br"),n._v("Tu luyện cấp bậc cao, nói rõ đối tu luyện lý giải cũng sâu, giờ học tự nhiên cũng càng có tâm đắc."),t("br"),t("br"),n._v("Không để ý tới ánh mắt của mọi người, Mạc Hoằng Nhất đi tới hai cái học sinh trước mặt, dọc theo dạo qua một vòng, nhìn về phía Tạ viện trưởng: “Viện trưởng, nếu như ta không nhìn lầm, ngươi nói hai cái này học sinh có chút vấn đề, cũng không phải là chỉ điểm bọn hắn tu vi, mà là có ám chỉ gì khác đi!”"),t("br"),t("br"),n._v("“Có ám chỉ gì khác?”"),t("br"),t("br"),n._v("“Lão sư dạy bảo, không chỉ điểm tu vi chỉ chút gì?”"),t("br"),t("br"),n._v("“Hoang đường! Không hiểu liền chớ nói nhảm...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được hắn, vừa rồi tham dự khảo hạch đám người, sắc mặt tất cả đều trầm xuống, cái thứ nhất nói chuyện thanh niên càng là mang theo mặt mũi tràn đầy khinh miệt."),t("br"),t("br"),n._v("Học sinh có vấn đề, không phải chỉ tu vi chỉ cái gì?"),t("br"),t("br"),n._v("Chẳng lẽ còn có thể chỉ thân thế gia đình?"),t("br"),t("br"),n._v("“Ồ? Còn xin nói một chút!”"),t("br"),t("br"),n._v("Vốn là tràn đầy thất vọng Tạ viện trưởng, nghe nói như thế, không chịu được ngẩng đầu lên."),t("br"),t("br"),n._v("“Nữ sinh này, vô luận tu luyện chính là Ngọc Tố công vẫn là Ngọc Nữ công, ảnh hưởng cũng không quá lớn, cùng trước đó Đỗ Tầm lão sư nói đồng dạng, thiên phú của nàng cực cao, coi như công pháp bên trên có chút khác biệt, cũng sẽ không thái quá ảnh hưởng.”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất nhìn qua: “Hơn nữa, coi như Ngọc Tố công hơi yếu, nàng tu luyện nhiều năm, đã quen thuộc, tùy tiện thay đổi, đối tiến bộ của nàng ngược lại là cái trở ngại. Lại nói, chỉ muốn chỉ điểm tốt, hai cái này vốn là không kém nhiều công pháp, không có khác nhau quá nhiều!”"),t("br"),t("br"),n._v("“Cho nên... Trong mắt của ta, nàng vấn đề lớn nhất, cũng không phải là tu luyện công pháp, cũng không phải phải thêm cố tu vi chân khí, mà là... Ghét học!”"),t("br"),t("br"),n._v("“Ghét học?”"),t("br"),t("br"),n._v("Tạ viện trưởng con mắt một cái phát sáng lên, nhịn không được nhìn qua: “Không sai, nói tiếp!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe hắn chấp nhận, tất cả mọi người đều con mắt trợn tròn."),t("br"),t("br"),n._v("Ghét học?"),t("br"),t("br"),n._v("Có lầm hay không, cái đồ chơi này làm sao nhìn ra được?"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-327"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-329"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);