(window.webpackJsonp=window.webpackJsonp||[]).push([[1555],{1910:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-321-nguoi-đuoc-trung-tuyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-321-nguoi-đuoc-trung-tuyen"}},[n._v("#")]),n._v(" Chương 321: Ngươi được trúng tuyển")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Tốt, xong!” Trương Huyền cười khanh khách phủi tay."),t("br"),t("br"),n._v("Nhìn thấy hắn bộ dáng thoải mái, tất cả mọi người từng đợt mê muội, muốn thổ huyết."),t("br"),t("br"),n._v("Đây cũng quá không theo lẽ thường ra bài."),t("br"),t("br"),n._v("Giáo sư công hội có văn bản rõ ràng quy định không cho phép thể phạt học sinh, ngươi trực tiếp động thủ đem người ta đánh thành đầu heo, một khi truy cứu tới, ai cũng trả không nổi trách nhiệm này."),t("br"),t("br"),n._v("“Liễu lão sư, ngươi...” Mạc Hoằng Nhất cũng là không còn gì để nói, suýt chút nữa không có ngất đi: “Lão sư không thể đánh học sinh!”"),t("br"),t("br"),n._v("Gia hỏa này cũng là đủ rồi, ngươi nói ngươi khảo hạch danh sư quy củ không hiểu ngược lại cũng thôi, khóa tổng mang qua đi, học sinh tổng dạy qua đi... Đều khảo hạch nhị tinh danh sư người, làm sao có thể liền điểm ấy quy củ cũng đều không hiểu?"),t("br"),t("br"),n._v("Lão sư muốn lấy đức phục người, thể phạt học sinh, một khi bị phát hiện, rất có thể liền sẽ bị thủ tiêu giáo sư tư chất."),t("br"),t("br"),n._v("“Đánh học sinh, ai nói ta đánh học sinh?” Trương Huyền mở to hai mắt nhìn, nhìn về phía nam sinh trước mắt: “Ta đánh ngươi nữa sao?”"),t("br"),t("br"),n._v("“Không! Lão sư đối ta ân trọng như núi, là vì cứu ta, không phải đánh ta!” Nam sinh vội vàng dập đầu, dùng hở miệng hô."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Đám người phát điên."),t("br"),t("br"),n._v("Đại ca, ngươi dám phách lối nữa một chút sao?"),t("br"),t("br"),n._v("Đây là muốn đem nhiều người như vậy đương mù lòa ah!"),t("br"),t("br"),n._v("Chúng ta tận mắt thấy ngươi đánh người, không thừa nhận ngược lại cũng thôi, lại còn uy hiếp học sinh, để hắn đừng bảo là, quả thực liền là cùng hung cực ác, phát rồ!"),t("br"),t("br"),n._v("Gặp qua làm cho người ta không nói được lời nào lão sư, chưa thấy qua như thế im lặng."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Hơi vung tay cánh tay, Tạ viện trưởng sắc mặt tái xanh."),t("br"),t("br"),n._v("Thân là viện trưởng, nhất tinh danh sư, giáo sư đánh học sinh sự kiện liền phát sinh ở trước mắt, thậm chí còn đối học sinh tiến hành uy hiếp, cũng quá không để hắn vào trong mắt!"),t("br"),t("br"),n._v("“Liễu lão sư, đối học sinh thể phạt, đã vi phạm với làm vì lão sư bản chức. Chuyện ngày hôm nay, ta sẽ như thực báo cáo công hội, ngươi liền đợi đến tiếp nhận công hội phán quyết đi! Mặt khác, chúng ta Thiên Vũ học viện cũng sẽ không thuê ngươi loại thái độ này ác liệt, không có sư đức người, còn xin lập tức rời đi!”"),t("br"),t("br"),n._v("Nói khoát tay áo."),t("br"),t("br"),n._v("“Phán quyết? Không có sư đức?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt một chút, nhìn về phía trước mắt Tạ viện trưởng: “Đây là phán đoán của ngươi?”"),t("br"),t("br"),n._v("“Không sai!” Tạ viện trưởng khẽ nói."),t("br"),t("br"),n._v("“Tốt a!” Trương Huyền lắc đầu: “Liền ngươi cái này nhãn lực, thật sự là nhất tinh danh sư? Nói thật, ta thật muốn hỏi một câu... Ngươi có phải hay không mù ah!”"),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("“Gia hỏa này trực tiếp mắng Tạ viện trưởng mù?”"),t("br"),t("br"),n._v("“Mẹ nó, thể phạt học sinh, nhục mạ nhất tinh danh sư, gia hỏa này điên rồi sao?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Các lão sư khác tất cả đều con mắt trừng đến sắp rơi ra hốc mắt."),t("br"),t("br"),n._v("Đây chính là Thiên Vũ vương quốc viện trưởng, Tông sư đỉnh phong siêu cấp cường giả, đơn thuần thực lực, có thể đủ xếp tới vương quốc mười vị trí đầu, càng là nhất tinh danh sư, nói hắn mắt mù..."),t("br"),t("br"),n._v("Bà mẹ nó!"),t("br"),t("br"),n._v("Vừa rồi chỉ cảm thấy ngươi là phách lối, bây giờ mới biết, ngươi là điên rồi!"),t("br"),t("br"),n._v("Bằng không thì, làm sao nếu ứng nghiệm mời người ta trường học lão sư, trước mặt mọi người đánh học sinh không nói, còn nhục mạ danh sư?"),t("br"),t("br"),n._v("Danh sư không thể nhục, chỉ điều này tội danh, đều có thể để ngươi vạn kiếp bất phục, triệt để xong đời!"),t("br"),t("br"),n._v("Một bên Mạc Hoằng Nhất cũng khóe miệng co giật."),t("br"),t("br"),n._v("Bất quá, hắn biết kẻ trước mắt này, mặc dù tính cách cổ quái, nói chuyện không thèm quan tâm, thế nhưng là có thực lực tuyệt đối, tuyệt không có khả năng bắn tên không đích."),t("br"),t("br"),n._v("Nhịn không được nhìn kỹ quá khứ, xem xét phía dưới, kìm lòng không được sững sờ tại nguyên chỗ: “Cái này...”"),t("br"),t("br"),n._v("Hắn ngẩn người công phu, Tạ viện trưởng đã thổ huyết."),t("br"),t("br"),n._v("Tức giận râu ria nhếch lên, cả người râu tóc trương dương, bất cứ lúc nào cũng sẽ bạo tạc."),t("br"),t("br"),n._v("“Liễu lão sư, ngươi cũng đã biết ngươi đang vũ nhục một vị danh sư!” Cố nén lửa giận, Tạ viện trưởng răng cắn khanh khách rung động."),t("br"),t("br"),n._v("Nếu không phải cố kỵ thân phận, khả năng hiện tại liền đã động thủ đem cái này không biết trời cao đất rộng gia hỏa đánh thành đầu heo."),t("br"),t("br"),n._v("“Vũ nhục danh sư? Ta?” Trương Huyền một mặt thương hại nhìn qua: “Là có người đang vũ nhục danh sư cái thân phận này, chẳng qua không phải ta, mà là ngươi!”"),t("br"),t("br"),n._v("“Ngươi...” Trên người khí tức bắn ra, chính muốn giáo huấn một lần cái này không che đậy miệng gia hỏa, chỉ thấy hai tay của hắn một cõng, đi tới nam sinh trước mặt, thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Huyết ách nhện, một loại đặc thù kỳ quái man thú, hình thể chỉ có người ngón út nhọn lớn nhỏ, lấy thôn phệ sinh mệnh huyết dịch làm thức ăn, sinh hoạt tại âm u hoàn cảnh, ưa thích ban đêm hành động, trong cơ thể có chứa kịch độc.”"),t("br"),t("br"),n._v("Vốn cho rằng gia hỏa này muốn nói gì trọng yếu lời nói, không nghĩ tới lại nói một cái râu ria man thú, đám người tất cả đều tràn đầy kỳ quái."),t("br"),t("br"),n._v("Ngay cả Tạ viện trưởng cũng mày nhăn lại, ngừng lại, muốn nhìn một chút gia hỏa này đến cùng chơi hoa dạng gì."),t("br"),t("br"),n._v("“Loại con nhện này độc, võ giả trúng, trong ngắn hạn sẽ không tử vong, chẳng qua lại tê liệt thần kinh, để cho người ta trong tính cách xuất hiện biến hóa, táo bạo bất an, muốn cùng người chiến đấu... Bởi vì chỉ có thông qua chiến đấu mới có thể làm dịu thể nội độc tố, thu hoạch được bình tĩnh!”"),t("br"),t("br"),n._v("Trương Huyền thanh âm tiếp tục vang lên."),t("br"),t("br"),n._v("“Còn có loại vật này?”"),t("br"),t("br"),n._v("“Trên đời có như thế kỳ quái độc?”"),t("br"),t("br"),n._v("Nghe được huyết ách nhện độc tác dụng, trong lòng mọi người ẩn ẩn toát ra một cái suy đoán, bất quá, không dám xác nhận, đồng loạt nhìn về phía thiếu niên ở trước mắt."),t("br"),n._v(" “Không sai, vị này nam sinh liền trúng phải độc này, hơn nữa đã vô cùng nghiêm trọng! Cho nên, hắn mới thường xuyên cùng người đánh nhau, nóng nảy bất an, khó mà ngăn chặn. Các ngươi có phát hiện hay không, hắn vừa rồi thi triển võ kỹ thời điểm, mặc dù chiêu số tinh chuẩn, trong mắt lại mang theo một tia ửng hồng. Bình thường người tu luyện, coi như nhiều lần kinh lịch chiến đấu, cũng không có khả năng ảnh hưởng tinh thần, để con mắt cải biến nhan sắc.”"),t("br"),t("br"),n._v("Trương Huyền nhìn quanh một tuần."),t("br"),t("br"),n._v("“Ửng hồng... Mới vừa rồi còn thật có, chỉ bất quá ta tưởng rằng tâm tình của hắn kích động đưa tới!”"),t("br"),t("br"),n._v("“Đúng vậy a, ta cũng nhìn thấy, cũng tưởng rằng hắn võ kỹ đặc hữu nguyên nhân, chẳng lẽ là... Một loại kịch độc?”"),t("br"),t("br"),n._v("“Làm sao có thể? Nếu như trúng độc, chúng ta làm sao một chút cũng không nhìn ra?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhớ tới vừa rồi thiếu niên thi triển võ kỹ lúc tình cảnh, đám người tất cả đều sững sờ."),t("br"),t("br"),n._v("Gia hỏa này ánh mắt là có chút không đúng, bất quá, một chút võ kỹ cũng có thể khiến người ta ánh mắt cải biến, bởi vậy đều không có hướng phương diện này suy nghĩ."),t("br"),t("br"),n._v("Lại nói, ai có thể biết trên thế giới còn có một loại độc, có thể ảnh hưởng người tinh thần, để cho người ta táo bạo dễ đấu?"),t("br"),t("br"),n._v("“Vừa rồi ta cũng không phải là đánh hắn, mà là dùng phương pháp đặc thù đem trong cơ thể hắn loại độc này dịch bức đi ra, nếu như không tin có thể nhìn xuống đất bên trên hắn vừa chảy ra huyết dịch!”"),t("br"),t("br"),n._v("Trương Huyền tiện tay một chỉ."),t("br"),t("br"),n._v("Thuận ngón tay hắn phương hướng nhìn lại, đám người quả nhiên thấy trên mặt đất có vị này Liễu lão sư đánh nam hài, chảy ra huyết dịch."),t("br"),t("br"),n._v("Lúc này huyết dịch, đã biến thành màu đen, tựa như chất keo ngưng kết tại mặt đất, mà cùng hắn tiếp xúc nham thạch, xuất hiện cái hố nhỏ, giống như là bị thứ gì hủ thực, dị thường quỷ dị."),t("br"),t("br"),n._v("“Là kịch độc...”"),t("br"),t("br"),n._v("Tạ viện trưởng sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Bình thường huyết dịch giọt trên mặt đất, không có khả năng nhanh như vậy liền ngưng đọng, càng không khả năng đem nham thạch đều ăn mòn ra cái hố."),t("br"),t("br"),n._v("Chỉ có trong máu ẩn chứa kịch độc mới có thể xuất hiện loại tình huống này."),t("br"),t("br"),n._v("Nói cách khác..."),t("br"),t("br"),n._v("Nam hài trong cơ thể hoàn toàn chính xác trúng kịch độc! Mà vừa rồi vị này Liễu lão sư cũng không phải là đánh người, mà là... Trừ độc!"),t("br"),t("br"),n._v("“Huyết ách nhện độc, trúng ba tháng liền sẽ chết, hắn bộ dáng bây giờ, có lẽ mới trúng hơn một tháng, không tính là nguy hiểm, bởi vậy, nhờ vào hắn bản thân năng lực mới có thể áp chế ở, không bị phát hiện.”"),t("br"),t("br"),n._v("Trương Huyền nhìn về phía Tạ viện trưởng: “Dù vậy, Thiên Vũ học viện, vương quốc học phủ cao nhất, để học sinh thân trúng kịch độc mà không biết! Ta hảo tâm cứu chữa, thế mà vu hãm ta đánh học viên, còn muốn cáo bên trên công lại... Đường đường viện trưởng, nhất tinh danh sư liền cái này đều không nhìn ra, xin hỏi... Không phải mắt mù, là cái gì?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Tạ viện trưởng sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Rõ ràng như vậy máu độc không nhìn ra, còn quát lớn đối phương, để sắc mặt hắn đỏ lên, muốn giải thích, nhưng cũng không giải thích được."),t("br"),t("br"),n._v("“Liễu lão sư còn xin bớt giận, độc dính dáng độc sư chức nghiệp, rất nhiều người đều nghe đến đã biến sắc, không biết cũng rất bình thường!”"),t("br"),t("br"),n._v("Thấy bầu không khí xấu hổ, Mạc Hoằng Nhất đi tới."),t("br"),t("br"),n._v("Hắn nói không sai, dính dáng độc phương diện sự tình, rất nhiều tư liệu đều bị Độc điện phong tỏa, không ít người đều không rõ lắm."),t("br"),t("br"),n._v("Lão sư lại lấy chính đạo tự cho mình là, rất ít liên quan đến loại này hạ cửu lưu đồ vật, tự nhiên kiến thức nửa vời."),t("br"),t("br"),n._v("Vừa mới biết được Trương Huyền không có khả năng bắn tên không đích, nhìn thấy nam hài phun ra huyết dịch ngậm độc, hắn cũng là mười phần khiếp sợ."),t("br"),t("br"),n._v("Chỉ nhìn thi triển võ kỹ, liền biết hắn trúng loại nào kịch độc, hơn nữa tiện tay hỗ trợ giải quyết... Nói rõ vị này Trương Huyền, đang dùng độc phương diện, cũng tuyệt đối đạt đến cảnh giới tông sư."),t("br"),t("br"),n._v("Tam tinh thư hoạ sư, nhị tinh tuần thú sư, thậm chí vượt qua tứ tinh luyện đan sư cao minh luyện đan thuật... Gia hỏa này đến cùng còn có bao nhiêu để cho người ta không biết bản lĩnh?"),t("br"),t("br"),n._v("Trước đó một mực còn cảm thấy, đối phương cũng là danh sư, luyện đan bên trên mạnh hơn hắn, những nghề nghiệp khác phương diện, chưa hẳn so ra mà vượt bản thân, bây giờ mới biết... Đối phương khẳng định còn có không ít chức nghiệp, căn bản không có biểu diễn ra."),t("br"),t("br"),n._v("“Coi như không biết kịch độc, vừa rồi người học sinh này đã nói, ta là tại cứu hắn, thế mà còn muốn đối ta chất vấn, nhất tinh danh sư liền loại này sức quan sát?”"),t("br"),t("br"),n._v("Không biết Mạc Hoằng Nhất ý nghĩ trong lòng, Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Không nói cái này còn tốt, nói chuyện, đám người toàn đều không còn gì để nói."),t("br"),t("br"),n._v("Cái kia học sinh nói là ngươi tại cứu hắn, nhưng ngươi vừa đánh xong người, lại loại thái độ đó, ai cũng tưởng rằng đang uy hiếp đối phương, dọa đến hắn chỉ có thể trả lời như vậy."),t("br"),t("br"),n._v("Ai nghĩ đến... Hắn nói là thật tâm lời nói."),t("br"),t("br"),n._v("“Là ta không có quan sát tốt, hổ thẹn nhất tinh danh sư xưng hô...”"),t("br"),t("br"),n._v("Bị liên tục chất vấn, Tạ viện trưởng sắc mặt trắng bệch, cả người giống như là già đi mười tuổi."),t("br"),t("br"),n._v("Vốn cho rằng thông báo tuyển dụng cái lão sư, cho đối phương khảo hạch, làm sao đều không nghĩ tới, nhiều chuyện như vậy, chính hắn cũng không phát hiện."),t("br"),t("br"),n._v("“Tốt, cũng không cần mất tinh thần, về sau học tập cho giỏi, gặp được sự tình không muốn ngông cuồng ước đoán là được!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Đám người lần nữa một cái lảo đảo."),t("br"),t("br"),n._v("Đây chính là Thiên Vũ học viện viện trưởng, thế mà bị một cái nho nhỏ lão sư, giáo huấn cùng học sinh đồng dạng..."),t("br"),t("br"),n._v("Một mặt im lặng, đám người nhưng cũng âm thầm chấn kinh."),t("br"),t("br"),n._v("Vị này Liễu lão sư, nhìn ra liền viện trưởng đều không nhìn ra kịch độc, tiện tay giải quyết hai cái học sinh vấn đề, nhãn lực, thủ đoạn chi cao, không phải tận mắt nhìn thấy, tuyệt đối khó có thể tin."),t("br"),t("br"),n._v("Trong nháy mắt, đều đối vị này tuổi không lớn lắm lão sư, lộ ra ngưỡng mộ núi cao bội phục cảm giác."),t("br"),t("br"),n._v("Mọi người ở đây ánh mắt tập trung tới, muốn biết hắn đến cùng làm thế nào nhìn ra được nam hài thân trúng kịch độc thời điểm, chỉ thấy vị này Liễu lão sư, cao nhân khí thế hoàn toàn không có, tựa như biến thành một cái gấp đón đỡ thành tích học sinh, gãi gãi đầu, nhìn lại: “Cái này... Ta như vậy, có thể được trúng tuyển sao?”"),t("br"),t("br"),n._v("“Trúng tuyển, đương nhiên có thể trúng tuyển!” Tạ viện trưởng mắt tối sầm lại, liên tục gật đầu."),t("br"),t("br"),n._v("Nếu như ngươi loại năng lực này đều không trúng tuyển, ta viện trưởng này cũng không cần làm nữa..."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-329"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-331"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);