(window.webpackJsonp=window.webpackJsonp||[]).push([[1557],{1912:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-323-nguoi-bi-khai-tru"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-323-nguoi-bi-khai-tru"}},[n._v("#")]),n._v(" Chương 323: Ngươi bị khai trừ!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Giải quyết xong hai cái học sinh vấn đề, Tạ viện trưởng lại không nghi ngờ năng lực của hắn, trực tiếp đem lớp học sinh lệnh bài giao tới."),t("br"),t("br"),n._v("Có tại Hồng Thiên học viện giảng bài kinh nghiệm, biết những lệnh bài này, mỗi một mai đều đại biểu một cái học sinh, thường phục tiến trữ vật giới chỉ, hướng phòng học bên này đi tới."),t("br"),t("br"),n._v("Liên tục đi nhầm mấy lần, tìm người nghe ngóng nửa ngày, mới tìm được địa phương."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Đẩy cửa đi vào."),t("br"),t("br"),n._v("Cái này phòng học so với lúc trước hắn tại Thiên Huyền học viện lớn, chừng bảy, tám trăm mét vuông, mặt đất toàn bộ từ bóng loáng mỏm đá xanh đá rèn luyện khảm nạm mà thành, hai bên điêu khắc man thú thân hình, uy vũ khí phách."),t("br"),t("br"),n._v("Gian phòng chính giữa mười mấy cái học sinh, chính vây quanh một cái bộ dáng mỹ lệ nữ hài bên cạnh, líu ríu nói cái này thứ gì, bởi vì quá mức đầu nhập, tựa hồ không có phát hiện hắn đến."),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đẩy cửa thanh âm nói lớn không lớn, nói nhỏ cũng không nhỏ, đám người này thế mà một cái cũng không phát hiện, tính cảnh giác cũng không tránh khỏi quá kém chút."),t("br"),t("br"),n._v("Đi về phía trước hai bước, đang muốn nói một tiếng, bọn hắn nói chuyện trời đất nội dung liền truyền lọt vào trong tai."),t("br"),t("br"),n._v("“Học tỷ, ngươi hôm qua thật gặp Trương Huyền danh sư? Hơn nữa có ý định muốn thu ngươi làm học sinh?”"),t("br"),t("br"),n._v("“Đây chính là Trương Huyền danh sư, Thiên Vũ vương quốc hiện tại đệ nhất thiên tài, rất muốn gặp một lần... Học tỷ, về sau ngươi thật thành học sinh của hắn, nhất định phải mang ta nhìn một chút, ta là hắn trung thực Fan hâm mộ!”"),t("br"),t("br"),n._v("“Ta cũng thế...”"),t("br"),t("br"),n._v("Vây quanh nữ hài học sinh, nguyên một đám hưng phấn sắc mặt đỏ lên, mặt mũi tràn đầy hoa si bộ dáng."),t("br"),t("br"),n._v("“Gặp qua ta? Còn có ý thu làm học sinh?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, lập tức nhìn sang."),t("br"),t("br"),n._v("Có thể xác nhận, trước mắt cô gái này tuyệt đối chưa thấy qua."),t("br"),t("br"),n._v("Lại thêm đừng nói cái gì thu học sinh."),t("br"),t("br"),n._v("Lại nói, hôm qua đến Danh Sư đường, khảo hạch xong liền bắt đầu đọc sách, một mực không có đi ra, làm sao có thể gặp qua một cái Thiên Vũ học viện học sinh?"),t("br"),t("br"),n._v("Hắn nhưng là liền học viện ở nơi nào, cũng không tìm tới..."),t("br"),t("br"),n._v("“Yên tâm đi, Trương sư hiện đang đi làm Danh Sư đường lời nhắn nhủ nhiệm vụ, một khi trở về, tất nhiên là nhị tinh danh sư, đến lúc đó, ta khẩn cầu một cái, để hắn cho lớp chúng ta giảng cái khóa...”"),t("br"),t("br"),n._v("Ở giữa nữ hài, cười khanh khách gật đầu, lời nói nói phân nửa, thấy được Trương Huyền, đôi mi thanh tú nhăn lại: “Ngươi là ai? Chạy đến lớp chúng ta cấp làm gì?”"),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Vừa dứt lời, vây chung quanh tất cả học sinh, đồng thời xoay người lại, nguyên một đám tràn đầy cảnh giác."),t("br"),t("br"),n._v("“Ta thụ học viện sai khiến, học kỳ này làm các ngươi mang khóa lão sư!” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Chúng ta lão sư?”"),t("br"),t("br"),n._v("Tất cả học sinh tất cả đều sững sờ."),t("br"),t("br"),n._v("Trước đó ghi chép ngọc tinh khoảng cách quá xa, nhìn không rõ ràng, đám người nghe nói hắn liền học sinh đều đánh, vốn cho rằng ít nhất là cái cao lớn thô kệch, hình thể hùng tráng gia hỏa, không nghĩ tới dung mạo nhìn như thế thanh tú."),t("br"),t("br"),n._v("Trương Huyền ngụy trang cái này Liễu Trình, hai bốn hai lăm tuổi, hình thể hơi gầy, mặc dù không bằng hắn bản tôn tuấn mỹ, vẻ ngoài điều kiện cũng là rất có mị lực."),t("br"),t("br"),n._v("“Gặp qua lão sư!”"),t("br"),t("br"),n._v("Ở giữa nữ hài Mộc Tuyết Tinh ho khan một tiếng, đám người cái này mới phản ứng được, đồng loạt cúi đầu."),t("br"),t("br"),n._v("“Ừm! Ta gọi Liễu Trình, về sau có thể gọi ta Liễu lão sư, ta sẽ chỉ điểm các ngươi tu vi, để thực lực các ngươi tiến bộ!”"),t("br"),t("br"),n._v("Nói xong Trương Huyền mấy bước đi tới bàn giáo viên trước mặt, ngồi xuống, nhìn về phía cách đó không xa nữ hài: “Ngươi gặp qua Trương Huyền danh sư?”"),t("br"),t("br"),n._v("“Hồi bẩm Liễu lão sư, ta cũng là vận khí tốt mà thôi!”"),t("br"),t("br"),n._v("Mộc Tuyết Tinh liền vội vàng gật đầu."),t("br"),t("br"),n._v("“Nói như vậy thực sự từng gặp rồi? Không biết hắn dáng dấp cái gì bộ dáng?” Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Không nghĩ tới vị này Liễu lão sư sẽ hỏi, Mộc Tuyết Tinh chần chờ một chút: “Hắn dáng người so Liễu lão sư cao hơn hơn mấy phần, hình thể hùng tráng, mang trên mặt mỉm cười mê người, một thân vải xanh áo gai, hai mắt như điện, cho người ta một loại không dính khói lửa trần gian, chao liệng cửu thiên cảm giác...”"),t("br"),t("br"),n._v("Nàng mặc dù nghe không ít liên quan tới Trương sư sự tích, nhưng người nào cũng không nói qua hắn như thế nào ah!"),t("br"),t("br"),n._v("Bất quá, lúc này vị này Liễu lão sư đã hỏi thăm, một khi không nói, tất nhiên gây nên hoài nghi, đành phải kiên trì dựa theo tưởng tượng bộ dáng miêu tả."),t("br"),t("br"),n._v("Trương Huyền một mặt cổ quái."),t("br"),t("br"),n._v("Chỉ nhìn từ bên ngoài, hắn liền là người bình thường, lúc nào như thế anh minh thần võ, bộ dáng bất phàm rồi?"),t("br"),t("br"),n._v("“Theo ta được biết, nhị tinh danh sư muốn thu đồ đệ, khẳng định sẽ có vô số người chen chúc mà tới. Vị này Trương Huyền danh sư càng là thiên tài trong thiên tài, lực hiệu triệu càng lớn, vì sao muốn thu ngươi làm học sinh?”"),t("br"),t("br"),n._v("Hiện tại đã xác định đối phương đang nói dối, theo vẻ mặt của mọi người, cũng nhìn ra một chút mánh khóe, Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Không nghĩ tới vị này chỉ biết đánh học sinh lão sư, vấn đề như thế xảo trá, Mộc Tuyết Tinh thân thể nhoáng một cái, cố nén muốn thổ huyết xúc động, kiên trì hướng xuống sắp xếp: “Là ta hôm qua giúp hắn một vấn đề nhỏ, Trương sư nhìn thấy ta thiên phú cũng không tệ lắm, lúc này mới động tâm tư, cụ thể... Vì cái gì, ta cũng không biết!”"),t("br"),t("br"),n._v("“Thì ra là thế... Đã Trương sư muốn thu ngươi làm học sinh, ta một cái bình thường lão sư cũng không tốt tới tranh đoạt. Chim khôn biết chọn cây mà đậu, như vậy, ngươi đi đi, đây là thân phận lệnh bài của ngươi!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, Trương Huyền lấy ra một cái lệnh bài ném tới."),t("br"),t("br"),n._v("Mặc dù không biết cô bé này kêu cái gì, nhưng lệnh bài bên trong có nàng giọt huyết dịch, căn cứ khí tức, vẫn là rất dễ dàng tìm tới."),t("br"),t("br"),n._v("“Đây là ý gì...”"),t("br"),t("br"),n._v("Tiếp nhận lệnh bài, Mộc Tuyết Tinh sửng sốt."),t("br"),n._v(" Kịch bản không phải như thế."),t("br"),t("br"),n._v("Một người cả đời không có khả năng chỉ có một cái lão sư, học sinh của mình nếu như bị mặt khác danh sư nhìn trúng, hắn cũng là rất có vinh dự."),t("br"),t("br"),n._v("Không nên hắn nghe được Trương sư nguyện ý thu làm học sinh, cảm thấy kinh ngạc, đối với mình lại thêm coi trọng hơn sao?"),t("br"),t("br"),n._v("Tướng lệnh bài ném qua đến có ý tứ gì?"),t("br"),t("br"),n._v("“Không có ý gì!” Trương Huyền khoát khoát tay, mí mắt vừa nhấc: “Nói đúng là... Ngươi bị khai trừ!”"),t("br"),t("br"),n._v("“Khai trừ...”"),t("br"),t("br"),n._v("Mộc Tuyết Tinh thân thể mềm mại nhoáng một cái, suýt chút nữa không có một ngụm máu tươi phun ra ngoài."),t("br"),t("br"),n._v("Ta là thiên tài, siêu cấp thiên tài, vô luận đi đến lão sư kia môn hạ, đều là bị người tranh đoạt tồn tại, thấy lần đầu tiên liền bị khai trừ?"),t("br"),t("br"),n._v("Ngươi xác định không phải đang nói đùa?"),t("br"),t("br"),n._v("“Không sai, làm học sinh, không giữ khuôn phép, dựa dẫm tiểu thông minh, lừa gạt lão sư... Ta muốn làm gì dùng? Yên tâm, ta sẽ đem chuyện này chi tiết bẩm báo học viện, sau đó gửi công văn đi thông cáo tất cả học sinh, hoan nghênh tất cả mọi người đến bình luận, sẽ không ủy khuất ngươi!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Mộc Tuyết Tinh thân thể nhoáng một cái."),t("br"),t("br"),n._v("Nàng bởi vì dung mạo, thiên phú, tại toàn bộ Thiên Vũ học viện học sinh bên trong, đều là tài năng xuất chúng nhất, danh khí rất lớn. Nếu quả thật bị toàn trường thông cáo khai trừ, về sau còn thế nào có mặt gặp người?"),t("br"),t("br"),n._v("Sớm biết vị lão sư này không theo lẽ thường ra bài, nhưng... Đây cũng quá không theo lẽ thường!"),t("br"),t("br"),n._v("Dựa theo nàng suy nghĩ, coi như đối phương không quá tin tưởng mình nhận biết Trương sư, cũng hẳn là có chỗ cố kỵ."),t("br"),t("br"),n._v("Dù sao vạn nhất nhận biết, có có thể trở thành Trương sư học sinh, cái thân phận này, đừng nói một cái bình thường lão sư, coi như viện trưởng đều muốn ước lượng mấy phần."),t("br"),t("br"),n._v("Ai ngờ gia hỏa này... Căn bản không có ước lượng ý tứ, trực tiếp khai trừ..."),t("br"),t("br"),n._v("Nếu như nói giáo viên khảo hạch thành tích hạng chót, là lão sư đến nhất mất mặt sự tình, bị khai trừ thì là học sinh nhất mất mặt."),t("br"),t("br"),n._v("Dù là về sau tu vi lại cao hơn, chỉ cần trên lưng cái tội danh này, cũng giống như bị một cái đại thủ ngăn chặn, không cách nào xoay người."),t("br"),t("br"),n._v("“Ta không có lừa gạt lão sư...”"),t("br"),t("br"),n._v("Cắn răng một cái, Mộc Tuyết Tinh vội nói."),t("br"),t("br"),n._v("“Không có lừa gạt ta?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu, đứng dậy, hai tay chắp sau lưng, trong phòng học chậm rãi đi lại: “Đầu tiên, ta vào cửa lớn tiếng như vậy âm, ngươi làm Ích Huyệt cảnh cường giả, thế mà không có phát hiện, còn cùng những người khác chậm rãi mà nói, ngươi cảm thấy ta sẽ tin tưởng?”"),t("br"),t("br"),n._v("“Lão sư đến, mà không cung kính, cố ý không rảnh để ý, đã vi phạm với Thiên Vũ học viện học sinh quy tắc thứ mười bảy đầu, không tôn sư trưởng, nghiêm trọng người nhưng khai trừ!”"),t("br"),t("br"),n._v("“Tiếp theo, cố ý tại ta có thể nghe được phạm vi, nói ngươi biết Trương Huyền danh sư... Trước không nói có phải thật vậy hay không, nếu như là thật, cái này là cố ý muốn dùng Trương sư tên tuổi ép ta. Học sinh quy tắc thứ bốn mươi hai điều quy định, mượn nhờ gia sự, bối cảnh, đối lão sư nói năng lỗ mãng, thậm chí uy hiếp, nghiêm trọng người cũng có thể khai trừ!”"),t("br"),t("br"),n._v("“Nếu như là giả, cái kia chính là cố ý lừa gạt lão sư, vi phạm với quy tắc thứ bốn mươi lăm đầu, lấy hư giả ngôn luận, lừa gạt mê hoặc lão sư, đồng dạng lại được khai trừ!”"),t("br"),t("br"),n._v("“Vô luận cái nào, nghiêm ngặt chấp hành đều sẽ bị khai trừ, ngươi còn có lời gì nói?”"),t("br"),t("br"),n._v("Thiên Vũ học viện học sinh quy tắc, dán đầy sân trường đều là, trên đường tới, đã ghi xuống."),t("br"),t("br"),n._v("Đám hài tử này muốn cho mình cái ra oai phủ đầu... Đã như vậy, vậy trước tiên để bọn hắn biết lợi hại!"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Mộc Tuyết Tinh mặt không có chút máu, liên tục lui về phía sau mấy bước, giống như là nhìn quái vật nhìn về phía trước mắt lão sư."),t("br"),t("br"),n._v("Không nói hắn vừa tới trường học sao?"),t("br"),t("br"),n._v("Làm sao lại nhìn trời võ học viện học sinh quy tắc biết đến rõ ràng như vậy?"),t("br"),t("br"),n._v("Càng quan trọng hơn... Không phải nói hắn ưa thích thể phạt học sinh sao? Làm sao miệng cũng lợi hại như vậy?"),t("br"),t("br"),n._v("“Dọn dẹp một chút đồ vật rời đi đi! Ta giảng bài, ưa thích học sinh có suy nghĩ của mình, có ý nghĩ của mình, nhưng không thích có người cổ động những học sinh khác cùng ta đối nghịch!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay."),t("br"),t("br"),n._v("Nha đầu này một câu, liền để những học sinh khác đến đây bái kiến, đủ thấy trong chúng nhân có rất lớn uy tín."),t("br"),t("br"),n._v("Nếu như gặp mặt lần thứ nhất không gõ một cái, về sau khẳng định không quản được."),t("br"),t("br"),n._v("Mặc dù học tập tự do, dạy học không phải luyện đan, nghiêm ngặt dựa theo quá trình, nhưng mang học sinh ngày đầu tiên, không đem đối phương trấn trụ, để hắn nghe lời, một khi nhảy nhót, quản lý không ở là chuyện nhỏ, càng quan trọng hơn là... Đối phương cái gì đều học không được."),t("br"),t("br"),n._v("Khinh thị ngươi vị lão sư này, tri thức tự nhiên cũng sẽ không hảo hảo học."),t("br"),t("br"),n._v("“Lão sư... Mộc học tỷ là lớp chúng ta có thiên phú nhất học viên, nay tuổi chưa qua mười tám tuổi, liền đạt tới Ích Huyệt cảnh, là lớp chúng ta tất cả học sinh phấn đấu mục tiêu cùng phương hướng, còn xin lão sư thu hồi mệnh lệnh đã ban ra!”"),t("br"),t("br"),n._v("Thấy vị này mới tới lão sư vừa đến liền muốn khai trừ đại tỷ của bọn hắn lớn, Mạnh Đào nhịn không được tiến lên."),t("br"),t("br"),n._v("“Có thiên phú nhất học viên? Mục tiêu cùng phương hướng?”"),t("br"),t("br"),n._v("“Bảy tuổi luyện khí, trọn vẹn mười một năm, nuốt ba cái Nhuận Huyệt đan, hơn mười viên thuốc, mới mở ra mười hai chỗ huyệt vị, lực lượng miễn cưỡng đạt tới mười hai đỉnh.”"),t("br"),t("br"),n._v("“Tu luyện một bộ Phù Thủy chưởng (phù thủy = bơi), ba năm tiểu thành, thậm chí không thuần thục, cùng người chiến đấu không cách nào phát huy toàn lực!”"),t("br"),t("br"),n._v("“Tu luyện chân khí, ngưng tụ tại thể, chí ít có bảy chỗ tĩnh mạch triệt để bị đục ngầu chân khí phong kín, khó mà tinh tiến, Thông Huyền cảnh là một đại quan...”"),t("br"),t("br"),n._v("Nói đến đây, Trương Huyền quay đầu nhìn về phía cách đó không xa Mạnh Đào, thanh âm không hề bận tâm: “Cái này... Cũng có thể được xưng là thiên tài? Ngươi nói đùa cái gì!”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Mộc Tuyết Tinh sắc mặt trong nháy mắt trở nên trắng bệch."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-331"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-333"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);