(window.webpackJsonp=window.webpackJsonp||[]).push([[1558],{1913:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-324-cac-hoc-sinh-choang-vang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-324-cac-hoc-sinh-choang-vang"}},[n._v("#")]),n._v(" Chương 324: Các học sinh choáng váng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Người người đều biết nàng tốc độ tu luyện rất nhanh, là thiên tài."),t("br"),t("br"),n._v("Nhưng lại không biết bởi vì gia thế nguyên nhân, những năm này nuốt đan dược quá nhiều, trong cơ thể sớm đã lưu lại không ít độc tố, phía sau tu luyện, mười phần khó khăn."),t("br"),t("br"),n._v("Nếu không, đạt tới Ích Huyệt cảnh liền có thể xin tốt nghiệp, cũng không cần một mực lưu tại học viện mà không rời đi."),t("br"),t("br"),n._v("Loại tình huống này, ngay cả bên trên một vị lão sư, đều không nhìn ra, cái này mới vừa vào đến liền nói không sai chút nào, không khỏi thật là đáng sợ đi!"),t("br"),t("br"),n._v("Chẳng lẽ... Hắn cũng không bạo lực lão sư, mà là một vị thật có đại bản lĩnh người?"),t("br"),t("br"),n._v("Mộc Tuyết Tinh thân thể mềm mại run rẩy."),t("br"),t("br"),n._v("Nếu không phải như thế, lại làm sao có thể liếc mắt liền nhìn ra trong cơ thể nàng bảy chỗ kinh mạch triệt để bị đục ngầu chân khí phong kín, Thông Huyền cảnh là một cửa ải đại nạn?"),t("br"),t("br"),n._v("Mạnh Đào cũng không nghĩ tới vị lão sư này mới mở miệng nói ra nhiều như vậy, coi như hắn cùng mộc học tỷ mỗi ngày cùng một chỗ đến trường, cũng cũng không biết, không tự chủ được cương tại nguyên chỗ, muốn giúp đỡ nói chuyện, cũng không biết trả lời như thế nào, á khẩu không trả lời được."),t("br"),t("br"),n._v("“Mộc học tỷ lần trước học viện thi đấu, xếp hạng thứ mười, là học viện nổi danh thiên tài, người người đều biết, làm sao... Liền không gọi là thiên tài?”"),t("br"),t("br"),n._v("Một cái nữ học sinh không phục."),t("br"),t("br"),n._v("“Thiên tài, không phải trong thời gian ngắn thực lực tăng lên nhanh, mà là có thể đi được càng xa! Nàng tuổi không lớn lắm, đạt tới Ích Huyệt cảnh, được cho tiến bộ nhanh, nhưng trong cơ thể đan độc quá nhiều, thậm chí tâm lý cũng sinh ra ỷ lại, coi như về sau có thể tiến bộ, cũng có hạn!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đã hướng dẫn, học sinh tư liệu cơ bản trường học vẫn phải có, hơi vừa so sánh, liền có thể biết cái này dung mạo không kém gì Vương Dĩnh nữ hài, liền là trong lớp thực lực mạnh nhất Mộc Tuyết Tinh."),t("br"),t("br"),n._v("Biết danh tự, hơi tra một cái, thân thế, bối cảnh, cũng là liếc qua thấy ngay."),t("br"),t("br"),n._v("Cô bé này, phụ thân là một vị nổi danh nhị tinh luyện đan sư, đối người khác mà nói, vô cùng trân quý đan dược, đối với nàng mà nói, không chút nào thiếu."),t("br"),t("br"),n._v("Đã là vượt qua những bạn học khác ưu thế, cũng là nàng về sau tu luyện thế yếu."),t("br"),t("br"),n._v("Mặc dù không dùng Thiên Đạo thư viện, nhưng hắn tâm cảnh khắc độ đạt tới 1 0.1, am hiểu quan sát cân nhắc, hơn nữa đối với luyện đan sư quen thuộc nhất, trong cơ thể có hay không đan độc tích lũy, một chút liền có thể nhìn ra."),t("br"),t("br"),n._v("Dù sao, bất kể nói thế nào hắn đều là nhờ vào bản lĩnh thật sự khảo hạch nhị tinh danh sư, nếu như ngay cả chút vấn đề nhỏ này đều phát giác không ra, cũng sẽ không cần lăn lộn."),t("br"),t("br"),n._v("Cái này Mộc Tuyết Tinh, hiện tại tu vi là không tệ, đáng tiếc, quá mức ỷ lại đan dược, đối sau này tiến bộ tạo thành rất lớn gông cùm xiềng xích."),t("br"),t("br"),n._v("Là thuốc ba điểm độc, đan dược cũng như thế, trừ phi đạt tới đan vân cấp bậc."),t("br"),t("br"),n._v("Vẻn vẹn thành đan, sung mãn, hoàn mỹ... Tạp chất vẫn như cũ rất nhiều, không có tinh thuần chân khí rửa sạch, thuốc độc sẽ tuỳ tiện ngăn chặn kinh mạch, để cho người ta tiến bộ càng ngày càng chậm."),t("br"),t("br"),n._v("Chính vì vậy, không đến tu luyện trước mắt, thực sự không cách nào đột phá, rất ít người lựa chọn phục dụng đan dược."),t("br"),t("br"),n._v("Liền cùng sinh bệnh đồng dạng, có thể nhẹ nhõm vượt qua đi, khẳng định không có người uống thuốc, dù là biết rõ uống thuốc qua đi, tốt sẽ nhanh hơn chút."),t("br"),t("br"),n._v("Nàng hiện tại tốc độ tu luyện là nhanh, bởi vì có một cái nhị tinh luyện đan sư phụ thân làm hậu thuẫn, cơ sở đánh cho kiên cố, nhưng nương theo thời gian chuyển dời, mười năm sau, liền chưa hẳn nhanh"),t("br"),t("br"),n._v("Thậm chí Thông Huyền cảnh, đều là một cái rất lớn nan quan."),t("br"),t("br"),n._v("Không riêng gì đan độc, quan trọng hơn là trong lòng sinh ra ỷ lại, đúng không dùng dược vật xông phá tu vi, không có lòng tin."),t("br"),t("br"),n._v("“Nàng không phải thiên tài, ngược lại ngươi cũng không tệ lắm!”"),t("br"),t("br"),n._v("Nói xong Mộc Tuyết Tinh, Trương Huyền nhìn về phía nói chuyện cô gái này: “Tu vi mặc dù không cao chỉ có Bì Cốt cảnh sơ kỳ, lại hảo hảo ma luyện thân thể, tâm cảnh cũng mười phần vững chắc, nếu như ta không nhìn lầm, trong nhà các ngươi tất nhiên có một vị tuần thú sư, lợi dụng man thú tinh huyết giúp ngươi tăng lên tu vi.”"),t("br"),t("br"),n._v("“Làm như vậy mặc dù cùng nuốt dược vật tăng thực lực lên có chút cùng loại, nhưng cũng không hoàn toàn giống nhau. Man thú tinh huyết càng thích hợp nhân thể, không những không sẽ hình thành trở ngại, sẽ còn để cơ bắp càng thêm dày đặc, lực lượng cao hơn!”"),t("br"),t("br"),n._v("“Nếu như ta không nhìn lầm, ngươi hẳn là có thể đủ vượt cấp chiến đấu, Bì Cốt cảnh sơ kỳ, ngay cả trung kỳ người, cũng có thể một trận chiến!”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Nữ hài dọa đến suýt chút nữa nhảy lên."),t("br"),t("br"),n._v("Phụ thân nàng là một vị nhất tinh tuần thú sư, tại nàng đột phá Bì Cốt cảnh thời điểm, hoàn toàn chính xác phục dụng man thú tinh huyết, này mới khiến hắn thể lực khác hẳn với thường nhân, có thể vượt cấp chiến đấu."),t("br"),t("br"),n._v("Chuyện này, cũng chỉ cùng mấy cái hảo bằng hữu nói qua, học viện tuyệt đối không biết rõ tình hình, vị lão sư này nhìn thoáng qua, liền theo miệng nói ra..."),t("br"),t("br"),n._v("Cũng quá dọa người đi!"),t("br"),t("br"),n._v("“Bất quá... Man thú tinh huyết là rất không tệ, nhưng có câu nói gọi quá bổ không tiêu nổi, cho một cái nam hài làm như vậy, có lẽ không có quá mầm họa lớn, mà ngươi... Nữ hài thân thể vốn là suy yếu, trong thời gian ngắn mượn nhờ tinh huyết kích phát thể năng, lực lượng mười phần, một lúc sau, vẫn như cũ sẽ xuất hiện đủ loại tai hoạ ngầm.”"),t("br"),t("br"),n._v("“Thật giống như lâu nằm tại giường bệnh nhân ăn vào ngàn năm nhân sâm, chỉ sợ bệnh không những không chữa khỏi, ngược lại muốn lệnh!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Cô gái này làn da phiếm hồng, máu trong cơ thể lao nhanh hữu lực, cùng tuần thú sư bên trong một chút bí pháp tương tự, hơi chú ý, cũng có thể nhìn ra, coi như tư liệu không có ghi chép, với hắn mà nói cũng không phải việc khó."),t("br"),t("br"),n._v("“Còn có ngươi!”"),t("br"),t("br"),n._v("Nói xong hai nữ hài, Trương Huyền con mắt rơi trên người Mạnh Đào: “Tính cách ngại ngùng, mang theo thư quyển chi khí. Ngón cái cùng ngón giữa trong lúc đó, có nhiều vết chai, khẳng định không phải tu luyện làm ra, góc áo có dính màu mực, nếu như không có đoán sai, ngươi có lẽ ưa thích thư hoạ, hơn nữa thường xuyên vẽ!”"),t("br"),t("br"),n._v("“Đáng tiếc, ánh mắt rời rạc, nhìn bất kỳ vật gì, không có bắt lấy bản chất tìm kiếm linh tính năng lực, coi như có thể vẽ tranh, thành tựu không cao, chớ nói chi là linh động, ý tồn!”"),t("br"),t("br"),n._v("“Về phần ngươi, có lẽ rất ưa thích trà đạo đi! Trên người có lưu hương trà, hương vị cũng rất nhẹ nhàng khoan khoái. Chỉ bất quá, không ba vị, hơn nữa đắm chìm trong trà đạo bên trong thời gian vượt qua tu luyện, để tu vi trì trệ không tiến, lực lượng phù phiếm... Nếu như ta không nhìn lầm ngươi có một năm không có tấn thăng qua tu vi, hơn nữa tu luyện ý chí không kiên định, lão sư đã phân phó về sau, không đi tuân thủ, lại thêm nhiều thời gian, đang miên man suy nghĩ, điển hình mê muội mất cả ý chí...”"),t("br"),n._v("Hai tay chắp sau lưng, Trương Huyền liên tục mở lời, liên tiếp nói bốn cái, tất cả đều mảy may không sai, dọa đến đám người tất cả đều ngây ra như phỗng, lặng ngắt như tờ."),t("br"),t("br"),n._v("Đến cùng tình huống gì?"),t("br"),t("br"),n._v("Vừa mới đám người thương nghị, cố ý hỏi hắn một chút những nghề nghiệp khác bên trên vấn đề, để cho làm khó, kết quả... Còn chưa kịp ra khỏi miệng, người ta chỉ nhìn thoáng qua, liền nói ra tất cả chức nghiệp, chậm rãi mà nói!"),t("br"),t("br"),n._v("Chẳng lẽ vị lão sư này... Nghề nghiệp gì đều tinh thông?"),t("br"),t("br"),n._v("Nếu như không phải, làm sao có thể chỉ bằng góc áo mực nước, mùi trên người, liền có thể phán định ra Mạnh Đào ưa thích thư hoạ, cái thứ hai nam hài ưa thích trà đạo?"),t("br"),t("br"),n._v("Cái này... Cũng quá dọa người đi!"),t("br"),t("br"),n._v("“Đến, mấy người các ngươi đánh một bộ quyền pháp để ta xem một chút!”"),t("br"),t("br"),n._v("Thấy đem mọi người chấn nhiếp, Trương Huyền nhìn về phía còn lại mấy người."),t("br"),t("br"),n._v("Phía trước mấy cái, hết sức rõ ràng, hơn nữa những nghề nghiệp này, cũng là hắn tương đối quen thuộc, cho nên mới có thể một ngụm nói ra."),t("br"),t("br"),n._v("Còn lại những học sinh này sẽ thứ gì, thích gì, cũng có chút nhìn không ra."),t("br"),t("br"),n._v("Thiên Vũ học viện loại này lớn địa phương, cùng Thiên Huyền loại này trường học nhỏ không giống, không ít học sinh trừ tu luyện ra, đều có phụ tu khóa, lựa chọn một cái ưa thích chức nghiệp học tập."),t("br"),t("br"),n._v("Cứ việc không ít người cả một đời cũng không đạt tới học đồ, chính thức chức nghiệp, nhưng đối bản thân hắn tới nói lại một loại yêu thích cùng lịch luyện."),t("br"),t("br"),n._v("Thật giống như Lưu sư đám người từng hướng Điền lão học tập trà đạo."),t("br"),t("br"),n._v("Mấy người bọn hắn đối trà đạo lý giải, cũng không có đạt tới học đồ cấp bậc, lại không trở ngại bọn hắn yêu thích."),t("br"),t("br"),n._v("Bọn gia hỏa này cũng thế, phụ tu chức nghiệp, tất cả đều học tám lạng nửa cân, khoảng cách chân chính học đồ còn có rất lớn một khoảng cách."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Bị trước mắt vị lão sư này chấn nhiếp, những người khác không dám do dự, tất cả đều đứng dậy đánh quyền."),t("br"),t("br"),n._v("Trong nháy mắt toàn bộ phòng học tiếng gió rít gào."),t("br"),t("br"),n._v("Một lát sau, Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Không thể không nói, Thiên Vũ học viện học sinh, chỉnh thể tố chất so Thiên Huyền vương quốc mạnh hơn nhiều lắm, vô luận võ kỹ, tu luyện công pháp, vẫn là đối những nghề nghiệp khác hiểu rõ, đều không phải là Chu Hồng, Mạc Hiểu đám người có thể so sánh."),t("br"),t("br"),n._v("“Ngươi tu luyện võ kỹ  nhìn uy lực không tệ, chiêu số cũng rất nhanh chóng nhưng mãnh liệt, nhưng bộ pháp bên trên theo không kịp, hơn nữa chân khí không tốt, gặp được hơi cường một điểm đối thủ, chỉ cần đứng tại chỗ bất động, công kích dưới nách của ngươi, liền sẽ trực tiếp bại nát bét!”"),t("br"),t("br"),n._v("“Sở dĩ xuất hiện loại tình huống này, cũng không phải là ngươi tu luyện không chăm chú. Nếu như ta không nhìn lầm, có lẽ phụ tu luyện khí sư chức nghiệp đi! Luyện khí sư cần chiết xuất các loại khoáng vật, kim loại. Cần tuyệt đối khí lực, vung vẩy đại chùy, dần dà, cánh tay phải lực lượng viễn siêu cánh tay trái, đưa đến không công bằng. Bạch Điểu Tình Không, chim có hai cánh, một cái cánh lực lượng lớn, một cái lực lượng nhỏ, không xảy ra vấn đề mới là lạ!”"),t("br"),t("br"),n._v("“Ngươi có lẽ ưa thích giám bảo đi, chỉ tiếc, giám bảo là cái đốt tiền chức nghiệp, ngươi mặc tuy là đồng phục, cũng đã mài mòn nghiêm trọng, ít nhất là một kiện nửa năm trở lên quần áo, nói cách khác... Gia cảnh của ngươi điều kiện cũng không tốt. Không có tiền học tập giám bảo? Có thể tìm tới bảo bối gì?”"),t("br"),t("br"),n._v("“Về phần ngươi, một lòng hướng võ, không có lựa chọn bất luận cái gì phụ tu chức nghiệp, điểm ấy không có gì. Nhưng ngươi có phát hiện hay không, ngươi tu luyện mặc dù khắc khổ, lại tu vi không có quá tiến nhanh bước? Cũng không phải là ngươi không đủ cố gắng, cũng không phải là bởi vì thiên phú không tốt, mà là tu luyện nhầm phương hướng! Rõ ràng thích hợp đại khai đại hợp bật hơi công pháp, lại đã luyện thành rút tay về bó chân bế khí chiêu số...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hành tẩu tại tất cả học sinh bên trong, Trương Huyền thuận miệng nói, tất nhiên có một vị học sinh sắc mặt trắng bệch, trên đầu toát ra mồ hôi lạnh."),t("br"),t("br"),n._v("Vị này Liễu lão sư, chỉ xem bọn hắn đánh ra võ kỹ, liền đem bọn hắn trong vấn đề tu luyện, cùng phụ tu chức nghiệp bên trong vấn đề, chỉ điểm đi ra, không sai chút nào!"),t("br"),t("br"),n._v("Nhãn lực mạnh, để cho người ta hoảng sợ."),t("br"),t("br"),n._v("Có thể nói... Thật là đáng sợ!"),t("br"),t("br"),n._v("Bọn hắn mặc dù phụ tu trình độ rất thấp, đều chỉ là tiếp xúc da lông, nhưng cũng hàm cái bảy, tám loại chức nghiệp, một ngụm nói ra, cùng bọn hắn phụ tu lão sư nói đồng dạng, đã không phải là võ tu giáo sư có thể nhìn ra được."),t("br"),t("br"),n._v("Thậm chí... Cho cảm giác của bọn hắn, một chút danh sư đều chưa hẳn có nhãn lực như thế!"),t("br"),t("br"),n._v("“Cái này, cái này...”"),t("br"),t("br"),n._v("Một bên Mộc Tuyết Tinh đã triệt để ngớ ngẩn."),t("br"),t("br"),n._v("Mới vừa rồi còn lời thề son sắt cùng đám người nói, chỉ cần đặt câu hỏi đối phương một chút những nghề nghiệp khác bên trên vấn đề, vị này mới tới lão sư, nhất định có thể biết khó mà lui, kết quả..."),t("br"),t("br"),n._v("Không chờ bọn hắn ra khỏi miệng, người ta đã đem đám người lột cái đáy đi, càng là nói chó má không phải!"),t("br"),t("br"),n._v("Ngươi không phải chỉ biết thể phạt học sinh bạo lực nam sao?"),t("br"),t("br"),n._v("Ngươi không phải liền minh tinh giáo sư đều không phải là phổ thông lão sư sao?"),t("br"),t("br"),n._v("Làm sao cái gì đều sẽ?"),t("br"),t("br"),n._v("Cái gì đều tinh thông?"),t("br"),t("br"),n._v("Cái này..."),t("br"),t("br"),n._v("Học viện đến cùng phái tới một cái dạng gì... Quái vật?"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-332"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-334"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);