(window.webpackJsonp=window.webpackJsonp||[]).push([[1560],{1915:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-326-ba-cau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-326-ba-cau"}},[n._v("#")]),n._v(" Chương 326: Ba câu")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Phòng học."),t("br"),t("br"),n._v("Mộc Tuyết Tinh nhìn chằm chằm phía trước, con mắt đều không nháy mắt một cái."),t("br"),t("br"),n._v("Để cho nàng đi vào, vốn cho rằng Liễu lão sư sẽ nói cái gì, kết quả chỉ là đứng tại một bên, tiếp tục cho những bạn học khác chỉ điểm."),t("br"),t("br"),n._v("“Ngươi lúc tu luyện, có phải hay không cảm thấy chân khí chảy qua ngực thời điểm, có chút tắc nghẽn?”"),t("br"),t("br"),n._v("Liễu lão sư đi tới một thiếu niên trước mặt, thản nhiên nói."),t("br"),t("br"),n._v("“Vâng!” Thiếu niên mặt mũi tràn đầy kinh ngạc, vẫn gật đầu."),t("br"),t("br"),n._v("“Ngươi vừa mới bắt đầu tu luyện công pháp là , môn công pháp này không có vấn đề quá lớn. Bất quá, tại ngươi trùng kích Chân Khí cảnh thời điểm, tìm nhầm kinh mạch, dẫn đến thân thể xuất hiện liệt nửa người, nhưng có việc này?”"),t("br"),t("br"),n._v("“Có...”"),t("br"),t("br"),n._v("Thiếu niên nắm đấm xiết chặt, nhẹ gật đầu."),t("br"),t("br"),n._v("Hoàn toàn chính xác có chuyện này, bất quá, đây là hai năm trước chuyện, lão sư đã hỗ trợ giải quyết."),t("br"),t("br"),n._v("“Sau đó, ngươi đổi tu , bộ công pháp này, so cái trước thoáng bình thản, hoàn toàn chính xác hóa giải ngươi liệt nửa người vấn đề, trên thực tế lại là trị ngọn không trị gốc, để ngươi ẩn tàng tai hoạ ngầm, càng thêm nghiêm trọng!”"),t("br"),t("br"),n._v("Liễu lão sư nhàn nhạt nhìn qua: “Nghe lời của ta, hiện tại đổi tu !”"),t("br"),t("br"),n._v("“Đông Hỏa công?”"),t("br"),t("br"),n._v("Mộc Tuyết Tinh khóe miệng giật một cái."),t("br"),t("br"),n._v("Đây là Thiên Vũ học viện nổi danh cuồng bạo công pháp, bình thường võ giả tu luyện, đều cẩn thận, sợ phạm sai lầm, trong cơ thể hắn có vấn đề, tu luyện cái này, há không cùng muốn chết không có gì khác biệt?"),t("br"),t("br"),n._v("“Ta...” Thiếu niên tựa hồ cũng phát hiện không thích hợp, sắc mặt nhăn nhó."),t("br"),t("br"),n._v("ngantruyen.com"),t("br"),n._v("“Yên tâm, ta ở chỗ này, sẽ không xảy ra chuyện!” Liễu lão sư bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Tốt!” Cắn răng một cái, thiếu niên gật đầu."),t("br"),t("br"),n._v("Đông Hỏa công không tính là gì trân quý công pháp, phòng học liền có, trước đó cũng nhìn qua, lần nữa mang tới một bản, tiện tay lật xem một lần, bắt đầu tu luyện."),t("br"),t("br"),n._v("Chân khí tại thể nội vận chuyển, thiếu niên sắc mặt lúc đỏ lúc trắng, giống như là thừa nhận khó mà ngăn chặn thống khổ, ngay tại Mộc Tuyết Tinh cảm thấy khẳng định không chịu nổi, sẽ tẩu hỏa nhập ma thời điểm, khí tức đột nhiên nhất chuyển."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một tiếng chân khí bạo phá tiếng âm vang lên, thiếu niên trong cơ thể khí thế trực trùng vân tiêu."),t("br"),t("br"),n._v("“Đột, đột phá?”"),t("br"),t("br"),n._v("Bờ môi run rẩy, Mộc Tuyết Tinh thân thể mềm mại run lên."),t("br"),t("br"),n._v("Những bạn học khác cũng nguyên một đám con mắt trừng phải tựa như chiêng đồng, khó có thể tin."),t("br"),t("br"),n._v("Làm mỗi ngày tại một khối đồng học, tình huống của thiếu niên này, bọn hắn biết đến rất rõ ràng, vây ở hiện tại cảnh giới này, hơn nửa năm không đến tiến bộ."),t("br"),t("br"),n._v("Trước một cái lão sư, dùng thật nhiều biện pháp, thậm chí ngay cả nàng, cũng ta van ngươi phụ thân, mua một viên trân quý đan dược... Kết quả, đều không có bất kỳ cái gì hiệu quả."),t("br"),t("br"),n._v("Dựa theo trước đó lão sư thuyết pháp, chỉ có thể dựa vào bản thân ý chí, tiến hành theo chất lượng, ít nhất thời gian hai năm mới có thể có chỗ tinh tiến... Kết quả, vị này Liễu lão sư chỉ làm cho hắn đổi sửa chữa một bộ công pháp, liền trực tiếp đột phá..."),t("br"),t("br"),n._v("Muốn hay không khoa trương như vậy?"),t("br"),t("br"),n._v("“Ngươi không phải lực lượng tắc nghẽn, mà là chân khí xuất hiện vấn đề...”"),t("br"),t("br"),n._v("Không để ý tới đám người chấn kinh, cách đó không xa Liễu lão sư, tựa hồ làm một kiện không có ý nghĩa sự tình, tiếp tục xem hướng vị kế tiếp đồng học."),t("br"),t("br"),n._v("Chừng mười phút đồng hồ về sau, vị bạn học này khí tức nhất chuyển, lực lượng cấp tốc lên cao, vậy “oanh!” Một tiếng, phá vỡ gông cùm xiềng xích, thành công tấn cấp."),t("br"),t("br"),n._v("Bành! Bành! Bành! Bành!"),t("br"),t("br"),n._v("Hai canh giờ không đến, trong phòng cơ hồ tất cả học sinh, tu vi đều có tiến bộ nhảy vọt."),t("br"),t("br"),n._v("Trước mắt vị này Liễu lão sư, tựa như là sửa đá thành vàng Tiên Nhân, thuận miệng chỉ điểm vài câu, liền có thể chính xác tìm ra trong tu luyện tồn tại vấn đề, hơn nữa tiến hành giải quyết."),t("br"),t("br"),n._v("Sắc mặt trắng bệch, Mộc Tuyết Tinh bắp thịt toàn thân cứng ngắc như là gỗ."),t("br"),t("br"),n._v("Một màn này thực sự quá làm cho người ta chấn kinh."),t("br"),t("br"),n._v("Trái tim không tốt, sợ rằng sẽ tại chỗ điên mất."),t("br"),t("br"),n._v("Phụ thân là nhị tinh luyện đan sư, địa vị tương đối cao, nàng bình thường cũng đã gặp không ít minh tinh giáo sư, thậm chí danh sư!"),t("br"),t("br"),n._v("Có thể coi là là danh sư, cũng không có lợi hại như vậy, thuận miệng chỉ điểm, liền để học sinh toàn bộ đột phá..."),t("br"),t("br"),n._v("Cái này đã vượt ra khỏi có thể tưởng tượng phạm trù."),t("br"),t("br"),n._v("“Làm sao lại lợi hại như vậy? Chỉ sợ... Đã có thể cùng Trương sư cùng so sánh...”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, nữ hài thái độ hoàn toàn thay đổi."),t("br"),t("br"),n._v("Trước đó, nghe được vị lão sư này thể phạt học sinh, rất là phẫn nộ, thậm chí mang theo chán ghét."),t("br"),t("br"),n._v("Bây giờ thấy đối phương theo ngón tay chỉ, để đám người nguyên một đám đột phá, giờ mới hiểu được, vị này tuyệt đối là một cái đặc biệt lợi hại lão sư."),t("br"),t("br"),n._v("Thậm chí so với thần tượng của nàng Trương Huyền danh sư, đều không hề yếu."),t("br"),t("br"),n._v("Học viện từ nơi nào thông báo tuyển dụng tới này dạng một cái quái thai?"),t("br"),t("br"),n._v("Nhìn thoáng qua những bạn học khác, Mộc Tuyết Tinh lần nữa run lên."),t("br"),t("br"),n._v("Những bạn học này nhìn về phía Liễu lão sư ánh mắt, cũng cũng thay đổi, tràn đầy sùng bái cùng tin phục."),t("br"),t("br"),n._v("Xem ra, coi như nàng mở miệng lần nữa, để mọi người và đối phương đối nghịch, đoán chừng cũng sẽ không đi nghe lời."),t("br"),t("br"),n._v("Cho tới trưa không đến hai canh giờ, liền để đám người tín nhiệm, vượt qua đối nàng..."),t("br"),t("br"),n._v("Coi như tận mắt thấy, đều cảm thấy giống như là muốn điên rồi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Đến ngươi!”"),t("br"),t("br"),n._v("Không đi quản khiếp sợ muốn muốn điên Mộc Tuyết Tinh, Trương Huyền nhìn về phía trước mắt cách đó không xa một thiếu niên."),t("br"),t("br"),n._v("Đây là cái lớp này cái cuối cùng học sinh, Lộ Xung, võ giả ngũ trọng Đỉnh Lực cảnh sơ kỳ."),t("br"),t("br"),n._v("Thấy lão sư đi tới trước mặt, thiếu niên cũng không nói chuyện, chỉ là ôm quyền, yên tĩnh đứng tại chỗ."),t("br"),t("br"),n._v("“Liễu lão sư, Lộ Xung không thích nói chuyện...” Mạnh Đào vội vàng đi tới trước mặt."),t("br"),t("br"),n._v("“Ừm! Nghe nói hắn vô luận tu vi hay là gia thế, đều rất bình thường, là ở ngoài cửa quỳ ròng rã bảy ngày bảy đêm, mới được phá cách thu lấy?”"),t("br"),t("br"),n._v("Nhớ tới học viện trên tư liệu miêu tả, Trương Huyền nói."),t("br"),n._v(" Cái lớp này, đại bộ phận học sinh phía sau đều có cực mạnh bối cảnh, chỉ có trước mắt vị này Lộ Xung, thân thế không rõ, gia thế không rõ, viết cực kỳ mập mờ, duy nhất ghi lại chính là, hắn bái sư nhập môn, là ở bên ngoài quỳ bảy ngày bảy đêm."),t("br"),t("br"),n._v("“Vâng!” Mạnh Đào gật đầu."),t("br"),t("br"),n._v("“Vì bái sư, có thể quỳ thời gian dài như vậy, là có một cỗ chơi liều, bất quá, tu luyện chỉ dựa vào chơi liều không được!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Trước mắt vị này, tuyệt đối là toàn bộ lớp nhất có thể chịu được cực khổ, đáng tiếc, nhập môn hai năm, cũng chỉ theo Bì Cốt cảnh sơ kỳ, đạt tới Đỉnh Lực cảnh sơ kỳ, chỉ tăng lên một cái đại cấp bậc."),t("br"),t("br"),n._v("“Ngươi bây giờ tu luyện công pháp, là Hàn Vũ quyết đi!”"),t("br"),t("br"),n._v("Cảm khái một câu, tiếp tục xem hướng trước mắt Lộ Xung."),t("br"),t("br"),n._v("Thiếu niên gật đầu, vẫn không có ngôn ngữ."),t("br"),t("br"),n._v("“Liễu sư chớ trách, hắn cứ như vậy, chúng ta đều cùng một chỗ hai năm, tổng cộng nghe hắn nói cũng không cao hơn ba câu...”"),t("br"),t("br"),n._v("Thấy gia hỏa này vẫn như cũ không nói lời nào, Mạnh Đào có chút xấu hổ, lần nữa nói."),t("br"),t("br"),n._v("“Ồ? Hai năm nói chuyện không cao hơn ba câu?”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng: “Ta có thể hiện tại liền để hắn nói chuyện vượt qua ba câu!”"),t("br"),t("br"),n._v("Mạnh Đào sững sờ, đám người cũng tất cả đều đầy là không tin."),t("br"),t("br"),n._v("Ngay cả một bên Mộc Tuyết Tinh cũng không nhịn được lắc đầu."),t("br"),t("br"),n._v("Cái này Lộ Xung, cùng câm điếc không có gì khác nhau, liền xem như nàng cái này Thiên Vũ học viện đều nổi danh đại mỹ nữ, đối phương đều chưa hề nói qua một câu, trước một cái lão sư dạy học thời điểm, cũng đều là nói cái gì làm gì, một chữ cũng sẽ không toác ra tới."),t("br"),t("br"),n._v("Nếu không phải biết hắn có thể nói chuyện, đều cảm thấy có phải hay không người câm."),t("br"),t("br"),n._v("Hai năm đều không nói được ba câu, hiện tại liền để hắn vượt qua? Làm sao có thể!"),t("br"),t("br"),n._v("“Không tin?”"),t("br"),t("br"),n._v("Nhìn ra đám người nghi hoặc, Trương Huyền nhìn về phía thiếu niên ở trước mắt: “Ngươi cũng không tin?”"),t("br"),t("br"),n._v("Lộ Xung vẫn như cũ ngột ngạt."),t("br"),t("br"),n._v("“Cái kia tốt...”"),t("br"),t("br"),n._v("Không lấy vì chọc, Trương Huyền mí mắt vừa nhấc, hai tay chắp sau lưng: “Dựa theo ngươi dạng này tu luyện, trong vòng mười năm đều không đột phá nổi Ích Huyệt cảnh, đời này chỉ sợ đều không thể... Báo thù!”"),t("br"),t("br"),n._v("“Báo thù?”"),t("br"),t("br"),n._v("Đám người sững sờ, làm sao hảo hảo nói, nói thẳng ra báo thù?"),t("br"),t("br"),n._v("Cùng Lộ Xung ở chung hai năm qua, theo chưa từng nghe qua hắn cùng ai có thù ah!"),t("br"),t("br"),n._v("Chính đang kỳ quái, chỉ thấy theo không nói lời nào, như là câm điếc thiếu niên, sắc mặt một cái trở nên trắng bệch, thân thể khẽ run: “Lão, lão sư làm sao biết ta muốn báo thù?”"),t("br"),t("br"),n._v("“Ngươi không cần phải để ý đến ta làm sao mà biết được! Cừu hận nén ở trong lòng, có thể là động lực, cũng có thể là trở ngại. Ngươi bây giờ đem tâm sự ép quá lợi hại, sẽ chỉ làm tu vi nhận gông cùm xiềng xích, khó mà đột phá! Ta biết ngươi không nói lời nào, là sợ nhịn không được, nói ra cừu nhân danh tự, lọt vào trả thù.”"),t("br"),t("br"),n._v("Trương Huyền thần sắc lạnh nhạt: “Yên tâm, chỉ cần ngươi tin tưởng ta, bảo đảm ngươi trong mười ngày, liền có thể báo thù!”"),t("br"),t("br"),n._v("“Mười ngày?”"),t("br"),t("br"),n._v("Lộ Xung trong mắt tràn đầy không thể tin được: “Cừu nhân của ta... Là Thông Huyền cảnh cường giả...”"),t("br"),t("br"),n._v("Hắn chỉ có Đỉnh Lực cảnh sơ kỳ, mà cừu nhân là Thông Huyền cảnh, cả hai chênh lệch hai cái đại cấp bậc, căn bản không có khả năng vượt qua, đừng nói mười ngày, coi như tiếp qua ba, năm năm, đều không thể nào làm được!"),t("br"),t("br"),n._v("“Có thể tin, cũng có thể không tin, lựa chọn từ ngươi! Bất quá... Cơ hội chỉ có một lần!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Lộ Xung mặt mũi tràn đầy xoắn xuýt, chần chờ phút chốc, cắn răng một cái, đầu gối mềm nhũn, “Phù phù!” Quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Còn xin Liễu lão sư dạy ta...”"),t("br"),t("br"),n._v("“Ba câu...”"),t("br"),t("br"),n._v("Nghe được hai người đối thoại, đám người phát điên."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Danh Sư đường phụ cận, một tòa rộng rãi phủ đệ."),t("br"),t("br"),n._v("“Lưu sư, lão sư lúc nào mới có thể thi xong nhị tinh danh sư trở về?”"),t("br"),t("br"),n._v("Triệu Nhã nhịn không được hỏi."),t("br"),t("br"),n._v("“Ta cũng không biết, sáng hôm nay ta đi Danh Sư đường nghe ngóng, nghe nói đã rời đi vương thành thí luyện đi, đoán chừng chí ít hơn mười ngày mới được!”"),t("br"),t("br"),n._v("Nhớ tới Danh Sư đường trả lời chắc chắn, Lưu Lăng nói."),t("br"),t("br"),n._v("“Lâu như vậy...” Triệu Nhã đôi mi thanh tú nhăn thành u cục."),t("br"),t("br"),n._v("“Hơn mười ngày thời gian nói dài cũng không dài, nhìn các ngươi ý kiến gì. Như vậy đi, ta mang các ngươi nhìn xem Thiên Vũ vương thành, cũng coi như thấy chút việc đời!” Nhìn Triệu Nhã đám người tất cả đều một mặt mướp đắng dạng, Lưu Lăng cười cười."),t("br"),t("br"),n._v("“Từng trải? Tốt! Đi nơi nào?” Viên Đào nhãn tình sáng lên."),t("br"),t("br"),n._v("Thiên Vũ vương thành viễn siêu Thiên Huyền vương quốc bất luận cái gì một tòa thành thị, hắn đã sớm muốn bốn phía đi dạo một chút, nghe nói như thế cái kia còn nhịn được."),t("br"),t("br"),n._v("“Như vậy đi, ta mang các ngươi đi trước Thiên Vũ học viện nhìn xem, đây chính là xung quanh mười ba nước học phủ cao nhất, tu luyện Thánh địa. Danh Sư đường danh sư, cũng cơ bản đều định kỳ quá khứ giảng bài, các ngươi đi xem một chút, có lẽ đối tu luyện cũng có chỗ tốt!”"),t("br"),t("br"),n._v("Lưu Lăng suy nghĩ một chút nói."),t("br"),t("br"),n._v("“Danh sư ở bên trong giảng bài?”"),t("br"),t("br"),n._v("“Học phủ cao nhất?”"),t("br"),t("br"),n._v("“Dù sao Trương sư còn cần hơn mười ngày mới có thể trở về, đi qua nhìn một chút cũng không sao!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được Thiên Vũ học viện, có danh sư giảng bài, tu luyện Thánh địa, trong lòng mọi người tất cả đều khẽ động."),t("br"),t("br"),n._v("“Hơn nữa, không chỉ như thế, ta nghe nói, Thiên Vũ học viện phong cách học tập thuần khiết, muốn nghe lão sư nào khóa, có thể trực tiếp đẩy cửa đi vào, không cần sớm trình báo. Các ngươi đi nghe một chút, hái thu được gia chi trường, tuyệt đối có chỗ tốt!”"),t("br"),t("br"),n._v("Lưu Lăng cười nói."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghe được có thể đẩy cửa đi vào, tùy ý nghe giảng bài, đám người tất cả đều hưng phấn gật đầu."),t("br"),t("br"),n._v("Nói đi là đi, không có chậm trễ quá nhiều thời gian, Triệu Nhã đám người liền hướng Thiên Vũ học viện phương hướng đi tới."),t("br"),t("br"),n._v("(Tạm ngừng, phía dưới là Triệu Nhã, cùng Mộc Tuyết Tinh hai nữ ẩu đả vở kịch, không tốt lắm viết. Ô ô! Cầu an ủi! Thuận tiện gửi cho bạn bè một quyển sách, 《 vạn năng tiểu thú y 》, thư hoang có thể đi nhìn một chút!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-334"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-336"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);