(window.webpackJsonp=window.webpackJsonp||[]).push([[1561],{1916:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-327-lao-su-ta-sai-roi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-327-lao-su-ta-sai-roi"}},[n._v("#")]),n._v(" Chương 327: Lão sư, ta sai rồi!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Lão sư, ngươi... Thật có thể để cho ta trong mười ngày báo thù?”"),t("br"),t("br"),n._v("Đơn độc phụ đạo phòng học nhỏ bên trong, Lộ Xung quỳ gối Trương Huyền trước mặt, đến bây giờ còn có chút không dám tin tưởng."),t("br"),t("br"),n._v("Hắn người mang huyết hải thâm cừu sự tình, chưa hề cùng người khác nói qua, càng không biểu hiện ra ngoài, vì phòng ngừa chuyện hoang đường loại hình tiết lộ, trực tiếp giả dạng làm câm điếc, tự tin không có lưu hạ bất luận cái gì sơ hở."),t("br"),t("br"),n._v("Nhưng trước mắt này vị Liễu lão sư, chỉ nghe được sự tích của mình cùng tu hành trạng thái, liền suy đoán ra gánh vác đại thù, cái này... Năng lực quá kinh khủng đi!"),t("br"),t("br"),n._v("Mấu chốt nhất là... Hứa hẹn bản thân mười ngày báo thù!"),t("br"),t("br"),n._v("Cừu nhân của hắn thế nhưng là Thông Huyền cảnh cường giả, nếu như đơn giản như vậy, cũng không cần hai năm qua, ngày đêm khổ tu!"),t("br"),t("br"),n._v("“Đã nói như vậy, tự nhiên có phương pháp, bất quá... Phương pháp của ta rất thống khổ, có thể hay không chịu được, liền muốn xem chính ngươi!” Trương Huyền nhìn về phía thiếu niên ở trước mắt."),t("br"),t("br"),n._v("Đối với người học sinh này, hắn là hết sức hài lòng."),t("br"),t("br"),n._v("Vì gia tăng thực lực, quỳ ở trước cửa bảy ngày bảy đêm; Sợ tiết lộ tin tức, giả bộ ròng rã hai năm câm điếc, vô luận bên nào, đều thuyết minh tâm trí của hắn cường đại, ý chí lực kiên cường."),t("br"),t("br"),n._v("Loại người này mặc dù thiên phú chẳng ra sao cả, nhưng chỉ cần hảo hảo bồi dưỡng, tuyệt đối sẽ thực lực bạo tăng, vượt qua Mộc Tuyết Tinh chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("Không phải như thế, cũng không có khả năng chuyên môn phí nhiều như vậy nước miếng."),t("br"),t("br"),n._v("Hắn thu học sinh, nhìn tâm tính, nhìn ý chí lực, về phần thiên phú, có hắn tại, không tính là cái gì."),t("br"),t("br"),n._v("“Chỉ cần có thể báo thù, lớn hơn nữa thống khổ ta cũng có thể nhịn được!” Lộ Xung nắm đấm xiết chặt, ánh mắt kiên định."),t("br"),t("br"),n._v("“Rất tốt!”"),t("br"),t("br"),n._v("Hai tay chắp sau lưng, Trương Huyền ánh mắt tĩnh mịch: “Đặc thù chức nghiệp bên trong, có một loại để cho người ta không nguyện ý tiếp xúc, cũng không muốn nhắc tới lên, gọi là độc sư, không biết nghe nói qua sao?”"),t("br"),t("br"),n._v("“Nghe qua!” Lộ Xung gật đầu."),t("br"),t("br"),n._v("Hắn lúc trước cũng muốn học tập độc sư chức nghiệp, đáng tiếc tìm không thấy phương pháp."),t("br"),t("br"),n._v("“Chẳng lẽ lão sư ngươi...”"),t("br"),t("br"),n._v("Sửng sốt một chút, nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Độc sư, không phải gặp những nghề nghiệp khác xa lánh, rất ít lộ diện sao? Chẳng lẽ vị lão sư này có được loại nghề nghiệp này?"),t("br"),t("br"),n._v("Hoặc là muốn cho hắn học tập?"),t("br"),t("br"),n._v("“Ta không phải độc sư, cũng sẽ không để ngươi học tập!”"),t("br"),t("br"),n._v("Tựa hồ nhìn ra ý nghĩ của hắn, Trương Huyền thản nhiên nói: “Ta có một bộ phương pháp, có thể để cho thực lực ngươi nhanh chóng gia tăng, chỉ bất quá, cần phải mượn độc dược, đối tu luyện về sau vô hại, lại phải thừa nhận cực lớn thống khổ, cửu tử nhất sinh, không chịu nổi liền sẽ chết.”"),t("br"),t("br"),n._v("Hắn nói, tự nhiên là tại Độc điện nhìn thấy, tu luyện độc thể phương pháp."),t("br"),t("br"),n._v("Độc thể, sau khi luyện thành, không chỉ có thể phục dụng kịch độc không lọt vào tổn thương, lực lượng, tinh thần, nhanh nhẹn lực cũng đều sẽ tăng nhiều, trong thời gian ngắn để cho người ta bộc phát lực lượng cường đại hơn."),t("br"),t("br"),n._v("Muốn cho trước mắt vị này, trong thời gian ngắn lực lượng bạo tăng, ngoại trừ Thiên Đạo Thần Công, cũng chỉ có cái này một cái biện pháp."),t("br"),t("br"),n._v("Bình thường độc sư tu luyện, lấy thân uy độc, đều sẽ ảnh hưởng tuổi thọ, cùng lúc trước Cổ Mục đồng dạng, tồn tại dạng này, như thế ám thương."),t("br"),t("br"),n._v("Nhưng Trương Huyền bộ này, đi qua thư viện chỉnh lý, ảnh hướng trái chiều đã rất nhỏ. Bất quá, không có thiên đạo chân khí duy trì, muốn tu luyện, cũng không dễ dàng như vậy, cần phải thừa nhận cực lớn thống khổ mới có thể hoàn thành."),t("br"),t("br"),n._v("Đã nhưng cái này Lộ Xung, như thế có nghị lực, để hắn thử xem, có lẽ liền có thể tạo được không sai hiệu quả."),t("br"),t("br"),n._v("“Vô luận cỡ nào thống khổ, chỉ cần có thể báo thù, ta đều nguyện ý!”"),t("br"),t("br"),n._v("Lộ Xung ánh mắt kiên định, trong ánh mắt mang theo kiên quyết."),t("br"),t("br"),n._v("Cơ hội chỉ có một lần."),t("br"),t("br"),n._v("Hắn tu luyện, đối phương cũng tu luyện, nhờ vào hắn hai năm mới tăng lên một cấp thiên phú, muốn vượt qua đối phương, coi như mười năm tám năm khổ công, đều chưa hẳn có thể làm đến."),t("br"),t("br"),n._v("Vị này Liễu lão sư, đã nói mười ngày có thể làm cho hắn báo thù, vậy liền khẳng định có thể làm đến."),t("br"),t("br"),n._v("Tận mắt thấy hắn giúp toàn bộ đồng học trong thời gian ngắn tăng thực lực lên, có ngốc cũng biết, vị này là chân chính có đại bản lãnh lão sư."),t("br"),t("br"),n._v("Gặp hắn ánh mắt kiên định, Trương Huyền hài lòng gật đầu, thanh âm rất nặng, mang theo lạnh lùng: “Ta truyền thụ cho ngươi công pháp, bất kỳ người nào đều không thể truyền ra ngoài, nếu không, không cần ta động thủ, chân khí liền sẽ tự động phản phệ, để ngươi chết không có chỗ chôn!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Lộ Xung gật đầu, một tay giơ lên: “Ta Lộ Xung thề, Liễu lão sư truyền thụ cho ta công pháp, bí tịch, như có tiết ra ngoài, để cho ta hồn phi phách tán, vạn kiếp bất phục!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền cũng không nói chuyện, ngón tay một điểm, một đạo chân khí chui vào đối phương huyệt đạo, lặng lẽ che giấu."),t("br"),t("br"),n._v("Hắn truyền thụ cho công pháp quá mức doạ người, một khi truyền bá ra ngoài, tuyệt đối sẽ gây nên hoạ lớn ngập trời, hắn cũng không muốn tự tìm phiền toái, mặc dù đối phương phát thề, vẫn là lại thêm tin tưởng mình."),t("br"),t("br"),n._v("Đi qua đối độc học tập cùng nghiên cứu, hắn Thiên Đạo chân khí có thể nhất niệm làm độc, nhất niệm làm thuốc."),t("br"),t("br"),n._v("Nếu như đối phương giữ miệng giữ mồm, đạo chân khí này, hoàn toàn có thể giúp hắn dọn sạch trong kinh mạch chướng ngại, trợ giúp tăng cao tu vi; Dùng sức mạnh làm xằng làm bậy, đồng dạng có thể đem hắn độc chết."),t("br"),t("br"),n._v("“Tu luyện độc thể, cần độc dược phối hợp, ta trên người bây giờ không đến những dược vật này! Công pháp ngày mai lại truyền thụ cho ngươi, bất quá, hôm nay ngược lại là có thể truyền thụ một bộ khác công pháp, để ngươi thể chất tăng nhiều. Chỉ có sức mạnh tăng lên, mới có thể chịu ở kịch độc xâm nhập, không bị chết mất!”"),t("br"),t("br"),n._v("Làm xong chuẩn bị, Trương Huyền nhìn qua: “Ta chỉ nói một lần, có thể nhớ kỹ bao nhiêu, lĩnh ngộ bao nhiêu, bằng của cá nhân ngươi năng lực, tuyệt đối đừng khiến ta thất vọng!”"),t("br"),t("br"),n._v("“Học sinh sẽ không để cho lão sư thất vọng!” Lộ Xung gật đầu."),t("br"),t("br"),n._v("“Rất tốt, ta đây là bộ luyện thể công pháp...”"),t("br"),t("br"),n._v("Trương Huyền lập tức mở lời."),t("br"),t("br"),n._v("Hắn nói chính là Thiên Đạo Kim Thân, đương nhiên cùng truyền thụ Triệu Nhã đám người đồng dạng, thuộc về bản rút gọn."),t("br"),t("br"),n._v("Tu luyện độc thể, kinh lịch kịch độc, cửu tử nhất sinh, thể chất nếu như không đủ cường đại, sợ rằng sẽ không kiên trì nổi."),t("br"),t("br"),n._v("Bộ này Thiên Đạo Kim Thân mặc dù không bằng hắn tu luyện cái kia, nhưng cũng là thư viện xuất phẩm, không thể khinh thường, chỉ phải nghiêm túc tu luyện, để hắn trong thời gian ngắn lực lượng tiêu thăng, không là vấn đề."),t("br"),t("br"),n._v("“... Vì thế, mới là hoàn chỉnh chi thể!”"),t("br"),t("br"),n._v("Rất nhanh, Trương Huyền đem khẩu quyết đọc một lần."),t("br"),t("br"),n._v("“Đa tạ lão sư trọng thưởng!”"),t("br"),t("br"),n._v("Nghe xong công pháp, Lộ Xung thân thể run rẩy, kích động sắc mặt đỏ lên."),t("br"),t("br"),n._v("Đi tới Thiên Vũ học viện hai năm, tu luyện tới Đỉnh Lực cảnh sơ kỳ, công pháp gặp qua không ít, cũng biết một chút luyện thể pháp quyết."),t("br"),n._v("Những vật này cùng vị này Liễu lão sư dạy so sánh, quả thực liền rác rưởi cũng không bằng."),t("br"),t("br"),n._v("Loại cấp bậc này công pháp, ít nhất đạt đến quỷ cấp đỉnh phong thậm chí trở lên!"),t("br"),t("br"),n._v("Tùy tiện xuất ra đi đấu giá, đều tuyệt đối là giá trên trời, thậm chí Thiên Vũ vương thất đều sẽ điên cuồng..."),t("br"),t("br"),n._v("Trân quý như thế công pháp, lão sư liền tùy tiện dạy... Phần ân tình này, căn bản là không có cách báo đáp."),t("br"),t("br"),n._v("“Tu luyện đi!”"),t("br"),t("br"),n._v("Không để ý tới hắn cảm kích, Trương Huyền tùy ý khoát tay áo."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Biết lão sư không thích nói nhảm, Lộ Xung cũng không nói thêm lời, lúc này khoanh chân ngồi tại hạ đến, bắt đầu tu luyện."),t("br"),t("br"),n._v("Thiên Đạo Kim Thân, chân khí du tẩu địa phương, cơ hồ tất cả đều là chính xác lộ tuyến, tu luyện phút chốc, Lộ Xung liền cảm thấy toàn thân tê dại, một trận thư thái, thậm chí trên người có lưu ám thương, cũng trong nháy mắt thanh trừ."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Không biết qua bao lâu, bắp thịt toàn thân một trận pháo vang lên, cả người khí thế tăng nhiều."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Lộ Xung mở to mắt."),t("br"),t("br"),n._v("Giờ phút này, hắn cảm thấy trên người có được không dùng hết lực lượng."),t("br"),t("br"),n._v("“Ừm, không sai, một canh giờ, liền đạt tới tiểu thành, nói rõ ngươi cơ sở không sai!”"),t("br"),t("br"),n._v("Trương Huyền hài lòng gật đầu."),t("br"),t("br"),n._v("Đối phương vì báo thù, khổ gì đều ăn, thân thể khẳng định đã sớm rèn luyện qua, nếu không, một canh giờ liền muốn để bản rút gọn Thiên Đạo Kim Thân tiểu thành, cũng là làm không được."),t("br"),t("br"),n._v("“Ra ngoài đi, đoán chừng các ngươi những bạn học khác, cũng đều chờ đợi gấp!”"),t("br"),t("br"),n._v("Gặp hắn tu luyện hoàn tất, Trương Huyền đẩy cửa ra khỏi phòng."),t("br"),t("br"),n._v("Hai người tiến đến, đã hơn một canh giờ, bên ngoài chờ lấy học sinh, chỉ sợ sớm đã sốt ruột."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trong lớp học."),t("br"),t("br"),n._v("“Liễu lão sư nói... Mười ngày liền để Lộ Xung có thể đủ thắng quá Thông Huyền cảnh cường giả, cái này... Có khả năng sao?”"),t("br"),t("br"),n._v("Một cái học sinh trên mặt đến bây giờ đều không thể tin được."),t("br"),t("br"),n._v("“Ta cũng không biết, bất quá, Liễu lão sư thuận miệng chỉ điểm, liền để chúng ta đột phá, nhãn lực mạnh, ta cảm thấy không thể so với danh sư kém!”"),t("br"),t("br"),n._v("“Danh sư khóa ta nghe qua, liền so chúng ta trước đó lão sư cao thâm một chút, cùng Liễu lão sư so, ta cảm thấy... Còn có chút không bằng!”"),t("br"),t("br"),n._v("“Đúng vậy a... Lợi hại như vậy, hắn thật chỉ là một cái bình thường lão sư?”"),t("br"),t("br"),n._v("Nghị luận ầm ĩ."),t("br"),t("br"),n._v("Vốn là đối vị lão sư này còn ôm lấy địch ý, kết quả hắn mới mở miệng chỉ điểm, đám người lập tức liền biết gặp một cái tốt sư trưởng, tới tấp phản bội."),t("br"),t("br"),n._v("“Thông Huyền cảnh, lực lượng vượt qua một trăm đỉnh, một quyền có thể đoạn cây khai sơn, Lộ Xung hiện tại chỉ là Đỉnh Lực cảnh sơ kỳ, kém nhiều lắm... Mười ngày, làm sao có thể!”"),t("br"),t("br"),n._v("Mộc Tuyết Tinh lắc đầu."),t("br"),t("br"),n._v("Nàng hiện tại là Ích Huyệt cảnh, biết đạo lực lượng tăng trưởng độ khó, ngắn ngủi mười ngày, có thể nhường đường xông theo Đỉnh Lực cảnh sơ kỳ, đạt tới trung kỳ cũng rất không tệ, vượt vượt hai cái đại cấp bậc, cùng Thông Huyền cảnh cường giả chiến đấu..."),t("br"),t("br"),n._v("Nghe cùng khoác lác không có gì khác biệt."),t("br"),t("br"),n._v("“Đúng vậy a...” Mạnh Đào cũng gật đầu."),t("br"),t("br"),n._v("Lúc trước hắn theo Đỉnh Lực cảnh sơ kỳ, đạt tới hậu kỳ, thế nhưng là hao tốn vô số tâm huyết, hao phí tiếp cận thời gian một năm, trong thời gian ngắn tăng lên... Hắn cũng không quá tin tưởng."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Ngay tại cảm khái, cửa phòng mở ra, Liễu lão sư cùng Lộ Xung đi ra."),t("br"),t("br"),n._v("Lúc này Lộ Xung, toàn thân tinh khí sung mãn, hai mắt như điện, trong cơ thể bộc phát lực lượng, như là lao nhanh thủy triều, cho người ta cực mạnh áp bách."),t("br"),t("br"),n._v("“Đỉnh Lực cảnh... Đỉnh phong?”"),t("br"),t("br"),n._v("Cảm nhận được cỗ khí tức này, Mộc Tuyết Tinh, Mạnh Đào câm lại, con mắt trừng đến sắp rơi ra tới."),t("br"),t("br"),n._v("Mới cùng Liễu lão sư đi vào bao lâu?"),t("br"),t("br"),n._v("Khoảng một canh giờ, liền theo Đỉnh Lực cảnh sơ kỳ, đạt tới đỉnh phong..."),t("br"),t("br"),n._v("Hai người cảm thấy muốn thổ huyết."),t("br"),t("br"),n._v("Bọn hắn tu luyện, tăng lên một cái cấp bậc, tựa như ốc sên, mấy tháng không nổi một điểm, vị này Liễu lão sư đem Lộ Xung mang vào một hồi, liền liên tục đột phá mấy cái tiểu cấp bậc..."),t("br"),t("br"),n._v("Muốn hay không khoa trương như vậy?"),t("br"),t("br"),n._v("Kỳ thật bọn hắn không biết, Lộ Xung bản rút gọn Thiên Đạo Kim Thân tiểu thành, chỉ bằng vào lực lượng, đã vượt qua phổ thông Đỉnh Lực cảnh đỉnh phong!"),t("br"),t("br"),n._v("Phối hợp chân khí, đã có được ròng rã năm đỉnh chi lực!"),t("br"),t("br"),n._v("Phổ thông Đỉnh Lực cảnh đỉnh phong, cũng bất quá bốn đỉnh mà thôi."),t("br"),t("br"),n._v("“Cái này... Cái này, quá lợi hại... Học tỷ, ta nhìn ngươi vẫn là chủ động nhận lầm đi, như thế lão sư tốt, đi nơi nào tìm...”"),t("br"),t("br"),n._v("Đè nén khiếp sợ trong lòng, Mạnh Đào nhịn không được truyền âm."),t("br"),t("br"),n._v("Lời nói nói phân nửa, thấy không có trả lời, coi là học tỷ tính cách quá ngạo, không nguyện ý khuất phục, vội vàng quay đầu, xem xét phía dưới suýt chút nữa không có tại chỗ ngất đi."),t("br"),t("br"),n._v("Chỉ thấy cao ngạo vô cùng theo không nhận sai tiểu ma nữ Mộc Tuyết Tinh, “Phù phù!” Quỳ rạp xuống liễu trước mặt lão sư, một mặt thành khẩn."),t("br"),t("br"),n._v("“Lão sư, ta sai rồi... Xin hãy tha thứ, về sau ngươi phân phó ta làm cái gì, thì làm cái đó, không dám có chút vi phạm! Coi như vị kia thiên tài danh sư Trương Huyền tới, ta cũng sẽ coi hắn là thành không khí, nhìn cũng không nhìn một chút!”"),t("br"),t("br"),n._v("“...” Trương Huyền."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-335"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-337"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);