(window.webpackJsonp=window.webpackJsonp||[]).push([[1563],{1918:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-329-uoc-chien"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-329-uoc-chien"}},[n._v("#")]),n._v(" Chương 329: Ước chiến")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Triệu Nhã, cái kia nữ mặc dù người chẳng ra sao cả, thực lực lại không thấp, ngươi phải cẩn thận!”"),i("br"),i("br"),n._v("Đối diện giương cung bạt kiếm, Trịnh Dương cũng tới đến trước mặt, giảm thấp xuống cuống họng."),i("br"),i("br"),n._v("“Ừm, yên tâm!” Triệu Nhã lên tiếng."),i("br"),i("br"),n._v("Hôm trước đi qua Trương lão sư chỉ điểm, vừa mở ra mười nơi huyệt đạo, cô bé đối diện thực lực coi như không yếu, cũng chỉ cùng nàng không kém bao nhiêu, chân chính chiến đấu, hươu chết vào tay ai, còn chưa nhất định."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Thân thể nhảy lên, nhẹ nhàng rơi vào tỷ thí trên đài, bàn tay như ngọc trắng vung lên, đen nhánh tú mục nhìn về phía đối diện Mộc Tuyết Tinh: “Bắt đầu đi!”"),i("br"),i("br"),n._v("“Hừ!”"),i("br"),i("br"),n._v("Mộc Tuyết Tinh cũng không cam chịu yếu thế, nhảy lên tỷ thí đài, chấn động toàn thân, vừa mới đột phá tu vi liền triển lộ ra, lực lượng lao nhanh như nước."),i("br"),i("br"),n._v("“Bắt đầu!”"),i("br"),i("br"),n._v("“Hai đại mỹ nữ tranh đấu...”"),i("br"),i("br"),n._v("Nhìn thấy hai nữ hai không nói nhiều nói, trực tiếp nhảy lên tỷ thí đài, chung quanh toàn bộ an tĩnh lại, sợ quấy rầy."),i("br"),i("br"),n._v("“Ta sẽ để cho ngươi nói xin lỗi!”"),i("br"),i("br"),n._v("Một tiếng thanh minh, Triệu Nhã thân thể mềm mại trên mặt đất đạp mạnh, thẳng tắp hướng về phía trước lao đến, đồng thời ngọc tay vừa lộn, gào thét khí kình, tựa như kiếm mang."),i("br"),i("br"),n._v("“Nói xin lỗi là ngươi!”"),i("br"),i("br"),n._v("Không nhường chút nào, Mộc Tuyết Tinh cũng tiến lên đón."),i("br"),i("br"),n._v("Bành bành bành bành!"),i("br"),i("br"),n._v("Rất nhanh, hai người liền liên tục giao thủ, cùng là Ích Huyệt cảnh, lực lượng bốn phía trùng kích, phát ra từng tiếng kịch liệt âm bạo."),i("br"),i("br"),n._v("“Lợi hại! Cô gái này từ nơi nào xuất hiện? Tuổi không lớn lắm, vậy mà cùng Mộc Tuyết Tinh đánh khó bỏ khó phân!”"),i("br"),i("br"),n._v("Trong đám người, một thanh niên nhìn xem trong sân chiến đấu, tràn đầy kỳ quái."),i("br"),i("br"),n._v("Chính là học viên thi đấu bên trong, xếp hàng thứ nhất Triệu Vũ Tinh."),i("br"),i("br"),n._v("“Ta cũng không biết, tựa như là hôm nay đột nhiên xuất hiện!” Bên cạnh hắn một người khác, cũng đầy là nghi ngờ nhìn về phía trên đài."),i("br"),i("br"),n._v("Nếu như Mộc Tuyết Tinh nhìn người nọ, khẳng định cũng có thể nhận ra, chính là Thiên Vũ học viện học sinh thi đấu bên trong, bài danh thứ hai Lưu Trường Nham."),i("br"),i("br"),n._v("Triệu Vũ Tinh, Lưu Trường Nham, học viện nổi danh nhất hai vị học sinh, vậy mà đều đến nơi này, xem nhìn các nàng tỷ thí."),i("br"),i("br"),n._v("“Ngươi nói các nàng ai có thể thắng lợi?”"),i("br"),i("br"),n._v("Lại nhìn một hồi, Lưu Trường Nham cười nói."),i("br"),i("br"),n._v("“Mộc Tuyết Tinh, mấy ngày không thấy, tu vi tăng lên không nói, trước kia chiến đấu bó tay bó chân tình huống cũng đã biến mất, sức chiến đấu tăng lên không ít; Đối diện nàng cô gái này, dùng võ kỹ mặc dù không thế nào cao minh, nhưng lực lượng mười phần, hơn nữa chiêu số bên trong hàn khí bức người, để cho người ta khó mà chống chọi... Muốn nói ai có thể thắng, ta cũng bắt không được!”"),i("br"),i("br"),n._v("Triệu Vũ Tinh chần chờ một chút, nói."),i("br"),i("br"),n._v("Hắn mặc dù là học sinh bên trong thứ nhất, nhãn lực kinh người, nhưng trên đài tỷ thí hai nữ hài, vô luận tu vi hay là thực lực, đều không kém bao nhiêu, thật muốn nói ai có thể thắng lợi, ngay cả hắn, cũng nói không chính xác."),i("br"),i("br"),n._v("Lưu Trường Nham gật gật đầu, lông mày nhăn lại: “Cái này đột nhiên xuất hiện nữ hài, tu vi như thế vững chắc, vì cái gì võ kỹ lại kém nhiều như vậy, dùng cơ hồ đều là Phàm cấp trung phẩm khoảng chừng, nếu như võ kỹ cao minh, có lẽ liền thắng!”"),i("br"),i("br"),n._v("“Đúng vậy a!”"),i("br"),i("br"),n._v("Triệu Vũ Tinh cũng đầy mặt kỳ quái."),i("br"),i("br"),n._v("Cái này mỹ lệ không tưởng nổi nữ hài, tu vi đạt tới Ích Huyệt cảnh, chỉ cần nguyện ý, tùy tiện liền có thể tại học viện tìm tới cấp bậc đạt tới Phàm cấp thượng phẩm công pháp, thậm chí đỉnh phong, chỉ dùng trung phẩm, không khỏi quá yếu một chút."),i("br"),i("br"),n._v("Nếu không phải trong cơ thể nàng khí âm hàn, thập phần cường đại, so Mộc Tuyết Tinh không cách nào gần phía trước, chỉ sợ đã sớm thua."),i("br"),i("br"),n._v("“Không ổn ah!”"),i("br"),i("br"),n._v("Trịnh Dương đám người lúc này cũng nhìn xảy ra vấn đề chỗ."),i("br"),i("br"),n._v("Triệu Nhã Thuần Âm chi thể, dựa theo đạo lý, nên thập phần cường đại, chiến thắng đối phương rất dễ dàng, nhưng tu luyện võ kỹ, rõ ràng chỉ ở Thiên Huyền vương quốc cấp độ, cùng đối phương vẫn là có chênh lệch rất lớn."),i("br"),i("br"),n._v("Hơn nữa, càng quan trọng hơn là, Triệu Nhã theo võ giả nhất trọng Tụ Tức cảnh đạt tới Ích Huyệt cảnh, chỉ dùng ngắn ngủi gần hai tháng, lực lượng tăng lên quá nhanh, thiếu khuyết thực chiến."),i("br"),i("br"),n._v("Đối diện Mộc Tuyết Tinh hoàn toàn trái lại, tại Thiên Vũ học viện hô phong hoán vũ, được xưng là tiểu ma nữ, liền có thể nhìn ra, nhất định đã trải qua vô số chiến đấu."),i("br"),i("br"),n._v("Võ kỹ yếu, thực chiến thiếu, nếu không phải thể chất cường đại, chân khí trong cơ thể bành trướng tựa như cự long, chỉ sợ sớm đã bại."),i("br"),i("br"),n._v("“Đừng có gấp, Triệu Nhã mặc dù thực chiến thiếu, đối phương nghĩ thắng, cũng không dễ dàng như vậy!”"),i("br"),i("br"),n._v("Lưu Dương nói."),i("br"),i("br"),n._v("Triệu Nhã thể chất lúc trước liền Lục Tầm lão sư đều có thể áp chế, cô gái này mạnh hơn, còn có thể mạnh hơn Lục Tầm?"),i("br"),i("br"),n._v("“Yên tâm đi, chỉ cần nàng thích ứng tới, thua trận đối phương, chỉ là vấn đề thời gian!”"),i("br"),i("br"),n._v("Triệu Nhã ăn thiệt thòi tại kinh nghiệm thực chiến thiếu, nương theo thời gian chuyển dời, dần dần thích ứng phương thức chiến đấu, đem đối phương đánh bại, cũng không khó."),i("br"),i("br"),n._v("“Đáng giận!”"),i("br"),i("br"),n._v("Mọi người dưới đài nhìn ra điểm ấy, Mộc Tuyết Tinh cũng ý thức được, sắc mặt tái xanh."),i("br"),i("br"),n._v("Nàng mặc dù không phải Thiên Vũ vương quốc kịch liệt nhất nữ sinh, lại cũng không xê xích gì nhiều, vốn cho rằng giáo huấn một cái so với chính mình còn nhỏ nha đầu, dễ như trở bàn tay, nằm mơ đều không nghĩ tới, đối phương lợi hại như vậy!"),i("br"),i("br"),n._v("Thiên Vũ học viện lúc nào xuất hiện một vị lợi hại như vậy tân sinh?"),i("br"),i("br"),n._v("“Tiếp tục mang xuống, chân khí của nàng so ta còn muốn hùng hậu, ta khẳng định thất bại, chỉ có thể sử dụng chiêu kia!”"),i("br"),i("br"),n._v("Cắn răng một cái."),i("br"),i("br"),n._v("Mộc Tuyết Tinh ánh mắt lộ ra một đạo tàn nhẫn chi ý."),i("br"),i("br"),n._v("Vừa mới bắt đầu đối phương còn ở vào hạ phong, nương theo thời gian chuyển dời, nàng đã có chút không kiên trì nổi."),i("br"),i("br"),n._v("Đan dược tăng lên tu vi, cẩn thận nói đến, vẫn còn có chút phù phiếm."),i("br"),i("br"),n._v("“Hừ!”"),i("br"),i("br"),n._v("Một tiếng hừ nhẹ, Mộc Tuyết Tinh nhướng mày, toàn thân khí tức đột nhiên bạo tăng, lập tức một quyền đánh ra."),i("br"),i("br"),n._v("“Là Mộc đan sư nổi danh !”"),i("br"),i("br"),n._v("Triệu Vũ Tinh sắc mặt ngưng tụ."),i("br"),i("br"),n._v("“Lạc Hoa quyền?” Lưu Trường Nham nhìn qua."),i("br"),i("br"),n._v("“Không sai, là một bộ Quỷ cấp thượng phẩm võ kỹ, nghe nói là Mộc đan sư đạt tới đột phá Tông Sư cảnh, tưởng niệm chết đi thê tử, sáng lập ra quyền pháp, tổng cộng ba chiêu, uy lực vô tận, ngay cả Mạc Hoằng Nhất đều tán dương không thôi!”"),i("br"),i("br"),n._v("Triệu Vũ Tinh hai mắt nhìn chằm chằm trên đài nữ hài: “Không nghĩ tới Mộc đan sư đem bộ quyền pháp này truyền, hơn nữa nàng đã luyện thành chiêu thứ nhất!”"),i("br"),n._v(" “Quỷ cấp thượng phẩm võ kỹ? Coi như tại vương thành cũng phải kể đến đi? Hắn cô bé đối diện phải xui xẻo!”"),i("br"),i("br"),n._v("Lưu Trường Nham mặt mũi tràn đầy ngạc nhiên, lập tức gật gật đầu."),i("br"),i("br"),n._v("Quỷ cấp thượng phẩm võ kỹ, coi như tại toàn bộ Thiên Vũ vương quốc, đều được cho hàng đầu, vốn là đối diện nữ hài võ kỹ liền yếu kém, Mộc Tuyết Tinh lại thi triển ra cái này, đối phương khẳng định khó mà chống cự."),i("br"),i("br"),n._v("Quả nhiên, tiếng nói của bọn họ vừa dứt, cảm nhận được đối diện vọt tới cuồng bạo áp lực, Triệu Nhã biến sắc."),i("br"),i("br"),n._v("“Liều mạng!”"),i("br"),i("br"),n._v("Cắn răng, biết tiếp tục thi triển phổ thông chiêu số, tất nhiên bị thua, một tiếng khẽ kêu, Triệu Nhã lực lượng toàn thân dọc theo kinh mạch điên cuồng tuôn ra."),i("br"),i("br"),n._v("Được ăn cả ngã về không."),i("br"),i("br"),n._v("Bành!"),i("br"),i("br"),n._v("Lạc Hoa quyền cùng bàn tay của nàng đối đụng nhau."),i("br"),i("br"),n._v("Đăng đăng đăng đăng!"),i("br"),i("br"),n._v("Hai người đồng thời hơi đỏ mặt, mỗi bên lùi về sau bảy, tám bước, máu tươi phun ra."),i("br"),i("br"),n._v("Ngang tay!"),i("br"),i("br"),n._v("“Triệu Nhã!”"),i("br"),i("br"),n._v("“Học tỷ!”"),i("br"),i("br"),n._v("Trịnh Dương, Mạnh Đào đám người riêng phần mình xông đi lên."),i("br"),i("br"),n._v("“Là ta xem thường ngươi, hôm nay bất phân thắng bại, nhưng dám cùng ta sau ba ngày tại so một hồi?”"),i("br"),i("br"),n._v("Hai tay chấn động, đẩy ra Mạnh Đào đám người, Mộc Tuyết Tinh lạnh lùng nhìn qua."),i("br"),i("br"),n._v("“Ba ngày liền ba ngày, nếu như đến lúc đó ngươi thua, nhất định phải xin lỗi!” Triệu Nhã hai mắt chăm chú chằm chằm tới."),i("br"),i("br"),n._v("“Xin lỗi? Ta chỉ nói là Trương sư dạy học sinh chẳng ra sao cả, mắc mớ gì tới ngươi?”"),i("br"),i("br"),n._v("Nói đến đây, Mộc Tuyết Tinh kịp phản ứng, đột nhiên sửng sốt: “Các ngươi không phải là... Trương sư học sinh a?”"),i("br"),i("br"),n._v("“Không sai, hắn là thầy của chúng ta, ngươi vũ nhục hắn, liền là vũ nhục chúng ta!” Triệu Nhã nói."),i("br"),i("br"),n._v("“Thì ra là thế!”"),i("br"),i("br"),n._v("Rốt cuộc biết vì sao đối phương nổi giận như vậy muốn cùng nàng quyết đấu, Mộc Tuyết Tinh lắc đầu: “Đối với Trương sư, ta cũng là rất sùng bái, chỉ là... Ta chỉ là luận sự, cũng không phải là nói bừa. Tại giảng bài bên trên, ta dám cam đoan, chúng ta Liễu lão sư tuyệt đối vượt qua các ngươi Trương sư!”"),i("br"),i("br"),n._v("“Liễu lão sư? Chưa từng nghe qua!” Triệu Nhã lắc đầu, một mặt kiêu ngạo: “Chúng ta Trương sư, tuyệt đối là giảng bài tốt nhất, không người có thể so sánh!”"),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Mộc Tuyết Tinh cắn răng một cái: “Đã chúng ta ai cũng không phục, vậy liền dưới nắm tay xem hư thực đi! Hôm nay đánh ngang tay, sau ba ngày tái chiến, người nào thua, ai liền thừa nhận lão sư của mình không bằng đối phương, có dám hay không đáp ứng?”"),i("br"),i("br"),n._v("“Có gì không dám!” Triệu Nhã gật đầu."),i("br"),i("br"),n._v("“Vậy thì tốt, một lời đã định, chúng ta đi!”"),i("br"),i("br"),n._v("Gặp nàng đáp ứng, Mộc Tuyết Tinh cánh tay hất lên, xoay người rời đi."),i("br"),i("br"),n._v("Mặc dù Liễu lão sư tiếp xúc thời gian ngắn, nhưng đối phương xuất thủ liền giải quyết trên người nàng tai hoạ ngầm, cũng trợ giúp tăng lên tu vi, nội tâm là mười phần cảm kích, hiện tại gặp Trương sư học sinh khiêu chiến, tự nhiên muốn vì hắn chính danh!"),i("br"),i("br"),n._v("Để người ta biết, liễu sư mặc dù không phải danh sư, lại cũng không cần danh sư kém."),i("br"),i("br"),n._v("“Chúng ta đi!”"),i("br"),i("br"),n._v("Triệu Nhã cũng cùng Vương Dĩnh đám người, quay người rời đi."),i("br"),i("br"),n._v("“Mấy người này lại là Trương sư học sinh?”"),i("br"),i("br"),n._v("“Lần này náo lớn...”"),i("br"),i("br"),n._v("“Trương sư ta nghe nói, đánh vỡ Mạc Hoằng Nhất thiên tài vô số ghi chép, vinh đăng đệ nhất nhân, khó trách cô gái này lợi hại như vậy, nguyên lai là học sinh của hắn!”"),i("br"),i("br"),n._v("Dưới đài xem náo nhiệt đám người, biết Triệu Nhã đám người lại là Trương sư học sinh, tất cả đều xôn xao."),i("br"),i("br"),n._v("Vốn là thấy Triệu Nhã dung mạo mỹ lệ, còn muốn nghĩ cách một ít học sinh, cũng cũng không dám suy nghĩ nhiều."),i("br"),i("br"),n._v("Muốn nói gần nhất ai danh tiếng lớn, khẳng định là vị này Trương Huyền danh sư."),i("br"),i("br"),n._v('Đánh hắn học sinh chủ ý, không là muốn chết sao?"'),i("br"),i("br"),n._v("“Đúng rồi, Mộc Tuyết Tinh không phải Bạch Phương lão sư học sinh sao? Liễu lão sư là ai?”"),i("br"),i("br"),n._v("“Các ngươi còn không biết biết đi, Bạch Phương lão sư sớm liền rời đi trường học, vị này Liễu lão sư, nghe nói là vừa thông báo tuyển dụng!”"),i("br"),i("br"),n._v("“Vừa thông báo tuyển dụng?”"),i("br"),i("br"),n._v("“Đúng vậy a, thông báo tuyển dụng lão sư thời điểm, ta chịu trách nhiệm tiếp đãi, tận mắt thấy, vị này Liễu lão sư tuyệt đối là một nhân tài...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Một vị người biết chuyện đem thông báo tuyển dụng giáo sư khảo hạch tình cảnh nói một lần."),i("br"),i("br"),n._v("“Một câu chữa khỏi ghét học, đánh cho một trận, chữa khỏi muốn bỏ học tật xấu?”"),i("br"),i("br"),n._v("“Ngươi đang nói đùa chứ? Trên thế giới có người có loại bản lãnh này?”"),i("br"),i("br"),n._v("Sau khi nghe xong, đám người tất cả đều trợn mắt hốc mồm, cảm thấy có phải hay không đang nghe cố sự."),i("br"),i("br"),n._v("“Là thật!” Người biết chuyện lời thề son sắt: “Nếu như không phải thật sự, Mộc Tuyết Tinh tính cách các ngươi cũng đều biết, làm sao có thể mới cùng vị lão sư này tiếp xúc một hồi, liền cam nguyện vì hắn cùng người quyết đấu?”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("“Ngươi vừa nói như vậy, ta nhớ ra rồi, giống như bọn hắn ban người, buổi sáng ta còn gặp, tu vi đều không có cao như vậy, làm sao nửa ngày không thấy, cơ hồ tất cả mọi người tu vi đều tăng lên?”"),i("br"),i("br"),n._v("“Thật đúng là...”"),i("br"),i("br"),n._v("“Chẳng lẽ... Đều là vị này Liễu lão sư công lao? Vị này Liễu lão sư đến cùng là thần thánh phương nào?”"),i("br"),i("br"),n._v("Vượt thảo luận đám người vượt cảm thấy kỳ quái, đều đối Mộc Tuyết Tinh vị lão sư mới này tràn ngập tò mò."),i("br"),i("br"),n._v("“Hắt xì!”"),i("br"),i("br"),n._v("Rời đi phòng học, đi trên đường, Trương Huyền xoa xoa cái mũi, vẻ mặt vô cùng nghi hoặc."),i("br"),i("br"),n._v("Đây là thế nào? Bị cảm?"),i("br"),i("br"),n._v("(Say, hôm nay lên tam tiết khóa, mở hai lần sẽ, đem người cả điên rồi. Đổi mới xong, chớ trách. Mặt khác, nói rõ tiếp một cái nhai quân hoạt động, đến lúc đó ngoại trừ phát bài viết đoạt lâu ở ngoài, còn lại phát ra thanh toán bảo khẩu lệnh hồng bao, người người đều có thể đoạt, hồng bao khẩu lệnh tại tháng 3 15 ban đêm, cũng chính là Hậu Thiên đổi mới chương tiết phần đuôi, công bố nửa bộ phận trên, tại công chúng kêu bên trong công bố nửa phần dưới. Công chúng kêu cùng chương tiết trước sau chân đổi mới, mọi người chú ý công chúng kêu “Hoành Tảo Thiên Nhai” sau tức có thể đạt được hoàn chỉnh khẩu lệnh, tại thanh toán bảo bên trong đoạt khẩu lệnh hồng bao. Cũng có thể lục soát (chút kính lúp) đưa vào “h Sty Sd S”, sau đó chút “Lục soát một chút” nhìn thấy công chúng kêu sau chú ý."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-337"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-339"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);