(window.webpackJsonp=window.webpackJsonp||[]).push([[1565],{1920:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-25-gay-phien-phuc"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-25-gay-phien-phuc"}},[n._v("#")]),n._v(" Chương 25: Gây phiền phức")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v(" “Còn thiếu!” "),g("br"),g("br"),n._v(" Tào Hùng một mặt khen tặng."),g("br"),g("br"),n._v(" “Hóa ra là Tào lão sư, ngươi tìm ta?” Thượng Bân nghi ngờ nhìn sang."),g("br"),g("br"),n._v(" Đều là học viện lão sư, bình thường cũng không có thiếu tiếp xúc, lại không sâu như thế nào giao quá."),g("br"),g("br"),n._v(" “Là có chuyện như vậy, ta có chuyện khả năng muốn phiền phức còn thiếu một hạ!” Tào Hùng do dự một chút, nói."),g("br"),g("br"),n._v(" “Chỉ cần ta có thể giúp đỡ, dùng hết khả năng!” Thượng Bân nói."),g("br"),g("br"),n._v(" Làm như Thượng Thần trưởng lão cháu trai ruột, mỗi ngày đều có thể gặp phải không ít lão sư lại đây cầu hắn, từ lâu nuôi thành quen thuộc."),g("br"),g("br"),n._v(" “Là như vậy, ta... Ngày hôm qua thật vất vả thu rồi một cái học sinh tốt, lại bị các lão sư khác dùng lừa phương pháp lừa gạt, còn trưởng lão không phải chủ quản giáo dục sao? Ta muốn hỏi hỏi... Có thể hay không đem người học sinh này ở cho ta triệu hồi tới...” "),g("br"),g("br"),n._v(" Tào Hùng nói."),g("br"),g("br"),n._v(" Lưu Dương là hắn tiêu tốn lớn tâm huyết chiêu thu tốt nhất học sinh, kết quả nhưng bởi vì một lần tỷ thí bại bởi Trương Huyền, sau khi trở về càng nghĩ càng giận, ngày hôm nay cũng không nhịn được nữa, trực tiếp tới tìm Thượng Bân."),g("br"),g("br"),n._v(" Thượng Bân gia gia Thượng Thần trưởng lão, học viện thầy chủ nhiệm, cho học sinh thay cái lão sư, vẫn là vô cùng đơn giản."),g("br"),g("br"),n._v(" “Triệu hồi đến?” Thượng Bân hơi nhướng mày: “Nếu như người học sinh kia là tự nguyện, rất khó! Ngươi cũng biết, hiện tại học sinh khá là phiền toái, hơi có bất mãn, thượng cáo Vương Đình, giáo sư công hội, chúng ta...” "),g("br"),g("br"),n._v(" “Hắn không phải tự nguyện! Ta có thể bảo đảm!” Tào Hùng vội hỏi."),g("br"),g("br"),n._v(" “Coi như không phải tự nguyện, đối phương lão sư nếu như không nguyện ý buông tay, cũng không quá dễ dàng, ngươi cũng biết trường học quy củ...” Thượng Bân khoát tay áo một cái, đột nhiên ngẩng đầu: “Ngươi mới vừa nói... Lừa? Không biết là vị nào lão sư, đem ngươi học sinh lừa gạt?” "),g("br"),g("br"),n._v(" “Chính là chúng ta học viện cái kia ‘Rác rưởi lão sư’ Trương Huyền...” Nói tới thiếu niên, Tào Hùng hàm răng cắn chặt, đầy mặt đều là sự thù hận."),g("br"),g("br"),n._v(" “Trương Huyền? Học sinh của ngươi, cho hắn lừa gạt đi tới?” Lần thứ hai nghe được danh tự này, Thượng Bân lửa giận cũng một hồi bốc cháy lên."),g("br"),g("br"),n._v(" Nếu không phải cái tên này, ngày hôm qua nhất định có thể cùng nữ thần đồng thời cùng đi ăn tối, một cái chỉ có võ giả ba tầng, thầy giáo sát hạch chỉ được không điểm tiểu tử thúi, lại dám không nhìn uy nghiêm của mình, thực sự là điếc không sợ súng!"),g("br"),g("br"),n._v(" Nếu không phải ngày hôm qua nữ thần ở đây, không tiện đánh, khẳng định đánh sớm hắn điếc không sợ súng!"),g("br"),g("br"),n._v(" “Phải! Lão sư như vậy, là người cũng không nguyện ý làm học sinh của hắn đi! Vì lẽ đó, ta muốn phiền phức còn thiếu cùng còn trưởng lão nói một chút, giúp ta sẽ đem hắn triệu hồi tới...” Tào Hùng thận trọng đạo, chỉ lo đối phương không đáp ứng."),g("br"),g("br"),n._v(" “Không cần hòa thượng trưởng lão nói, ta hiện tại phải ngươi giải quyết!” Thượng Bân khoát tay chặn lại."),g("br"),g("br"),n._v(" “Giúp ta giải quyết?” "),g("br"),g("br"),n._v(" “Các lão sư khác khả năng còn muốn ông nội ta hỗ trợ phối hợp, Trương Huyền tên rác rưởi này, còn cần đến phiền toái như vậy? Trực tiếp đi qua muốn người là được, hắn không cho, đem hắn đánh cho tàn phế!” "),g("br"),g("br"),n._v(" Thượng Bân vung tay lên, khí thế rộng rãi."),g("br"),g("br"),n._v(" Đối phó lão sư khác, hắn vẫn đúng là hết cách rồi, nhưng đối với người thầy giáo này sát hạch là không phân gia hỏa, muốn ra tay giáo huấn quá đơn giản!"),g("br"),g("br"),n._v(" Thực lực kém không nói, mấu chốt là trong học viện căn bản không thể có người cho hắn ra mặt!"),g("br"),g("br"),n._v(" Điểm trọng yếu nhất, tự mình đang định đánh cái tên này một trận hả giận, sầu không lý do tốt!"),g("br"),g("br"),n._v(" Không có lý do gì liền ra tay, cho Trầm Bích Như biết, nhất định sẽ lưu lại ấn tượng xấu."),g("br"),g("br"),n._v(" Lần này tốt, hắn lừa gạt người khác học sinh, người trong cuộc đi tìm đến rồi, vừa vặn có thể mượn đề tài để nói chuyện của mình!"),g("br"),g("br"),n._v(" “Chuyện này...” Không nghĩ tới Thượng Bân rộng lượng như vậy, Tào Hùng không nhịn được sững sờ."),g("br"),g("br"),n._v(" “Không cần cái này, cái kia, đi theo ta đi, ngươi nếu như sợ sệt, ta thay ngươi ra tay!” Thượng Bân khóe miệng vung lên, trong mắt loé ra một nói tàn nhẫn ánh sáng."),g("br"),g("br"),n._v(" “Vậy thì đa tạ còn thiếu đi!” Tào Hùng hưng phấn ánh mắt sáng lên, liền vội vàng gật đầu, hai người đồng thời hướng về Trương Huyền lớp học đi nhanh tới."),g("br"),g("br"),n._v(" Thời gian không lâu, đi tới Trương Huyền lớp học trước cửa, thấy được xác ướp dường như Diêu Hàn cùng Vương gia đại thiếu."),g("br"),g("br"),n._v(" “Vương Đào, ngươi không ở gia gia nơi đó cố gắng tu luyện, tới này làm gì?” Thượng Bân hơi nhướng mày."),g("br"),g("br"),n._v(" Tuy rằng hắn là Thượng Thần trưởng lão cháu,"),g("br"),n._v("  Nhưng cùng Vương Đào thân phận so sánh, vẫn là có khoảng cách."),g("br"),g("br"),n._v(" “Thượng Bân lão sư!” Vương Đào khom người lại: “Muội muội ta bị người lừa gạt trong này dạy học, ta định đem nàng mang về...” "),g("br"),g("br"),n._v(" “Thượng Bân?” "),g("br"),g("br"),n._v(" Nguyên bản chính đang suy nghĩ hướng về ai đánh nghe tin tức Diêu Hàn, nghe được trước mắt người thanh niên này chính là Thượng Bân, gấp vội vàng ngẩng đầu lên."),g("br"),g("br"),n._v(" “Ngươi nói Vương Dĩnh tiểu thư? Nàng cũng bị lừa?” Thượng Bân sầm mặt lại, ánh mắt lóng lánh ra lạnh lùng nghiêm nghị ánh sáng: “Cái này Trương Huyền thực sự là thật là lớn gan chó! Liền Vương tiểu thư đủ dám lừa gạt, thực sự là muốn chết! Xem ra không dạy dỗ một hồi, khó có thể phục chúng!” "),g("br"),g("br"),n._v(" “Đúng vậy a...” Vương Đào gật đầu."),g("br"),g("br"),n._v(" “Nếu Vương Dĩnh tiểu thư cũng bị lừa gạt làm học sinh của hắn, ngươi làm sao không đi vào ngăn cản, mà ở chỗ này chờ?” Hừ một tiếng, Thượng Bân nghi ngờ nhìn sang."),g("br"),g("br"),n._v(" “Cái này...” Vương Đào không dám nói tự mình là bị đánh đi ra, gãi đầu một cái, chính không biết trả lời như thế nào, một bên Diêu Hàn mở miệng: “Học viện không phải có quy định, không tan học không cho phép tiến vào sao, chúng ta cũng chỉ có thể chờ ở bên ngoài đợi!” "),g("br"),g("br"),n._v(" “Vị này chính là...” "),g("br"),g("br"),n._v(" Thượng Bân lúc này mới chú ý tới xác ướp như thế Diêu Hàn."),g("br"),g("br"),n._v(" “Vị này chính là Bạch Ngọc Thành, Triệu thành chủ Đại quản gia, Diêu Hàn!” Vương Đào vội vã giới thiệu."),g("br"),g("br"),n._v(" “Hóa ra là Diêu quản gia, ngưỡng mộ đã lâu ngưỡng mộ đã lâu!” Thượng Bân liền vội vàng gật đầu, nói tiếp: “Trong học viện là có quy định, không tan học không cho phép tùy tiện đi vào, bất quá, không liên quan, các ngươi đi theo ta mặt sau đi vào là được, ngày hôm nay ta tự tay giáo huấn cái này ăn nói bừa bãi, không có sư đức gia hỏa!” "),g("br"),g("br"),n._v(" “Hảo!” Nghe được hắn muốn đích thân ra tay, Vương Đào ánh mắt sáng lên, chặt chẽ theo tới, Diêu Hàn do dự một chút, cũng đi theo."),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v(" Trong phòng."),g("br"),g("br"),n._v(" “Hồng Thiên Cửu Trọng Quyết, mặc dù là Hồng Thiên học viện trụ cột nhất công pháp, nhưng trong đó cũng không có thiếu vấn đề, tỷ như...” "),g("br"),g("br"),n._v(" Năm vị học sinh ngồi ở trong lớp, nghe Trương Huyền giảng giải."),g("br"),g("br"),n._v(" Chỉ nghe một hồi, tất cả đều chấn động không tên, như cùng ăn Nhân Sâm Quả, như si như say."),g("br"),g("br"),n._v(" Ở đây mấy vị học sinh, ngoại trừ Viên Đào bên ngoài, mỗi người thân thế không đơn giản, không có tới học viện, liền hệ thống đã học, bọn họ nghe qua không ít lão sư khóa, thậm chí một số võ giả năm, sáu trọng cường giả, đều vì bọn họ chỉ điểm quá... Nhưng từ nhỏ đến lớn, còn chưa từng nghe qua này loại lớp học."),g("br"),g("br"),n._v(" Cho người ta một loại “thể hồ quán đỉnh” cảm giác, trước không hiểu nghi hoặc, rộng rãi sáng sủa, trong cơ thể khí tức dựa theo lão sư nói tới rục rà rục rịch, thật giống túc là đều sẽ đột phá."),g("br"),g("br"),n._v(" Giảng quá tốt rồi!"),g("br"),g("br"),n._v(" Nghe được Trương Huyền khóa, bọn họ mới biết, cái gọi là cao cấp giáo sư, đều kém xa tít tắp."),g("br"),g("br"),n._v(" Đặc biệt là Triệu Nhã, thân là thành chủ con gái, phụ thân cũng thường thường cho nàng giảng giải công pháp, lúc trước vẫn cho rằng, cha nàng tuyệt đối là toàn bộ Thiên Huyền vương quốc giảng người tốt nhất một trong, dù sao cha nàng thực lực trong đó bày!"),g("br"),g("br"),n._v(" Nhưng giờ khắc này cùng Trương lão sư so sánh, quả thực chính là ếch ngồi đáy giếng thảo luận trời to nhỏ, buồn cười đến cực điểm, giữa hai người hoàn toàn không khả năng so sánh, không thể giống nhau!"),g("br"),g("br"),n._v(" Trương lão sư giảng giải, gãi đúng chỗ ngứa, lợi hại đến đâu công pháp, võ kỹ, đều có thể trong nháy mắt vạch ra thiếu hụt chỗ, để nhân chấn động, suy tư, thật lâu không thể lắng lại."),g("br"),g("br"),n._v(" Thường thường Trương lão sư giảng giải một câu, liền muốn suy tư nửa ngày, thật vất vả nghĩ thông suốt, kích động không thôi thời gian mới phát hiện, hắn cũng đã nói thật lâu rồi, trong đó tri thức, dĩ nhiên bỏ qua."),g("br"),g("br"),n._v(" “Trương lão sư... Trình độ thực sự quá cao!” "),g("br"),g("br"),n._v(" Thời khắc này, năm cái học viên cuối cùng đã rõ ràng rồi, trước mắt cái này xem ra không thể so bọn họ lớn quá nhiều thiếu niên, đến cùng có rồi thâm hậu bao nhiêu võ học bản lĩnh, cường hãn cỡ nào tri thức hệ thống!"),g("br"),g("br"),n._v(" “Ta trước còn có thể cười cho rằng Trương lão sư chỉ là may mắn, mới khiến cho ta trong nháy mắt sức mạnh tăng lên dữ dội, bây giờ mới biết... Dựa vào kiến thức của hắn, làm ra điểm ấy, quả thực liền là trò trẻ con! Để cho ta sức mạnh tăng lên dữ dội gấp đôi, hoàn toàn có thể có trên trăm loại phương pháp...” "),g("br"),g("br"),n._v(" Trước một mực tự cho là Lưu Dương, cũng cần phải tại nguyên chỗ liên tục run rẩy: “Tào Hùng lão sư cùng hắn so sánh... Ai, vẫn là khác dựng lên, căn bản không khả năng so sánh, chênh lệch quá xa, hoàn toàn không ở cùng một thế giới! May mà bọn họ đánh cược, không phải vậy, ta làm sao có khả năng gặp phải như thế lão sư tốt...” "),g("br"),g("br"),n._v(" Năm vị đệ tử, từng người kích động, chăm chú nghe giảng, chỉ lo bỏ qua cái gì trọng yếu tin tức, chính đang sư sinh Nhạc Dung Dung thời gian."),g("br"),g("br"),n._v(" Kẹt kẹt!"),g("br"),g("br"),n._v(" Cửa phòng bị người đẩy ra, mấy người bước đi vào."),g("br"),g("br"),n._v("Convert by: Hoang Chau"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-33"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-35"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);