(window.webpackJsonp=window.webpackJsonp||[]).push([[1571],{1926:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-336-duoc-lieu-toi-tay"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-336-duoc-lieu-toi-tay"}},[n._v("#")]),n._v(" Chương 336: Dược liệu tới tay")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Triệu Nhã bị thương so với trong tưởng tượng nặng nhiều lắm, nếu không phải thể chất cường hãn, chỉ sợ đều kiên trì đi không trở lại."),t("br"),t("br"),n._v("Không nghĩ tới để bọn hắn đi một chuyến học viện, được người bị thương thành như vậy, Lưu Lăng giật nảy mình, vội vàng chào đón."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("“Nàng là được một cái ác nữ nhân đả thương, còn xin Lưu sư hỗ trợ trị liệu!” Trịnh Dương nói."),t("br"),t("br"),n._v("“Ừm!” Nhìn thoáng qua, Lưu Lăng nhẹ nhàng thở ra: “Chỉ là chân khí nhận lấy chấn động, vấn đề không lớn, điều dưỡng nửa tháng khoảng chừng liền tốt.”"),t("br"),t("br"),n._v("“Nửa tháng? Không được, ba ngày sau còn muốn cùng nữ nhân kia tỷ thí...”"),t("br"),t("br"),n._v("Trịnh Dương sững sờ, đang muốn nói xong, chỉ thấy Lưu sư khoát tay áo, hướng về phía trước ôm quyền: “Vị này chính là các ngươi sư tổ, Dương sư, còn không tiến đến bái kiến!”"),t("br"),t("br"),n._v("“Sư tổ?”"),t("br"),t("br"),n._v("“Dương sư?”"),t("br"),t("br"),n._v("Trịnh Dương đám người lúc này mới phát hiện gian phòng chính giữa ngồi Trương Huyền."),t("br"),t("br"),n._v("Vị này Dương sư, tại Thiên Huyền vương quốc sáng lập vô số truyền kỳ, lão sư của mình, càng là hắn học sinh thân truyền."),t("br"),t("br"),n._v("Trước đó chỉ nghe qua truyền thuyết, chưa bao giờ thấy qua."),t("br"),t("br"),n._v("Lúc này nghe được, tất cả đều thân thể chấn động, cung kính quỳ mọp xuống đất, hành đại lễ."),t("br"),t("br"),n._v("“Đồ tôn Triệu Nhã (Trịnh Dương, Vương Dĩnh, Lưu Dương, Viên Đào), gặp qua sư tổ!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, nhướng mày nhìn về phía Triệu Nhã: “Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Không nghĩ tới mới rời khỏi một ngày, liền bị đánh thành như vậy, đến cùng ai sao mà to gan như vậy?"),t("br"),t("br"),n._v("“Hồi bẩm sư tổ, cùng người công bằng luận võ bị thương nhẹ, là Triệu Nhã tu vi không đủ!” Triệu Nhã nói."),t("br"),t("br"),n._v("“Công bằng luận võ?” Trương Huyền lông mày nhíu một cái."),t("br"),t("br"),n._v("Công bằng luận võ thua, chỉ có thể nói bản thân tài nghệ không bằng người, coi như hắn là đối phương lão sư, cũng không có cách nào bao che khuyết điểm."),t("br"),t("br"),n._v("“Ngươi qua đây!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Vâng!” Triệu Nhã đi lên phía trước."),t("br"),t("br"),n._v("Ngón tay tại đối phương mạch bên trên một đáp, tinh thuần chân khí, lập tức xuyên thấu qua kinh mạch mạnh vọt qua."),t("br"),t("br"),n._v("Lốp bốp!"),t("br"),t("br"),n._v("Được hắn Thiên Đạo chân khí thoải mái, Triệu Nhã trên người hỗn loạn chân khí, rất nhanh được một lần nữa tụ lại, trong cơ thể bị thương cũng mắt trần có thể thấy khôi phục."),t("br"),t("br"),n._v("“Đa tạ sư tổ vì Triệu Nhã trị thương!” Cảm thụ thân thể lần nữa long tinh hổ mãnh, Triệu Nhã hưng phấn con mắt lóe lên."),t("br"),t("br"),n._v("Vị sư tổ này quả nhiên cùng trong truyền thuyết đồng dạng lợi hại, tùy tiện vừa ra tay, không chỉ để thương thế của nàng phục hồi, tu vi nhận khuấy động, cũng tựa hồ có loại muốn đột phá xúc động."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Thấy cảnh này, Lưu Lăng lần nữa đối vị lão sư này bội phục không thôi."),t("br"),t("br"),n._v("Vừa rồi Triệu Nhã vết thương trên người, hắn nhìn thuộc về cưỡng ép làm dùng sức mạnh, nhận lấy phản phệ, nội tạng lệch vị trí, chân khí rời rạc. Coi như không có gì đáng ngại, muốn chữa khỏi, không có mười ngày nửa tháng, không cách nào làm đến."),t("br"),t("br"),n._v("Lão sư chỉ đem ngón tay khoác lên mạch bên trên, mấy hơi thở công phu liền triệt để chữa khỏi, phần này thủ đoạn thật sự là quỷ dị khó lường, không tận mắt nhìn thấy, để cho người ta khó có thể tin."),t("br"),t("br"),n._v("“Mới vừa nói sau ba ngày muốn cùng người luận võ, có ý tứ gì?”"),t("br"),t("br"),n._v("Khoát tay áo, đánh gãy đối phương cảm kích, Trương Huyền lông mày nhíu một cái nhìn lại."),t("br"),t("br"),n._v("Bọn này tiểu gia hỏa chuyện gì xảy ra? Làm sao vừa đến liền cùng người khác đánh nhau, mấu chốt còn ước đấu?"),t("br"),t("br"),n._v("“Hồi bẩm sư tổ, là chúng ta ở bên ngoài đi dạo, gặp một cái nói khoác không biết ngượng nữ tử, ngang nhiên nói xấu thầy ta, Triệu Nhã lúc này mới tức không nhịn nổi, xuất thủ giáo huấn, ai ngờ gia hỏa này thực lực không yếu, đưa nàng đánh thành như vậy!”"),t("br"),t("br"),n._v("Triệu Nhã vẫn chưa trả lời, Viên Đào vội vàng nói."),t("br"),t("br"),n._v("Nghe được hắn, đám người toàn bộ giật nảy mình."),t("br"),t("br"),n._v("Rất nhiều danh sư, đều không thích học sinh của mình gây chuyện thị phi."),t("br"),t("br"),n._v("Đi ra ngoài cùng người khác đánh nhau, bản thân liền không đúng, thế mà không biết hối cải, còn nói như thế lẽ thẳng khí hùng, cùng Trương lão sư nói một chút có thể, cùng vị này vượt qua tam tinh siêu cấp danh sư nói như vậy, khẳng định sẽ chọc cho đến không cao hứng, làm không cẩn thận để lão sư đều muốn chịu một trận phê..."),t("br"),t("br"),n._v("Nghĩ đến điểm này, tất cả mọi người trắng Viên Đào một chút, từng cái tràn đầy sốt ruột, sợ Dương sư tức giận."),t("br"),t("br"),n._v("“Vũ nhục ngươi sư?”"),t("br"),t("br"),n._v("Dương sư lông mày giương lên: “Không sai, học sinh nhất định phải giữ gìn lão sư uy nghiêm, làm rất khá!”"),t("br"),t("br"),n._v("“Làm rất khá?”"),t("br"),t("br"),n._v("Vốn cho rằng muốn chịu một trận huấn, không muốn Dương sư trực tiếp tán dương, đám người tất cả đều sững sờ, ngay cả Lưu Lăng cũng là thân thể nhoáng một cái."),t("br"),t("br"),n._v("Nói như vậy, há không đang khích lệ bọn hắn đánh nhau?"),t("br"),t("br"),n._v("Bản thân vị lão sư này ý nghĩ cũng quá... Cực đoan đi!"),t("br"),t("br"),n._v("“Bất quá, giáo huấn người khác, lại bị người khác đả thương, mất mặt đến cực điểm!” Trương Huyền khẽ nói."),t("br"),t("br"),n._v("“Kỳ thật... Triệu Nhã luận tu vi không hề yếu, chỉ là đối phương võ kỹ quá lợi hại...” Trịnh Dương chần chờ một chút, nói."),t("br"),t("br"),n._v("Đối phương có thể thắng, cũng không phải là thực lực mạnh, mà là võ kỹ bên trên ưu thế."),t("br"),t("br"),n._v("Thiên Vũ vương quốc là nhất đẳng vương quốc, Thiên Huyền vương quốc chỉ là cái bất nhập lưu quốc gia, cả hai chênh lệch hai cái cấp bậc, trong đó võ kỹ, tự nhiên cũng có cách nhau một trời một vực."),t("br"),t("br"),n._v("Có thể giữ cho không bị bại, đã nói rõ Triệu Nhã thực lực không yếu."),t("br"),t("br"),n._v("“Võ kỹ?”"),t("br"),t("br"),n._v("Trương Huyền lúc này mới nhớ tới, cũng không dạy qua Triệu Nhã võ kỹ."),t("br"),t("br"),n._v("Vương Dĩnh truyền thụ Thiên Đạo thối pháp cùng thân pháp, Trịnh Dương truyền thụ Thiên Đạo thương pháp, lưu Dương Thiên nói quyền pháp, Viên Đào, Triệu Nhã hai người mở ra thể chất về sau, cũng không truyền qua bất kỳ vũ kỹ nào, khó trách cùng người khác chiến đấu, có chút ăn thiệt thòi."),t("br"),t("br"),n._v("“Đã các ngươi lão sư ngay tại khảo hạch nhị tinh danh sư, trong thời gian ngắn không cách nào trở về, ta liền thay hắn truyền thụ cho ngươi một bộ võ kỹ, không biết có thể nguyện học!”"),t("br"),t("br"),n._v("Muốn đến nơi này, Trương Huyền nhìn về phía cô bé trước mắt."),t("br"),t("br"),n._v("“Nguyện học!” Nghe được sư tổ muốn đích thân truyền thụ võ kỹ, Triệu Nhã vội vàng ôm quyền."),t("br"),t("br"),n._v("Nói đùa, đây chính là lão sư sư phụ, hắn truyền thụ cho võ kỹ, khẳng định cao minh đến cực điểm."),t("br"),n._v(" “Ngươi từ nhỏ dùng kiếm, vậy ta liền truyền thụ cho ngươi một bộ kiếm pháp!”"),t("br"),t("br"),n._v("Suy tư một chút, Trương Huyền nói: “Bất quá, kiếm pháp của ta tương đối sắc bén, ngươi vừa bị thương, tạm thời còn không thích hợp tu luyện, hôm nay trước điều chỉnh một chút, ngày mai lại đem kiếm pháp truyền thụ!”"),t("br"),t("br"),n._v("Triệu Nhã Thuần Âm thể chất mở ra, quyền pháp, thối pháp loại hình, đều không thích hợp, đã nàng từ nhỏ đã tu luyện kiếm pháp, truyền thụ kiếm pháp, mới là thích hợp nhất."),t("br"),t("br"),n._v("“Vâng!” Triệu Nhã gật đầu."),t("br"),t("br"),n._v("Nàng vừa nhận qua tổn thương, học tập lợi hại kiếm pháp, quả thực không quá phù hợp, điều chỉnh đến trạng thái tốt nhất tại học, mới có thể phát huy tốt hơn uy lực."),t("br"),t("br"),n._v("“Tốt, các ngươi đi xuống trước đi, Tôn Cường, ngươi phái người đi Danh Sư đường đem Khương Thư đi tìm đến, liền nói ta tìm hắn!”"),t("br"),t("br"),n._v("Đáp ứng Triệu Nhã, Trương Huyền không nói thêm lời, khoát tay áo."),t("br"),t("br"),n._v("Tại Danh Sư đường nhìn không ít sách, kiếm pháp loại hình võ kỹ tự nhiên cũng không ít. Chỉ bất quá, đều có chút cao thâm, trực tiếp truyền thụ cho lời nói, chỉ sợ khó mà luyện thành, vẫn là chờ hắn trước học xong thử xem uy lực suy nghĩ thêm như thế nào truyền thụ."),t("br"),t("br"),n._v("Bằng không thì, liền hắn đều không có luyện, liền tùy tiện dạy học, một khi xảy ra vấn đề, phiền phức không nhỏ."),t("br"),t("br"),n._v("Tôn Cường hiệu suất làm việc rất nhanh, không đến nửa canh giờ, Khương đường chủ liền đến đến trước mặt."),t("br"),t("br"),n._v("Nhìn thấy ngồi tại chủ vị Trương Huyền, hưng phấn tiến lên quỳ gối: “Khương Thư gặp qua lão sư!”"),t("br"),t("br"),n._v("Trước mắt vị này, không chỉ giúp hắn thu phục cường đại man thú, lại thêm ban cho công pháp, giải quyết trong cơ thể tai hoạ ngầm, ám tật, trong lòng sớm đã tràn ngập cảm kích."),t("br"),t("br"),n._v("“Ta cần hai loại dược liệu, kim diệp hỏa căn cùng Cửu Trùng hoa, ngươi có thể hay không tìm tới?”"),t("br"),t("br"),n._v("Cũng không nói nhảm, Trương Huyền nhàn nhạt nhìn qua."),t("br"),t("br"),n._v("“Kim diệp hỏa căn cùng Cửu Trùng hoa? Hồi bẩm lão sư, học sinh nơi này vừa vặn có!”"),t("br"),t("br"),n._v("Khương Thư chần chờ một chút, cổ tay khẽ đảo, lòng bàn tay thêm ra hai gốc dược liệu, đúng là hắn thứ muốn tìm."),t("br"),t("br"),n._v("“Ừm!” Tiện tay tiếp nhận dược liệu, Trương Huyền khoát tay áo: “Vi sư cũng không chiếm tiện nghi của ngươi, cái này hai gốc dược liệu bao nhiêu tiền, một lát nữa để Tôn Cường cho ngươi!”"),t("br"),t("br"),n._v("“Lão sư đối học sinh ân trọng như núi, hai gốc dược liệu mà thôi, xem như học sinh hiếu kính ngài...”"),t("br"),t("br"),n._v("Khương Thư vội nói."),t("br"),t("br"),n._v("Không có lão sư công pháp, trên người hắn ám tật, sớm tối đều sẽ phát tác, nguy hiểm đến tính mạng. Mà bây giờ, không chỉ thu phục lợi hại man thú, lại thêm đem tai hoạ ngầm giải quyết triệt để, đừng nói hai gốc dược liệu, coi như xông pha khói lửa, đều cam tâm tình nguyện."),t("br"),t("br"),n._v("Không phải loại này chân chính cảm kích, cũng không có khả năng để thư viện, sinh ra kim sắc trang sách."),t("br"),t("br"),n._v("“Ta thu học sinh, là nhìn có hay không duyên phận, chẳng lẽ còn có thể chiếm tiện nghi của ngươi hay sao?” Trương Huyền nhướng mày, khí quyển lăng nhiên: “Nên bao nhiêu tiền, thì bấy nhiêu tiền!”"),t("br"),t("br"),n._v("Thấy lão sư kiên trì, Khương Thư âm thầm bội phục."),t("br"),t("br"),n._v("Thấy được không?"),t("br"),t("br"),n._v("Đây mới thực sự là danh sư độ lượng."),t("br"),t("br"),n._v("Để cho người ta không phục không được."),t("br"),t("br"),n._v("“Vâng!” Nhẹ gật đầu, cẩn thận suy nghĩ một chút, nói: “Cái này hai gốc dược liệu, là lúc trước ta theo Đại Dược Vương tay ở bên trong lấy được, hơn nữa niên đại đều đạt đến trăm năm trở lên, dùng kim tệ mua, cộng lại muốn hai ngàn vạn!”"),t("br"),t("br"),n._v("“Hai ngàn vạn... Khụ khụ!”"),t("br"),t("br"),n._v("Đang định vung tay lên, đem tiền cho đối phương, nghe nói như thế, Trương Huyền ho khan một tiếng, suýt chút nữa không có thổ huyết ngất đi."),t("br"),t("br"),n._v("Hai ngàn vạn? Kim tệ?"),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Bán hắn đi cũng không đáng số tiền này ah!"),t("br"),t("br"),n._v("Còn tưởng rằng nhiều nhất mấy trăm vạn, hắn trang cái hào phóng liền cho, nằm mơ đều không nghĩ tới, cái này hai gốc so trước đó mua tất cả cộng lại đều quý."),t("br"),t("br"),n._v("Cái này mẹ nó đều là thuốc gì ah..."),t("br"),t("br"),n._v("Vốn là muốn nhận cái học sinh, nhanh chóng tăng thực lực lên, tốt thu hoạch đối phương độ tín nhiệm, nằm mơ đều không nghĩ tới, trực tiếp để hắn phá sản..."),t("br"),t("br"),n._v("Quá hố đi!"),t("br"),t("br"),n._v("“Tiền chính là vật ngoài thân, làm danh sư, mỗi ngày nghĩ đến tiền... Sẽ ảnh hưởng tâm trí!”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Trương Huyền bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Khương Thư lơ ngơ."),t("br"),t("br"),n._v("Là ta không muốn, ngươi nhất định phải cho, nói thế nào giá cả về sau, còn nói nghĩ đến tiền, ảnh hưởng tâm trí?"),t("br"),t("br"),n._v("Gặp hắn một mặt quái dị bộ dạng, Trương Huyền có chút xấu hổ, lần nữa khoát tay áo: “Như vậy đi, ngươi hảo hảo điều chỉnh một chút, ta mấy ngày nay dành thời gian, chỉ điểm ngươi đột phá... Coi như cái này hai gốc dược liệu thù lao!”"),t("br"),t("br"),n._v("Đáp ứng đưa tiền, kết quả không có, hắn còn không vứt được người này, đành phải hứa hẹn chỗ tốt."),t("br"),t("br"),n._v("“Chỉ điểm ta đột phá?”"),t("br"),t("br"),n._v("Chính đang kỳ quái lão sư làm sao đột nhiên cải biến thái độ, nghe nói như thế, Khương Thư hưng phấn suýt chút nữa nhảy dựng lên, một cái quỳ mọp xuống đất."),t("br"),t("br"),n._v("“Đa tạ lão sư...”"),t("br"),t("br"),n._v("Hắn vây ở Tông sư đỉnh phong đã không biết đã bao nhiêu năm, nếu như có thể đột phá, đạt tới nửa bước Chí Tôn, vô luận tuổi thọ vẫn là lực lượng, đều sẽ tăng nhiều."),t("br"),t("br"),n._v("“Ngươi cũng đi xuống đi!”"),t("br"),t("br"),n._v("Dược liệu đến đông đủ, Trương Huyền cũng không có ý định chờ lâu, cùng đám người nói một tiếng: “Ta còn có việc, nên rời đi trước, ngày mai sẽ lần nữa tới!”"),t("br"),t("br"),n._v("Nói xong bước nhanh ra ngoài đi đến."),t("br"),t("br"),n._v("Rời đi phủ đệ, thấy không có người phát giác, lúc này mới biến trở về Liễu lão sư dung mạo, hướng lên trời võ học viện đi đến."),t("br"),t("br"),n._v("Bạch Thiềm tìm tòa phủ đệ này, ngay tại Danh Sư đường cùng Thiên Vũ trong học viện ở giữa chỗ, khoảng cách đều không xa, thời gian không dài, liền trở lại phòng học của mình."),t("br"),t("br"),n._v("Còn chưa đi đi vào, chỉ thấy Mạnh Đào tiến lên đón: “Lão sư, ngươi trở về, còn xin xuất thủ cứu cứu học tỷ, nàng... Bị thương!”"),t("br"),t("br"),n._v("“Mộc Tuyết Tinh bị thương?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Hoành Tảo Thiên Nhai nói"),t("br"),t("br"),n._v("Mọi người nói chương trước chất lượng có chỗ hạ xuống, có thể là lão nhai gần nhất quá bận rộn, tinh lực không đủ đưa đến. Đi làm, họp, trong nhà sự tình lại nhiều, lại có chút đuổi bản thảo, hôm qua càng là đầu đau gần chết, trực tiếp liền đem đại cương lấy ra, mồ hôi. Thực sự thật không tiện, đối đãi ta một lần nữa điều chỉnh, hôm nay lại chính là cái nhảy nhót tưng bừng, tinh lực dồi dào lão nhai!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-344"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-346"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);