(window.webpackJsonp=window.webpackJsonp||[]).push([[1575],{1930:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-340-lo-xung-đai-thu-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-340-lo-xung-đai-thu-ha"}},[n._v("#")]),n._v(" Chương 340: Lộ Xung đại thù (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Cùng một chút kịch nam bên trong kiều đoạn không có khác nhau quá nhiều."),t("br"),t("br"),n._v("Lộ Xung chỗ Lục gia, tại Thiên Vũ vương thành, không tính là gì thế gia, nhưng cũng vốn liếng giàu có."),t("br"),t("br"),n._v("Từ nhỏ, thiên phú của hắn liền không ra hồn, ngược lại chói mắt nhất chính là hắn tỷ tỷ, mười bảy tuổi liền khảo hạch trận pháp sư học đồ thành công, truyền vì giai thoại."),t("br"),t("br"),n._v("Bất quá, cũng chính là như vậy, tạo thành tai hoạ nguồn gốc."),t("br"),t("br"),n._v("Thiên phú tốt, người cũng dung mạo xinh đẹp, hồng nhan gây tai hoạ, bị một cái ăn chơi thiếu gia nhìn trúng."),t("br"),t("br"),n._v("Về sau sự tình, đoán đều đoán được, vì yêu sống giận, cuối cùng tạo thành bi kịch."),t("br"),t("br"),n._v("“Chúng ta cả nhà một trăm ba mươi bảy miệng, trong vòng một đêm bị tàn sát sạch sẽ, ta vừa vặn xa nhà đi săn, mới may mắn thoát khỏi tại khó...”"),t("br"),t("br"),n._v("Nhớ tới cừu hận trong lòng, hắn liền toàn thân run rẩy, nắm đấm không tự chủ được xiết chặt."),t("br"),t("br"),n._v("“Gia tộc của ngươi, tại Thiên Vũ vương thành?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Nếu như không phải tại Thiên Vũ vương thành, cũng không cần cẩn thận như vậy, liền lời cũng không dám nói."),t("br"),t("br"),n._v("“Vâng!” Lộ Xung gật đầu: “Ta tu vi quá kém, biết chắc trốn không thoát, mới trà trộn vào học viện, thâm cư không ra ngoài, không thể nào nói chuyện. Đương nhiên, đối phương cũng có thể là không có đem ta coi thành chuyện gì to tát, lúc này mới sống tạm đến bây giờ.”"),t("br"),t("br"),n._v("“Chỗ nguy hiểm nhất là chỗ an toàn nhất, đối phương khả năng không nghĩ tới ngươi có thể giấu ở làm người khác chú ý nhất địa phương. Chỉ là... Một cái gia tộc bị diệt, một trăm ba mươi bảy miệng bị tàn sát sạch sẽ, có lẽ náo ra rất động tĩnh lớn, chẳng lẽ Danh Sư đường cùng Thiên Vũ vương quốc không có quản?” Trương Huyền có chút không dám tin tưởng."),t("br"),t("br"),n._v("Danh Sư đường, trấn áp một phương, không chỉ bồi dưỡng danh sư, giảng dạy học sinh, càng quan trọng hơn là, duy trì lấy một phương trật tự, bình thường tiểu đả tiểu nháo có thể sẽ không để ý, nhưng một cái tàn sát toàn cả gia tộc, tuyệt đối sẽ gây nên oanh động, phái người dò xét."),t("br"),t("br"),n._v("Lại nói, coi như Danh Sư đường mặc kệ, Thiên Vũ vương quốc vương thất, cũng là đớp cứt? Có thể tại vương thành tùy tiện giết người? Thậm chí diệt tộc?"),t("br"),t("br"),n._v("“Cụ thể chuyện gì xảy ra, ta cũng không biết, chỉ biết là, tất cả mọi người chết rồi, liền ta sống, cũng không dám đi nghe ngóng, sợ bị phát hiện.” Lộ Xung mặt lộ vẻ dữ tợn, trong mắt hận ý chảy xuôi."),t("br"),t("br"),n._v("Lại hỏi vài câu, phát hiện hắn nói chi không rõ, Trương Huyền không hỏi tới nữa, an ủi một câu: “Cái khác không nên nghĩ, hảo hảo tu luyện, hôm qua truyền thụ cho ngươi phương pháp, chỉ cần kiên trì năm lần, không sai biệt lắm liền có thành tựu, có thể nhẹ nhõm chém giết Thông Huyền cảnh cường giả đỉnh phong. Hôm nay nghỉ ngơi trước đi, điều chỉnh trạng thái, dưỡng đủ tinh thần, ban đêm tiến hành lần thứ hai!”"),t("br"),t("br"),n._v("“Vâng!” Lộ Xung ôm quyền, quay người hướng ở ký túc xá đi đến."),t("br"),t("br"),n._v("Hắn nói đơn giản, nhưng Trương Huyền biết, tuyệt không phải dễ dàng như vậy."),t("br"),t("br"),n._v("Như thế đồ diệt gia tộc sự kiện, để Thiên Vũ vương quốc không có động tĩnh chút nào, hoặc là cừu nhân của hắn có được cực cao thực lực, hoặc là liền là có rất cao địa vị."),t("br"),t("br"),n._v("Trọng yếu nhất chính là, nếu như chỉ là vì yêu sinh hận, nhất giết chết thêm tỷ tỷ của hắn coi như xong, làm sao có thể đem toàn cả gia tộc đều hủy diệt?"),t("br"),t("br"),n._v("Chỉ sợ trong đó dính dáng đồ vật, cũng không phải là nói đơn giản như vậy."),t("br"),t("br"),n._v("Xem ra người học sinh này, mặc dù nói nghiêm túc, lại nghĩ một đằng nói một nẻo, ẩn giấu đi không ít thứ."),t("br"),t("br"),n._v("“Không nói được rồi, chờ hắn muốn nói, lại kỹ càng hỏi thăm đi!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu, không nghĩ nhiều nữa."),t("br"),t("br"),n._v("Mặc dù giúp đối phương tăng thực lực lên, nhưng thời gian chung đụng còn thiếu, không dám cùng bản thân đàm phán, cũng rất bình thường, không có gì có thể xoắn xuýt."),t("br"),t("br"),n._v("Về phần Thiên Đạo thư viện, chỉ có thể nhìn ra hắn có đại thù, kinh lịch chuyện tình hình chung, cũng không thể kỹ càng hắn nói ra cừu nhân của hắn là ai, vì sao lấy được thù."),t("br"),t("br"),n._v("Liền cùng Triệu Nhã, Mộc Tuyết Tinh đồng dạng, có thể nhìn ra hai người bị thương, thậm chí nguyên do, lại không thể biết, bị ai đả thương đồng dạng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đi ra phòng học, nhìn về phía giáo sư bên trong cái tuổi này không lớn Liễu lão sư, Lộ Xung hốc mắt ửng đỏ, nắm đấm xiết chặt."),t("br"),t("br"),n._v("Không phải hắn không muốn nói, mà là... Không dám!"),t("br"),t("br"),n._v("Cừu nhân của hắn cầm giữ có cái gì thân phận và địa vị, hắn biết đến rất rõ ràng, không muốn để cho trên cái thế giới này duy nhất đối với hắn thành tâm trả giá lão sư, bị độc thủ."),t("br"),t("br"),n._v("“Liễu lão sư, ngươi giúp ta tăng thực lực lên, ta đã thiếu to lớn ân tình, không cách nào trả lại, thực sự không muốn để cho ngươi dính vào... Ngươi dù sao chỉ là học viện một vị phổ thông lão sư! Một khi bị người phát hiện, thu ta vì học sinh, truyền thụ thực lực, khẳng định sẽ để cho ngươi lâm vào tai hoạ!”"),t("br"),t("br"),n._v("“Mối thù của ta... Liền để chính ta đi giải quyết đi!”"),t("br"),t("br"),n._v("Trong lòng ngầm hạ quyết định, Lộ Xung xoay người rời đi."),t("br"),t("br"),n._v("Một đêm bạo tăng nhiều như vậy lực lượng, để hắn một lần nữa dấy lên báo thù hi vọng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lộ Xung rời đi không lâu, những học sinh khác liền trở lại."),t("br"),t("br"),n._v("Cho những người khác giảng giải một chút kiến thức căn bản, lại chỉ điểm một hồi tu vi, lúc này mới đem Mộc Tuyết Tinh đơn độc gọi tới gian phòng, truyền thụ đao pháp."),t("br"),t("br"),n._v("Vừa mới bắt đầu nghe nói chỉ có một chiêu, Mộc Tuyết Tinh còn có chút thất vọng, học được một hồi, lúc này mới phát hiện ảo diệu, thân thể mềm mại không ngừng run rẩy."),t("br"),t("br"),n._v("Chiêu này đao pháp, mặc dù không nhận ra cấp bậc, lại biết, so phụ thân nàng truyền thụ cho Lạc Hoa quyền cao minh không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("Căn bản không phải cùng một cái khái niệm."),t("br"),t("br"),n._v("Một chiêu đao pháp, ẩn chứa thiên địa chí lý, hóa phức tạp thành đơn giản, chỉ muốn xuất thủ, cũng cảm giác làm cho không người nào có thể ngăn cản."),t("br"),t("br"),n._v("“Đa tạ Liễu lão sư trọng thưởng!”"),t("br"),t("br"),n._v("Luyện suốt một canh giờ, chân khí tại thể nội lao nhanh, không còn trở ngại, Mộc Tuyết Tinh lúc này mới tràn đầy hưng phấn quỳ mọp xuống đất, đối trước mắt vị lão sư này tràn đầy cảm kích cùng sùng bái."),t("br"),t("br"),n._v("Tiện tay giúp nàng giải quyết đan độc, lại đem lợi hại như thế võ kỹ truyền thụ..."),t("br"),t("br"),n._v("Phần ân tình này, đã khó mà dùng ngôn ngữ biểu đạt."),t("br"),t("br"),n._v("“Ta nhất định phải đem nữ nhân kia đánh bại, vì Liễu lão sư chính danh!”"),t("br"),t("br"),n._v("Răng ngà cắn chặt, âm thầm làm ra quyết định."),t("br"),t("br"),n._v("Lão sư như vậy đãi nàng, không có gì có thể báo đáp, chỉ có thể thay hắn dương danh, mà đánh bại danh sư Trương Huyền học sinh, chính là tốt nhất đường tắt."),t("br"),t("br"),n._v("Không biết tân thu người học sinh này, đã cắn răng nghiến lợi muốn muốn giáo huấn Triệu Nhã, Trương Huyền bàn giao đám người hảo hảo tu luyện, liền muốn rời khỏi phòng học."),t("br"),t("br"),n._v("Thật vất vả sưu tập Tông Sư cảnh công pháp, lại bởi vì linh khí không đủ, tu luyện chậm chạp, hiện tại có rảnh, đương nhiên muốn đi luyện đan sư công hội, tìm xem nhìn có hay không ẩn chứa linh lực dược vật."),t("br"),n._v("..."),t("br"),t("br"),n._v("“Ngươi nói vị này Liễu lão sư, thật có bọn hắn nói lợi hại như vậy? Tùy tiện chỉ điểm, cũng làm người ta đột phá?”"),t("br"),t("br"),n._v("Khoảng cách Trương Huyền lớp học chỗ không xa, hai người thiếu niên đứng tại một bên."),t("br"),t("br"),n._v("Bên trong một cái mở miệng nói."),t("br"),t("br"),n._v("Nếu có những học sinh khác tại cái này, nhất định có thể nhận ra, hai người này chính là học viện nổi danh nhất minh tinh, thi đấu đệ nhất Triệu Vũ Tinh cùng thứ hai Lưu Trường Nham."),t("br"),t("br"),n._v("Nói chuyện chính là Triệu Vũ Tinh."),t("br"),t("br"),n._v("“Đã nói như vậy, khẳng định có lý do! Mộc Tuyết Tinh tính tình bản tính ngươi cũng không phải không biết, có thể làm cho nàng ngoan ngoãn nghe lời, thậm chí không tiếc cùng Trương sư học sinh trở mặt, vị này Liễu lão sư khẳng định có không muốn người biết thủ đoạn.” Lưu Trường Nham nói tiếp."),t("br"),t("br"),n._v("Hôm qua tận mắt thấy hai nữ tranh đấu, hai người liền đối vị này trong truyền thuyết Liễu lão sư tràn ngập tò mò."),t("br"),t("br"),n._v("“Hôm nay làm sao cũng muốn đi mở mang kiến thức một chút, đến cùng phải hay không thật, ta có thể không tin, cái này học viện, tại dạy học bên trên, còn có người có thể siêu qua được Bạch lão sư!” Mặc dù biết đối phương nói như vậy, khẳng định không có vấn đề, Triệu Vũ Tinh vẫn như cũ có chút không tin."),t("br"),t("br"),n._v("Bọn hắn đều lại ở sân trường, cũng không biết trong miệng vị này Liễu lão sư tại y sư công hội náo ra thêm động tĩnh lớn, nếu không, cũng sẽ không tốt như vậy kỳ."),t("br"),t("br"),n._v("Về phần Bạch lão sư, là học viện nổi danh nhất minh tinh giáo sư, cùng lúc trước Lục Tầm tại Thiên Huyền vương quốc đồng dạng."),t("br"),t("br"),n._v("“Ừm? Vương thất xe ngựa tới đây làm gì?”"),t("br"),t("br"),n._v("Ngay tại nói thầm, đột nhiên, Lưu Trường Nham chỉ về phía trước."),t("br"),t("br"),n._v("Thuận nhìn lại, Triệu Vũ Tinh cũng kìm lòng không được sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("Chỉ thấy ngay phía trước một cỗ xe ngựa màu vàng óng, dọc theo đường đi chậm rãi chạy vào."),t("br"),t("br"),n._v("Chính là Thiên Vũ vương quốc vương thất xe ngựa."),t("br"),t("br"),n._v("“Tám con tuấn mã, xem bộ dáng là... Vương thất hạch tâm thành viên mới có thể cưỡi, chẳng lẽ là thái tử điện hạ hoặc là... Tam công chúa?”"),t("br"),t("br"),n._v("Nói lên Tam công chúa, Triệu Vũ Tinh, Lưu Trường Nham tất cả đều ánh mắt lửa nóng."),t("br"),t("br"),n._v("Tam công chúa Mạc Vũ, thế nhưng là trong lòng bọn họ hoàn mỹ nhất nữ thần, người xinh đẹp, thiên phú cao... Càng là khảo hạch danh sư thành công, tiếng chấn vương thành."),t("br"),t("br"),n._v("Nếu không phải hai ngày này Trương Huyền danh sư cùng Mạc Hoằng Nhất tên tuổi đè ép, khẳng định càng thêm loá mắt."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Xa ngựa dừng lại, một cái xinh đẹp thân ảnh đi xuống."),t("br"),t("br"),n._v("“Quả nhiên là công chúa điện hạ...”"),t("br"),t("br"),n._v("Nhìn thấy thân ảnh này, hai vị thiên tài thiếu niên, tất cả đều kích động sắc mặt đỏ lên, khó mà tự chế."),t("br"),t("br"),n._v("Bất quá, lập tức cũng cảm thấy có chút kỳ quái: “Nàng đến học viện làm gì?”"),t("br"),t("br"),n._v("Vị này Mạc Vũ công chúa, đều là nhất tinh danh sư, lại có cao như vậy thiên phú và tu vi, đương nhiên không có khả năng tới đến trường, càng không cần chạy tới mang khóa, cái kia muốn làm gì?"),t("br"),t("br"),n._v("Chính đang nghi ngờ, chỉ thấy nàng mấy bước đi tới Liễu lão sư lớp học phía trước, thân thể mềm mại khom người."),t("br"),t("br"),n._v("“Tại hạ Thiên Vũ vương quốc Mạc Vũ, chuyên tới để bái kiến Liễu Trình lão sư!”"),t("br"),t("br"),n._v("Thanh thúy to rõ thanh âm vang vọng tứ phương."),t("br"),t("br"),n._v("“Bái kiến Liễu lão sư?”"),t("br"),t("br"),n._v("“Đường đường nhất tinh danh sư, nhị tinh luyện đan sư, nhất tinh tuần thú sư... Mạc Vũ công chúa, lại để cho bái kiến vị này Liễu Trình?”"),t("br"),t("br"),n._v("Triệu Vũ Tinh, Lưu Trường Nham sững sờ, suýt chút nữa dọa đến răng rơi trên mặt đất."),t("br"),t("br"),n._v("Mạc Vũ công chúa thế nhưng là Thiên Vũ vương quốc, gần với Mạc Hoằng Nhất siêu cấp thiên tài, không riêng gì danh sư, thực lực bản thân, cũng đạt tới Thông Huyền cảnh... Chuyên đến bái kiến học viện một cái bình thường lão sư?"),t("br"),t("br"),n._v("Không nhìn lầm a?"),t("br"),t("br"),n._v("Hắn không phải hôm qua mới thi được học viện sao?"),t("br"),t("br"),n._v("Có tư cách gì để một vị danh sư, đường đường vương quốc công chúa tự mình tới?"),t("br"),t("br"),n._v("Bất quá, chấn kinh còn không có kết thúc, chỉ thấy một mực để bọn hắn rất ngạc nhiên Liễu lão sư, đẩy cửa đi ra, nhìn thoáng qua Mạc Vũ công chúa."),t("br"),t("br"),n._v("“Ngươi tới đây làm gì... Khụ khụ, tìm ta có chuyện gì?”"),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Triệu Vũ Tinh, Lưu Trường Nham thân thể nhoáng một cái, cảm thấy muốn thổ huyết."),t("br"),t("br"),n._v("Đây là thái độ gì?"),t("br"),t("br"),n._v("Cho dù có chút bản lĩnh, nhìn thấy công chúa, cũng nên cúi người chào a?"),t("br"),t("br"),n._v("Cho dù không như vậy, cũng chí ít xưng hô công chúa, khách khí... Trực tiếp “Ngươi tới đây làm gì”... “Tìm ngươi chuyện gì?”"),t("br"),t("br"),n._v("Ngươi cho rằng ngươi là ai a?"),t("br"),t("br"),n._v("Học viện một cái bình thường lão sư, liền minh tinh giáo sư đều không phải là, nhìn thấy đường đường nhất tinh danh sư, một điểm tôn kính đều không có, còn trong giọng nói mang theo chất vấn, cũng quá không đem người để ở trong mắt đi!"),t("br"),t("br"),n._v("Hai người chỉ cảm thấy lửa giận vọt tới da mặt, đang nghĩ ngợi như thế nào báo cáo học viện, cảnh cáo cái này tự cho là đúng gia hỏa, để hắn tuân thủ giáo sư công hội quyết định quy củ, chỉ thấy Mạc Vũ công chúa tiến về phía trước một bước: “Nhà ta phụ vương, chính chờ ở bên ngoài đợi, hi vọng Liễu lão sư có thể đi qua thấy một lần...”"),t("br"),t("br"),n._v("“Phụ vương? Vương quốc Mạc Thiên Tuyết bệ hạ?”"),t("br"),t("br"),n._v("“Hi vọng Liễu lão sư có thể... Thấy một lần? Đây là tình huống gì? Bệ hạ tự mình đến bái kiến một vị phổ thông lão sư?”"),t("br"),t("br"),n._v("Triệu Vũ Tinh, Lưu Trường Nham hai vị thiên tài, con mắt trợn tròn, giờ khắc này, thật cảm thấy điên rồi."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-348"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-350"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);