(window.webpackJsonp=window.webpackJsonp||[]).push([[1576],{1931:function(n,h,i){"use strict";i.r(h);var g=i(44),t=Object(g.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-26-hoc-tam-tra-hoi"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-26-hoc-tam-tra-hoi"}},[n._v("#")]),n._v(" Chương 26: Học tâm tra hỏi")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v(" “Trương Huyền, không nghĩ tới một ngày không gặp, ngươi lá gan như thế mập!” Đi vào gian phòng, Thượng Bân nhìn quanh một tuần, nhìn thấy cái này “Rác rưởi” lại thu rồi năm cái học sinh, trong mắt loé ra vẻ dữ tợn, cười lạnh: “Lại dám nói đầu độc Vương Dĩnh tiểu thư, Triệu Nhã tiểu thư đám người, càng lừa Tào Hùng lão sư học sinh, ai cho ngươi lá gan!” "),i("br"),i("br"),n._v(" “Thượng Bân lão sư? Tào Hùng lão sư?” "),i("br"),i("br"),n._v(" Nhìn thấy đột nhiên xông vào mấy người, Trương Huyền lông mày vừa nhíu: “Ta đang dạy, có chuyện gì, kính xin tan học lại nói!” "),i("br"),i("br"),n._v(" “Dạy bà nội mày học a! Còn thiếu tự mình lại đây, trang cái gì trang? Liền ngươi cái kia trình độ, không dạy hư học sinh là tốt lắm rồi, trả lại khóa, hai năm tám cũng không biết đồ vật, ở trước mặt chúng ta giả trang cái gì đầu to tỏi!” Tào Hùng lão sư cười gằn."),i("br"),i("br"),n._v(" Ngược lại hai người đã trở mặt, hắn cũng không cần thiết giả vờ."),i("br"),i("br"),n._v(" “Đi ra ngoài! Nơi này không hoan nghênh các ngươi!” Trương Huyền híp mắt lại."),i("br"),i("br"),n._v(" “Vẫn đúng là mặc lên rồi? Trương Huyền, ngươi còn biết mình họ gì không?” Thượng Bân cười lạnh: “Lập tức đem Triệu Nhã tiểu thư, Vương Dĩnh tiểu thư khóa lui đi, đem Lưu Dương một lần nữa chuyển cho Tào Hùng lão sư, ngày hôm nay ta có thể không ra tay giáo huấn ngươi, không phải vậy... Ta chỉ sợ ngươi cái này học kỳ, muốn ở xe lăn vượt qua!” "),i("br"),i("br"),n._v(" “Ngươi muốn động thủ với ta?” Trương Huyền nhìn sang: “Bên trong học viện, cấm chế lão sư lẫn nhau tư đấu, lẽ nào Thượng lão sư không biết nội quy trường học?” "),i("br"),i("br"),n._v(" “Nội quy trường học là bởi vì người mà định ra, đối phó người khác, ta sẽ lo lắng chịu đến học viện trừng phạt, đối phó ngươi nha... Ha ha, ngươi cảm thấy học viện sẽ có người thay ngươi tên rác rưởi này ra mặt?” Thượng Bân một bộ ăn chắc dáng dấp của đối phương."),i("br"),i("br"),n._v(" “Ta biết rồi, là không phải là bởi vì ngày hôm qua ta trong lời nói đắc tội rồi ngươi, ngươi mới cố ý tìm ta phiền phức? Ngươi làm như vậy, chẳng lẽ không sợ Trầm Bích Như lão sư tức giận?” Trương Huyền hừ nói."),i("br"),i("br"),n._v(" “Khà khà, coi như ngươi có tự mình biết mình! Ta nếu muốn giáo huấn ngươi, đương nhiên không sợ ngươi nhiều lời...” Thượng Bân cũng không phủ nhận, đang muốn dương dương đắc ý giáo huấn kẻ trước mắt này một trận, nói còn chưa dứt lời liền bị một cái nổi giận đùng đùng âm thanh đánh gãy."),i("br"),i("br"),n._v(" “Là ngươi!” "),i("br"),i("br"),n._v(" Diêu Hàn con mắt đỏ."),i("br"),i("br"),n._v(" Ngày hôm qua Trương Huyền đắc tội quá hắn, lại liên luỵ Trầm Bích Như lão sư, nếu như lại không cách nào xác định, hắn đều cảm giác mình là kẻ ngu!"),i("br"),i("br"),n._v(" “Cái gì?” Thấy mình đang định giáo huấn Trương Huyền, Diêu quản gia ra tay trước nổi giận, Thượng Bân nghi ngờ nhìn sang."),i("br"),i("br"),n._v(" “Chuyện của mình làm, tự mình không biết sao?” "),i("br"),i("br"),n._v(" Còn không có phản ứng lại, nương theo quát to một tiếng, một cái nắm đấm mạnh mẽ nện ở trên mặt."),i("br"),i("br"),n._v(" “Mịa nó!” "),i("br"),i("br"),n._v(" Thượng Bân chỉ cảm thấy mắt tối sầm lại, lập tức đã biến thành ô mắt thanh, thân thể lắc lư hai lần, trực tiếp ném tới."),i("br"),i("br"),n._v(" “Dám đem ta đánh thảm như vậy, ta muốn ngươi đẹp mặt!” "),i("br"),i("br"),n._v(" Nhớ tới ngày hôm qua bị đánh thống khổ, Diêu Hàn một kích thành công, không chút do dự nào, vừa tung người ngồi ở Thượng Bân trên thân, một quyền tiếp theo một quyền, bạo mưa gió giống như đập xuống."),i("br"),i("br"),n._v(" “Diêu quản gia...” "),i("br"),i("br"),n._v(" Tất cả đã đến quá đột nhiên, Vương Đào, Tào Hùng đều bối rối."),i("br"),i("br"),n._v(" Kỳ thực không riêng bọn họ bối rối, liền ngay cả Triệu Nhã, Vương Dĩnh mấy người cũng hai mặt nhìn nhau."),i("br"),i("br"),n._v(" đọc ｔruyện tại //ngantruyen.com/"),i("br"),n._v(" Vừa nãy những người này đến tìm Trương lão sư phiền phức, bọn họ còn rất tức giận, đang định giải thích một chút, kết quả bọn hắn tự mình liền đánh lên."),i("br"),i("br"),n._v(" Điều này cũng quá quái lạ đi!"),i("br"),i("br"),n._v(" Đặc biệt là Triệu Nhã, nàng biết rõ Diêu quản gia làm người, bình tĩnh thận trọng, làm việc có đầu có căn cứ, bằng không, cha cũng không thể để hắn làm quản gia."),i("br"),i("br"),n._v(" Có thể... Ngày hôm nay đây là thế nào? Làm sao nói còn chưa dứt lời liền động thủ?"),i("br"),i("br"),n._v(" Muốn nói trong đám người rõ ràng nhất xảy ra chuyện gì, chỉ sợ sẽ là Trương Huyền."),i("br"),i("br"),n._v(" Vừa nãy hắn cố ý nói ngày hôm qua đắc tội quá Thượng Bân, lại cố ý nói ra tên Trầm Bích Như, để Diêu quản gia biết hai cái điều kiện này... Kết quả, quả nhiên cùng đoán như thế, đánh nhau!"),i("br"),i("br"),n._v(" Cũng không phải Diêu quản gia lỗ mãng, mà là bất luận ai vô duyên vô cớ bị đánh thành như vậy, khẳng định trong lòng đều có chứa oán hận, hơn nữa thân phận của hắn tôn sùng, đại biểu Bạch Ngọc Thành bộ mặt, khi nào bị quá bực này oan ức? Hơn nữa, hai ngày nay bị Trương Huyền tức giận rối tinh rối mù, luân phiên bị tiểu thư ngăn cản, không chỗ phát tiết,"),i("br"),i("br"),n._v(" Từ lâu hỏa khí tràn ngập lồng ngực, giờ khắc này nhìn thấy kẻ thù, đâu còn có thể nhịn được."),i("br"),i("br"),n._v(" “Diêu quản gia, ngươi nổi điên làm gì?” Cảm nhận được giọt mưa giống như nắm đấm liên tục rơi xuống, lòng tràn đầy mộng bức Thượng Bân chỉ cảm thấy trước mắt đen kịt, một ngụm máu tươi không kém chút phun ra."),i("br"),i("br"),n._v(" Đến cùng chuyện ra sao?"),i("br"),i("br"),n._v(" Cái này Diêu quản gia, bộ dạng như thế lần thứ nhất gặp, không có đắc tội quá hắn đi, làm gì ác như vậy?"),i("br"),i("br"),n._v(" Hắn làm sao đều không nghĩ ra, đến cùng nơi nào đắc tội đối phương!"),i("br"),i("br"),n._v(" “Nổi điên làm gì? Ngươi bớt ở chỗ này giả ngu, ngươi chính mình không biết sao?” "),i("br"),i("br"),n._v(" Bành bành bành oành!"),i("br"),i("br"),n._v(" Diêu quản gia chút nào đều không ngừng nghỉ ngơi, từng quyền liên tục đánh xuống."),i("br"),i("br"),n._v(" Thượng Bân là cao cấp lão sư, thực lực từ lâu đạt đến võ giả năm tầng Đỉnh Lực cảnh, nhưng cùng Diêu Hàn sáu tầng Ích Huyệt cảnh, vẫn có chênh lệch nhất định, bình thường chiến đấu cũng không là đối thủ, huống chi xuất kỳ bất ý, một quyền đánh ngã."),i("br"),i("br"),n._v(" “Diêu quản gia, bớt giận, bớt giận, chuyện gì cũng từ từ...” "),i("br"),i("br"),n._v(" Tào Hùng, Vương Đào này mới phản ứng được, vội vàng đem hai người kéo dài."),i("br"),i("br"),n._v(" Lúc này, Thượng Bân anh tuấn gương mặt đã sưng cùng đầu heo không khác nhau chút nào."),i("br"),n._v(" “Đáng ghét!” Thượng Bân giờ khắc này sắp tức điên."),i("br"),i("br"),n._v(" Hắn nguyên bản muốn tới đây giáo huấn Trương Huyền một trận, nằm mơ đều không có nghĩ đến, cái này Bạch Ngọc Thành Diêu quản gia, đột nhiên phát rồ, đối với hắn liền đánh!"),i("br"),i("br"),n._v(" Thời khắc này, hắn đã đem đầy ngập lửa giận chuyển đến Diêu quản gia trên thân!"),i("br"),i("br"),n._v(" “Mấy vị có cái gì ân oán cá nhân, kính xin đến lớp học bên ngoài giải quyết, nơi này không phải là các ngươi đánh nhau ẩu đả địa phương, thật sự nếu không đi, tiếp tục nhiễu loạn bình thường giảng bài, ta liền thông báo chính giáo nơi, để bọn họ đi tới...” "),i("br"),i("br"),n._v(" Trương Huyền mở miệng."),i("br"),i("br"),n._v(" “Rất tốt... Các ngươi đều chờ đó cho ta!” "),i("br"),i("br"),n._v(" Nghe được muốn thông tri chính giáo nơi, Thượng Bân xoay người rời đi."),i("br"),i("br"),n._v(" Hắn hiện tại dáng vẻ ấy, truyền đi, còn thế nào gặp người? Tuyệt không thể để các lão sư khác nhìn thấy!"),i("br"),i("br"),n._v(" “Còn thiếu... Còn thiếu!” "),i("br"),i("br"),n._v(" Vốn tưởng rằng là chuyện rất đơn giản, còn thiếu tự thân xuất mã, Trương Huyền nhất định sẽ đem học sinh để trở về, nằm mơ đều không có nghĩ đến xuất hiện tình huống như thế, Tào Hùng vội vàng đuổi theo."),i("br"),i("br"),n._v(" Rất nhanh, hai người biến mất ở trước mắt."),i("br"),i("br"),n._v(" “Diêu thúc thúc, ngươi muốn làm gì?” "),i("br"),i("br"),n._v(" Hai người vừa đi, Triệu Nhã đi lên, nhìn trước mắt Diêu thúc thúc, tức giận đến mặt cười thấu đỏ."),i("br"),i("br"),n._v(" Vị này Diêu thúc thúc lấy trước thoạt nhìn rất bình thường, làm sao hiện tại càng ngày càng quá mức!"),i("br"),i("br"),n._v(" “Tiểu thư, ta...” Diêu Hàn không biết trả lời như thế nào."),i("br"),i("br"),n._v(" Hắn cũng không thể nói, ngày hôm qua đặc biệt tới giáo huấn Trương Huyền, muốn đem hắn thiến sạch, kết quả bị Thượng Bân đánh, mới vừa ở chỉ là xác nhận thân phận đối phương, báo thù rửa hận đi!"),i("br"),i("br"),n._v(" “Tốt, ta đang dạy, còn mấy vị mời đi ra ngoài!” "),i("br"),i("br"),n._v(" Trương Huyền xua tay."),i("br"),i("br"),n._v(" Do dự một chút, Diêu Hàn, Vương Đào, Lưu lão ba người lần thứ hai đi ra ngoài."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" “Đáng ghét, đáng ghét!” "),i("br"),i("br"),n._v(" Rời đi Trương Huyền lớp học, Thượng Bân tức giận oa oa kêu loạn: “Cái này Diêu Hàn nhất định phải dạy dỗ một trận, còn có cái kia Trương Huyền, lại dám coi rẻ ta, cũng phải cố gắng giáo huấn!” "),i("br"),i("br"),n._v(" Hắn đường đường trưởng lão cháu, luôn luôn thuận buồm xuôi gió, đâu chịu nổi này loại oan ức!"),i("br"),i("br"),n._v(" Hơn nữa, mấu chốt nhất là, cái này oan ức chịu không hiểu ra sao."),i("br"),i("br"),n._v(" Mới vừa rồi còn nói chuyện khỏe mạnh, thời gian nháy mắt liền xông lại đánh lén, quả thực không hề có đạo lý!"),i("br"),i("br"),n._v(" “Cái này Diêu Hàn khẳng định cùng Trương Huyền là một nhóm! Không phải vậy, làm sao có khả năng đối với còn thiếu ra tay? Ta cảm thấy khẳng định là bị chỉ thị của hắn mới làm như vậy!” Tào Hùng quát."),i("br"),i("br"),n._v(" “Không sai, khẳng định đúng!” Thượng Bân cũng phản ứng lại."),i("br"),i("br"),n._v(" Tự mình chưa bao giờ tội lỗi cái này Diêu Hàn, đối phương nhưng trực tiếp động thủ, khẳng định là bởi vì chính mình dự định giáo huấn Trương Huyền!"),i("br"),i("br"),n._v(" “Ngươi xác định cái kia Lưu Dương bái Trương Huyền vi sư, không phải tự nguyện?” "),i("br"),i("br"),n._v(" Một lát sau, Thượng Bân từ nổi giận tình huống bình phục lại, quay đầu nói."),i("br"),i("br"),n._v(" “Ta có thể xác nhận, tuyệt đối không phải tự nguyện!” Nhớ tới ngày hôm qua Lưu Dương trước khi đi không cam lòng, Tào Hùng đầy cõi lòng tự tin gật đầu."),i("br"),i("br"),n._v(" “Vậy thì tốt, đã ngươi như vậy xác định, ngươi bây giờ liền đi xin học tâm tra hỏi!” Thượng Bân nói."),i("br"),i("br"),n._v(" “Học tâm tra hỏi? Chuyện này...” Tào Hùng biến sắc mặt."),i("br"),i("br"),n._v(" Học tâm tra hỏi, là học viện lão sư trong lúc đó thật sự có không cách nào phán quyết học viên, tiến hành một loại phương pháp đặc thù, bị lão sư cạnh tranh học sinh, sẽ tiếp thu Học Tâm Tháp sát hạch, Học Tâm Tháp sẽ đem hắn chân chính ý nghĩ lấy độ tín nhiệm phương thức chiếu rọi đi ra."),i("br"),i("br"),n._v(" Nếu như ứng viên xin học viên, học tâm tra hỏi cũng không phải là tự nguyện, sẽ đối với một cái khác lão sư tiến hành trừng phạt, mà nếu như ứng viên vu cáo, cũng đồng dạng sẽ phải chịu tương ứng trừng phạt."),i("br"),i("br"),n._v(" Đây là hay vị lão sư tranh đoạt cùng một cái học sinh, thực sự không có cách nào tình huống mới cách sử dụng."),i("br"),i("br"),n._v(" Tào Hùng mặc dù biết, nhưng cảm giác được còn không có đến một bước này."),i("br"),i("br"),n._v(" “Làm sao? Ngươi là gạt ta? Lẽ nào cái tên này là tự nguyện?” Thấy hắn do dự, Thượng Bân vốn là tâm tình không tốt, sầm mặt lại."),i("br"),i("br"),n._v(" “Không phải, hắn tuyệt đối không phải tự nguyện, ta... Vậy thì xin! Để cái này Trương Huyền cố gắng tiếp thu trừng phạt!” Tào Hùng gật đầu."),i("br"),i("br"),n._v(" “Hừm, vậy thì động tác nhanh lên một chút!” Thấy hắn đáp ứng, Thượng Bân lúc này mới thoả mãn gật đầu: “Xin thời điểm, có thể đem trừng phạt tăng lên tới to lớn nhất! Để cái này Trương Huyền ăn thật ngon một trận vị đắng, tốt nhất có thể khai trừ!” "),i("br"),i("br"),n._v(" Trừng phạt có thể căn cứ ứng viên yêu cầu tăng cường, thêm càng lớn, một phương thất bại chịu đến trừng phạt cũng là càng nặng."),i("br"),i("br"),n._v(" “Hảo!” Tào Hùng ánh mắt sáng lên, cũng đầy là vẻ hưng phấn, trong mắt mang theo Trương Huyền bị khai trừ ước mơ."),i("br"),i("br"),n._v("Convert by: Hoang Chau"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-34"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-36"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);