(window.webpackJsonp=window.webpackJsonp||[]).push([[1577],{1932:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-341-linh-thach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-341-linh-thach"}},[n._v("#")]),n._v(" Chương 341: Linh thạch")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Không chỉ hai người bọn họ buồn bực, ngay cả Mạc Vũ cũng cảm thấy lông mày nhảy loạn, suýt chút nữa bạo tẩu."),t("br"),t("br"),n._v("Nàng tự mình tới, đã cảm thấy có chút biệt khuất, gia hỏa này ngược lại tốt."),t("br"),t("br"),n._v("Một bộ thái độ thờ ơ!"),t("br"),t("br"),n._v("Người khác lần thứ nhất gặp mặt, kém cỏi nhất đều là “Bái kiến Mạc Vũ công chúa”, “Tại hạ xin kính chào” tận lực giả bộ thân sĩ, hắn trực tiếp tới câu, ngươi tới đây làm gì..."),t("br"),t("br"),n._v("Ta cùng ngươi rất quen sao?"),t("br"),t("br"),n._v("Nếu không phải phụ vương dặn đi dặn lại bàn giao, ngươi cho rằng ta sẽ tới?"),t("br"),t("br"),n._v("Bái kiến không có lễ phép, chưa thấy qua không lễ phép như vậy!"),t("br"),t("br"),n._v("Ngực miệng không ngừng chập trùng, Mạc Vũ khí nghiến răng nghiến lợi."),t("br"),t("br"),n._v("Nhớ tới trước khi đến trước, phụ vương bàn giao, đã cảm thấy càng thêm buồn bực, chỉ có thể cố nén tức giận."),t("br"),t("br"),n._v("Phụ vương kết giao vị này Liễu lão sư, nhất định phải nàng sớm tới, ý tứ rất đơn giản... Để bọn hắn nhận thức một chút, tốt nhất có thể phát triển thứ gì!"),t("br"),t("br"),n._v("Như vậy, đối nàng có ích lợi rất lớn."),t("br"),t("br"),n._v("Vương thất cạnh tranh tàn khốc, Mạc Hoằng Nhất mặc dù họ Mạc, lại không phải đích không phải thân, lập tức liền muốn trở thành nhị tinh danh sư, lại thêm bản thân thiên phú cường đại, thật muốn cướp đoạt vương vị, cái gọi là thái tử, vương tử căn bản cũng không phải là đối thủ."),t("br"),t("br"),n._v("Chỉ sợ cũng chỉ có cùng là danh sư nàng mới có thể chống chọi một hai."),t("br"),t("br"),n._v("Cho nên, Mạc Thiên Tuyết bệ hạ có ý định muốn vun trồng Mạc Vũ, để nàng thành làm một đời Nữ Đế, chống chọi Mạc Hoằng Nhất, không đến mức quyền lực bên ngoài rơi... Như vậy trải qua, nàng sau này vị hôn phu liền rất là trọng yếu."),t("br"),t("br"),n._v("Hiện tại toàn bộ vương thành, danh tiếng thịnh nhất người trẻ tuổi, không ai qua được Trương Huyền danh sư cùng vị này Liễu lão sư."),t("br"),t("br"),n._v("Trương Huyền danh sư địa vị cao, thực lực mạnh, phía sau lại có lợi hại lão sư chỗ dựa, coi như về sau có tính thực chất quan hệ, cũng không tốt khống chế."),t("br"),t("br"),n._v("Vị này Liễu lão sư lại khác biệt."),t("br"),t("br"),n._v("Phụ vương chuyên môn phái người điều tra qua, sinh trưởng ở địa phương Thiên Vũ vương quốc người, bởi vì thân thể nguyên nhân, không cách nào khảo hạch danh sư... Tu vi cũng chỉ có Thông Huyền cảnh sơ kỳ, không tính là quá cao."),t("br"),t("br"),n._v("Vốn là, những loại người này không có khả năng xứng được với Mạc Vũ, ai cũng không nghĩ tới, hắn y thuật cao minh, thậm chí nhảy lên trở thành y sư công hội hội trưởng."),t("br"),t("br"),n._v("Y sư mặc dù không phải chủ chiến chức nghiệp, lại là vô số người không dám đắc tội chức nghiệp một trong, bởi vì ai cũng không thể cam đoan, sẽ không nhiễm bệnh."),t("br"),t("br"),n._v("Nếu như người này thật cùng Mạc Vũ có quan hệ, vua của nàng vị, khẳng định liền có thể ngồi vững vàng, coi như Mạc Hoằng Nhất muốn cướp đoạt, cũng muốn cố kỵ một hai."),t("br"),t("br"),n._v("Bởi vì những này, Mạc Vũ mới nhịn xuống buồn bực, đặc biệt tới mời, vốn cho rằng sẽ thụ sủng nhược kinh, cao hứng khoa tay múa chân, nằm mơ đều không nghĩ tới, một bộ không thèm để ý chút nào bộ dáng."),t("br"),t("br"),n._v("Nàng có thể là công chúa, danh sư..."),t("br"),t("br"),n._v("Cái này coi là thái độ gì?"),t("br"),t("br"),n._v("Răng cắn chặt, hừ một tiếng."),t("br"),t("br"),n._v("Đưa ra phụ vương, cho rằng vị này Liễu Trình coi như phách lối nữa, cũng muốn cố kỵ một hai, đã thấy hắn nhướng mày: “Phụ vương của ngươi? Hắn tìm ta làm gì? Ta còn có việc, không có quá nhiều thời gian!”"),t("br"),t("br"),n._v("Đối với cái gọi là vương quốc quốc vương, Trương Huyền cũng không để ý."),t("br"),t("br"),n._v("Đường đường nhị tinh danh sư, cũng quả thực có vốn liếng này cùng ngạo khí."),t("br"),t("br"),n._v("Bất quá, nghe vào Mạc Vũ công chúa trong tai, cũng có chút phát điên."),t("br"),t("br"),n._v("Coi như ngươi là y sư công hội hội trưởng, nhưng bây giờ thực lực bản thân không đủ, đẳng cấp không đạt tới, cũng chỉ là hữu danh vô thực, có gì có thể phách lối?"),t("br"),t("br"),n._v("Còn không có quá nhiều thời gian gặp cha..."),t("br"),t("br"),n._v("Cuồng vọng như vậy, tại sao không đi chết..."),t("br"),t("br"),n._v("Một bên Triệu Vũ Tinh, Lưu Trường Nham cũng sắp thổ huyết."),t("br"),t("br"),n._v("Vốn cho rằng đối mặt công chúa không có chút nào cung kính, liền rất làm cho người ta không nói được lời nào, không nghĩ tới nghe được bệ hạ đồng dạng không quan tâm..."),t("br"),t("br"),n._v("Ngươi làm như vậy, chẳng lẽ liền không sợ chọc giận Mạc Vũ công chúa, bị một bàn tay quất chết?"),t("br"),t("br"),n._v("“Phụ vương chuyên mà đến, cứ như vậy chối từ, không khỏi không tốt lắm đâu!” Răng ngà cắn chặt, Mạc Vũ khẽ nói."),t("br"),t("br"),n._v("“Ây... Vậy thì tốt, mang ta tới!” Trương Huyền khoát khoát tay."),t("br"),t("br"),n._v("Hắn cũng không phải hữu tâm khí vị này kiêu ngạo công chúa, mà là theo Thú đường một mạch đi đến Hồng Liên thành, quá quen thuộc, gặp nàng tới bái kiến, trong lúc nhất thời liền không có chú ý liễu thân phận lão sư."),t("br"),t("br"),n._v("Bất quá, hiện tại xem ra Danh Sư đường mặt nạ da người hết sức lợi hại, lại thêm làm ra thân phận giả không chê vào đâu được, liền xem như nàng, cũng không nhìn ra sơ hở."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Hất lên ống tay áo, Mạc Vũ đi thẳng về phía trước."),t("br"),t("br"),n._v("Nhìn hai người rời đi, Triệu Vũ Tinh, Lưu Trường Nham hai mặt nhìn nhau."),t("br"),t("br"),n._v("Gia hỏa này không phải liền là cái phổ thông lão sư sao? Vì sao có thể làm cho quốc vương bệ hạ tự mình bái kiến, lại thêm đem Mạc Vũ công chúa tức thành như vậy, cũng không bão nổi?"),t("br"),t("br"),n._v("Đừng nói lão sư, coi như Tạ viện trưởng, chỉ sợ cũng không có cái đặc quyền này a?"),t("br"),t("br"),n._v("“Đi qua nhìn một chút...”"),t("br"),t("br"),n._v("Rốt cuộc kìm nén không được hiếu kỳ, hai người gấp đi theo."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ngoài cửa học viện, đỗ lấy một cỗ to lớn xe ngựa, Trương Huyền đám người còn chưa tới đến trước mặt, chỉ thấy hoàng màn kích động, một cái khí tức uy vũ trung niên nhân đi xuống."),t("br"),t("br"),n._v("Người này hai mắt như điện, khí tức như rồng, còn chưa tới đến trước mặt, liền cho người ta một loại cảm giác áp bách mãnh liệt, lại là Tông Sư cảnh cường giả đỉnh phong, khoảng cách nửa bước Chí Tôn, cũng chỉ kém một bước cuối cùng."),t("br"),t("br"),n._v("“Nghĩ đến vị này liền là Liễu hội trưởng...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết cười tiến lên đón."),t("br"),t("br"),n._v("Vừa đi vừa tử quan sát kỹ."),t("br"),t("br"),n._v("Một lát sau, ánh mắt lộ ra tán dương hương vị."),t("br"),t("br"),n._v("Thân cư cao vị, trên người hắn tự mang một loại lực áp bách, coi như cùng cấp bậc cường giả gặp gỡ, đều sẽ cảm thấy bối rối."),t("br"),t("br"),n._v("Thanh niên trước mắt, tuổi không lớn lắm, lại ung dung tự nhiên, không khẩn trương chút nào, đơn phần này tâm tính, cũng không phải là bình thường người có thể so sánh."),t("br"),t("br"),n._v("Không hổ là nghi vấn khó xử lý tường liền giải quyết mười chín đạo siêu cấp cường nhân, can đảm cùng tâm trí, không phải người thường có thể bằng."),t("br"),t("br"),n._v("“Không biết quốc vương bệ hạ tìm tại hạ chuyện gì!” Trương Huyền nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Liễu hội trưởng liên tục giải quyết nghi vấn khó xử lý tường mười chín đạo nan đề, một tiếng hót lên làm kinh người, là ta một mực rất sùng bái. Hôm nay vừa vặn đi ngang qua nơi này, muốn mời hội trưởng đi hoàng cung ngồi xuống, để cho ta cũng tận một chút chủ nhà tình nghĩa!”"),t("br"),n._v("Mạc Thiên Tuyết nói."),t("br"),t("br"),n._v("“Hoàng cung?” Trương Huyền lắc đầu: “Thật không tiện, ta còn có việc muốn đi luyện đan sư công hội một chuyến, khả năng không rảnh đi qua, hảo ý tâm lĩnh!”"),t("br"),t("br"),n._v("Hắn hiện tại còn muốn đi luyện đan sư công hội, tìm kiếm ẩn chứa linh khí dược vật, về phủ đệ truyền thụ Triệu Nhã võ kỹ, quả thực không có cái gì thời gian đi hoàng cung."),t("br"),t("br"),n._v("“Cự tuyệt bệ hạ mời?”"),t("br"),t("br"),n._v("Triệu Vũ Tinh, Lưu Trường Nham vừa tới đến cách đó không xa, liền nghe nói như thế, dọa đến tất cả đều rụt cổ lại."),t("br"),t("br"),n._v("Vị này Liễu lão sư cũng quá độc ác đi!"),t("br"),t("br"),n._v("Không chỉ đối Mạc Vũ công chúa thái độ bất kính, hiện tại bệ hạ ở trước mặt mời, đều trực tiếp từ chối..."),t("br"),t("br"),n._v("“Đã Liễu hội trưởng có việc, ta cũng không tiện quấy rầy, đây là đang ở dưới một phen tâm ý, xin hãy nhận lấy!”"),t("br"),t("br"),n._v("Nghe hắn cự tuyệt, Mạc Thiên Tuyết cũng không tức giận, ngược lại mỉm cười, một vị thái giám đi tới, đưa đến một cái hộp ngọc."),t("br"),t("br"),n._v("Y sư công hội mặc dù tại vương thành phạm vi bên trong, nhưng có thể làm được hội trưởng, đã có tư cách để hắn ngang hàng luận giao."),t("br"),t("br"),n._v("Vẫy tay một cái."),t("br"),t("br"),n._v("Thái giám mở hộp ngọc ra, một đạo linh khí nồng nặc đập vào mặt, để cho người ta toàn thân huyết mạch cũng vì đó hưng phấn."),t("br"),t("br"),n._v("Trong hộp ngọc trưng bày một cái lớn chừng hột đào óng ánh sáng long lanh tảng đá, phản xạ ánh nắng, kim cương sáng chói."),t("br"),t("br"),n._v("“Linh thạch?”"),t("br"),t("br"),n._v("“Là linh thạch!”"),t("br"),t("br"),n._v("Nhìn thấy thứ này, Triệu Vũ Tinh, Lưu Trường Nham tất cả đều thân thể run lên, trong mắt đồng thời dấy lên hỏa diễm."),t("br"),t("br"),n._v("Linh thạch, phong hào vương quốc đều rất ít gặp đặc thù khoáng thạch, sản xuất tại linh khí hội tụ chi địa, mỗi một khối đều vô cùng trân quý, ức vạn khó cầu, là người tu luyện dùng tới tu luyện vô thượng bảo bối."),t("br"),t("br"),n._v("Nghe nói thứ này, chỉ giao lưu tại cao tầng bên trong, có tiền mà không mua được, coi như nhị tinh danh sư, muốn cầu lấy một viên đều không thể được, không nghĩ tới... Quốc vương bệ hạ thế mà tiện tay xuất ra một viên, đưa cho trước mắt vị này Liễu lão sư!"),t("br"),t("br"),n._v("Phần lễ vật này quả thực quá lớn!"),t("br"),t("br"),n._v("Đổi lại bọn họ, sợ rằng sẽ trong nháy mắt kích động, khó mà tự chế, tìm không thấy phương hướng."),t("br"),t("br"),n._v("Vội vàng nhìn về phía cách đó không xa Liễu lão sư... Đã thấy hắn một bộ thờ ơ biểu lộ, không nhúc nhích chút nào."),t("br"),t("br"),n._v("“Khó trách... Chỉ Liễu lão sư phần này bình tĩnh, cũng làm người ta bội phục!”"),t("br"),t("br"),n._v("Thấy cảnh này, hai người đồng thời phục sát đất."),t("br"),t("br"),n._v("Đây chính là linh thạch, chỉ cần là người tu luyện, nhìn thấy không có không hô hấp dồn dập, khó mà tự chế, Liễu lão sư có thể mặt không đổi sắc, chỉ bằng vào điểm ấy, đã làm cho bệ hạ như thế lấy lễ để tiếp đón..."),t("br"),t("br"),n._v("Ngay tại âm thầm cảm khái, giữa người và người đích thật là có khoảng cách, liền nghe đến cách đó không xa thanh niên vẻ mặt vô cùng nghi hoặc nhìn qua: “Đây là cái gì?”"),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Hai người mắt tối sầm lại, sắp khóc."),t("br"),t("br"),n._v("Mới vừa rồi còn phục sát đất, náo loạn nửa ngày, vị này Liễu lão sư... Căn bản không biết!"),t("br"),t("br"),n._v("Hại bọn hắn lãng phí không biểu lộ..."),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Chính một mặt tự ngạo, hi vọng nhìn thấy Trương Huyền khiếp sợ Mạc Thiên Tuyết, thiếu chút nữa cũng bị ngụm nước bị nghẹn, tràn đầy im lặng."),t("br"),t("br"),n._v("Linh thạch, coi như chưa thấy qua, cũng hầu như nghe qua đi!"),t("br"),t("br"),n._v("Vị này Liễu hội trưởng, đối y đạo như thế tinh thông, kết quả... Lại ngay cả thứ này nghe đều chưa từng nghe qua..."),t("br"),t("br"),n._v("Trương Huyền mặc dù tại Danh Sư đường nhìn không ít viết, nhưng những sách vở này, cũng không dung hợp thành trí nhớ của mình, chỉ có kiểm tra thời điểm mới có thể xuất hiện, bình thường cho dù có tri thức gì, cũng không rõ ràng."),t("br"),t("br"),n._v("Thật giống như kiếp trước một chút công cụ tìm kiếm, có thể lục soát không ít tri thức, tùy thời tùy chỗ để người hiểu không biết sự tình... Nhưng điều kiện tiên quyết là, muốn sớm biết muốn lục soát chính là cái gì, nếu không, cũng không có cách nào!"),t("br"),t("br"),n._v("Nói cách khác, những kiến thức này, coi như tại thư viện, có thể tùy thời đọc qua, cũng không thuộc về với hắn."),t("br"),t("br"),n._v("Hiện tại liền là loại tình huống này, đột nhiên nhìn thấy một cái óng ánh sáng long lanh tảng đá, coi như trong óc thư tịch lại nhiều, không biết từ chỗ nào vốn tìm lên, cũng không khớp kêu."),t("br"),t("br"),n._v("“Cái này là một cái linh thạch, ẩn chứa trong đó hùng hậu linh khí, có thể để người ta tu luyện làm ít công to...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết giải thích."),t("br"),t("br"),n._v("“Linh thạch?”"),t("br"),t("br"),n._v("Biết danh tự, tinh thần khẽ động, trong đầu lập tức xuất hiện một bản tương quan thư tịch, Trương Huyền tiện tay lật ra."),t("br"),t("br"),n._v("Quả nhiên thấy kỹ càng miêu tả: Linh thạch là từ linh khí hội tụ mà thành khoáng thạch, ẩn chứa trong đó phong phú linh khí, dùng tới tu luyện, có thể giải quyết trong không khí linh khí thưa thớt bối rối..."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nháy con mắt, Trương Huyền hơi đỏ mặt."),t("br"),t("br"),n._v("Hắn chính thật mong muốn tìm kiếm linh khí sung túc đan dược, có thứ này, còn đi cái gì luyện đan sư công hội?"),t("br"),t("br"),n._v("Hoàn toàn có thể hấp thu linh khí trong đó, trùng kích Tông sư đỉnh phong!"),t("br"),t("br"),n._v("“Đa tạ!”"),t("br"),t("br"),n._v("Biết thứ này trân quý, cũng không đợi đối phương giới thiệu xong, Trương Huyền khẽ vươn tay lập tức cầm ở lòng bàn tay, thu vào nạp vật nhẫn."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Nhìn hắn trước một khắc còn bình tĩnh không thèm quan tâm, sau một khắc trực tiếp lấy đi, sợ người khác cướp đoạt, Mạc Thiên Tuyết, Mạc Vũ tất cả đều phát điên."),t("br"),t("br"),n._v("Mẹ nó, muốn hay không biến hóa nhanh như vậy?"),t("br"),t("br"),n._v("Chính cảm giác đối phương có phải hay không có chút tinh thần phân liệt, chỉ thấy thanh niên trước mắt, hai tay chắp sau lưng, con mắt nhìn xéo không trung, thần sắc cao thâm xa xăm, như là hiếm có thế ngoại cao nhân."),t("br"),t("br"),n._v("“Ta sẽ không tùy tiện muốn ngươi đồ vật, như vậy đi, ta có thể miễn phí vì ngươi chữa bệnh một lần!”"),t("br"),t("br"),n._v("“...” Mạc Thiên Tuyết, Mạc Vũ."),t("br"),t("br"),n._v("“...” Triệu Vũ Tinh, Lưu Trường Nham."),t("br"),t("br"),n._v("(Đề cử hảo hữu một quyển sách 《 Thần Môn 》, đại thần xuất phẩm, không tầm thường, đáng giá xem xét!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-349"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-351"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);