(window.webpackJsonp=window.webpackJsonp||[]).push([[1578],{1933:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-342-truyen-thu-kiem-phap"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-342-truyen-thu-kiem-phap"}},[n._v("#")]),n._v(" Chương 342: Truyền thụ kiếm pháp")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Linh thạch mười phần trọng yếu, đã đối phương đưa ra, đương nhiên muốn bắt lấy."),i("br"),i("br"),n._v("Bất quá, Trương Huyền cũng sẽ không trắng thu tiền trà nước, cho bọn hắn miễn phí trị liệu một lần, liền làm thù lao."),i("br"),i("br"),n._v("“Muốn nói trị liệu, khả năng thật đúng là cần Liễu hội trưởng xuất thủ một lần!”"),i("br"),i("br"),n._v("Bó tay rồi một hồi, Mạc Thiên Tuyết ôm quyền nói."),i("br"),i("br"),n._v("Hắn giao hảo trước mắt vị này, mục đích chủ yếu, liền là muốn mượn đối phương y thuật, hiện tại đã mở lời, đương nhiên sẽ không chối từ."),i("br"),i("br"),n._v("“Đánh quyền đi!” Trương Huyền khoát tay."),i("br"),i("br"),n._v("“Đánh quyền?” Mạc Thiên Tuyết lần nữa im lặng, chữa bệnh không nên trình bày bệnh tình sao? Đánh quyền cái quỷ gì?"),i("br"),i("br"),n._v("Cười cười xấu hổ: “Không phải trị liệu cho ta, mà là... Như vậy đi, nếu như Liễu hội trưởng hiện tại có rảnh, có thể hay không đi hoàng cung một lần?”"),i("br"),i("br"),n._v("“Ta còn có chút việc, xử lý xong, liền đi qua một chuyến!” Chần chờ một chút, Trương Huyền nói."),i("br"),i("br"),n._v("“Vậy thì tốt, tiểu Vũ, Liễu hội trưởng không phải muốn đi luyện đan sư công hội sao? Ngươi cùng hắn đi qua một chuyến, vừa vặn ngươi là nhị tinh luyện đan sư, đối nơi đó cũng tương đối quen thuộc!”"),i("br"),i("br"),n._v("Gặp hắn đáp ứng, Mạc Thiên Tuyết cười vuốt râu: “Lại nói, các ngươi đều là thiên tài, cũng có thể thừa cơ hiểu rõ hơn một hai, lẫn nhau nghiên cứu thảo luận một phen.”"),i("br"),i("br"),n._v("Vừa rồi vị này nói muốn đi luyện đan sư công hội một chuyến chính là cơ hội tốt, vạn một, hai người chỗ mọi nơi lấy có cảm giác, liền càng hoàn mỹ hơn."),i("br"),i("br"),n._v("“Bệ hạ để Mạc Vũ công chúa tiếp khách?”"),i("br"),i("br"),n._v("“Rất rõ ràng là muốn đem công chúa giới thiệu cho Liễu lão sư...”"),i("br"),i("br"),n._v("Thấy cảnh này, Triệu Vũ Tinh, Lưu Trường Nham tất cả đều vẻ mặt cầu xin."),i("br"),i("br"),n._v("Thân phận địa vị, mỹ mạo, thiên phú... Giống như trời cao cũng yêu quý Mạc Vũ công chúa một người, đem chỗ có chỗ tốt đều cho, cùng nàng cùng một chỗ, chỉ sợ bất kỳ nam nhân nào đều không thể cự tuyệt."),i("br"),i("br"),n._v("Hiện tại bệ hạ nói như thế rõ ràng, còn kém trực tiếp hôn phối, để bọn hắn những này thầm sùng bái người, tan nát cõi lòng đầy đất."),i("br"),i("br"),n._v("Liền tại bọn hắn cảm thấy vị này Liễu lão sư, khẳng định sẽ vui vẻ đáp ứng, mặt mũi tràn đầy cao hứng thời điểm, đã thấy hắn nhướng mày, lắc đầu: “Không cần...”"),i("br"),i("br"),n._v("“Không cần?” Mạc Thiên Tuyết cũng là sững sờ: “Liễu hội trưởng không phải muốn đi luyện đan sư công hội sao? Tiểu nữ mặc dù ở bên trong coi là không đến đỉnh tiêm, nhưng cũng có chút quyền nói chuyện, để nàng mang ngươi tới, mua sắm đan dược các loại, có thể miễn đi không ít phiền phức!”"),i("br"),i("br"),n._v("Thiên Vũ vương quốc luyện đan sư công hội, đan thuật cao nhất hội trưởng, cũng chỉ tam tinh, nhị tinh xem như địa vị cấp cao."),i("br"),i("br"),n._v("“A, luyện đan sư công hội ta không đi, còn có chuyện khác, liền cáo từ trước, bên này làm xong, sẽ tiến về hoàng cung bái phỏng!” Trương Huyền ôm quyền cáo từ."),i("br"),i("br"),n._v("Hắn đi luyện đan sư công hội, chính là vì tìm kiếm ẩn chứa linh khí dược vật, hiện tại có linh thạch, đương nhiên không cần."),i("br"),i("br"),n._v("Việc cấp bách, là về phủ đệ, đem kiếm pháp truyền thụ cho Triệu Nhã."),i("br"),i("br"),n._v("Dù sao nàng hai ngày sau còn muốn cùng người nào tỷ thí."),i("br"),i("br"),n._v("“Không đi?”"),i("br"),i("br"),n._v("Mạc Thiên Tuyết nháy con mắt."),i("br"),i("br"),n._v("Mạc Vũ càng là nghiến răng nghiến lợi."),i("br"),i("br"),n._v("Nàng phụ vương đều nói như thế minh xác, rõ ràng, gia hỏa này thế mà cự tuyệt... Cự tuyệt ngược lại cũng thôi, vừa mới thái độ kiên quyết nói muốn đi luyện đan sư công hội, nghe nói mình bồi tiếp, mã không thể đi lên..."),i("br"),i("br"),n._v("Ta là gái xấu sao? Đem ngươi sợ đến như vậy?"),i("br"),i("br"),n._v("“Cáo từ...”"),i("br"),i("br"),n._v("Không biết tùy tiện một câu làm cho đối phương hiểu lầm, Trương Huyền xoay người rời đi."),i("br"),i("br"),n._v("Danh sư, cầm giữ có thể không nhìn vương quyền địa vị, đối phương nhiều nhất cũng chính là một cái nhất đẳng vương quốc quốc vương mà thôi, không nói danh sư công hội uy hiếp, coi như một cái bình thường nhị tinh danh sư, bọn hắn cũng không dám thế nào."),i("br"),i("br"),n._v("Bởi vì danh sư lực hiệu triệu thực sự quá lớn, thật đắc tội loại quái vật này, tùy tiện đến cái giảng bài, chỉ sợ cũng có thể hội tụ toàn bộ vương quốc đại bộ phận tinh nhuệ."),i("br"),i("br"),n._v("Một khi nghe giảng bài nhận được chỗ tốt, chẳng khác nào có nửa sư tình nghĩa, để bọn hắn hủy diệt vương quyền, cũng dễ như trở bàn tay."),i("br"),i("br"),n._v("“Phụ vương, gia hỏa này quả thực thật không có quy củ!” Gặp hắn đi xa, Mạc Vũ công chúa thở phì phò nói."),i("br"),i("br"),n._v("“Thiên tài người, cậy tài khinh người, không có quy củ cũng rất bình thường, ta nhớ được ngươi đã nói, có vẻ như Trương sư càng không quy củ đi!” Mạc Thiên Tuyết nói."),i("br"),i("br"),n._v("Phàm là đại tài, khẳng định cùng người bình thường không giống, nếu là cùng tất cả mọi người đồng dạng, làm sao có thể siêu quần bạt tụy, ngạo cười quần hùng?"),i("br"),i("br"),n._v("“Trương sư cái kia là có bản lĩnh thật sự, hắn tính là gì, có thể so sánh cùng nhau?” Mạc Vũ khẽ nói."),i("br"),i("br"),n._v("Theo Thú đường cùng nhau đi tới, Trương sư bản lĩnh nàng nhìn ở trong mắt, không nói cử thế vô song, lại cũng không xê xích gì nhiều, là nàng sùng bái đối tượng, cái này Liễu Trình... Bất quá là cái không có thấy qua việc đời tiểu nhân vật, may mắn y thuật cao mà thôi, như thế nào so sánh cùng nhau?"),i("br"),i("br"),n._v("“Cái này có thể không nhất định, vị này Liễu hội trưởng, không chỉ y thuật cao minh, giảng dạy học sinh bản lĩnh cũng là nhất lưu!” Mạc Thiên Tuyết cười cười, cổ tay khẽ đảo, lấy ra một phần mật tấu: “Ngươi xem một chút cái này!”"),i("br"),i("br"),n._v("Mạc Vũ nghi ngờ tiếp nhận, chỉ nhìn thoáng qua tú mục lập tức trợn tròn: “Hắn, hắn chỉ chỉ điểm một ngày, liền để môn hạ tất cả học sinh đều có chỗ đột phá? Càng là tiện tay hóa giải Mộc Tuyết Tinh trên người đan độc?”"),i("br"),i("br"),n._v("Phụ thân của Mộc Tuyết Tinh là Mộc đan sư, xem như Mạc Vũ khảo hạch lão sư một trong, bái kiến mấy lần, cũng coi như quen thuộc, biết cô gái này trên người ẩn chứa đan độc."),i("br"),i("br"),n._v("Làm y sư công hội hội trưởng, y thuật cao minh, tiện tay giải quyết đan độc rất bình thường, nhưng chỉ điểm một ngày, liền để toàn lớp người toàn bộ đột phá, đây cũng không phải là người bình thường có thể làm được!"),i("br"),i("br"),n._v("Coi như nàng vị này nhất tinh danh sư, đều chỉ sợ không cách nào hoàn thành."),i("br"),i("br"),n._v("“Đúng vậy a, nếu như không phải lợi hại như vậy, ta lại làm sao có thể để ngươi kết giao!” Mạc Thiên Tuyết cười cười: “Hơn nữa, ngươi tiếp tục hướng xuống nhìn, đằng sau còn có càng thú vị!”"),i("br"),i("br"),n._v("Mạc Vũ công chúa tiếp tục hướng xuống nhìn, lần nữa sững sờ: “Trương sư học sinh Triệu Nhã, cùng học sinh của hắn Mộc Tuyết Tinh tỷ thí ngang tay? Ước chiến hai ngày sau?”"),i("br"),i("br"),n._v("“Ừm, tỷ thí lần này, nếu như Mộc Tuyết Tinh thắng, đã nói lên vị này Liễu lão sư tại giảng bài bên trên, so Trương sư đều còn đáng sợ hơn! Loại người này coi như không phải danh sư, cũng tuyệt đối là một nước trụ cột của quốc gia, thử nghĩ một hồi, nếu để cho hắn đi quân đội giảng bài... Sẽ mang đến hiệu quả bao lớn?”"),i("br"),n._v("Mạc Thiên Tuyết nói."),i("br"),i("br"),n._v("“Cái này...” Mạc Vũ trầm mặc."),i("br"),i("br"),n._v("Tùy tiện chỉ điểm, liền để toàn lớp người đều đột phá, phần này năng lực, đặt ở quân đội, tuyệt đối có thể sinh ra để cho người ta khó có thể tưởng tượng hiệu quả, thậm chí để một cái vương quốc lực lượng quân sự, nhanh chóng bạo tăng."),i("br"),i("br"),n._v("“Vị này Liễu hội trưởng, y thuật cao minh, giảng bài lợi hại, mặc dù tại luyện đan, thuần thú các phương diện kém xa Trương sư, nhưng cũng không hề yếu. Tới giao hảo, chắc chắn sẽ không có sai, đương nhiên, Trương sư bên kia, ngươi cũng muốn theo vào, nếu là hắn có thể giúp ngươi nói chuyện, Mạc Hoằng Nhất coi như lợi hại hơn nữa, cũng không có khả năng ảnh hưởng đến vị trí của ngươi!”"),i("br"),i("br"),n._v("Mạc Thiên Tuyết nghiêm túc bàn giao."),i("br"),i("br"),n._v("“Vâng!” Mạc Vũ nhẹ gật đầu, nhớ tới cái gì chần chờ một chút nhìn qua: “Đã hai người kia chúng ta đều muốn giao hảo, Mộc Tuyết Tinh cùng Triệu Nhã tỷ thí, muốn không nên ngăn cản?”"),i("br"),i("br"),n._v("“Làm gì ngăn cản? Chúng ta không những không nên ngăn cản, còn trắng trợn hơn tuyên dương, làm cho tất cả mọi người đều biết chuyện này, gây nên càng lớn oanh động! Như vậy trải qua, mới có thể triệt để để Liễu hội trưởng danh khí tăng thêm, thu hoạch được hắn hảo cảm!”"),i("br"),i("br"),n._v("Mạc Thiên Tuyết trí cuốn tại nắm: “Không có ngăn cản Mộc Tuyết Tinh cùng Triệu Nhã chiến đấu, rất rõ ràng, hắn muốn mượn chuyện này, dương danh thiên hạ, đã như vậy, chúng ta liền giúp hắn một tay!”"),i("br"),i("br"),n._v("Mạc Vũ hiểu được, trong lòng cũng đối hai ngày sau tỷ thí cũng tràn ngập tò mò, đến cùng Trương sư học sinh Triệu Nhã có thể chiến thắng, còn là vị này Liễu hội trưởng học sinh Mộc Tuyết Tinh có thể chiến thắng?"),i("br"),i("br"),n._v("Bất quá, mặc kệ vị kia có thể thắng được hai, người giao chiến ngang tay qua một lần, đã nói rõ, vị này Liễu hội trưởng giảng bài, không kém chút nào Trương sư!"),i("br"),i("br"),n._v("“Hai ngày sau, nhất định phải tới xem một chút các nàng luận võ...”"),i("br"),i("br"),n._v("Trong lòng ngầm hạ quyết định."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Rời đi học viện, phát hiện cũng không có người theo dõi, Trương Huyền lúc này mới tìm một chỗ, biến thành “Dương sư” bộ dáng, đi vào phủ đệ."),i("br"),i("br"),n._v("“Lão gia, đêm qua tới mấy tên hộ vệ, nói là đến đầu nhập vào thiếu gia, hiện tại thiếu gia không tại, ngươi nhìn làm sao bây giờ?”"),i("br"),i("br"),n._v("Tôn Cường tiến lên đón."),i("br"),i("br"),n._v("“Đầu nhập vào thiếu gia?” Trương Huyền nghi ngờ nhìn qua."),i("br"),i("br"),n._v("“Vâng, bọn hắn nói từng là Quý gia hộ vệ, bởi vì cùng thiếu gia có ước định, mới tới tìm kiếm che chở...”"),i("br"),i("br"),n._v("Tôn Cường nói."),i("br"),i("br"),n._v("“Nha!” Trương Huyền gật gật đầu, nghĩ tới."),i("br"),i("br"),n._v("Hôm trước vừa mới đến vương thành thời điểm, đắc tội vị kia thư hoạ sư Quý Mặc công tử, gia hỏa này phái người đang tìm bản thân phiền phức, kết quả đám người này bị bản thân xâu đánh cho một trận, lại uy hiếp phục dụng độc dược, thay hắn giáo huấn đối phương."),i("br"),i("br"),n._v("Hiện tại Trương Huyền khảo hạch nhị tinh danh sư không tại vương thành... Đám người này vì phòng ngừa bị Quý gia truy sát, chỉ có thể trốn tới đây."),i("br"),i("br"),n._v("“Chuyện này Trương Huyền cùng ta nói qua, để bọn hắn ở chỗ này hảo hảo làm việc, trên người tai hoạ ngầm, ta sẽ ra tay giúp đỡ giải quyết!” Trương Huyền khoát khoát tay."),i("br"),i("br"),n._v("Những người này với hắn mà nói tu vi chẳng ra sao cả, lại đều đạt đến Thông Huyền cảnh, dùng để bảo hộ phủ đệ an toàn, so thông báo tuyển dụng những cái kia mạnh lớn hơn nhiều lắm."),i("br"),i("br"),n._v("Mặc dù biết đây là Trương sư nơi ở, không có người chạy tới quấy rối, cũng muốn phòng bị vạn nhất."),i("br"),i("br"),n._v("“Vâng!” Mặc dù không biết lão gia nói như vậy có ý tứ gì, Tôn Cường còn là ghi xuống."),i("br"),i("br"),n._v("“Đem Triệu Nhã kêu đến!”"),i("br"),i("br"),n._v("Không đi quản mấy cái này hộ vệ, đến đến đại sảnh, Trương Huyền bàn giao."),i("br"),i("br"),n._v("Tôn Cường gật đầu rời đi, thời gian không dài, Triệu Nhã liền đến đến trước mặt."),i("br"),i("br"),n._v("“Ta chỗ này một bộ kiếm pháp, hiện đang truyền thụ cho ngươi, mặc kệ hai ngày sau ngươi cùng ai tỷ thí, hy vọng có thể chiến thắng, đừng để sư tổ cùng ngươi lão sư thất vọng!”"),i("br"),i("br"),n._v("Nhìn về phía cô bé trước mắt, Trương Huyền thản nhiên nói."),i("br"),i("br"),n._v("“Sư tổ yên tâm, Triệu Nhã tất nhiên không phụ kỳ vọng!” Triệu Nhã cắn răng."),i("br"),i("br"),n._v("“Vậy là tốt rồi, đây là kiếm pháp bí tịch, xem hết có cái gì không hiểu, trực tiếp hỏi!”"),i("br"),i("br"),n._v("Tiện tay đưa tới hắn chỉnh lý qua đi tinh giản bản Thiên Đạo kiếm pháp."),i("br"),i("br"),n._v("Triệu Nhã biết sư tổ truyện truyền thụ cho, khẳng định không đơn giản, không chút do dự, lập tức nghiêm túc học tập."),i("br"),i("br"),n._v("Thiên Đạo kiếm pháp hóa phức tạp thành đơn giản, trực chỉ đại đạo, học cũng không phức tạp, một canh giờ sau, Triệu Nhã đã nắm giữ cơ bản, bất quá, khoảng cách triệt để thuần thục, còn phải cần một khoảng thời gian."),i("br"),i("br"),n._v("Dù vậy, cũng biết bộ kiếm pháp kia cường đại!"),i("br"),i("br"),n._v("Thậm chí so với Trịnh Dương thương pháp, đều chỉ có hơn chứ không kém."),i("br"),i("br"),n._v("“Lão sư, Khương đường chủ cầu kiến!”"),i("br"),i("br"),n._v("Truyền thụ xong Triệu Nhã kiếm pháp, Lưu Lăng đi lên phía trước."),i("br"),i("br"),n._v("“Để hắn tiến đến...”"),i("br"),i("br"),n._v("Trương Huyền khoát tay áo, đột nhiên sững sờ, vỗ trán một cái, một cái ý nghĩ xông ra."),i("br"),i("br"),n._v("Cho tới nay đều lấy thân phận của mình làm việc, quên còn có hiện tại ngụy trang vị này."),i("br"),i("br"),n._v("Hắn Trương Huyền thân phận, chỉ là nhất tinh danh sư, không có tư cách tiến vào Danh Sư đường nhị tinh tàng thư khố quan sát, hiện tại làm “Dương sư” muốn muốn đi vào, hẳn là rất dễ dàng ah!"),i("br"),i("br"),n._v("Tin tưởng chỉ cần đưa ra yêu cầu, cũng không phải cái đại sự gì, Khương đường chủ chắc chắn sẽ không ngăn cản!"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-350"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-352"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);