(window.webpackJsonp=window.webpackJsonp||[]).push([[1581],{1936:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-345-cam-tam-bi-đanh-thanh-ung-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-345-cam-tam-bi-đanh-thanh-ung-thu"}},[n._v("#")]),n._v(" Chương 345: Cam tâm bị đánh Thanh Ưng Thú")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Làm càn!”"),t("br"),t("br"),n._v("Nghe đối phương nói thẳng nhà mình vương tử đầu óc có bệnh, Nhiên Mộc đặc sứ sầm mặt lại: “Nhà chúng ta vương tử, thân thể khoẻ mạnh, ngươi nói bậy bạ gì đó?”"),t("br"),t("br"),n._v("“Thân thể khoẻ mạnh?” Trương Huyền kỳ quái: “Cái kia... Chẳng lẽ là ngươi có bệnh?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết bệ hạ chuyên môn mời hắn sang đây xem bệnh, tự nhiên có người có vấn đề, bệ hạ cùng Mạc Vũ hôm qua mới thấy, không có khả năng có việc, cung điện kia chỉ còn lại hai cái này, không phải bọn họ là ai?"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nhiên Mộc đặc sứ tức thiếu chút nữa phun máu, hơi đỏ mặt, vung tay áo một cái, quay đầu nhìn về phía vương tọa: “Thiên Tuyết bệ hạ, cái này chẳng lẽ liền là các ngươi Thiên Vũ vương quốc đạo đãi khách? Chúng ta vương tử điện hạ, mặc dù không phải thái tử, nhưng cũng quyền cao chức trọng, là bệ hạ thân phong Thân Vương, phái một người tùy tiện nhục nhã, chẳng lẽ là hướng chúng ta Khôn Càn vương quốc khiêu khích?”"),t("br"),t("br"),n._v("“Nhiên Mộc đặc sứ chớ nên tức giận...”"),t("br"),t("br"),n._v("Cũng không nghĩ tới vị này Liễu lão sư sẽ nói thẳng ra lời này, Mạc Thiên Tuyết trên mặt một hồi xấu hổ, vội vàng giải thích: “Liễu lão sư là ta chuyên môn mời tới trị liệu bệnh chứng, không biết hai vị, mới trong lúc vô tình mạo phạm, mong được tha thứ!”"),t("br"),t("br"),n._v("Nói xong, vội vàng nhìn về phía Trương Huyền: “Liễu lão sư, bọn hắn hai vị là vương quốc khách nhân, cũng không phải là bệnh nhân...”"),t("br"),t("br"),n._v("“Nha!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, nghi ngờ nhìn Phí Hiên vương tử một chút."),t("br"),t("br"),n._v("Không phải bệnh nhân, làm sao nói bừa bãi, xem ra cái này cái gọi là Khôn Càn vương quốc cũng chỉ thường thôi, thế mà để một cái đầu óc có vấn đề người lên làm Thân Vương..."),t("br"),t("br"),n._v("Nhìn thấy ánh mắt của hắn, Phí Hiên vương tử suýt chút nữa tại một ngụm máu tươi phun ra, trực tiếp ngất đi."),t("br"),t("br"),n._v("Ngươi đây là ý gì?"),t("br"),t("br"),n._v("Ánh mắt tràn ngập thương hại cái quỷ gì?"),t("br"),t("br"),n._v("Ta đường đường vương tử, dưới một người, trên vạn người, vô số người sùng bái thần tượng, cần phải ngươi thương hại sao?"),t("br"),t("br"),n._v("Hắn tức giận sắp bạo tạc, một bên Mạc Vũ công chúa mở to hai mắt nhìn, suýt chút nữa không có cười ra tiếng."),t("br"),t("br"),n._v("Khó trách gia hỏa này có thể làm cho mình tức giận như vậy, kéo cừu hận thủ đoạn thực sự quá mạnh."),t("br"),t("br"),n._v("Hai câu nói liền để trầm ổn Phí Hiên vương tử tức giận con mắt thấu đỏ, muốn giết người, cũng thật làm cho người say."),t("br"),t("br"),n._v("Vốn là nàng còn đối gia hỏa này có chút ý kiến, gặp hắn nhanh như vậy liền đem Phí Hiên vương tử tức giận bạo tẩu, lập tức hài lòng gật đầu."),t("br"),t("br"),n._v("Cố nén sắp bạo tạc lửa giận, Phí Hiên vương tử lạnh lùng chằm chằm tới: “Không dám so liền trực tiếp mở lời, ít tìm lý do khác!”"),t("br"),t("br"),n._v("Vừa rồi tại công chúa trước mặt mất mặt mũi, lại bị gia hỏa này chế giễu, sớm đã một bồn lửa giận."),t("br"),t("br"),n._v("“Tỷ thí? So cái gì?”"),t("br"),t("br"),n._v("Gặp gia hỏa này vừa đến liền nhất định phải lôi kéo bản thân tỷ thí, Trương Huyền có chút buồn bực."),t("br"),t("br"),n._v("Ta trị bệnh của ta, ngươi cầu ngươi thân, cùng ta có lông quan hệ? Không phải muốn tỷ thí?"),t("br"),t("br"),n._v("“Rất đơn giản, Mạc Vũ công chúa vừa rồi chính miệng nói, muốn để nàng tiếp nhận cầu thân, đầu tiên đánh bại nàng thú sủng, nếu như ngươi có thể đánh bại, ta liền nhận nhận thua! Có dám hay không so?”"),t("br"),t("br"),n._v("Cười lạnh, Phí Hiên vương tử vung tay áo một cái."),t("br"),t("br"),n._v("Mới vừa ở đầu kia Thanh Ưng Thú trước mặt mất đi như thế đại người, gia hỏa này đụng vào, tự nhiên cũng sẽ không để hắn tốt hơn."),t("br"),t("br"),n._v("Dù sao quy củ là Mạc Vũ công chúa định, nếu như ngươi cũng không cách nào thông qua, ta xem qua một hồi, Mạc Thiên Tuyết trả lời thế nào."),t("br"),t("br"),n._v("Về phần Trương Huyền có thể thông qua, căn bản là không có nghĩ tới."),t("br"),t("br"),n._v("Hắn đường đường Thông Huyền cảnh đỉnh phong, đều bị cái này con man thú hai chiêu đánh bại, kẻ trước mắt này, chẳng qua Thông Huyền cảnh sơ kỳ, làm sao có thể chống đỡ được nửa bước cấp bậc tông sư công kích?"),t("br"),t("br"),n._v("“Đánh bại man thú?”"),t("br"),t("br"),n._v("Trương Huyền quay đầu nhìn sang, lúc này mới chú ý tới Mạc Vũ đầu kia Thanh Ưng Thú."),t("br"),t("br"),n._v("Hắn cải biến bộ dáng cùng khí tức, coi như man thú mẫn cảm, cũng chưa nhận ra được."),t("br"),t("br"),n._v("“Không sai, không dám lời nói, ta khuyên ngươi đừng tiếp tục quấn lấy Mạc Vũ công chúa, nàng đối như ngươi loại này hèn nhát, không có hứng thú!” Phí Hiên vương tử khóe miệng vung lên."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Chính không thèm để ý cái này tự cho là đúng gia hỏa, trị xong bệnh rời đi, đột nhiên nghĩ đến cái gì, ánh mắt sáng lên."),t("br"),t("br"),n._v("Hắn không phải cái gì vương tử sao?"),t("br"),t("br"),n._v("Có lẽ... Cũng có linh thạch đi!"),t("br"),t("br"),n._v("Muốn đến nơi này, nhìn liền không thể nào chán ghét, ngược lại giống như là một đống bảo tàng: “Đánh bại cái này con man thú với ta mà nói, không tính là gì, bất quá... Đã ngươi muốn so, cũng nên có chút tiền đặt cược a?”"),t("br"),t("br"),n._v("“Tiền đặt cược?” Phí Hiên vương tử sững sờ."),t("br"),t("br"),n._v("Kẻ thất bại, mất đuổi theo Mạc Vũ công chúa tư cách, chẳng lẽ không phải tiền đặt cược?"),t("br"),t("br"),n._v("“Như vậy đi, chúng ta người tu luyện, linh thạch trọng yếu hơn, nếu như ta có thể đánh bại cái này con man thú, ngươi thua cho ta một viên linh thạch! Nếu vì không cách nào thành công, thua ngươi một viên, có dám hay không?”"),t("br"),t("br"),n._v("Trương Huyền khóe miệng giương lên, cười khanh khách nhìn qua."),t("br"),t("br"),n._v("“Linh thạch?”"),t("br"),t("br"),n._v("Phí Hiên vương tử con ngươi co rụt lại."),t("br"),t("br"),n._v("Thứ này vô cùng trân quý, coi như hắn là vương tử, cũng chỉ có một viên, còn là phụ vương vì trợ hắn trùng kích nửa bước Tông sư, chuyên môn ban thưởng, bởi vì tu vi không có tích lũy đủ, vẫn không có cam lòng dùng."),t("br"),t("br"),n._v("Cầm thứ này làm tiền đặt cược?"),t("br"),t("br"),n._v("“Thế nào, không dám? Coi như không dám, vừa vặn ta cũng không hứng thú đối phó cái gì man thú!”"),t("br"),t("br"),n._v("Trương Huyền duỗi lưng một cái, một bộ muốn ăn đòn biểu lộ."),t("br"),t("br"),n._v("“Ngươi... Có gì không dám!” Gặp hắn bộ dáng này, Phí Hiên vương tử cũng nhịn không được nữa, khoát tay chặn lại: “Cược thì cược!”"),t("br"),t("br"),n._v("“Điện hạ...”"),t("br"),t("br"),n._v("Nhiên Mộc đặc sứ nhịn không được khuyên can."),t("br"),t("br"),n._v("Linh thạch thứ này, liền xem như Khôn Càn vương quốc vương thất, cũng là rất thưa thớt, Phí Hiên vương tử thân là Thân Vương, mới có tư cách ban cho một viên, lấy cái này làm tiền đặt cược, thật là quá lớn nhiều."),t("br"),t("br"),n._v("“Yên tâm, hắn liền phô trương thanh thế, ta không tin nho nhỏ Thông Huyền cảnh sơ kỳ, có thể chiến thắng nửa bước Tông sư man thú!” Phí Hiên vương tử khẽ nói."),t("br"),t("br"),n._v("Tu vi trong lúc đó chênh lệch như biển, nhất là nửa bước Tông sư, đã lĩnh ngộ “Tông sư” khí độ."),t("br"),n._v(" Bình thường một nửa bước Tông sư, bốn năm cái Thông Huyền cảnh đỉnh phong đều chiến thắng không nổi, chớ nói chi là nho nhỏ sơ kỳ."),t("br"),t("br"),n._v("Thật muốn chiến đấu, tuyệt đối là bị miểu sát hàng."),t("br"),t("br"),n._v("Cái này Liễu lão sư cố ý đánh cược, rất đơn giản, chính là vì hù dọa hắn."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một cái hộp ngọc xuất hiện, mở ra về sau, một cái óng ánh sáng long lanh tảng đá, bình phương trong đó, tản mát ra linh khí nồng nặc."),t("br"),t("br"),n._v("Linh thạch!"),t("br"),t("br"),n._v("“Linh thạch của ta ở chỗ này, không biết ngươi ở đâu?”"),t("br"),t("br"),n._v("Đem linh thạch cầm tại lòng bàn tay, Phí Hiên vương tử nhìn qua."),t("br"),t("br"),n._v("“Ta sao? Ta đương nhiên tại, thua, khẳng định sẽ cho ngươi!” Trương Huyền nói: “Không tin ngươi có thể hỏi một chút bệ hạ!”"),t("br"),t("br"),n._v("Hắn linh thạch đều bị hấp thu quang thay đổi thành bụi phấn, tự nhiên không bỏ ra nổi đến, bất quá, muốn nói không có, đối phương khả năng liền dọa đến không thể so sánh."),t("br"),t("br"),n._v("“Liễu lão sư quả thực có một viên, là ta hôm qua tặng lễ vật!”"),t("br"),t("br"),n._v("Mặc dù kỳ quái trước mắt vị này Liễu lão sư, ở đâu ra tự tin, Mạc Thiên Tuyết chần chờ một chút, vẫn gật đầu."),t("br"),t("br"),n._v("“Tốt!” Phí Hiên vương tử khoát tay chặn lại: “Vậy thì mời đi! Mạc Vũ công chúa, còn hi vọng ngươi đừng cho man thú hạ thủ lưu tình, nếu không, trước ngươi nói quy củ, cũng đừng trách ta cũng không đi tuân thủ!”"),t("br"),t("br"),n._v("Hắn cố ý để Trương Huyền khiêu chiến, vừa đến, muốn nhục nhã hắn một phen, thứ hai, cũng giống nhìn xem Mạc Vũ công chúa phản ứng."),t("br"),t("br"),n._v("Nếu như hắn cũng thua, ta nhìn các ngươi còn thế nào dự định, thấy thế nào tốt..."),t("br"),t("br"),n._v("Mạc Vũ công chúa đôi mi thanh tú nhăn lại, nhịn không được mở lời."),t("br"),t("br"),n._v("“Liễu lão sư, ngươi suy nghĩ kỹ càng, ta đầu này Thanh Ưng Thú, đã đạt tới nửa bước Tông sư cấp bậc!”"),t("br"),t("br"),n._v("Thật nếu để cho nửa bước Tông sư cấp bậc Thanh Ưng Thú động thủ, hắn chỉ có Thông Huyền cảnh sơ kỳ tu vi, khẳng định là ngăn cản không nổi."),t("br"),t("br"),n._v("Phụ vương ý tứ hảo hảo tới giao hảo, còn không có chỗ tốt quan hệ, liền bị bản thân thú sủng đả thương, tiếp sau còn muốn giữ gìn mối quan hệ, liền không quá dễ dàng."),t("br"),t("br"),n._v("Nàng nói chưa dứt lời, nói chuyện, một bên Phí Hiên vương tử đều sắp tức giận nổ."),t("br"),t("br"),n._v("Để cho ta cùng gia hỏa này so thời điểm, ngươi vui vẻ đồng ý, hưng phấn cùng cái gì giống như, nghe được hắn lập tức chần chờ, có ý tứ gì?"),t("br"),t("br"),n._v("“Đánh bạc ta đã tiếp, hiện tại đổi ý, liền đại biểu thua. Mạc Vũ công chúa cũng không hy vọng vị này Liễu lão sư, còn chưa bắt đầu tỷ thí, cũng đã thất bại thảm hại đi!”"),t("br"),t("br"),n._v("Hừ một tiếng, Phí Hiên vương tử nói."),t("br"),t("br"),n._v("“Đa tạ công chúa nhắc nhở, nửa bước Tông sư liền nửa bước Tông sư!”"),t("br"),t("br"),n._v("Trương Huyền đối Mạc Vũ gật gật đầu, tiếp lấy nhìn về phía Phí Hiên vương tử: “Ta sẽ cùng đầu này Thanh Ưng Thú giao đấu, bất quá, muốn sớm hỏi một chút, dạng gì là đánh bại?”"),t("br"),t("br"),n._v("“Đánh bại liền là đánh đầu này Thanh Ưng Thú không dám cùng ngươi đối kháng...” Phí Hiên vương tử hai tay chắp sau lưng."),t("br"),t("br"),n._v("“Cái kia ta đã biết!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu, đối cách đó không xa Thanh Ưng Thú ngoắc ngón tay: “Ngươi qua đây, để cho ta đánh một trận, đánh không dám phản kháng liền tốt!”"),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Nghe nói như thế, không chỉ Mạc Vũ công chúa, ngay cả vương tọa lên Mạc Thiên Tuyết bệ hạ, cũng thân thể nhoáng một cái, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Man thú trời sinh tính ngang ngược, tức giận nhất liền là khiêu khích, ngươi ngoắc ngón tay, mà lại nói đến phách lối như vậy..."),t("br"),t("br"),n._v("Liền không sợ bị đối phương một cánh đánh chết tươi?"),t("br"),t("br"),n._v("“Tự mình muốn chết...”"),t("br"),t("br"),n._v("Vốn đang lo lắng gia hỏa này dùng âm mưu quỷ kế gì, nghe được loại khiêu khích này lời nói, Phí Hiên vương tử lập tức hưng phấn con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Hắn không có khiêu khích, đều suýt chút nữa bị đánh tại chỗ quải điệu, nói như vậy, không là muốn chết là làm gì?"),t("br"),t("br"),n._v("Có thể đoán được, đầu này Thanh Ưng Thú khẳng định sẽ lập tức bạo tẩu, hung hăng đem gia hỏa này đầu đập nát."),t("br"),t("br"),n._v("“Thu!”"),t("br"),t("br"),n._v("Quả nhiên, loại ý nghĩ này vừa kết thúc, trước mắt Thanh Ưng Thú con mắt lập tức đỏ lên, một tiếng vang lên, tiếng động Cửu Tiêu."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thô to cánh múa, lập tức theo Mạc Vũ sau lưng vọt ra ngoài."),t("br"),t("br"),n._v("“Cẩn thận...”"),t("br"),t("br"),n._v("Cùng đầu này Thanh Ưng Thú ở chung lâu như vậy, đây coi như là lần thứ hai thấy nó nổi giận như vậy, lần đầu tiên là tại Thú đường, bị Trương sư khiêu khích."),t("br"),t("br"),n._v("Chỉ là, người ta Trương sư nắm giữ thực lực chân chính, gia hỏa này, chỉ có Thông Huyền cảnh sơ kỳ, trang cái gì trang?"),t("br"),t("br"),n._v("Thật muốn một cánh bị quất chết, Y Sư công hội người còn không qua đây nháo lật trời?"),t("br"),t("br"),n._v("Đang nghĩ ngợi muốn hay không quát lớn lại Thanh Ưng Thú, để nó dưới vuốt lưu tình, lập tức con mắt trợn tròn, thân thể mềm mại nhoáng một cái, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Ngay cả mặt mũi tràn đầy đắc ý, cho rằng kẻ trước mắt này hẳn phải chết không nghi ngờ Phí Hiên vương tử, cũng cảm thấy mắt tối sầm lại."),t("br"),t("br"),n._v("Chỉ gặp khí thế hùng hổ Thanh Ưng Thú, cánh mở đến đến Liễu lão sư trước mặt, dùng miệng lôi kéo nắm đấm của hắn, không ngừng hướng trên người mình tiễn."),t("br"),t("br"),n._v("Xem ra, là chủ động yêu cầu thanh niên động thủ đánh nó..."),t("br"),t("br"),n._v("Ngươi không phải là bị hắn vũ nhục, khí thế hùng hổ muốn giết người sao?"),t("br"),t("br"),n._v("Không phải là không muốn từ bỏ ý đồ sao?"),t("br"),t("br"),n._v("Cái này làm cái quỷ gì?"),t("br"),t("br"),n._v("Lại thêm hố cha chính là, nhìn thấy quả đấm đối phương xiết chặt, lập tức đình chỉ động tác, cánh mở ra, đứng tại chỗ không nhúc nhích, cũng không phản kháng, cũng không tránh né, ánh mắt bên trong ngược lại lộ ra một bộ chờ mong, khát vọng thần sắc."),t("br"),t("br"),n._v("Rất rõ ràng, đang chờ bị đánh..."),t("br"),t("br"),n._v("Bị vũ nhục một trận, không động thủ với hắn, còn chờ mong, khát vọng..."),t("br"),t("br"),n._v("Phí Hiên vương tử chỉ cảm thấy một ngụm máu tươi tại nơi cổ họng không ngừng cuồn cuộn, bất cứ lúc nào cũng sẽ phun ra."),t("br"),t("br"),n._v("Ai có thể nói cho ta biết đã xảy ra chuyện gì sao?"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-353"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-355"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);