(window.webpackJsonp=window.webpackJsonp||[]).push([[1584],{1939:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-348-tran-quoc-than-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-348-tran-quoc-than-thu"}},[n._v("#")]),n._v(" Chương 348: Trấn quốc Thần Thú")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Ta cũng sẽ không tùy tiện muốn ngươi dược liệu, như vậy đi!”"),t("br"),t("br"),n._v("Ngay tại Phí Hiên vương tử tràn đầy buồn bực thời điểm, đối diện thanh niên mở miệng lần nữa: “Cùng Thiên Tuyết bệ hạ đồng dạng, ta cũng cho ngươi miễn phí chữa bệnh một lần!”"),t("br"),t("br"),n._v("Mặc dù Thú Linh thảo, có thể so với hai khối linh thạch, Trương Huyền nhất định phải cầm tới, nhưng cũng không thể để đối phương quá mức ăn thiệt thòi."),t("br"),t("br"),n._v("Hai tay chắp sau lưng, xuôi theo Phí Hiên vương tử dạo qua một vòng: “Vừa rồi chữa cho ngươi tổn thương thời điểm, ta liền phát hiện, ngươi chân khí trong cơ thể tích tụ, khí tức không thuận, thân thể nhìn rất tốt, trên thực tế lại có chút suy yếu! Nếu như không nhìn lầm, đoạn thời gian gần nhất, có phải hay không buổi sáng rời giường khó khăn, ban đêm lại rất khó ngủ?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Phí Hiên vương tử đang một mặt buồn bực, nghĩ đến có cần hay không đem Thú Linh thảo cướp về, nghe nói như thế, thân thể run lên, sắc mặt trắng bệch: “Ngươi... Làm sao ngươi biết?”"),t("br"),t("br"),n._v("Hắn gần nhất quả thực có loại tình huống này."),t("br"),t("br"),n._v("Chân khí tích tụ, khí tức không thuận, nghỉ ngơi cũng không tốt lắm, cả người nóng nảy bất an."),t("br"),t("br"),n._v("Nếu không phải như thế, hắn khẳng định đã sớm mượn nhờ linh thạch trùng kích nửa bước Tông sư. Cũng không trở thành, vẫn giữ lại, bị trước mắt vị này thắng đi qua..."),t("br"),t("br"),n._v("Bất quá, những chuyện này, hắn ai cũng chưa nói qua, ngay cả bên người vị này Nhiên Mộc đặc sứ cũng không biết, hắn làm sao có thể một ngụm nói ra?"),t("br"),t("br"),n._v("Thật chẳng lẽ là nhìn ra chứng bệnh?"),t("br"),t("br"),n._v("“Ta là y sư, mới vừa rồi giúp ngươi chữa thương thời điểm, đã kiểm tra qua, tự nhiên sẽ hiểu!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Còn xin... Hội trưởng ra tay!”"),t("br"),t("br"),n._v("Sắc mặt trắng nhợt, Phí Hiên vương tử vội vàng ôm quyền, lại không có trước đó kiêu ngạo."),t("br"),t("br"),n._v("Bệnh chứng này để hắn mỗi ngày đều tinh lực không tập trung, từ đó làm cho gần nhất tu luyện đều không chút tiến bộ, nếu như có thể chữa khỏi, Thú Linh thảo mặc dù trân quý, đưa ra ngoài, cũng là đưa ra ngoài, không tính là cái gì."),t("br"),t("br"),n._v("“Ừm, kỳ thật rất đơn giản, thiên phú của ngươi không sai, nhưng khoảng cách loại này niên kỷ liền đạt tới Thông Huyền cảnh đỉnh phong, còn có chênh lệch nhất định. Nếu như ta không nhìn lầm, vì tăng thực lực lên, từng dùng qua đặc thù tấn thăng phương pháp đi! Nói thật cho ngươi biết, chính là loại phương pháp này, lưu lại cho ngươi tai hoạ ngầm!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Phí Hiên vương tử thân thể run lên."),t("br"),t("br"),n._v("Đối phương nói không sai."),t("br"),t("br"),n._v("Thiên phú của hắn chỉ có thể coi là được trung thượng, không tính là đặc biệt cao minh."),t("br"),t("br"),n._v("Vương thất không tình thân, tu vi không đủ, rất dễ dàng gặp phải đủ loại phiền phức, muốn sống càng tốt hơn, cũng chỉ có thể nhanh chóng tu luyện. Vì mau chóng đem thực lực lấy tới, từng dùng một loại bí pháp đặc thù, cũng chính là phục dụng đặc thù dược liệu, kích thích huyệt vị, kích phát tiềm năng."),t("br"),t("br"),n._v("Như vậy mặc dù tốc độ tiến bộ rất nhanh, nhưng cũng lưu lại tai hoạ ngầm."),t("br"),t("br"),n._v("Chẳng lẽ... Gần đây thân thể xuất hiện tình huống, liền bởi vì cái này?"),t("br"),t("br"),n._v("“Chính là cái này, ta cho ngươi mở một bộ phương thuốc, ngươi chiếu vào phục dụng, hẳn là trong một tháng liền có thể triệt để giải trừ tai hoạ ngầm, đến lúc đó thừa cơ trùng kích nửa bước Tông sư, cũng không phải là không được!”"),t("br"),t("br"),n._v("Cười cười, Trương Huyền đi tới cách đó không xa trên mặt bàn, tiện tay lấy giấy bút, viết một cái phương thuốc đưa tới."),t("br"),t("br"),n._v("“Đa tạ Liễu hội trưởng!”"),t("br"),t("br"),n._v("Tiếp nhận phương thuốc, Phí Hiên vương tử hơi đỏ mặt."),t("br"),t("br"),n._v("Hắn bị bệnh chứng này, tra tấn mỗi thiên tinh lực không tốt, người cũng có chút hoảng hốt, nôn nóng, chính vì vậy, trước mắt vị này vẩy một cái hấn, liền khắc chế không được, hoàn toàn không có vương tử vốn có trầm ổn."),t("br"),t("br"),n._v("Nếu như có thể chữa khỏi, hơn nữa trùng kích nửa bước Tông sư, phương thuốc liền quá trân quý."),t("br"),t("br"),n._v("Đừng nói một viên linh thạch, một gốc Thú Linh thảo, coi như lại nhiều đồ vật, cũng cam tâm tình nguyện!"),t("br"),t("br"),n._v("Bởi vì... Khôn Càn vương quốc cũng không xác lập thái tử, nếu như hắn có thể sớm tấn cấp nửa bước Tông sư, vị trí này, vô cùng có khả năng liền là hắn."),t("br"),t("br"),n._v("Trương Huyền khoát khoát tay, nhìn về phía cách đó không xa Mạc Thiên Tuyết: “Bệ hạ, còn xin muốn trị liệu người bệnh mang tới đi, trị xong, ta còn có việc!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết lúc này giống như những người khác, cũng ở vào trong lúc khiếp sợ, nghe nói như thế, liền vội vàng đứng dậy: “Liễu hội trưởng, người mắc bệnh này tình huống tương đối đặc thù, khả năng làm phiền ngươi dời bước tiến về...”"),t("br"),t("br"),n._v("“Muốn ta đi qua? Cũng được, phía trước dẫn đường đi!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Hai vị còn xin đi dịch quán nghỉ ngơi...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết hướng Phí Hiên vương tử cùng Nhiên Mộc đặc sứ ôm quyền."),t("br"),t("br"),n._v("“Chúng ta cáo từ!”"),t("br"),t("br"),n._v("Hai người cũng biết, đối phương dính dáng quốc sự, không muốn quá nhiều người biết được, cũng thức thời quay người rời đi, ngay sau đó một tên thái giám liền tiến lên đón, mang theo đi ra ngoài."),t("br"),t("br"),n._v("“Liễu hội trưởng, mời tới bên này!”"),t("br"),t("br"),n._v("Hai người rời đi, Mạc Thiên Tuyết đi đầu dẫn đường."),t("br"),t("br"),n._v("Trương Huyền theo sát mà lên, mới vừa đi vài bước, một cỗ mùi thơm nhàn nhạt liền bay vào lỗ mũi, ngay sau đó bên tai vang lên truyền âm."),t("br"),t("br"),n._v("“Trương Huyền, Trương sư! Ngươi lừa mọi người thật khổ...”"),t("br"),t("br"),n._v("“Khụ khụ!” Trương Huyền sững sờ: “Mạc Vũ công chúa, nhận lầm đi, ta là Liễu Trình, nào có cái gì Trương sư!”"),t("br"),t("br"),n._v("“Không muốn nói coi như xong, vốn là có một việc muốn nói cho ngươi, đã ngươi không nói, quên đi, ta còn chẳng muốn nói nhảm!”"),t("br"),t("br"),n._v("Thấy hắn không thừa nhận, Mạc Vũ hừ một tiếng."),t("br"),t("br"),n._v("Nàng muốn nói, chính là Triệu Nhã cùng Mộc Tuyết Tinh giao đấu sự tình, gia hỏa này đã không thừa nhận, cũng là chẳng muốn lắm miệng."),t("br"),t("br"),n._v("Dù sao đều là học sinh của ngươi, thích thế nào đánh thế nào đánh tới."),t("br"),t("br"),n._v("Gặp nữ hài xoay người rời đi, nếu không nói, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Xem ra sau này còn là ít tại người quen trước mặt triển lộ quá nhiều, nếu không, rất dễ dàng bị nhìn xuyên."),t("br"),t("br"),n._v("Cùng vị này Mạc Vũ thời gian chung đụng không ngắn, lại thêm Thanh Ưng Thú, đối phương nhận không ra cũng khó khăn."),t("br"),t("br"),n._v("“Mạc Vũ công chúa, đây là Danh Sư đường một lần thí luyện, còn hi vọng không muốn đem tin tức truyền đi!”"),t("br"),t("br"),n._v("Biết đối phương đã xác nhận, giấu diếm cũng không gạt được, đành phải truyền âm qua."),t("br"),n._v(" “Yên tâm, muốn cho ta nói, ta cũng sẽ không nhiều nói!” Thấy hắn thừa nhận, Mạc Vũ mỉm cười: “Đã ngươi nói, ta cũng không giấu diếm, ngày mai buổi trưa, ngươi tìm đến ta, ta dẫn ngươi đi xem trò hay!”"),t("br"),t("br"),n._v("“Trưa mai? Cái gì tốt đùa giỡn?”"),t("br"),t("br"),n._v("Trương Huyền kỳ quái."),t("br"),t("br"),n._v("“Đi thì biết!”"),t("br"),t("br"),n._v("Mạc Vũ mỉm cười."),t("br"),t("br"),n._v("Nhìn hắn mê mang bộ dạng, rất rõ ràng không biết Triệu Nhã cùng Mộc Tuyết Tinh tại tỷ thí, ngày mai liền dẫn hắn đi xem một chút, tình cảnh nhất định chơi rất vui."),t("br"),t("br"),n._v("Nói thật, nàng thật muốn nhìn một chút, nhìn thấy bản thân hai cái học sinh, riêng phần mình vì lão sư danh dự mà chiến, lại là biểu tình gì..."),t("br"),t("br"),n._v("Gia hỏa này vẫn trêu tức nàng, thật đúng là muốn gặp một chút, hắn kinh ngạc bộ dạng."),t("br"),t("br"),n._v("Đương nhiên, điều kiện tiên quyết là không thể để cho gia hỏa này lại quay về học viện, nếu không, vạn nhất ai không cẩn thận nói lộ miệng, sớm biết, liền không dễ chơi..."),t("br"),t("br"),n._v("Trong lòng âm thầm kế hoạch, con mắt loạn chuyển."),t("br"),t("br"),n._v("“Chính là chỗ này!”"),t("br"),t("br"),n._v("Trương Huyền đang muốn tiếp tục hỏi thăm, đến cùng chuyện gì, thần bí như vậy, liền nghe đến phía trước Mạc Thiên Tuyết tiếng âm vang lên."),t("br"),t("br"),n._v("Ngẩng đầu nhìn lại, chỉ thấy chẳng biết lúc nào đã đi tới một cái rộng lớn viện lạc."),t("br"),t("br"),n._v("Viện lạc nhìn không sâu, lại chẳng biết tại sao, từ đầu đến cuối không nhìn thấy cuối cùng, giống như bị cái gì phong bế đồng dạng, che cản ánh mắt, không biết bên trong đến cùng có cái gì."),t("br"),t("br"),n._v("“Chẳng lẽ... Đây là trận pháp?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Chỉ sợ cũng chỉ có trận pháp, mới có như thế năng lực, để cho người ta đứng tại bên ngoài viện, lại không nhìn thấy đồ vật bên trong."),t("br"),t("br"),n._v("“Để Liễu hội trưởng đến trị liệu, cũng không phải nhân loại, mà là chúng ta vương quốc... Trấn quốc man thú!” Tựa hồ nhìn ra hắn nghi hoặc, Mạc Thiên Tuyết giải thích."),t("br"),t("br"),n._v("“Trấn quốc man thú?”"),t("br"),t("br"),n._v("“Ừm, Thiên Vũ vương quốc cũng không đặc biệt lợi hại lão tổ, chỉ dựa vào cái này con man thú trấn thủ, mới có thể ổn định cương thổ, không bị những vương quốc khác ngấp nghé.”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết giải thích: “Chỉ bất quá, nương theo tuổi tác phát triển, chúng ta trấn quốc man thú, lực lượng không lớn bằng lúc trước, thể lực cũng bắt đầu suy kiệt! Thẳng đến đoạn thời gian trước, sinh một cơn bệnh nặng, chỉ sợ đã không kiên trì được quá lâu, cho nên... Mới muốn phiền toái hội trưởng hỗ trợ nhìn xem, có thể hay không trị liệu, nếu như thực sự không cách nào chữa khỏi, cũng hy vọng có thể nghĩ biện pháp để nó kéo dài thêm một đoạn thời gian, đợi Mạc Vũ trưởng thành!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vương quốc muốn sừng sững, không bị cái khác quốc gia quấy nhiễu, tất nhiên có trấn giữ siêu cấp cường giả."),t("br"),t("br"),n._v("Thật giống như lúc trước Thiên Huyền vương quốc Thẩm Hồng lão tổ, không có hắn chấn nhiếp, những vương quốc khác, liền rất có thể đánh tới, đem cương thổ chia cắt."),t("br"),t("br"),n._v("Về phần Danh Sư đường, rất ít lẫn vào chính sự, trừ phi cùng chính đương sự quan hệ thật sự không tệ, nếu không, cơ bản đều duy trì trung lập."),t("br"),t("br"),n._v("Dù sao, coi như vương quốc đổi chủ, cũng không ai dám đối với trên cái này đại lục kịch liệt nhất dưới chức nghiệp tay."),t("br"),t("br"),n._v("Nói cách khác, vô luận vương quốc làm sao tan vỡ, đối với Danh Sư đường tới nói, đều tác động đến không đến."),t("br"),t("br"),n._v("“Trận pháp này...”"),t("br"),t("br"),n._v("Biết muốn trị liệu cũng không phải nhân loại, Trương Huyền kỳ quái nhìn qua."),t("br"),t("br"),n._v("Đã đây là trấn quốc man thú chỗ ở, vì sao muốn bố trí trận pháp?"),t("br"),t("br"),n._v("“Trấn quốc man thú tuổi tác lớn, lại bệnh nặng, gần nhất vẫn nổi điên, liên tục tổn thương không ít người, không có cách nào ta mới mời trận pháp sư công hội cường giả, bố trí cái này, đem vây khốn. Nhờ vào thực lực của nó, thật muốn tại hoàng cung làm ầm ĩ lên, ai đều không thể trấn áp!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết cười khổ."),t("br"),t("br"),n._v("Trương Huyền giờ mới hiểu được tới."),t("br"),t("br"),n._v("Tuổi thọ nhanh muốn đến điểm cuối, lại thêm thân mắc bệnh nặng, vô luận người vẫn là man thú, khẳng định đều sẽ không cam lòng, muốn giày vò một cái, nghĩ biện pháp cỡ nào sống một đoạn thời gian."),t("br"),t("br"),n._v("Nó giày vò không sao, nhìn trời Võ Vương nước liền là tai nạn, lúc này mới cố ý dùng trận pháp phong bế, sợ xông ra không cần thiết mầm họa."),t("br"),t("br"),n._v("“Liễu hội trưởng yên tâm, ta đã phái người đi mời trận pháp sư công hội Tông sư, để hắn mang bọn ta đi vào. Nếu không, trận pháp vận chuyển trong lúc đó, mười phần nguy hiểm, tìm không rõ phương hướng, rất dễ dàng bị lạc trong đó...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết nói."),t("br"),t("br"),n._v("Vì phòng ngừa trấn quốc man thú lao ra, trận pháp vẫn luôn là vận chuyển, coi như Mạc Thiên Tuyết thân là quốc vương bệ hạ, cũng không biết như thế nào mới có thể đi vào, chỉ có thể chờ đợi trận pháp sư công hội Tông sư tới, mới có thể thuận lợi tiến vào."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn đối với trận pháp một chữ cũng không biết, mặc dù Danh Sư đường có không ít liên quan thư tịch, hắn nhưng lại chưa bao giờ đọc qua qua, càng không chút đọc, đối với trận pháp hiểu rõ, cũng chỉ giới hạn ở danh tự."),t("br"),t("br"),n._v("“Ừm, đúng, đã nhưng trận pháp này tại vận chuyển, Thiên Đạo thư viện có thể hay không nhìn ra?”"),t("br"),t("br"),n._v("Tại nguyên chỗ đứng một hồi, trong lòng đột nhiên động một cái, một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("Võ giả thi triển võ kỹ, đó có thể thấy được võ giả thiếu hụt, võ kỹ lỗ thủng."),t("br"),t("br"),n._v("Luyện đan sư luyện đan, đó có thể thấy được thủ pháp luyện đan vấn đề, đan dược phải chăng có thể thành công."),t("br"),t("br"),n._v("Trận pháp sư bày trận thời điểm, cũng có thể nhìn xảy ra vấn đề."),t("br"),t("br"),n._v("Nói cách khác, chỉ cần là động thái, thư viện liền có thể nhìn ra thiếu hụt."),t("br"),t("br"),n._v("Hiện tại trận pháp này, ngay tại vận chuyển, phải chăng có thể nhìn ra trong đó lỗ thủng?"),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, Trương Huyền vội vàng nhìn sang."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Thư viện chấn động, một quyển sách xuất hiện."),t("br"),t("br"),n._v("“Quả nhiên có thể thực hiện...”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên, vội vàng lật ra thư tịch nhìn sang."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-356"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-358"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);