(window.webpackJsonp=window.webpackJsonp||[]).push([[1586],{1940:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-350-benh-gi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-350-benh-gi"}},[n._v("#")]),n._v(" Chương 350: Bệnh gì?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Một hồi yên tĩnh."),t("br"),t("br"),n._v("Mạc Thiên Tuyết dụi dụi con mắt, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Chỉ thấy trong sân để cho người ta thấy không rõ trận pháp, quả thực đã biến mất."),t("br"),t("br"),n._v("Đạp một cước, liền để cấp ba trận pháp ngừng vận chuyển?"),t("br"),t("br"),n._v("Làm sao... Như là đang nằm mơ?"),t("br"),t("br"),n._v("Phàm là trận pháp, có thể y theo địa hình, sông núi, dòng sông, thậm chí thiên địa tinh thần tạo dựng ra đặc thù từ trường, có thể làm cho tiến vào bên trong sinh mệnh, lâm vào hỗn loạn, tìm không rõ phương hướng."),t("br"),t("br"),n._v("Không đối trận pháp có cực sâu nghiên cứu, muốn tìm được trận cơ hạch tâm ở chỗ đó, để hắn đình chỉ, tuyệt đối không thể."),t("br"),t("br"),n._v("Kẻ trước mắt này, một mặt mê mang, đối với trận pháp hoàn toàn không hiểu bộ dạng, một cước đạp tới liền để cấp ba trận pháp ngừng vận chuyển..."),t("br"),t("br"),n._v("Có thể hay không lại khoa trương một chút?"),t("br"),t("br"),n._v("Nếu như chỉ là vận khí, cái này vận khí cứt chó cũng quá nghịch thiên đi!"),t("br"),t("br"),n._v("Mạc Vũ cũng là sững sờ."),t("br"),t("br"),n._v("Thấy tận mắt Trương Huyền rất nhiều thủ đoạn, biết hắn thần bí, cường đại, thấy cảnh này, còn là suýt chút nữa không có dọa đến tại chỗ ngất đi."),t("br"),t("br"),n._v("Lâm gia sở dĩ lợi hại, tại toàn bộ vương thành hoành hành không sợ, thậm chí liền vương thất mặt mũi đều không cần nhìn, chính yếu nhất liền là dựa vào cấp ba trận pháp."),t("br"),t("br"),n._v("Loại cấp bậc này đại trận bố trí đi ra, Chí Tôn sơ kỳ cường giả gặp gỡ, đều rất khó đào thoát..."),t("br"),t("br"),n._v("Kết quả, cái này liền Lâm gia là cái gì đồ chơi cũng không biết gia hỏa, một cước... Nhẹ nhõm phá giải!"),t("br"),t("br"),n._v("Sớm biết thủ đoạn của ngươi rất nhiều, làm người ta giật mình, có thể cái này... Giật mình cũng có chút quá độc ác đi!"),t("br"),t("br"),n._v("Chẳng lẽ, ngươi loại trừ danh sư, luyện đan sư, y sư, tuần thú sư, độc sư ở ngoài... Còn là một vị trận pháp sư?"),t("br"),t("br"),n._v("Cái này đều là thượng cửu lưu bài danh cao nhất chức nghiệp (độc sư ngoại trừ)..."),t("br"),t("br"),n._v("Người người đều nói nàng là thiên tài, bất kỳ cái gì chức nghiệp vừa học liền biết, cùng gia hỏa này so sánh... Mẹ nó, liền cùng cái hai đồ đần đồng dạng... Vô tri tới cực điểm!"),t("br"),t("br"),n._v("Ngẫm lại đều để người buồn bực có chút phát điên."),t("br"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("Hai người chỉ là chấn kinh, mới vừa còn đầy vẻ khinh bỉ Lâm Đào Tông sư, cái cằm suýt chút nữa rơi trên mặt đất."),t("br"),t("br"),n._v("Sở dĩ liên tục chối từ, cũng không phải là lo lắng Kim Vĩ Hùng Sư chạy đến, cũng không phải cảm thấy trị không hết, mà là... Trận pháp này, là hội trưởng tự tay bố trí, hắn chẳng qua nhị tinh đỉnh phong, liền trận cơ, trận kỳ ở nơi nào cũng không tìm tới, chớ nói chi là triệt bỏ!"),t("br"),t("br"),n._v("Triệt đều triệt không rơi, đương nhiên cũng làm không được để trận pháp ngừng vận chuyển."),t("br"),t("br"),n._v("Ai ngờ gia hỏa này chẳng những để trận pháp đình chỉ, còn như thế đơn giản..."),t("br"),t("br"),n._v("Người khác đình chỉ một cái trận pháp, hao phí vô số tâm huyết, chẳng những nhiều lần nếm thử, còn cần la bàn định vị, chỉnh thể quan sát, tìm đọc cổ tịch, nghiên cứu tư liệu... Làm không cẩn thận sẽ còn bị trận pháp vây khốn, sinh tử không phải do bản thân..."),t("br"),t("br"),n._v("Gia hỏa này ngược lại tốt, nhìn cũng không nhìn một cước đạp tới, cùng đá cọc gỗ không có gì khác nhau..."),t("br"),t("br"),n._v("Nếu là người khác làm như vậy, khẳng định lọt vào trận pháp phản phệ, tại chỗ liền treo."),t("br"),t("br"),n._v("Hắn chẳng những không có việc gì, trận pháp thật đúng là mẹ nó dừng lại..."),t("br"),t("br"),n._v("Có lầm hay không?"),t("br"),t("br"),n._v("“Đi vào đi!”"),t("br"),t("br"),n._v("http://ngantruyen.com/"),t("br"),n._v("Đang khiếp sợ không biết nên nói cái gì, liền nghe cách đó không xa thanh niên, nói một tiếng, đi đầu đi vào sân nhỏ."),t("br"),t("br"),n._v("Theo sát trên đó, nhìn chung quanh một vòng, Lâm Đào Tông sư bất đắc dĩ phát hiện, trận pháp quả nhiên không có lần nữa vận chuyển... Quả thực bị hắn một cước đạp ngừng lại."),t("br"),t("br"),n._v("“Trước đó còn tưởng rằng gia hỏa này chỉ là hư danh, thực lực chênh lệch, không đủ căn cứ, không nghĩ tới lợi hại như vậy... Chỉ sợ cũng chỉ có vị kia thanh danh lên cao Trương Huyền Trương sư, mới có thể cùng chi so sánh!”"),t("br"),t("br"),n._v("Nhìn một hồi, xác nhận trận pháp loại trừ ngừng vận chuyển, cũng không có bất kỳ cái gì tổn hại, Lâm Đào Tông sư khinh thị khuôn mặt, trở nên ngưng trọng lên."),t("br"),t("br"),n._v("Mới vừa nghe được vị này Liễu lão sư, giải quyết nghi vấn khó xử lý tường trở thành hội trưởng tin tức, còn khinh thường một chú ý."),t("br"),t("br"),n._v("Dù sao, bất kỳ cái gì chức nghiệp, thực lực mới là trọng yếu nhất, thực lực không bằng, hết thảy đều là hoa trong gương, trăng trong nước."),t("br"),t("br"),n._v("Thông Huyền cảnh sơ kỳ, không chút nào thu hút, trước đó căn bản không có để ở trong lòng... Hiện tại một cước liền để cấp ba trận pháp đình chỉ, phần này thủ đoạn, liền hội trưởng đều làm không được, xem ra vị này Liễu lão sư, cũng không tưởng tượng bên trong đơn giản như vậy."),t("br"),t("br"),n._v("“Chuyện này nhất định phải nhanh nói cho tộc trưởng...”"),t("br"),t("br"),n._v("Trong lòng âm thầm có quyết định."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trận pháp có sinh môn, tử môn, đương nhiên cũng có ưu điểm cùng khuyết điểm."),t("br"),t("br"),n._v("Tìm tới loại này khuyết điểm, lợi hại hơn nữa cũng sẽ trở nên cùng rơm rạ đồng dạng, không đỡ nổi một đòn."),t("br"),t("br"),n._v("Đương nhiên, loại thiếu sót này, nói trắng ra là là trận pháp từ trường chỗ giao hội."),t("br"),t("br"),n._v("Có ở giữa, có tại một bên, Trương Huyền vận khí không tệ, cái này vừa vặn tại một bên, bị thư viện chỉ rõ vị trí."),t("br"),t("br"),n._v("Ngoại nhân thoạt nhìn hắn một cước đạp tới, liền để trận pháp đình chỉ, đơn giản không gì sánh được, trên thực tế là Thiên Đạo chân khí tiến vào bên trong, dung hợp linh khí trong đó, phá hủy trận pháp bình thường từ trường, mới đưa đến lực lượng đối với hướng, đã mất đi tác dụng."),t("br"),t("br"),n._v("Thiên Đạo chân khí, là tinh thuần nhất linh lực dung hợp mà thành, cùng nam châm đồng dạng, có thể tuỳ tiện phá hư trận pháp kết cấu."),t("br"),t("br"),n._v("Chính vì vậy, đơn giản một cước, liền làm ra ngoài dự liệu hiệu quả. Đổi lại những người khác, coi như biết chỗ thiếu hụt này, sợ rằng cũng phải tốn hao công phu rất lớn mới được."),t("br"),t("br"),n._v("Dọc theo quanh co đá cuội đường nhỏ tiến lên."),t("br"),t("br"),n._v("Viện lạc sạch sẽ gọn gàng, cũng không như trong tưởng tượng dơ dáy bẩn thỉu, đi về phía trước không bao xa, một cái cự đại cung điện xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Ngay sau đó một cái đinh tai nhức óc tiếng ngáy vang lên, chấn mặt đất bụi bặm vung lên, để cho người ta từng đợt mê muội."),t("br"),t("br"),n._v("“Quá tốt rồi, Kim Vĩ Hùng Sư ngủ thiếp đi!” Mạc Thiên Tuyết ánh mắt sáng lên."),t("br"),n._v(" Hắn sợ trận pháp đình chỉ, Kim Vĩ Hùng Sư liền lao ra, không nghĩ tới vừa vặn ngủ, miễn đi không ít phiền phức."),t("br"),t("br"),n._v("Theo thanh âm đi đến, lập tức nhìn thấy đại điện bên trong, một đầu to lớn man thú nằm rạp trên mặt đất, hai mắt nhắm nghiền."),t("br"),t("br"),n._v("Bộ dáng cùng hùng sư có chút giống, hình thể càng lớn, cái đuôi vì kim hoàng sắc, chỉ có đỉnh cao nhất vị trí, hơi hơi trắng bệch."),t("br"),t("br"),n._v("Thiết Xỉ Khiếu Thiên thú cùng nó cùng là nửa bước Chí Tôn man thú, rất rõ ràng cái trước lông tóc càng thêm mềm mại, cơ bắp cũng càng thêm căng cứng."),t("br"),t("br"),n._v("Trước mắt đầu này, cơ bắp lỏng, lông tóc rối tung, cùng người già đồng dạng, chỉ từ bề ngoài, liền có thể nhìn ra, tiến nhập tuổi già."),t("br"),t("br"),n._v("“Liễu hội trưởng, đây chính là chúng ta trấn quốc man thú Kim Vĩ Hùng Sư, bệnh càng thêm nghiêm trọng...”"),t("br"),t("br"),n._v("Nhìn thoáng qua nằm rạp trên mặt đất ngủ Kim Vĩ Hùng Sư, Mạc Thiên Tuyết lắc đầu."),t("br"),t("br"),n._v("Nửa bước Chí Tôn man thú, tính cảnh giác cực mạnh, bọn hắn đi tới trước mặt, đều không có tỉnh lại, đủ thấy bệnh cỡ nào lợi hại."),t("br"),t("br"),n._v("“Nó trước đó có cái gì chứng bệnh?” Trương Huyền hỏi."),t("br"),t("br"),n._v("“Vốn là nó vẫn rất yên tĩnh, đại khái nửa tháng trước, đột nhiên điên lên, hai mắt đỏ thẫm, gặp người liền cắn. Liên tục cắn bị thương bảy, tám cái thái giám cùng cung nữ, hết cách rồi, ta mới mời Lâm hội trưởng ra tay, đem dùng trận pháp vây khốn!”"),t("br"),t("br"),n._v("Nghĩ đến cùng ngày tình cảnh, Mạc Thiên Tuyết còn lòng còn sợ hãi."),t("br"),t("br"),n._v("Đây chính là nửa bước cấp Chí Tôn khác man thú, thật muốn khởi xướng điên đến, ngay cả hắn đều ngăn cản không nổi."),t("br"),t("br"),n._v("May mắn, nó còn có thể khắc chế, nếu không, Ám Ảnh vệ chỉ sợ đều phải chết đi một nhóm lớn."),t("br"),t("br"),n._v("“Đột nhiên nổi điên?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Lợi hại như vậy man thú, coi như già, thân thể suy kiệt, cũng không trở thành đột nhiên bệnh nặng, tinh thần xuất hiện rối loạn đi."),t("br"),t("br"),n._v("“Đúng vậy a, Kim Vĩ Hùng Sư thoạt nhìn hung mãnh, trên thực tế bình thường rất dịu dàng ngoan ngoãn. Là tiên tổ thuần phục lưu lại, tại hoàng cung chờ đợi trọn vẹn bảo vệ hai trăm năm, luôn luôn bình ổn, không có tổn thương qua bất luận kẻ nào, không biết đây là bị quái bệnh gì!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết nói tiếp: “Lúc ấy tìm Mộc Hoành y sư nhìn, hắn nói là tuổi thọ không lâu, đưa tới nôn nóng, nhất định phải nghĩ biện pháp trấn an mới được. Ngay sau đó lại mời không ít y sư đến đây hội chẩn, kết luận cùng hắn đại khái tương đương. Nói là loại bệnh này, không có cách nào trị liệu, chỉ có thể dựa vào bản thân chậm rãi khôi phục! Nếu như có thể vượt qua đi, còn có thể sống lâu một đoạn thời gian, không kháng nổi, cũng là không sai biệt lắm đi đến đầu...”"),t("br"),t("br"),n._v("“Hơn nữa, mặc kệ có thể hay không vượt qua đi, ba tháng đều là cái kỳ hạn, nói cách khác... Lại thế nào trị liệu, tốn hao đại giới lại lớn, trong vòng ba tháng tất nhiên sẽ chết, đã không có khỏi hẳn hy vọng...”"),t("br"),t("br"),n._v("“Ba tháng hẳn phải chết? Ăn người?”"),t("br"),t("br"),n._v("Nghe được những này kết luận, Trương Huyền tinh thần khẽ động, đem Danh Sư đường nội quan tại y sư thư tịch toàn bộ lật ra một lần, rất bất đắc dĩ phát hiện... Cùng dự đoán đồng dạng, không có bất kỳ cái gì trứng dùng."),t("br"),t("br"),n._v("Những sách vở này mặc dù ghi chép không ít cổ quái kỳ lạ chứng bệnh, đều cùng đầu này Kim Vĩ Hùng Sư bệnh tình hoàn toàn không hợp, thậm chí không có nửa điểm chỗ tương tự."),t("br"),t("br"),n._v("Cũng đúng, nếu như dễ dàng như vậy trong sách tìm tới, Mộc Hoành y sư đám người khẳng định đã sớm giải quyết, cũng không trở thành cầu đến trên đầu mình."),t("br"),t("br"),n._v("“Xem ra thư tịch sưu tập còn là không được đầy đủ...”"),t("br"),t("br"),n._v("Lắc đầu."),t("br"),t("br"),n._v("Nếu như thư tịch đủ nhiều, biết chứng bệnh, liền vô cùng có khả năng tìm tới tương tự giống như tình huống."),t("br"),t("br"),n._v("Cái gì cũng không phát hiện, cái kia chính là thư tịch sưu tập quá ít, tri thức lượng còn chưa đủ."),t("br"),t("br"),n._v("“Xem ra bớt thời gian, đem Y Sư công hội thư tịch, cũng thu sạch...”"),t("br"),t("br"),n._v("Hắn mặc dù khảo hạch y sư, thậm chí thành hội trưởng, công hội bên trong thư tịch, lại không đi xem qua, trong thư viện chứa đựng quyển sách, đều là Danh Sư đường thu nhận sử dụng."),t("br"),t("br"),n._v("Người sau mặc dù bao gồm rất nhiều chức nghiệp, muốn nói đối với chứng bệnh, dược vật lý giải, khẳng định vẫn là không bằng chuyên môn công hội."),t("br"),t("br"),n._v("“Liễu hội trưởng, có cần hay không ta đem Mộc Hoành y sư đám người chẩn bệnh kết quả lấy cho ngươi tới? Cũng có thể tham khảo một chút?”"),t("br"),t("br"),n._v("Thấy hắn cúi đầu trầm tư, Mạc Thiên Tuyết nói."),t("br"),t("br"),n._v("“Không cần...”"),t("br"),t("br"),n._v("Khoát tay áo."),t("br"),t("br"),n._v("Đã đối phương không có tìm ra như thế về sau, kết quả này đối với hắn mà nói cũng không có tác dụng gì."),t("br"),t("br"),n._v("“Cái kia... Kim Vĩ Hùng Sư ngủ thiếp đi, có cần hay không đưa nó làm tỉnh lại, ngươi tốt quan sát?”"),t("br"),t("br"),n._v("Y sư xem bệnh, muốn người bệnh thanh tỉnh, mới có thể nhìn, ngửi, hỏi, biết tình huống cặn kẽ, hiện tại đầu này trấn quốc man thú ngủ thiếp đi, bất kỳ cái gì chứng bệnh đều không nhìn thấy, đương nhiên cũng là không pháp trị liệu."),t("br"),t("br"),n._v("Trương Huyền vội vàng lắc đầu."),t("br"),t("br"),n._v("Nếu là thanh tỉnh, nhất định phải gia hỏa này có công kích động tác, mới có thể tại trong óc xuất hiện thư tịch, vạn nhất nó không công kích, hoặc là tốc độ công kích quá nhanh, liền phiền toái."),t("br"),t("br"),n._v("“Ta đi xem một chút...” Nói xong, nhấc chân hướng trước mắt cao lớn man thú đi tới."),t("br"),t("br"),n._v("“Tu vi của hắn không được, có thể giải quyết nghi vấn khó xử lý trên tường rất nhiều vấn đề, y thuật xác thực không thể nói, ta phải thật tốt nhìn xem, đến cùng làm sao cho đầu này man thú chẩn bệnh...”"),t("br"),t("br"),n._v("Một bên Lâm Đào Tông sư, thấy hắn tiến lên, cũng vội vàng đem ánh mắt tập trung tới."),t("br"),t("br"),n._v("Làm trận pháp sư công hội phó hội trưởng, hắn cùng không ít y sư quan hệ giao hảo, biết chữa bệnh rất nhiều thủ đoạn."),t("br"),t("br"),n._v("Có cẩn thận hỏi thăm, quan sát lông tóc, thể sắc;"),t("br"),t("br"),n._v("Có quan sát hô hấp, lắng nghe nhịp tim;"),t("br"),t("br"),n._v("Cũng có quan sát ẩm thực phân và nước tiểu, từ đó tìm kiếm vấn đề..."),t("br"),t("br"),n._v("Trước mắt cái này đánh vỡ Y Sư công hội ghi chép người, biết dùng một loại phương pháp nào?"),t("br"),t("br"),n._v("Trong lòng kỳ quái, hai mắt không dám nháy một cái, sợ bỏ lỡ một chỗ chi tiết!"),t("br"),t("br"),n._v("Bất quá, ngay sau đó nhìn thấy... Thanh niên đi tới man thú trước mặt, sờ soạng một cái, sau đó... Liền một mặt cổ quái đi trở về."),t("br"),t("br"),n._v("“Cái này... Cái này xong?”"),t("br"),t("br"),n._v("Lâm Đào Tông sư một mặt mộng bức."),t("br"),t("br"),n._v("(Cầu phiếu đề cử!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-358"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-360"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);