(window.webpackJsonp=window.webpackJsonp||[]).push([[1588],{1943:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-351-hoa-cuc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-351-hoa-cuc"}},[n._v("#")]),n._v(" Chương 351: Hoa cúc")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Xem bệnh không phải trò đùa, không thể chỉ tốt ở bề ngoài, đi qua, liền man thú lông mọc trên thân thể nhan sắc, tình trạng cơ thể, nhịp tim hô hấp đều không có nhìn, chỉ mò một cái liền trở lại... Có ý tứ gì?"),t("br"),t("br"),n._v("Đừng nói cho ta, đã xem hết đối phương chứng bệnh, biết trị liệu phương pháp?"),t("br"),t("br"),n._v("Mạc Thiên Tuyết, Mạc Vũ cũng từng cái mặt mũi tràn đầy mê hoặc, không biết vị này Liễu hội trưởng trong hồ lô bán cái loại thuốc gì."),t("br"),t("br"),n._v("Đi về tới, vốn cho rằng sẽ nói thứ gì, lại bất đắc dĩ phát hiện, một mình hắn đứng tại chỗ... Bắt đầu ngẩn người."),t("br"),t("br"),n._v("Cái gì cũng không nhìn, tùy tiện sờ một chút liền trở lại ngẩn người, loại này xem bệnh thủ đoạn, đừng nói hắn, ngay cả thu được ngửi rộng biết Lâm Đào Tông sư cũng không hiểu ra sao."),t("br"),t("br"),n._v("“Khụ khụ, Liễu hội trưởng, Kim Vĩ Hùng Sư... Nhưng còn có cứu?”"),t("br"),t("br"),n._v("Thấy hắn ngây người một hồi thật lâu, biểu hiện trên mặt lúc sáng lúc tối, Mạc Thiên Tuyết cũng nhịn không được nữa hỏi."),t("br"),t("br"),n._v("Có thể hay không trị, ngươi ngược lại là nói một câu, vẫn chọc ở chỗ này, làm sao cái ý tứ?"),t("br"),t("br"),n._v("“A, ta nói mấy thứ đồ, ngươi bây giờ đi chuẩn bị ngay, càng nhanh càng tốt!”"),t("br"),t("br"),n._v("Trương Huyền lúc này mới lấy lại tinh thần, cũng không trả lời, sắc mặt nghiêm túc phân phó."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết gật đầu."),t("br"),t("br"),n._v("“Vô Cốt hoa, kim huyết nọc độc, ba ngàn trùng huyết...”"),t("br"),t("br"),n._v("Trương Huyền thuận miệng nói mười mấy dạng dược liệu danh tự."),t("br"),t("br"),n._v("“Cái này... Đây đều là độc dược?”"),t("br"),t("br"),n._v("Vốn cho là hắn sẽ nói một chút đại bổ các loại dược vật, kết quả nói tất cả đều là kịch độc chi vật, nghe Mạc Thiên Tuyết tê cả da đầu."),t("br"),t("br"),n._v("“Ừm, đi thôi!” Trương Huyền khoát tay."),t("br"),t("br"),n._v("“Độc dược, thật có thể trị Kim Vĩ Hùng Sư chứng bệnh?” Mạc Thiên Tuyết không hiểu ra sao."),t("br"),t("br"),n._v("Kim Vĩ Hùng Sư, sinh mệnh suy kiệt, càng có chút nổi điên, dựa theo tình huống bình thường, hẳn là tìm kiếm một chút đại bổ dược vật, cho nó phục dụng, để hắn khôi phục tinh lực, một lần nữa tỉnh lại."),t("br"),t("br"),n._v("Tìm một chút độc dược làm gì?"),t("br"),t("br"),n._v("“Phụ vương, nghe Liễu hội trưởng lời nói, hắn đã để ngươi mua những vật này, nhất định có mục đích của mình!” Thấy cha chần chờ, Mạc Vũ vội vàng truyền âm."),t("br"),t("br"),n._v("Thấy tận mắt vị này cho Đại Dược Vương chữa bệnh, thủ đoạn thần kỳ hay thay đổi, đã để phụ vương chuẩn bị độc dược, đương nhiên có đạo lý của hắn."),t("br"),t("br"),n._v("“Tốt!” Đáp ứng, Mạc Thiên Tuyết khoát tay áo, một lát sau, một cái người áo xám xuất hiện, phân phó một câu."),t("br"),t("br"),n._v("Không hổ là Thiên Vũ vương quốc vương thất, làm việc tốc độ cực nhanh, không bao lâu, dược liệu liền toàn bộ đưa tới."),t("br"),t("br"),n._v("“Liễu hội trưởng, cái này làm như thế nào dùng?”"),t("br"),t("br"),n._v("Nhìn xem một đống độc dược, Mạc Thiên Tuyết chỉ cảm thấy khóe miệng co giật, nhịn không được nhìn lại."),t("br"),t("br"),n._v("Những dược liệu này, tùy tiện một gốc, đều có thể tuỳ tiện hạ độc chết Tông sư cường giả, mấy chục dạng chung vào một chỗ, càng thêm đáng sợ, thật không biết hắn muốn những vật này làm gì."),t("br"),t("br"),n._v("“Có hay không trường mâu? Chuẩn bị ba mươi sáu cái!”"),t("br"),t("br"),n._v("Vẫn như cũ không có trả lời, Trương Huyền nói tiếp."),t("br"),t("br"),n._v("Trường mâu là một loại binh khí, trong vương cung hộ vệ, rất nhiều đều sẽ đeo, vô cùng tốt tìm, mấy phút đồng hồ sau, trước mắt liền thả một đống lớn."),t("br"),t("br"),n._v("“Lại đi chuẩn bị bốn mươi con trâu dê, cũng đem chém giết, máu tươi toàn bộ chứa đựng tại trong thùng...”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục phân phó."),t("br"),t("br"),n._v("Mặc dù lòng tràn đầy nghi hoặc, nhưng biết trước mắt vị này Liễu hội trưởng, chắc chắn sẽ không bắn tên không đích, Mạc Thiên Tuyết tiếp tục để cho người ta chuẩn bị."),t("br"),t("br"),n._v("Hết thảy chuẩn bị thỏa đáng, Trương Huyền lúc này mới thở phào nhẹ nhõm, mấy bước đi tới ban đầu chuẩn bị dược liệu trước mặt, bàn tay trên bàn vỗ."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Mười mấy loại dược vật, lập tức bay lên, song chưởng hợp lại, chân khí trùng kích vào, độc dược đồng thời chấn vỡ, biến thành thuốc bột, từng loại dung hợp lại cùng nhau, xanh mơn mởn, để cho người ta xem xét bên dưới liền rùng mình."),t("br"),t("br"),n._v("Những dược vật này, đơn độc đồng dạng liền có thể hạ độc chết Tông Sư cảnh cường giả, mười mấy dạng dung hợp, uy lực càng lớn, chỉ sợ nửa bước Chí Tôn ngửi bên trên một ngụm, đều ít nhất phải hôn mê nửa ngày."),t("br"),t("br"),n._v("“Đây là... Độc sư phối dược thủ đoạn? Chẳng lẽ... Vị này Liễu hội trưởng, là một vị độc sư?”"),t("br"),t("br"),n._v("Nhìn thấy độc phấn tung bay, dược vật nương theo dung hợp không ngừng cải biến nhan sắc, Mạc Thiên Tuyết, Lâm Đào Tông sư tất cả đều âm thầm cảnh giác."),t("br"),t("br"),n._v("Độc sư đáng sợ ở chỗ có thể vượt cấp giết người."),t("br"),t("br"),n._v("Đừng nhìn có chút độc sư bản thân tu vi không tính là gì, nhưng ai cũng không biết hắn có thể hợp với dạng gì dược vật, làm không cẩn thận liền sẽ chủ quan mất Kinh Châu, chết tại tay của đối phương bên trên."),t("br"),t("br"),n._v("Loại này ví dụ chỗ nào cũng có, nguyên nhân chính là như thế, cái nghề nghiệp này mới bị người kiêng kị, để cho người ta hoảng sợ."),t("br"),t("br"),n._v("Vị này Liễu hội trưởng, đem nhiều như vậy kịch độc dược vật, tiện tay dung hợp, nói rõ đối với dược vật độc tính, phối trộn có cực kỳ cao minh lực khống chế, sẽ không thật sự là một vị độc sư đi!"),t("br"),t("br"),n._v("“Lợi hại y sư, nắm giữ dược liệu thuộc tính, coi như kịch độc cũng có thể lý giải một hai, không có gì kỳ quái!”"),t("br"),t("br"),n._v("Nhìn ra hai người nghi hoặc, Mạc Vũ truyền âm."),t("br"),t("br"),n._v("Y sư cùng độc sư, một cái cứu người một cái giết người, thoạt nhìn tương sinh tương khắc, trên thực tế nhưng lại có rất nhiều chỗ tương đồng!"),t("br"),t("br"),n._v("Thậm chí truyền thuyết, lúc trước vị thứ nhất độc sư, ban đầu là một vị cao minh y sư, vợ con bị cừu nhân thảm sát về sau, lúc này mới tính tình đại biến, nghiên cứu kịch độc đồ vật, lưu lại  một sách, đem cừu nhân cả nhà đều toàn bộ diệt tuyệt."),t("br"),t("br"),n._v("Đương nhiên, sự thật đến tột cùng như thế nào, niên đại lâu, dù ai cũng không cách nào xác nhận, nhưng thông qua cái này truyền thuyết, có thể biết, độc sư cùng y sư, dung hợp chỗ rất nhiều. Cao minh y sư, có thể phối độc, cũng không có gì có thể kỳ quái."),t("br"),t("br"),n._v("“Tốt rồi...”"),t("br"),t("br"),n._v("Ba người ngay tại giao lưu, chỉ thấy cách đó không xa thanh niên ngừng lại, thuốc bột đã triệt để phối chế hoàn thành, tiện tay bỏ vào nước thùng, trong đó thanh thủy lập tức biến thành màu xám tro."),t("br"),t("br"),n._v("Làm xong những này, Trương Huyền nhẹ nhàng thở ra, dọc theo Kim Vĩ Hùng Sư dạo qua một vòng, lần nữa ngừng lại, ngẩng đầu nhìn về phía cách đó không xa Lâm Đào Tông sư."),t("br"),t("br"),n._v("“Trận pháp này, có hay không nhanh chóng kích hoạt thủ đoạn?”"),t("br"),t("br"),n._v("“Nhanh chóng kích hoạt?” Lâm Đào sửng sốt."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Lúc trước đi Thú đường thời điểm, từng thấy tận mắt Vân Đào kích hoạt qua trận pháp, cần sớm chuẩn bị, khá là phiền toái."),t("br"),t("br"),n._v("“Tốt nhất có thể tại nửa cái hô hấp bên trong, để trận pháp khởi động, mới có thể phát huy hiệu quả!” Suy nghĩ một chút, Trương Huyền nói."),t("br"),n._v("“Nửa cái hô hấp?” Lâm Đào suýt chút nữa không có bị ngụm nước bị nghẹn: “Cái này sao có thể! Như thế lớn cấp ba trận pháp, muốn kích hoạt, chí ít cần tám vị Tông sư cường giả đỉnh phong, canh giữ ở tám cái phương vị, đồng thời rót vào chân khí. Đợi khí tức toàn bộ dung hợp mới có thể vận chuyển... Biện pháp nhanh nhất, cũng muốn mười phút đồng hồ trở lên!”"),t("br"),t("br"),n._v("“Mười phút đồng hồ?”"),t("br"),t("br"),n._v("“Ừm, nếu như nắm giữ Chí Tôn thực lực, một người liền có thể kích hoạt, tốc độ cũng sẽ tăng tốc, không qua, không có hai ba phút cũng là không cách nào hoàn thành!”"),t("br"),t("br"),n._v("Lâm Đào một mặt im lặng."),t("br"),t("br"),n._v("Ngươi đến cùng biết hay không trận pháp?"),t("br"),t("br"),n._v("Trận pháp kích hoạt, nghe đơn giản, trên thực tế lại muốn chân khí truyền vào, giao phó hoạt tính, quá trình này rườm rà phức tạp, cực kỳ phiền phức."),t("br"),t("br"),n._v("Chỉ cần đối với trận pháp hơi hiểu rõ người, đều hiểu điểm ấy, ngươi một cước đem đại trận này đều đạp ngừng vận chuyển, làm sao có thể không biết?"),t("br"),t("br"),n._v("“Thời gian này quá dài...”"),t("br"),t("br"),n._v("Trương Huyền nhíu mày: “Chẳng lẽ không có biện pháp tốt hơn?”"),t("br"),t("br"),n._v("“Có là có, chỉ là... Ta làm không được!” Chần chờ một chút, Lâm Đào nói."),t("br"),t("br"),n._v("“Ồ? Nói qua tới nghe một chút!” Trương Huyền vội vàng nhìn qua."),t("br"),t("br"),n._v("“Mỗi cái trận pháp đều có một cái trận tâm, là toàn bộ đại châm vận chuyển nhất đầu mối then chốt địa phương, chỉ cần trong này rót vào chân khí, trận pháp liền có thể rất nhanh khởi động... Đây cũng là trận bàn vận chuyển phương pháp!”"),t("br"),t("br"),n._v("Lâm Đào nói đến đây, có chút xấu hổ: “Ngươi cũng biết, muốn khắc hoạ trận bàn, kém cỏi nhất đều là tam tinh trận pháp sư, ta chỉ là nhị tinh đỉnh phong... Còn kém một chút!”"),t("br"),t("br"),n._v("Trận bàn, là thông qua phương pháp đặc thù, đem lớn như vậy trận pháp khắc hoạ tại một cái có thể mang theo người vật phẩm bên trên, cần dùng lúc, chỉ cần chân khí truyền vào là được, cho dù không phải trận pháp sư, cũng có thể nhẹ nhõm kích hoạt."),t("br"),t("br"),n._v("Mặc dù uy lực so chân chính trận pháp yếu đi không ít, lại có thể trong nháy mắt kích thích, hơn nữa thuận tiện mang theo, là không ít người dùng để thủ đoạn bảo mệnh."),t("br"),t("br"),n._v("Mạc Vũ trên người liền có một viên, đây cũng là vì sao Mạc Thiên Tuyết, yên tâm để nàng đi ra ngoài nguyên nhân."),t("br"),t("br"),n._v("“Ngươi ý tứ, tìm tới trận tâm... Liền có thể rất nhanh kích hoạt trận pháp đúng không?”"),t("br"),t("br"),n._v("Trương Huyền hỏi."),t("br"),t("br"),n._v("“Vâng!” Lâm Đào gật đầu: “Nói thì nói như thế, có thể nghĩ muốn tìm tới trận tâm, nào có dễ dàng như vậy. Loại này bố trí tốt trận pháp, cùng cố định trận bàn không giống, trận tâm sẽ căn cứ địa hình, canh giờ phát sinh biến hóa, liền xem như ta, cũng tìm không thấy, chớ nói chi là... Kích hoạt lên!”"),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Trương Huyền không tại nhiều hỏi, mà là chần chờ một chút, một lát sau con mắt rơi vào một chỗ."),t("br"),t("br"),n._v("Ngay sau đó, không có quá nhiều dừng lại, lần nữa đi tới Kim Vĩ Hùng Sư trước, cầm lấy một chuôi trường mâu."),t("br"),t("br"),n._v("Bàn tay run lên, mũi thương cắm vào ẩn chứa nọc độc trong thùng gỗ, trường mâu bên trên lập tức phát ra “Tư tư!” Thanh âm, tựa như sắt thép đều bị ăn mòn."),t("br"),t("br"),n._v("“Đi!”"),t("br"),t("br"),n._v("Cầm dính đầy nọc độc trường mâu, Trương Huyền nhướng mày, cổ tay bỗng nhiên run lên ra ngoài."),t("br"),t("br"),n._v("Sưu!"),t("br"),t("br"),n._v("Như là tiêu thương đồng dạng, trường mâu thẳng tắp bay về phía trước ra ngoài, ẩn chứa lực lượng khổng lồ, trực tiếp cắm ở ngủ Kim Vĩ Hùng Sư trên người."),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Còn tưởng rằng hắn muốn làm gì, thấy cảnh này, Mạc Thiên Tuyết, Lâm Đào đám người tất cả đều trong nháy mắt lông tơ nổ lên."),t("br"),t("br"),n._v("Đây chính là kịch độc... Không nói trước có thể hay không đem đầu này trấn quốc man thú hạ độc chết, như vậy trực tiếp công kích, tương đương với đang gây hấn với đối phương, rất dễ dàng đem triệt để chọc giận."),t("br"),t("br"),n._v("Vốn là gia hỏa này liền tính khí không tốt, khắp nơi ăn người, một khi chọc giận, còn không đem tất cả mọi người giết?"),t("br"),t("br"),n._v("Rống!"),t("br"),t("br"),n._v("Quả nhiên, trong lòng sợ hãi còn không có kết thúc, liền nghe đến một tiếng nổi giận gầm rú, nguyên bản rơi vào trạng thái ngủ say Kim Vĩ Hùng Sư, bỗng nhiên mở mắt, là đèn lồng con mắt, trong nháy mắt trở nên đỏ thẫm."),t("br"),t("br"),n._v("“Xong...”"),t("br"),t("br"),n._v("Thấy cảnh này, mấy người trái tim đồng thời lạnh một nửa."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Kim Vĩ Hùng Sư cúi đầu nhìn thoáng qua đâm vào trên người trường mâu, phẫn nộ chi ý xông lên mây xanh, lần nữa bạo rống một tiếng, đứng dậy, thân hình cao lớn, tựa như một tòa núi nhỏ, còn không có động thủ, liền cho người ta áp lực cường đại, để cho người ta có chút run chân."),t("br"),t("br"),n._v("“Liễu hội trưởng, nguy hiểm...”"),t("br"),t("br"),n._v("Mạc Vũ nhịn không được hô một tiếng."),t("br"),t("br"),n._v("Nổi giận Kim Vĩ Hùng Sư, liền phụ thân hắn loại này Tông sư đỉnh phong đều ngăn cản không nổi, đối phương chẳng qua Tông sư sơ kỳ, làm sao có thể chống chọi."),t("br"),t("br"),n._v("Tiếng la kết thúc, vốn cho rằng phía trước thanh niên, sẽ lập tức lui lại, ai ngờ đã thấy hắn lại cầm lấy một cái trường mâu, dính nọc độc, lần nữa đâm tới."),t("br"),t("br"),n._v("Hơn nữa... Lần này đâm vào phương vị là... Kim Vĩ Hùng Sư lỗ đít."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Trường mâu phương hướng rất chuẩn, trực tiếp ghim đi vào, máu tươi lập tức theo thân mâu điên cuồng hướng ra phía ngoài phun ra."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Lần này không chỉ Mạc Thiên Tuyết, Lâm Đào đám người điên rồi, Kim Vĩ Hùng Sư càng là điên rồi."),t("br"),t("br"),n._v("Nó mở to mắt, muốn xem đến cùng ai dám động đến nó, kết quả là nhìn thấy gia hỏa này cầm trường mâu đâm nó hoa cúc, lửa giận trong lồng ngực suýt chút nữa để hắn bạo tạc."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("To lớn cái đuôi một quyển, bỗng nhiên đối với Trương Huyền đánh tới, bởi vì tốc độ quá nhanh, phát ra liên tiếp âm bạo."),t("br"),t("br"),n._v("Tựa hồ sớm biết nó sẽ có động tác này, Trương Huyền cũng không hoảng hốt, thân thể nhoáng một cái, đã xuất hiện tại mười mấy mét có hơn trước đó xem trọng vị trí, bàn chân trên mặt đất bỗng nhiên đạp mạnh."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Nương theo thanh âm, Mạc Thiên Tuyết, Mạc Vũ đám người chỉ cảm thấy thấy hoa mắt, một cái trận pháp trong nháy mắt khởi động, lần nữa đem viện lạc bao phủ."),t("br"),t("br"),n._v("“Trận pháp... Kích hoạt?”"),t("br"),t("br"),n._v("Lâm Đào Tông sư con mắt lần nữa trợn tròn."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-359"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-361"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);