(window.webpackJsonp=window.webpackJsonp||[]).push([[1589],{1944:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-352-chua-khoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-352-chua-khoi"}},[n._v("#")]),n._v(" Chương 352: Chữa khỏi")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Trong nháy mắt liền đem trận pháp kích hoạt, chỉ nói rõ một việc... Gia hỏa này tìm được trận tâm!"),t("br"),t("br"),n._v("Từ vừa rồi trò chuyện, có thể biết, trận tâm là cái gì, khẳng định là không biết, sau khi nói xong trực tiếp tìm tới, hơn nữa lợi dụng..."),t("br"),t("br"),n._v("Đây có phải hay không là thật?"),t("br"),t("br"),n._v("Lâm Đào Tông sư chỉ cảm thấy thân thể loạn chiến, cả người đều nhanh ngất đi."),t("br"),t("br"),n._v("Trận tâm sẽ căn cứ bố trí hoàn cảnh mà thay đổi, coi như gia chủ loại này tam tinh sơ kỳ trận pháp sư, không có la bàn định vị, cẩn thận quan sát, đều rất khó tìm đến, gia hỏa này cái gì đều vô dụng, tùy tiện nhảy một cái, sau đó... Bàn chân đạp mạnh, trận pháp liền vận chuyển!"),t("br"),t("br"),n._v("Coi như tận mắt thấy, làm sao đều cảm thấy như là đang nằm mơ..."),t("br"),t("br"),n._v("Trước đó còn cảm thấy mình là trận pháp tông sư, đối mặt một cái chỉ có Thông Huyền cảnh tiểu tử, nắm giữ cực đại ưu thế... Thấy cảnh này, đều nhanh nhịn không được khóc."),t("br"),t("br"),n._v("Liễu hội trưởng, đến cùng ngươi là trận pháp tông sư, hay ta là?"),t("br"),t("br"),n._v("Vì sao ở trước mặt ngươi, ta cảm thấy bản thân đối với trận pháp lý giải, cùng ngớ ngẩn đồng dạng, cái gì cũng sẽ không đâu?"),t("br"),t("br"),n._v("Rống! Rống! Rống!"),t("br"),t("br"),n._v("Trận pháp khởi động, Mạc Thiên Tuyết đám người rốt cuộc thấy không rõ trước mặt tình cảnh, không qua, ngay sau đó nghe được Kim Vĩ Hùng Sư, có chút sụp đổ gầm thét, giống như có chuyện gì muốn cho nó phát điên cùng nổi điên."),t("br"),t("br"),n._v("Mấy chục cái hô hấp qua đi, thanh âm ngừng."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Trong lòng sốt ruột, Mạc Thiên Tuyết mấy người vội vàng đi thẳng về phía trước."),t("br"),t("br"),n._v("Bọn hắn vừa rồi cũng không rời đi bao xa, lại có Lâm Đào dẫn đường, cũng không chạm đến trận pháp, rất nhanh liền đi ra mê vụ, thấy được cảnh tượng trước mắt, xem xét phía dưới, tất cả đều lông mày nhảy loạn, cảm thấy đều nhanh điên rồi."),t("br"),t("br"),n._v("Chỉ thấy để thanh niên cứu chữa Kim Vĩ Hùng Sư, toàn thân cắm đầy trường mâu, trên mũi, trong lỗ tai, lỗ đít..."),t("br"),t("br"),n._v("Ba mươi sáu cái trường mâu, toàn bộ đâm đi lên, máu tươi chảy xuôi đầy đất, mới vừa rồi còn uy phong lẫm liệt, không ngừng gào thét hùng sư, giờ phút này đã ủ rũ con mắt đều không mở ra được, hơi thở mong manh."),t("br"),t("br"),n._v("“Liễu hội trưởng...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết chỉ cảm thấy da đầu gân xanh nhảy tưng, một ngụm lão huyết không có suýt chút nữa phun ra."),t("br"),t("br"),n._v("Mời ngươi tới, là vì tới trị liệu trấn quốc man thú, không phải để ngươi tới giết nó..."),t("br"),t("br"),n._v("Trên người đâm nhiều như vậy trường mâu, làm cùng chổi lông gà giống như, mấu chốt nhất mũi thương bên trên đều ẩn chứa kịch độc, ngươi đây là... Không giết chết nó không cam tâm ah!"),t("br"),t("br"),n._v("Không mới lần thứ nhất gặp mặt sao?"),t("br"),t("br"),n._v("Cái gì thù cái gì oán? Về phần xuống ác như vậy tay sao?"),t("br"),t("br"),n._v("Một bồn lửa giận tại ở ngực thiêu đốt, Mạc Thiên Tuyết nắm đấm xiết chặt, nếu không phải cưỡng ép nhịn xuống, chỉ sợ sớm đã tiến lên đem gia hỏa này đánh thành bánh thịt."),t("br"),t("br"),n._v("Mặc dù trấn quốc man thú, mạng không lâu dài, hơn nữa còn có chút nổi điên đả thương người, dù sao bảo vệ Thiên Vũ vương quốc mấy trăm năm, nắm giữ cực sâu tình cảm."),t("br"),t("br"),n._v("Đối xử với nó như thế, quả thực liền là ở trước mặt đánh mặt!"),t("br"),t("br"),n._v("Sắc mặt tái xanh hướng cách đó không xa thanh niên nhìn lại, chỉ thấy hắn sắc mặt trắng bệch, tựa hồ cũng có chút hao tổn quá lớn."),t("br"),t("br"),n._v("Nhìn thấy ba người, thanh niên giống như nhẹ nhàng thở ra, thân thể mềm nhũn ngồi dưới đất: “Nó bệnh chứng này thực sự khó mà trị liệu, không qua, may mắn không làm nhục mệnh...”"),t("br"),t("br"),n._v("“May mắn không làm nhục mệnh?”"),t("br"),t("br"),n._v("Thấy đối phương nụ cười tự tin, Mạc Thiên Tuyết sững sờ."),t("br"),t("br"),n._v("Chẳng lẽ hiểu lầm hắn?"),t("br"),t("br"),n._v("Thật sự là tại cứu chữa Kim Vĩ Hùng Sư?"),t("br"),t("br"),n._v("Ý nghĩ này dâng lên, nhịn không được lần nữa hướng cách đó không xa man thú nhìn lại, xem xét phía dưới, đã cảm thấy da đầu lần nữa nổ tung, suýt chút nữa không có tại chỗ điên rồi."),t("br"),t("br"),n._v("Chỉ thấy mới vừa rồi còn có thể mở mắt ra, phát ra gào thét hùng sư, lúc này hai mắt nhắm nghiền, nằm ngang trên mặt đất, liền hô hấp cũng bị mất."),t("br"),t("br"),n._v("“Kim Vĩ Hùng Sư... Chết rồi...”"),t("br"),t("br"),n._v("Mạc Vũ hai người cũng nhìn ra không thích hợp, nguyên một đám tròng mắt sắp rơi ra tới."),t("br"),t("br"),n._v("Để ngươi đến chữa bệnh, không phải để ngươi tới giết đi thú..."),t("br"),t("br"),n._v("May mắn không làm nhục mệnh... Hạnh em gái ngươi ah!"),t("br"),t("br"),n._v("Ngươi ý tứ, đem nó giết chết, là may mắn không làm nhục mệnh?"),t("br"),t("br"),n._v("“Liễu hội trưởng, đây rốt cuộc chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Cố nén sắp bạo tạc lửa giận, Mạc Thiên Tuyết gắt gao nhìn chằm chằm tên trước mắt."),t("br"),t("br"),n._v("Lời thề son sắt mời hắn tới, thật đúng là cho rằng có thể làm cho trấn quốc man thú khởi tử hồi sinh, sống lâu một đoạn thời gian, nằm mơ đều không có nghĩ đến, không những không chữa khỏi, trả lại chữa chết..."),t("br"),t("br"),n._v("Nếu không phải cố kỵ thân phận đối phương, khẳng định đã sớm hô người đem gia hỏa này chặt thành thịt vụn."),t("br"),t("br"),n._v("“Cái gì chuyện gì xảy ra? Ta trị xong...” Không nhìn ra đối phương phẫn nộ, Trương Huyền nói: “Còn tốt, xem như trị liệu rất thuận lợi!”"),t("br"),t("br"),n._v("“Thuận lợi?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết lông mày lay động."),t("br"),t("br"),n._v("Đem người bệnh trị chết, xem như thuận lợi?"),t("br"),t("br"),n._v("Ngươi không phải y sư, mà là... Đồ tể đi!"),t("br"),t("br"),n._v("“Khụ khụ, Liễu hội trưởng, Kim Vĩ Hùng Sư làm sao... Không có hô hấp, sẽ không chết chứ?”"),t("br"),t("br"),n._v("Nhìn ra phụ vương cũng nhịn không được nữa, bất cứ lúc nào cũng sẽ bạo tạc, Mạc Vũ vội vàng tiến về phía trước một bước, mở miệng hỏi."),t("br"),t("br"),n._v("“Chết rồi?” Trương Huyền thế mới biết mọi người vì sao bộ dáng này, lắc đầu: “Ta ở bên cạnh, muốn chết nào có dễ dàng như vậy...”"),t("br"),t("br"),n._v("Nói xong, mấy bước đi tới hùng sư trước mặt, một cước đạp tới."),t("br"),t("br"),n._v("“Bớt ở chỗ này giả chết, nhanh đứng lên cho ta...”"),t("br"),t("br"),n._v("Rống!"),t("br"),t("br"),n._v("Bàn chân rơi vào hùng sư gương mặt bên trên, sau một khắc, thoạt nhìn như là đoạn tuyệt hô hấp Kim Vĩ Hùng Sư, giống như bị chạm điện nhảy dựng lên."),t("br"),t("br"),n._v("Một tiếng điên cuồng gào thét, toàn thân lông tóc chấn động mạnh một cái."),t("br"),t("br"),n._v("Sưu sưu sưu sưu!"),t("br"),t("br"),n._v("Cắm vào nó thân thể trường mâu toàn bộ bắn đi ra, rơi xuống một chỗ."),t("br"),t("br"),n._v("“Đem những cái kia huyết dịch uống...”"),t("br"),n._v("Trương Huyền tiện tay một chỉ."),t("br"),t("br"),n._v("Trước đó chuẩn bị không ít máu tươi, tất cả đều đặt ở cách đó không xa trong thùng gỗ, hùng sư vội vàng đi tới, miệng lớn thôn phệ."),t("br"),t("br"),n._v("Rất nhanh, mấy cái thùng gỗ huyết dịch đều bị uống sạch, Kim Vĩ Hùng Sư cũng tựa hồ khôi phục không ít, không tiếp tục trước đó ủ rũ bộ dáng, tinh lực sung mãn."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Đem một màn này nhìn ở trong mắt, Mạc Thiên Tuyết đám người tất cả đều giống như nằm mơ, nguyên một đám ngốc tại nguyên chỗ."),t("br"),t("br"),n._v("Như thế kịch độc trường mâu, cắm vào trong cơ thể ba mươi sáu cái, Kim Vĩ Hùng Sư không những không chết, còn tinh lực tràn đầy..."),t("br"),t("br"),n._v("Đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("Hơn nữa, không phải mới vừa rõ ràng cảm giác nó đoạn tuyệt hít thở sao?"),t("br"),t("br"),n._v("Làm sao đạp một cước, liền sống lại?"),t("br"),t("br"),n._v("Vị này Liễu hội trưởng, đi tới phía sau tổng cộng đá ba cước, cước thứ nhất, trận pháp đình chỉ, thứ hai chân trận pháp kích hoạt, thứ ba chân... Hùng sư thanh tỉnh..."),t("br"),t("br"),n._v("Cảm giác chân của hắn liền cùng có ma lực đồng dạng, lần lượt để cho người ta chấn kinh."),t("br"),t("br"),n._v("“Liễu hội trưởng...”"),t("br"),t("br"),n._v("Thấy cách đó không xa hùng sư, tinh khí thần tựa hồ khôi phục không ít, cũng nhịn không được nữa, Mạc Thiên Tuyết nhìn lại."),t("br"),t("br"),n._v("“Có phải là kỳ quái hay không, đây là chuyện như vậy?”"),t("br"),t("br"),n._v("Trương Huyền cười cười: “Kỳ thật... Các ngươi đầu này Kim Vĩ Hùng Sư, cũng không phải là nhiễm bệnh, mà là... Bị người hạ độc!”"),t("br"),t("br"),n._v("“Bị người hạ độc?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết sững sờ, Lâm Đào Tông sư Tông sư thì là biến sắc."),t("br"),t("br"),n._v("Đối với Thiên Vũ vương quốc lão tổ cấp bậc man thú hạ độc? Đến cùng là ai?"),t("br"),t("br"),n._v("Lại là làm sao làm được?"),t("br"),t("br"),n._v("“Kim Vĩ Hùng Sư cái đuôi, khỏe mạnh tình huống dưới, toàn bộ đều là màu vàng, trước đó lại có một chỗ hơi trắng, nói rõ khẳng định không phải phổ thông chứng bệnh đơn giản như vậy!”"),t("br"),t("br"),n._v("Trương Huyền mở lời giải thích."),t("br"),t("br"),n._v("Nghe nói như thế, Mạc Thiên Tuyết mấy người cũng nghĩ tới, trước đó hùng sư cái đuôi đỉnh, quả thực có nhiều chỗ nhan sắc không đúng."),t("br"),t("br"),n._v("“Ngươi nói nó nóng nảy nổi điên... Đã nổi điên, vì sao không phải người gì đều ăn, chỉ ăn mấy cái thái giám cung nữ liền xong việc? Cái này đủ để chứng minh, nó cũng không phải là triệt để nổi điên, mà là còn có nhất định lý trí!”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục nói: “Kết hợp hai điểm này, ta phỏng đoán, nó khả năng trúng một loại kịch độc, đem khí độc bức bách đến cái đuôi, mới khiến cho nhan sắc sửa đổi!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người sững sờ."),t("br"),t("br"),n._v("Quả thực."),t("br"),t("br"),n._v("Nếu như nó thật sự là triệt để nổi điên, khẳng định gặp người liền ăn, tuyệt không có khả năng ủng hộ mấy cái râu ria thái giám, cung nữ."),t("br"),t("br"),n._v("“Có phải hay không là những này thái giám cung nữ, chính là cho nó hạ độc người, trong lòng tức giận, mới đem chém giết...” Đột nhiên, Mạc Vũ tinh thần khẽ động."),t("br"),t("br"),n._v("Muốn để đầu này nửa bước Chí Tôn man thú trúng độc, nhờ vào lực lượng khẳng định làm không được, duy nhất phương pháp, chính là cho nó ăn đồ vật bên trong nạp liệu."),t("br"),t("br"),n._v("Mà có khả năng nhất liền là hầu hạ nó những cái kia thái giám cung nữ."),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu: “Không người cùng chi khế ước, ý nghĩ của nó cũng là nói không nên lời, giết những này thái giám cung nữ, vừa đến, là muốn báo thù, thứ hai, cũng là nói cho các ngươi biết, những người này có hiềm nghi... Kết quả các ngươi lại khi nó điên rồi, trực tiếp dùng trận pháp phong tỏa...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết hơi đỏ mặt."),t("br"),t("br"),n._v("Lúc trước trấn quốc man thú, ăn hết thái giám cung nữ, đem hắn sợ hãi, còn tưởng rằng điên thật rồi, vừa vặn Lâm hội trưởng cùng với hắn một chỗ, liền mời cầu người sau bố trí xuống trận pháp."),t("br"),t("br"),n._v("Không nghĩ tới... Đối phương là muốn truyền đưa ý tứ này."),t("br"),t("br"),n._v("“Trúng độc lợi hại, nó dù sao cũng là sống mấy trăm năm man thú, lại thêm nửa bước Chí Tôn cảnh tu vi, sẽ không lập tức chết ngay, lại cùng Mộc Hoành y sư đám người nói đồng dạng, sẽ không vượt qua ba tháng!”"),t("br"),t("br"),n._v("“Loại độc này, nếu như sớm trị liệu, sẽ đơn giản không ít, nhưng bây giờ đã tán đến kinh mạch toàn thân, muốn trị liệu rất khó!”"),t("br"),t("br"),n._v("“Chần chờ hồi lâu, ta quyết định dùng phương pháp lấy độc công độc!”"),t("br"),t("br"),n._v("“Cho nên, ta mới khiến cho ngươi chuẩn bị kịch độc, cùng sử dụng trường mâu, đem những này nọc độc, đâm vào hùng sư trong cơ thể kịch độc tụ tập địa phương.”"),t("br"),t("br"),n._v("“May mắn, may mắn không làm nhục mệnh, đưa nó cứu sống...”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Bàn tay tại trên người đối phương đụng một cái, là hắn biết trúng loại nào kịch độc."),t("br"),t("br"),n._v("Loại độc này, từng tại Độc điện tàng thư khố bên trong thấy qua, biết phương pháp trị liệu, muốn lấy độc trị độc."),t("br"),t("br"),n._v("Biết những này, còn lại liền đơn giản, chỉ muốn những này nọc độc, cùng nó phân tán tại thể nội độc tiếp xúc, chỉ có thể lẫn nhau trung hoà, triệt để hóa giải."),t("br"),t("br"),n._v("Đương nhiên, đổi lại người khác, vẫn như cũ bất lực."),t("br"),t("br"),n._v("Bởi vì nọc độc đi khắp toàn thân, tán dật tại thân thể rất nhiều kinh mạch bên trong, coi như muốn tìm, cũng không tìm được, lại thế nào khả năng nhẹ nhõm hóa giải?"),t("br"),t("br"),n._v("Phải biết một khi tìm nhầm địa phương, không những không thể giải quyết, cái này nọc độc cũng sẽ đem kỳ độc chết."),t("br"),t("br"),n._v("Bất quá, Trương Huyền có Thiên Đạo thư viện, thư tịch bên trong rõ ràng ghi chép nọc độc vị trí, tổng cộng ba mươi sáu chỗ, thậm chí ** bên trong đều có, tìm ra được liền đơn giản."),t("br"),t("br"),n._v("Về phần phía sau dê bò huyết dịch, là bởi vì đâm nhiều như vậy trường mâu, mất máu quá nhiều, để hắn khôi phục thể lực."),t("br"),t("br"),n._v("Nghe xong giải thích, Mạc Thiên Tuyết đám người lần nữa trợn tròn tròng mắt, nhìn về phía thanh niên trước mắt, phục sát đất."),t("br"),t("br"),n._v("Khó trách có thể giải quyết nghi vấn khó xử lý trên tường tất cả nan đề, trở thành công hội hội trưởng, phần này y thuật thực sự quá cường đại!"),t("br"),t("br"),n._v("Cho dù tận mắt nhìn thấy, đều khó mà tin tưởng."),t("br"),t("br"),n._v("“Ta mặc dù giải quyết trên người nó kịch độc, nhưng nó quả thực tuổi thọ sắp đạt tới cuối cùng, coi như tạm thời không ngại, cũng tuyệt đối sống không quá một năm, trừ phi...”"),t("br"),t("br"),n._v("Trương Huyền chần chờ một chút, nói: “Nghĩ biện pháp để nó đột phá đến Chí Tôn cảnh!”"),t("br"),t("br"),n._v("“Đột phá Chí Tôn?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết hô hấp lập tức dồn dập lên, nắm đấm xiết chặt: “Liễu hội trưởng nhưng có biện pháp?”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-360"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-362"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);