(window.webpackJsonp=window.webpackJsonp||[]).push([[1590],{1946:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-353-tong-su-đinh-phong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-353-tong-su-đinh-phong"}},[n._v("#")]),n._v(" Chương 353: Tông sư đỉnh phong")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chí Tôn cảnh, võ giả đỉnh phong nhất, đến cái này một cấp bậc, người tu luyện thân thể, chân khí, tinh thần, đều đạt đến nhân thể cực hạn, đi đến cuối con đường."),t("br"),t("br"),n._v("Nhất là man thú, huyết mạch giam giữ lại tiến lên khả năng, không có huyết mạch tiến dời, muốn thành công, gần như không có khả năng."),t("br"),t("br"),n._v("Chính vì vậy, Kim Vĩ Hùng Sư thủ hộ Thiên Vũ vương quốc hai trăm năm, đều không thể lại làm đột phá."),t("br"),t("br"),n._v("Bằng không, nhờ vào vương thất tài lực, vật lực, khẳng định đã sớm để nó tấn cấp, không đến mức vẫn lưu tại nửa bước Tông sư, mà không cách nào dịch bước."),t("br"),t("br"),n._v("“Biện pháp... Là có, có điều, khả năng cần tốn hao cực lớn đại giới!” Trương Huyền đầu lâu vung lên, ánh mắt lộ ra mênh mông chi ý."),t("br"),t("br"),n._v("“Chỉ cần có thể đột phá, lại lớn đại giới, ta cũng nguyện ý trả giá...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết vội nói."),t("br"),t("br"),n._v("Trấn quốc man thú một khi tử vong, Thiên Vũ vương quốc liền sẽ lập tức lâm vào bấp bênh bên trong, nếu có thể để hắn đột phá đến Chí Tôn, không chỉ miễn trừ nguy hiểm, quốc lực cũng chắc chắn cường thịnh hơn, trước đó một chút ngo ngoe muốn động gia tộc, khẳng định cũng sẽ thu liễm lại tới."),t("br"),t("br"),n._v("Cho nên, Kim Vĩ Hùng Sư có thể đột phá, tốn hao lại lớn đại giới, đều sẽ không tiếc."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thấy đối phương loại thái độ này, Trương Huyền hài lòng gật đầu, thần sắc lạnh nhạt nhìn qua, như là không trệ tại vật thế ngoại cao nhân: “Nói khó rất khó, nói đơn giản cũng đơn giản! Nếu có linh thạch, số lượng càng nhiều, đột phá tỷ lệ cũng là càng lớn; Mặt khác, còn cần một chút Chí Tôn cảnh công pháp bí tịch, không yêu cầu chất lượng, chỉ cầu số lượng, càng nhiều càng tốt!”"),t("br"),t("br"),n._v("Hai thứ đồ này, đương nhiên không phải cho Kim Vĩ Hùng Sư đột phá dùng, mà là vì tăng thực lực lên chuẩn bị."),t("br"),t("br"),n._v("Giúp đối phương giải xong độc, trước đó linh thạch ân tình đã báo đáp, nếu như sẽ giúp man thú tấn cấp, vậy liền thuộc về một cái khác phục vụ hạng mục, cần mặt khác thu phí đấy."),t("br"),t("br"),n._v("Đương nhiên, đưa ra linh thạch, bí tịch, cũng không hoàn toàn là vì tư tâm, Thiên Đạo chân khí có thể tịnh hóa man thú huyết mạch, điều kiện tiên quyết là tu vi của hắn cùng đối phương không thể cách biệt quá xa."),t("br"),t("br"),n._v("Giúp Tông sư đỉnh phong man thú tấn cấp đến nửa bước Chí Tôn, còn có thể làm đến. Muốn cho nửa bước Chí Tôn bước ra một bước cuối cùng, không đạt tới Tông sư đỉnh phong, chỉ sợ rất khó hoàn thành."),t("br"),t("br"),n._v("Nếu như không có loại này giới hạn, không ngừng giúp man thú tịnh hóa huyết mạch, Thiết Xỉ Khiếu Thiên thú hoàn toàn có thể liên tục đột phá, đạt tới Chí Tôn đỉnh phong thậm chí lần nữa đột phá, coi như phong hào vương quốc, cũng có thể quét ngang, không cần thiết trong này giả vờ giả vịt."),t("br"),t("br"),n._v("Có thể nói, yêu cầu linh thạch, Chí Tôn cảnh bí tịch, là vì hắn tăng thực lực lên, cũng là vì để trước mắt đầu này man thú, có thể thuận lợi đột phá, cũng là không phải lừa gạt đối phương."),t("br"),t("br"),n._v("“Linh thạch... Trong vương quốc là còn có một số, có điều, chỉ có năm mai khoảng chừng, lại nhiều cũng không lấy ra được!”"),t("br"),t("br"),n._v("Nghe được cần những vật này, Mạc Thiên Tuyết mặc dù kỳ quái, còn là mở miệng nói: “Về phần Chí Tôn cảnh công pháp bí tịch... Một bản đều không có!”"),t("br"),t("br"),n._v("“Chỉ có năm mai? Một bản đều không có?”"),t("br"),t("br"),n._v("“Là... Linh thạch chỉ có phong hào vương quốc mới có tư cách giao dịch vật phẩm, chúng ta Thiên Vũ vương quốc có mấy cái, cũng là tốn hao to lớn đại giới lấy được. Những năm này dùng để ban thưởng cùng kết giao, cơ bản đều tiêu hao sạch...”"),t("br"),t("br"),n._v("“Chí Tôn cảnh công pháp, nhất đẳng vương quốc bị hạn chế, lại thêm tài nguyên thiếu thốn, căn bản là không có khả năng tu luyện tới loại cảnh giới này, cho nên... Công pháp có cũng không có gì dùng, ngược lại sẽ mang đến tai hoạ...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết có chút xấu hổ."),t("br"),t("br"),n._v("Linh thạch thứ này, quá mức trân quý, coi như có thể lưu thông phong hào vương quốc, cũng không có bao nhiêu viên, huống chi bọn hắn."),t("br"),t("br"),n._v("Có thể có năm mai hàng tồn, đã coi như là không ít."),t("br"),t("br"),n._v("Về phần Chí Tôn cảnh tu luyện công pháp, toàn bộ Thiên Vũ vương quốc, đều không có một vị Chí Tôn cảnh, đi nơi nào tìm loại cấp bậc này công pháp?"),t("br"),t("br"),n._v("Lại nói, thật muốn có cấp Chí Tôn khác cường giả xuất hiện, phong hào vương quốc tất nhiên kiêng kị, đến lúc đó chỉ có thể càng thêm phiền phức."),t("br"),t("br"),n._v("“Năm mai... Liền năm mai, toàn bộ cho ta, ta nghĩ biện pháp để nó đột phá!”"),t("br"),t("br"),n._v("Mặc dù không có lấy tới quá nhiều linh thạch, có chút ăn thiệt thòi, Trương Huyền còn là gật đầu đáp ứng."),t("br"),t("br"),n._v("Nương theo tu luyện cấp bậc càng cao, muốn linh khí cũng càng nhiều, một viên linh thạch liền để hắn đạt tới Tông sư trung kỳ, sáu cái, hẳn là đầy đủ tấn thăng đến đỉnh phong."),t("br"),t("br"),n._v("“Liền xin nhờ Liễu hội trưởng!”"),t("br"),t("br"),n._v("Nghe được năm mai linh thạch đầy đủ, Mạc Thiên Tuyết nhẹ nhàng thở ra, cổ tay khẽ đảo, một cái thật dài hộp ngọc xuất hiện tại lòng bàn tay, đưa tới."),t("br"),t("br"),n._v("Mở hộp ra, bên trong linh khí khuấy động, một loạt năm mai linh thạch bày ra trong đó, để cho người ta lỗ chân lông mở rộng, không nói ra được dễ chịu."),t("br"),t("br"),n._v("Trương Huyền tiện tay thu hồi, hài lòng nhẹ gật đầu."),t("br"),t("br"),n._v("“Liễu hội trưởng, còn cần chuẩn bị cái gì? Đan dược các loại muốn hay không?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết hỏi tiếp."),t("br"),t("br"),n._v("Huyết mạch tiến dời vô cùng phức tạp, coi như Thú đường một chút lợi hại tuần thú sư, đều không thể làm đến, hắn thực sự nghĩ không ra, chỉ bằng vào linh thạch, muốn làm sao, mới có thể để cho Kim Vĩ Hùng Sư đột phá nhất đủ một cửa ải."),t("br"),t("br"),n._v("“Không cần, giúp ta tìm một kiện tĩnh thất, ta phải thật tốt chuẩn bị một phen, mặt khác, đừng cho người quấy rầy!”"),t("br"),t("br"),n._v("Trương Huyền khoát khoát tay."),t("br"),t("br"),n._v("“Vâng!” Mạc Thiên Tuyết lên tiếng."),t("br"),t("br"),n._v("Kim Vĩ Hùng Sư chữa khỏi, không tại nổi điên, sân nhỏ cũng là an toàn, không có bất kỳ cái gì nguy hiểm."),t("br"),t("br"),n._v("Mạc Thiên Tuyết trong này tìm cái an tĩnh gian phòng, để hắn đi vào nghỉ ngơi, lúc này mới cùng Mạc Vũ đám người lui ra ngoài."),t("br"),t("br"),n._v("Vừa rời đi viện lạc, Lâm Đào Tông sư liền xoay người cáo từ, vội vã rời đi."),t("br"),t("br"),n._v("Nhìn hắn đi xa, Mạc Thiên Tuyết con mắt đột nhiên híp lại, bắn ra một đạo quang mang."),t("br"),t("br"),n._v("“Phụ vương...” Mạc Vũ nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Xem ra phải cẩn thận Lâm gia!” Mạc Thiên Tuyết lắc đầu."),t("br"),t("br"),n._v("“Lâm gia?” Mạc Vũ kỳ quái."),t("br"),t("br"),n._v("Trước đó không phải quan hệ cũng không tệ lắm sao? Vì sao đột nhiên nói ra lời này?"),t("br"),t("br"),n._v("“Ta hoài nghi Kim Vĩ Hùng Sư trúng độc, phía sau có Lâm gia cái bóng!” Mạc Thiên Tuyết khẽ nói."),t("br"),t("br"),n._v("Có thể trở thành một nước chi chủ, đương nhiên không phải giá áo túi cơm hạng người, Trương Huyền chẩn đoán chính xác Kim Vĩ Hùng Sư chứng bệnh, trong lòng của hắn liền có suy đoán."),t("br"),t("br"),n._v("Mạc Vũ có chút không dám tin tưởng."),t("br"),n._v("“Kim Vĩ Hùng Sư, là vương quốc chúng ta trấn quốc man thú, nếu như nó chết rồi, ngươi cảm thấy ai sẽ đến lợi?” Mạc Thiên Tuyết nhìn qua."),t("br"),t("br"),n._v("“Vương thất lập quốc vượt qua ngàn năm, căn cơ vững chắc, coi như không có Kim Vĩ Hùng Sư, một vài gia tộc, thế lực cũng có thể tuỳ tiện hủy diệt, sẽ không tạo thành quá lớn ảnh hưởng, chỉ có Lâm gia, thoát lực khống chế... Không có Kim Vĩ Hùng Sư áp chế, chỉ sợ thật đúng là sẽ...”"),t("br"),t("br"),n._v("Phân tích một chút, Mạc Vũ thần sắc cũng biến thành ngưng trọng."),t("br"),t("br"),n._v("Lâm gia có một vị nửa bước Chí Tôn lão tổ, rất cường đại trận pháp, còn có Hiên Viên phong hào vương quốc ở sau lưng chống đỡ, thật muốn mưu đồ quấy rối, quả thực phần thắng rất lớn."),t("br"),t("br"),n._v("Hơn nữa, có thể đối với nửa bước Chí Tôn hữu dụng độc dược, không phải bình thường gia tộc, chỉ sợ cũng không có khả năng nắm bắt tới tay."),t("br"),t("br"),n._v("Còn nữa, có thể đem nhãn tuyến bố đến hoàng cung, để đường đường bệ hạ cũng không biết tình huống dưới, đút cho trấn quốc man thú ăn hết, làm đến điểm này, liền có thể đếm được trên đầu ngón tay."),t("br"),t("br"),n._v("“Kim Vĩ Hùng Sư nổi điên thời điểm, Lâm Nhược Thiên vừa vặn đến hoàng cung, cùng ta thương nghị sự tình, lúc ấy ta đã cảm thấy kỳ quái, sự tình quá mức kỳ quặc. Hiện tại xem ra, hắn chỉ sợ cũng là sớm có dự mưu! Chỉ cần trận pháp vây khốn hùng sư, tra không ra chứng bệnh, liền sớm muộn gì đều sẽ độc phát thân vong!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết nắm đấm xiết chặt."),t("br"),t("br"),n._v("Trước đó còn không cảm thấy được, giờ phút này từ man thú trúng độc đẩy ngược, mới phát hiện, rất nhiều manh mối đều chỉ hướng Lâm gia."),t("br"),t("br"),n._v("“Coi như xác định là hắn, thì có biện pháp gì? Lâm Nhược Thiên nữ nhi là Hiên Viên vương quốc thái tử phi...”"),t("br"),t("br"),n._v("Mạc Vũ lắc đầu."),t("br"),t("br"),n._v("Mặc dù đoán được có thể là Lâm gia làm, nhưng bọn hắn cũng không dám đem đối phương như thế nào."),t("br"),t("br"),n._v("Không nói trước Lâm Nhược Thiên nắm giữ nửa bước Chí Tôn thực lực, chỉ nói nữ nhi của hắn tầng kia quan hệ, liền không có bất luận cái gì vương quốc dám động."),t("br"),t("br"),n._v("“Đúng vậy a... Chỉ hy vọng Liễu hội trưởng có thể để cho Kim Vĩ Hùng Sư đột phá, một khi nó đạt tới Chí Tôn cảnh, coi như bọn hắn muốn làm gì, cũng muốn kiêng kị mấy phần!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết nói."),t("br"),t("br"),n._v("Mặc dù Danh Sư đường tại vương thành bày biện, không quản lý vương quyền tranh bá, nhưng nếu như vi phạm với chuẩn tắc, lợi dụng thủ đoạn khác, làm sinh linh đồ thán, khẳng định sẽ trực tiếp ra tay."),t("br"),t("br"),n._v("Danh Sư đường, giáo hóa thiên hạ, mục tiêu là người người như rồng, tiểu đả tiểu nháo có thể, xúc phạm đạo đức ranh giới cuối cùng, chẳng cần biết ngươi là ai, đều có thể tuỳ tiện đem hủy diệt."),t("br"),t("br"),n._v("Chính là bởi vì có này chấn nhiếp, mặc dù vương quốc san sát, nhưng lại chưa thống nhất."),t("br"),t("br"),n._v("Đương nhiên, một chút vương quốc thực sự vị trí địa lý quá lệch, thật giống như Thiên Huyền vương quốc, vị trí quá lệch, lại thêm khốn cùng, thống nhất cũng sẽ hướng bên trong đập tiền, không có người sẽ ngốc lấy tốn hao tâm huyết."),t("br"),t("br"),n._v("Kim Vĩ Hùng Sư đột phá đến Chí Tôn, Lâm gia không cách nào chống chọi, dục niệm tự nhiên là sẽ đánh tiêu, nếu không, chỉ có thể tự tìm phiền phức."),t("br"),t("br"),n._v("“Ta tin tưởng Liễu hội trưởng, nhất định có thể thành công!”"),t("br"),t("br"),n._v("Mạc Vũ nói."),t("br"),t("br"),n._v("Trương sư đánh thuần thú pháp, giúp man thú đột phá tình cảnh, nàng còn rõ mồn một trước mắt, có lẽ cái này không gì làm không được thanh niên, thật sự có thể sáng tạo kỳ tích, để Thiên Vũ vương quốc vượt qua cửa ải khó khăn này."),t("br"),t("br"),n._v("“Chỉ mong đi!” Mạc Thiên Tuyết cũng gật gật đầu, nhớ tới cái gì, mặt mũi tràn đầy nghi ngờ nhìn lại: “Tiểu Vũ, ngươi không phải nói hôm qua cùng vị này Liễu lão sư chung đụng cũng không vui sướng sao? Làm sao của ngươi Thanh Ưng Thú, cùng hắn quen thuộc như vậy, giống như ngươi đối với hắn cũng hiểu rất rõ?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Mạc Vũ khuôn mặt đỏ lên: “Ta làm sao có thể hiểu rõ hắn? Chỉ là... Thuận miệng nói một chút!”"),t("br"),t("br"),n._v("“Mặc kệ ngươi có ý nghĩ gì, phụ vương vẫn là câu nói kia, hảo hảo nắm chắc cơ hội, vị này Liễu hội trưởng, là nhân trung long phượng, nếu như ngươi có thể đem lòng của hắn lưu lại, Thiên Vũ vương quốc về sau tấn cấp phong hào, cũng chưa chắc làm không được!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết nói: “Đương nhiên, còn có cái kia Trương Huyền Trương sư, hai người kia, bất kể là ai, có thể bắt lấy một cái liền tốt.”"),t("br"),t("br"),n._v("“Bắt lấy một cái...”"),t("br"),t("br"),n._v("Mạc Vũ vỗ trán một cái, ta cũng muốn tùy tiện bắt lấy một cái, có thể cái này hình như là cùng là một người..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Kim Vĩ Hùng Sư viện lạc yên tĩnh gian phòng."),t("br"),t("br"),n._v("Ngồi ngay ngắn ở trong, điều chỉnh không biết bao lâu, lúc này mới từ trị liệu Kim Vĩ Hùng Sư ủ rũ bên trong khôi phục lại."),t("br"),t("br"),n._v("Ngay sau đó, cổ tay khẽ đảo, một viên linh thạch xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Trạng thái điều chỉnh tốt, sưu tập đầy đủ linh thạch, tự nhiên muốn lần nữa tăng cao tu vi."),t("br"),t("br"),n._v("Tinh thần khẽ động, Tông Sư cảnh Thiên Đạo công pháp, nhanh chóng vận chuyển lên tới."),t("br"),t("br"),n._v("Cô cô cô ục ục!"),t("br"),t("br"),n._v("Thiên Đạo công pháp nhanh chóng từ linh thạch bên trong rút ra linh khí, chiết xuất vì chân khí dung nhập đan điền, mà tu vi của hắn cũng đang không ngừng tiến bộ."),t("br"),t("br"),n._v("Tông sư trung kỳ đỉnh phong!"),t("br"),t("br"),n._v("Tông sư hậu kỳ!"),t("br"),t("br"),n._v("Tông sư hậu kỳ đỉnh phong!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Hơn nửa canh giờ về sau, khí hải một hồi oanh minh, toàn thân giống như là phá vỡ một loại nào đó bình chướng, cuồng bạo kình khí, đi khắp toàn thân, một cỗ cường đại khí tức, từ trên người tự nhiên sinh ra."),t("br"),t("br"),n._v("Cùng lúc trước so sánh, cường đại không chỉ một lần, tựa hồ nắm giữ không dùng hết lực lượng."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Cảm nhận được trên người bành trướng hung mãnh chân khí, Trương Huyền đột nhiên đứng dậy, hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Tông Sư cảnh đỉnh phong... Cuối cùng đã tới!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-361"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-363"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);