(window.webpackJsonp=window.webpackJsonp||[]).push([[1595],{1949:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-358-lan-thu-hai-uoc-đau-ha"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-358-lan-thu-hai-uoc-đau-ha"}},[n._v("#")]),n._v(" Chương 358: Lần thứ hai ước đấu (hạ)")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Nghe nói như thế, nhìn nhau, Triệu Nhã đám người mang theo chần chờ."),i("br"),i("br"),n._v("Cùng Trương sư tiếp xúc thời gian không dài, cũng biết hắn luôn luôn không thích phiền phức."),i("br"),i("br"),n._v("Nếu như biết mình đám người chạy đến Thiên Vũ học viện đại náo, thậm chí cùng một cái lão sư suýt chút nữa trở mặt, chỉ sợ thật sẽ không cao hứng."),i("br"),i("br"),n._v("Nhưng cứ thế mà đi, lửa giận trong lòng làm sao có thể nuốt được đi?"),i("br"),i("br"),n._v("“Mạc sư, ngươi là danh sư, cũng cho chúng ta phân tích một cái, vừa rồi tỷ thí, nếu như không phải hắn nhúng tay, Mộc Tuyết Tinh tất thua không thể nghi ngờ, thua không thừa nhận ngược lại cũng thôi, thế mà còn nói ngang tay...”"),i("br"),i("br"),n._v("Trịnh Dương khí mở lời."),i("br"),i("br"),n._v("“Tốt rồi, Liễu lão sư bất kể nói thế nào đều là lão sư, chừa cho hắn cái mặt mũi. Còn Trương sư bên kia, ta sẽ thay các ngươi giải thích, hắn sẽ không tức giận cũng sẽ không trách tội các ngươi!”"),i("br"),i("br"),n._v("Thấy hắn thao thao bất tuyệt, sợ nói tiếp, càng thêm khó mà kết thúc, Mạc Vũ vội vàng truyền âm."),i("br"),i("br"),n._v("“Làm phiền Mạc sư!”"),i("br"),i("br"),n._v("Trịnh Dương còn muốn nói tiếp, bị Triệu Nhã cắt ngang."),i("br"),i("br"),n._v("“Ừm!” Mạc Vũ gật đầu."),i("br"),i("br"),n._v("Quay người nhìn về phía Trương Huyền, Triệu Nhã mở lời: “Liễu lão sư, xưng hô ngươi một tiếng lão sư, là xem ở Thiên Vũ học viện mặt mũi, đã ngươi hôm nay nói ngang tay, cái kia coi như ngang tay! Bất quá... Mộc Tuyết Tinh, ba ngày sau, ta vẫn như cũ khiêu chiến ngươi, ngươi có dám hay không tiếp?”"),i("br"),i("br"),n._v("“Làm sao không dám!” Mộc Tuyết Tinh cắn răng."),i("br"),i("br"),n._v("“Vậy là tốt rồi, hi vọng ba ngày sau tỷ thí, Liễu lão sư lại không muốn nhúng tay; Cũng hi vọng ngươi thua, sẽ không lại không thừa nhận!”"),i("br"),i("br"),n._v("Hất lên ống tay áo, Triệu Nhã đối với Mạc Vũ ôm quyền: “Mạc sư, cáo từ!”"),i("br"),i("br"),n._v("Nói xong, lạnh lùng nhìn Trương Huyền một chút, mang theo mọi người quay người đi ra ngoài."),i("br"),i("br"),n._v("//ngantruyen.com/"),i("br"),n._v("“Ây...”"),i("br"),i("br"),n._v("Không nghĩ tới chạy tới ngăn cản, không những không thành công, còn làm các nàng vừa hẹn trận thứ ba, mấu chốt nhất... Còn bị bản thân học sinh ghi hận, Trương Huyền xoa xoa mi tâm, một mặt khổ bức."),i("br"),i("br"),n._v("Chuyện này là sao!"),i("br"),i("br"),n._v("Cái này, cũng quá hố đi!"),i("br"),i("br"),n._v("“Trước ngươi vì sao không nhắc nhở ta...” Tràn đầy buồn bực, quay đầu nhìn về phía Mạc Vũ."),i("br"),i("br"),n._v("“Ta...” Mạc Vũ hơi đỏ mặt."),i("br"),i("br"),n._v("Nàng vốn là nghĩ đến hai cái học sinh đùa giỡn, không ảnh hưởng toàn cục, cũng không nghĩ tới bầu không khí như vậy xấu hổ..."),i("br"),i("br"),n._v("Đang không biết trả lời như thế nào, linh cơ khẽ động, truyền âm qua: “Kỳ thật, như vậy cũng tốt, có cạnh tranh, của ngươi hai nhóm học sinh đều sẽ cố gắng tu luyện, ngươi nhìn... Triệu Nhã cùng Mộc Tuyết Tinh, tiến bộ bao nhanh...”"),i("br"),i("br"),n._v("Nàng nói như vậy chỉ muốn thoát khỏi xấu hổ, Trương Huyền nghe được trong tai, lại là sững sờ."),i("br"),i("br"),n._v("Khoan hãy nói, hai người có cạnh tranh, quả thực liều mạng tu luyện, tiến bộ đều rất lớn."),i("br"),i("br"),n._v("Bản thân truyền thụ cho kiếm pháp, đao pháp, mặc dù là bản rút gọn, càng thích hợp các nàng tu luyện, nhưng muốn hoàn toàn nắm giữ, lĩnh ngộ ra kiếm ý, đao ý, không có mười ngày nửa tháng rất khó làm đến, ngắn ngủi hơn hai ngày thời gian, đều hoàn thành... Đủ thấy hai người khắc khổ mức độ."),i("br"),i("br"),n._v("Hơn nữa, mấu chốt nhất là... Đã trải qua loại này tỷ thí, Mộc Tuyết Tinh tín nhiệm với hắn độ, nhanh chóng gia tăng."),i("br"),i("br"),n._v("“Được rồi, cứ như vậy... Tuyệt đối đừng đem thân phận của ta nói lộ ra!”"),i("br"),i("br"),n._v("Chần chờ một chút, đành phải không tại nhiều nói."),i("br"),i("br"),n._v("Ván đã đóng thuyền, sự tình đã như vậy, nói nhiều hơn nữa cũng vô ích, luôn không khả năng khắp nơi tuyên dương, Liễu Trình liền là Trương Huyền, các ngươi là đồng môn sư huynh đệ chứ?"),i("br"),i("br"),n._v("Thật nói như vậy, nhị tinh danh sư khảo hạch chỉ sợ cũng không cần tiếp tục tiến hành, không sai biệt lắm liền phế đi."),i("br"),i("br"),n._v("“Lão sư, thật xin lỗi...”"),i("br"),i("br"),n._v("Mộc Tuyết Tinh đi tới trước mặt, một mặt xấu hổ."),i("br"),i("br"),n._v("Trương Huyền lắc đầu, đang muốn an ủi một câu, chỉ thấy đám người lập tức bị đẩy ra, một cái lão giả vội vã chạy tới: “Vừa rồi thi triển đao ý tiền bối ở đâu? Còn xin thu ta vì học sinh, tại hạ Bạch Hâm, đối với đao pháp lý giải đã rất sâu, tuyệt sẽ không bôi nhọ lão sư tên tuổi...”"),i("br"),i("br"),n._v("“...” Trương Huyền, Mộc Tuyết Tinh."),i("br"),i("br"),n._v("“...” Mạc Vũ."),i("br"),i("br"),n._v("“...” Mọi người."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("“Đáng giận! Đáng giận!”"),i("br"),i("br"),n._v("Đi tại về phủ đệ trên đường, Trịnh Dương đám người nguyên một đám sắc mặt tái xanh, âm trầm đáng sợ."),i("br"),i("br"),n._v("Nhớ tới cái kia Liễu lão sư đáng giận sắc mặt, đã cảm thấy lửa giận thiêu đốt."),i("br"),i("br"),n._v("“Triệu Nhã, ngươi làm gì ngăn cản ta? Nói thật, nếu không phải ngươi lôi kéo, ta đều nghĩ xin sư chiến đài!”"),i("br"),i("br"),n._v("Trịnh Dương khẽ nói."),i("br"),i("br"),n._v("“Sư chiến đài? Vị này Liễu lão sư, tiện tay một chỉ, phá vỡ ta trường kiếm, đối với nắm chắc thời cơ diệu đến đỉnh phong, nhãn lực mạnh, chỉ sợ không tại Trương sư phía dưới, coi như giảm thấp xuống tu vi, chúng ta cũng tuyệt không phải đối thủ!”"),i("br"),i("br"),n._v("Triệu Nhã lắc đầu."),i("br"),i("br"),n._v("“Coi như không phải là đối thủ lại như thế nào? Hắn đây là hiểu khi dễ người! Che chở học sinh của mình, thua không thừa nhận, ta còn là lần thứ nhất thấy không biết xấu hổ như vậy lão sư, coi như đánh không thắng, cũng quyết không để hắn đẹp mắt!” Trịnh Dương cắn răng."),i("br"),i("br"),n._v("“Trịnh Dương nói không sai, cũng là thực lực của ta không được, thực lực mạnh, khẳng định hảo hảo giáo huấn một lần, để hắn đẹp mắt!”"),i("br"),i("br"),n._v("Viên Đào tại một bên giúp gió, không ngại chuyện lớn."),i("br"),i("br"),n._v("“Tốt rồi, vị này Liễu lão sư mặc dù vô sỉ, nhưng cũng quả thực có bản lĩnh, lần này cần không phải ta thể chất cường hãn, sư tổ vừa truyền thụ kiếm pháp, thua khẳng định là ta! Việc cấp bách, không phải trong này nhục mạ, mà là nghĩ biện pháp tăng thực lực lên, nếu không ba ngày sau thua, khó chịu chính là chúng ta!”"),i("br"),i("br"),n._v("Triệu Nhã cắt ngang hai người thảo luận."),i("br"),i("br"),n._v("“Như thế...” Mấy người gật đầu."),i("br"),n._v(" Vừa rồi loại tình huống kia, ước chiến là tất nhiên, nếu không, đối phương khẳng định cho là mình đám người dễ khi dễ, Trương sư danh dự, chẳng thèm ngó tới."),i("br"),i("br"),n._v("Nhưng ngắn ngủi ba ngày thời gian, liền để cái kia Mộc Tuyết Tinh trưởng thành đến Triệu Nhã suýt chút nữa đánh không lại tình trạng, đủ thấy vị này Liễu lão sư năng lực, quả thực đáng sợ."),i("br"),i("br"),n._v("Hiện tại Trương sư không tại, ba ngày sau tỷ thí, nếu là Triệu Nhã thua, nói nhiều hơn nữa cũng vô ích!"),i("br"),i("br"),n._v("“Trương sư không tại, chúng ta chỉ có thể khẩn cầu sư tổ, hi vọng hắn có thể giúp đỡ chỉ điểm... Mấy ngày nay các ngươi cũng muốn hảo hảo tu luyện, vạn nhất đối phương lần nữa thua không thừa nhận, cùng lắm thì chúng ta liền khiêu chiến sư chiến đài, náo cái long trời lở đất!”"),i("br"),i("br"),n._v("Bàn tay như ngọc trắng hất lên, Triệu Nhã hai mắt tỏa ánh sáng, lộ ra tư thế hiên ngang chi ý, lúc trước nghe được người khác thảo luận Lục Tầm không bằng Trương lão sư, trực tiếp chạy tới tìm phiền toái, đủ thấy nàng cũng là không biết sợ người."),i("br"),i("br"),n._v("Đối phương một lần vô sỉ ngược lại cũng thôi, hai lần vô sỉ, vậy cũng đừng trách bọn hắn không nể mặt mũi!"),i("br"),i("br"),n._v("Tại Thiên Huyền vương quốc, Lục Tầm lão sư có thể đánh, một cái Liễu lão sư mà thôi, cùng lắm thì liên thủ đánh bên trên một trận, cũng không có gì!"),i("br"),i("br"),n._v("“Tốt, ta liền đợi đến câu nói này...” Trịnh Dương gật gật đầu nhìn về phía Vương Dĩnh, Viên Đào đám người: “Thế nào? Nếu như cái này Liễu lão sư vẫn như cũ vô sỉ như vậy, có dám hay không đem sự tình làm lớn chuyện?”"),i("br"),i("br"),n._v("“Có gì không dám? Cái gì Liễu lão sư, chơi hắn nha!” Viên Đào gào thét."),i("br"),i("br"),n._v("“Ta cũng sẽ không e ngại!” Lưu Dương gật đầu."),i("br"),i("br"),n._v("“Tính ta một người!” Vương Dĩnh cũng ánh mắt kiên định."),i("br"),i("br"),n._v("“Vậy là tốt rồi, quyết định như vậy đi...”"),i("br"),i("br"),n._v("Mấy người thương nghị xong, cảm thấy tâm tình tốt rất nhiều, nhanh chân hướng phủ đệ phương hướng đi đến, vừa tới đến viện lạc cửa ra vào, liền thấy hai bóng người đứng ở bên ngoài."),i("br"),i("br"),n._v("“Làm phiền hai vị tiến đến bẩm báo, nghe nói ta là Hồng Liên thành cố nhân, chuyên tới để bái kiến Bạch y sư!” Bên trong một cái quản gia bộ dáng người, đứng tại hộ vệ trước mặt, nói."),i("br"),i("br"),n._v("“Vâng!” Nghe được là Bạch y sư cố nhân, hộ vệ chần chờ một chút, đi vào trong, thời gian không dài, chỉ thấy Bạch y sư theo ở phía sau, đi ra."),i("br"),i("br"),n._v("“Bạch y sư, chúng ta lại gặp mặt!”"),i("br"),i("br"),n._v("Nhìn thấy Bạch Thiềm, hai người một mặt hưng phấn, trái lại Bạch Thiềm một mặt mê mang: “Hai vị là...”"),i("br"),i("br"),n._v("“Ừm?” Thấy hắn không nhận ra bản thân, hai người một hồi kỳ quái, quản gia bộ dáng người nhịn không được nói: “Bạch y sư, ta là Lộ quản gia, vị này là lão gia nhà chúng ta Đại Dược Vương, ngươi làm sao...”"),i("br"),i("br"),n._v("Hai cái này, chính là Hồng Liên thành Đại Dược Vương cùng Lộ quản gia."),i("br"),i("br"),n._v("Độc điện sự tình bình phục, Đại Dược Vương với tư cách đặc sứ “Sư thúc tổ” bằng hữu, nhận lấy càng lớn coi trọng, lại không ai dám cho hắn ăn khế ước cổ."),i("br"),i("br"),n._v("Người khác không biết đặc sứ “Sư thúc tổ” là ai, Đại Dược Vương thế nhưng là rõ ràng, chính là Thiên Vũ vương thành vị này Bạch Thiềm y sư."),i("br"),i("br"),n._v("Lần này, vừa vặn vương thành có dược liệu giao dịch, liền dẫn đường quản gia tới, thừa cơ bái phỏng, giữ gìn mối quan hệ."),i("br"),i("br"),n._v("Bạch Thiềm làm vương thành danh nhân, hiện tại càng là trèo lên Trương Huyền danh sư, nghe ngóng chỗ ở còn là rất đơn giản, thời gian không dài đã tìm được nơi này."),i("br"),i("br"),n._v("Chỉ là... Khuôn mặt chưa đổi, làm sao Bạch y sư không biết mình hai người?"),i("br"),i("br"),n._v("“Đại Dược Vương?”"),i("br"),i("br"),n._v("Bạch Thiềm run run một cái."),i("br"),i("br"),n._v("Hắn sở dĩ tại vương thành lăn lộn tai to mặt lớn, cũng bởi vì Trương sư mượn nhờ dung mạo của hắn, cứu chữa Đại Dược Vương, không nghĩ tới bản nhân thế mà tới."),i("br"),i("br"),n._v("“Đại Dược Vương, kỳ thật lúc trước cứu chữa của ngươi cũng không phải là ta, mà là... Trương sư, chuyện là như thế này...”"),i("br"),i("br"),n._v("Không dám giấu diếm, đem sự tình nói rõ chi tiết một lần."),i("br"),i("br"),n._v("“Nguyên lai là Trương sư... Không biết hắn hiện tại nơi nào? Có thể thuận tiện để chúng ta bái kiến?” Nghe được lại là loại tình huống này, Đại Dược Vương, Lộ quản gia tất cả đều sững sờ, lập tức nói."),i("br"),i("br"),n._v("Nhất là Đại Dược Vương, thấy tận mắt Trương Huyền cải biến dung mạo, tin đến càng nhanh."),i("br"),i("br"),n._v("“Trương sư khảo hạch nhị tinh danh sư, không tại phủ đệ...”"),i("br"),i("br"),n._v("Giải thích một câu, Bạch Thiềm đang muốn tiếp tục nói chuyện, đột nhiên nhìn thấy Triệu Nhã đám người đi tới, vội vàng giới thiệu: “Mấy vị này chính là Trương sư cao túc, nếu như hai vị muốn cho Trương sư lưu lời gì, nói với bọn họ đúng đấy!”"),i("br"),i("br"),n._v("“Trương sư học sinh?” Đại Dược Vương cùng Lộ quản gia vội vàng ôm quyền."),i("br"),i("br"),n._v("Trương sư thế nhưng là đặc sứ sư thúc tổ, bối phận cực cao, coi như học sinh của hắn tuổi không lớn lắm, hai người cũng không dám thờ ơ."),i("br"),i("br"),n._v("“Đại Dược Vương?”"),i("br"),i("br"),n._v("Nghe xong giới thiệu, Triệu Nhã đám người tất cả đều giật mình: “Hồng Liên thành Đại Dược Vương?”"),i("br"),i("br"),n._v("Đến Thiên Vũ vương quốc trên đường, nghe Lưu sư kỹ càng giới thiệu, biết vị này Đại Dược Vương địa vị cùng quyền thế."),i("br"),i("br"),n._v("Không nghĩ đến người này đối với lão sư cung kính như thế."),i("br"),i("br"),n._v("“Lão sư ra khỏi thành thí luyện, đại khái còn có năm, sáu ngày thời gian mới có thể trở về, không biết Đại Dược Vương có chuyện gì, có thể chuyển cáo chúng ta, chờ lão sư trở về, lại nói cho hắn biết một tiếng!”"),i("br"),i("br"),n._v("Triệu Nhã nói."),i("br"),i("br"),n._v("“A, không có việc gì, liền là vừa vặn đi ngang qua Thiên Vũ vương thành, đến đây bái phỏng một cái!”"),i("br"),i("br"),n._v("Nghe Trương Huyền không tại, Đại Dược Vương hơi có chút thất vọng: “Đã Trương sư không tại, chúng ta liền cáo từ!”"),i("br"),i("br"),n._v("“Trước không vội...”"),i("br"),i("br"),n._v("Viên Đào vội vàng tiến lên, một mặt hiếu kỳ nhìn qua: “Ta nghe nói... Ngươi cùng Độc điện quan hệ mật thiết, có phải hay không trong tay cũng có một chút độc dược?”"),i("br"),i("br"),n._v("“Cái này...” Đại Dược Vương không nghĩ tới hắn hỏi trực tiếp như vậy, xấu hổ nở nụ cười: “Là có một ít!”"),i("br"),i("br"),n._v("“Như vậy, có hay không có thể để cho Thông Huyền cảnh cường giả chịu đau khổ, lại không muốn mạng? Ta muốn mua một chút...”"),i("br"),i("br"),n._v("Viên Đào ánh mắt sáng lên."),i("br"),i("br"),n._v("“Để Thông Huyền cảnh cường giả chịu đau khổ, lại không muốn mạng?”"),i("br"),i("br"),n._v("Đại Dược Vương không biết hắn muốn thứ này làm gì, chần chờ một chút, lập tức gật đầu: “Quả thực có... Nếu như ngươi muốn dùng, cho ngươi chính là, không cần làm phiền! Bất quá, phải cẩn thận, thứ này dược lực cực lớn, coi như Tông sư cường giả không cẩn thận phục dụng, cũng muốn thiệt thòi lớn!”"),i("br"),i("br"),n._v("Nói xong cổ tay khẽ đảo, một cái bình ngọc xuất hiện tại lòng bàn tay."),i("br"),i("br"),n._v("(Canh [4] đến! Nguyệt phiếu cùng phiếu đề cử đâu?)"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-366"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-368"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);