(window.webpackJsonp=window.webpackJsonp||[]).push([[1598],{1954:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-28-phan-no-mac-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-28-phan-no-mac-lao"}},[n._v("#")]),n._v(" Chương 28: Phẫn nộ Mặc lão")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" Vương gia chuyện phát sinh, Trương Huyền không hề cảm kích, rời đi lớp học về sau, lần thứ hai hướng về Tàng Thư Các đi đến."),t("br"),t("br"),n._v(" “Tuy rằng tu vi tiến bộ không ít, nhưng đối với võ kỹ loại hình, vẫn là không hiểu nhiều, nhất định phải dành thời gian nghiên cứu học tập! Còn có tầng thứ nhất, thứ hai công pháp, cũng muốn bắt chước tập một hồi, không phải vậy cho học sinh dạy học, cái gì cũng không hiểu, khẳng định lòi!” "),t("br"),t("br"),n._v(" Trương Huyền đêm hôm qua tu vi liên tiếp đột phá, thực lực là gia tăng rồi không ít, nhưng đối với võ kỹ loại hình hiểu rõ cực nhỏ, hơn nữa, đối với công pháp tầng thứ nhất, tầng thứ hai cũng biết không nhiều, thật giống như ngày hôm nay cho Triệu Nhã giảng bài, liền không biết nên truyền thụ nàng dạng gì công pháp, mới có thể giải quyết Thuần Âm thân thể."),t("br"),t("br"),n._v(" Vừa vặn nhân lúc hiện tại cố gắng nghiên cứu một chút, vì là hạ tiết khóa làm chuẩn bị."),t("br"),t("br"),n._v(" Không phải vậy, những học sinh khác hỏi gì cũng không biết, sớm muộn đều sẽ lòi."),t("br"),t("br"),n._v(" “Mặc lão!” "),t("br"),t("br"),n._v(" Lần thứ hai đi tới Tàng Thư Các, Mặc lão theo cũ canh giữ ở bên ngoài."),t("br"),t("br"),n._v(" “Ngươi tới làm gì?” "),t("br"),t("br"),n._v(" Ngày hôm qua tiểu tử này, tham mộ hư vinh, đi tới liền xoay loạn, để tâm hắn sinh chán ghét ác, nhìn thấy ngày hôm nay lại tới, không nhịn được hơi nhướng mày."),t("br"),t("br"),n._v(" “Ta muốn thấy nhìn võ kỹ loại hình thư tịch!” Trương Huyền khom người."),t("br"),t("br"),n._v(" “Võ kỹ? Tu vi chính là tu luyện chi cơ, không cố gắng tu luyện, một mực cùng người khác học tham mộ hư vinh, hưởng lạc xốc nổi, không việc chính đáng đồ! Tàng Thư Các, ngươi ngày hôm nay cũng đừng đi vào!” "),t("br"),t("br"),n._v(" Mặc lão khoát tay áo một cái."),t("br"),t("br"),n._v(" Dưới cái nhìn của hắn, cái này Trương Huyền tới này căn bản không phải dự định đọc sách, mà là lung tung lật qua, nhớ cái danh mục đặc thù, đến làm bộ bác học."),t("br"),t("br"),n._v(" Dạy dỗ học sinh, tối kỵ chuồn chuồn lướt nước, kiến thức của ngươi đều nửa thùng ầm, truyền thụ học sinh, chẳng phải rất dễ dàng tẩu hỏa nhập ma?"),t("br"),t("br"),n._v(" Cái này Trương Huyền hôm qua tới hai lần, mỗi một lần đều tùy ý xoay loạn thư tịch, lấy cái kia loại tốc độ, đừng nói nhớ kỹ, e sợ tên sách đều không thấy rõ, làm như vậy, không phải tham mộ hư vinh là cái gì?"),t("br"),t("br"),n._v(" Tàng Thư Các là lão sư tìm đọc tư liệu, chỗ học tập, làm sao có khả năng để người như thế đi vào!"),t("br"),t("br"),n._v(" “Ta tham mộ hư vinh, hưởng lạc phù hoa?” Trương Huyền chớp con mắt, tràn đầy mê hoặc."),t("br"),t("br"),n._v(" Này từ nơi nào nói tới?"),t("br"),t("br"),n._v(" Không nhịn được nói: “Mặc lão, ta thực sự là đến học tập, tuyệt không quấy rối...” "),t("br"),t("br"),n._v(" Cộc cộc cộc!"),t("br"),t("br"),n._v(" Đang muốn cẩn thận giải thích, còn chưa kịp nói hết lời, liền nghe đến phía sau liên tiếp tiếng bước chân vang lên, tiếp theo một cái thanh nhã âm thanh vang lên."),t("br"),t("br"),n._v(" “Mặc lão, ta đi vào tuyển vài cuốn sách!” "),t("br"),t("br"),n._v(" Quay đầu nhìn lại, chính là ngày hôm qua đụng với giáo sư nữ thần, Trầm Bích Như!"),t("br"),t("br"),n._v(" Thời khắc này Trầm Bích Như, thân mang nhạt quần áo màu tím, Linh Lung có hứng thú tuyệt mỹ vóc người phối hợp không chút tì vết khuôn mặt, thanh lệ thoát tục, khiến cho nhân sáng mắt lên."),t("br"),t("br"),n._v(" “Há, hóa ra là Trầm lão sư, vào đi thôi!” Thấy là nàng, Mặc lão không có một chút nào ngăn cản."),t("br"),t("br"),n._v(" Trầm Bích Như không riêng gì nhân dung mạo xinh đẹp, càng là trong phòng học có tiếng thiên tài, mới 20 tuổi, dĩ nhiên đạt đến võ giả năm tầng đỉnh cao, cách Ích Huyệt cảnh, cũng chỉ có kém một bước."),t("br"),t("br"),n._v(" “Đa tạ Mặc lão!” U nhã gật đầu, Trầm Bích Như xoay một cái mặt, lúc này mới nhìn thấy đứng ở một bên Trương Huyền, vừa nhìn phía dưới, không nhịn được trong lòng hơi kinh ngạc."),t("br"),t("br"),n._v(" Trước đây Trương Huyền, tuy rằng chưa bao giờ đã nói thích nàng, nhưng vừa thấy được nàng liền mặt đỏ, nói không ra lời, nhăn nhó khác nào cô nương, kẻ ngu đến đâu cũng có thể nhìn ra đầu mối."),t("br"),t("br"),n._v(" Vốn tưởng rằng lần này lại là như thế này, ai biết hắn chỉ liếc mắt nhìn, liền quay đầu đi, trong ánh mắt lại không trước quyến luyến, mà là trong suốt thấy đáy, dường như một vũng sạch sẽ nước suối, thật giống như mỹ mạo của nàng không có một chút nào tác dụng."),t("br"),t("br"),n._v(" Hơn nữa điểm trọng yếu nhất, trước đây Trương Huyền bởi vì thực lực không cao, thầy giáo sát hạch thành tích lại không tốt, nhìn thấy các lão sư khác, đều sẽ có một loại đặc thù tự ti cảm giác, cảm giác kém người một bậc, nhưng bây giờ, đứng tại chỗ, khí Vũ Hiên ngang, tự mang phong độ, đơn thuần khí chất mà nói, cùng trước quả thực như hai người khác nhau."),t("br"),t("br"),n._v(" Kết hợp với ngày hôm qua nhìn thấy thời điểm, liền Thượng Bân cũng dám chính diện phản bác, Trầm Bích Như trong lòng không nhịn được bay lên nghi hoặc, khi nào cái này nhăn nhó, tự ti thiếu niên, biến bộ dáng?"),t("br"),t("br"),n._v(' "Mặc lão, ta cũng là vào xem sách, nàng tại sao có thể vào,'),t("br"),t("br"),n._v(' Ta liền không thể?" Trương Huyền không biết ý nghĩ của đối phương, mà là bất đắc dĩ xua tay.'),t("br"),t("br"),n._v(" Cùng là lão sư, vào xem cái sách đều chịu đến kỳ thị, thực sự là tất cẩu!"),t("br"),t("br"),n._v(" “Trầm lão sư là đi vào học tập, ngươi đi vào làm gì, trong lòng mình rõ ràng! Còn muốn ta nói đi ra à?” Mặc lão hừ lạnh."),t("br"),t("br"),n._v(" “Bên trong đều là thư tịch, ta còn có thể làm gì?” Trương Huyền lắc đầu cười khổ: “Đương nhiên chính là nhìn sách, học tập một hồi kiến thức!” "),t("br"),t("br"),n._v(" “Học tập tri thức? Hừ!” Mặc lão khuôn mặt không thích."),t("br"),t("br"),n._v(" Liên tục lật sách, liền không thèm nhìn, cái này gọi là học tập? Nơi này mỗi một bản công pháp, đều vô cùng trân quý, không thể một lần mà thôi, liền sao chép đều không chép sao lục, làm sao học tập?"),t("br"),t("br"),n._v(" “Mặc lão!” "),t("br"),n._v("  Ngay ở hắn còn muốn tiếp tục giải thích thời điểm, một bên Trầm Bích Như khẽ mỉm cười, khác nào hoa tươi mở ra: “Trương lão sư khả năng thực sự là muốn học tập, ta nhìn nếu không để hắn vào đi thôi! Một mực ngăn, để các lão sư khác nhìn, ảnh hưởng cũng không tốt lắm a!” "),t("br"),t("br"),n._v(" “Ừm! Ta liền xem ở Trầm lão sư tử trên để ngươi đi vào, bất quá, sớm nói cho ngươi, muốn xem sách liền chăm chú nhìn, nếu như ta phát hiện ngươi đi dạo, ở bên trong giả vờ giả vịt, xem ta như thế nào giáo huấn ngươi!” "),t("br"),t("br"),n._v(" Mặc lão vung vung tay."),t("br"),t("br"),n._v(" “Đa tạ!” Trương Huyền gật gù, lần thứ hai đối với Trầm Bích Như liền ôm quyền, nhấc chân hướng về Tàng Thư Các đi đến."),t("br"),t("br"),n._v(" “Ừm?” "),t("br"),t("br"),n._v(" Vốn cho là mình giúp cái tên này, hắn nhất định sẽ nhân cơ hội trò chuyện vài câu, kết quả, xoay người rời đi, Trầm Bích Như hơi kinh ngạc."),t("br"),t("br"),n._v(" Bất quá, lúc này cũng không nói thêm lời, theo sát lấy đi vào Tàng Thư Các."),t("br"),t("br"),n._v(" Nàng giúp Trương Huyền, chỉ là thuận miệng mà vì, cũng không có gì đặc thù nguyên nhân, cũng không nghĩ những chuyện khác, nếu đối phương không nói chuyện cùng nàng, vừa vặn cũng vui vẻ được thanh tịnh."),t("br"),t("br"),n._v(" “Lần này tới Tàng Thư Các đều phiền toái như vậy, lần sau khẳng định không có cách nào đi vào!” "),t("br"),t("br"),n._v(" Đứng ở trước kệ sách, Trương Huyền vẫn chưa sốt ruột đọc sách, mà là vò đầu."),t("br"),t("br"),n._v(" Hắn không biết Mặc lão vì sao cản hắn, nhưng khẳng định cùng hắn thứ nhất đếm ngược thân phận có quan hệ!"),t("br"),t("br"),n._v(" Thân phận này đi tới chỗ nào đều rất lúng túng, không riêng chiêu thu học sinh, liền giữa đồng nghiệp đều có chút kỳ thị, lần này có thể đi vào, là trùng hợp Trầm Bích Như hỗ trợ nói chuyện, lần sau đây?"),t("br"),t("br"),n._v(" “Cái này Tàng Thư Các cũng không phải đặc biệt lớn, nếu như làm nhanh, hay là có thể một ngày xem xong! Như vậy, lần sau cũng sẽ không cần đi vào nữa!” "),t("br"),t("br"),n._v(" Đột nhiên, ánh mắt sáng lên."),t("br"),t("br"),n._v(" Nếu cái này Mặc lão cản hắn, quá mức lần sau không đến chính là."),t("br"),t("br"),n._v(" Lớn như vậy thư viện, tàng thư mấy chục vạn sách, đổi lại người khác, coi như nhìn chết ở này, cũng không được xem mấy quyển, hắn không giống nhau! Bất kỳ thư tịch, chỉ cần lật một cái, liền có thể ở Thiên Đạo Thư Viện tự động hình thành một quyển giống như đúc cũng đánh dấu thiếu hụt!"),t("br"),t("br"),n._v(" Động tác nhanh lời, một Thiên tướng nhiều như vậy thư tịch toàn bộ khắc sâu vào đầu óc, cũng không phải là không được!"),t("br"),t("br"),n._v(" Một khi thành công, liền không cần tiếp tục tới nơi này tìm sách phiền toái như vậy."),t("br"),t("br"),n._v(" “Bắt đầu!” "),t("br"),t("br"),n._v(" Nghĩ đến liền làm, Trương Huyền cũng không phí lời, đi thẳng tới hàng thứ nhất giá sách trước mặt."),t("br"),t("br"),n._v(" Cũng mặc kệ sách gì, tiện tay rút ra mấy quyển, “Rầm!” Lật một chút."),t("br"),t("br"),n._v(" Ào ào ào!"),t("br"),t("br"),n._v(" Cùng dự đoán như thế, Thiên Đạo Thư Viện trong nháy mắt hơi động, trên giá sách xuất hiện cùng trong tay mình giống như đúc thư tịch."),t("br"),t("br"),n._v(" “Hừm, cứ như vậy!” "),t("br"),t("br"),n._v(" Phát hiện đồng thời cầm cẩn thận mấy quyển cũng có thể thành công, hắn cũng không làm lỡ, chậm rãi hướng về phía trước, mỗi quá một chỗ, đều rút ra vài bản, đồng thời khắc sâu vào đầu óc."),t("br"),t("br"),n._v(" Ào ào ào! Ào ào ào!"),t("br"),t("br"),n._v(" Tàng Thư Các rất nhanh vang lên liền chuỗi tiếng bước chân cùng lật sách âm thanh."),t("br"),t("br"),n._v(" “Cái tên này, còn nói không phải... Giả vờ giả vịt, tham mộ hư vinh?” "),t("br"),t("br"),n._v(" Giữ ở ngoài cửa Mặc lão, tuy rằng đem Trương Huyền thả đi vào, theo cũ bất cứ lúc nào quan tâm, trước cái tên này một lần chỉ lấy một quyển lật qua bỏ vào, tuy rằng không chăm chú, chí ít còn có chút dáng dấp, hiện tại ngược lại tốt, một nắm mấy chục bản, tùy ý thao túng, thậm chí ngay cả tên đều không có thấy rõ, lại thả vào."),t("br"),t("br"),n._v(" Làm cái gì?"),t("br"),t("br"),n._v(" Đọc sách vẫn là chọn cải trắng đây?"),t("br"),t("br"),n._v(" Hơn nữa, nhìn một hồi đối phương lật xem thư tịch, càng là từng trận mê muội."),t("br"),t("br"),n._v(" Tử Đằng Căn trồng trọt, Nam Nữ Quan Hệ Giản Yếu, Âm Dương Hòa Hợp Pháp, Bích Thanh Đan luyện chế, luyện khí nên chú ý hạng mục công việc..."),t("br"),t("br"),n._v(" Đa dạng, đủ loại đều có."),t("br"),t("br"),n._v(" Công pháp, võ kỹ, dược liệu đào tạo, đan dược luyện chế... Thậm chí còn có quan hệ nam nữ... Chỉ cần là thư tịch, cái tên này không có không ngã!"),t("br"),t("br"),n._v(" Xích có sở đoản tấc có sở trường, người tinh lực có hạn, không thể không rõ chi tiết đều có thể toàn bộ học được, vì lẽ đó, đọc sách cũng là có lựa chọn, cái tên này cái gì ngoạn ý đều lật, ngươi xác định không phải tới quấy rối?"),t("br"),t("br"),n._v(" “Khẳng định là quấy rối! Chờ hắn đi ra, không phải dạy dỗ một trận không thể!” "),t("br"),t("br"),n._v(" Mặc lão khuôn mặt khó coi, hừ lạnh một tiếng."),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-36"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-38"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);