(window.webpackJsonp=window.webpackJsonp||[]).push([[1599],{1953:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-361-vay-nguoi-thi-mot-cai-la-đuoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-361-vay-nguoi-thi-mot-cai-la-đuoc"}},[n._v("#")]),n._v(" Chương 361: Vậy ngươi thi một cái là được!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Mộc đan sư dẫn đường, thời gian không dài đi tới trước một căn phòng."),t("br"),t("br"),n._v("“Làm phiền thông bẩm một tiếng, đan sư Mộc Dương Phong cầu kiến!”"),t("br"),t("br"),n._v("Mộc đan sư đối với giữ ở ngoài cửa một cái học đồ ôm quyền."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Biết thân phận của hắn, học đồ quay người đi vào, một lát sau một mặt áy náy đi ra: “Lão sư đang cùng quý khách trao đổi sự tình, hiện tại không rảnh, còn xin Mộc đan sư ngày mai lại đến!”"),t("br"),t("br"),n._v("“Ta có chuyện quan trọng, thực sự đợi không được ngày mai...”"),t("br"),t("br"),n._v("Mộc đan sư lắc đầu, nói: “Mong rằng lần nữa hỗ trợ thông truyền, liền nói ta có việc gấp, cần Hồng đan sư hỗ trợ!”"),t("br"),t("br"),n._v("Liễu lão sư đã mở lời, khẳng định cái này tứ phẩm đan phương mười phần trọng yếu, bằng không thì cũng không có khả năng liền mấy ngày cũng chờ không bằng, vô luận như thế nào đều phải giúp hắn hoàn thành."),t("br"),t("br"),n._v("“Cái này... Ta lại đi qua hỏi một chút!” Học đồ gật gật đầu, quay người đi vào."),t("br"),t("br"),n._v("“Các ngươi không đều là luyện đan sư sao? Làm sao...”"),t("br"),t("br"),n._v("Đem đối thoại nhìn ở trong mắt, Trương Huyền không hiểu."),t("br"),t("br"),n._v("Cái này Hồng Vân cùng Mộc Dương Phong đều là luyện đan sư, một cái tam tinh sơ kỳ, một cái nhị tinh đỉnh phong, chênh lệch cũng không lớn, làm sao gặp một lần phiền toái như vậy?"),t("br"),t("br"),n._v("“Hội trưởng niên kỷ đã cao, cơ bản bất quá hỏi trong hội sự tình, ngươi đi tàng thư khố, ta cũng là hao tốn thật lớn công phu! Hiện tại cơ bản đều là Hồng đan sư chưởng quản đại cục, tất cả đan sư đều nghe hắn, cho nên, địa vị tương đối cao...” Mộc đan sư nói."),t("br"),t("br"),n._v("Giải thích một hồi, Trương Huyền tính toán hiểu được."),t("br"),t("br"),n._v("Cái này Hồng Vân đan sư vốn là ngạo khí, nếu không ban đầu ở Thiên Huyền vương quốc, cũng không trở thành thấy đều không có nhìn thấy bản thân, liền vênh váo hung hăng nhục mạ cái khác đan sư."),t("br"),t("br"),n._v("Hiện tại đột phá cửa ải cuối cùng, trở thành tam tinh đan sư, càng thêm đắc ý, cái khác đan sư đừng nói cầu hắn làm việc, muốn gặp một mặt cũng khó khăn."),t("br"),t("br"),n._v("“Liễu lão sư, một lát nữa đến gian phòng, có thể muốn làm phiền ngươi không vội nói chuyện. Đan phương sẽ không dễ dàng cho ngoại nhân nhìn, nếu như nói ngươi muốn, ta sợ chẳng những sẽ bị trực tiếp từ chối không tiếp, còn phiền phức không ít!”"),t("br"),t("br"),n._v("Mộc đan sư bàn giao."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đan phương là luyện đan sư của quý, sẽ không dễ dàng cùng ngoại nhân biểu hiện ra, hắn một cái y sư muốn đan phương, quả thực có chút không ổn."),t("br"),t("br"),n._v("Hai người ở ngoài cửa đợi trọn vẹn hai nén nhang, cũng sắp các không ngừng thời điểm, học đồ lần nữa đi ra."),t("br"),t("br"),n._v("“Mộc đan sư mời đến! Bất quá, chúng ta lão sư chỉ có một chén trà thời gian, một lát nữa còn muốn ra ngoài, cho nên, có chuyện gì, còn xin nhanh lên...”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Mộc đan sư gật gật đầu."),t("br"),t("br"),n._v("Hai người đi vào gian phòng, quả nhiên thấy Hồng Vân đan sư ngồi tại chủ vị, bên cạnh ngồi một thanh niên, đang một mặt lạnh nhạt uống trà."),t("br"),t("br"),n._v("Không phải người khác, chính là trước đó khảo hạch danh sư trước, ái mộ Mạc Vũ, vẫn muốn ở trước mặt hắn biểu hiện ưu việt tính nhất tinh danh sư, Khương đường chủ nhi tử, Khương Thần!"),t("br"),t("br"),n._v("“Mộc đan sư, gấp gáp như vậy tìm ta chuyện gì?”"),t("br"),t("br"),n._v("Nâng chung trà lên, sờ sờ phía trên trôi nổi lá trà, Hồng Vân đan sư thần sắc khoan thai nhìn qua."),t("br"),t("br"),n._v("“Quả thực có việc muốn phiền phức!”"),t("br"),t("br"),n._v("Mộc đan sư một mặt cung kính: “Ta muốn tìm Hồng đan sư hỗ trợ xin cái tứ phẩm đan phương, về phần phí tổn, ta sẽ tự mình gánh chịu!”"),t("br"),t("br"),n._v("“Tứ phẩm đan phương?”"),t("br"),t("br"),n._v('Hồng Vân đan sư nhướng mày: "Ngươi chẳng qua nhị tinh luyện đan sư, muốn cái này làm cái gì?'),t("br"),t("br"),n._v("Mộc đan sư ôm quyền: “Tại hạ có cách dùng khác, có điều, Hồng đan sư yên tâm, sẽ không làm vi phạm công hội quy định sự tình!”"),t("br"),t("br"),n._v("Luyện đan sư có thể xin đan phương, có điều, không cho phép làm ra bán ra, mượn nhờ đan phương tiến hành lừa gạt các loại, trái với đạo đức quy củ sự tình."),t("br"),t("br"),n._v("“Cách dùng khác? Hừ!”"),t("br"),t("br"),n._v("Hồng Vân đan sư đứng dậy, hai tay chắp sau lưng, hai bước đi tới trước mặt, hai mắt như điện: “Thân là luyện đan sư, mơ tưởng xa vời, làm một chút tự mình làm không đến sự tình, không những không thể một tiếng hót lên làm kinh người, sẽ còn mất mặt xấu hổ! Của ngươi luyện đan trình độ mặc dù không tệ, nhị phẩm đỉnh phong đan dược đã là cực hạn, ta nhìn ngươi vẫn là chờ lúc nào có thể luyện ra tam phẩm đan dược, lại xin cái gọi là tứ phẩm đan phương đi!”"),t("br"),t("br"),n._v("Hắn thấy, kẻ trước mắt này muốn tứ phẩm đan phương, khẳng định là muốn thử luyện chế, dự định một tiếng hót lên làm kinh người."),t("br"),t("br"),n._v("Nhưng luyện đan sư đẳng cấp sâm nghiêm, ngươi một cái liền tam phẩm đan dược đều không luyện được người, đi luyện chế tứ phẩm, không phải tự tìm cái chết là làm gì?"),t("br"),t("br"),n._v("“Ta...” Không nghĩ tới đối phương phản ứng như thế lớn, Mộc đan sư hơi đỏ mặt."),t("br"),t("br"),n._v("“Tốt rồi, đi xuống đi!” Hồng Vân đan sư sầm mặt lại, đầu lâu vung lên, một mặt ngạo nghễ."),t("br"),t("br"),n._v("“Liền xin một cái, lại không phức tạp, giúp một chuyện mà thôi...” Mộc đan sư vội nói."),t("br"),t("br"),n._v("Bất kể nói thế nào tất cả mọi người là một cái công hội luyện đan sư, cấp bậc chênh lệch cũng không lớn, tứ phẩm đan phương mặc dù trân quý, chỉ cần nguyện ý trả giá đắt, hỗ trợ xin cũng không có gì."),t("br"),t("br"),n._v("Một tiếng cự tuyệt, còn nói ra lời này, để hắn quá mức khó xử."),t("br"),t("br"),n._v("“Thế nào, lời của ta vẫn không rõ? Tứ tinh đan phương, chỉ bằng ngươi bây giờ luyện đan thuật, còn không có tư cách đi xem!”"),t("br"),t("br"),n._v("Hồng Vân đan sư hất lên ống tay áo: “Ta còn có việc, Mộc đan sư còn xin quay về đi!”"),t("br"),t("br"),n._v("Nói xong không để ý Mộc Dương Phong, mà là một mặt mỉm cười đúng không xa xa Khương Thần liền ôm quyền: “Khương thiếu, Trương sư bên kia làm phiền ngươi phí tâm! Lúc trước được hắn chỉ điểm một lần, được ích lợi không nhỏ, ngươi có thể nhất định phải giúp ta dẫn tiến.”"),t("br"),t("br"),n._v("“Yên tâm, đây là việc nhỏ, Trương sư bá cùng phụ thân ta là sư huynh đệ, thi xong nhị tinh danh sư, ta muốn bái kiến, vẫn là rất đơn giản, chuyện này ta đáp ứng!” Khương Thần thản nhiên nói."),t("br"),t("br"),n._v("Đối với Trương Huyền có địch ý, bởi vì Mạc Vũ, hiện tại biết hắn đối với người sau căn bản không có ý nghĩa, lại thêm lại là sư huynh của phụ thân, tiền đồ vô lượng, trong lòng không những không có địch ý, càng đem trở thành thần tượng."),t("br"),t("br"),n._v("“Vậy liền đa tạ Khương thiếu...”"),t("br"),t("br"),n._v("Nghe đối phương đáp ứng, Hồng Vân đan sư hưng phấn ánh mắt sáng lên, liền vội vàng gật đầu."),t("br"),t("br"),n._v("Thiên Huyền vương quốc cùng vị này Trương sư, tiếp xúc thời gian mặc dù không dài, lại chỉ điểm hắn mở ra luyện đan một cánh cửa, rộng mở trong sáng."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, mới bước vào tam tinh cấp bậc, nếu như có thể lại nghe hắn một tiết khóa, luyện đan thuật tuyệt đối sẽ còn tăng lên, tấn cấp cao hơn cũng chưa biết chừng."),t("br"),t("br"),n._v("“Ừm!” Khương Thần gật gật đầu."),t("br"),t("br"),n._v("Hồng Vân đan sư mặt mũi tràn đầy cao hứng, ngay sau đó nhìn thấy Mộc Dương Phong hai người còn chưa rời đi, sắc mặt lần nữa trầm xuống: “Tại sao còn chưa đi? Chẳng lẽ ta mới vừa nói không đủ rõ ràng?”"),t("br"),n._v("“Hồng đan sư, ta muốn tứ tinh đan phương, thật sự có việc gấp, mong rằng hỗ trợ...”"),t("br"),t("br"),n._v("Mộc Dương Phong lần nữa khẩn cầu."),t("br"),t("br"),n._v("“Làm càn!”"),t("br"),t("br"),n._v("Hồng Vân đan sư lông mày giương lên, không giận tự uy, mang theo ngạo nghễ không thể xâm phạm khí thế: “Mộc đan sư, xin chú ý thân phận của mình! Tứ phẩm đan phương, chỉ có tam tinh luyện đan sư mới có tư cách xin, ta còn có việc, không nguyện ý xin, làm sao... Ngươi chẳng lẽ còn muốn buộc ta đi không được?”"),t("br"),t("br"),n._v("“Thật muốn tứ phẩm đan phương, chờ ngươi khi nào thì thành tam tinh luyện đan sư, bản thân đi xin đi! Lại trong này hung hăng càn quấy, cẩn thận ta lấy phạm thượng tội danh, báo cáo tổng hội!”"),t("br"),t("br"),n._v("Mỗi cái chức nghiệp đều có khắc nghiệt chế độ đẳng cấp, đẳng cấp thấp mạo phạm đẳng cấp cao, một khi báo cáo tổng hội, rất có thể sẽ bị trừng phạt."),t("br"),t("br"),n._v("Thấy đối phương loại thái độ này, Mộc đan sư thân thể run rẩy, tức giận đến sắc mặt tái xanh, đang muốn nói chuyện, liền nghe đến một cái mang theo nghi vấn thanh âm vang lên."),t("br"),t("br"),n._v("“Tứ tinh đan phương... Đạt tới tam tinh luyện đan sư liền có thể xin đúng không?”"),t("br"),t("br"),n._v("Nói chuyện đúng là hắn sau lưng Liễu hội trưởng."),t("br"),t("br"),n._v("“Vâng!” Mộc đan sư gật gật đầu."),t("br"),t("br"),n._v("“Cái kia... Ngươi thi cái tam tinh luyện đan sư không được sao?” Liễu hội trưởng nghi ngờ nhìn qua."),t("br"),t("br"),n._v("Nghe nói như thế, vốn đang tràn đầy tức giận Mộc đan sư lập tức một mặt cổ quái, nhịn không được cười khổ lắc đầu: “Khảo hạch tam tinh luyện đan sư, đầu tiên nếu có thể luyện chế tam phẩm đan dược, ta... Còn kém xa lắm!”"),t("br"),t("br"),n._v("Mặc dù hắn vây ở nhị tinh đỉnh phong không biết đã bao nhiêu năm, có thể khoảng cách có thể luyện chế tam tinh đan dược, còn có rất lớn một khoảng cách, thậm chí y theo thiên phú của hắn, đời này đều chưa hẳn có thể làm được."),t("br"),t("br"),n._v("Đây là người người đều biết sự thật, vị này Liễu hội trưởng lại nói như vậy, để hắn thực sự không biết trả lời như thế nào."),t("br"),t("br"),n._v("“Vô tri!”"),t("br"),t("br"),n._v("“Tam phẩm đan dược, nếu như tốt như vậy luyện chế, toàn bộ công hội cũng sẽ không chỉ có ta và hội trưởng hai vị tam tinh luyện đan sư!”"),t("br"),t("br"),n._v("Nghe được Mộc Dương Phong sau lưng cái này “Học đồ” nói ra như thế vô tri lời nói, Hồng Vân đan sư cùng Khương Thần tất cả đều hơi vung tay cánh tay."),t("br"),t("br"),n._v("Thứ đồ gì!"),t("br"),t("br"),n._v("Còn... Khảo hạch tam tinh luyện đan sư không được sao... Ngươi cho rằng tam tinh đan sư là cà rốt a?"),t("br"),t("br"),n._v("Nói thi liền thi, muốn thi liền thi?"),t("br"),t("br"),n._v("Toàn bộ Thiên Huyền vương quốc luyện đan sư công hội, nhị tinh đỉnh phong luyện đan sư, không dưới năm vị, nhiều năm như vậy, có thể đột phá, cũng chỉ hắn một cái, đủ thấy độ khó!"),t("br"),t("br"),n._v("Vị này Mộc đan sư nếu có thể luyện chế ra tam phẩm đan dược, đã sớm khảo hạch, còn về phần chờ tới bây giờ?"),t("br"),t("br"),n._v("Thật sự là vô tri đến cực điểm!"),t("br"),t("br"),n._v("“Tam phẩm đan dược mà thôi, cái này đơn giản, dựa theo ta trình tự luyện chế, bảo đảm ngươi thành công!” Không để ý tới một bên hừ lạnh hai người, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Vốn còn nghĩ vị này Hồng đan sư có thể giúp đỡ xin, đã không nguyện ý coi như xong."),t("br"),t("br"),n._v("Chỉ cần tam tinh luyện đan sư đều có thể xin, cái kia... Bản thân liền làm ra một cái đến!"),t("br"),t("br"),n._v("Bao nhiêu chút chuyện!"),t("br"),t("br"),n._v("“Đơn giản?”"),t("br"),t("br"),n._v("“Nói khoác không biết ngượng!”"),t("br"),t("br"),n._v("Thấy cái này “Học đồ” càng nói khẩu khí càng lớn, Hồng Vân đan sư sắc mặt tái xanh, suýt chút nữa sắp nổ."),t("br"),t("br"),n._v("Ngươi nơi đó xuất hiện gia hỏa?"),t("br"),t("br"),n._v("Còn đơn giản..."),t("br"),t("br"),n._v("Ngươi biết tam phẩm đan dược là cái gì không? Ngay ở chỗ này khẩu xuất cuồng ngôn!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mộc đan sư chần chờ một chút, một cắn răng: “Tốt!”"),t("br"),t("br"),n._v("Mặc dù không biết trước mắt vị này Liễu lão sư vì sao nói ra lời này, nhưng đường đường Y Sư công hội hội trưởng, không có khả năng ăn nói bừa bãi, đã dám nói, khẳng định có biện pháp."),t("br"),t("br"),n._v("“Vậy cũng chớ chậm trễ công phu, ngay ở chỗ này luyện chế đi!”"),t("br"),t("br"),n._v("Thấy hắn đáp ứng, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("Lúc trước có thể chỉ điểm Mạc Vũ luyện chế ra nhị phẩm đan dược, chỉ điểm một cái nhị tinh đỉnh phong luyện đan sư luyện chế ra tam phẩm đan dược, cũng không phải việc khó gì."),t("br"),t("br"),n._v("Sớm biết đơn giản như vậy, đến tìm cái này Hồng Vân đan sư làm gì, phiền phức không gì sánh được."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết lúc này đã đâm lao phải theo lao, Mộc Dương Phong cũng không do dự nữa, cổ tay khẽ đảo, một cái cự đại đan lô xuất hiện ở đại sảnh."),t("br"),t("br"),n._v("“Được... Ta ngược lại muốn xem xem ngươi có thể luyện chế ra đan dược gì!”"),t("br"),t("br"),n._v("Không nghĩ tới cái này “Học đồ” phách lối như vậy, vậy mà để Mộc Dương Phong trong này trực tiếp luyện chế tam phẩm đan dược, Hồng Vân đan sư khí giận quá mà cười."),t("br"),t("br"),n._v("Gặp qua trang bức, chưa thấy qua giả bộ như vậy!"),t("br"),t("br"),n._v("Ngươi chỉ điểm hắn luyện đan... Còn luyện chế tam phẩm... Ngươi cho rằng ngươi là ai a?"),t("br"),t("br"),n._v("Danh Sư đường Khương đường chủ sao?"),t("br"),t("br"),n._v("Siêu cấp đan dược thiên tài Trương sư sao?"),t("br"),t("br"),n._v("Nói đùa, không biết từ cái kia xuất hiện học đồ, người không lớn, khẩu khí không nhỏ!"),t("br"),t("br"),n._v("Một bên Khương Thần cũng nhướng mày."),t("br"),t("br"),n._v("Thân là danh sư biết luyện đan độ khó, chỉ điểm luyện chế nhị tinh đỉnh phong luyện đan sư, luyện chế tam phẩm đan dược... Đây không phải nằm mơ chứ?"),t("br"),t("br"),n._v("Ngươi nha thật không có bệnh?"),t("br"),t("br"),n._v("(Cuối tháng, khoảng cách tháng này kết thúc chỉ có hơn hai ngày điểm, chúng ta thứ sáu vị trí vẫn là tràn ngập nguy hiểm, khẩn cầu mọi người nguyệt phiếu ủng hộ! Cám ơn!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-369"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-371"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);