(window.webpackJsonp=window.webpackJsonp||[]).push([[1601],{1955:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-363-đai-gioi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-363-đai-gioi"}},[n._v("#")]),n._v(" Chương 363: Đại giới")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Liễu Trình, Liễu hội trưởng?”"),t("br"),t("br"),n._v("Hồng đan sư, Khương Thần đồng thời chấn động, thân thể run lên."),t("br"),t("br"),n._v("Vị này Liễu hội trưởng mặc dù gần nhất danh khí lớn trướng, đuổi sát siêu cấp thiên tài Trương Huyền, có thể hai người đều không có gặp qua chân dung."),t("br"),t("br"),n._v("Vốn là thấy hắn tuổi trẻ lại đi theo Mộc đan sư sau lưng, liền một cách tự nhiên tưởng rằng cái học đồ, thế nào đều không có nghĩ đến, nắm giữ loại thân phận này."),t("br"),t("br"),n._v("Y Sư công hội hội trưởng, địa vị so với luyện đan sư công hội hội trưởng đều không hề yếu!"),t("br"),t("br"),n._v("Coi như Hồng đan sư là tam tinh luyện đan sư, cũng vẫn là kém một mảng lớn."),t("br"),t("br"),n._v("Chỉ là... Hắn không chỉ là tinh thông y đạo sao? Thế nào cũng biết luyện đan, tùy tiện chỉ đạo liền để một cái hai sao luyện đan sư, luyện chế ra tam phẩm đan dược?"),t("br"),t("br"),n._v("“Vô luận y đạo, hay là luyện đan, đều muốn đối với dược tính có tuyệt đối nắm chắc, mới có thể chế biến ra tốt nhất phương thuốc, luyện chế ra tốt nhất dược vật! Ta mặc dù không phải luyện đan sư, không biết luyện đan, lại đối với dược vật dược tính, khống chế thuần thục, bất kỳ cái gì một vị thuốc, tại đan lô bên trong biến hóa, đều nhất thanh nhị sở. Chỉ điểm luyện chế, đừng nói tam phẩm đan dược, coi như tứ phẩm đan dược, luyện chế ra đến cũng không khó!”"),t("br"),t("br"),n._v("Biết bọn hắn ý nghĩ, Trương Huyền nhàn nhạt nhìn ra."),t("br"),t("br"),n._v("“Biết dược tính có thể làm đến điểm ấy?”"),t("br"),t("br"),n._v("Hồng Vân đan sư cảm thấy sắp điên rồi."),t("br"),t("br"),n._v("Cùng một gốc dược liệu, tại đan lô bên trong nhiệt độ không giống, phát huy dược lực không giống, sinh ra phản ứng cũng là không hoàn toàn giống nhau, hơi không may xuất hiện, liền sẽ sai chi ngàn dặm, dẫn đến luyện đan thất bại."),t("br"),t("br"),n._v("Không biết luyện đan, chỉ bằng mượn đối với dược vật dược tính khống chế, liền có thể chỉ điểm người luyện được tam phẩm đan dược... Còn là người sao?"),t("br"),t("br"),n._v("Trước đó vẫn cảm thấy, vị kia Liễu hội trưởng giải quyết mười chín nói nghi vấn khó xử lý tạp chứng, có thể là sớm chuẩn bị, hậu tích bạc phát, bây giờ mới biết... Người ta là thật là có bản lĩnh!"),t("br"),t("br"),n._v("Không nói cái khác, liền phần này đối với dược vật dược tính nhận biết cùng lý giải, liền là Thiên Vũ vương thành bất luận kẻ nào đều không thể so sánh!"),t("br"),t("br"),n._v("Đừng nói hắn, ngay cả bọn hắn hội trưởng, đều kém xa tít tắp!"),t("br"),t("br"),n._v("“Dược vật chi đạo, bác đại tinh thâm, há lại một câu liền có thể nói tận? Luyện đan sư luyện đan, cùng y sư phương thuốc, kỳ thật đều là lợi dụng các loại phối hợp, đem dược hiệu ngưng tụ cùng một chỗ, hoàn mỹ phát huy ra, chỉ cần đối với dược vật rất nhiều, trên thực tế trăm sông đổ về một biển, lý giải lên không khó!”"),t("br"),t("br"),n._v("Nhàn nhạt nhìn qua, Trương Huyền ống tay áo hơi hơi múa, tựa như thế ngoại cao nhân."),t("br"),t("br"),n._v("Hắn nói mặc dù nhịp nhàng ăn khớp, làm cho không người nào có thể phản bác, trên thực tế những vật này, chỉ tồn tại lý luận bên trong."),t("br"),t("br"),n._v("Nói cách khác, nhân loại bình thường căn bản là không có cách thực hiện, dù là lục tinh, thất tinh y sư, đối với dược liệu lý giải rất nhiều, cũng không có khả năng chỉ bằng vào điểm ấy, liền tùy tiện chỉ điểm người luyện thành đan dược."),t("br"),t("br"),n._v("Thật giống như dựa theo nhân loại phát dục kỳ dài ngắn, trên lý luận muốn sống hai trăm tuổi mới tính bình thường, đáng tiếc, cơ hồ không ai có thể làm đến."),t("br"),t("br"),n._v("Lý luận là lý luận, cùng thực tế vẫn là có rất lớn chênh lệch, đan lô chất lượng, hỏa diễm nhiệt độ, luyện đan người tu vi, hoàn cảnh, dược liệu năm, sinh trưởng trạng thái... Đây đều là có thể biến đổi nhân tố, một, hai dạng không quan trọng, đa dạng chung vào một chỗ, liền cơ hồ không có cách nào khống chế."),t("br"),t("br"),n._v("Hắn cũng là mượn thư viện lực lượng mới làm được."),t("br"),t("br"),n._v("“Thụ giáo...”"),t("br"),t("br"),n._v("Biết trong đó độ khó, Hồng đan sư khâm phục không dứt."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền hài lòng gật đầu: “Hắn hiện tại có thể tính tam tinh luyện đan sư? Nhưng có tư cách đi đan phương tường xin đan phương?”"),t("br"),t("br"),n._v("“Mộc đan sư có thể luyện chế tam tinh đan dược, tuy là nhận chỉ điểm của ngươi, nhưng có lần này kinh nghiệm, về sau thành công, hẳn không phải là việc khó, dựa theo quy định, có tư cách đi đan phương tường xin đan phương!”"),t("br"),t("br"),n._v("Chần chừ một lúc một cái, Hồng đan sư nói."),t("br"),t("br"),n._v("Mặc dù lần này Mộc đan sư là tại đối phương chỉ điểm luyện chế thành công, nhưng có lần này kinh nghiệm, chỉ cần thêm chút luyện tập, đơn độc có thể luyện được, đã không phải là việc khó."),t("br"),t("br"),n._v("Nói cách khác, vị này Mộc đan sư, về sau chỉ cần thêm chút luyện tập, trở thành chân chính tam tinh luyện đan sư chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("Loại tình huống này, đi xin đan phương, đã không tính vi quy."),t("br"),t("br"),n._v("“Sớm biết đơn giản như vậy, liền không đến tìm ngươi, phí công chậm trễ thời gian dài như vậy...”"),t("br"),t("br"),n._v("Nghe được xác nhận trả lời, lắc đầu, Trương Huyền cảm thán một câu."),t("br"),t("br"),n._v("Chuyện này là sao, bên ngoài chờ lâu như vậy, cuối cùng còn không nguyện ý, trên thực tế liền luyện cái đan dược sự tình... Phí công chậm trễ công phu!"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Hắn chỉ là thuận miệng nói thầm, có thể Mộc đan sư nghe được trong tai, khóe miệng nhịn không được co lại."),t("br"),t("br"),n._v("Đây cũng quá không nể mặt mũi, tương đương với ở trước mặt đánh mặt."),t("br"),t("br"),n._v("Lặng lẽ hướng Hồng đan sư nhìn lại, quả nhiên thấy hắn da mặt lay động, thân thể run rẩy, suýt chút nữa một ngụm lão huyết phun ra ngoài."),t("br"),t("br"),n._v("Liễu hội trưởng, ngươi là cố ý a?"),t("br"),t("br"),n._v("Đi tới về sau, cố ý không nói lời nào, để cho người ta không biết thân phận, chờ lấy chế giễu..."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Sớm biết ngươi có bản sự này, ta còn làm ra vẻ ah! Lần này tốt rồi, trang bức không thành, ngược lại biến thành ngu xuẩn!"),t("br"),t("br"),n._v("Càng quan trọng hơn là, bởi vậy đắc tội như vậy một thiên tài, về sau coi như muốn cầu người, cũng không có biện pháp!"),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Không để ý tới một mặt buồn bực Hồng Vân đan sư, tại Mộc đan sư dẫn đầu xuống, đi ra đại sảnh, chỉ chốc lát đi tới một cái phòng kín mít."),t("br"),t("br"),n._v("Cùng Y Sư công hội nghi vấn khó xử lý tường đồng dạng, trong phòng không có những vật khác, chỉ có một cái cự đại vách tường."),t("br"),t("br"),n._v("Hồng đan sư, Khương Thần cũng theo sau, muốn nhìn một chút vị này Mộc đan sư đến cùng muốn xin dạng gì đan phương."),t("br"),t("br"),n._v("“Xin đi, muốn tứ phẩm Hàn Âm đan đan phương!”"),t("br"),t("br"),n._v("Trương Huyền truyền âm qua."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Mộc đan sư gật gật đầu, đem đại biểu thân phận luyện đan sư huy chương, khảm nạm ở trên vách tường, triệt để kích hoạt, bàn tay khẽ động, tại đan phương trên tường, viết xuống ba chữ, Hàn Âm đan!"),t("br"),t("br"),n._v("Đan phương tường, dùng rất đơn giản, chỉ cần kích hoạt bức tường, ở phía trên viết lên cần đan phương danh tự là được, không cao hơn quá nhiều, tổng bộ bên kia, liền sẽ đáp ứng."),t("br"),n._v("Thiên Vũ vương quốc công hội, nắm giữ tam tinh đan sư, xin tứ phẩm đan phương, không có cái gì vấn đề, muốn xin Ngũ phẩm, liền không khả năng!"),t("br"),t("br"),n._v("Cái khác công hội cũng là cứ thế mà suy ra."),t("br"),t("br"),n._v("Ông..."),t("br"),t("br"),n._v("Danh tự viết lên đi, một lát sau, mặt tường một hồi rung động, hiện lên một hàng chữ..."),t("br"),t("br"),n._v("Thù lao: Linh thạch ba cái hoặc Dịch Trúc thảo một gốc!"),t("br"),t("br"),n._v("“Đây là... Cần đại giới?” Nhìn thấy hàng chữ này dấu vết, Khương Thần nhịn không được nói."),t("br"),t("br"),n._v("“Ừm, hướng tổng bộ xin đan phương, là cần tốn hao đại giới, nếu không, người người đều xin, tổng bộ còn không bề bộn chết?”"),t("br"),t("br"),n._v("Hồng Vân đan sư lên tiếng, lập tức lộ ra nghi hoặc: “Chỉ là... Cái khác tứ phẩm đan phương, nhiều nhất đều là một viên linh thạch, cái này lại muốn ba cái, hoặc là Dịch Trúc thảo một gốc, đại giới thật là quá lớn đi!”"),t("br"),t("br"),n._v("“Dịch Trúc thảo là cái gì?”"),t("br"),t("br"),n._v("“Là một loại trân quý dược liệu, rất khó tìm, coi như Hiên Viên vương quốc, cũng khó có thể mua được, chớ nói chi là nơi này!”"),t("br"),t("br"),n._v("Hồng Vân đan sư lắc đầu, tràn đầy vẻ tiếc hận: “Cái này đại giới thực sự quá lớn, Mộc đan sư rất khó giao nổi, e là cho dù có xin tư cách, cũng không có tiền mua sắm!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Quả nhiên, cùng hắn đoán đồng dạng, nhìn thấy phía trên chữ, Mộc đan sư sắc mặt tái xanh, trở nên hết sức khó coi."),t("br"),t("br"),n._v("Vốn cho rằng giá cả không cao, hắn hỗ trợ giao bên trên, không tính là gì, có thể ba cái linh thạch... Thực sự quá mắc, hắn tuy là nhị tinh đỉnh phong luyện đan sư, cũng không bỏ ra nổi tới."),t("br"),t("br"),n._v("Về phần Dịch Trúc thảo, chỉ nghe qua nghe đồn, tìm cũng không tìm tới... Thì càng không cần nói."),t("br"),t("br"),n._v("“Liễu hội trưởng, thực sự thật có lỗi...”"),t("br"),t("br"),n._v("Quay đầu lại, Mộc đan sư một mặt áy náy."),t("br"),t("br"),n._v("Đối phương đến luyện đan sư công hội nghĩ biện pháp giúp nàng nữ nhi tăng thực lực lên, vốn cho rằng nhờ vào thân phận của hắn, có thể để cho hỗ trợ, kết quả, chuyện gì đều không có giúp thành."),t("br"),t("br"),n._v("“Đây là muốn thanh toán thù lao? Linh thạch thế nào cho?”"),t("br"),t("br"),n._v("Cũng kịp phản ứng, Trương Huyền tràn đầy kỳ quái."),t("br"),t("br"),n._v("Đan phương là tin tức, có thể thông qua đan phương tường truyền lại, linh thạch hoặc là Dịch Trúc thảo, đều là thực thể, thế nào đưa qua làm cho đối phương xác nhận?"),t("br"),t("br"),n._v("“Rất đơn giản, chỉ cần đáp ứng thanh toán, trong nửa tháng sẽ có người đặc biệt tới nhận lấy, chỉ cần xác nhận đồ vật không sai, liền sẽ truyền đến đan phương!” Mộc đan sư nói."),t("br"),t("br"),n._v("“Nửa tháng?” Trương Huyền im lặng."),t("br"),t("br"),n._v("Vậy cái này cùng trực tiếp mua sắm tứ tinh đan dược khác nhau ở chỗ nào?"),t("br"),t("br"),n._v("Mua sắm đan dược, liền cần tốn hao thời gian nửa tháng, bởi vì chờ không được, mới muốn cầu truyền lại đan phương, hiện tại ngược lại tốt, làm ra làm đi, lại vòng trở về."),t("br"),t("br"),n._v("“Muốn đối phương lập tức liền cho đan phương, cũng được, liền là để huy chương một mực để ở chỗ này, không thu hồi! Cứ như vậy, tương đương với lấy chính mình chức nghiệp cuộc đời cam đoan nhất định sẽ thanh toán. Đối phương truyền đến đan phương về sau, đồng dạng trong nửa tháng phái người đến đây nhận lấy thù lao...”"),t("br"),t("br"),n._v("Mộc đan sư chần chờ một chút: “Dùng ta huy chương thế chấp không có gì, chỉ là... Ta thực sự không bỏ ra nổi những vật này, một khi đối phương đến muốn... Vậy liền không có biện pháp!”"),t("br"),t("br"),n._v("Nếu như hắn có thể xuất ra ba cái linh thạch, thế chấp huy chương cũng không tính là gì, có thể... Trong tay không có, đối phương một khi tới lấy, thanh toán không lên, phiền phức liền lớn."),t("br"),t("br"),n._v("Chờ tại những năm này vất vả không công chôn vùi, nhị tinh đỉnh phong luyện đan sư chức nghiệp cũng biết bởi vậy mất đi."),t("br"),t("br"),n._v("“Một cái tứ phẩm đan phương mà thôi... Quá mắc đi!”"),t("br"),t("br"),n._v("Hiểu rõ chuyện gì xảy ra, Trương Huyền một mặt buồn bực."),t("br"),t("br"),n._v("Linh thạch thứ này, hắn còn không biết hỏi ai muốn đâu, một cái ba cái... Không nói trước không có, cho dù có, cũng muốn tự mình tu luyện, dùng để mua sắm một cái đan phương, nói đùa cái gì!"),t("br"),t("br"),n._v("“Chẳng lẽ không có những biện pháp khác?”"),t("br"),t("br"),n._v("Nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Loại trừ vật thật thanh toán ở ngoài, còn có thể tri thức, tin tức thanh toán, chỉ cần có thể xuất ra đối phương cho rằng vật có giá trị, đồng dạng có thể coi như thù lao!”"),t("br"),t("br"),n._v("Mộc đan sư cười khổ lắc đầu: “Chỉ là... Đan phương tường đối diện, cơ bản đều là tứ tinh, ngũ tinh luyện đan sư, chúng ta còn kém rất rất xa, coi như biết một chút tri thức, tin tức cũng là không có giá trị gì!”"),t("br"),t("br"),n._v("Đan phương tường đối diện kém cỏi nhất đều là tứ tinh luyện đan sư, loại cấp bậc này người, đối với đan thuật lý giải đã đạt tới cao thâm mạt trắc tình trạng. Hắn loại này nhị tinh luyện đan sư, sao có thể cầm được nhượng lại bọn hắn cảm thấy hứng thú tin tức hoặc là tri thức!"),t("br"),t("br"),n._v("“Còn có thể dùng cái này thanh toán?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt một chút, lập tức ánh mắt sáng lên: “Thật?”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, Mộc đan sư đám người sững sờ."),t("br"),t("br"),n._v("Chẳng lẽ hắn một cái y sư, một vị người ngoài nghề, có thể xuất ra tứ tinh luyện đan sư cảm thấy hứng thú tin tức?"),t("br"),t("br"),n._v("“Vậy thì tốt, ta tới đi!”"),t("br"),t("br"),n._v("Không để ý tới, mọi người giật mình, Trương Huyền suy nghĩ một chút, cầm lấy bút lông đi tới đan phương tường trước, múa bút vẩy mực, viết một hàng chữ."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Nhìn thấy hắn viết nội dung, Hồng Vân đan sư một cái lảo đảo, suýt chút nữa không có thổ huyết ngất đi."),t("br"),t("br"),n._v("Mộc đan sư càng là thân thể nhoáng một cái, nước mắt chảy ra."),t("br"),t("br"),n._v("Đây là dùng hắn huy chương kích hoạt, đối diện có tên của hắn, Liễu hội trưởng, ngươi viết cái đồ chơi này..."),t("br"),t("br"),n._v("Là giết chết ta nha..."),t("br"),t("br"),n._v("Ô ô, ta không mang theo như thế hố người..."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-371"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-373"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);