(window.webpackJsonp=window.webpackJsonp||[]).push([[1602],{1957:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-364-thu-lao-ba-cai-linh-thach-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-364-thu-lao-ba-cai-linh-thach-thuong"}},[n._v("#")]),n._v(" Chương 364: Thù lao ba cái linh thạch (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Kỳ Thánh Vũ là vạn quốc liên minh một vị tứ tinh trung kỳ luyện đan sư, làm người khắc nghiệt, làm việc kỹ lưỡng."),t("br"),t("br"),n._v("Luôn luôn do hắn chịu trách nhiệm chiếu khán đan phương tường."),t("br"),t("br"),n._v("Đan phương tường, liên thông cấp dưới rất nhiều nhất đẳng vương quốc, dùng để giải quyết những này công hội vấn đề xuất hiện, bình thường quanh năm suốt tháng, cũng sẽ không có chuyện gì, không nghĩ tới hôm nay mới đi đến trước mặt, liền bị người kích hoạt."),t("br"),t("br"),n._v("“Là Thiên Vũ nhất đẳng vương quốc?”"),t("br"),t("br"),n._v("Mặt tường kích hoạt, cho thấy vị trí đối diện cùng kích hoạt người là ai."),t("br"),t("br"),n._v("“Mộc Dương Phong?”"),t("br"),t("br"),n._v("Tiện tay lật ra liên quan tới luyện đan sư danh sách, nhìn thoáng qua, nhướng mày: “Nhị tinh đỉnh phong luyện đan sư? Xem ra hẳn là khảo hạch tam tinh thành công, còn chưa kịp thay đổi huy chương! Bất quá, vừa mới thi xong, liền kích hoạt đan phương tường, xin tứ phẩm đan phương, không khỏi quá nóng nảy đi!”"),t("br"),t("br"),n._v("Sắc mặt tái xanh, hai mắt như điện."),t("br"),t("br"),n._v("Đối với khát vọng học tập luyện đan sư, Kỳ Thánh Vũ cũng không bài xích, nhưng tối thiểu cũng muốn đạt tới tam tinh đỉnh phong, xin tứ phẩm đan phương, lục lọi luyện chế."),t("br"),t("br"),n._v("Ngươi một cái vừa mới đột phá gia hỏa, liền huy chương đều không có đổi, liền chạy tới xin, nói đùa cái gì?"),t("br"),t("br"),n._v("“Vẫn là tứ phẩm trung kỳ Hàn Âm đan đan phương!”"),t("br"),t("br"),n._v("Xem hết đối diện truyền đến đan phương danh tự, càng thêm phẫn nộ."),t("br"),t("br"),n._v("Mới vừa khảo hạch thành công tam tinh luyện đan sư, tam phẩm đan dược đều chưa hẳn có thể nhiều lần luyện chế thành công, trực tiếp xin tứ phẩm trung kỳ đan phương..."),t("br"),t("br"),n._v("Chẳng lẽ hiện tại đan sư, đều như thế táo bạo sao?"),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, tiện tay viết ra giao dịch thù lao."),t("br"),t("br"),n._v("Muốn tứ phẩm đan phương cũng được, cầm được ra thù lao lại nói, không bỏ ra nổi, liền ngoan ngoãn, bớt ở chỗ này mơ tưởng xa vời."),t("br"),t("br"),n._v("Tại giá cả bên trong, cố ý chỉnh cả gấp ba, mục đích đúng là cho đối phương đề tỉnh một câu, làm cho đối phương biết khó mà lui."),t("br"),t("br"),n._v("Quả nhiên, viết xong tin tức, đan phương tường yên tĩnh lại, không có động tĩnh."),t("br"),t("br"),n._v("“Xem ra đối phó những này vô tri gia hỏa, nên để hắn biết khó mà lui...”"),t("br"),t("br"),n._v("Nói thầm một tiếng, tiện tay đổ đầy một chén nước trà, Kỳ Thánh Vũ nhấp một miếng."),t("br"),t("br"),n._v("Ngay tại hắn cho rằng đối phương cảm thấy khó mà thanh toán, hành quân lặng lẽ thời điểm, đột nhiên quang mang Nhất Thiểm, một nhóm chữ chậm rãi hiện lên."),t("br"),t("br"),n._v("“Linh thạch không có, có thể giúp ngươi giải đáp vấn đề, có cái gì nghi vấn khó xử lý, không hiểu, có thể hỏi thăm!”"),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Một miệng nước trà phun tới."),t("br"),t("br"),n._v("Kỳ Thánh Vũ con mắt trợn tròn, không ngừng ho khan."),t("br"),t("br"),n._v("Hỏi thăm?"),t("br"),t("br"),n._v("Giúp ta giải đáp?"),t("br"),t("br"),n._v("Kỳ Thánh Vũ chỉ cảm thấy trên đầu có một vạn đầu Thần Thú bay qua."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Lão tử là tứ tinh trung kỳ luyện đan sư, có cái gì nghi vấn khó xử lý, không hiểu muốn hỏi, cũng phải hỏi ngũ tinh luyện đan sư, ngươi một cái vừa mới đạt tới tam tinh gia hỏa, khẩu khí như thế cuồng, thế nào không sợ bị sét đánh chết?"),t("br"),t("br"),n._v("Vốn đang cho rằng, gấp ba giá cả sẽ để cho hắn biết khó mà lui, không nghĩ tới trả lời cuồng vọng như vậy, tức giận đến hắn đều muốn điên rồi."),t("br"),t("br"),n._v("“Kỳ đan sư? Làm sao vậy?”"),t("br"),t("br"),n._v("Ngay tại nổi giận, dự định hảo hảo giáo huấn một cái cái này không biết tôn ti gia hỏa, gian phòng “Kẹt kẹt!” Một tiếng, một cái lão giả đi tới."),t("br"),t("br"),n._v("Vạn quốc liên minh luyện đan sư công hội hội trưởng, tô để lọt!"),t("br"),t("br"),n._v("Vương quốc cảnh nội, một vị duy nhất tứ tinh đỉnh phong luyện đan sư!"),t("br"),t("br"),n._v("“Không có gì, liền là Thiên Vũ vương quốc ra một cái người ngông cuồng!” Cố nén tức giận, Kỳ Thánh Vũ ngâm nga đến."),t("br"),t("br"),n._v("“Người ngông cuồng?”"),t("br"),t("br"),n._v("Tô hội trưởng sửng sốt một chút, rất nhanh biết rõ ràng chuyện gì xảy ra, cũng mở to hai mắt nhìn, nhìn trước mắt đan phương tường, giống như là nhìn xem một đầu quái vật."),t("br"),t("br"),n._v("Em gái ngươi!"),t("br"),t("br"),n._v("Một cái nhị tinh đỉnh phong, nhiều nhất mới vừa đột phá gia hỏa, lấy lão sư giọng điệu, để tứ tinh trung kỳ đan sư tùy tiện trưng cầu ý kiến, hỏi thăm?"),t("br"),t("br"),n._v("Đây cũng không phải là người ngông cuồng, mà là đầu óc có vấn đề!"),t("br"),t("br"),n._v("Dù sao, chỉ cần hơi người bình thường, đều khó có khả năng nói lời này!"),t("br"),t("br"),n._v("Khó trách Kỳ Thánh Vũ nổi điên, liền xem như hắn mới vừa nhìn thấy, cũng cảm thấy chóng mặt, cảm thấy vị này Mộc Dương Phong đan sư, có phải hay không cái nào gân rối loạn, hóng gió."),t("br"),t("br"),n._v("“Đem hắn tư liệu cho ta xem một chút...”"),t("br"),t("br"),n._v("Vương quốc liên minh luyện đan sư công hội, làm khống chế nhất đẳng vương quốc công hội đầu mối then chốt, chỉ cần có luyện đan sư khảo hạch, cũng sẽ ở nơi này báo cáo chuẩn bị, tất cả mọi người tư liệu, cơ bản đều có thể tra được."),t("br"),t("br"),n._v("Tiếp nhận tư liệu, hoàn chỉnh nhìn một lần, gia hỏa này... Không có biểu hiện quá nhiều lợi hại thiên phú, chỉ có thể nói thượng trung dung."),t("br"),t("br"),n._v("Đạt tới nhị tinh đỉnh phong đến bây giờ đã trọn vẹn mười bảy năm, không có bất kỳ cái gì đột phá, thế nào vừa đột phá, giống như này tùy tiện?"),t("br"),t("br"),n._v("Đầu óc là bị cửa chen lấn, vẫn là bị cái gì man thú đá?"),t("br"),t("br"),n._v("Thức tỉnh nháy con mắt."),t("br"),t("br"),n._v("“Hội trưởng, làm sao bây giờ?”"),t("br"),t("br"),n._v("Kỳ Thánh Vũ khẽ nói: “Có cần hay không cho gia hỏa này kế tiếp không nhìn đẳng cấp tội danh, hủy bỏ luyện đan sư tư chất?”"),t("br"),t("br"),n._v("Đại lục ở bên trên, trung, xuống chức nghiệp đông đảo, đẳng cấp sâm nghiêm, cấp thấp chức nghiệp muốn tôn trọng cao đẳng chức nghiệp, lấy sư trưởng chi lễ đối đãi, đây là quy định."),t("br"),t("br"),n._v("Gia hỏa này như thế tùy tiện, phách lối không ai bì nổi, hoàn toàn có thể định một cái không tôn trọng đẳng cấp tội danh, trực tiếp hủy bỏ luyện đan sư tư cách."),t("br"),t("br"),n._v("“Trước không vội, không bằng trước cho hắn cái vấn đề thử một chút, không đáp lại được lại trị tội cũng được!” Chần chờ một chút, Tô hội trưởng nói."),t("br"),t("br"),n._v("Kỳ hoa mỗi năm có, cũng không thể đối phương nói chuyện cuồng vọng, liền muốn khai trừ hắn luyện đan sư tư cách đi."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Kỳ Thánh Vũ gật gật đầu: “Vừa vặn ta có một vấn đề, trước đó liền muốn thỉnh giáo hội trưởng, một mực không có cơ hội. Hiện tại liền viết lên, hội trưởng cũng giúp ta nhìn xem, hắn giải thích như thế nào! Chỉ cần là lung tung giải đáp, liền để hắn biết, cao cấp luyện đan sư quyền uy không cho khiêu khích!”"),t("br"),t("br"),n._v("“Ừm!” Thức tỉnh hội trưởng gật đầu."),t("br"),t("br"),n._v("Thấy hắn đồng ý, Kỳ Thánh Vũ đem bút lông cầm lấy, tiện tay viết lên đi."),t("br"),n._v("“Luyện chế , Tử Châu thảo, Dậu Dương hoa, Tam Diệp thanh ba loại dược liệu dung hợp, trình tự nghiêm ngặt dựa theo quy định, tại sao lại dẫn đến dược tính xói mòn!”"),t("br"),t("br"),n._v("Nhìn thấy hắn viết hàng chữ này, thức tỉnh nhẹ gật đầu."),t("br"),t("br"),n._v("Hắn vấn đề này rất là xảo trá, Khuê Hải đan là tứ phẩm đan dược, viết ba vị dược tài dung hợp thời điểm, quả thực sẽ dẫn đến dược tính xói mòn, không ít tứ tinh luyện đan sư, đều sẽ phạm loại này sai lầm."),t("br"),t("br"),n._v("Coi như hắn năm đó cũng chưa từng ngoại lệ."),t("br"),t("br"),n._v("Về sau vẫn là nhiều mặt nghe ngóng, lục lọi hồi lâu, mới tìm được chính xác giải pháp."),t("br"),t("br"),n._v("Đem vấn đề này hỏi ra, xem ra Kỳ Thánh Vũ là thật tức giận, trực tiếp hỏi một cái liền hắn chính mình đều khó mà giải đáp vấn đề."),t("br"),t("br"),n._v("“Cái vấn đề này phương pháp giải quyết, coi như một chút ngũ tinh luyện đan sư cũng rất khó trả lời, ta cũng là vận khí tốt, mới tại một bản trong cổ tịch tìm được đáp án, một cái mới vừa đột phá tam tinh luyện đan sư, làm sao có thể trả lời...”"),t("br"),t("br"),n._v("Trong lòng hừ lạnh, nhìn về phía trước mắt đan phương tường, muốn nhìn một chút tên lớn lối này, đến cùng có thể trả lời cái gì nội dung."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Đối diện."),t("br"),t("br"),n._v("Không chỉ Mộc đan sư phát điên, ngay cả Khương Thần, Hồng Vân đan sư cũng lông mày nhảy loạn."),t("br"),t("br"),n._v("Đây cũng không phải là quấy rối, mà là không nhìn luyện đan sư đẳng cấp uy nghiêm, khiêu chiến quyền uy!"),t("br"),t("br"),n._v("Một khi đối phương ngồi vững tội danh, hoàn toàn có thể trực tiếp khai trừ Mộc Dương Phong luyện đan sư tư cách!"),t("br"),t("br"),n._v("“Đoán chừng... Đối diện tiền bối bị chọc tức!”"),t("br"),t("br"),n._v("Khóe miệng giật một cái, Hồng Vân đan sư đối với Mộc Dương Phong tràn đầy đồng tình."),t("br"),t("br"),n._v("Đoán chừng gia hỏa này chức nghiệp cuộc đời sắp xong rồi..."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Ngay tại lắc đầu, mặt tường Nhất Thiểm, một nhóm chữ xuất hiện."),t("br"),t("br"),n._v("“Khuê Hải đan?”"),t("br"),t("br"),n._v("“Tử Châu thảo, Dậu Dương hoa, Tam Diệp thanh?”"),t("br"),t("br"),n._v("Xem hết nội dung, Hồng Vân đan sư đầu óc mơ hồ."),t("br"),t("br"),n._v("Nói thật, hắn liền Khuê Hải đan là cái gì cũng không biết, chớ nói chi là cái này ba loại dược liệu tại sao lại dược tính trôi mất."),t("br"),t("br"),n._v("Vụng trộm nhìn về phía Mộc Dương Phong, chỉ thấy hắn càng là mặt mũi tràn đầy run rẩy, theo táo bón một bộ dáng."),t("br"),t("br"),n._v("Lần nữa nhìn về phía kẻ cầm đầu Liễu Trình."),t("br"),t("br"),n._v("Gia hỏa này cũng là một mặt mờ mịt, tựa hồ hoàn toàn không hiểu, đang kỳ quái kết cuộc như thế nào, chỉ thấy hắn đứng dậy, đưa tay tại đan phương trên tường vuốt một cái..."),t("br"),t("br"),n._v("Ngay sau đó, cầm lấy bút lông, viết lên đi."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Tử Châu thảo phơi khô ba ngày, Dậu Dương hoa bỏ rễ, Tam Diệp thanh lấy năm năm trong vòng, niên đại không đổi quá lớn!”"),t("br"),t("br"),n._v("Nhìn xem đan phương tường rất nhanh hiện ra chữ viết, Kỳ Thánh Vũ khí bỗng nhiên đứng lên, lửa giận sắp từ miệng mũi phun ra."),t("br"),t("br"),n._v("“Nói càn, quả thực liền là nói càn!”"),t("br"),t("br"),n._v("“Tử Châu thảo trời sinh tính âm hàn, sinh trưởng tại chỗ râm mát, một khi phơi khô, dược hiệu liền sẽ nhanh chóng xói mòn, cuối cùng dẫn đến không thấy, thật muốn đem loại dược liệu này ném đi, đừng nói ba ngày, một ngày đều không chịu nổi!”"),t("br"),t("br"),n._v("“Dậu Dương hoa, giờ Dậu nắng chiều chìm thời điểm, mới có thể nở rộ, đóa hoa kiều diễm, mang theo Thiên hỏa chìm, địa hỏa khôi phục mới có dược tính, nhất là sinh trưởng ở trong đất bùn rễ cây, là liên tiếp thiên địa cầu nối, một khi bỏ rễ, còn có cái gì hiệu quả?”"),t("br"),t("br"),n._v("“Về phần sau cùng Tam Diệp thanh, bình thường thành thục, cần thời gian mười năm, trong vòng năm năm dược liệu, cùng phổ thông hoa cỏ đồng dạng, không có chút nào dược tính...”"),t("br"),t("br"),n._v("“Thật dựa theo nói như vậy đi làm, đừng nói xói mòn, có lẽ một điểm dược tính đều không có...”"),t("br"),t("br"),n._v("Càng nói càng nhanh, Kỳ Thánh Vũ nắm đấm niết khanh khách rung động."),t("br"),t("br"),n._v("Chỉ cần là tứ phẩm luyện đan sư, đối với Khuê Hải đan có hiểu biết, liền biết cái này ba loại chủ dược dược tính."),t("br"),t("br"),n._v("Đối phương nói những này, quả thực liền là nói đùa, nói bậy tám đạo!"),t("br"),t("br"),n._v("“Hội trưởng, không cần chần chờ, gia hỏa này tuyệt đối đầu óc có bệnh, ta xin hiện tại liền khai trừ hắn luyện đan sư tư cách...”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, Kỳ Thánh Vũ quay đầu đi, đang muốn từ Tô hội trưởng trong miệng nghe được quyết định, lập tức nhìn thấy luôn luôn trầm ổn hội trưởng, con mắt trừng lớn, choáng váng đồng dạng đứng tại chỗ."),t("br"),t("br"),n._v("“Hội trưởng...”"),t("br"),t("br"),n._v("Nhịn không được hô một tiếng."),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Thức tỉnh lúc này mới kịp phản ứng, bờ môi run rẩy: “Cái này... Phương pháp của hắn, một điểm không sai, hoàn toàn chính xác!”"),t("br"),t("br"),n._v("“Chính xác?”"),t("br"),t("br"),n._v("Lần này luân Kỳ Thánh Vũ choáng váng."),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("“Tử Châu thảo thích âm sợ dương, trong cơ thể tích lũy cực kỳ dày nặng khí âm hàn, luyện chế Khuê Hải đan thời điểm, cỗ này âm khí dung hợp tại dược liệu bên trong, liền sẽ ảnh hưởng dược tính phát huy, quả thực cần phơi khô mới có thể giải quyết!”"),t("br"),t("br"),n._v("“Về phần Dậu Dương hoa, là làm trung hoà dược liệu sử dụng, tăng thêm rễ cây, mặc dù dược lực gia tăng, trên thực tế lại hăng quá hoá dở, ảnh hưởng Tử Châu thảo dược tính phát huy, dẫn đến chỉnh thể dược tính xói mòn, Tam Diệp thanh cũng là như thế...”"),t("br"),t("br"),n._v("Thức tỉnh há to miệng: “Đây là ta từ một bản cổ tịch trên nhìn thấy, không ít ngũ tinh luyện đan sư đều khó mà giải đáp... Hắn thế nào biết?”"),t("br"),t("br"),n._v("Kỳ Thánh Vũ mộng: “Hội trưởng có ý tứ là nói hắn... Không sai?”"),t("br"),t("br"),n._v("“Không sai, không những không sai, còn hết sức chính xác, ta chuyên môn nghiệm chứng qua, không có vấn đề gì!”"),t("br"),t("br"),n._v("Thức tỉnh gật đầu."),t("br"),t("br"),n._v("“Cái này... Làm sao có thể?”"),t("br"),t("br"),n._v("Kỳ Thánh Vũ điên rồi."),t("br"),t("br"),n._v("Vốn cho rằng đối phương chỉ là cái mới vừa khảo hạch thành công tam tinh luyện đan sư, khẩu xuất cuồng ngôn, thế nào đều không có nghĩ đến, liền hắn cũng sẽ không luyện đan nan đề, tuỳ tiện giải quyết..."),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("Đến cùng là hắn là tứ tinh trung kỳ luyện đan sư, vẫn là chính mình?"),t("br"),t("br"),n._v("(Thức tỉnh, Kỳ Thánh Vũ, công chúng số báo danh diễn viên quần chúng, lão nhai trước viết ra. Nếu như còn muốn, mọi người có thể chú ý nhắn lại ah! (Wechat lục soát “h Sty Sd S”, chú ý là được!) )"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-372"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-374"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);