(window.webpackJsonp=window.webpackJsonp||[]).push([[1604],{1960:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-366-lieu-mang-lo-xung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-366-lieu-mang-lo-xung"}},[n._v("#")]),n._v(" Chương 366: Liều mạng Lộ Xung")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Nghe được những dược liệu này giá cả, Trương Huyền nội tâm là sụp đổ."),t("br"),t("br"),n._v("Phế đi một đêm miệng lưỡi, làm ba cái linh thạch, kết quả mới đủ mua dược tài..."),t("br"),t("br"),n._v("Các ngươi đây là muốn đoạt ah!"),t("br"),t("br"),n._v("Ta kiếm chút linh thạch dễ dàng sao? Muốn hay không ác như vậy?"),t("br"),t("br"),n._v("Cầm bút lông, chờ nửa ngày, đối diện đều không có truyền lại tin tức tới, rất rõ ràng... Không có vấn đề."),t("br"),t("br"),n._v("“Thật keo kiệt! Còn tứ tinh luyện đan sư đâu, chút tiền lẻ này đều không muốn ra, quỷ nghèo!”"),t("br"),t("br"),n._v("Đem bút lông quăng ra, Trương Huyền lên tiếng gào thét."),t("br"),t("br"),n._v("Cái gì đồ chơi sao!"),t("br"),t("br"),n._v("Không nói là tứ tinh luyện đan sư sao? Không phải cái gì vạn quốc liên minh đại nhân vật sao? Liền ba cái linh thạch đều không bỏ được ra... Liền chưa thấy qua như thế keo kiệt..."),t("br"),t("br"),n._v("“Quỷ nghèo...”"),t("br"),t("br"),n._v("Mộc Dương Phong, Hồng Vân đan sư run rẩy."),t("br"),t("br"),n._v("Đại ca, rõ ràng là ngươi keo kiệt có được hay không?"),t("br"),t("br"),n._v("Người ta không có vấn đề, cũng không thể không công hướng ngươi tiễn linh thạch đi!"),t("br"),t("br"),n._v("Trực tiếp mắng tứ tinh luyện đan sư keo kiệt, quỷ nghèo... Đừng nói toàn bộ công hội, có lẽ toàn bộ vương quốc phạm vi, cũng là ngươi một phần..."),t("br"),t("br"),n._v("“Liễu hội trưởng, tổng hội bên kia linh thạch tới, coi như mua dược liệu, ta hiện tại liền chuẩn bị cho ngươi...”"),t("br"),t("br"),n._v("Hô một tiếng, Mộc Dương Phong sợ đối phương lại làm ra cái gì quá kích sự tình, xoay người rời đi."),t("br"),t("br"),n._v("Trước mắt vị này, quả thực có đại bản lĩnh, nhưng lá gan cũng thực sự quá lớn."),t("br"),t("br"),n._v("Đổi lại hắn, cho dù có chút năng lực, đối mặt đẳng cấp cao hơn chính mình luyện đan sư, cũng biết lễ độ cung kính, hắn ngược lại tốt, một điểm không hài lòng, chửi ầm lên..."),t("br"),t("br"),n._v("Nếu như tiếp tục đợi ở chỗ này, thật lo lắng có thể hay không bị dọa chết tươi."),t("br"),t("br"),n._v("“Chúng ta...”"),t("br"),t("br"),n._v("Hồng Vân đan sư, Khương Thần đang muốn rời đi, chỉ thấy thanh niên một mặt ý cười nhìn qua, hai mắt tỏa ánh sáng: “Bọn hắn là quỷ nghèo, nghe nói đòi tiền, dọa đến liền hỏi đề cũng không có... Các ngươi hai cái có hay không? Vấn đề gì đều được, chỉ cần cho linh thạch, lại khó đều có thể hỗ trợ giải quyết...”"),t("br"),t("br"),n._v("“Không có...”"),t("br"),t("br"),n._v("Hai người dọa đến sắc mặt trắng nhợt, đồng thời khoát tay."),t("br"),t("br"),n._v("“Không có vấn đề? Cái kia... Có bệnh cũng được, ta có thể cho các ngươi trị liệu...” Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Hồng Vân đan sư, Khương Thần."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không hổ là luyện đan sư công hội, dược liệu sưu tập tốc độ cực nhanh, không đến nửa canh giờ, luyện chế Hàn Âm đan tất cả dược liệu liền sưu tập hoàn chỉnh, mang theo tới."),t("br"),t("br"),n._v("Liên tục xác định hai người không chữa bệnh, cũng không thành vấn đề, Trương Huyền có chút ít tiếc nuối theo sau lưng Mộc Dương Phong, đi tới công hội chuyên dụng luyện đan thất."),t("br"),t("br"),n._v("Nơi này có tốt nhất lô đỉnh, chung quanh cũng bố trí trận pháp, coi như nổ lô, cũng không biết ảnh hưởng công hội vận hành bình thường."),t("br"),t("br"),n._v("“Liễu hội trưởng, ngươi... Thật để cho ta luyện chế?”"),t("br"),t("br"),n._v("Nhìn trước mắt dọn xong dược liệu cùng đan lô, Mộc đan sư mặt mũi tràn đầy chần chờ."),t("br"),t("br"),n._v("Tứ phẩm đan dược, lại được xưng là linh phẩm đan dược, coi như không cần hộp ngọc chứa đựng, cũng có thể tự chủ hấp thu thiên địa linh khí, mà không hao tổn."),t("br"),t("br"),t("br"),n._v("Truyền thuyết, một chút cổ mộ từng móc ra qua loại cấp bậc này đan dược, bao nhiêu năm qua đi, dược hiệu chẳng những không có tiêu hao, còn gia tăng rất nhiều, một chút thành đan cấp bậc, đều đạt đến viên mãn, thậm chí hoàn mỹ cấp bậc!"),t("br"),t("br"),n._v("Loại cấp bậc này dược vật, không chỉ ngươi luyện đan cơ sở nắm giữ thuần thục, càng phải có siêu cường tu vi, nếu không, một khi xuất hiện biến cố, đều sẽ gây nên nổ lô, vạn kiếp bất phục."),t("br"),t("br"),n._v("Trước mắt vị này Liễu hội trưởng, để hắn ra tay, trong lòng thực sự không có gì tự tin."),t("br"),t("br"),n._v("“Một lát nữa nghiêm ngặt dựa theo ta lời nhắn nhủ trình tự luyện chế, không chút nào có thể có lỗi!” Trương Huyền không còn trước đó cười đùa tí tửng trạng thái, trái lại một hồi ngưng trọng."),t("br"),t("br"),n._v("Nhìn qua công hội thư tịch, cũng biết tứ phẩm đan dược độ khó."),t("br"),t("br"),n._v("Mặc dù có Thiên Đạo thư viện, có thể tìm ra luyện chế bên trong tất cả sai lầm, muốn cho Tông sư luyện chế ra tứ phẩm đan dược, vẫn là rất khó."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết luyện chế tứ phẩm đan dược nguy hiểm, Mộc đan sư cũng rõ ràng, một khi thành công, đối với hắn đan thuật tăng lên, không thể nghi ngờ là to lớn, chần chờ không bao lâu, cắn răng."),t("br"),t("br"),n._v("Trước mắt người thanh niên này, sáng tạo ra quá nhiều kỳ tích, trong lòng đã đối với hắn sinh ra tin cậy cảm giác, hôm nay thật muốn cự tuyệt, bỏ lỡ cơ hội lần này, chỉ sợ cả đời đều sẽ hối hận."),t("br"),t("br"),n._v("“Bắt đầu!”"),t("br"),t("br"),n._v("Điều chỉnh một hồi, Mộc Dương Phong đạt đến trạng thái tốt nhất, lúc này mới đi tới đan lô trước mặt, bàn tay giương lên, đan hỏa sôi trào mà lên."),t("br"),t("br"),n._v("Hừng hực!"),t("br"),t("br"),n._v("Hỏa diễm thiêu đốt, lô đỉnh bắt đầu biến đỏ, nóng lên."),t("br"),t("br"),n._v("“Để vào Mục Túc hoa, hai cái rưỡi hô hấp về sau, để vào Tiết Mẫu thảo, mười bốn hô hấp phía sau để vào Hàn Băng diệp...”"),t("br"),t("br"),n._v("Nhìn đối phương bắt đầu luyện đan, Trương Huyền không ngừng hô lên."),t("br"),t("br"),n._v("Mộc Dương Phong tuân thủ một cách nghiêm chỉnh, không dám chút nào vi phạm."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Từng cây dược liệu như bay rơi vào lô đỉnh bên trong, hoàn mỹ tiến hành dung hợp."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Luyện đan thất bên ngoài."),t("br"),t("br"),n._v("Vì phòng ngừa quấy rầy hai người, Hồng Vân đan sư đám người cũng chưa đi đến vào."),t("br"),t("br"),n._v("“Các ngươi nói... Bọn hắn có thể thành công hay không?”"),t("br"),t("br"),n._v("Qua một hồi thật lâu, Khương Thần mới nói."),t("br"),t("br"),n._v("“Ta cũng không biết... Người khác còn dễ nói, nhưng cái này Liễu hội trưởng, thực sự không cách nào dùng lẽ thường suy nghĩ...”"),t("br"),t("br"),n._v("Hồng Vân đan sư lắc đầu."),t("br"),t("br"),n._v("Người khác còn dễ nói, nhưng cái này Liễu Trình thực sự để cho người ta nhìn không thấu."),t("br"),t("br"),n._v("Đan phương tường tìm kiếm đan phương, có thể làm cho đối diện luyện đan sư lại nói không ra lời nói, giải quyết tứ tinh luyện đan sư đều không giải quyết được vấn đề..."),t("br"),t("br"),n._v("Phần này năng lực, tuyệt đối vượt qua tưởng tượng."),t("br"),n._v(" “Đúng vậy a!”"),t("br"),t("br"),n._v("Khương Thần cảm thấy từng đợt cảm giác bị thất bại."),t("br"),t("br"),n._v("Hắn là Khương đường chủ nhi tử, cũng là thiên tài, luôn luôn tràn đầy tự tin, mặc dù so ra kém Mạc Hoằng Nhất, Mạc Vũ hai cái quái vật, nhưng là cùng tuổi bên trong, cũng coi như được nổi trội nhất."),t("br"),t("br"),n._v("Vốn cho rằng, nhờ vào ngày mai cố gắng, sớm muộn có thể đuổi kịp bọn hắn, nhìn thấy Trương Huyền cùng vị này Liễu hội trưởng mới hiểu được..."),t("br"),t("br"),n._v("Cùng thiên tài chân chính trong lúc đó, vẫn là có rất lớn chênh lệch."),t("br"),t("br"),n._v("Một cái y sư, nhờ vào đối với dược liệu dược tính nắm giữ, chỉ điểm một cái nhị tinh đỉnh phong luyện đan sư, luyện chế tứ phẩm đan dược... Không phải tận mắt nhìn thấy, truyền đi đều sẽ bị người xem như tên điên."),t("br"),t("br"),n._v("“Bất quá, mặc kệ có thành công hay không, Mộc đan sư sau này tiến bộ, sợ rằng đều ngăn cản không được nữa, đợi một thời gian, thành tựu tứ tinh luyện đan sư cũng không phải không có khả năng...”"),t("br"),t("br"),n._v("Cảm khái một tiếng, Hồng Vân đan sư mặt mũi tràn đầy hối hận."),t("br"),t("br"),n._v("Coi như lần này luyện đan thất bại, tự mình luyện chế tứ phẩm đan dược phần này trải qua, cũng biết để Mộc đan sư đan thuật tăng nhiều. Toàn bộ chuyển hóa làm kinh nghiệm, về sau muốn cực kỳ hắn, chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("Có thể đoán được, mấy năm sau, cái gọi là hội trưởng chi vị, tất nhiên rơi vào đối phương trên đầu, mà không phải hắn."),t("br"),t("br"),n._v("Nếu không phải trước đó trong đó giả vờ giả vịt, cố ý chối từ, lần này cơ hội, rất có thể sẽ là hắn, đáng tiếc..."),t("br"),t("br"),n._v("Cơ hội chỉ có một lần, thời gian cũng không thể lát nữa!"),t("br"),t("br"),n._v("Lại hối hận cũng là vô dụng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thiên Vũ học viện một cái đơn độc phòng nhỏ."),t("br"),t("br"),n._v("Toàn thân trần trụi Lộ Xung mở to mắt."),t("br"),t("br"),n._v("Dùng khăn mặt xoa xoa trên người chảy xuôi mà ra mồ hôi, một ngụm trọc khí phun ra."),t("br"),t("br"),n._v("“Đây là lần thứ tư tu luyện...”"),t("br"),t("br"),n._v("Nhìn xem còn thừa lại hơn phân nửa thuốc bột, Lộ Xung chần chờ."),t("br"),t("br"),n._v("“Lão sư nói, năm lần tu luyện, trong cơ thể ta lực lượng liền sẽ đạt tới cực hạn, dược vật cũng là không có tác dụng... Thông qua cái này mấy lần tu luyện, có thể hiệu quả xác thực một lần so một lần kém, không có gì bất ngờ xảy ra, lần thứ năm luyện xong, tối đa cũng liền nắm giữ Thông Huyền cảnh đỉnh phong thực lực, muốn báo thù... Vẫn là rất khó!”"),t("br"),t("br"),n._v("Răng cắn chặt."),t("br"),t("br"),n._v("Liễu lão sư nói qua, hắn độc thể, đại khái cần năm lần rèn luyện, liền có thể thành công."),t("br"),t("br"),n._v("Mấy ngày nay không biết ngày đêm tu luyện, chỉ cần hơi chút khôi phục, liền tiếp tục tiến hành, quả thực để thực lực có tiến bộ nhảy vọt."),t("br"),t("br"),n._v("Nhưng cũng phát hiện một vấn đề."),t("br"),t("br"),n._v("Cái kia chính là... Độc phấn hiệu quả dần dần yếu bớt, nhất là vừa rồi lần kia, tiến bộ đã rất yếu đi, cùng lần thứ nhất so, kém một nửa không thôi."),t("br"),t("br"),n._v("Có thể đoán được, lại tiếp tục, hiệu quả khẳng định càng yếu, hơn cho đến không có hiệu quả."),t("br"),t("br"),n._v("“Thực sự không được... Đem những thuốc này phấn tất cả đều dùng!”"),t("br"),t("br"),n._v("Ánh mắt hiện lên một đạo tàn nhẫn."),t("br"),t("br"),n._v("Dựa theo lão sư yêu cầu, năm lần tu luyện xong, độc phấn cũng chỉ dùng chừng phân nửa."),t("br"),t("br"),n._v("Đã lần thứ năm tiến bộ hiệu quả cực kỳ bé nhỏ, tăng lớn lượng thuốc đâu?"),t("br"),t("br"),n._v("“Thâm cừu đại hận, không thể không báo, liều mạng!”"),t("br"),t("br"),n._v("Song mi vung lên, Lộ Xung cắn răng một cái, không còn dùng ngân châm, trực tiếp đem thuốc bột toàn bộ bôi lên tại huyệt vị chỗ."),t("br"),t("br"),n._v("Ngân châm dính vào một điểm, cùng trực tiếp đem thuốc bột bôi lên đi lên, hoàn toàn là hai loại khái niệm, coi như Trương Huyền ở bên cạnh, cũng biết cảm thấy hắn có phải điên rồi hay không."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một cỗ toàn tâm đau đớn, trong nháy mắt đánh tới, mãnh liệt cảm giác hôn mê nhường đường hướng thân thể kịch liệt run rẩy."),t("br"),t("br"),n._v("“Ta không thể chết, ta muốn báo thù, cha, mẹ, tỷ tỷ, ta nhất định sẽ cho các ngươi báo thù...”"),t("br"),t("br"),n._v("Từng cái quen thuộc khuôn mặt lần nữa hiện lên trong óc, một tiếng gào thét, Lộ Xung trong lòng cuồng hống."),t("br"),t("br"),n._v("Toàn cả gia tộc một trăm ba mươi bảy miệng, chỉ còn lại có hắn một cái, chưa báo thù trước, sao có thể chết?"),t("br"),t("br"),n._v("Cô cô cô ục ục!"),t("br"),t("br"),n._v("Kịch độc dọc theo làn da thấm, tại toàn thân chảy xuôi, đau đớn kịch liệt tựa như xé rách toàn thân, để hắn như rơi xuống địa ngục."),t("br"),t("br"),n._v("Nếu như nói lần thứ nhất tu luyện liền để hắn khó mà chịu đựng, lần này thống khổ so lần kia trọn vẹn tăng lên gấp mười lần!"),t("br"),t("br"),n._v("Nếu không phải mấy ngày nay một mực tiếp nhận, khẳng định liền một cái hô hấp đều không kiên trì nổi!"),t("br"),t("br"),n._v("Dù vậy, cũng cảm thấy trước mắt từng đợt mê muội, tựa như lúc nào cũng sẽ chết."),t("br"),t("br"),n._v("“Lão sư đối với ta có cực lớn kỳ vọng, phụ mẫu đám người vẫn chờ ta vì bọn họ báo thù, kiên trì...”"),t("br"),t("br"),n._v("Từng tiếng gầm thét, răng cắn chặt đầu lưỡi, miệng đầy máu tươi."),t("br"),t("br"),n._v("Hai năm qua, cố gắng tu luyện, lời cũng không dám nói, vì cái gì?"),t("br"),t("br"),n._v("Không sợ khó khăn, lựa chọn tu luyện độc thể, vì cái gì?"),t("br"),t("br"),n._v("Chỉ cần đại thù không báo, ta Lộ Xung, liền không thể chết!"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Không biết qua bao lâu, đau đớn rốt cục chậm rãi tiêu tán, Lộ Xung lúc này mới tâm thần buông lỏng, mắt tối sầm lại, ngất đi."),t("br"),t("br"),n._v("Ngay tại hắn sau khi hôn mê, một đạo tinh thuần chân khí, phối hợp độc phấn, chậm rãi cùng nhục thể của hắn dung hợp, để lực lượng của hắn càng ngày càng mạnh."),t("br"),t("br"),n._v("Đạo chân khí này, chính là Trương Huyền lần thứ nhất vì hắn tấn thăng thực lực, lưu tại trong cơ thể."),t("br"),t("br"),n._v("Nếu như không có nó bảo hộ tâm mạch, coi như Lộ Xung ý chí lực mạnh hơn, cũng không có khả năng chống đỡ được nhiều như vậy kịch độc."),t("br"),t("br"),n._v("Vốn là, chân khí không phải hắn, cùng thân thể không hợp nhau, không có khả năng tương dung, nhưng chẳng biết tại sao, kịch độc xâm nhập xuống, ma xui quỷ khiến tiến vào thân thể, cải thiện lấy bắp thịt hoạt tính cùng lực lượng."),t("br"),t("br"),n._v("Để nguyên bản chỉ có thể đạt tới Thông Huyền cảnh đỉnh phong thực lực, nhanh chóng gia tăng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hồng Vân đan sư, Khương Thần đợi tiếp cận một canh giờ, luyện đan thất lúc này mới “Kẹt kẹt!” Một tiếng mở ra, hai bóng người đi ra."),t("br"),t("br"),n._v("Chính là Mộc đan sư cùng Liễu hội trưởng."),t("br"),t("br"),n._v("Lúc này hai người tất cả đều sắc mặt bạc màu, thân thể tựa như hư thoát, mồ hôi ướt đẫm quần áo, không nói ra được chật vật."),t("br"),t("br"),n._v("“Chẳng lẽ...”"),t("br"),t("br"),n._v("Gặp bọn họ bộ dáng này, trong lòng hai người “Lộp bộp” một cái, đồng thời toát ra dự cảm bất tường, vội vàng nghênh đón tiếp lấy."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-374"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-376"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);