(window.webpackJsonp=window.webpackJsonp||[]).push([[1605],{1959:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-367-mac-hoang-nhat-roi-ho-phan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-367-mac-hoang-nhat-roi-ho-phan"}},[n._v("#")]),n._v(" Chương 367: Mạc Hoằng Nhất rơi hố phân")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Hồng Vân đan sư đi tới trước mặt nhịn không được hỏi lên."),t("br"),t("br"),n._v("“Thành công...”"),t("br"),t("br"),n._v("Mộc Dương Phong thoạt nhìn rã rời, trong mắt lại lộ ra nồng đậm lửa nóng, nhất là nhìn về phía cách đó không xa Liễu hội trưởng, tràn đầy sùng bái."),t("br"),t("br"),n._v("Chân chính cùng vị này cùng một chỗ luyện đan, mới biết được hắn đối với luyện đan hiểu rõ đến cùng sâu bao nhiêu."),t("br"),t("br"),n._v("Tuyệt không phải chỉ bằng vào đối với dược tính lý giải, liền có thể đạt tới."),t("br"),t("br"),n._v("Mặc dù nghiêm ngặt dựa theo trình tự tiến hành, nhưng nửa đường vẫn là ra khỏi mấy lần nguy cơ."),t("br"),t("br"),n._v("Không phải làm sai, mà là thực lực của hắn thực sự quá kém, tại Thiên Vũ vương quốc, cũng tạm được, có thể dùng để luyện chế tứ phẩm đan dược, cũng có chút không đáng chú ý."),t("br"),t("br"),n._v("Luyện chế đan dược, duy trì đan hỏa, khống chế lô đỉnh nhiệt độ đều cần chân khí."),t("br"),t("br"),n._v("Nhiều lần, bởi vì chân khí suy kiệt, suýt chút nữa bỏ xuống, nếu không phải những năm này luyện chế ra không ít dùng để hồi khí đan dược, thời khắc mấu chốt phát huy được tác dụng, chỉ sợ sớm đã mệt chết tại chỗ."),t("br"),t("br"),n._v("Mỗi lần chân khí không đủ, xuất hiện tình thế nguy hiểm, đều có vị này Liễu hội trưởng chỉ điểm, sử dụng thủ đoạn không thể tưởng tượng, có thể hiệu quả lại là lớn nhất, rõ ràng nhìn xem liền muốn không khống chế nổi, lại đều có thể mạnh mẽ vãn hồi, sáng chế kỳ tích."),t("br"),t("br"),n._v("Thật giống như hắn có thể nhìn ra trong lò đan chân thực tình huống, như thế nào làm mới có thể tránh miễn bạo tạc đồng dạng."),t("br"),t("br"),n._v("Thật không biết cái tuổi này đến cùng thế nào học, có như thế bác học tri thức cùng luyện đan thuật!"),t("br"),t("br"),n._v("Cùng Hồng Vân đan sư đoán đồng dạng, thông qua lần này luyện đan, thật sự là hắn học được rất nhiều, vô luận tri thức vẫn là tâm lý, đều có cực lớn phát triển, có thể đoán được, coi như không cần chỉ điểm, luyện chế ra tam phẩm sơ kỳ, thậm chí trung kỳ đan dược, cũng chỉ là vấn đề thời gian!"),t("br"),t("br"),n._v("“Thành công?”"),t("br"),t("br"),n._v("Nhìn thấy Mộc đan sư sùng bái quang mang, Hồng Vân cùng Khương Thần cũng nhìn về phía cách đó không xa Liễu hội trưởng, triệt để ngây người."),t("br"),t("br"),n._v("Không nghĩ tới đối phương thật sáng chế ra kỳ tích, một cái để cho người ta tận mắt nhìn đến, đều khó mà tiếp nhận thành tích!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hàn Âm đan luyện chế thành công, còn lại cũng là không có việc gì, nghỉ ngơi một hồi, khôi phục chút thể lực, Trương Huyền bắt đầu giúp Mộc Tuyết Tinh kích hoạt trong cơ thể lưu lại dược lực."),t("br"),t("br"),n._v("Trước đó dược dịch, Mộc đan sư đã sớm sớm làm xong, người sau cũng điều chỉnh đến trạng thái đỉnh cao nhất, mượn nhờ ngân châm, đem Thiên Đạo chân khí độ vào đối phương trong cơ thể."),t("br"),t("br"),n._v("Tinh thuần chân khí xuống, Mộc Tuyết Tinh trong cơ thể chất đống lên dược lực, giống như là bị hoả tinh nhen lửa xăng, lập tức cháy hừng hực lên, lực lượng khổng lồ dọc theo kinh mạch, đi khắp toàn thân."),t("br"),t("br"),n._v("Lốp bốp!"),t("br"),t("br"),n._v("Thân thể giống pháo vang lên, phát ra liên tiếp giòn vang, rất nhiều huyệt đạo đều giải khai."),t("br"),t("br"),n._v("Nhìn một hồi, biết có dược dịch hộ thể, không có nguy hiểm gì, Trương Huyền lập tức cùng Mộc Dương Phong cáo từ, rời đi công hội."),t("br"),t("br"),n._v("Tìm cái không có người địa phương, một lần nữa biến trở về Dương Huyền bộ dáng, lần nữa hướng phủ đệ đi đến."),t("br"),t("br"),n._v("Trở lại phủ đệ, đã rất muộn, may mắn Triệu Nhã đám người không ngủ, đem luyện chế tốt Hàn Âm đan đưa tới, để nàng mau chóng phục dụng."),t("br"),t("br"),n._v("Lại đem ban đầu ở Thú đường Vương thú sư đưa thanh trường kiếm kia đưa ra."),t("br"),t("br"),n._v("Chuôi này so với bị hắn bắn đánh gãy cấp bậc kia cao không ít, cùng Triệu Nhã ngược lại là xứng đôi."),t("br"),t("br"),n._v("Xử lý xong những này, lúc này mới cảm động toàn thân rã rời, Trương Huyền nằm xuống ngủ thật say."),t("br"),t("br"),n._v("Nói thật, hắn vẫn là coi thường luyện chế Hàn Âm đan độ khó, nếu không có Thiên Đạo thư viện thời gian thực đổi mới nội dung, biết trong quá trình luyện chế khuyết điểm ở đâu, có lẽ sớm đã nổ lô."),t("br"),t("br"),n._v("Dù vậy, tinh thần cao độ khẩn trương, tiêu hao quá lớn, lúc này mới dẫn đến sắc mặt trắng bệch, toàn thân mềm nhũn."),t("br"),t("br"),n._v("Xem ra sau này vẫn là bớt làm loại này vượt cấp quá nhiều chuyện, nếu không, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tỉnh lại sau giấc ngủ, mặt trời lên cao."),t("br"),t("br"),n._v("Nghỉ ngơi sung túc, Trương Huyền lần nữa cảm thấy tinh lực dồi dào."),t("br"),t("br"),n._v("Hỏi một câu, Triệu Nhã tựa hồ còn đang bế quan tu luyện, bàn giao Tôn Cường một tiếng, rời đi phủ đệ, hướng Thiên Vũ học viện đi đến."),t("br"),t("br"),n._v("Liên tục hai ngày không thấy Lộ Xung, cũng không biết có hay không hảo hảo tu luyện, hiện tại có rảnh, vừa vặn đi qua nhìn một chút."),t("br"),t("br"),n._v("“Lão sư, ngươi đã đến...”"),t("br"),t("br"),n._v("Mới đi đến lớp học bên ngoài, Mạnh Đào liền vội vã tiến lên đón."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Cái này Mạnh Đào luôn luôn trầm ổn, không có chuyện gì không có khả năng chờ ở bên ngoài."),t("br"),t("br"),n._v("“Lão sư, Tôn lão sư mới vừa buổi sáng liền đến tìm ngươi, đã đợi hơn một canh giờ!” Mạnh Đào vội nói."),t("br"),t("br"),n._v("“Tôn Thừa?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ."),t("br"),t("br"),n._v("Không phải liền là Mạc Hoằng Nhất sao? Lúc này tìm bản thân làm gì?"),t("br"),t("br"),n._v("Đẩy cửa đi vào gian phòng, quả nhiên thấy, Mạc Hoằng Nhất đang an tĩnh ngồi ở phòng học bên trên trên ghế, an tĩnh uống nước trà."),t("br"),t("br"),n._v("“Ngươi rốt cuộc đã đến, có thể để ta đợi thật lâu...”"),t("br"),t("br"),n._v("Thấy hắn đi vào cửa phòng, Mạc Hoằng Nhất cười khổ."),t("br"),t("br"),n._v("Gia hỏa này thật làm cho người không còn cách nào khác, vì thông qua nhị tinh danh sư khảo hạch, hắn cơ hồ mỗi ngày đều ngâm mình ở phòng học, không dám ra đến, một ngày ít nhất trên bốn, năm canh giờ khóa, cố gắng cùng học sinh bồi dưỡng độ tín nhiệm."),t("br"),t("br"),n._v("Gia hỏa này ngược lại tốt, ba ngày đánh cá hai ngày phơ lưới, một ngày tới không được nửa canh giờ, kết quả... Học sinh của hắn đối với hắn lại khăng khăng một mực, triệt để bội phục!"),t("br"),t("br"),n._v("Thậm chí vì danh dự của hắn, không tiếc cùng “Trương sư” học sinh tranh đấu..."),t("br"),t("br"),n._v("Thật sự là người so với người làm người ta tức chết!"),t("br"),t("br"),n._v("Để cho người ta không phục không được."),t("br"),t("br"),n._v("“Đi vào nói đi!”"),t("br"),n._v("Bên ngoài đều là học sinh, không tiện nhiều lời, Trương Huyền nói một tiếng, cùng Mạc Hoằng Nhất đi vào phòng nhỏ."),t("br"),t("br"),n._v("“Tới tìm ngươi có hai chuyện!”"),t("br"),t("br"),n._v("Hàn huyên hai câu, Mạc Hoằng Nhất mở lời: “Thứ nhất, viện trưởng để cho ta tới nên nói khách, để ngươi hẹn thời gian cùng Trương sư gặp mặt một lần, cùng một chỗ ngồi uống chút trà, ăn chút cơm, miễn cho bởi vì học sinh tranh đấu sự tình, náo ra mâu thuẫn!”"),t("br"),t("br"),n._v("“Cùng Trương sư gặp mặt?” Trương Huyền nháy con mắt, dở khóc dở cười: “Khẳng định không thể đáp ứng...”"),t("br"),t("br"),n._v("Để cho mình cùng mình ước lấy gặp mặt? Còn uống trà ăn cơm?"),t("br"),t("br"),n._v("Thế nào ăn?"),t("br"),t("br"),n._v("Thật muốn đáp ứng, khẳng định lập tức để lộ lộ hãm."),t("br"),t("br"),n._v("“Ai bảo ngươi cao điệu như vậy...” Thấy cái này đánh vỡ hắn thêm hạng ghi chép gia hỏa, rốt cục kinh ngạc, Mạc Hoằng Nhất nở nụ cười."),t("br"),t("br"),n._v("Ngươi nói ngươi ngụy trang phổ thông lão sư liền phổ thông lão sư, một cái làm cái Y Sư công hội hội trưởng, dạy học sinh càng là không phải muốn cùng “Trương sư” học sinh giao đấu..."),t("br"),t("br"),n._v("Cao điệu như vậy, muốn không làm cho người khác chú ý cũng khó khăn!"),t("br"),t("br"),n._v("Hiện tại loại này đâm lao phải theo lao cục diện, cũng là tự tìm."),t("br"),t("br"),n._v("“Tốt rồi, không nói cái này, ta cũng biết ngươi chắc chắn sẽ không đáp ứng. Bất quá, nhìn viện trưởng thái độ, lời khuyên của ta vô dụng, hắn khẳng định sẽ đích thân tới! Đến lúc đó, coi như muốn từ chối, cũng không tiện lắm!”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất cũng lắc đầu, không trong vấn đề này tiếp tục dây dưa, nói: “Vẫn là nói chuyện thứ hai đi, đi qua mấy ngày nay, ta cảm thấy học sinh độ tín nhiệm không sai biệt lắm đủ bốn mươi, muốn quay về Danh Sư đường đo lường, mau chóng thông qua khảo hạch!”"),t("br"),t("br"),n._v("“Ta nhìn ngươi học sinh, đối với ngươi cũng rất là tín nhiệm, khẳng định từ lâu vượt qua bốn mươi, không bằng cùng một chỗ! Một khi thông qua, ngoại nhân biết Liễu lão sư liền là Trương sư, liền có thể thoát khỏi cái thân phận này, tránh cho xấu hổ, cũng có thể để ngươi học sinh, không tự giết lẫn nhau!”"),t("br"),t("br"),n._v("“Ừm...”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Lúc trước Danh Sư đường cho bọn hắn điều kiện là, trong mười ngày dùng ngụy trang thân phận thu hoạch được học sinh bốn mươi trở lên độ tín nhiệm."),t("br"),t("br"),n._v("Thời gian này, chỉ là cái phạm vi, nếu như sớm đạt đến, tự nhiên có thể trở về Danh Sư đường thông qua cửa ải cuối cùng."),t("br"),t("br"),n._v("Bất quá, một khi đo lường có điều, học sinh khẳng định cũng đã biết xác thực thân phận, lại không cách nào tiếp tục ngụy trang, nhị tinh danh sư khảo hạch chẳng khác nào không thành công."),t("br"),t("br"),n._v("Đi qua mấy ngày nay chỉ điểm, không nói trước Mộc Tuyết Tinh, Lộ Xung, coi như Mạnh Đào đám người, cũng cơ hồ đối với hắn nói gì nghe nấy, loại này độ tín nhiệm, coi như không đạt tới bốn mươi, cũng chênh lệch không xa, có thể đủ hoàn thành khảo hạch."),t("br"),t("br"),n._v("Thấy hắn đáp ứng, Mạc Hoằng Nhất cười cười: “Vậy liền đã nói, ngày mai buổi sáng cùng đi Danh Sư đường!”"),t("br"),t("br"),n._v("“Tốt!” Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Sự tình nói đến cũng không phức tạp, thương nghị xong, Mạc Hoằng Nhất không có tiếp tục dừng lại, quay người rời đi, tiếp tục đi học, làm đánh cược lần cuối."),t("br"),t("br"),n._v("Một lần nữa trở lại lớp học, chỉ điểm một hồi Mạnh Đào đám người tu luyện, Trương Huyền lúc này mới hỏi: “Lộ Xung đâu? Thế nào hôm nay không có tới đi học?”"),t("br"),t("br"),n._v("“Hẳn là tại tu luyện đi, ta cái này tìm hắn tới...”"),t("br"),t("br"),n._v("Mạnh Đào lên tiếng, quay người đi ra ngoài."),t("br"),t("br"),n._v("Lộ Xung là học viện học sinh, tự nhiên ở tại học sinh nhà trọ."),t("br"),t("br"),n._v("Biết đem đối phương đi tìm đến, phải cần một khoảng thời gian, Trương Huyền cũng không nóng nảy, ngồi xuống, để mọi người dựa theo chỉ điểm của hắn tu luyện, rót một chén nước, nâng chung trà lên."),t("br"),t("br"),n._v("Nâng chung trà lên, đang muốn uống nước, bờ môi cùng cái chén tiếp xúc, lông mày đột nhiên nhíu một cái."),t("br"),t("br"),n._v("“Địa Càn phấn?”"),t("br"),t("br"),n._v("Hắn mặc dù không có khảo hạch qua độc sư, nhưng đem Độc điện thư tịch học xong, đã nắm giữ nhị tinh đỉnh phong độc sư năng lực."),t("br"),t("br"),n._v("Lúc này còn không có đụng phải nước trà, liền phát hiện bên trong ẩn chứa kịch độc."),t("br"),t("br"),n._v("Địa Càn phấn là một loại nhị phẩm đỉnh phong cấp bậc độc dược, đối với Tông sư cường giả đỉnh phong đều hữu hiệu quả, mặc dù không chí tử, lại có thể khiến người ta trong thời gian ngắn thể lực hoàn toàn không có, thượng thổ hạ tả, khổ không thể tả."),t("br"),t("br"),n._v("Ai sẽ tại hắn trong chén trà hạ độc?"),t("br"),t("br"),n._v("Để làm cái gì?"),t("br"),t("br"),n._v("Nhướng mày, đang muốn hỏi thăm ai động đậy hắn đồ uống trà, chỉ thấy mới vừa đi ra Mạnh Đào vội vã chạy tới, một mặt cổ quái cùng bối rối."),t("br"),t("br"),n._v("“Liễu lão sư, không xong!”"),t("br"),t("br"),n._v("“Làm sao vậy? Chẳng lẽ Lộ Xung xảy ra chuyện?”"),t("br"),t("br"),n._v("Nhìn thấy bộ dáng của hắn, Trương Huyền lông mày giương lên."),t("br"),t("br"),n._v("Để hắn đi tìm Lộ Xung, bây giờ lại bộ dáng này, chẳng lẽ tên kia không có dựa theo hắn dạy bảo phương pháp tu luyện, nghiêm túc tu luyện, dẫn đến xảy ra vấn đề?"),t("br"),t("br"),n._v("Thật muốn tại ngụy trang trong lúc đó đem học sinh dạy dỗ sự cố, tẩu hỏa nhập ma hoặc là dạy chết..."),t("br"),t("br"),n._v("Đừng nói nhị tinh luyện đan sư không cách nào khảo hạch, có lẽ đời này cũng sẽ cùng danh sư vô duyên."),t("br"),t("br"),n._v("Nếu là trước kia, nắm giữ Thiên Đạo thư viện, khảo hạch không khảo hạch danh sư đối với hắn mà nói, không có quá lớn ảnh hưởng."),t("br"),t("br"),n._v("Nhưng bây giờ ba mươi tuổi trước, không đạt tới cửu tinh danh sư, Tiên Thiên thai độc liền sẽ phát tác, không khảo hạch danh sư, khẳng định không được!"),t("br"),t("br"),n._v("Cho dù có những biện pháp khác, cũng phải tìm đến mới được..."),t("br"),t("br"),n._v("Tìm không thấy, tương đương với không dùng! Trước mắt đáng tin nhất, vẫn là cái này."),t("br"),t("br"),n._v("Đối với những người khác, cái mục tiêu này thực sự quá khó, có thể định tính vì không có khả năng, nhưng đối với hắn mà nói, chỉ cần hảo hảo tu luyện, mau chóng tìm kiếm đối ứng công pháp bí tịch, ngược lại là có rất lớn cơ hội thành công."),t("br"),t("br"),n._v("“Không nên ah, trong cơ thể hắn có của ta thiên đạo chân khí bảo vệ tâm mạch, lại chịu đựng qua hiếm thấy nhất một cửa, không có khả năng xảy ra chuyện a?”"),t("br"),t("br"),n._v("Trước khi đi, hắn chuyên môn nhìn qua, Lộ Xung trong cơ thể có hắn một đạo chân khí, liền hiếm thấy nhất lần thứ nhất đều vượt qua được, cơ bản tính mệnh không lo, làm sao sẽ xảy ra vấn đề?"),t("br"),t("br"),n._v("“Không phải Lộ Xung!” Mạnh Đào lắc đầu, lập tức trên mặt lộ ra cổ quái chi ý: “Là Tôn Thừa lão sư, hắn, hắn...”"),t("br"),t("br"),n._v("“Hắn làm sao vậy?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Mạnh Đào mặt mũi tràn đầy xấu hổ: “Hắn, hắn rơi hố phân...”"),t("br"),t("br"),n._v("(Đây là chúng ta nhai quân thành lập tháng thứ nhất, chư vị có thể theo giúp ta cùng một chỗ trùng kích cao hơn, cùng một chỗ Hoành Tảo Thiên Nhai? Làm cho tất cả mọi người tất cả xem một chút chúng ta nhai quân thực lực? Đã như vậy, nguyệt phiếu đưa tới đi! Chỉ cần tháng trước bình thường đặt mua, tháng này liền sẽ có ba tấm giữ gốc, xin bầu cho quyển sách, lão nhai cảm tạ! Mấy ngày nay tăng ca khả năng không có cách nào bộc phát, có điều, khẳng định cũng biết tăng thêm! Vẫn là câu nói kia, gõ chữ về ta, nguyệt phiếu giao cho các ngươi! Để chúng ta hướng! Hướng! Hướng!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-375"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-377"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);