(window.webpackJsonp=window.webpackJsonp||[]).push([[1611],{1966:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-372-lam-lang"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-372-lam-lang"}},[n._v("#")]),n._v(" Chương 372: Lâm Lang")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Lâm gia, là Thiên Vũ vương quốc đệ nhất gia tộc, Lâm Lang công tử là Lâm gia chủ sủng ái nhất con trai độc nhất, Hiên Viên vương quốc thái tử phi đệ đệ. Hắn mở lời lời nói, Mạc Thiên Tuyết bệ hạ đều muốn cho chút thể diện, chỉ cần đáp ứng, thiếu gia cũng không cần lo lắng!”"),t("br"),t("br"),n._v("Gã sai vặt chần chờ một chút, trọng trọng gật đầu."),t("br"),t("br"),n._v("Trước mắt loại tình huống này, không có biện pháp tốt hơn."),t("br"),t("br"),n._v("Vị này Trương Huyền, một khi khảo hạch nhị tinh danh sư thành công, địa vị, thân phận nước lên thì thuyền lên, thật muốn truy cứu chuyện ngày đó, Quý gia coi như thân là một trong tam đại gia tộc, cũng không dám chống lại."),t("br"),t("br"),n._v("Nhị tinh danh sư, nhất là loại siêu cấp thiên tài này cấp bậc, có thể nói, nắm giữ sức mạnh cực kỳ mạnh."),t("br"),t("br"),n._v("Lại thêm vẫn là Khương đường chủ sư huynh, phía sau có cái lợi hại hơn lão sư..."),t("br"),t("br"),n._v("Trả thù khẳng định là không dám, chỉ muốn tìm có địa vị người trung gian, chuyện lớn hóa nhỏ, chuyện nhỏ hóa không."),t("br"),t("br"),n._v("Mà cái này Lâm gia, chính là không có chỗ thứ hai."),t("br"),t("br"),n._v("“Ừm, đi vào nhanh một chút đi!”"),t("br"),t("br"),n._v("Vung tay lên, Quý Mặc công tử nhanh chân hướng Phỉ Thúy các đi đến."),t("br"),t("br"),n._v("Nhìn hắn xe nhẹ đường quen bộ dạng, hẳn là thường xuyên đến cái này, dọc theo đá xanh lát thành đường nhỏ, đi một hồi, đi tới một cái rộng rãi trước gian phòng."),t("br"),t("br"),n._v("Cùng hộ vệ thông bẩm một tiếng, đi vào."),t("br"),t("br"),n._v("Vừa tiến vào gian phòng, rộng mở trong sáng."),t("br"),t("br"),n._v("Rộng lớn đại sảnh, khảm nạm đủ loại bảo thạch, da thú, trang trí tráng lệ, coi như hoàng cung so sánh cùng nhau, đều kém một mảng lớn."),t("br"),t("br"),n._v("Trận pháp gia trì xuống, linh khí kinh người, bịt kín đại điện, không những không lộ vẻ ngột ngạt, trả lại người một loại thế ngoại đào nguyên cảm giác."),t("br"),t("br"),n._v("Gian phòng ở giữa, một thanh niên nghiêng dựa vào một tấm to lớn da thú bên trên, hai bên mỹ nữ vờn quanh, trước mặt trên bàn trà bày đầy các loại mỹ vị món ngon, mùi rượu xông vào mũi."),t("br"),t("br"),n._v("Hắn đối diện, cũng là thanh niên, một thân ung dung hoa quý, chính đoan lên chén rượu cùng đối phương uống."),t("br"),t("br"),n._v("“Quý Mặc lão đệ, đã lâu không gặp...”"),t("br"),t("br"),n._v("Thấy hắn đi vào, nghiêng dựa vào mỹ nữ trong ngực thanh niên, vẫy vẫy tay."),t("br"),t("br"),n._v("“Lâm Lang công tử!”"),t("br"),t("br"),n._v("Quý Mặc ôm quyền."),t("br"),t("br"),n._v("“Đến, ta giới thiệu cho ngươi một chút, vị này là Khôn Càn vương quốc Phí Hiên vương tử!”"),t("br"),t("br"),n._v("Lâm Lang công tử chỉ một cái đối diện thanh niên, cho hai người lẫn nhau giới thiệu: “Phí Hiên vương tử, vị này là Thiên Vũ vương thành Quý gia Quý Mặc công tử!”"),t("br"),t("br"),n._v("“Nguyên lai là Quý công tử, tuổi còn trẻ liền trở thành chính thức thư họa sư, ta tại Khôn Càn vương quốc liền nghe ngửi đại danh!”"),t("br"),t("br"),n._v("Phí Hiên vương tử cười nói."),t("br"),t("br"),n._v("Vị này, chính là tại Trương Huyền tại hoàng cung nhìn thấy cái kia, hướng Mạc Vũ công chúa cầu thân vương tử."),t("br"),t("br"),n._v("Cầu thân thất bại, lại biết Liễu hội trưởng chữa khỏi trấn quốc Thần Thú, đem chủ ý đánh tới Lâm gia."),t("br"),t("br"),n._v("Dùng trận pháp sư công hội cùng thái tử phi ở sau lưng chống đỡ, Lâm gia tại nhất đẳng trong vương quốc, tựa như thế lực bá chủ, coi như hắn là vương tử, cũng không dám trang lớn."),t("br"),t("br"),n._v("Tới này trước đó, tam đại gia tộc, vương thành các loại thế lực phân bố, đã sớm nghe ngóng, về phần cái này Quý Mặc công tử, nghe xong danh tự, tự nhiên là biết xong việc dấu vết."),t("br"),t("br"),n._v("Song phương nhận biết, ngồi xuống, hàn huyên vài câu, Quý Mặc công tử cũng không nói nhảm: “Lâm công tử, lần này lão đệ tới tìm ngươi, là cầu ngươi hỗ trợ...”"),t("br"),t("br"),n._v("“Ồ?”"),t("br"),t("br"),n._v("Lâm Lang liếc mắt nhìn lại."),t("br"),t("br"),n._v("“Là như vậy, ta... Trước đó trong lúc vô tình đắc tội Trương Huyền Trương sư, hắn hiện tại khảo hạch nhị tinh danh sư thành công, sợ tìm phiền toái, cho nên muốn mời Lâm công tử làm người trung gian, từ đó điều giải một cái...”"),t("br"),t("br"),n._v("Quý Mặc công tử nói."),t("br"),t("br"),n._v("“Trương Huyền? Ngươi nói nhưng mà những ngày này danh khí rất lớn siêu cấp thiên tài?” Lâm Lang đổ đầy một chén rượu, một mặt cũng không thèm để ý."),t("br"),n._v("“Đúng vậy...”"),t("br"),t("br"),n._v("Quý Mặc công tử liền vội vàng gật đầu, cổ tay khẽ đảo: “Ta biết chuyện này rất khó làm, cũng biết Lâm công tử cũng đối thư hoạ có chút yêu thích, đặc biệt chuẩn bị Kim Mãn Đường tiền bối lưu lại  mong rằng vui vẻ nhận!”"),t("br"),t("br"),n._v("“Khô Tùng đồ? Đây chính là Kim Mãn Đường tiền bối lưu tồn ở thế cuối cùng một bức họa! Coi như tại ngũ cảnh tác phẩm bên trong, đều được cho trân phẩm. Bản vẽ này, giá trị cực cao, các ngươi Quý gia, năm đó có ân cùng lão tiên sinh, lúc này mới may mắn cất giữ, lần này thế mà cam lòng lấy ra cho ta, không tệ, không tệ!”"),t("br"),t("br"),n._v("Tiếp nhận bức tranh, mở ra nhìn một hồi, Lâm Lang hài lòng gật đầu: “Ngươi chuyện này, ta đáp ứng!”"),t("br"),t("br"),n._v("“Đa tạ Lâm công tử!”"),t("br"),t("br"),n._v("Quý Mặc công tử hơi đỏ mặt."),t("br"),t("br"),n._v("“Tốt rồi, cũng không phải cái đại sự gì. Nhị tinh danh sư mặc dù lợi hại, nhưng ở Lâm gia chúng ta trước mặt còn không tính cái gì! Một cái vận khí tốt, bái cái tốt sư phụ gia hỏa mà thôi!”"),t("br"),t("br"),n._v("Lâm Lang công tử khoát tay áo: “Chờ hắn khảo hạch hết, ta phát cái thiếp mời, đem hắn mời đi theo, các ngươi gặp mặt một lần, có cái gì mâu thuẫn, cũng là hóa giải!”"),t("br"),t("br"),n._v("“Vâng!” Quý Mặc công tử liên tục gật đầu."),t("br"),t("br"),n._v("Nếu như người khác nói như vậy, hắn khẳng định khịt mũi coi thường, nhưng trước mắt vị này Lâm công tử, quả thực có vốn liếng này."),t("br"),t("br"),n._v("Chỉ so với hắn lớn không đến ba tuổi, liền nắm giữ nhị tinh trận pháp sư thực lực, bản thân tu vi cũng tại hai tháng trước, thành công đột phá, bước vào Tông Sư cảnh!"),t("br"),t("br"),n._v("Từ Thông Huyền cảnh đỉnh phong đến Tông sư, chỉ dùng ngắn ngủi nửa năm không đến, mặc dù có Hiên Viên vương quốc cùng Lâm gia đại lượng vật tư cung ứng, cũng đủ để chứng minh thiên phú!"),t("br"),t("br"),n._v("Bản thân có thực lực, lại thêm Lâm gia làm chỗ dựa."),t("br"),t("br"),n._v("Cái kia Trương Huyền coi như thiên tài, thoạt nhìn phong quang vô hạn, ở trước mặt hắn, cũng coi như không là cái gì."),t("br"),t("br"),n._v("Hiên Viên phong hào vương quốc, nhưng mà có tam tinh danh sư trấn giữ."),t("br"),t("br"),n._v("“Lâm công tử không nên khinh thường, ta có thể nghe nói cái này Trương Huyền Trương sư, có rất lớn năng lực, thiên phú cao tuyệt, liền các ngươi vương quốc Mạc Hoằng Nhất, đều bị ép xuống!” Phí Hiên vương tử xen vào nói."),t("br"),t("br"),n._v("Tại Thiên Vũ vương thành chờ đợi vài ngày, không ít chuyện cũng nghe nói."),t("br"),t("br"),n._v("Vị này Trương Huyền khảo hạch danh sư, liên phá Mạc Hoằng Nhất vô số ghi chép, sớm đã vang vọng phố lớn ngõ nhỏ."),t("br"),t("br"),n._v("“Là có chút thiên tài lại như thế nào? Thực lực bản thân chỉ có Tông Sư cảnh sơ kỳ... Thiên Vũ vương thành liền muốn tốt còn đợi, không dám làm loạn!”"),t("br"),t("br"),n._v("Lâm Lang cười khinh bỉ."),t("br"),t("br"),n._v("Thiên tài tính là gì?"),t("br"),t("br"),n._v("Thực lực bản thân không đủ, hết thảy đều không dùng."),t("br"),t("br"),n._v("Dù sao thế giới này, cuối cùng vẫn nhìn thực lực, cấp bậc càng cao vương quốc càng rõ hiện ra. Thực lực càng cao, địa vị càng cao, thực lực không đủ, coi như nắm giữ đặc thù chức nghiệp, cũng đồng dạng bị người khinh thị."),t("br"),t("br"),n._v("“Như thế...”"),t("br"),t("br"),n._v("Phí Hiên vương tử thâm dĩ vi nhiên nhẹ gật đầu, nói tiếp: “Đúng rồi, gần nhất còn có một thiên tài, không biết Lâm công tử nghe nói không? Gọi Liễu Trình, nghe nói là Thiên Vũ học viện lão sư!”"),t("br"),t("br"),n._v("“Ngươi nói nhưng mà Y Sư công hội, giải quyết mười chín đạo nghi vấn khó xử lý tạp chứng Lâm hội trưởng?”"),t("br"),t("br"),n._v("Lâm Lang bàn tay vung lên, mang theo chỉ điểm giang sơn chi ý: “Đương nhiên nghe qua, xem như một nhân tài! Bất quá, thực lực bản thân càng kém, chỉ có Thông Huyền cảnh sơ kỳ, tiện tay liền có thể bóp chết... Không tính là gì!”"),t("br"),t("br"),n._v("“Tốt rồi, đến ta Phỉ Thúy các đừng nói là những thứ này, tận hưởng lạc thú trước mắt mới là vương đạo! Người tới, những này thịt rượu đã nguội, cho ta đổi thành nóng!”"),t("br"),t("br"),n._v("Nhẹ nhàng nở nụ cười, Lâm Lang phân phó."),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Nương theo tiếng la của hắn, mấy cái người hầu gã sai vặt đi tới, bưng lên món ăn nóng."),t("br"),t("br"),n._v("Bên trong một cái, cố ý giảm thấp xuống vành nón, chậm rãi đi tới, nhìn về phía Lâm Lang ánh mắt, lộ ra nồng đậm hận ý."),t("br"),t("br"),n._v("Chính là Trương Huyền cái kia đi ra ngoài báo thù học sinh... Lộ Xung!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-380"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-382"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);