(window.webpackJsonp=window.webpackJsonp||[]).push([[1613],{1967:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-374-thien-vu-vuong-quoc-tang-thu-kho"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-374-thien-vu-vuong-quoc-tang-thu-kho"}},[n._v("#")]),n._v(" Chương 374: Thiên Vũ vương quốc tàng thư khố")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thiên Vũ học viện, Trương Huyền phòng học."),t("br"),t("br"),n._v("Giao phó xong Mộc Tuyết Tinh, bắt đầu đi học."),t("br"),t("br"),n._v("Nắm giữ Minh Lý Chi Nhãn, đối với tu vi lý giải, có biến hóa."),t("br"),t("br"),n._v("Cho lúc trước Mạnh Đào đám người chỉ điểm, cần phải mượn Thiên Đạo thư viện năng lực, mới có thể thẩm tra thiếu hụt, chỉ điểm tu vi."),t("br"),t("br"),n._v("Mà bây giờ, chỉ cần tiêu hao một tia tâm cảnh khắc độ, liền có thể biết đối phương trong tu luyện vấn đề xuất hiện, cấp cho phán đoán chính xác nhất."),t("br"),t("br"),n._v("“Đây mới thực sự là danh sư thực lực...” Trương Huyền hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Trước kia đối với danh sư, chảy tại hình thức, mặc dù làm lão sư, nhưng trong lòng không có rõ ràng khái niệm."),t("br"),t("br"),n._v("Lần này đốn ngộ, tâm cảnh nhận được thăng hoa, cả người giơ tay nhấc chân đều có nhà giáo phong phạm, không tiếp tục trước đó không hài hòa cảm giác."),t("br"),t("br"),n._v("Hơn nữa, có Minh Lý Chi Nhãn, coi như không cần thư viện, cũng có thể nhẹ nhõm bắt lấy bản chất của sự vật, không giống trước kia đồng dạng, làm một chuyện gì đều trước tiên đem người gõ bất tỉnh, hoặc là thi triển võ kỹ."),t("br"),t("br"),n._v("Mặc dù cùng Thiên Đạo thư viện so, chỉ có thể nhìn ra viết da lông, tu vi cao hơn hắn, cũng nhìn không ra đến, lại tương đương có tốt bắt đầu, nương theo sau này thực lực gia tăng, tri thức lượng dự trữ càng nhiều, khẳng định sẽ còn tiến bộ."),t("br"),t("br"),n._v("Chí ít cùng trước kia giả vờ giả vịt so, đã có danh sư phong phạm, càng ngày càng nghĩ chân chính danh sư áp sát."),t("br"),t("br"),n._v("Rất nhanh, một bài giảng lên hết, tất cả học sinh như si như say, cảm giác tu vi lần nữa nhảy lên, bất cứ lúc nào cũng sẽ đột phá."),t("br"),t("br"),n._v("Trước kia đi học, đều là ép buộc bản thân học tập tri thức, dần dà, không ít người trong lòng đều sinh ra chán ghét chi ý."),t("br"),t("br"),n._v("Mà vị này Liễu lão sư đi tới về sau, tất cả mọi người khát vọng đi học, mỗi lần một tiết, đều như uống tiên nhưỡng, toàn thân lỗ chân lông mở rộng."),t("br"),t("br"),n._v("Thay đổi một cách vô tri vô giác bên trong, tín nhiệm với hắn độ tất cả đều chậm rãi tăng lên, chậm rãi gia tăng."),t("br"),t("br"),n._v("“Lão sư, Mạc Vũ công chúa cầu kiến!”"),t("br"),t("br"),n._v("Khóa mới vừa lên hết, Mạnh Đào liền đi tới."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Lên tiếng, Trương Huyền đi ra ngoài."),t("br"),t("br"),n._v("Tiến lên đón, Mạc Vũ mỉm cười, tựa như trăm hoa đua nở: “Liễu lão sư lần trước không phải nói muốn đi vương quốc tàng thư khố đọc sách sao? Phụ vương để cho ta tới mời...”"),t("br"),t("br"),n._v("Trước đó Trương Huyền trị liệu trấn quốc Thần Thú thời điểm, từng nói muốn đi vương quốc tàng thư khố nhìn xem, ngay sau đó hắn hai cái học sinh giao đấu, náo ra liên tiếp sự tình, liền chưa kịp đi qua."),t("br"),t("br"),n._v("Hiện tại vừa vặn vô sự, đặc biệt tới mời."),t("br"),t("br"),n._v("Vừa đến, đại biểu vương thất hướng hắn lấy lòng; Thứ hai, cũng đối Triệu Nhã, Mộc Tuyết Tinh giao đấu giấu diếm, bày tỏ xin lỗi."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền đáp ứng."),t("br"),t("br"),n._v("Lộ Xung không biết chạy chỗ nào báo thù đi, Triệu Nhã hiện tại khẳng định tại kích hoạt thể chất, đi qua cũng không giúp được một tay."),t("br"),t("br"),n._v("Cùng Mạc Hoằng Nhất hẹn xong ngày mai mới đi kết thúc danh sư khảo hạch, nghĩ tới nghĩ lui, hôm nay cũng không có chuyện gì khác, vừa vặn đi vương quốc tàng thư khố nhìn xem sách, sưu tập chút tri thức."),t("br"),t("br"),n._v("Có công chúa dẫn đường, tại tăng thêm tới qua một lần, xe nhẹ đường quen, rất mau tới đến tàng thư khố trước."),t("br"),t("br"),n._v("“Đây chính là chúng ta vương quốc tàng thư khố, bên trong có Thiên Vũ vương quốc sưu tập hơn ngàn năm thư tịch, muốn nhìn loại nào loại hình, đều có thể ở bên trong tìm kiếm!” Mạc Vũ giới thiệu nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Trương Huyền lên tiếng."),t("br"),t("br"),n._v("Nếu là Thiên Vũ vương quốc sưu tập hơn ngàn năm, khẳng định số lượng khổng lồ, đối với hắn mà nói, không cần nhìn cái gì chủng loại, chỉ cần số lượng đầy đủ là đủ."),t("br"),t("br"),n._v("Đọc sách... Hắn thật không chọn."),t("br"),t("br"),n._v("Cho người khác biết ý nghĩ này, tuyệt đối sẽ tại chỗ phun máu."),t("br"),t("br"),n._v("Lợi hại hơn nữa danh sư, dù là đã gặp qua là không quên được, một ngày có thể nhớ kỹ hơn vạn bản thư tịch nội dung, nhận tinh lực ảnh hưởng, cũng chỉ có thể có mang tính lựa chọn đọc sách."),t("br"),t("br"),n._v("Không chỉ là bởi vì không có nhiều thời gian như vậy, càng quan trọng hơn là, thư tịch chính là tiền nhân lưu lại, mỗi người tư tưởng không giống, kiến thức không giống, nội dung tự nhiên cũng không hoàn toàn giống nhau."),t("br"),t("br"),n._v("Rất nhiều kiến giải tự mâu thuẫn, để cho người ta thật giả khó mà nhận ra, nhìn quá nhiều, tương đương với dung hợp vô số người tư tưởng, ngược lại dễ dàng để cho người ta ý chí hỗn loạn, cuối cùng không phân rõ phương hướng."),t("br"),t("br"),n._v("Cho nên, thư tịch cũng không phải là nhìn càng nhiều càng tốt, mà nhất định phải có lựa chọn nhìn, mới có thể để cho người tri thức càng để lâu mệt mỏi càng nhiều, đầu não càng thanh minh."),t("br"),t("br"),n._v("Không tuyển chọn, sách gì đều nhìn, làm không cẩn thận sẽ cho người lâm vào Hỗn Độn, tẩu hỏa nhập ma."),t("br"),t("br"),n._v("Trương Huyền không giống, chỉ cần ghi vào Thiên Đạo thư viện thư tịch, có thể tự động phân biệt sai lầm, thiếu hụt, để cho người ta hoang mang sự tình, đối với hắn mà nói, không đáng kể chút nào."),t("br"),t("br"),n._v("Đi vào tàng thư khố, giương mắt nhìn lại, quả nhiên thấy vô số giá sách đứng sừng sững ở bên trong, lít nha lít nhít tàng thư, cộng lại có lẽ vượt qua ngàn vạn vốn."),t("br"),t("br"),n._v("“Ngươi muốn nhìn cái gì loại hình, ta mang ngươi tới...”"),t("br"),t("br"),n._v("Mạc Vũ công chúa cười nhìn qua."),t("br"),t("br"),n._v("Nàng sở dĩ được người xưng là thiên tài, chủ yếu nhất liền là đọc sách nhiều."),t("br"),t("br"),n._v("Từ sáu tuổi biết chữ bắt đầu, cơ hồ mỗi ngày đều muốn đi qua coi trọng một canh giờ, đồng niên cấp bên trong, nàng tuyệt đối là bác học nhất, đối với cái này tàng thư khố, cũng hiểu rõ nhất!"),t("br"),t("br"),n._v("Cái gì loại hình thư tịch, đặt ở địa phương nào, chỉ cần nói ra, đều có thể rất mau tìm đến."),t("br"),t("br"),n._v("“Ngươi làm việc của ngươi đi, cần nhìn cái gì, bản thân tìm là được, không cần phiền phức công chúa! Hơn nữa, ta đọc sách thời điểm ưa thích yên tĩnh, cho nên...”"),t("br"),t("br"),n._v("Trương Huyền khoát khoát tay một mặt áy náy."),t("br"),t("br"),n._v("Hắn đọc sách thực sự quá mức kinh thế hãi tục, một mạch phi nước đại liền có thể đem thư tịch nội dung thu sạch ghi chép trong óc."),t("br"),t("br"),n._v("Cho người khác nhìn thấy, khẳng định sẽ bị xem như quái vật, vẫn là khiêm tốn một chút tốt."),t("br"),t("br"),n._v("“Vậy ta đi ra ngoài trước!”"),t("br"),t("br"),n._v("Thấy đối phương không cần nàng hỗ trợ, Mạc Vũ có chút thất lạc đi ra ngoài."),t("br"),t("br"),n._v("Tại người thanh niên này trước mặt, thật cảm thấy mình mỹ mạo, không có chút tác dụng chỗ."),t("br"),t("br"),n._v("Khác cùng tuổi khác phái, chỉ cần nhìn thấy, đều sẽ bị dung mạo của nàng hấp dẫn, đủ loại biểu hiện, khoe khoang, mà kẻ trước mắt này, căn bản không quan tâm, coi nàng là thành không khí đồng dạng."),t("br"),t("br"),n._v("Có đôi khi ngẫm lại thật cảm thấy buồn bực."),t("br"),n._v("Chẳng lẽ nàng đối với nữ nhân không có hứng thú? Là cái... Không có năng lực người?"),t("br"),t("br"),n._v("Khụ khụ!"),t("br"),t("br"),n._v("Mạc Vũ công chúa khuôn mặt đỏ lên."),t("br"),t("br"),n._v("Đối phương cao minh như vậy y thuật, cho dù có cũng tất nhiên có thể chữa khỏi, không thể nào... Có thể là mị lực của nàng, còn không có đạt tới hấp dẫn đối phương tình trạng đi!"),t("br"),t("br"),n._v("Cũng đúng."),t("br"),t("br"),n._v("Đối phương loại này liền Mạc Hoằng Nhất cũng không sánh nổi thiên tài, ánh mắt khẳng định cao rối tinh rối mù, nàng thế là tốt rồi, cũng chỉ dừng lại tại nhất đẳng vương quốc phương diện, ánh mắt của đối phương khẳng định càng thêm cao xa, chướng mắt cũng coi như bình thường."),t("br"),t("br"),n._v("Rõ ràng những này, quay đầu nhìn thoáng qua đứng tại trước kệ sách, cúi đầu trầm tư thanh niên, mang theo tiếc nuối, quay người đi ra ngoài."),t("br"),t("br"),n._v("Thiên Tuyết bệ hạ để nàng nghĩ biện pháp cùng Trương Huyền hoặc Liễu hội trưởng một người trong đó giữ gìn mối quan hệ, tốt nhất có thể trở thành tình lữ, hiện tại xem ra... Không thể nào!"),t("br"),t("br"),n._v("Nếu như cho Trương Huyền biết ý nghĩ của nàng, khẳng định sẽ trực tiếp lắc đầu."),t("br"),t("br"),n._v("Không phải hắn đối với mỹ nữ không thích, chỉ là không có gặp để tâm hắn động."),t("br"),t("br"),n._v("Mạc Vũ mặc dù xinh đẹp, còn có thể xinh đẹp qua Triệu Nhã?"),t("br"),t("br"),n._v("Người sau cũng chỉ là học sinh mà thôi."),t("br"),t("br"),n._v("Hơn nữa, kiếp trước sinh hoạt tại tin tức nổ lớn thời kì, dạng gì mỹ nữ chưa thấy qua? Đối với những này sớm có sức miễn dịch."),t("br"),t("br"),n._v("Mấu chốt nhất là, vừa tới một cái hoàn cảnh xa lạ, còn không có triệt để dung hợp, tại tăng thêm thân trúng kịch độc, ba mươi tuổi trước không đạt tới cửu tinh danh sư liền sẽ bỏ xuống, sinh tử bức bách, cũng không tâm tình cân nhắc những thứ này."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Gặp nàng rời đi, tàng thư khố lại không có một người, Trương Huyền một ngụm trọc khí phun ra, đi tới giá sách hàng thứ nhất, ngón tay đưa tới, bàn chân vạch một cái, hướng về phía trước chạy đi."),t("br"),t("br"),n._v("Hoa lạp lạp lạp!"),t("br"),t("br"),n._v("Từng dãy thư tịch, tại hắn chạm đến xuống, tiến vào trong óc, cất giữ tiến thư viện."),t("br"),t("br"),n._v("Tu vi đạt tới Tông Sư cảnh đỉnh phong, linh hồn càng thêm cường đại, thu nhận sử dụng tốc độ cũng càng nhanh."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một cái yên tĩnh ngõ nhỏ, Lộ Xung ngừng lại, miệng lớn thở hổn hển."),t("br"),t("br"),n._v("Rời đi Phỉ Thúy các, hắn liền đổi đã sớm chuẩn bị xong quần áo, cải biến kiểu tóc, ẩn nặc khí tức, vốn cho rằng không có sơ hở nào, thế nào đều không có nghĩ đến, đằng sau còn có người đuổi tới."),t("br"),t("br"),n._v("Liên tục tránh né mấy lần, đổi mấy bộ quần áo, lại còn là cùng chuẩn không bỏ."),t("br"),t("br"),n._v("“Tiến vào Phỉ Thúy các, ta một mực tận lực che giấu, không có để cho người ta nhìn thấy chân chính dung mạo, trốn tới về sau, lại chú ý ẩn nấp, sao có thể một mực đuổi theo?”"),t("br"),t("br"),n._v("Lông mày vung lên, tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Tuổi không lớn lắm, trải qua sinh tử, biết như thế nào mới có thể đào thoát truy sát, mặc dù tại Liễu lão sư chỉ điểm, thực lực tăng lên không ít, đạt tới Tông sư trung kỳ, thực lực bạo tăng, nhưng hắn cũng không lỗ mãng!"),t("br"),t("br"),n._v("Lần này hành thích, chuẩn bị hồi lâu, từ lẫn vào Phỉ Thúy các bắt đầu, vẫn che giấu, không có để cho người ta gặp qua chân thực dung mạo, thế nào chạy trốn đã lâu như vậy, còn sẽ có người một mực theo sau mà đến?"),t("br"),t("br"),n._v("“Như vậy tiếp tục bị đuổi tiếp, không phải cái biện pháp...”"),t("br"),t("br"),n._v("Sắc mặt khó coi."),t("br"),t("br"),n._v("Bằng vào thực lực cùng cơ cảnh, liên tục tránh né mấy phát đuổi bắt, có thể một mực như vậy không phải biện pháp ah."),t("br"),t("br"),n._v("Nhìn đối phương kiên nhẫn bộ dạng, sớm muộn gì đều sẽ bị bắt lấy."),t("br"),t("br"),n._v("“Thiên Vũ học viện không thể đi, như vậy sẽ liên lụy lão sư cùng đồng học, ngoài thành càng không thể đi, mênh mông vô bờ, mênh mông vô biên, rất dễ dàng bị phát hiện! Biện pháp tốt nhất liền là lưu tại vương thành che giấu...”"),t("br"),t("br"),n._v("Trong lòng sốt ruột, nhưng không có bối rối."),t("br"),t("br"),n._v("Nếu như lúc này đi Thiên Vũ học viện, khẳng định sẽ bị người nhận ra hắn là Liễu lão sư học sinh, đến lúc đó, tất nhiên khẳng định sẽ cho lão sư mang đến tai hoạ."),t("br"),t("br"),n._v("Lão sư đối với hắn ân trọng như núi, quyết không thể làm như vậy!"),t("br"),t("br"),n._v("Về phần chạy đi."),t("br"),t("br"),n._v("Ngoài thành mênh mông, một khi mất đi bức tường dòng người yểm hộ, lại càng dễ bị bắt."),t("br"),t("br"),n._v("Lâm gia cao như vậy địa vị, hoàn toàn có thể điều động vương quốc quân đội vòng vây."),t("br"),t("br"),n._v("Thật muốn bị ngăn ở mênh mông dã ngoại, thật sự lên trời không đường, xuống đất không cửa."),t("br"),t("br"),n._v("Đường ra duy nhất, liền là lưu tại vương thành, kiến trúc đông đảo, khắp nơi đều là chen chúc dòng người, dễ dàng hơn che giấu."),t("br"),t("br"),n._v("“Đi nơi nào che giấu có thể để cho Lâm gia không lục ra được?”"),t("br"),t("br"),n._v("Xác định đối sách, Lộ Xung trong đầu nhanh chóng suy tư."),t("br"),t("br"),n._v("Liên tục tránh né thật nhiều lần, đối phương đều có thể đuổi theo, nói rõ khẳng định có đặc thù nào đó phương pháp, không cất chỗ tốt, rất dễ dàng bị phát hiện, chỉ cần bị ngăn chặn, khẳng định hẳn phải chết không nghi ngờ."),t("br"),t("br"),n._v("“Đúng rồi, Thiên Vũ hoàng cung!”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, đột nhiên nghĩ đến một chỗ."),t("br"),t("br"),n._v("Lúc trước gia tộc bọn họ bị diệt, sự tình huyên náo như thế lớn, lại một chút tin tức đều không có truyền ra, Danh Sư đường không người biết được, nếu như nói không có Thiên Vũ vương thất ra tay, tuyệt không tin tưởng!"),t("br"),t("br"),n._v("Đã bọn hắn cấu kết với nhau làm việc xấu, không bằng trực tiếp giấu ở hoàng cung."),t("br"),t("br"),n._v("Lâm gia coi như lợi hại hơn nữa, luôn không khả năng quang minh chính đại điều tra hoàng cung đi!"),t("br"),t("br"),n._v("Có so đo, bàn chân nhất chuyển, thẳng tắp hướng hoàng cung phương hướng bay lượn mà đi."),t("br"),t("br"),n._v("Tu luyện độc thể thành công, hiện tại hắn thực lực, sớm đã đạt tới Tông sư trung kỳ, loại tu vi này Thiên Vũ vương quốc mặc dù không tính là cao cấp nhất, nhưng cũng chênh lệch không xa, bình thường hộ vệ các loại, căn bản không phát hiện được."),t("br"),t("br"),n._v("Thời gian không dài, tiến vào bên trong."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-382"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-384"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);