(window.webpackJsonp=window.webpackJsonp||[]).push([[1614],{1970:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-375-tam-tich-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-375-tam-tich-thu"}},[n._v("#")]),n._v(" Chương 375: Tầm Tích thử")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thiên Vũ hoàng cung."),t("br"),t("br"),n._v("Nhìn vẻ mặt mất tinh thần nữ nhi, Mạc Thiên Tuyết hơi nghi hoặc một chút: “Thế nào? Liễu hội trưởng không đến?”"),t("br"),t("br"),n._v("“Tới...” Mạc Vũ lắc đầu: “Hắn muốn tự mình đọc sách, không cần ta bồi!”"),t("br"),t("br"),n._v("“Được rồi!” Mạc Thiên Tuyết than thở một tiếng: “Xem duyên phận đi, không có duyên phận, nghĩ lại nhiều cũng vô dụng!”"),t("br"),t("br"),n._v("Hắn vẫn nghĩ đem nữ nhi cùng vị này Liễu hội trưởng góp thành một đôi, một khi như thế, tương đương với đem cột vào trên chiến thuyền của mình, về sau coi như Lâm gia, Mạc Hoằng Nhất có cái gì dị động cùng ý nghĩ, cũng muốn cố kỵ một hai."),t("br"),t("br"),n._v("Hiện tại xem ra, có chút ý nghĩ hão huyền."),t("br"),t("br"),n._v("Nữ nhi của hắn mặc dù cũng thuộc về nhân trung long phượng, cùng Liễu hội trưởng, Trương sư loại người này so, vẫn là kém rất lớn một đoạn."),t("br"),t("br"),n._v("Mạc Vũ gật đầu."),t("br"),t("br"),n._v("Nàng cũng không phải đối với Trương Huyền có ý tứ, cho tới nay đều là người khác vây quanh chuyển, đột nhiên gặp phải một vị không quan tâm, dâng lên lòng háo thắng, hiện tại xem ra, không có gì hi vọng, cũng rất nhanh bình phục lại."),t("br"),t("br"),n._v("“Phụ vương, trấn quốc man thú có phải hay không Lâm gia hạ thủ? Bọn hắn nhưng có động tĩnh gì?”"),t("br"),t("br"),n._v("Đem lộn xộn tâm cảnh dứt bỏ, Mạc Vũ lần nữa khôi phục dĩ vãng cơ trí."),t("br"),t("br"),n._v("“Liên quan đến việc này tổng cộng có bảy vị thái giám, có điều, vừa mới tra được liền toàn bộ tự sát!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết ánh mắt ngưng trọng lên: “Ta biết như vậy tra khẳng định tìm không thấy cái gì, hơn nữa chúng ta ở ngoài sáng, đối phương ở trong tối, có thể sớm chuẩn bị, coi như tìm tới, cũng có thời gian đi làm chuẩn bị, làm không cẩn thận sẽ còn nhận được sai lầm tin tức. Cho nên... Công khai truy tra cái này, gióng trống khua chiêng, trên thực tế lại phái người âm thầm điều tra!”"),t("br"),t("br"),n._v("“Âm thầm điều tra?” Mạc Vũ nghi hoặc."),t("br"),t("br"),n._v("“Ừm, đi thăm dò... Độc nơi phát ra!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết mắt sáng lên: “Có thể để cho nửa bước Chí Tôn Kim Vĩ Hùng Sư bệnh nặng, mà chúng ta đều không thể phát giác, loại độc này, ít nhất cũng phải đạt tới nhị phẩm đỉnh phong, thậm chí khoảng cách tam phẩm đều không xa!”"),t("br"),t("br"),n._v("Có thể đối với Kim Vĩ Hùng Sư có tác dụng, loại độc này khẳng định mười phần đáng sợ."),t("br"),t("br"),n._v("Nhất phẩm độc dược, có thể độc chết Tông Sư cảnh trở xuống cường giả; Nhị phẩm đan dược, Tông sư thậm chí nửa bước Chí Tôn, đều có thể bị liên lụy."),t("br"),t("br"),n._v("Cái độc dược này mặc dù trong thời gian ngắn không thể để cho Kim Vĩ Hùng Sư tử vong, lại làm cho hắn gia tốc già yếu, cấp bậc cao, tuyệt đối đạt đến nhị phẩm đỉnh phong."),t("br"),t("br"),n._v("“Có thể xuất ra loại độc dược này, chỉ có Độc điện, đi qua ta điều tra, Lâm gia vừa vặn cùng Hồng Liên thành có lui tới, thậm chí vị kia lợi hại nhất Đại Dược Vương, mấy ngày nay cũng đến vương thành!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết hừ một tiếng, vung tay áo một cái: “Hết thảy hết thảy, đều quá mức trùng hợp. Bởi vậy, ta chuyên môn phái người điều tra, rất nhanh tra ra kết quả, quả thực cùng cái này Lâm gia có không thể trốn tránh trách nhiệm!”"),t("br"),t("br"),n._v("“Vậy chúng ta làm sao bây giờ?”"),t("br"),t("br"),n._v("Nghe được đã xác nhận là Lâm gia ra tay, Mạc Vũ sắc mặt cũng biến thành khó coi."),t("br"),t("br"),n._v("Đối phương hôm nay có thể đối với Kim Vĩ Hùng Sư ra tay, ngày mai liền có khả năng đối bọn hắn vương thất thành viên ra tay."),t("br"),t("br"),n._v("Một khi đối với quyền lợi động tâm tư, dã tâm liền sẽ cùng mọc lên như nấm đồng dạng không ngừng lan tràn, ai cũng không cách nào ngăn chặn."),t("br"),t("br"),n._v("“Mặc dù Kim Vĩ Hùng Sư đạt đến Chí Tôn cảnh, nhưng Lâm gia có Hiên Viên vương quốc làm chỗ dựa, hay là không thể đắc tội!” Biết nữ nhi lo lắng, Mạc Thiên Tuyết lắc đầu."),t("br"),t("br"),n._v("Biết là bọn hắn làm lại như thế nào?"),t("br"),t("br"),n._v("Đối phương không chỉ có tam tinh trận pháp sư tọa trấn, càng có một cái Hiên Viên phong hào vương quốc thái tử phi nữ nhi, căn bản không thể trêu vào."),t("br"),t("br"),n._v("Hiện tại không làm rõ, mọi người giả bộ hồ đồ, có danh sư đường tọa trấn, Lâm gia còn không dám vượt qua."),t("br"),t("br"),n._v("Thật muốn vạch mặt, Hiên Viên vương quốc chỉ cần phái một cái hơi lợi hại cường giả chí tôn, liền có thể đem bọn hắn vương thất, triệt để diệt tuyệt."),t("br"),t("br"),n._v("“Đúng vậy a! Tựa như lúc trước Cù gia... Chúng ta cũng không có cách nào!” Mạc Vũ cũng rõ ràng điểm ấy, than thở một tiếng."),t("br"),t("br"),n._v("“Cù gia mặc dù không phải danh môn vọng tộc, nhưng cũng được cho gia đại nghiệp đại, không nghĩ tới không biết bởi vì cái gì, cả nhà một trăm ba mươi bảy miệng, bị Lâm gia một đêm diệt tuyệt... Chuyện này, ta vẫn nhớ...” Mạc Thiên Tuyết nắm đấm xiết chặt."),t("br"),t("br"),n._v("Hai năm trước, Thiên Vũ vương thành có cái Cù gia không biết thế nào đắc tội Lâm gia, trong vòng một đêm, cả nhà đều bị tàn sát sạch sẽ, phái người hỏi thăm, người còn chưa tới, Lâm gia gia chủ liền tự mình tới, làm cho bọn hắn phong tỏa tin tức, để chuyện này cuối cùng không giải quyết được gì."),t("br"),t("br"),n._v("Thân là quốc vương bệ hạ, ngay cả mình vương quốc thần dân đều không bảo vệ được, cho tới nay chuyện này cũng giống như một cây gai đồng dạng, đâm vào trong lòng."),t("br"),t("br"),n._v("“Kỳ thật cũng trách không được phụ vương, đối phương lúc ấy cầm Hiên Viên vương quốc lệnh bài, nghe nói là dâng thái tử mệnh lệnh, chúng ta chỉ là nhất đẳng vương quốc, thực sự không có cách nào cự tuyệt!” Thấy cha bộ dáng này, Mạc Vũ an ủi."),t("br"),t("br"),n._v("Lúc đó cũng không phải là bọn hắn vương thất e ngại Lâm gia không làm, mà là đối phương cầm Hiên Viên vương quốc thái tử điện hạ thủ lệnh."),t("br"),t("br"),n._v("Đối mặt cái này, bọn hắn loại này cấp thấp vương quốc chỉ có thể tuân thủ, căn bản không có những biện pháp khác."),t("br"),t("br"),n._v("“Ta biết...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết lắc đầu: “Tình hình không bằng người, ta cũng không có cách nào! Cho nên, ta muốn cho ngươi lôi kéo Liễu hội trưởng hoặc là Trương sư, chỉ cần hai cái này thiên tài bên trong một cái trở thành phò mã, bọn hắn coi như muốn động thủ, cũng không dám!”"),t("br"),t("br"),n._v("Những ngày này vẫn muốn để nữ nhi cùng hai cái này thiên tài đến gần một chút, đáng tiếc... Đối phương căn bản không coi trọng nữ nhi của hắn."),t("br"),t("br"),n._v("Nghe được phụ thân lời nói, Mạc Vũ có chút thất lạc, lắc đầu, đang muốn nói chuyện, chỉ thấy một cái thái giám vội vã vọt vào."),t("br"),t("br"),n._v("“Làm sao vậy?” Mạc Thiên Tuyết lông mày nhăn lại."),t("br"),t("br"),n._v("“Hồi bẩm bệ hạ, Lâm gia Tam trưởng lão mang theo rất nhiều người tới, nói muốn chúng ta vương thất cho cái bàn giao...” Thái giám toàn thân run rẩy."),t("br"),t("br"),n._v("“Muốn chúng ta vương thất cho bàn giao? Có ý tứ gì?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết híp mắt lại."),t("br"),t("br"),n._v("Tra ra Lâm gia muốn độc chết trấn quốc man thú, còn không có tìm các ngươi tính sổ sách, thế nào chạy trước tới tìm chúng ta?"),t("br"),t("br"),n._v("Còn bàn giao?"),t("br"),t("br"),n._v("Chẳng lẽ, bọn hắn không cố kỵ nữa, muốn vạch mặt?"),t("br"),t("br"),n._v("“Thuộc hạ không biết, bây giờ đang ở bên ngoài cửa cung, lập tức sẽ xông tới...” Thái giám vội nói."),t("br"),t("br"),n._v("“Đi qua nhìn một chút!”"),t("br"),n._v(" Hừ một tiếng, Mạc Thiên Tuyết cùng Mạc Vũ nhấc chân đi ra ngoài."),t("br"),t("br"),n._v("Rất mau tới đến bên ngoài cửa cung, quả nhiên thấy Lâm gia không ít người đứng ở bên ngoài, từng cái trong ánh mắt mang theo lãnh ngạo."),t("br"),t("br"),n._v("“Thiên Tuyết bệ hạ, ta khuyên ngươi nhanh lên đem người giao ra, mọi người không cần vạch mặt, nếu không, chuyện ngày hôm nay, ta sợ cũng đừng nghĩ kết thúc yên lành!”"),t("br"),t("br"),n._v("Một cái lão giả thấy Mạc Thiên Tuyết đám người đi ra, tiến về phía trước một bước, sắc mặt trầm thấp."),t("br"),t("br"),n._v("“Giao người? Ngươi có ý tứ gì?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết không hiểu ra sao."),t("br"),t("br"),n._v("“Không cần phải giả bộ đâu, ngươi phái người ám sát chúng ta Lâm Lang thiếu gia, dẫn đến hắn hiện tại còn bản thân bị trọng thương, hôn mê bất tỉnh, chẳng lẽ nghĩ không thừa nhận?” Lâm gia Tam trưởng lão híp mắt lại."),t("br"),t("br"),n._v("“Ám sát Lâm Lang thiếu gia?” Mạc Thiên Tuyết càng thêm nghe không hiểu: “Trong này có phải hay không có cái gì hiểu lầm?”"),t("br"),t("br"),n._v("Hắn mặc dù rất muốn diệt đi Lâm gia, nhưng cũng biết hậu quả, một mực cố gắng khắc chế, chẳng lẽ Lâm Lang thiếu gia xảy ra chuyện?"),t("br"),t("br"),n._v("“Hiểu lầm? Hung thủ đã chạy trốn tới hoàng cung, ngay tại cái này thành cung bên trong, ngươi nói hiểu lầm?” Lâm gia Tam trưởng lão cười lạnh."),t("br"),t("br"),n._v("Mạc Thiên Tuyết căn bản không biết chuyện gì xảy ra, đang muốn tiếp tục hỏi thăm, một cái người áo xám lặng lẽ đi tới sau lưng, nói vài câu."),t("br"),t("br"),n._v("Ám Ảnh vệ thành viên."),t("br"),t("br"),n._v("Phỉ Thúy các náo ra động tĩnh lớn như vậy, Lâm gia toàn thể xuất động, Ám Ảnh vệ rất nhanh đến mức đến tin tức."),t("br"),t("br"),n._v("Nghe xong đối phương, Mạc Thiên Tuyết giờ mới hiểu được chuyện gì xảy ra, một mặt buồn bực."),t("br"),t("br"),n._v("Hắn căn bản không có phái hơn người, hơn nữa coi như phái người, cũng muốn làm đến nhất kích tất sát, làm gần chết không sống... Đây không phải chờ lấy đối phương trả thù sao?"),t("br"),t("br"),n._v("“Lâm Huy trưởng lão, ta có thể rõ ràng nói cho ngươi, Lâm Lang công tử bị thương, tuyệt không phải chúng ta vương thất gây nên...”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết đạo."),t("br"),t("br"),n._v("“Không phải?” Tam trưởng lão Lâm Huy cười lạnh: “Muốn nói không phải cũng được, làm phiền ngươi khiến cái này hộ vệ tránh ra, để chúng ta điều tra một cái, chỉ cần bắt được hung thủ, tự nhiên tra ra manh mối!”"),t("br"),t("br"),n._v("Sắc mặt khó coi, Mạc Thiên Tuyết toàn thân xiết chặt."),t("br"),t("br"),n._v("Hoàng cung không riêng gì vương thất căn cơ, thay thế biểu một cái vương quốc tôn nghiêm, thật nếu để cho bọn hắn điều tra, về sau Thiên Vũ vương thất còn mặt mũi nào hiệu lệnh thiên hạ?"),t("br"),t("br"),n._v("“Các ngươi nói hung thủ chạy trốn tới hoàng cung, liền chạy đến hoàng cung, có chứng cứ gì?” Mạc Vũ nhịn không được quát lớn."),t("br"),t("br"),n._v("“Tại hạ gặp qua Mạc sư!”"),t("br"),t("br"),n._v("Nhìn Mạc Vũ một chút, Lâm Huy nhẹ nhàng nở nụ cười: “Dám nói như vậy, đương nhiên phải có căn cứ!”"),t("br"),t("br"),n._v("Vẫy tay một cái."),t("br"),t("br"),n._v("Lập tức có người đi lên phía trước, cầm một cái chiếc lồng."),t("br"),t("br"),n._v("Lồng bên trong một cái tuyết trắng chuột ghé vào bên trong, nhếch lên cái mũi, không ngừng loạn ngửi."),t("br"),t("br"),n._v("“Đây là... ?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết chấn động."),t("br"),t("br"),n._v("Tầm Tích thử, một loại đặc thù man thú, nắm giữ siêu cường cái mũi, bất kỳ cái gì đồ vật chỉ cần bị ngửi truy cập, coi như cách nhau ngàn vạn dặm, đều có thể truy tìm đến."),t("br"),t("br"),n._v("Thứ này, so linh thạch còn muốn hi hữu, coi như Hiên Viên phong hào vương quốc cũng không nhiều thấy, không nghĩ tới Lâm gia lại có một đầu, còn mang theo tới."),t("br"),t("br"),n._v("Nếu thật là thứ này, chỉ cần cái kia thích khách lưu lại mùi, trốn lại xa, đổi lại nhiều quần áo, đều không thể đào thoát."),t("br"),t("br"),n._v("“Không sai, xem ra Thiên Tuyết bệ hạ rất biết hàng, thích khách giấu ở hoàng cung, không phải ta nói, là Tầm Tích thử tìm đến... Thế nào, bệ hạ không tin phán đoán của nó?”"),t("br"),t("br"),n._v("Lâm Huy khẽ nói."),t("br"),t("br"),n._v("“Cái này...” Mạc Thiên Tuyết trầm mặc."),t("br"),t("br"),n._v("Nếu như là Tầm Tích thử tìm đến, nói rõ hung thủ thật giấu ở hoàng cung, chỉ là... Tự mình cũng không cảm kích, đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Đã bệ hạ nói không phải ngươi phái người, chúng ta cũng chỉ là bắt được tổn thương thiếu gia hung thủ, sẽ không trắng trợn tìm kiếm, nếu là bệ hạ khăng khăng ngăn cản, cái kia chính là chột dạ...”"),t("br"),t("br"),n._v("Lâm Huy nói tiếp."),t("br"),t("br"),n._v("Nếu như đầu kia Kim Vĩ Hùng Sư chết rồi, Lâm gia khẳng định sẽ mượn cơ hội lần này, thừa cơ bức thoái vị, để chiếm cứ Thiên Vũ vương quốc ngàn năm vương thất, đi xuống lịch sử sân khấu."),t("br"),t("br"),n._v("Bất quá, đầu này man thú chẳng những không chết, còn đột phá thành cường giả chí tôn, cho dù có ý nghĩ này, cũng muốn chần chờ một cái."),t("br"),t("br"),n._v("Không có hoàn toàn chuẩn bị trước, vẫn là đừng triệt để vạch mặt tốt."),t("br"),t("br"),n._v("Nếu không, Chí Tôn man thú động thủ, coi như Lâm gia có tam phẩm đại trận, chỉ sợ cũng rất khó chống chọi."),t("br"),t("br"),n._v("“Tốt, chúng ta vương thất tuyệt đối không có phái người đi tìm Lâm Lang công tử phiền phức, chỉ cần Tầm Tích thử tìm tới hung thủ, tự nhiên là chân tướng rõ ràng!”"),t("br"),t("br"),n._v("Chần chờ một chút, Mạc Thiên Tuyết gật đầu."),t("br"),t("br"),n._v("Đã đối phương có Tầm Tích thử, tìm người liền sẽ rất đơn giản, không có khả năng trắng trợn xoay loạn, chủ yếu hắn cũng muốn nhìn xem, đến cùng là ai, đánh lén Lâm Lang, lại chạy đến hoàng cung đối bọn hắn tiến hành giá họa."),t("br"),t("br"),n._v("“Bệ hạ đồng ý, chúng ta liền không khách khí!”"),t("br"),t("br"),n._v("Thấy hắn nhả ra, Lâm Huy vẫy tay một cái, thuộc hạ mở ra chiếc lồng, Tầm Tích thử lập tức nhảy ra ngoài, duỗi ra dài nhỏ cái mũi, hít hà, lập tức vọt lên phía trước ra ngoài."),t("br"),t("br"),n._v("“Mọi người đuổi theo!”"),t("br"),t("br"),n._v("Thấy nó dẫn đường, Lâm Huy mang theo mọi người đi theo, Mạc Thiên Tuyết mấy người cũng theo sát trên đó."),t("br"),t("br"),n._v("(Lập tức cao trào, viết không nhanh, yên tâm, già nhai sẽ ủng hộ viết, tranh thủ ngày mai bộc phát một cái, còn có nguyệt phiếu sao?)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-383"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-385"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);