(window.webpackJsonp=window.webpackJsonp||[]).push([[1617],{1969:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-378-sup-đo-moc-hoanh-y-su-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-378-sup-đo-moc-hoanh-y-su-thuong"}},[n._v("#")]),n._v(" Chương 378: Sụp đổ Mộc Hoành y sư (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Có tấm này trang sách, những ngày này thu vào thư viện sách, đều có thể trở thành kiến thức của mình!”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("Từ phía trên huyền vương quốc đi ra đến bây giờ, đã trải qua Thú đường, Độc điện, luyện đan sư công hội, vương quốc Tàng Thư khố... Trong thư viện thu nhận sử dụng thư tịch, đã vượt qua hơn ngàn vạn bản, nếu như toàn bộ biến thành kiến thức của mình, tri thức lượng dự trữ sẽ lập tức bạo tăng."),t("br"),t("br"),n._v("Đến lúc đó, coi như không mượn thư viện, chỉ bằng vào học thức cũng có thể để vô số người ghé mắt, khảo hạch nhị tinh, thậm chí tam tinh danh sư, không thành vấn đề."),t("br"),t("br"),n._v("“Chờ đem Tàng Thư khố sách toàn bộ xem hết lại nói!”"),t("br"),t("br"),n._v("Biết kim sắc trang sách kiếm không dễ, Trương Huyền cũng không nóng nảy sử dụng."),t("br"),t("br"),n._v("Dù sao đây là duy nhất một lần đồ vật, tích lũy thư tịch càng nhiều, tác dụng càng lớn, vương quốc Tàng Thư khố mới nhìn chừng phân nửa, chờ toàn bộ xem hết, tái sử dụng cũng không muộn."),t("br"),t("br"),n._v("Ngưng tụ ra một tấm kim sắc trang sách, trong lòng cao hứng, càng xem trước mắt Lộ Xung càng cảm thấy hài lòng."),t("br"),t("br"),n._v("“Đứng lên đi! Ngươi là đệ tử của ta, ta là lão sư của ngươi, chỉ cần thầy trò tình nghĩa tại... Không có khả năng trơ mắt nhìn xem chịu nhục, mà thờ ơ!”"),t("br"),t("br"),n._v("Đưa tay đem thiếu niên đỡ dậy, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("Lĩnh ngộ thầy trò chân lý, câu nói này nói đến không có chút nào làm ra vẻ, hoàn toàn xuất phát từ nội tâm, tựa như thầy nói thiên bẩm, vang vọng chung quanh, khiến tâm linh người ta không tự chủ được cảm thấy rung động."),t("br"),t("br"),n._v("Ngay cả chính hắn, sau khi nói xong, cũng cảm thấy tâm niệm Thông Đạt, tinh thần vì đó nhất sảng."),t("br"),t("br"),n._v("Nghe nói như thế, Mạc Vũ tú quyền xiết chặt: “Khó trách Triệu Nhã, Mộc Tuyết Tinh đều nguyện ý cho hắn, liều mạng tranh đấu, không tiếc bị thương, nguyên lai đây mới thực sự là ‘Thầy’ hàm nghĩa!”"),t("br"),t("br"),n._v("Nàng là danh sư, cũng có học sinh, cũng thụ qua khóa, nhưng cùng học sinh quan hệ trong đó, giới hạn tại tri thức truyền thụ, nan đề giảng giải."),t("br"),t("br"),n._v("Trước đó một mực không rõ vì cái gì Trương Huyền học sinh có thể bởi vì một câu mà đánh khó bỏ khó phân, không ai nhường ai, nhìn thấy lúc này mới tính toán rõ ràng... Là học sinh đối với lão sư, phát ra từ nội tâm kính nể!"),t("br"),t("br"),n._v("Lão sư làm gương tốt, làm gương sáng cho người khác, mới có thể để cho học sinh cam tâm tình nguyện đi theo, thậm chí vì bảo vệ cho hắn danh dự cố gắng phấn đấu."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết cũng gật gật đầu."),t("br"),t("br"),n._v("Thân là Thiên Vũ vương quốc quốc vương bệ hạ, gặp qua không ít lão sư, cũng đã gặp không ít học sinh, có thể giống như bây giờ, thầy trò tín nhiệm lẫn nhau, thực sự ít càng thêm ít!"),t("br"),t("br"),n._v("Không ít lão sư, học sinh vì lợi ích, mỗi người một ngả, cả đời không qua lại với nhau."),t("br"),t("br"),n._v("Khó trách tiến vào Thiên Vũ học viện chỉ có mấy ngày liền có thể để học sinh hoàn toàn tin phục, phần này nhân cách mị lực đúng là không ít lão sư, thậm chí danh sư, đều không thể làm được."),t("br"),t("br"),n._v("“Muốn người khác thành tâm, đầu tiên muốn bản thân thành tâm...”"),t("br"),t("br"),n._v("Đồng thời cảm khái."),t("br"),t("br"),n._v("Vị này Liễu lão sư, đối với học sinh thành thật mà đối đãi, không lấn không dối gạt, không bỏ không rời, lấy tâm đối xử mọi người, tự nhiên cũng đã nhận được học sinh thành tâm ủng hộ cùng kính trọng."),t("br"),t("br"),n._v("Cảm khái hết, quay đầu nhìn thoáng qua đầy đất hộ vệ cùng trong hôn mê vẫn như cũ không ngừng co giật Lâm Huy trưởng lão, lần nữa đầu to, nhịn không được mở lời."),t("br"),t("br"),n._v("“Khụ khụ, không phải ta muốn phá hư bầu không khí như thế này, Liễu hội trưởng, ngươi lần này là thật... Quá lỗ mãng!”"),t("br"),t("br"),n._v("Các ngươi sư đồ đánh cũng không phải bình thường người, mà là để hắn cũng nhức đầu không dứt Lâm gia!"),t("br"),t("br"),n._v("Coi như Lâm gia kiêng kị ngươi là Y Sư công hội hội trưởng, không dám đem ngươi thế nào, có thể học sinh của ngươi đây? Thân nhân bằng hữu đây?"),t("br"),t("br"),n._v("Lâm gia không chỉ có trận pháp sư công hội chỗ dựa, càng quan trọng hơn là... Phía sau còn có cái Hiên Viên vương quốc thái tử phi!"),t("br"),t("br"),n._v("Lâm Lang cái kia không nói đạo lý tỷ tỷ!"),t("br"),t("br"),n._v("Đây mới là để cho người ta lo lắng nhất."),t("br"),t("br"),n._v("Hai năm trước Cù gia, liền là tỷ tỷ của hắn thăm viếng thời điểm, bộc phát mâu thuẫn, tự mình để một vị Chí Tôn thuộc hạ, hạ thủ."),t("br"),t("br"),n._v("Vương thất biết rất rõ ràng chuyện này, tại Chí Tôn cường giả áp bách dưới, cũng không dám nhiều lời nửa câu!"),t("br"),t("br"),n._v("Hiện tại đồ đệ đánh lén đem Lâm Lang đánh thành trọng thương, nghe nói còn ném vào ao phân, làm hoàn toàn thay đổi; Lão sư càng đem Tam trưởng lão đánh lại không cách nào nhân sự, như là thái giám..."),t("br"),t("br"),n._v("Này bằng với cùng Lâm gia kết không chết không thôi đại thù, lại không có cách nào điều giải!"),t("br"),t("br"),n._v("“Lỗ mãng?”"),t("br"),t("br"),n._v("Đối với hắn lời nói,"),t("br"),t("br"),n._v("Trương Huyền từ chối cho ý kiến, nhìn lại: “Coi như hôm nay không xuất thủ, ngươi cảm thấy Lâm gia sẽ cùng ta nâng cốc nói chuyện vui vẻ?”"),t("br"),t("br"),n._v("“Cái này...” Mạc Thiên Tuyết sửng sốt."),t("br"),t("br"),n._v("Nếu như cái này Lộ Xung thật bị bắt đi, nhờ vào Lâm gia năng lượng, tra ra lão sư của hắn là ai, vô cùng đơn giản."),t("br"),t("br"),n._v("Coi như không bởi vì việc này giận chó đánh mèo, trước mắt vị này cứu chữa Kim Vĩ Hùng Sư, chỉ ra trúng độc, chẳng khác nào phá vỡ Lâm gia đối với vương thất bố trí... Có lẽ đã thành đối phương cái đinh trong mắt, thịt xông vào, sớm muộn gì đều sẽ xung đột chính diện."),t("br"),t("br"),n._v("Giờ phút này, bất quá là đem xung đột trước thời hạn mà thôi."),t("br"),t("br"),n._v("“Lộ Xung một ngày gọi ta lão sư, ta liền không thể làm đến khoanh tay đứng nhìn!”"),t("br"),t("br"),n._v("Hừ một tiếng, Trương Huyền không trong vấn đề này tiếp tục xoắn xuýt, thản nhiên nói: “Dư sự tình, các ngươi xử lý đi, ta sách còn chưa xem xong, nếu lại đi xem một hồi. Còn Lộ Xung, ngươi ngay tại một bên chờ ta, trước đừng đi ra ngoài...”"),t("br"),t("br"),n._v("Nói xong trực tiếp hướng Tàng Thư khố đi đến, rất nhanh biến mất ở trước mặt mọi người."),t("br"),t("br"),n._v("“Đọc sách?”"),t("br"),t("br"),n._v("“Lúc này, còn có tâm tình đọc sách?”"),t("br"),t("br"),n._v("Thấy hắn quay người đọc sách đi, Mạc Thiên Tuyết, Mạc Vũ hai mặt nhìn nhau, một hồi mê muội."),t("br"),t("br"),n._v("Đại ca, đây chính là Lâm gia, tới đối mặt, vương thất đều lo lắng không thôi, nơm nớp lo sợ, ngươi đánh người ta trưởng lão, ném xuống đất liền mặc kệ..."),t("br"),t("br"),n._v("Đọc sách?"),t("br"),t("br"),n._v("Cái này Tàng Thư khố chừng mấy trăm vạn bản thư tịch, thật muốn nhìn, mười năm, tám năm cũng không nhìn xong, nhìn nhiều một hồi, có cái lông gà dùng?"),t("br"),t("br"),n._v("“Cái này... Gia hỏa, có lẽ... Là ưa thích đọc sách!” Nhẫn nhịn cả buổi, Mạc Vũ mới biệt xuất một câu."),t("br"),n._v("Cùng vị này Trương sư tính toán nhận biết rất lâu, ban đầu ở Thú đường, cũng tranh cãi muốn nhìn sách;"),t("br"),t("br"),n._v("Về sau đi Hồng Liên thành, cứu chữa Đại Dược Vương muốn thù lao liền là thư tịch."),t("br"),t("br"),n._v("Hiện tại Lâm gia đều nhanh đánh tới, thế mà còn phải xem sách... Đây là đối với sách có bao nhiêu yêu quý ah!"),t("br"),t("br"),n._v("Khó trách trẻ tuổi như vậy liền sẽ nhiều như vậy chức nghiệp, khả năng cùng cái này đọc sách, có rất lớn quan hệ."),t("br"),t("br"),n._v("Trong lòng hai người suy nghĩ lung tung, đều theo đuổi suy nghĩ riêng của mình."),t("br"),t("br"),n._v("Kỳ thật Trương Huyền không thèm để ý, cũng không phải là giả vờ giả vịt, mà là Lâm gia tại Thiên Vũ vương quốc một tay che trời, đối với hắn mà nói, thật đúng là không tính là gì."),t("br"),t("br"),n._v("Hiện tại hắn thực lực mặc dù chỉ là Tông sư đỉnh phong, nhưng bằng mượn thư viện, Minh Lý Chi Nhãn cùng nửa bước Chí Tôn so ra đều không kém nhiều!"),t("br"),t("br"),n._v("Coi như thực lực không bằng, phía sau còn có Thiết Xỉ Khiếu Thiên thú cái này vương bài, lại lớn không được... Bại lộ Trương sư thân phận là được!"),t("br"),t("br"),n._v("Thiên tài danh sư, đường chủ Khương Thư sư huynh, danh sư Dương Huyền học sinh... Có những này thân phận tại, coi như đối phương to gan, chỉ sợ cũng không dám làm gì mình!"),t("br"),t("br"),n._v("Cho nên... Để Mạc Thiên Tuyết đám người kiêng dè không thôi Lâm gia, trong mắt hắn căn bản không phải chuyện gì."),t("br"),t("br"),n._v("Cùng hắn lãng phí thời gian, còn không bằng đi trước đọc sách tới thực tế."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Lâm gia."),t("br"),t("br"),n._v("Xa hoa nhất rộng lớn đại sảnh, Lâm gia trưởng lão nghị sự nơi chốn, bình thường người không phận sự miễn vào, coi như trưởng lão tiến vào, đều muốn sớm xin, mà giờ khắc này, dòng người cuồn cuộn, hỗn loạn tưng bừng."),t("br"),t("br"),n._v("Hai cái thanh niên nằm thẳng dưới đất, mặt sưng phù cùng đầu heo, chỉ từ bề ngoài đã rất khó phân biệt ra được đến cùng là ai."),t("br"),t("br"),n._v("Chính là bị Lộ Xung cuồng đánh một trận Lâm Lang cùng Quý Mặc công tử."),t("br"),t("br"),n._v("Việc này trên thân hai người tang vật đã bị rửa ráy sạch sẽ, có điều, mùi thối đã xông vào làn da, mặc kệ tắm bao nhiêu lần đều xử lý không xong, từng cái ruồi xanh không ngừng vờn quanh, tựa hồ tìm được mỹ thực, nhìn thấy người chán ghét muốn phun ra."),t("br"),t("br"),n._v("Gian phòng bên trong đứng đấy mấy người, chính là Lâm gia gia chủ Lâm Nhược Thiên đám người."),t("br"),t("br"),n._v("“Lâm gia chủ, đây rốt cuộc chuyện gì xảy ra? Nhi tử ta liền đi một chuyến Phỉ Thúy các, làm sao lại biến thành như vậy?”"),t("br"),t("br"),n._v("Một hồi ồn ào, một cái cao lớn trung niên nhân đi nhanh tới, tràn đầy lo lắng."),t("br"),t("br"),n._v("Quý gia gia chủ Quý Phong!"),t("br"),t("br"),n._v("Đi vào đại sảnh, nhìn thấy nằm trên mặt đất như là người chết nhi tử, sắc mặt tái xanh, một cơn lửa giận qua lại nhúc nhích, tựa như sắp bạo tạc."),t("br"),t("br"),n._v("Quý Mặc công tử chạy tới tìm Lâm Lang công tử biện hộ cho, hắn là biết đến."),t("br"),t("br"),n._v("Thậm chí còn mang theo cổ vũ hương vị, nếu không, bị gia tộc giam lại, làm sao có thể ngồi xe ngựa này mang theo gã sai vặt quang minh chính đại rời đi?"),t("br"),t("br"),n._v("Vốn cho rằng tìm tới Lâm Lang, nhờ vào Lâm gia biện hộ cho, có thể tuỳ tiện giải quyết, để con của hắn miễn ở trách cứ, nằm mơ đều không có nghĩ đến, Trương sư người đều không thấy, nhi tử liền bị đánh thành đầu heo."),t("br"),t("br"),n._v("Phỉ Thúy các, Lâm Lang sản nghiệp, vương thành động tiêu tiền!"),t("br"),t("br"),n._v("Trong này hoàng cung quý tộc cũng không dám làm loạn, Quý Mặc lại bị đánh hôn mê bất tỉnh, bất tỉnh nhân sự... Đến cùng là ai? Còn có thiên lý sao? Còn có vương pháp sao?"),t("br"),t("br"),n._v("Quá phách lối đi!"),t("br"),t("br"),n._v("“Quý gia chủ đừng có gấp, Tam trưởng lão đã dẫn người đuổi bắt hung thủ, hẳn là rất nhanh liền có kết quả!”"),t("br"),t("br"),n._v("Lâm Nhược Thiên nói: “Hơn nữa, không chỉ con của ngươi bị đánh, ngay cả Lang nhi cũng bị độc thủ!”"),t("br"),t("br"),n._v("“Lâm Lang công tử cũng bị đánh?”"),t("br"),t("br"),n._v("Quý Phong chỉ lấy đến nhi tử bị đánh tin tức, cũng không biết sự tình khác, nhịn không được sững sờ, vội vàng hỏi: “Lâm Lang công tử ở đâu? Có nghiêm trọng không?”"),t("br"),t("br"),n._v("“Không cùng ngươi gia công tử cùng một chỗ nằm sao?” Lâm Nhược Thiên sắc mặt khó coi."),t("br"),t("br"),n._v("Ngươi đây là ánh mắt gì? Chỉ xem con của ngươi trọng thương, nhi tử ta cũng nằm, thế nào không thấy được?"),t("br"),t("br"),n._v("“Đây là... Lâm Lang công tử?”"),t("br"),t("br"),n._v("Lúc này mới chú ý nhi tử bên cạnh cũng nằm một cái, Quý Phong giật nảy mình."),t("br"),t("br"),n._v("Con của hắn mặc dù bị thương nặng, chí ít còn có thể nhìn ra đại khái bộ dáng, mơ hồ có thể nhận ra. Có thể vị này Lâm Lang công tử, đã bị đánh hình người cũng không có, lại thêm toàn thân xú khí huân thiên, nếu không phải Lâm gia chủ mở lời thừa nhận, thế nào cũng không nghĩ đến, cái này giống như đầu heo gia hỏa... Là danh chấn vương thành đệ nhất công tử."),t("br"),t("br"),n._v("Chỉ từ bề ngoài căn bản nhìn không ra ah..."),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Nuốt nước bọt, Quý Phong nhịn không được nói."),t("br"),t("br"),n._v("Vừa rồi cho rằng đánh con của hắn, cũng đã vô pháp vô thiên, hiện tại biết liền Lâm Lang công tử đều đánh, mới biết được, đã không phải là vô pháp vô thiên, mà là căn bản không quản trời cao đất rộng!"),t("br"),t("br"),n._v("“Ta nhận được tin tức đi qua lúc, đã như vậy!” Lâm Nhược Thiên lắc đầu."),t("br"),t("br"),n._v("Cụ thể tình huống gì, hắn cũng nói không chính xác, còn phải đợi Tam trưởng lão trở về mới có thể xác nhận."),t("br"),t("br"),n._v("“Cái kia... Bọn hắn bị thương nặng, cần nhanh trị liệu...”"),t("br"),t("br"),n._v("Thấy hắn cũng không rõ ràng, Quý Phong không trong vấn đề này dây dưa, vội nói."),t("br"),t("br"),n._v("“Thương thế của bọn hắn rất nặng, hơn nữa có chút cổ quái, bình thường y sư rất khó trị liệu. Ta đã mời Y Sư công hội Mộc Hoành y sư, dựa theo thời gian, hẳn là chẳng mấy chốc sẽ đến...”"),t("br"),t("br"),n._v("Lâm Nhược Thiên gật đầu."),t("br"),t("br"),n._v("“Ừm!” Quý Phong gật đầu."),t("br"),t("br"),n._v("Y sư bên trong loại trừ gần nhất danh khí lớn nhất cái kia Liễu hội trưởng, chỉ sợ cũng chỉ có Mộc Hoành y sư."),t("br"),t("br"),n._v("Làm tiền nhiệm hội trưởng, tam tinh y sư, coi như địa vị rất cao, kiêu ngạo lớn nha, Lâm gia tự mình mời, vẫn là không dám cự tuyệt."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-386"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-388"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);