(window.webpackJsonp=window.webpackJsonp||[]).push([[1618],{1973:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-379-sup-đo-moc-hoanh-y-su-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-379-sup-đo-moc-hoanh-y-su-ha"}},[n._v("#")]),n._v(" Chương 379: Sụp đổ Mộc Hoành y sư (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chờ thời gian không dài, Mộc Hoành y sư ngay tại một cái lão giả dẫn đầu xuống, đi đến.."),t("br"),t("br"),n._v("“Gặp qua Lâm gia chủ, Quý gia chủ!”"),t("br"),t("br"),n._v("Liền ôm quyền, Mộc Hoành thần sắc thản nhiên."),t("br"),t("br"),n._v("Thân là tam tinh y sư, có bản thân ngạo khí, hai vị này mặc dù là quyền nghiêng vương thành gia chủ, còn không đến mức khúm núm."),t("br"),t("br"),n._v("“Còn xin Mộc tông sư ra tay cứu trị tiểu nhi...” Lâm Nhược Thiên vội nói."),t("br"),t("br"),n._v("“Có ta ở đây, yên tâm!”"),t("br"),t("br"),n._v("Cho đối phương làm cam đoan, Mộc Hoành mặt không biểu tình, mấy bước đi tới Lâm Lang, Quý Mặc công tử hai người trước mặt, ngồi xổm xuống, cẩn thận quan sát."),t("br"),t("br"),n._v("Thế giới này mặc dù không có Trung y “Bắt mạch”, nhưng cũng có nhìn, ngửi, hỏi, ba loại cơ sở xem bệnh thủ đoạn."),t("br"),t("br"),n._v("Coi như đối phương hôn mê không thể hỏi thăm, làm tam tinh y sư, cũng vẫn là có thể nhìn ra đại khái."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Thấy hắn nhìn hồi lâu, không nói một câu, Lâm Nhược Thiên nhịn không được nhìn qua."),t("br"),t("br"),n._v("Lâm Lang là con trai độc nhất của hắn, luôn luôn sủng ái có thừa, hiện tại bộ dáng này, so với ai khác đều gấp."),t("br"),t("br"),n._v("“Thương thế có chút nặng, là bị người dùng ngoại lực mạnh mẽ đánh ra tới. Nếu như đoán không sai, đem hắn đả thương người này, hẳn là am hiểu thân thể tu luyện!”"),t("br"),t("br"),n._v("Triệt để kiểm tra hết, Mộc Hoành thở ra một hơi, đứng dậy, hai tay chắp sau lưng, thản nhiên nói."),t("br"),t("br"),n._v("Người tu luyện cùng người chiến đấu, đều dùng chân khí, dễ dàng tạo thành nội thương, cũng chính là kinh mạch, nội tạng bị hao tổn. Mà trước mắt cái này, răng đánh tróc ra, trên người nứt xương, da thịt xé mở, có thể tuỳ tiện phân biệt ra được, vì ngoại lực gây nên."),t("br"),t("br"),n._v("Loại tình huống này, hoặc là am hiểu thân thể cường giả ra tay, hoặc là liền là man thú."),t("br"),t("br"),n._v("“Thân thể cường giả?” Lâm Nhược Thiên lông mày nhíu một cái."),t("br"),t("br"),n._v("Chưa nghe nói qua Thiên Vũ vương thành, có cái gì thân thể đạt tới cấp bậc tông sư cường giả a?"),t("br"),t("br"),n._v("Trong lòng kỳ quái, vẫn còn có chút lo lắng nhìn qua: “Mộc tông sư nhưng có biện pháp trị liệu? Thực sự không được... Ta tìm người hội chẩn hoặc là... Mời Liễu hội trưởng tới xem một chút!”"),t("br"),t("br"),n._v("“Yên tâm, nho nhỏ ngoại thương mà thôi, không tính là gì!”"),t("br"),t("br"),n._v("Thấy hắn trong giọng nói mang theo nghi vấn, còn nói ra Liễu hội trưởng, Mộc Hoành sầm mặt lại, bàn tay lớn hất lên."),t("br"),t("br"),n._v("Trước đó hắn tại Thiên Vũ vương quốc y thuật giới, cao cao tại thượng, không ai dám chất vấn, từ khi toát ra cái kia Liễu hội trưởng, ai cũng hoài nghi, để hắn tràn đầy buồn bực."),t("br"),t("br"),n._v("Chẳng lẽ loại trừ Liễu hội trưởng, liền không có người có thể chữa bệnh?"),t("br"),t("br"),n._v("“Làm phiền Tông sư!”"),t("br"),t("br"),n._v("Nghe có thể trị liệu, Lâm Nhược Thiên cùng Quý Phong tất cả đều nhẹ nhàng thở ra."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Không tại nói nhảm, Mộc Hoành y sư cổ tay khẽ đảo, lòng bàn tay thêm ra một cái bình ngọc, nhẹ nhàng run lên, dược dịch chảy ra đến, chậm rãi nhỏ tại Lâm Lang trần trụi trên da."),t("br"),t("br"),n._v("Xì xì xì!"),t("br"),t("br"),n._v("Cùng dược dịch tiếp xúc, miệng vết thương lập tức toát ra vô số bọt khí, trước đó nứt ra lỗ hổng, lấy mắt thường có thể thấy được tốc độ nhanh khép lại."),t("br"),t("br"),n._v("“Lợi hại...”"),t("br"),t("br"),n._v("Nhìn thấy hiệu quả rõ ràng như vậy, Lâm Nhược Thiên, Quý Phong con mắt đồng thời sáng lên."),t("br"),t("br"),n._v("Không hổ là tam tinh y sư, ra tay quả nhiên bất phàm!"),t("br"),t("br"),n._v("“Đây là ta độc môn phối trí dược dịch, đối ngoại tổn thương có hiệu quả, bất kỳ cái gì vết thương chỉ cần tiếp xúc, đều có thể rất nhanh khép lại, sinh bắp thịt dài thịt, thần diệu phi phàm...”"),t("br"),t("br"),n._v("Thấy hai người chấn kinh, Mộc Hoành y sư hài lòng gật đầu, đang muốn nói tiếp, chỉ thấy Lâm Nhược Thiên sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("“Không đúng, Mộc tông sư ngươi nhìn...”"),t("br"),t("br"),n._v("Thấy hắn biểu lộ kỳ quái, theo ngón tay nhìn lại, xem xét phía dưới, Mộc Hoành lập tức sửng sốt."),t("br"),t("br"),n._v("Chỉ thấy vừa rồi khép lại vết thương, lần nữa xé rách, thậm chí so trước đó lỗ hổng còn lớn hơn, hơn nữa không ngừng có máu tươi tràn ra."),t("br"),t("br"),n._v("Lấy một thí dụ, trước đó vết thương chỉ có hai ngón tay, trị liệu thoáng cái, không những không thấy tốt, còn biến thành ba ngón, thậm chí bắt đầu chảy máu!"),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Mới vừa khoe khoang khoác lác, hắn thuốc là độc môn phối phương, liền xuất hiện loại sự tình này, Mộc Hoành sắc mặt khó coi, vội vàng đem dược dịch té ở một nơi khác."),t("br"),t("br"),n._v("Tê lạp!"),t("br"),t("br"),n._v("Cùng vừa rồi đồng dạng, vừa mới mọc tốt cơ bắp lần nữa xé rách, trở nên càng thêm dữ tợn."),t("br"),t("br"),n._v("“Không đúng...”"),t("br"),t("br"),n._v("Thế nào đều không có nghĩ đến sẽ xuất hiện loại biến cố này, cổ tay khẽ đảo, Mộc Hoành y sư lại lấy ra bảy, tám loại dược dịch, phân biệt ngã xuống mấy chỗ không giống vết thương."),t("br"),t("br"),n._v("Bất quá, những này trước đó đối ngoại tổn thương có thần kỳ hiệu quả dược dịch, cùng hắn lần thứ nhất dùng đồng dạng, vừa mới bắt đầu hữu dụng, thời gian không dài, lại lần nữa sụp ra, vừa rồi đã đình chỉ đổ máu thần thái an tường Lâm Lang, lúc này, không những không chữa khỏi, huyết dịch ngược lại càng chảy càng nhiều, vết thương càng lúc càng lớn, sắc mặt cũng càng ngày càng trắng."),t("br"),t("br"),n._v("“Mộc tông sư còn xin... Ngừng một chút!”"),t("br"),t("br"),n._v("Thấy hắn lại lấy ra mười cái chai chai lọ lọ, dự định lần lượt thí nghiệm, Lâm Nhược Thiên run run thoáng cái, vội vàng ngăn cản."),t("br"),t("br"),n._v("Mộc Hoành Tông sư, ta là mời ngươi tới trị bệnh cứu người, không phải tới giết người... Còn như vậy trị đi xuống, ta sợ người còn không có chữa khỏi, liền sẽ bởi vì đổ máu quá nhiều, trực tiếp treo ở đây!"),t("br"),t("br"),n._v("“Yên tâm, ta tự có đúng mực!”"),t("br"),t("br"),n._v("Sầm mặt lại, cắt ngang đối phương, Mộc Hoành y sư rất mau đem trong tay dược vật toàn bộ thử một lần, lúc này mới mày nhíu lại gấp, sắc mặt khó coi."),t("br"),t("br"),n._v("Tất cả dược liệu toàn bộ thử hết, lúc này Lâm Lang cũng hơi thở mong manh, khoảng cách tắt thở cũng không kém quá xa."),t("br"),t("br"),n._v("“Đến cùng thế nào?”"),t("br"),t("br"),n._v("Lâm Nhược Thiên bờ môi run rẩy."),t("br"),t("br"),n._v("Vừa rồi nhi tử mặc dù trọng thương, chí ít còn giống sống, kết quả trị liệu nửa ngày, càng ngày càng kém, bất cứ lúc nào cũng sẽ tử vong, để hắn tràn đầy buồn bực, nhưng lại không thể lửa."),t("br"),t("br"),n._v("Cái kia Liễu hội trưởng, hỏng chuyện tốt của hắn, ở sâu trong nội tâm không muốn cùng chi tiếp xúc, nếu không, cũng không có khả năng trực tiếp mời Mộc Hoành y sư."),t("br"),t("br"),n._v("Nếu như vị này tiền nhiệm hội trưởng đều không giải quyết được, thật không biết nên làm gì bây giờ."),t("br"),t("br"),n._v("“Đi qua phán đoán của ta, Lâm Lang công tử tổn thương, không riêng gì ngoại thương đơn giản như vậy, có lẽ... Còn trúng độc!”"),t("br"),t("br"),n._v("Chần chờ một lát, Mộc Hoành chậm rãi nói."),t("br"),n._v(" “Trúng độc?”"),t("br"),t("br"),n._v("Lâm Nhược Thiên sững sờ."),t("br"),t("br"),n._v("Chẳng lẽ đối với hắn nhi tử xuất thủ người kia, còn là một vị độc sư hay sao?"),t("br"),t("br"),n._v("Nếu không, phổ thông độc, đối với Tông sư cường giả, cơ hồ là không có cái gì tác dụng."),t("br"),t("br"),n._v("“Không sai! Cái này độc mười phần kỳ quặc, ta cũng không nhận ra được, chẳng qua cũng không cần lo lắng quá mức, ta một lát nữa dùng chân khí nghĩ biện pháp đem bức đi ra, chỉ cần không có độc, miệng vết thương của hắn nên sẽ không nứt toác, chẳng mấy chốc sẽ khỏi hẳn!”"),t("br"),t("br"),n._v("Mộc Hoành y sư nói tiếp."),t("br"),t("br"),n._v("Thí nghiệm mấy loại dược liệu, hắn cũng rốt cục xác nhận, đối phương khẳng định là trúng độc, nếu không, không có khả năng trên người người khác không có cái gì vấn đề dược vật, ở trên người hắn liên tiếp xuất hiện biến cố."),t("br"),t("br"),n._v("Chỉ bất quá, độc chủng loại ngàn ngàn vạn vạn, hắn không phải chuyên môn độc sư, căn bản không có cách nào xác nhận."),t("br"),t("br"),n._v("“Vậy làm phiền Mộc tông sư!” Lâm Nhược Thiên liền vội vàng gật đầu."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Lên tiếng, Mộc Hoành đem Lâm Lang đỡ dậy, khoanh chân ngồi tại sau lưng, bàn tay để ở lưng của hắn, một đạo chân khí chậm rãi truyền vào đi vào."),t("br"),t("br"),n._v("Giúp người khác trừ độc, cần phương pháp đặc thù, thân là tam tinh y sư, chuyên môn học qua, hết sức quen thuộc."),t("br"),t("br"),n._v("Chân khí dọc theo đối phương kinh mạch, từng tấc từng tấc dò xét."),t("br"),t("br"),n._v("“Chính là chỗ này...”"),t("br"),t("br"),n._v("Rất nhanh, độc vị trí liền bị xuất hiện, con mắt nhịn không được sáng lên."),t("br"),t("br"),n._v("Trúng độc về sau, độc sẽ theo kinh mạch chạy toàn thân, muốn khu trừ, trước muốn tìm tới ở nơi nào, nếu không, thủ đoạn lại nhiều, cũng là vô dụng."),t("br"),t("br"),n._v("“Lâm Lang công tử tu luyện Lâm gia công pháp, cùng ta tu luyện chân khí thuộc về hai cái phái, không hợp tính, chỉ cần khống chế chân khí bao vây, đem độc bức ra bên ngoài cơ thể là đủ...”"),t("br"),t("br"),n._v("Tìm tới vị trí, Mộc Hoành không có hưng phấn, ngược lại càng thêm cảnh giác."),t("br"),t("br"),n._v("Bệnh nhân trúng độc, nói cách khác, độc đã cùng chân khí của hắn, thân thể dung hợp được, muốn khu trừ, trước muốn chân khí của mình tính chất cùng đối phương không giống mới được."),t("br"),t("br"),n._v("Nếu không chân khí tương dung, không những không thể đem độc khu trừ đi, còn rất dễ dàng chạm phải đến trên người mình, được không bù mất."),t("br"),t("br"),n._v("Lâm Lang tu luyện Lâm gia công pháp, cùng hắn hoàn toàn khác biệt, thật giống như dầu cùng nước quan hệ, chỉ cần cẩn thận một chút, hẳn là rất dễ dàng đem nọc độc tống ra bên ngoài cơ thể."),t("br"),t("br"),n._v("Chân khí chậm rãi chảy xuôi, Mộc Hoành y sư cẩn thận từng li từng tí khống chế cùng nọc độc tiếp xúc, tới đụng một cái."),t("br"),t("br"),n._v("Đang định đem thứ này trục xuất khỏi bên ngoài cơ thể, “Ầm!” Một tiếng, trong nháy mắt giống như là diêm gặp xăng."),t("br"),t("br"),n._v("Nọc độc giống như là truyền nhiễm đồng dạng, trong chốc lát chui vào chân khí của hắn bên trong."),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại, còn chưa kịp phản ứng, liền thấy bàn tay nhanh chóng biến thành đen, ngay sau đó kịch độc, lập tức lan tràn lên phía trên, chui vào cánh tay."),t("br"),t("br"),n._v("“Cái này... Cái này... Làm sao có thể?”"),t("br"),t("br"),n._v("Sắc mặt trắng nhợt, Mộc Hoành suýt chút nữa điên rồi."),t("br"),t("br"),n._v("Gặp qua bỗng nhiên, chưa thấy qua mạnh như vậy!"),t("br"),t("br"),n._v("Trước kia hắn đã từng tự tay giúp người giải quyết qua nhị phẩm độc dược, dựa theo tình huống bình thường, chỉ cần cùng đối phương chân khí tương dung, sẽ rất khó đối với những khác người chân khí tạo thành uy hiếp..."),t("br"),t("br"),n._v("Dù sao chân khí chỉ cần không đồng tông đồng nguyên, cơ bản đều là lẫn nhau bài xích, rất khó dung hợp!"),t("br"),t("br"),n._v("Đây là cái quỷ gì?"),t("br"),t("br"),n._v("Thế nào chân khí mới đụng một cái bên trên, liền cùng đốt lên thuốc nổ đồng dạng?"),t("br"),t("br"),n._v("“Nguy rồi, khẳng định là... Độc này bên trong, ẩn chứa càng tinh khiết hơn chân khí, loại này chân khí có thể cùng bất luận cái gì chân khí tương dung...”"),t("br"),t("br"),n._v("Linh quang chợt lóe, nhớ tới một bản y thuật bên trên ghi lại sự tình."),t("br"),t("br"),n._v("Có thể cùng chân khí của hắn nhanh dung hợp, hơn nữa đem độc truyền tới, chỉ có một khả năng, cái kia chính là... Hạ độc người, chân khí cực hắn tinh thuần, có thể cùng chân khí của hắn hoàn mỹ dung hợp, không có chút nào phản kháng chỗ trống."),t("br"),t("br"),n._v("“Hết rồi...”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái."),t("br"),t("br"),n._v("Cho người ta giải độc không có giải thành, ngược lại bản thân trúng độc..."),t("br"),t("br"),n._v("Mộc Hoành khóc không ra nước mắt."),t("br"),t("br"),n._v("Mới vừa rồi còn nghĩ đến hảo hảo trị liệu, cho Lâm gia lưu cái ấn tượng tốt, triệt để đánh mặt, làm cho tất cả mọi người đều biết, hắn Mộc Hoành không phải ăn chay, so với cái kia Liễu hội trưởng, cũng không kém chút nào, kết quả là làm ra tình huống này..."),t("br"),t("br"),n._v("Chuyện này là sao!"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, vội vàng lấy ra một viên giải độc viên thuốc ngậm vào trong miệng, vừa mới hóa giải thoáng cái, liền thấy Lâm Nhược Thiên một mặt nóng nảy đi tới trước mặt, bàn tay ở trên người hắn đụng một cái: “Mộc tông sư, đã sinh cái gì sự tình, ngươi thế nào...”"),t("br"),t("br"),n._v("“Đừng động...”"),t("br"),t("br"),n._v("Vốn chính là cưỡng ép áp chế khí độc lan tràn, đối phương đụng một cái, chân khí rốt cuộc khống chế không nổi, ẩn chứa độc chân khí xông phá hai tay ràng buộc, chui vào toàn thân của hắn."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Một ngụm máu tươi cuồng phún mà ra, Mộc Hoành y sư thân thể nhoáng một cái, không thể kiên trì được nữa."),t("br"),t("br"),n._v("“Trong cơ thể hắn độc cổ quái...”"),t("br"),t("br"),n._v("Nói còn chưa dứt lời, mắt tối sầm lại, hôn mê bất tỉnh."),t("br"),t("br"),n._v("“Mộc y sư...”"),t("br"),t("br"),n._v("Thấy đường đường tam tinh y sư, Y Sư công hội tiền nhiệm y sư, trực tiếp té xỉu, Lâm Nhược Thiên cùng Quý Phong tất cả đều mộng."),t("br"),t("br"),n._v("Ai có thể nói cho ta biết đã sinh cái gì?"),t("br"),t("br"),n._v("Mộc Hoành y sư, ngươi không phải đến trị thương sao?"),t("br"),t("br"),n._v("Thế nào càng chậm Lâm Lang thương thế càng nghiêm trọng hơn... Không chỉ như thế, ngươi chính mình mặt mũi tràn đầy đen kịt, ngất đi tính là gì?"),t("br"),t("br"),n._v("Cho người ta trị thương, kết quả bản thân lại trúng độc choáng..."),t("br"),t("br"),n._v("Mộc Hoành, ngươi vừa rồi không trả lời thề son sắt nói “Có ngươi tại, yên tâm” sao?"),t("br"),t("br"),n._v("Yên tâm, liền tài nghệ này... Ta yên tâm cái rắm ah!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-387"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-389"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);