(window.webpackJsonp=window.webpackJsonp||[]).push([[1619],{1974:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-380-đanh-đen-tan-cua"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-380-đanh-đen-tan-cua"}},[n._v("#")]),n._v(" Chương 380: Đánh đến tận cửa")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Làm sao đây?”"),i("br"),i("br"),n._v("Người không chữa khỏi, y sư trước ngất đi, Lâm Nhược Thiên, Quý Phong hai mặt nhìn nhau, đều có chút choáng."),i("br"),i("br"),n._v("Lâm Lang, Quý Mặc đến cùng gặp cái cái gì dạng đối thủ, đem bọn hắn mới đưa bọn hắn làm thành như vậy?"),i("br"),i("br"),n._v("“Mộc Hoành y sư y thuật, tại Thiên Vũ vương quốc đều có thể sắp xếp bên trên thứ tự, hắn đều không được, đổi lại cái khác y sư khẳng định cũng không có cách nào!”"),i("br"),i("br"),n._v("Một lát sau, Quý Phong đạo: “Trừ phi... Dựa theo hắn nói như vậy, trước hết nghĩ biện pháp cho bọn hắn giải độc!”"),i("br"),i("br"),n._v("“Giải độc? Liền độc là cái gì cũng không biết, thế nào giải?”"),i("br"),i("br"),n._v("Lâm Nhược Thiên lắc đầu."),i("br"),i("br"),n._v("Độc vật này không phải cái gì người nói giải liền có thể giải, không xác định chủng loại, không xác định lượng thuốc, muốn giải quyết, gần như không có khả năng!"),i("br"),i("br"),n._v("“Đúng vậy a, muốn giải quyết liền Mộc tông sư đều bó tay toàn tập kịch độc, có lẽ chỉ có chân chính độc sư mới được...”"),i("br"),i("br"),n._v("Quý Phong gật đầu."),i("br"),i("br"),n._v("“Độc sư? Đúng, Hồng Liên thành Đại Dược Vương ngay tại vương thành, mau phái người mời đi theo, ta còn nghe nói, Độc điện Liêu Huân điện chủ, hôm qua cũng đến, cùng một chỗ mời tới cho ta!”"),i("br"),i("br"),n._v("Nghe được độc sư, Lâm Nhược Thiên mi mắt sáng lên, vội vàng quay đầu phân phó."),i("br"),i("br"),n._v("Hắn cùng Đại Dược Vương một mực có liên hệ, bằng không thì cũng không có khả năng mua được độc chết Kim Vĩ Hùng Sư dược vật."),i("br"),i("br"),n._v("“Liêu điện chủ? Độc điện điện chủ không phải...” Quý Phong gia chủ mặt mũi tràn đầy nghi hoặc."),i("br"),i("br"),n._v("Hắn cùng Độc điện tiếp xúc thiếu, nhưng xem như uy hách một phương đặc thù chức nghiệp quần thể, vẫn là có nghe thấy, mặc dù không xác thực cắt biết điện chủ tính danh, lại biết, tuyệt không họ liêu."),i("br"),i("br"),n._v("Hơn nữa đường đường Độc điện điện chủ không tại hang ổ đợi, chạy Thiên Vũ vương thành làm cái gì?"),i("br"),i("br"),n._v("“Cụ thể thế nào chuyện ta cũng không rõ ràng, tựa như là trước đó điện chủ đã qua đời, học sinh của hắn tiếp nhận. Lần này là vì tiễn một vị nhân vật lợi hại rời đi, mới đến nơi này!” Lâm Nhược Thiên nghĩ nghĩ, đạo."),i("br"),i("br"),n._v("Hắn cũng là theo Đại Dược Vương trong miệng đạt được tin tức, tựa hồ là tổng bộ phái cái gì đặc sứ đến đây, Liêu điện chủ cũng là vì tiễn đối phương rời đi, mới tới nơi này."),i("br"),i("br"),n._v("  “Mặc kệ cái gì nguyên nhân, bọn hắn tại tin tức này thuộc về tuyệt mật, không thể để cho bất kỳ người nào biết!”"),i("br"),i("br"),n._v("Giải thích hết, Lâm Nhược Thiên nhìn qua, mắt sáng lên: “Quý gia chủ, ta là nhìn hai chúng ta gia thế thay mặt giao hảo mới không có giấu diếm, nói rõ sự thật! Ngươi chắc hẳn cũng hẳn là biết thế nào làm!”"),i("br"),i("br"),n._v("Độc sư cùng những nghề nghiệp khác quan hệ cũng không tốt, nếu như cho những người khác biết đường đường Độc điện điện chủ tại vương thành, rất có thể phát sinh không cần thiết mâu thuẫn."),i("br"),i("br"),n._v("“Lâm gia chủ yên tâm, ta còn không có như thế nhiều chuyện!” Thấy đối phương bộ dáng nghiêm túc, Quý Phong biến sắc, liền vội vàng gật đầu đáp ứng."),i("br"),i("br"),n._v("Không đáp ứng cũng không được ah, Lâm gia cường thế, không đáp ứng có lẽ không dùng đến ngày mai, chính xác Quý gia liền sẽ bị đồ diệt sạch sẽ."),i("br"),i("br"),n._v("Mà một màn này, Thiên Vũ vương thất tuyệt đối vui lòng nhìn thấy."),i("br"),i("br"),n._v("Giống bọn hắn loại này đại gia tộc, đúng vương thất quyền uy là một loại ngăn cản, có thể bị người diệt đi không để cho nhúng tay, tuyệt đối cao hứng cũng không kịp."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Lâm gia phái người đi mời, thời gian không dài, Đại Dược Vương cùng Liêu Huân điện chủ liền đến đến nơi đây."),i("br"),i("br"),n._v("Lúc này Liêu Huân đã có điện chủ khí thế cùng uy nghiêm, hai tay vác tại phía sau, ánh mắt tĩnh mịch cao xa, cho người ta một loại thâm bất khả trắc bộ dáng."),i("br"),i("br"),n._v("Trước đó phách lối không gì sánh được Lâm Nhược Thiên, Quý Phong thấy hắn đi tới, cũng không dám trang lớn, đồng loạt ôm quyền: “Lâm Nhược Thiên (Quý Phong), gặp qua Liêu điện chủ!”"),i("br"),i("br"),n._v("Độc sư không giống với những nghề nghiệp khác, tính cách tựa hồ cũng có chút lạ đam mê, vạn nhất đắc tội, trêu đến không cao hứng, hạ cái độc chơi đùa, chết cũng không biết thế nào chết."),i("br"),i("br"),n._v("“Ừm!” Nhìn hai người bộ dáng, Liêu Huân hài lòng nhẹ gật đầu, mí mắt vừa nhấc: “Không biết Lâm gia chủ như thế nóng nảy tìm ta tới, cần làm chuyện gì?”"),i("br"),i("br"),n._v("“Là như vậy!” Lâm Nhược Thiên nhìn qua: “Khuyển tử không cẩn thận trúng kịch độc, liền Mộc Hoành y sư bó tay toàn tập, hi vọng điện chủ có thể xuất thủ cứu giúp...”"),i("br"),i("br"),n._v("“Trúng độc?”"),i("br"),i("br"),n._v("Nhìn thoáng qua nằm dưới đất ba người (còn có Mộc Hoành y sư), sắc mặt không buồn không vui, Liêu Huân mí mắt vừa nhấc: “Đích thật là trúng độc, bất quá... Ta tại sao muốn xuất thủ cứu giúp?”"),i("br"),i("br"),n._v("Con của ngươi trúng độc liên quan ta cái rắm?"),i("br"),i("br"),n._v("Người khác muốn nịnh bợ ngươi Lâm gia, cùng ta không có liên quan quá nhiều!"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Nhìn thấy đối phương chẳng hề để ý biểu lộ, hơi đỏ mặt, Lâm Nhược Thiên buồn bực suy nghĩ muốn thổ huyết."),i("br"),i("br"),n._v("  Lâm gia làm Thiên Vũ vương quốc đệ nhất thế gia, luôn luôn đều là khi dễ người khác, hỏi người khác muốn chỗ tốt, lúc nào người khác như vậy nói chuyện cùng hắn?"),i("br"),i("br"),n._v("Có thể đối mặt người trước mắt này, lại không dám phát tác."),i("br"),i("br"),n._v("Thực lực của đối phương là không bằng hắn, nhưng độc sư công kích quỷ dị, không thể dùng lẽ thường phỏng đoán, không ít thực lực thấp, cũng có thể nhẹ nhõm đem siêu việt hắn cường giả độc chết."),i("br"),i("br"),n._v("Vị này Liêu Huân, thân là Độc điện điện chủ, thực lực lại đạt đến Tông sư đỉnh phong, tốt nhất vẫn là đừng đắc tội, nếu không trêu đến hắn nổi giận, có lẽ một sân người đều không đủ hắn độc."),i("br"),i("br"),n._v("Nghĩ đến cái này, cố nén buồn bực, tay lật một cái bình ngọc xuất hiện tại lòng bàn tay."),i("br"),i("br"),n._v("“Đây là một viên , tam phẩm trung kỳ đan dược, Hiên Viên vương quốc Thiết Du Tông sư tự tay luyện chế mà thành, đạt đến hoàn mỹ cấp bậc! Phục dụng sau, tuyệt đối có thể để cho điện chủ tu vi tiến thêm một bước, bước vào nửa bước Chí Tôn cảnh, cũng chưa biết chừng!”"),i("br"),i("br"),n._v("Kim Nguyên đan thuộc tính là kim, vô cùng sắc bén, mang theo bài trừ bình chướng hiệu quả."),i("br"),i("br"),n._v("Đạt tới hoàn mỹ cấp bậc, giá trị càng lớn, đã không kém gì một viên linh thạch!"),i("br"),i("br"),n._v("Đem thứ này xuất ra, Lâm Nhược Thiên cũng coi là thả máu."),i("br"),n._v(" “Kim Nguyên đan?”"),i("br"),i("br"),n._v("Nhìn cũng không nhìn một chút, khoát tay áo, Liêu Huân một mặt không thèm để ý chút nào: “Thứ đan dược này, đúng độc sư tới nói vô dụng, cũng không cần lấy ra mất mặt!”"),i("br"),i("br"),n._v("Độc sư muốn tăng lên, muốn phục dụng độc vật, tu luyện độc công, đan dược quả thực tác dụng không lớn."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Không nghĩ tới cắn răng xuất ra đồ vật, đối phương mảy may đều không thèm để ý, Lâm Nhược Thiên tràn đầy xấu hổ, không biết như thế nào cho phải."),i("br"),i("br"),n._v("“Như vậy đi! Ta nói một kiện, chỉ cần ngươi có thể làm được, đừng nói hỗ trợ giải độc, coi như giúp ngươi giết người cũng không sao!” Liêu Huân lần nữa nhìn qua."),i("br"),i("br"),n._v("“Điện chủ mời nói!” Thấy sự tình còn có bước ngoặt, Lâm Nhược Thiên vội nói."),i("br"),i("br"),n._v("“Rất đơn giản, ta nghe nói các ngươi Lâm gia tại vương quốc rất có địa vị, muốn mời ngươi hỗ trợ tại vương thành xây cái có thể làm cho độc sư giao dịch nơi chốn!” Liêu Huân đem mục đích của mình nói ra."),i("br"),i("br"),n._v("Nếu không phải là bởi vì cái này, thế nào khả năng đáp ứng đối phương tới đây."),i("br"),i("br"),n._v("Độc sư không có khả năng một mực đối đãi tại Độc điện không ra, cũng cần sinh hoạt cùng giao dịch. Nếu như có thể trong này thành lập cái dạng này chợ giao dịch chỗ, chẳng khác nào chính thức thừa nhận thân phận, miễn đi không ít phiền phức, không cần cả ngày che che lấp lấp."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Lâm Nhược Thiên tràn đầy chần chờ, một lát sau: “Điện chủ chắc hẳn cũng biết Lâm gia chúng ta mặc dù tại Thiên Vũ vương quốc có nhất định quyền lợi, mà dù sao không phải vương quốc chân chính chưởng khống giả. Tùy tiện thành lập loại này chợ giao dịch chỗ, sợ sẽ khiến rất nhiều công hội bất mãn...”"),i("br"),i("br"),n._v("“Không cần cùng ta quanh co! Ngươi ý gì, Đại Dược Vương đã cùng ta nói. Cho vương thất Kim Vĩ Hùng Sư hạ độc, không phải liền là muốn thay vào đó sao? Yên tâm, chỉ cần ngươi đáp ứng ta vừa rồi yêu cầu, coi như trấn quốc man thú đạt đến Chí Tôn cấp bậc, chúng ta Độc điện đồng dạng có dược vật đối phó!”"),i("br"),i("br"),n._v("Liêu Huân thản nhiên nói."),i("br"),i("br"),n._v("“Chí Tôn cũng có thể đối phó?” Lâm Nhược Thiên mi mắt sáng lên."),i("br"),i("br"),n._v("Trước đó, Kim Vĩ Hùng Sư đột phá để hắn thu không ít tâm tư, hiện tại vị điện chủ này như vậy hứa hẹn, để thu liễm tâm tư lần nữa sinh động."),i("br"),i("br"),n._v("Nói thật, coi như không dàn xếp dễ trận, độc sư cũng có thể âm thầm giao lưu."),i("br"),i("br"),n._v("Trên thực tế Hồng Liên thành liền là lớn nhất chợ giao dịch, vì một cái không ảnh hưởng đại cục quyết định, lôi kéo một cái cường đại giúp đỡ... Sao lại không làm?"),i("br"),i("br"),n._v("Nhẹ gật đầu, Lâm Nhược Thiên đang muốn mở lời, chỉ thấy một cái hộ vệ vội vàng hấp tấp chạy vào."),i("br"),i("br"),n._v("“Tộc trưởng, không xong...” Còn không có đi vào phòng, thanh âm liền truyền vào."),i("br"),i("br"),n._v("“Cái gì sự tình?” Bị người quấy rầy, Lâm Nhược Thiên mặt mũi tràn đầy không vui."),i("br"),i("br"),n._v("“Tam trưởng lão, hắn...” Đi tới hộ vệ, bờ môi run rẩy."),i("br"),i("br"),n._v("“Thế nào? Chẳng lẽ bắt được hung thủ? Bắt được hung thủ có cái gì thật khẩn trương?” Lâm Nhược Thiên nhìn qua."),i("br"),i("br"),n._v("Tam trưởng lão bị phái đi bắt hung thủ, hiện tại lại tin tức, có lẽ hung thủ đã đền tội."),i("br"),i("br"),n._v("“Không phải... Là Tam trưởng lão bị người tiễn trở về! Bản thân bị trọng thương, hôn mê bất tỉnh, hơn nữa, hơn nữa...”"),i("br"),i("br"),n._v("“Hơn nữa cái gì?”"),i("br"),i("br"),n._v("“Hạ thân của hắn bị người đá bể, đã triệt để phế đi...” Hộ vệ vẻ mặt cầu xin."),i("br"),i("br"),n._v("“Bị người đá bể?” Lâm Nhược Thiên sững sờ, lập tức mặt mũi tràn đầy phát điên: “Là ai? Ai như thế gan to?”"),i("br"),i("br"),n._v("Bà mẹ nó!"),i("br"),i("br"),n._v("Lâm gia một mực hùng bá vương thành, không ai dám trêu chọc, cái này mẹ nó hôm nay thế nào?"),i("br"),i("br"),n._v("Nhi tử trước bị người đánh thành trọng thương ném vào ao phân, tiến đến truy tra Tam trưởng lão càng được người bị đá ** phát nổ"),i("br"),i("br"),n._v("Thời điểm nào bọn hắn Lâm gia bị người khi dễ như vậy?"),i("br"),i("br"),n._v("Nếu như bị khi phụ thành như vậy, lại không có cái gì cử động, Lâm gia sau này còn thế nào tại Thiên Vũ vương quốc đặt chân?"),i("br"),i("br"),n._v("“Vâng... Là vương thất phái người đưa tới! Nghe nói đánh lén thiếu gia hung thủ chạy trốn tới hoàng cung, bị Tầm Tích thử tìm tới. Kết quả song phương phát sinh mâu thuẫn, Tam trưởng lão bị đối phương một cước đá cho như vậy, những hộ vệ khác cũng đều là không rõ sống chết...”"),i("br"),i("br"),n._v("Hộ vệ đem biết đến tin tức nói ra."),i("br"),i("br"),n._v("“Về phần xuất thủ người, tựa như là gần nhất danh tiếng vang xa Y Sư công hội hội trưởng Liễu Trình, mà đánh lén thiếu gia hung thủ, đúng là hắn học sinh...”"),i("br"),i("br"),n._v("Thiên Vũ hoàng cung có Lâm gia nội gian, hỏi thăm ra những việc này, cũng không khó."),i("br"),i("br"),n._v("“Liễu Trình? Đáng giận... Dám đả thương con của ta cùng Tam trưởng lão, xem Lâm gia chúng ta uy nghiêm tại không có gì, ta Lâm Nhược Thiên cùng hắn không chết không ngớt...”"),i("br"),i("br"),n._v("Biết rõ ràng thế nào chuyện, một tiếng gào thét, Lâm Nhược Thiên sắp điên rồi!"),i("br"),i("br"),n._v("Gia hỏa này chỗ nào xuất hiện, một mực tìm bọn hắn Lâm gia phiền phức, còn nghiện hay sao?"),i("br"),i("br"),n._v("Chữa khỏi Kim Vĩ Hùng Sư, phá hủy bọn hắn bố trí cùng kế hoạch, không có tìm ngươi phiền phức, là tốt lắm rồi, thế mà còn dám phái học sinh đả thương người, bản thân càng ra tay đả thương Tam trưởng lão..."),i("br"),i("br"),n._v("Một cái danh dự hội trưởng mà thôi, ai cho ngươi lá gan?"),i("br"),i("br"),n._v("“Người tới, cho ta đem cái này Liễu Trình bắt tới, không giết hắn, khó tiêu mối hận trong lòng ta...”"),i("br"),i("br"),n._v("Buồn bực sắp thổ huyết, Lâm Nhược Thiên bàn tay lớn vẫy một cái, khôi phục một đời kiêu hùng khí chất."),i("br"),i("br"),n._v("Quản ngươi cái gì hội trưởng, cái gì thiên tài, đắc tội ta, ta liền đem ngươi giết, sau này coi như Y Sư công hội đến tìm phiền phức, ta cũng có lý..."),i("br"),i("br"),n._v("Phân phó của hắn còn không có kết thúc, chỉ thấy một cái hộ vệ cũng đầy là hốt hoảng chạy tới: “Gia chủ, không xong... Cái kia Liễu hội trưởng, mang theo học sinh của hắn, đã đi tới ngoài cửa, nói muốn tìm Lâm gia chúng ta tính sổ sách, đòi lại công đạo, muốn chúng ta... Xin lỗi!”"),i("br"),i("br"),n._v("“Xin lỗi? Hắn đả thương người của ta, còn muốn để cho ta xin lỗi?”"),i("br"),i("br"),n._v("Mi mắt ửng hồng, Lâm Nhược Thiên điên thật rồi!"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-388"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-390"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);