(window.webpackJsonp=window.webpackJsonp||[]).push([[1620],{1975:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-30-tram-bich-nhu-khiep-so"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-30-tram-bich-nhu-khiep-so"}},[n._v("#")]),n._v(" Chương 30: Trầm Bích Như khiếp sợ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Chăm chú đọc sách?” Mặc lão râu mép thổi lên, cảm giác lập tức liền muốn chọc giận nổ: “Không chép sao lục, không nghiên cứu, nhìn thấy sách vở liền xoay loạn, ngươi đúng là nói một chút, có ngươi nhìn như vậy sách sao?” "),t("br"),t("br"),n._v(" “Lẽ nào đọc sách nhất định phải sao chép nghiên cứu?” Trương Huyền giờ mới hiểu được vì sao đối phương tức giận như vậy, có chút dở khóc dở cười."),t("br"),t("br"),n._v(" Kiếp trước hắn là thư viện nhân viên quản lý, công tác tính chất cùng cái này Mặc lão gần như, chỉ cần có thẻ mượn sách, là có thể tiến vào bên trong, ở bên trong ngủ ăn cơm, chỉ cần không giết người phóng hỏa, đều không có nhân quản ngươi!"),t("br"),t("br"),n._v(" Vốn tưởng rằng đối phương làm khó dễ, là bởi vì tiền thân thầy giáo sát hạch quá kém, náo loạn nửa ngày, bởi vì cái này!"),t("br"),t("br"),n._v(" “Những sách vở này đều là học viện tiền bối tiêu tốn vô số đại giới lấy được, mỗi một bản đều ẩn chứa tâm huyết của bọn họ, nếu như không muốn học tập, cũng không cần động! Giống như ngươi vậy tùy tiện xoay loạn, không phải quấy rối là cái gì?” Mực Lão Đại vung tay lên."),t("br"),t("br"),n._v(" “Ta... Được rồi, nếu như ngươi nói ta là quấy rối, liền quấy rối đi!” "),t("br"),t("br"),n._v(" Muốn giải thích một câu, suy nghĩ một chút Trương Huyền không nhịn được lắc đầu."),t("br"),t("br"),n._v(" Thiên Đạo Thư Viện sự tình khẳng định không thể nói, cứ như vậy, xác thực không cách nào giải thích vì sao điên cuồng như vậy lật sách."),t("br"),t("br"),n._v(" Ngược lại sau đó cũng không tới, không cần thiết tiếp tục nét mực, danh tiếng đều thúi như vậy, cũng không để ý nhiều trên lưng mấy cái."),t("br"),t("br"),n._v(" “Thừa nhận là tốt rồi! Cút đi!” Thấy hắn thừa nhận, Mặc lão ánh mắt lộ ra nồng đậm căm ghét, bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v(" “Cáo từ!” "),t("br"),t("br"),n._v(" Đang muốn rời đi, đột nhiên trước mặt bóng người lóe lên, một nói hương gió đập vào mặt, Trầm Bích Như che ở trước mặt: “Chậm đã!” "),t("br"),t("br"),n._v(" “Lại làm sao?” Trương Huyền một mặt bất đắc dĩ: “Ngươi xem ngươi sách, ta xem ta, ta cũng không có làm lỡ ngươi đi!” "),t("br"),t("br"),n._v(" “Hừ!” "),t("br"),t("br"),n._v(" Tự mình ngăn cản người khác, đối phương tuyệt đối hưng phấn nhảy dựng lên, cái tên này nhưng một mặt thiếu kiên nhẫn, Trầm Bích Như đôi mi thanh tú nhăn lại: “Không phải chuyện này, ngươi không phải mới vừa nói ngươi đang đọc sách sao? Nếu nhìn lâu như vậy, lẽ ra có thể nhớ kỹ một ít đi!” "),t("br"),t("br"),n._v(" “Có ý gì?” "),t("br"),t("br"),n._v(" Không biết nữ nhân này lại làm ra cái gì yêu thiêu thân, Trương Huyền nghi ngờ nhìn sang."),t("br"),t("br"),n._v(" “Rất đơn giản, ta vừa nãy thấy ngươi xem qua Luyện Đan Bát Pháp quyển sách này, vừa vặn có chút không hiểu, muốn thỉnh giáo một hồi, đều là lão sư, ngươi sẽ không cự tuyệt chứ?” Trầm Bích Như nói."),t("br"),t("br"),n._v(" Kỳ thực ý nghĩ của nàng rất đơn giản, người thiếu niên trước mắt này, không phải giả vờ giả vịt trong đó lật sách sao?"),t("br"),t("br"),n._v(" Đã như vậy, cố ý vấn đề, giải đáp không được, đọc sách lời nói dối liền tiết lộ!"),t("br"),t("br"),n._v(" “Luyện Đan Bát Pháp?” Trương Huyền tinh thần hơi động, một quyển sách liền xuất hiện ở đầu óc, vừa nãy xác thực vượt qua có quyển sách này: “Có cái gì không hiểu cứ hỏi đi, ta sẽ giúp ngươi giải đáp, bất quá, ta còn muốn trở lại ăn cơm, không quá nhiều thời gian, ngươi cũng động tác nhanh lên một chút...” "),t("br"),t("br"),n._v(" “Ngươi...” "),t("br"),t("br"),n._v(" Nắm đấm xiết chặt, Trầm Bích Như tức thiếu chút nữa không ngất đi."),t("br"),t("br"),n._v(" Đối phương giọng điệu này, thật giống như một cái học sinh thỉnh giáo lão sư như thế, tràn đầy thiếu kiên nhẫn."),t("br"),t("br"),n._v(" Số một, ta là mỹ nữ, thứ hai, ta là học viện cao cấp lão sư, cũng là minh tinh giáo sư! Ta hỏi ngươi đó là thi ngươi, làm sao nghe tới, như là ta hướng về ngươi lĩnh giáo học vấn?"),t("br"),t("br"),n._v(" Thuật nghiệp hữu chuyên công, nghe đạo có trước sau, trong học viện là có lão sư lẫn nhau hỏi dò, học tập, nhưng ngươi một sư tư sát hạch được không điểm gia hỏa, có tư cách gì để cho ta lĩnh giáo?"),t("br"),t("br"),n._v(" Cố nén sắp nổ tung lửa giận, Trầm Bích Như cắn răng nhìn sang: “Ta vừa nhìn thấy phía trên viết ‘Dược tán bát phương, đan khó thành! Thuốc ngưng lò lửa, đan như Hoàng!’ Câu nói này, có chút kỳ quái, cũng có chút không rõ, không biết Trương lão sư có thể hay không vì ta giải đáp?” "),t("br"),t("br"),n._v(" Nói ra “Giải đáp” hai chữ, cơ hồ cắn nát hàm răng, trong thanh âm đều có thể nghe được hàm răng ma sát âm thanh."),t("br"),t("br"),n._v(" Nàng đường đường đại mỹ nữ, nếu như không phải là vì để hắn xấu mặt, chắc chắn sẽ không hướng về một cái rác rưởi thỉnh giáo."),t("br"),t("br"),n._v(' "Liền ngươi trí nhớ này, còn hỏi vấn đề..." Không nghĩ tới đối phương hỏi ra câu nói này, Trương Huyền bĩu môi: "Những lời này là ‘Thuốc ngưng lò lửa, đan khó thành! Dược tán bát phương, đan như Hoàng!’ Ý tứ rất đơn giản, luyện chế đan dược lúc, thuốc bột, nước thuốc một khi ở lò lửa bên trong đông lại thành mụn nhọt, dược hiệu liền không cách nào đầy đủ dung hợp,'),t("br"),t("br"),n._v(' Không thể luyện chế thành đan dược! Ngược lại, có thể triệt để tản ra, đầy đủ dung hợp, luyện chế thành đan dược, khác nào lòng đỏ trứng!"'),t("br"),t("br"),n._v(" Đem Tàng Thư Các sở hữu thư tịch đều lật hết, tinh thần hơi động, liền đem chính xác luyện đan phương pháp hút ra đi ra, điểm ấy tri thức, làm khó người khác còn có thể, làm khó hắn, quả thực đùa giỡn!"),t("br"),t("br"),n._v(" Then chốt... Đối phương còn đem muốn hỏi vấn đề vác sai rồi, trình độ cũng quá lần đi!"),t("br"),t("br"),n._v(" “Như thế nào, không đáp lại được đi! A... Ngươi nói cái gì?” "),t("br"),t("br"),n._v(" Vốn tưởng rằng thiếu niên không đáp lại được, nằm mơ đều không có nghĩ đến hắn thuận miệng nói ra, Trầm Bích Như không chút phòng bị, sợ hết hồn, tú mục kém chút trợn lên rơi xuống."),t("br"),n._v(" Câu nói kia, là nàng cố ý vác sai, chính là muốn cho hắn nhầm lẫn, dưới cái nhìn của nàng, cái tên này vừa rồi tại Tàng Thư Các chỉ là cố làm ra vẻ, cái gì cũng không thấy, tự mình tùy tiện nói ra một quyển sách, tất nhiên chưa từng thấy!"),t("br"),t("br"),n._v(" Sau đó sẽ cố ý vác sai, liền có thể để hắn mạnh mẽ xấu mặt, mất mặt xấu hổ!"),t("br"),t("br"),n._v(" Ai biết... Tự mình cõng sai, bị hắn một chút nhìn ra, hơn nữa cấp ra chính xác nhất giải đáp!"),t("br"),t("br"),n._v(" Chuyện này... Làm sao có khả năng?"),t("br"),t("br"),n._v(" Lẽ nào cái tên này, chỉ lật một chút, liền thật sự nhớ kỹ Luyện Đan Bát Pháp?"),t("br"),t("br"),n._v(" Không nên a!"),t("br"),t("br"),n._v(" Cái tên này liền thầy giáo sát hạch đều chỉ có thể được không điểm, luyện đan này loại thiên môn đồ vật, làm sao sẽ biết?"),t("br"),t("br"),n._v(" “Tốt, còn có vấn đề gì không?” Vuốt xẹp cùng nhau cái bụng, Trương Huyền giục."),t("br"),t("br"),n._v(" “Còn có!” Trong lòng khiếp sợ, nhìn thấy hắn cái bộ dáng này, Trầm Bích Như lần thứ hai hừ nói: “Còn có một câu nói, ta cũng không hiểu rõ lắm, gọi 'Kim đốt ở lửa, không tôi không tan!” "),t("br"),t("br"),n._v(" Nói xong trong lòng liên tục cười lạnh, chờ nhìn đối phương trả lời không ra."),t("br"),t("br"),n._v(" Vừa nãy hỏi là luyện đan, mà đây là luyện khí bên trong ngôn ngữ, câu nói này nàng ở một quyển trong cổ thư nhìn thấy thời điểm, nghiên cứu hơn một tháng, đều không rõ vì sao, mãi đến tận cuối cùng gặp phải một vị luyện khí đại sư, hỏi qua mới hiểu được!"),t("br"),t("br"),n._v(" Nguyên bản chỉ muốn làm khó đối phương một hồi, cho hắn biết đắc tội tự mình đánh đổi, ai biết hắn một cái liền giải thích Luyện Đan Bát Pháp bên trong câu nói kia ý tứ!"),t("br"),t("br"),n._v(" Nhất thời làm cho nàng lòng háo thắng nổi lên, dự định ra một nan đề, để hắn không có gì để nói."),t("br"),t("br"),n._v(" “Chuyện này...” "),t("br"),t("br"),n._v(" Một bên Mặc lão nhìn thấy Trương Huyền trực tiếp trả lời ra vấn đề thứ nhất, cũng cảm thấy rất kinh ngạc, bất quá, không hề là đặc biệt khiếp sợ."),t("br"),t("br"),n._v(" Luyện Đan Bát Pháp là vô số luyện đan người nhập môn cơ bản phương pháp, cùng Hồng Thiên Cửu Trọng Quyết đối với võ giả như thế, coi như không học luyện đan, cũng có thể là biết một ít."),t("br"),t("br"),n._v(" Làm Trầm Bích Như hỏi ra vấn đề thứ hai thời điểm, hắn sửng sốt, bởi vì mặc dù là hắn, đối với câu nói này, cũng rất khó trả lời."),t("br"),t("br"),n._v(" “Kim đốt ở lửa, không tôi không tan?” Ngay ở hai người đều cảm thấy hắn trả lời không ra thời điểm, Trương Huyền lắc lắc đầu, nhìn thằng ngốc như thế ánh mắt nhìn lại đây: “Câu nói này có cái gì không hiểu? Là chỉ luyện khí thời điểm, các loại vật phẩm muốn đầu tiên tại trong lửa thiêu đốt, triệt để rèn luyện, đem tạp chất hoàn toàn thanh trừ sạch sẽ, mới có thể tốt hơn nung nấu cùng nhau! Làm phiền ngươi muốn hỏi một chút một ít khó khăn, những này đơn giản tri thức sau đó xem thêm sách liền tự nhiên rõ ràng!” "),t("br"),t("br"),n._v(" “Chuyện này...” "),t("br"),t("br"),n._v(" Trầm Bích Như thân thể mềm mại chấn động."),t("br"),t("br"),n._v(" Vấn đề này, nàng nhưng là thỉnh giáo không ít người, mới biết đáp án, thiếu niên một cái nói ngay, chuyện này..."),t("br"),t("br"),n._v(" Không riêng gì nàng, liền ngay cả một bên Mặc lão, cũng thiếu chút nữa không dọa ngất đi qua."),t("br"),t("br"),n._v(" Liền hắn đều không đáp lại được vấn đề, đối phương tiện tay nói ra, lẽ nào... Không phải mới vừa lật sách, mà là thật sự đang nhìn?"),t("br"),t("br"),n._v(" “Trương Huyền lão sư, ngươi câu trả lời này không đúng sao, ta nhớ được có bản Tạp Nghi Luận trên giải thích, cùng này không giống, nói là đan dược bên trong muốn gia nhập Kim thuộc tính vật phẩm, đầu tiên phải đem rèn luyện, bằng không không thể cùng hình ảnh tan!” "),t("br"),t("br"),n._v(" Trong lòng còn có chút không tin, mở miệng nói."),t("br"),t("br"),n._v(" “Ngươi nhớ lầm, Tạp Nghi Luận trên không có câu nói này, cũng không có tương tự, ngươi nói hẳn là liễu trạch tiền bối lưu lại Tạp Thuyết Luận, trong này xác thực có một câu nói cùng với tương tự, bất quá là ‘Kim đốt ở đan, không tôi không tan’, mà không phải kim đốt ở lửa!” "),t("br"),t("br"),n._v(" Trương Huyền xua tay."),t("br"),t("br"),n._v(" Hắn cũng không biết hai người là cố ý thi hắn, còn tưởng rằng đối phương thật sự có chút nhớ không rõ, thuận miệng nói: “Há, quyển sách này ngay ở Tàng Thư Các thứ chín sắp xếp nhất bên trong góc, ngươi nói câu nói này, ghi lại ở thứ 49 trang! Cho tới vừa nãy nàng hỏi câu kia, nếu như ngươi không tin lời giải thích của ta, có thể mở ra một hồi đỗ Khúc tiền bối lưu lại Khí Hỏa Tam Quyết, quyển sách này ở thứ 15 sắp xếp giá sách góc, mặt trên từng có giải thích, ghi lại ở thứ 24 trang!” "),t("br"),t("br"),n._v(" “Ta xem một chút!” "),t("br"),t("br"),n._v(" Trầm Bích Như nghi ngờ liếc mắt nhìn, vội vội vàng vàng đi vào Tàng Thư Các, một lát sau đi trở về, trong tay đã nhiều hai bản sách, chính là Tạp Thuyết Luận cùng Khí Hỏa Tam Quyết, làm lật đến đối phương nói số trang, thân thể mềm mại loáng một cái, kém chút không tại chỗ ngất đi."),t("br"),t("br"),n._v(" Cùng thiếu niên nói... Lại giống như đúc, không kém chút nào!"),t("br"),t("br"),n._v(" Đối phương không chỉ nhớ kỹ câu nói này, liền quyển sách kia, vị trí nào, lại cái nào trang đều có thể nhớ kỹ?"),t("br"),t("br"),n._v(" Thật hay giả?"),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-38"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-40"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);