(window.webpackJsonp=window.webpackJsonp||[]).push([[1626],{1981:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-386-đien-chu-cuu-ta"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-386-đien-chu-cuu-ta"}},[n._v("#")]),n._v(" Chương 386: Điện chủ cứu ta")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Trương Huyền không có kiếm, cũng không có bất luận cái gì binh khí, duy nhất một chuôi Thú đường trưởng lão đưa tặng trường kiếm, cho Triệu Nhã."),i("br"),i("br"),n._v("Lúc này đối phương xuất ra binh khí, buộc hắn sử dụng binh khí, con mắt rơi vào nữ hài trên người, lập tức la lên."),i("br"),i("br"),n._v("“Kiếm?”"),i("br"),i("br"),n._v("Triệu Nhã chần chờ một chút, vẫn là bàn tay như ngọc trắng run lên, trường kiếm thẳng tắp hướng trước mắt Liễu lão sư bay đi."),i("br"),i("br"),n._v("Không biết vị này Liễu lão sư tại sao lại hướng nàng mượn kiếm, nhưng lần này nếu là đến giúp đỡ hắn, cũng là không quan trọng."),i("br"),i("br"),n._v("Soạt!"),i("br"),i("br"),n._v("Trường kiếm ra khỏi vỏ, hàn mang bắn ra bốn phía, Trương Huyền cả người khí chất lập tức thay đổi, tựa như lập tức cùng kiếm dung hợp lại cùng nhau, khí tức trực trùng vân tiêu, muốn đem trời xanh đều cắt vỡ."),i("br"),i("br"),n._v("Đinh đinh đinh đinh đinh!"),i("br"),i("br"),n._v("Tất cả kéo kiếm võ giả, trường kiếm trong tay tựa như thấy được Vương Giả, đồng thời phát ra thanh thúy vang lên, tụ lại hình thành nhất đạo to rõ thanh âm."),i("br"),i("br"),n._v("“Vạn kiếm tề minh... Đây là so kiếm ý còn cảnh giới cao Kiếm Tâm cảnh?”"),i("br"),i("br"),n._v("“Hai mươi tuổi Liễu hội trưởng, lĩnh ngộ kiếm tâm?”"),i("br"),i("br"),n._v("“Hắn... Làm sao làm được?”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Tất cả mọi người run rẩy kịch liệt, nhìn quái vật nhìn qua."),i("br"),i("br"),n._v("Tu luyện binh khí, lĩnh ngộ binh khí chân ý, chỉ là bước đầu tiên, đạt tới cao siêu hơn cảnh giới mới là binh khí chi tâm!"),i("br"),i("br"),n._v("Kiếm tâm, nói rõ đối với kiếm lĩnh ngộ, đã đạt đến một loại mức làm người nghe kinh hãi, nghe nói liền một chút siêu việt Chí Tôn cường giả đều không thể làm đến, giờ khắc này ở một cái chỉ có Tông sư đỉnh phong thanh niên trên người sử xuất, làm sao không để cho người ta kinh ngạc?"),i("br"),i("br"),n._v("Kiếm tâm, lấy tâm làm kiếm, lấy ý xu thế, chỗ đến, đều là kiếm chiêu."),i("br"),i("br"),n._v("Bằng chừng ấy tuổi lĩnh ngộ ý cảnh như thế này, dùng kiếm phương diện, cùng cấp bậc, đã mất địch thủ."),i("br"),i("br"),n._v("“Không đúng, không phải kiếm tâm!”"),i("br"),i("br"),n._v("Đám người đằng sau vừa tới đến Khương đường chủ, chậm rãi lắc đầu."),i("br"),i("br"),n._v("Người khác không biết Liễu hội trưởng thân phận, hắn biết đến rất rõ ràng, Danh Sư đường một đời thiên kiêu, vạn nhất tại Lâm gia vẫn lạc, tổng hội khẳng định sẽ đem hắn tươi sống giết chết."),i("br"),i("br"),n._v("Bởi vậy, nghe được tin tức, liền lập tức chạy tới."),i("br"),i("br"),n._v("Bất quá, lúc này Trương Huyền chiếm thượng phong, không có bất kỳ cái gì bị động, cũng sẽ không cần ra tay."),i("br"),i("br"),n._v("“Không phải?” Chúc trưởng lão nghi ngờ nhìn qua."),i("br"),i("br"),n._v("Vạn kiếm tề minh, long ngâm Khiếu Thiên, chính là lĩnh ngộ kiếm tâm dấu hiệu, nếu như không phải lời nói, đó là cái gì?"),i("br"),i("br"),n._v("“Kiếm tâm chia làm thượng, trung, hạ ba loại, căn cứ lĩnh ngộ nhiều ít, theo thứ tự sắp xếp. Bình thường hạ kiếm tâm, liền có thể để trường kiếm dựng dục ra linh tính, cùng người phù hợp, tựa như cánh tay! Trương sư mặc dù dẫn động vạn kiếm tề minh, trên thực tế chỉ là điều động thân kiếm khí tức, đối với loại này linh tính, còn không có hoàn toàn nắm giữ...”"),i("br"),i("br"),n._v("Khương đường chủ giải thích nói."),i("br"),i("br"),n._v("Kiếm tâm, là một loại cảnh giới, cũng đại biểu kiếm có linh, kiếm có ý."),i("br"),i("br"),n._v("Trương Huyền mặc dù đối với kiếm lĩnh ngộ rất cao thâm, thậm chí dẫn động vạn kiếm tề minh, trên thực tế đối với “Linh” nắm giữ rất ít, thậm chí liền biên giới đều không có tìm tòi đến, tự nhiên cũng không có hoàn chỉnh đạt tới cái gọi là Kiếm Tâm cảnh."),i("br"),i("br"),n._v("“Vậy cái này là...”"),i("br"),i("br"),n._v("Hiểu được, Chúc trưởng lão nhịn không được ngẩng đầu."),i("br"),i("br"),n._v("Đã không phải Kiếm Tâm cảnh, đó là cái gì?"),i("br"),i("br"),n._v("“Là bán tâm cảnh!”"),i("br"),i("br"),n._v("Khương đường chủ vẻ mặt nghiêm túc: “Hắn trên thực tế là lĩnh ngộ kiếm tâm, chỉ bất quá tu vi không có đột phá, đối với linh tính chưa hoàn chỉnh nắm giữ, chỉ cần thực lực tăng lên, chỉ sợ cũng sẽ chân chính nắm giữ, kiếm thuật nhất đạo, không ai địch nổi!”"),i("br"),i("br"),n._v("“Liền ‘Linh’ là cái gì cũng không biết, liền nắm giữ bán tâm... Làm sao làm được?”"),i("br"),i("br"),n._v("Nói xong, đồng thời chấn kinh tột đỉnh."),i("br"),i("br"),n._v("Rất nhiều siêu việt Chí Tôn siêu cấp cường giả, ngày ngày quan sát, mỗi ngày nghiên cứu, đều không thể lĩnh ngộ kiếm tâm, gia hỏa này liền linh là cái gì cũng không biết, thậm chí... Liền bội kiếm đều không có, liền đạt tới loại cảnh giới này..."),i("br"),i("br"),n._v("Suy nghĩ một chút đều để người sụp đổ."),i("br"),i("br"),n._v("Nếu như tin tức này cho những cái kia nghiên cứu kiếm thuật, cả một đời mà không nhập môn người biết, không biết có thể hay không tại chỗ buồn bực mà chết."),i("br"),i("br"),n._v("“Thật dày đặc kiếm ý...”"),i("br"),i("br"),n._v("Triệu Nhã không biết còn có kiếm tâm câu chuyện, nhưng trước mắt Liễu lão sư, trên người bốc lên kiếm ý, để nàng cảm thấy thân thể mềm mại run rẩy, tựa hồ cả người đều bị áp chế, ở trước mặt đối phương không sinh ra rút kiếm dũng khí."),i("br"),i("br"),n._v("Kiếm ý nghiền ép!"),i("br"),i("br"),n._v("Đồng dạng lĩnh ngộ kiếm ý, lĩnh ngộ khắc sâu, có thể nhẹ nhõm nghiền ép lĩnh ngộ thấp."),i("br"),i("br"),n._v("“Dùng binh khí, chỉ sợ là ngươi làm ra ngốc nhất quyết định!”"),i("br"),i("br"),n._v("Trường kiếm nơi tay, nhẹ nhàng nở nụ cười, Trương Huyền nhìn lại."),i("br"),i("br"),n._v("Thiên Đạo quyền pháp, Thiên Đạo thân pháp, chỉ có một chiêu, hơn nữa nhất định phải cận thân mới có thể công kích, Thiên Đạo kiếm pháp không giống, lĩnh ngộ kiếm ý, một chiêu phá vạn chiêu, tiện tay đều là chiêu số!"),i("br"),i("br"),n._v("Trước đó, cùng đối phương giao thủ, vì đó chân khí bức bách, không có cách nào công kích, chỉ có thể trốn tránh, trong tay có kiếm, lập tức không giống."),i("br"),i("br"),n._v("Hoàn toàn có thể tới ngạnh kháng!"),i("br"),i("br"),n._v("Soạt!"),i("br"),i("br"),n._v("Trường kiếm run lên, kiếm mang gào thét, nhất đạo sáng chói kiếm mang theo mũi kiếm bắn ra, hướng Lâm Nhược Thiên quẹt tới."),i("br"),i("br"),n._v("“Cái gì?”"),i("br"),i("br"),n._v("Lâm Nhược Thiên khóc."),i("br"),i("br"),n._v("Vừa rồi đối phương một mực trốn tránh, để hắn đánh không đến, vốn cho rằng dùng tới binh khí, lực lượng ngưng tụ, phạm vi rộng công kích... Có thể chiếm được tiện nghi, làm cho hắn lại không cách nào chạy trốn, nằm mơ đều không có nghĩ đến... Người ta lĩnh ngộ binh khí chi tâm!"),i("br"),i("br"),n._v("Mẹ nó!"),i("br"),i("br"),n._v("Này làm sao đánh?"),i("br"),i("br"),n._v("Mặc dù tu vi đạt tới nửa bước Chí Tôn, nhưng đối với kiếm lĩnh ngộ, liền kiếm ý đều không có đạt tới, chớ nói chi là kiếm tâm!"),i("br"),i("br"),n._v("Sắc mặt khó coi, thân thể bỗng nhiên co rụt lại, tránh thoát kiếm khí của đối phương, đang muốn đánh trả, liền thấy một đạo bạch mang, kề da đầu đâm tới."),i("br"),n._v("“Mẹ nó!”"),i("br"),i("br"),n._v("Cảm nhận được bám vào ở phía trên sát khí, biết một khi đụng tới, da đầu đều sẽ bị xé mở, Lâm Nhược Thiên lại không quản không để ý, ngay tại chỗ lăn ra ngoài."),i("br"),i("br"),n._v("Soạt!"),i("br"),i("br"),n._v("Kiếm mang rơi trên mặt đất, vạch ra nhất đạo rãnh sâu, nham thạch, bùn đất bắn tung toé."),i("br"),i("br"),n._v("“Đáng giận!”"),i("br"),i("br"),n._v("Đường đường nửa bước Chí Tôn, vậy mà dựa vào lại lư đả cổn mới có thể tránh né công kích, Lâm Nhược Thiên khí sắp bạo tạc, cổn động bên trong, bàn tay trên mặt đất vỗ, đang định nhảy dựng lên, lông tơ lần nữa nổ lên, kìm lòng không được hướng về phía trước lăn một vòng."),i("br"),i("br"),n._v("Ầm!"),i("br"),i("br"),n._v("Mới vừa thoát đi tại chỗ, vừa rồi vị trí, xuất hiện lần nữa nhất đạo rãnh sâu."),i("br"),i("br"),n._v("Lĩnh ngộ kiếm ý, có thể thi triển kiếm khí, kiếm khí vì chân khí phối hợp trường kiếm ngưng tụ mà thành, vô cùng sắc bén, không gì không phá, hắn mặc dù là nửa bước Chí Tôn cường giả, đụng tới loại vật này, cũng chỉ có chịu làm thịt phần, căn bản ngăn cản không nổi."),i("br"),i("br"),n._v("Trước đó hắn khí lực hùng hậu, làm cho đối phương không thể tới gần người, nhưng tại kiếm khí sắc bén bên dưới, loại lực lượng này liền vô dụng, thật muốn thi triển, làm không cẩn thận sẽ bị cắt ra, bản thân bị trọng thương."),i("br"),i("br"),n._v("“Liễu Trình, ngươi...”"),i("br"),i("br"),n._v("Liên tục lăn hai vòng, vừa vội vừa tức, Lâm Nhược Thiên lần nữa gào thét, có điều, thanh âm không có kết thúc, lại một đường kiếm mang thẳng tắp đâm tới."),i("br"),i("br"),n._v("Tê lạp!"),i("br"),i("br"),n._v("Lần này né tránh không kịp, trên người mát lạnh, rộng rãi ống tay áo, từ đó xé mở, tóc cũng mất tận mấy cái."),i("br"),i("br"),n._v("Cảm nhận được trong kiếm mang băng hàn thấu xương, biết vừa rồi chậm một bước nữa, khẳng định ruột xuyên bụng nát, lại không để ý tới nói nhảm, liên tục lăn lộn."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("“Thật sự là kì quái, dụng quyền pháp, Liễu hội trưởng liên tục trốn tránh, ngăn cản không nổi, không có bất kỳ biện pháp nào... Dùng kiếm, Lâm hội trưởng liền đứng dậy đều làm không được, chỉ có thể lăn lộn đầy đất...”"),i("br"),i("br"),n._v("“Lâm gia là lấy kiếm pháp nổi tiếng gia tộc, Lâm gia chủ càng là vương quốc nổi danh kiếm thuật đại sư... Liễu hội trưởng là y sư, phổ thông lão sư, liền kiếm đều không có! Hiện tại kiếm thuật đại sư quyền pháp mạnh, không có kiếm y sư kiếm thuật tinh...”"),i("br"),i("br"),n._v("“Chuyện này là sao...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Hai mặt nhìn nhau, tất cả mọi người choáng váng đồng dạng."),i("br"),i("br"),n._v("Lâm gia chỉ dùng kiếm gia tộc, kiếm thuật kéo dài không dưới mấy trăm năm, bàn về kiếm thuật, thậm chí toàn bộ Thiên Vũ vương quốc đều lấy hắn vi tôn."),i("br"),i("br"),n._v("Vốn là mọi người thấy hắn xuất ra trường kiếm, đều cho rằng vị này Liễu hội trưởng tất thua không thể nghi ngờ, nằm mộng cũng nghĩ không ra, sẽ xuất hiện loại cục diện này."),i("br"),i("br"),n._v("Đường đường kiếm thuật đại sư Lâm gia chủ, liên tục mấy chục chiêu, phản kích đều phản kích không được, chỉ có thể lăn lộn đầy đất..."),i("br"),i("br"),n._v("Cũng quá kém đi!"),i("br"),i("br"),n._v("“Không phải Lâm gia chủ quá kém, mà là... Liễu hội trưởng quá mạnh!”"),i("br"),i("br"),n._v("Tạ viện trưởng vẻ mặt nghiêm túc: “Dựa theo bình thường đạo lý, coi như hắn lĩnh ngộ kiếm ý, kiếm tâm, chỉ có Tông sư đỉnh phong thực lực, không có khả năng liên tục thi triển kiếm khí công kích, hơn nữa, kiếm khí công kích cũng có nhất định phạm vi, công kích càng xa, đối với chân khí tiêu hao cũng là càng lớn!”"),i("br"),i("br"),n._v("Thiên Vũ học viện mấy vị lão sư đồng thời gật đầu."),i("br"),i("br"),n._v("Kiếm khí là chân khí ngoại phóng, công kích khoảng cách càng xa, tán phát càng nhanh, uy lực càng nhỏ không nói, hao tổn còn càng lớn."),i("br"),i("br"),n._v("“Các ngươi nhìn, liên tục công kích nhiều như vậy chiêu, kiếm khí vẫn như cũ huy hoàng, uy lực rất mạnh, làm cho Lâm gia chủ cũng không dám chống cự... Đủ để chứng minh, chân khí trong cơ thể hắn, hoặc là không gì sánh được hùng hậu, hoặc là không gì sánh được tinh thuần!”"),i("br"),i("br"),n._v("Tạ viện trưởng chậm rãi nói: “Chỉ có chân khí hùng hậu đến cảnh giới nhất định, mới dám không kiêng nể gì như thế sử dụng, không lo lắng khô kiệt! Cũng chỉ có tinh thuần đến cảnh giới nhất định, mới có thể để cho kiếm mang rời đi thân thể xa như vậy, còn vẫn như cũ duy trì tụ mà không tiêu tan, lực lượng mười phần!”"),i("br"),i("br"),n._v("“Đúng vậy a!”"),i("br"),i("br"),n._v("Mọi người tất cả đều gật đầu."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Bên này tất cả mọi người khiếp sợ câm lại không nói gì, bên kia Trương Huyền trường kiếm trong tay múa như gió."),i("br"),i("br"),n._v("“Lâm gia chủ, ngươi không phải mới vừa muốn cùng ta dùng binh khí tỷ thí sao? Ỷ lại trên mặt đất lăn lộn có ý tứ gì...”"),i("br"),i("br"),n._v("Hét lớn một tiếng, Trương Huyền nghĩa chính ngôn từ: “Có thể hay không chính diện cùng ta chiến đấu?”"),i("br"),i("br"),n._v("“Phốc!”"),i("br"),i("br"),n._v("Nghe nói như thế, một hồi buồn bực, Lâm Nhược Thiên không cẩn thận bị kiếm khí quét trúng, một ngụm máu tươi cuồng phún mà ra."),i("br"),i("br"),n._v("Điên rồi!"),i("br"),i("br"),n._v("Đây chính là hắn mới vừa nói đối phương, không nghĩ tới bị trực tiếp dời đi ra."),i("br"),i("br"),n._v("Chính diện đối chiến?"),i("br"),i("br"),n._v("Bà mẹ nó ah!"),i("br"),i("br"),n._v("Ngươi kiếm khí tung hoành, đánh ta liền năng lực ngăn cản đều không có, thế nào chính diện chiến đấu?"),i("br"),i("br"),n._v("Vừa rồi quyền pháp làm cho hắn bốn phía trốn tránh, không dám chính diện tiếp xúc, đánh thật tốt, làm ra vẻ bức, nhất định phải dùng binh khí, lần này tốt rồi, bức không có giả dạng làm, biến thành mộng bức."),i("br"),i("br"),n._v("Thiên Vũ vương quốc đệ nhất cao thủ, nửa bước Chí Tôn cường giả..."),i("br"),i("br"),n._v("Ai từng thấy bị người đánh, lăn lộn đầy đất cao thủ?"),i("br"),i("br"),n._v("“Liêu điện chủ, Đại Dược Vương, nhanh lên động thủ... Chỉ cần độc chết gia hỏa này, các ngươi điều kiện gì ta đều đáp ứng, hơn nữa cấp cho gấp đôi!”"),i("br"),i("br"),n._v("Lại đánh một hồi cút, biết tiếp tục nữa, sớm muộn gì đều sẽ bị kiếm khí của đối phương chém giết, Lâm Nhược Thiên cũng nhịn không được nữa, hét dài một tiếng."),i("br"),i("br"),n._v("Gia tộc tất cả trưởng lão đều không khác mấy phế đi, đã không có cách nào đối phó người thanh niên này, hy vọng duy nhất liền là sau lưng vị này Liêu điện chủ cùng Đại Dược Vương."),i("br"),i("br"),n._v("Thân là Độc điện điện chủ, có thể vượt cấp giết địch, chỉ cần dùng độc, coi như kiếm khí mạnh hơn cũng vô dụng!"),i("br"),i("br"),n._v("“Tốt, nhớ kỹ ngươi lời nói, người này, ta tới giúp ngươi giải quyết!” Nghe được hứa hẹn, Liêu Huân hài lòng gật đầu đi ra."),i("br"),i("br"),n._v("“Liêu điện chủ? Đại Dược Vương? Độc chết? Chẳng lẽ là... Hồng Liên thành Đại Dược Vương, Độc điện điện chủ Liêu Huân?”"),i("br"),i("br"),n._v("Trong đám người không biết ai hô lên, tất cả mọi người sắc mặt đồng thời tái đi, một hồi xôn xao."),i("br"),i("br"),n._v("(Thứ hai, cầu phiếu đề cử! Mặt khác, lại tuyết rơi, lạnh quá, ô ô!)"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-394"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-396"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);