(window.webpackJsonp=window.webpackJsonp||[]).push([[1631],{1986:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-2-khong-biet-xau-ho"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-2-khong-biet-xau-ho"}},[n._v("#")]),n._v(" Chương 2: Không biết xấu hổ")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v(" Lại nghiên cứu một hồi, phát hiện trong thư viện sách, tất cả đều trong sương nhìn hoa, ngắm trăng trong nước, căn bản không lấy được, Trương Huyền đã mất đi hứng thú, tinh thần thối lui ra khỏi thức hải."),i("br"),i("br"),n._v(" “Nên ăn cơm trưa, buổi chiều lại nghĩ cách lắc lư hai cái!” "),i("br"),i("br"),n._v(" Ngẩng đầu nhìn về phía ngoài cửa sổ, mặt trời chạy tới ngay chính giữa, buổi sáng tổng cộng đến rồi mười tám học sinh, chỉ làm cho một cái bái sư, xác suất thật là đủ thấp, buổi chiều cũng không thể tiếp tục như vậy, bất kể nói thế nào, đều là cái người xuyên việt, nếu như ngay cả một cái cổ nhân đều không lừa được, làm sao có mặt nói cuộc sống mình ở tin tức quá độ đạt thời đại?"),i("br"),i("br"),n._v(" Vặn eo bẻ cổ đi ra tự mình giảng đường, chậm rãi đi vào học viện căng tin."),i("br"),i("br"),n._v(" Cùng kiếp trước trường đại học như thế, Hồng Thiên học viện căng tin mười phần rộng lớn, có thể khiến hơn vạn học viên đồng thời cùng ăn, chiêu thu đến một người học viên, tâm tình thật tốt, Trương Huyền đánh thêm vài phần món ăn, tìm hẻo lánh địa phương, ăn như gió cuốn."),i("br"),i("br"),n._v(" “Đây không phải Trương lão sư sao?” "),i("br"),i("br"),n._v(" Chính ăn được cao hứng, một giọng nói vang lên, ngẩng đầu nhìn lại, chỉ thấy một người thanh niên cười khanh khách nhìn sang, không có ấm áp, trái lại cho người ta ngoài cười nhưng trong không cười cảm giác."),i("br"),i("br"),n._v(" “Tào lão sư?” Trương Huyền nhận ra được."),i("br"),i("br"),n._v(" Tào lão sư gọi Tào Hùng, cùng hắn đồng thời tiến vào học viện, luôn luôn yêu thích cùng với ganh đua so sánh, sau đó mượn cơ hội đả kích."),i("br"),i("br"),n._v(" Tiền thân cũng là bởi vì không chịu được các loại trào phúng, mới say rượu tử vong, để cho chịu đựng loại áp lực này, cái tên này khẳng định cũng không trốn được can hệ."),i("br"),i("br"),n._v(" “Ngày hôm nay tân sinh đưa tin, tự chủ lựa chọn lão sư, thu hoạch làm sao? Nhìn ngươi còn có tâm tình trong này ăn cơm, phải rất khá đi! Nhìn, đây đều là ta mới chiêu học viên, tổng cộng mười hai cái, ta trước tiên dẫn bọn họ ăn một bữa cơm! Sau đó sắp xếp chỗ cư trú!” "),i("br"),i("br"),n._v(" Tào lão sư mang trên mặt cao cao tại thượng tâm ý, hướng về sau chỉ tay, trần trụi khoe khoang."),i("br"),i("br"),n._v(" Không sai, hắn chính là lại đây khoe khoang."),i("br"),i("br"),n._v(" Hắn cùng Trương Huyền trong lúc đó cũng không có cái gì cừu hận, hai người đồng thời vào trường học, tự nhiên khó tránh khỏi bị nhân so sánh đánh giá, người sau là thất bại điển phạm, hắn đương nhiên muốn biểu lộ ra một hồi cảm giác ưu việt."),i("br"),i("br"),n._v(" Phía sau hắn quả nhiên theo một đám thiếu niên, từng cái từng cái tinh thần chấn hưng, đối với chuyện mới mẻ vật tràn ngập tò mò."),i("br"),i("br"),n._v(" “Chư vị, ta giới thiệu cho các ngươi một chút, vị này Trương Huyền lão sư, là chúng ta học viện danh nhân, nha, là xây trường qua nhiều năm như vậy, một cái duy nhất thầy giáo sát hạch đến không điểm! Khai sáng một cái mới lịch sử!” "),i("br"),i("br"),n._v(" Tào lão sư hướng về mọi người giới thiệu."),i("br"),i("br"),n._v(" “Thầy giáo sát hạch đến không điểm?” "),i("br"),i("br"),n._v(" “A, khi ta tới nghe nói, hắn dạy học sinh tẩu hỏa nhập ma, kém chút phế bỏ!” "),i("br"),i("br"),n._v(" “Ta cũng nghe nói, trước khi đến không ít người nói với ta, tuyệt đối không nên tuyển hắn làm lão sư, bằng không, không chỉ không có cách nào tu luyện, còn tương đương tự sát!” "),i("br"),i("br"),n._v(" “Không nghĩ tới hắn chính là a, xem ra vẫn rất hiền lành!” "),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" Nghe được Tào Hùng giới thiệu, một đám học viên nghị luận sôi nổi."),i("br"),i("br"),n._v(" Thầy giáo sát hạch tổng hợp nhiều loại nhân tố, nhiều hạng tính gộp lại, tiến hành thống nhất cho điểm, trong đó, học sinh kiểm tra cũng chiếm một phần, chỉ cần vị lão sư này có học sinh, liền sẽ có thành tích, trực tiếp đến không điểm, xem như là đột phá lịch sử."),i("br"),i("br"),n._v(" “Giới thiệu xong rồi?” "),i("br"),i("br"),n._v(" Đối mặt mọi người nghị luận, Trương Huyền không hề cảm thấy tức giận."),i("br"),i("br"),n._v(" Đến không điểm chính là trước cái kia Trương Huyền, quan tâm chính mình đánh rắm?"),i("br"),i("br"),n._v(" Bất quá, tuy rằng không thế nào tức giận, nhưng đối với vị này Tào lão sư đè thấp người khác, nâng lên hành vi của chính mình, vẫn là hết sức khó chịu, tùy ý khoát tay áo một cái: “Giới thiệu xong là có thể cút đi, đừng ở chỗ này làm lỡ ta ăn cơm!” "),i("br"),i("br"),n._v(" Không nghĩ tới nói ra của hắn lịch sử, cái tên này không có một chút nào xấu mặt giác ngộ, còn để cho mình cút đi, Tào Hùng sắc mặt khó coi, hơi vung tay, lộ ra lão sư mới có sư đạo uy nghiêm: “Thầy giáo sát hạch được không điểm, phá học viện ghi chép, ngươi lẽ nào liền không có một chút nào xấu hổ chi tâm sao?” "),i("br"),i("br"),n._v(' "Xấu hổ? Ta tại sao muốn xấu hổ? Ngươi cũng đã nói, ta phá lịch sử, thành danh nhân, mới tới học sinh đều biết ta là ai, mà ngươi đây?" Trương Huyền giơ tay chỉ về, Tào Hùng sau lưng rất nhiều học viên: "Ngươi thầy giáo sát hạch được mấy phần? Bọn họ biết không? Không có tới học viện trước, biết ngươi là ai sao? Nếu như không phải liều mạng lôi kéo,'),i("br"),i("br"),n._v(' Thậm chí còn xin mời ăn cơm, ngươi cảm thấy bọn họ sẽ bái ngươi làm thầy? Làm như một cái lão sư, một chút tiếng tăm đều không có, cùng ta khoe khoang, ngươi khoe khoang cái rắm a!"'),i("br"),i("br"),n._v(" “Dát?” "),i("br"),i("br"),n._v(" Người khác thầy giáo sát hạch được không điểm, tuyệt đối sẽ che lại đầu đi ra, chỉ lo mất mặt xấu hổ, vị này ngược lại tốt, trái lại dương dương tự đắc, một mặt kiêu ngạo, tự mình không có không điểm, trái lại đã thành bị hắn khinh bỉ đối tượng."),i("br"),i("br"),n._v(" Tào Hùng đều sắp điên rồi."),i("br"),i("br"),n._v(" Này trái tim cũng quá lớn đi! Mấu chốt là... Liền này loại thành tích, ngươi đến cùng từ đâu tới vốn để kiêu ngạo?"),i("br"),i("br"),n._v(" Đứng sau lưng hắn học sinh, cũng từng cái từng cái hai mặt nhìn nhau, triệt để mộng bức."),i("br"),i("br"),n._v(" Mặt đây, mặt đây?"),i("br"),i("br"),n._v(" Vị lão sư này... Cũng quá không biết xấu hổ đi!"),i("br"),i("br"),n._v(" Đối với Trương Huyền tới nói, cái gì xấu hổ, da mặt, đùa gì thế, hắn sinh hoạt thời đại, có chút minh tinh vì thượng vị, mất mặt gì làm gì! Cái gì khuếch đại làm cái gì, quả chiếu, đường viền hoa tin tức, các loại cửa, cũng không cảm thấy được xấu hổ, hắn cũng là thầy giáo thi không điểm mà thôi, không đáng kể chút nào."),i("br"),i("br"),n._v(" Tào Hùng tức giận sắc mặt ửng hồng: “Lão sư hay là muốn lấy giáo dục vì chủ, ngày hôm nay ta không so đo với ngươi, chờ ngươi chừng nào thì chiêu đến học sinh, có bản lĩnh chúng ta lại nhiều lần, ai dạy học sinh càng có một tay!” "),i("br"),i("br"),n._v(" Nói xong cũng muốn xoay người rời đi."),i("br"),i("br"),n._v(" Đang lúc này, phía sau vang lên một nam một nữ đối thoại âm thanh."),i("br"),i("br"),n._v(" “Người lão sư kia thật là khá, tính cách cũng tốt...” "),i("br"),i("br"),n._v(" Một cái ngốc manh giọng nữ vang lên, trong giọng nói tràn đầy chần chờ."),i("br"),i("br"),n._v(" “Ta Nhị tiểu thư, ngươi liền nghe ta, lâm tới thời điểm, thiếu gia đều giao cho ta, cho ngươi đi bái Lục Tầm lão sư vi sư, ngươi lệch không nghe, còn cố ý đem ta đẩy ra, này ngược lại cũng thôi, bái người nào không được, nhất định phải bái hắn...” "),i("br"),n._v("  Tiếp theo một ông già âm thanh, trong giọng nói mang theo bất đắc dĩ."),i("br"),i("br"),n._v(" “Vị lão sư kia... Không giống ngươi nói như vậy không thể tả, hắn... Người khác rất tốt, còn giúp ta chỉ điểm, nói ta... Cố gắng tu luyện, có thể trở thành là lớp đệ nhất...” Giọng nữ bên trong vẫn là mang theo do dự."),i("br"),i("br"),n._v(" “Còn lớp số một, thật muốn với hắn học, không tẩu hỏa nhập ma chính là chuyện tốt, Nhị tiểu thư biết hắn là ai sao? Học viện nổi danh nhất rác rưởi lão sư, đi học kỳ thầy giáo sát hạch được không điểm... Ta tiểu tổ tông, ngươi nhanh lên một chút lui đi đi, không phải vậy thiếu gia nếu như biết chắc sẽ đem ta giết...” Ông lão trong thanh âm mang theo cầu xin."),i("br"),i("br"),n._v(" “Ca ca!” "),i("br"),i("br"),n._v(" Nghe được ông lão nói tới thiếu gia, nữ hài bắt đầu sợ lên, sắc mặt nhăn nhó, không biết nên làm sao bây giờ."),i("br"),i("br"),n._v(" Đem những câu nói này nghe vào trong tai, đang muốn rời đi Tào Hùng ánh mắt sáng lên, cười khanh khách nhìn về phía đang dùng cơm Trương Huyền: “Trương lão sư, cô bé này lẽ nào là ngươi vừa chiêu thu học sinh? Ha ha, xem ra không tốt lắm, nhân gia dự định lui đi a!” "),i("br"),i("br"),n._v(" Giáo sư có thể lựa chọn học sinh, học sinh cũng có thể lựa chọn lão sư."),i("br"),i("br"),n._v(" Nếu như cảm thấy lão sư cùng mình không thích hợp, hoàn toàn có thể mang hắn ban cho lệnh bài trả lại."),i("br"),i("br"),n._v(" Tào Hùng âm thanh rất lớn, lập tức đưa tới không ít người chú ý, chính đang thảo luận chủ tớ hai, cũng đưa mắt tập trung lại đây."),i("br"),i("br"),n._v(" “Nhị tiểu thư, hắn chính là ngươi vừa bái lão sư?” Ông lão ánh mắt lạc trên người Trương Huyền."),i("br"),i("br"),n._v(" “Rõ!” Nữ hài gật đầu."),i("br"),i("br"),n._v(" Ông lão lập tức đứng dậy, vài bước đi tới Trương Huyền trước mặt: “Trương lão sư, tiểu thư nhà chúng ta, dự định lui đi chương trình học của ngươi!” "),i("br"),i("br"),n._v(" “Lưu lão...” Không nghĩ tới ông lão động tác nhanh như vậy, nữ hài hơi đỏ mặt, vội vàng đi lên phía trước, tiếp theo quay đầu nhìn về phía Trương Huyền đầy mặt áy náy: “Lão sư, ta...” "),i("br"),i("br"),n._v(" Chính là Trương Huyền vừa thu học sinh, Vương Dĩnh."),i("br"),i("br"),n._v(" “Vương Dĩnh, ngươi cũng biết ta luôn luôn không thu học sinh, thu phục ngươi, là cùng ngươi hữu duyên, ngươi muốn lui đi, không công từ bỏ tốt đẹp như vậy cơ hội? Ngươi biết bao nhiêu người đều hi vọng trở thành học sinh của ta, mà ta nhưng không thu sao?” "),i("br"),i("br"),n._v(" Trương Huyền đương nhiên không thể để cho thật vất vả tới tay tiêu chuẩn chạy mất, giả trang ra một bộ lão khí hoành thu (như ông cụ non) dáng dấp."),i("br"),i("br"),n._v(" “Giời ạ...” "),i("br"),i("br"),n._v(" Nghe được hắn lời này, xung quanh biết nội tình người, mỗi một người đều cảm thấy trước mắt biến thành màu đen, sắp ngất đi."),i("br"),i("br"),n._v(" Đại ca, ngươi có thể muốn chút mặt không? Còn luôn luôn không thu học sinh, cùng ngươi hữu duyên, vẫn ít nhiều người đều hi vọng trở thành học sinh của ngươi... Rõ ràng là ngươi không thu được học sinh được rồi..."),i("br"),i("br"),n._v(" “Ta, không phải...” "),i("br"),i("br"),n._v(" Bị hắn chất vấn, Vương Dĩnh vội vã xua tay, bất quá lời còn chưa nói hết liền bị cắt đứt."),i("br"),i("br"),n._v(" “Rõ!” Thấy Nhị tiểu thư tính cách không quả đoán, ông lão Lưu thúc bước lên trước: “Trương lão sư, nhà chúng ta Nhị tiểu thư, đã quyết định muốn lui đi chương trình học của ngươi, nàng không tiện nói ra, còn xin ngươi giúp một tay công việc thủ tục đi!” "),i("br"),i("br"),n._v(" “Lui đi?” Trương Huyền mí mắt một phen: “Ngươi cần phải hiểu rõ, lui đi lão sư, ở trong học viện lại sẽ lưu lại danh tiếng xấu, sau đó e sợ lại không lão sư dám thu! Ngươi chẳng lẽ muốn bởi vì chính mình tùy hứng, làm lỡ nhà các ngươi tiểu thư cả đời? Trách nhiệm này ngươi gánh chịu nổi?” "),i("br"),i("br"),n._v(" “Chuyện này...” Lưu lão sững sờ."),i("br"),i("br"),n._v(" Học sinh là có thể lui đi lão sư, bất quá lui đi lão sư, là đối lão sư sỉ nhục, hơn nữa, ngươi có thể lui đi vị này, liền cho thấy có thể lui đi một vị khác, bình thường tới nói, có này loại “Việc xấu” học viên, các lão sư khác là sẽ không lại thu."),i("br"),i("br"),n._v(" Dù sao sư đạo tôn nghiêm bị nhân ngay mặt sỉ nhục, đổi lại ai cũng không muốn đón thêm bị loại học viên này."),i("br"),i("br"),n._v(" Hơn nữa, tiếp nhận rồi, đại biểu đánh một cái khác lão sư mặt, tất cả mọi người là đồng sự, không thể là vì một học sinh, đắc tội đồng liêu."),i("br"),i("br"),n._v(" Ở học viện tu luyện, không có lão sư, đời này chẳng khác nào phế bỏ."),i("br"),i("br"),n._v(" Lưu lão mới vừa rồi còn khí thế hừng hực, Trương Huyền một câu nói, liền để hắn không biết nên làm gì bây giờ."),i("br"),i("br"),n._v(" Hắn chỉ là cái hạ nhân, vạn nhất Nhị tiểu thư bởi vậy làm lỡ, hắn trăm chết không đền được tội."),i("br"),i("br"),n._v(" “Yên tâm đi, nhà các ngươi Nhị tiểu thư thiên tư không sai, ta cố gắng giáo dục, nhất định sẽ có không tệ thành tích...” Thấy hắn dao động, Trương Huyền lần thứ hai triển khai lắc lư đại pháp."),i("br"),i("br"),n._v(" Đùa giỡn, con vịt đã đun sôi làm sao có khả năng làm cho nàng bay!"),i("br"),i("br"),n._v(" “Chậm đã, ai nói lại không lão sư dám thu? Tiểu cô nương, ngươi chỉ cần lui đi vị này Trương lão sư, ta lợi mã thu ngươi làm học sinh của ta!” "),i("br"),i("br"),n._v(" Trương Huyền lời còn chưa nói hết, một bên Tào Hùng bước lên trước, bàn tay lớn vẫy một cái."),i("br"),i("br"),n._v(" Hắn mới vừa rồi bị Trương Huyền hao tổn mặt mũi, lần này có cơ hội, sao có thể buông tha."),i("br"),i("br"),n._v(" “Tào Hùng, ngươi muốn làm gì?” "),i("br"),i("br"),n._v(" Trương Huyền sầm mặt lại."),i("br"),i("br"),n._v(" “Làm gì? Cái này tốt mầm, không thể lãng phí, nếu như nàng muốn đem ngươi lui đi, ta lập tức tiếp thu! Nhân gia đến học viện học tập, đương nhiên muốn tuyển chọn tốt nhất có thể cho nàng chỉ điểm lão sư, mà không phải thầy giáo sát hạch đến không điểm!” "),i("br"),i("br"),n._v(" Cười hì hì, Tào Hùng tràn đầy dương dương tự đắc."),i("br"),i("br"),n._v(" “Ngươi đây là công nhiên cướp học sinh, có tin ta hay không bẩm báo chính giáo nơi?” "),i("br"),i("br"),n._v(" Đây cũng không phải là đơn thuần âu khí, mà là công nhiên cướp học sinh!"),i("br"),i("br"),n._v(" Học viện tuy rằng cổ vũ lão sư tự chủ lựa chọn, nhưng không đề xướng này loại lung tung cướp giật! Một khi phát sinh, ảnh hưởng giáo sư ở giữa cảm tình không nói, còn làm toàn bộ học viện hỏng bét, ảnh hưởng bầu không khí."),i("br"),i("br"),n._v(" “Cướp học sinh? Ngươi nói quá lời, quá mức chúng ta đến cái hiện trường chỉ điểm, công bằng cạnh tranh, để học sinh tự chủ lựa chọn, có dám hay không đáp ứng?” "),i("br"),i("br"),n._v(" Tào Hùng nói."),i("br"),i("br"),n._v("Convert by: Hoang Chau"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-3"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-5"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);