(window.webpackJsonp=window.webpackJsonp||[]).push([[1640],{1995:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-398-tien-su-bi-tu-choi-hoa-chuong-than-khoc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-398-tien-su-bi-tu-choi-hoa-chuong-than-khoc"}},[n._v("#")]),n._v(" Chương 398: Tiên sư bị từ chối, hóa chuông than khóc")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Vạn quốc liên minh, Danh Sư đường, nghị sự điện."),t("br"),t("br"),n._v("“Chuyện lần này, chắc hẳn mọi người đều biết, hi vọng chư vị có thể dốc hết toàn lực, đừng rơi chúng ta tên tuổi!”"),t("br"),t("br"),n._v("Một cái lão giả ngồi ngay ngắn ở ở giữa nhất trên chỗ ngồi, lông mi lạnh lùng, bàn tay lớn phất một cái."),t("br"),t("br"),n._v("Vạn quốc liên minh Danh Sư đường đường chủ, tứ tinh đỉnh phong danh sư, khang dám đường chủ."),t("br"),t("br"),n._v("“Đường chủ yên tâm, chúng ta sẽ dốc hết toàn lực, tìm kiếm thí sinh thích hợp, sẽ không rơi chúng ta vạn quốc liên minh tên tuổi!”"),t("br"),t("br"),n._v("“Tô trưởng lão, Lăng trưởng lão nghe nói Hiên Viên vương quốc bên kia ra một thiên tài, vài ngày trước liền đã chạy tới!”"),t("br"),t("br"),n._v("“Bạch trưởng lão, Lộc trưởng lão, nghe nói bồn xa vương quốc bên kia có thiên tài khảo hạch thành công, cũng tại Bát Thiên trước đi qua!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trong sảnh rất nhiều lão giả nhao nhao trả lời."),t("br"),t("br"),n._v("Những lão giả này, đều mặc danh sư đặc hữu trường bào, trước ngực huy chương ghi chú đẳng cấp, kém cỏi nhất đều là nhị tinh, cao càng là tam tinh, tứ tinh, phản xạ quang mang, để cho người ta quáng mắt."),t("br"),t("br"),n._v("“Vậy là tốt rồi, sớm chuẩn bị, thật tốt ứng đối. Dĩ vãng chúng ta vạn quốc liên minh đều là hạng chót, lần này nhất định phải để bọn hắn nhìn xem, chúng ta thiên tài không kém chút nào!”"),t("br"),t("br"),n._v("Đường chủ hừ một tiếng, gật gật đầu."),t("br"),t("br"),n._v("“Ừm...”"),t("br"),t("br"),n._v("Mọi người lên tiếng, đang muốn tiếp tục nói chuyện, một giờ tiếng đột nhiên vang lên."),t("br"),t("br"),n._v("Mang theo than khóc, tựa như xuất hiện to lớn gì biến cố."),t("br"),t("br"),n._v("Đông đông đông đông!"),t("br"),t("br"),n._v("Ngay sau đó vô số tiếng chuông liên tiếp vang lên."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?” Khang đường chủ đột nhiên đứng dậy."),t("br"),t("br"),n._v("“Vị trí đó là... Phong Sư điện!”"),t("br"),t("br"),n._v("Nhìn thoáng qua thanh âm truyền đến phương hướng, một vị danh sư biến sắc, la lên."),t("br"),t("br"),n._v("“Nhìn xem!”"),t("br"),t("br"),n._v("Khang đường chủ đám người cũng nhịn không được nữa, vội vàng liền xông ra ngoài, đi tới Phong Sư điện, đẩy cửa nhìn lại, chỉ thấy vô số tiên sư bài vị không ngừng run rẩy, tựa như gặp cái gì chuyện thương tâm, không ngừng than khóc, từng đạo đặc thù khí tức, vờn quanh trong đó, sấm rền đồng dạng quanh quẩn một chỗ, gào thét không dứt."),t("br"),t("br"),n._v("“Đây là... Tiên sư bị từ chối, hóa chuông than khóc?”"),t("br"),t("br"),n._v("Biến sắc, khang đường chủ thân thể nhoáng một cái, kém chút ngã sấp xuống."),t("br"),t("br"),n._v("“Tiên sư bị từ chối? Có ý tứ gì?”"),t("br"),t("br"),n._v("Thấy hắn như thế thất thố, một vị danh sư nhịn không được hỏi lên."),t("br"),t("br"),n._v("“Khảo hạch danh sư thành công, cần tiền bối tán thành, rất nhiều người đều coi đây là vinh dự!” Khang đường chủ giải thích."),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Đây là danh sư đều biết chuyện, khảo hạch đẳng cấp càng thấp, công nhận tiền bối càng nhiều, đại biểu sau này thành tựu càng lớn, cũng là càng tự hào."),t("br"),t("br"),n._v("“Làm danh sư, chúng ta đều biết, lão sư có thể lựa chọn học sinh, học sinh cũng tương tự có thể lựa chọn lão sư!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, khang đường chủ nói đến đây, tựa hồ vẫn là không dám tin tưởng: “Các tiền bối có thể tán thành vãn bối, đồng dạng... Cũng sẽ lọt vào cự tuyệt!”"),t("br"),t("br"),n._v("“Cự tuyệt? Tiền bối tán thành, chẳng những là vinh dự, cũng có thể ban cho đặc thù khí tức, để tâm cảnh tăng lên, có người sẽ cự tuyệt?”"),t("br"),t("br"),n._v("Mọi người sững sờ, cũng tất cả đều con mắt trợn tròn."),t("br"),t("br"),n._v("Tiền bối tán thành, đại biểu vô thượng vinh dự không nói, càng quan trọng hơn là, có thể trợ giúp tâm cảnh tăng lên, có người cự tuyệt? Có ý tứ gì?"),t("br"),t("br"),n._v("“Được, có người sẽ cự tuyệt! Dung hợp ban cho khí tức, chẳng khác nào lạc ấn vị này tiền bối ý niệm, cùng hắn nắm giữ bán sư tình nghĩa. Chư vị cũng biết, tình nghĩa đón lấy, chẳng khác nào chạm phải nhân quả, phải có hồi báo mới có thể hoàn chỉnh. Một chút siêu cấp thiên tài, không nguyện ý tiếp nhận loại này tình nghĩa, liền trực tiếp cự tuyệt!”"),t("br"),t("br"),n._v("Khang đường chủ nói."),t("br"),t("br"),n._v("“Cái này...” Mọi người ngây người."),t("br"),t("br"),n._v("Bọn hắn những người này, còn mỗi ngày nằm mơ muốn cho tiền bối tán thành, lại có thể có người cự tuyệt... Đến cùng làm sao làm được?"),t("br"),t("br"),n._v("“Tiền bối bài vị, mặc dù chỉ là một đạo tàn niệm, lại ẩn chứa bọn hắn khi còn sống tinh thần, bị hậu bối cự tuyệt, đương nhiên than khóc không dứt! Liền là trước mắt loại tình huống này, tiên sư bị từ chối, hóa chuông than khóc! Những này ta cũng chỉ tại trong thư tịch thấy qua, không nghĩ tới... Là thật!”"),t("br"),t("br"),n._v("Khang đường chủ vẻ mặt trắng bệch: “Mấu chốt nhất là... Nhiều như vậy bài vị đồng thời than khóc, cho thấy... Những này tiền bối đồng thời công nhận người nào đó, kết quả bị... Từ chối!”"),t("br"),t("br"),n._v("“Nhiều như vậy tiền bối đồng thời tán thành?”"),t("br"),t("br"),n._v("“Cái này... Loại này làm sao có thể?”"),t("br"),t("br"),n._v("Tất cả mọi người giật nảy mình, kém chút không có tại chỗ ngất đi."),t("br"),t("br"),n._v("Vạn quốc liên minh mấy trăm năm qua, nổi danh nhất thiên tài Khương Tử Kiệt, cũng đành phải đến bảy vị tiền bối tán thành, đây là ai? Thoáng cái đạt được tất cả tiền bối tán thành, mấu chốt nhất là... Còn cự tuyệt?"),t("br"),t("br"),n._v("Ta nghe lầm chứ?"),t("br"),t("br"),n._v("Vạn quốc liên minh Danh Sư đường tất cả danh sư, tất cả đều bờ môi run rẩy, mặt mũi tràn đầy phát điên."),t("br"),t("br"),n._v("“Cho ta thật tốt điều tra thêm, khẳng định tại chúng ta vạn quốc liên minh bên trong, nếu không, không có khả năng xuất hiện loại sự tình này! Một vị để chư hiền bái phục, toàn bộ công nhận thiên tài, nếu như có thể mời tới, tuyệt đối có thể tạo thành oanh động...”"),t("br"),n._v(" Khang đường chủ hai mắt tỏa ánh sáng, đang muốn tiếp tục phân phó, mặt đất đột nhiên chấn động."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Chỉ thấy ngay phía trước tượng Khổng sư cũng run rẩy kịch liệt, không ngừng than khóc."),t("br"),t("br"),n._v("“Khổng sư than khóc? Chẳng lẽ... Khổng sư cũng bị cự tuyệt?”"),t("br"),t("br"),n._v("Tất cả mọi người đần độn tại chỗ, tựa như gà gỗ."),t("br"),t("br"),n._v("Khang đường chủ cũng miệng há mở, câu nói kế tiếp, rốt cuộc nói không nên lời."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Cái này... Thật không cần phải để ý đến?”"),t("br"),t("br"),n._v("Thấy Khương đường chủ đám người quay người rời đi, bị đè ở phía dưới tiền bối bài vị, tượng Khổng sư cũng không để ý, Trương Huyền nhịn không được kỳ quái."),t("br"),t("br"),n._v("Đây chính là thiên hạ sư pho tượng, vô số tiền bối bài vị, thế nào..."),t("br"),t("br"),n._v("“Danh sư xem thấu sự vật bản chất, trọng ý không trọng hình, đối Khổng sư cùng tiền bối kính ngưỡng dưới đáy lòng, mà không phải tại những này bài vị bên trên. Nếu không, bài vị ngã sấp xuống, vốn là đại bất kính, thế nào còn sẽ có vãn bối cam tâm tình nguyện chờ lấy? Yên tâm đi, Danh Sư đường có chuyên môn thợ thủ công phụ trách chữa trị, hẳn là không bao lâu, liền có thể để Phong Sư điện, lần nữa hoàn hảo như lúc ban đầu!”"),t("br"),t("br"),n._v("Khương đường chủ giải thích."),t("br"),t("br"),n._v("“Trọng ý không trọng hình?” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nói như vậy lên, cũng không tệ."),t("br"),t("br"),n._v("Hắn nhận biết danh sư, Lưu Lăng, Trang Hiền, Khương Thư đám người, cũng không có lấy cái gì danh sư giá đỡ, ngược lại cùng người bình thường đồng dạng, gặp phải đúng, trực tiếp thừa nhận, gặp phải sai, mở lời phản bác."),t("br"),t("br"),n._v("Thuận ý mà làm, hài lòng là chuyện."),t("br"),t("br"),n._v("Biết đối phương biết hắn đánh nát tượng Khổng sư cũng là cử chỉ vô tâm, lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Một đường tiến lên, chỉ chốc lát đi tới phòng nghị sự."),t("br"),t("br"),n._v("“Ta hiện tại giới thiệu cho các ngươi một chút, hai vị này là đến từ vạn quốc liên minh tứ tinh danh sư, Tô sư, Lăng sư!” Mọi người vào chỗ, Khương đường chủ giới thiệu."),t("br"),t("br"),n._v("“Tứ tinh danh sư?”"),t("br"),t("br"),n._v("Trương Huyền, Mạc Hoằng Nhất sửng sốt, đồng thời giật nảy mình."),t("br"),t("br"),n._v("Trước đó bọn hắn đều cho rằng hai cái này là theo những vương quốc khác tới nhất tinh danh sư, thế nào đều không có nghĩ đến, lại là tứ tinh..."),t("br"),t("br"),n._v("Nhất là Trương Huyền, cảm thấy mặt đều tái rồi."),t("br"),t("br"),n._v("Trước đó, thế nhưng là quát lớn qua đối phương."),t("br"),t("br"),n._v("“Hai vị thiên tư tung hoành, về sau trở thành tứ tinh danh sư, chỉ là vấn đề thời gian, không cần thiết câu nệ!” Tô sư cười cười, nhìn qua: “Trương sư, tại hạ có cái yêu cầu quá đáng, không biết phương không tiện?”"),t("br"),t("br"),n._v("“Tô sư mời nói!”"),t("br"),t("br"),n._v("Biết đối phương là tứ tinh danh sư, Trương Huyền không dám làm lớn, cung kính ôm quyền."),t("br"),t("br"),n._v("Tứ tinh danh sư, không chỉ tri thức lượng uyên bác, trọng yếu nhất chính là tu vi cao thâm!"),t("br"),t("br"),n._v("Muốn trở thành tam tinh danh sư, ít nhất phải nắm giữ Chí Tôn cấp bậc thực lực, tứ tinh... Có lẽ tu vi đã đạt tới Chí Tôn trở lên."),t("br"),t("br"),n._v("“Nghe nói Dương sư ngay tại vương thành, không biết ta cùng Lăng sư, có thể hay không bái kiến một hai?” Tô sư nói."),t("br"),t("br"),n._v("“Bái kiến... Dương sư?” Trương Huyền khóe miệng giật một cái, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Nào có cái gì Dương sư... Phía sau cái này lão sư, liền là chính hắn!"),t("br"),t("br"),n._v("Hai vị tứ tinh danh sư, muốn đi bái kiến, làm sao bây giờ?"),t("br"),t("br"),n._v("Lừa gạt Khương Thư, đều phí hết sức chín trâu hai hổ, đối phương thế nhưng là tứ tinh danh sư, muốn lừa gạt ở, có lẽ không có đơn giản như vậy!"),t("br"),t("br"),n._v("E là cho dù hắn dùng ra ngụy trang sư năng lực, đối phương cũng có thể nhìn ra một hai."),t("br"),t("br"),n._v("“Thế nào? Không tiện?” Tô sư nhìn qua."),t("br"),t("br"),n._v("“Cũng không phải, chỉ là gia sư... Luôn luôn lơ lửng không cố định, ngay cả ta cũng không biết ở nơi nào...”"),t("br"),t("br"),n._v("Trương Huyền vội vàng giải thích: “Như vậy đi, gia sư một khi xuất hiện, ta liền bẩm báo!”"),t("br"),t("br"),n._v("“Ừm! Vậy làm phiền Trương sư!” Tô sư cười cười."),t("br"),t("br"),n._v("Mặc dù là tứ tinh danh sư, hai người không có bất kỳ cái gì vênh váo hung hăng cảm giác, ngược lại mang theo hòa ái."),t("br"),t("br"),n._v("Truyền thuyết, càng lợi hại danh sư càng là như thế, nếu không, cũng không có khả năng rất nhanh thu hoạch được học sinh tán thành, tốt hơn học tập tri thức."),t("br"),t("br"),n._v("“Tốt, các ngươi khảo hạch hết, ta cũng là không vòng quanh, ta cùng Lăng sư lần này tới cái này, vừa đến, là bởi vì Trương sư khảo hạch thành tích quá mức kinh người, muốn tự mình tới xem một chút. Thứ hai, còn có một việc, khả năng cần hỗ trợ!”"),t("br"),t("br"),n._v("Thấy hắn đáp ứng, Tô sư không trong vấn đề này tiếp tục xoắn xuýt, vuốt râu, nói."),t("br"),t("br"),n._v("“Tô sư có việc phân phó chính là, chúng ta Thiên Vũ vương quốc Danh Sư đường có thể làm đến, nhất định dốc hết toàn lực!” Khương đường chủ vội nói."),t("br"),t("br"),n._v("Không nói trước, đối phương là cấp trên của hắn, coi như không phải, tứ tinh danh sư, cũng có tư cách ra lệnh cho bọn họ."),t("br"),t("br"),n._v("(Hài tử phát sốt 39 độ, đêm qua bắt đầu đốt, buổi sáng tiếp tục đốt, đáy lòng sốt ruột, gõ một chương, cũng không chút kiểm tra liền lên truyền, chất lượng khả năng không hết nhân ý, thứ lỗi ah. Đêm nay lại bắt đầu đốt đi, đánh xong châm trở về liền bắt đầu viết, đổi mới đã chậm, thật có lỗi.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-406"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-408"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);