(window.webpackJsonp=window.webpackJsonp||[]).push([[1642],{1997:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-400-truong-lao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-400-truong-lao"}},[n._v("#")]),n._v(" Chương 400: Trưởng lão?")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Lại thêm ra một bản?”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Nhọc nhằn khổ sở là Mộc Tuyết Tinh chỉ điểm, càng là truyền thụ kiếm pháp, sư đồ tạm biệt, không nghĩ tới đạt được thành tâm cảm kích, lần nữa đạt được một bản kim sắc thư tịch."),t("br"),t("br"),n._v("Cái này Thiên Đạo chi sách, cụ thể có bao nhiêu hiệu quả, mặc dù không biết, nhưng có thể nhẹ nhõm tăng lên tâm cảnh khắc độ, để cho mình ghi lại vô số tri thức, hai thứ này, thì tương đương với vô giới chi bảo."),t("br"),t("br"),n._v("“Lão sư, vô luận ngươi đi tới chỗ nào, chúng ta đều trở về tìm ngươi!”"),t("br"),t("br"),n._v("Trong lòng âm thầm thề, Mộc Tuyết Tinh mang theo mọi người quay người rời đi."),t("br"),t("br"),n._v("Gian phòng rất nhanh vắng vẻ xuống, chỉ có Lộ Xung cũng không đi theo, vẫn như cũ mặt không đổi sắc đứng tại chỗ."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Trương Huyền đang muốn nói chuyện, liền bị hắn đánh gãy."),t("br"),t("br"),n._v("“Lão sư, ta một người không có rễ không bình, ngươi đi đâu, ta liền đi đó!” Lộ Xung trong ánh mắt mang theo kiên định."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Thấy hắn không có chút nào do dự, Trương Huyền rõ ràng người học sinh này tính tình, biết thuyết phục lại nhiều cũng vô dụng, lúc này nhẹ gật đầu."),t("br"),t("br"),n._v("Xử lý xong rất nhiều học viên, vừa đi ra gian phòng, Lưu sư liền tiến lên đón, lộ ra vẻ lo lắng."),t("br"),t("br"),n._v("“Cứ như vậy để bọn hắn quỳ?”"),t("br"),t("br"),n._v("Trịnh Dương, Viên Đào, Vương Dĩnh đám người còn quỳ gối đại sảnh, không nhúc nhích, chẳng lẽ một mực như vậy quỳ đi xuống?"),t("br"),t("br"),n._v("“Bọn hắn tu vi tấn thăng quá nhanh, có chút phập phồng không yên, nhiều quỳ một chút thời gian, cũng có thể để tâm cảnh càng thêm trầm ổn, làm việc không như thế lỗ mãng!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Hạ độc đối với hắn mà nói, căn bản không gọi chuyện, đương nhiên cũng sẽ không tức giận, sở dĩ cố ý phơi nắng, để bọn hắn quỳ gối tại chỗ mặc kệ không hỏi, mục đích chỉ có một cái, ma luyện tâm cảnh, để bọn hắn tương lai, đi càng xa."),t("br"),t("br"),n._v("Thật giống như Lộ Xung, đi qua gia tộc biến cố, tâm như sắt thép, coi như mấy ngày ngắn ngủi tu vi đột nhiên tăng mạnh, cũng không có một điểm phù phiếm, ngược lại càng thêm lắng đọng."),t("br"),t("br"),n._v("Trịnh Dương, Viên Đào, Vương Dĩnh đám người, một đường đi theo bản thân, tiếp nhận tốt nhất giáo dục, ưu việt đã quen, là thời điểm cho bọn hắn học một khóa, để hắn rõ ràng tu luyện gian nan, đừng quá mức đắc ý."),t("br"),t("br"),n._v("“Không sai, là ta không để ý đến...”"),t("br"),t("br"),n._v("Lưu sư gật gật đầu, giờ này khắc này, đối vị sư huynh này chân chính bội phục không thôi."),t("br"),t("br"),n._v("Mới vừa nhìn thấy vị sư huynh này thời điểm, làm một chuyện gì còn rất non nớt, mà bây giờ, đã có “Sư” phong phạm, khắp nơi đều có “Sư” phong độ."),t("br"),t("br"),n._v("Vi sư giả, không chỉ muốn tăng lên học sinh tu vi, còn muốn củng cố tâm cảnh."),t("br"),t("br"),n._v("Nếu như đem tu luyện so sánh kiến trúc, tâm cảnh liền là kiến trúc bên trong sắt thép dàn khung."),t("br"),t("br"),n._v("Không có dàn khung, cho dù có lại nhiều gạch đá, cao siêu đến đâu kỹ xảo, về sau độ cao cũng có hạn, hơn nữa, rất khó tiếp nhận mưa gió."),t("br"),t("br"),n._v("Mượn đề tài để nói chuyện của mình, nghiêm khắc tư thái đối mọi người trừng phạt, trên thực tế lại tại vất vả ma luyện..."),t("br"),t("br"),n._v("Không trải qua một phen hàn triệt cốt, sao đến hoa mai xông vào mũi hương!"),t("br"),t("br"),n._v("“Chỉ mong bọn hắn có thể rõ ràng ngươi một phen khổ tâm...” Lưu sư cảm khái."),t("br"),t("br"),n._v("Có đôi khi học sinh không hiểu lão sư vì cái gì như thế nghiêm khắc, từ đó sinh lòng phản nghịch, đợi cho hối hận thời điểm, đã tới đã không kịp."),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, cùng Lưu sư hàn huyên một hồi, đi ra phủ đệ, đem chuyện còn lại xử lý một phen."),t("br"),t("br"),n._v("Đại Dược Vương, Liêu Thanh đám người đã rời đi vương thành, Quý gia đến đây chịu nhận lỗi, đưa không ít thứ, bị hắn một mạch thu vào trữ vật giới chỉ."),t("br"),t("br"),n._v("Quý gia chuyện, vốn là không tính là gì, cũng không có gì có thể truy cứu."),t("br"),t("br"),n._v("Sự tình xử lý xong, đã đến đêm khuya."),t("br"),t("br"),n._v("Ngày thứ hai đi tới Y Sư công hội, đem chưa xem xong sách toàn bộ quét một lần, lại đem vương quốc cái khác mấy cái công hội thư tịch, thu sạch ghi chép tiến thư viện, bận đến trời tối, lúc này mới trở lại phủ đệ."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, một bản kim sắc thư tịch bị điều đi ra."),t("br"),t("br"),n._v("Hai vị tứ tinh danh sư, chỉ mặt gọi tên muốn gặp Dương Huyền, một mực kéo lấy không gặp khẳng định không có khả năng, muốn không được đối phương xem thấu, chỉ có nắm giữ phong phú tri thức lượng, dù sao hiện tại có hai quyển kim sắc thư tịch, sử dụng một cái cũng không tính lãng phí."),t("br"),t("br"),n._v("“Học tập tri thức...”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Nương theo ý niệm sinh ra, chấn động kịch liệt oanh minh, kim sắc thư tịch đột nhiên phóng xuất ra hào quang chói sáng, trong thư viện sách ghi lại nội dung, trong nháy mắt truyền vào mà đến, tiến vào trong óc, Trương Huyền mắt tối sầm lại, hôn mê bất tỉnh."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Y Sư công hội."),t("br"),t("br"),n._v("Nhìn xem nằm trên mặt đất vẫn như cũ bất tỉnh nhân sự Mộc Hoành y sư, Thành Phong y sư mặt mũi tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("Vị này tiền nhiệm hội trưởng, chạy đến Lâm gia giúp người ta giải độc, kết quả... Độc không có hiểu, người biến thành như vậy."),t("br"),t("br"),n._v("Đường đường tam tinh y sư... Thật là mất mặt."),t("br"),t("br"),n._v("“Thành Phong y sư, nhưng có cứu chữa chi pháp?”"),t("br"),t("br"),n._v("Mộc Hoành y sư học sinh, chính là công hội nhị tinh y sư Tạ Thuận Đạt."),t("br"),t("br"),n._v("Giờ phút này, đang tràn đầy khẩn trương nhìn qua."),t("br"),t("br"),n._v("“Tất cả biện pháp đều dùng, hắn trúng độc... Thực sự quá lợi hại, ta bất lực!”"),t("br"),t("br"),n._v("Thành Phong y sư lắc đầu."),t("br"),t("br"),n._v("Cái này độc, cẩn thận nghiên cứu, có thể cùng chân khí dung hợp, để cho người ta căn bản không có cách nào."),t("br"),t("br"),n._v("“Trừ phi...”"),t("br"),t("br"),n._v("“Cái gì?” Tạ Thuận Đạt vẻ mặt xiết chặt."),t("br"),t("br"),n._v("“Trừ phi Liễu hội trưởng, cũng chính là Trương sư tự mình ra tay, hắn có thể giải quyết đa nghi như vậy khó tạp chứng, y thuật kinh người, độc này mặc dù lợi hại, có lẽ liền có giải quyết chi pháp!”"),t("br"),t("br"),n._v("Thành Phong y sư nói."),t("br"),t("br"),n._v("“Trương Huyền...”"),t("br"),t("br"),n._v("Nghe được cái tên này, Tạ Thuận Đạt không những không cao hứng, vẻ mặt ngược lại trầm xuống, phát ra thật dài gào thét: “Liền là hắn học sinh hạ độc, khẳng định là hắn cấu kết kia cái gì Độc điện điện chủ làm!”"),t("br"),t("br"),n._v("Cái kia Liêu điện chủ, gặp vị này Trương sư, theo gặp cha ruột đồng dạng, gọi là một cái cung kính."),t("br"),t("br"),n._v("Mộc Hoành y sư thân trúng kịch độc, Độc điện điện chủ lại xuất hiện, muốn nói cả hai không quan hệ, ai sẽ tin tưởng?"),t("br"),n._v(" “Làm càn, lời này không thể nói bậy!”"),t("br"),t("br"),n._v("Thành Phong y sư nghiêm sắc mặt: “Trương sư là danh sư, lại là y đạo đại tông sư, làm sao có thể cùng Độc điện có liên quan?”"),t("br"),t("br"),n._v("“Ta nói bậy? Hừ, nhiều người như vậy tận mắt nhìn đến, còn có thể là giả?”"),t("br"),t("br"),n._v("Tạ Thuận Đạt hừ lạnh: “Còn xông nghi vấn khó xử lý tường, hội trưởng, ta nhổ vào! Cùng Độc điện người lai vãng, có tư cách gì trở thành Y Sư công hội hội trưởng?”"),t("br"),t("br"),n._v("“Thậm chí, ta cảm thấy hắn căn bản cũng không hiểu y thuật, nghi vấn khó xử lý trên tường vấn đề, đều là sớm chuẩn bị tốt giải đáp phương pháp, nếu không, làm sao có thể ngắn như vậy thời gian, liền giải quyết nhiều vấn đề như vậy?”"),t("br"),t("br"),n._v("Nhìn thấy lão sư nằm trên mặt đất, không nhúc nhích, Tạ Thuận Đạt cũng nhịn không được nữa, lên tiếng gào thét."),t("br"),t("br"),n._v("Nghi vấn khó xử lý trên tường nghi vấn khó xử lý tạp chứng, mỗi một cái cũng khó khăn đến không thể lại khó, một cái hai mươi tuổi không đến gia hỏa, coi như theo trong bụng mẹ học y, lại có thể có bao nhiêu lợi hại?"),t("br"),t("br"),n._v("Rất hiển nhiên, là có người sớm đưa ra đáp án, chẳng qua máy móc mà thôi."),t("br"),t("br"),n._v("Nếu không, đã y thuật cao minh, vì cái gì không có ra tay cho người khác đã chữa bệnh? Chưa từng nghe qua giúp ai giải quyết chứng bệnh? (Trị liệu trấn quốc man thú chuyện, là cơ mật, rất nhiều người đều không biết rõ tình hình.)"),t("br"),t("br"),n._v("“Cái này...” Thành Phong y sư chần chờ."),t("br"),t("br"),n._v("Vị này Trương sư đến Y Sư công hội thời điểm, hắn cũng tại, đối rất nhiều chuyện đều không biết, trực tiếp xông vào nghi vấn khó xử lý tường... Rất rõ ràng là cái gì cũng đều không hiểu."),t("br"),t("br"),n._v("Loại người này thế mà có thể liên tục phá giải mười chín đạo nghi vấn khó xử lý tạp chứng, bản thân cũng làm người ta kỳ quái."),t("br"),t("br"),n._v("Cẩn thận nói đến, hắn cũng là có chút lo nghĩ."),t("br"),t("br"),n._v("“Loại người này, căn bản không xứng làm chúng ta Y Sư công hội hội trưởng, hi vọng Thành Phong y sư có thể chủ trì công đạo, tổ chức y sư đại hội, hủy bỏ hội trưởng tên tuổi, đừng để hắn giả danh lừa bịp...”"),t("br"),t("br"),n._v("Tạ Thuận Đạt tiếp tục khẽ nói."),t("br"),t("br"),n._v("Lời còn chưa nói hết, chỉ thấy một người trung niên vội vã chạy tới."),t("br"),t("br"),n._v("“Thành Phong, Tạ Thuận Đạt y sư, nghi vấn khó xử lý tường bên kia... Có động tĩnh, truyền đến tin tức!”"),t("br"),t("br"),n._v("Vừa vào cửa, trung niên nhân liền mặt mũi tràn đầy hưng phấn la lên."),t("br"),t("br"),n._v("“Truyền đến tin tức? Có phải hay không cuối cùng một đạo trả lời sai?”"),t("br"),t("br"),n._v("Tạ Thuận Đạt ánh mắt sáng lên, vội vàng nhìn qua."),t("br"),t("br"),n._v("Vài ngày trước, vị kia Trương sư liên tục trả lời hai mươi đạo nghi vấn khó xử lý tạp chứng, cuối cùng một đạo, nghi vấn khó xử lý tường đối diện, cũng không cấp cho chính xác trả lời chắc chắn."),t("br"),t("br"),n._v("Vốn cho rằng muốn chờ hơn mười ngày, không nghĩ tới hôm nay liền có tin tức truyền đến."),t("br"),t("br"),n._v("“Sai?” Chạy vào trung niên nhân, sửng sốt một chút, liền vội vàng lắc đầu: “Không nói chính xác sai lầm...”"),t("br"),t("br"),n._v("“Không nói?”"),t("br"),t("br"),n._v("Hai người đồng thời sững sờ."),t("br"),t("br"),n._v("Đã trả lời, tự nhiên là có chính xác cùng sai lầm, không có nói là có ý tứ gì?"),t("br"),t("br"),n._v("“Cái kia... Phía trên nói cái gì?”"),t("br"),t("br"),n._v("Tạ Thuận Đạt nhịn không được nói."),t("br"),t("br"),n._v("Đi vào trung niên nhân, chần chờ một chút: “Nói chính là... Mời Liễu Trình là vạn quốc liên minh Y Sư công hội trưởng lão, cụ thể đẳng cấp, đợi tu vi đạt tới cái khác khảo thí!”"),t("br"),t("br"),n._v("“Mời hắn... Là vạn quốc liên minh Y Sư công hội trưởng lão?”"),t("br"),t("br"),n._v("Thành Phong y sư, Tạ Thuận Đạt nhìn nhau, đồng thời thân thể nhoáng một cái, kém chút thổ huyết."),t("br"),t("br"),n._v("Mẹ nó, tình huống gì?"),t("br"),t("br"),n._v("Vạn quốc liên minh, cái kia là vương quốc đẳng cấp cao nhất, trong đó Y Sư công hội trưởng lão, kém cỏi nhất đều đạt đến tứ tinh cấp bậc."),t("br"),t("br"),n._v("Mời vị này Liễu Trình làm trưởng lão?"),t("br"),t("br"),n._v("Tạ Thuận Đạt chỉ cảm thấy trên mặt nóng bỏng, phảng phất bị người hung hăng quất một cái tát."),t("br"),t("br"),n._v("Mới vừa nói đối phương không xứng làm hội trưởng, kết quả... Người ta vạn quốc liên minh Y Sư công hội phát tới tin tức, muốn hắn làm đối phương trưởng lão..."),t("br"),t("br"),n._v("Ta đi!"),t("br"),t("br"),n._v("Ngươi đây là cố ý a..."),t("br"),t("br"),n._v("Mặt mũi tràn đầy đỏ bừng, Tạ Thuận Đạt sắp khóc."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Danh Sư đường một cái phòng, trước mắt chỉ có một cái tuyết trắng bức tường."),t("br"),t("br"),n._v("Đưa tin tường!"),t("br"),t("br"),n._v("Tô sư, Lăng sư, Khương đường chủ bọn người đứng tại bức tường phía trước."),t("br"),t("br"),n._v("“Hồi bẩm Tô sư, Lăng sư, Khương đường chủ, Phong Sư điện đã một lần nữa chữa trị tốt!”"),t("br"),t("br"),n._v("Chúc trưởng lão đi vào gian phòng, ôm quyền nói."),t("br"),t("br"),n._v("“Vậy là tốt rồi!”"),t("br"),t("br"),n._v("Khương đường chủ nhẹ nhàng thở ra."),t("br"),t("br"),n._v("“Tượng Khổng sư đột nhiên lắc lư, sau đó gây nên vô số tiền bối bài vị ngã xuống, loại sự tình này, tại cái khác địa phương, chưa hề phát sinh qua, Lăng sư, có thể tìm được vấn đề?”"),t("br"),t("br"),n._v("Tô sư nhíu mày nhìn qua."),t("br"),t("br"),n._v("Ngày đó bọn hắn đều tại hiện trường, tượng Khổng sư đột nhiên ngã sấp xuống, dẫn đến toàn bộ đại điện lắc lư, đến bây giờ đều không có hiểu rõ đến cùng chuyện gì xảy ra."),t("br"),t("br"),n._v("Trước đó, một mực hoài nghi, có phải hay không Trương sư hạ bái đưa tới tiền bối tán thành, cuối cùng vẫn lắc đầu."),t("br"),t("br"),n._v("Đây chính là tượng Khổng sư, ẩn chứa trong đó Khổng sư ý niệm, làm sao có thể tuỳ tiện tán thành người khác?"),t("br"),t("br"),n._v("Truyền thuyết từng có cửu tinh danh sư hạ bái hy vọng có thể trở thành Khổng sư học sinh, đều bị cự tuyệt, một cái nhị tinh danh sư... Nói đùa cái gì!"),t("br"),t("br"),n._v("“Không nghĩ thông suốt!”"),t("br"),t("br"),n._v("Lăng sư cũng cảm thấy hoàn toàn không nghĩ ra, lắc đầu, tiện tay cầm lấy bút lông, cười cười: “Bất quá, không cần phải gấp, ta dự định hiện tại liền hỏi thăm thoáng cái tổng bộ bên kia... Chúng ta không hiểu, hội trưởng bọn hắn hẳn nghe nói qua!”"),t("br"),t("br"),n._v("“Ừm, nhanh hỏi đi...”"),t("br"),t("br"),n._v("Tô sư gật gật đầu, đang muốn nói một chút như thế nào hỏi thăm, chỉ thấy trước mắt to lớn bức tường, đột nhiên quang mang chợt lóe, một nhóm chữ hiển lộ ra."),t("br"),t("br"),n._v("“Tiên sư bị từ chối, hóa chuông than khóc! Vạn quốc liên minh phạm vi bên trong, có người khảo hạch danh sư về sau, cự tuyệt tiền bối tán thành. Như có Phong Sư điện sụp đổ, nhanh bẩm!”"),t("br"),t("br"),n._v("“Phong Sư điện sụp đổ?” Tô sư, Lăng sư, Khương đường chủ đám người trợn mắt hốc mồm."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-408"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-410"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);