(window.webpackJsonp=window.webpackJsonp||[]).push([[1643],{2013:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-32-hong-thien-lau"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-32-hong-thien-lau"}},[n._v("#")]),n._v(" Chương 32: Hồng Thiên Lâu")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v(" “Tâm như chỉ thủy?” "),i("br"),i("br"),n._v(" Rất nhanh, Trương Huyền từ đặc thù ý cảnh bên trong tỉnh lại, ánh mắt sáng lên."),i("br"),i("br"),n._v(" Tâm tình tác dụng, hắn xem qua nhiều như vậy thư tịch, dĩ nhiên biết."),i("br"),i("br"),n._v(" “Ta là người xuyên việt, trải qua sinh tử, tâm thái muốn so với người bình thường tốt hơn rất nhiều, hơn nữa tu luyện Thiên Đạo Công Pháp, Chân Khí tinh khiết dường như thanh thủy! Tâm tình nhanh như vậy đạt đến tầng thứ hai, cũng rất bình thường!” "),i("br"),i("br"),n._v(" Tuy rằng cao hứng, nhưng không có quá bất cẩn bên ngoài."),i("br"),i("br"),n._v(" Nắm giữ Thiên Đạo Thư Viện này loại thần khí nghịch thiên, tại cùng người bình thường như thế, còn không bằng mua khối đậu hũ đâm chết được rồi."),i("br"),i("br"),n._v(" Một đường không nói chuyện, rất mau tới đến học viện trước cửa."),i("br"),i("br"),n._v(" “Hồng Thiên Lâu!” "),i("br"),i("br"),n._v(" Nhìn trước mắt ba chữ lớn, Trương Huyền gật gù."),i("br"),i("br"),n._v(" Trí nhớ của đời trước, có liên quan với ngôi tửu lâu này ghi chép, là Hồng Thiên học viện sang trọng nhất tửu lâu, trong này ăn cơm, mỗi một bữa đều có giá trị không nhỏ, tiền thân bởi vì tiền lương không cao, chưa từng tới bao giờ."),i("br"),i("br"),n._v(" Vốn tưởng rằng cái này Trầm Bích Như mời khách, tùy tiện một cái quán cơm nhỏ là được, không nghĩ tới đến rồi nơi này, thật là hào phóng."),i("br"),i("br"),n._v(" Không hổ là có tiếng tửu lâu, bên trong chứa tu xa hoa cực kỳ, từng cái từng cái lớn chừng quả đấm Dạ Minh Châu khảm nạm ở bên trong, soi sáng khác nào ban ngày."),i("br"),i("br"),n._v(" Tìm cái vị trí gần cửa sổ ngồi xuống."),i("br"),i("br"),n._v(" “Muốn ăn cái gì?” Trầm Bích Như nhìn về phía thiếu niên ở trước mắt."),i("br"),i("br"),n._v(" “Tùy tiện!” Trương Huyền xua tay: “Ngược lại ngươi mời khách, ngươi điểm là được rồi!” "),i("br"),i("br"),n._v(" Hắn chưa từng tới bao giờ chỗ này, cũng không biết ăn cái gì tốt, có người trả tiền, lười đi nghĩ."),i("br"),i("br"),n._v(" Thấy thiếu niên một bộ ăn hôi dáng dấp, Trầm Bích Như chẳng biết vì sao cảm thấy lên cơn giận dữ, rồi lại không phát tác được, không thể làm gì khác hơn là gật gù, tùy ý điểm mấy thứ."),i("br"),i("br"),n._v(" “Trương lão sư, ngươi thầy giáo sát hạch được không điểm, có phải là cố ý hay không?” "),i("br"),i("br"),n._v(" Điểm xong món ăn, Trầm Bích Như nhìn chằm chằm thiếu niên."),i("br"),i("br"),n._v(" “Cố ý?” Không biết đối phương tại sao lại hỏi cái này câu nói, Trương Huyền sững sờ: “Dĩ nhiên không phải!” "),i("br"),i("br"),n._v(" Thầy giáo khảo hạch thời điểm, tự mình còn không có xuyên việt tới, cũng không có Thiên Đạo Thư Viện, khẳng định không phải trang!"),i("br"),i("br"),n._v(" Nghe thiếu niên phủ nhận, Trầm Bích Như một bộ đã sớm biết hắn lại như vậy trả lời dáng dấp, trong lòng cười lạnh."),i("br"),i("br"),n._v(" Để ngươi không thừa nhận, xem ta như thế nào vạch trần ngươi!"),i("br"),i("br"),n._v(" “Ngược lại hiện tại nhàn rỗi không chuyện gì, ta vừa vặn còn có mấy vấn đề, có chút không hiểu, Trương lão sư, có thể hay không giúp ta giải thích nghi hoặc?” Con mắt hơi chuyển động, Trầm Bích Như hỏi."),i("br"),i("br"),n._v(" “Nói đi!” "),i("br"),i("br"),n._v(" Đối phương nếu mời ăn cơm, trả lời một hai vấn đề cũng không tính là gì."),i("br"),i("br"),n._v(" “Tốt, trong tu luyện gặp phải linh khí không cách nào tụ tập đến kinh mạch...” Trầm Bích Như suy tư một chút, đem một vấn đề hỏi lên."),i("br"),i("br"),n._v(" “Này cũng không hiểu?” Nghe được vấn đề này, so trước đó còn muốn đơn giản, Trương Huyền nghi ngờ nhìn đối phương một chút, thuận miệng giải thích: “Như vậy có thể giải quyết, ta chỗ này có chín loại phương pháp...” "),i("br"),i("br"),n._v(" Rất nhanh, giải thích xong."),i("br"),i("br"),n._v(" “Chín loại...” Nghe được thiếu niên thuận miệng nói ra chín loại phương pháp, Trầm Bích Như sắc mặt lại là biến đổi."),i("br"),i("br"),n._v(" Nàng coi như bác nghe rộng rãi biết, cũng chỉ có thể biết ba loại phương pháp, đối phương một hơi nói ra chín loại, hơn nữa còn có vài loại nghe đều chưa từng nghe nói, quả thực khó mà tin nổi."),i("br"),i("br"),n._v(" “Cái kia... Linh khí tiến vào trong cơ thể không cách nào khống chế tiến vào kinh mạch đây?” Cố nén trong lòng chấn động, hỏi lần nữa."),i("br"),i("br"),n._v(" “Cũng rất đơn giản, ta chỗ này cũng có tám loại phương pháp!” Trương Huyền giải thích một câu: “Loại thứ nhất...” "),i("br"),i("br"),n._v(" Trầm Bích Như thân thể mềm mại run rẩy, tuy rằng sớm có chuẩn bị tâm lý, cũng đã cảm thấy có chút điên rồi."),i("br"),i("br"),n._v(" Những phương pháp này, quả thực không thể tưởng tượng nổi, nếu như truyền đi, tuyệt đối lật đổ Tu Luyện Giới!"),i("br"),i("br"),n._v(" “Ngươi nếu biết nhiều như vậy phương pháp, vì sao thầy giáo khảo hạch thời điểm, một cái cũng không đáp lại được, được không điểm?” "),i("br"),i("br"),n._v(" "),i("br"),n._v(" Đè nén chấn động, Trầm Bích Như không nhịn được nói."),i("br"),i("br"),n._v(" Nàng vừa hỏi hai vấn đề, chính là lần trước thầy giáo sát hạch tới nguyên đề, đối phương nếu biết nhiều như vậy phương pháp, làm sao sẽ một cái cũng không lấp đi ra? Cuối cùng được không điểm?"),i("br"),n._v(" “Ế?” Trương Huyền này mới phản ứng được."),i("br"),i("br"),n._v(" Này mấy vấn đề hóa ra là thầy giáo sát hạch tới,"),i("br"),i("br"),n._v(" Bởi vì là tiền thân trải qua, hắn tuy rằng cảm thấy quen thuộc, vẫn chưa nhớ tới."),i("br"),i("br"),n._v(" “Cái này...” "),i("br"),i("br"),n._v(" Chính không biết trả lời như thế nào, chỉ thấy điểm món ăn đã lục tục tới."),i("br"),i("br"),n._v(" “Ăn cơm trước đi!” "),i("br"),i("br"),n._v(" Nhìn thấy mỹ vị, ngửi được mùi thơm, Trương Huyền thèm ăn nhỏ dãi, lại không thèm để ý những này việc vặt, mở miệng lớn tước."),i("br"),i("br"),n._v(" “Hừ...” "),i("br"),i("br"),n._v(" Thấy cái tên này nhìn thấy mỹ vị, so với nhìn thấy nàng vẫn vui vẻ, Trầm Bích Như tức giận miệng nhô lên."),i("br"),i("br"),n._v(" Vô số người muốn mời nàng ăn cơm, đều là do đầu, cớ, mục đích đúng là vì có thể cùng nàng chờ lâu một hồi, cái tên này ngược lại tốt, ngươi... Ngươi cũng thật là tới ăn cơm a..."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" “Còn ít, đừng nóng giận, ta đã xin học tâm tra hỏi, học viện ngày mai nhất định phải đem Lưu Dương đề cập tới đến xét duyệt, Trương Huyền bữa này trừng phạt không thể thiếu! Cho tới cái kia người điên, lần này còn trưởng lão đem hắn thú sủng cho ngươi mượn sử dụng, ngày mai gặp lại, nhất định có thể cố gắng dạy dỗ một trận, hiện tại nên cao hứng mới là! Đi, ta mời ngươi ăn một bữa no nê, cố gắng bổ sung một hồi hao tổn, sau đó sẽ tìm mấy cái nữ hài... Khà khà!” "),i("br"),i("br"),n._v(" Học viện trên đường phố, hai cái thanh niên nhanh chân cất bước."),i("br"),i("br"),n._v(" Trước tiên một cái, sắc mặt sưng đỏ, máu ứ đọng, viền mắt khác nào Hùng Miêu, vừa nhìn liền biết cùng nhân đánh nhau, bị đánh mặt, thứ hai thì lại cúi đầu khom lưng, thái độ có chút thấp kém, nói chuyện đúng là hắn."),i("br"),i("br"),n._v(" Hai người phía sau, theo một con to lớn sư tử, nghênh ngang đi tới, mang theo hung hãn khí tức, còn chưa tới đến trước mặt, liền cho người ta một loại khí tức kinh khủng, để nhân cảm thấy nồng đậm áp lực."),i("br"),i("br"),n._v(" Chính là ban ngày đi lớp học quấy rối, bị Diêu Hàn đánh cho một trận Thượng Bân, còn có một con kìm nén muốn tìm Trương Huyền phiền phức Tào Hùng."),i("br"),i("br"),n._v(" Về phần bọn hắn sau lưng sư tử, nhưng là Thượng Thần trưởng lão thú sủng, cấp sáu Man Thú Bạo Thiên Sư!"),i("br"),i("br"),n._v(" Thế giới này, có Luyện đan sư, Luyện khí sư rất nhiều nghề nghiệp, mỗi một loại nghề nghiệp đều vô cùng tôn quý, khiến cho nhân sùng bái."),i("br"),i("br"),n._v(" Thượng Thần trưởng lão chính là một vị Tuần thú sư."),i("br"),i("br"),n._v(" Này loại nghề nghiệp có thể đem Man Thú, linh thú chờ thuần phục, biến thành của mình, làm như tự mình chiến đấu trợ lực, hết sức lợi hại, ở rất nhiều nghề nghiệp bên trong cũng coi như được với tiếng tăm lừng lẫy."),i("br"),i("br"),n._v(" Đầu này Bạo Thiên Sư chính là Thượng Thần trưởng lão huấn luyện thú sủng."),i("br"),i("br"),n._v(" Thượng Bân bị người điên Diêu Hàn cuồng đánh một trận, biết thực lực mình không kịp, lúc này mới khẩn cầu gia gia đem thú sủng cho hắn mượn sử dụng."),i("br"),i("br"),n._v(" “Ừm!” Nghe xong đối phương quyến rũ, Thượng Bân gật gù, sắc mặt dữ tợn: “Cái kia Diêu Hàn nhất định phải cố gắng giáo huấn, Trương Huyền cũng giống vậy!” "),i("br"),i("br"),n._v(" “Đó là tự nhiên, có đầu này Bạo Thiên Sư, còn thiếu còn lo lắng cái gì!” Tào Hùng cười nói."),i("br"),i("br"),n._v(" Cấp sáu Man Thú tương đương với võ giả sáu tầng cường giả, thậm chí còn hơn."),i("br"),i("br"),n._v(" Coi như cái kia phát rồ Diêu Hàn quản gia thực lực không yếu, toàn thịnh thời gian muốn vượt qua Bạo Thiên Sư cũng không quá dễ dàng, chớ nói chi là hiện tại dáng vẻ ấy."),i("br"),i("br"),n._v(" Ban ngày nhìn hắn bao khỏa cùng xác ướp như thế, không hề so với còn thiếu hiện tại tình cảnh hảo tới chỗ nào."),i("br"),i("br"),n._v(" “Bạo Thiên Sư là ông nội ta thú sủng, ngươi cũng biết, dã tính mười phần, rất khó thuần phục, tuy rằng gia gia hiện tại giao nó cho ta, cũng chỉ ở có người công kích ta thời điểm, ra tay chống đối, muốn mệnh lệnh nó, khả năng còn có chút không làm được!” "),i("br"),i("br"),n._v(" Liếc mắt nhìn phía sau khí thế hùng hồn Bạo Thiên Sư, Thượng Bân có chút bất đắc dĩ nói."),i("br"),i("br"),n._v(" Man Thú thực lực mạnh mẽ, tính cách kiêu ngạo, trừ phi thực lực mạnh hơn nó, hoặc là Tuần thú sư, bằng không, rất khó để cho tâm phục khẩu phục nghe theo mệnh lệnh."),i("br"),i("br"),n._v(" “Cũng không nhất định, đến lúc đó chúng ta rượu ngon thức ăn ngon bắt chuyện, làm không cẩn thận liền nghe bảo!” "),i("br"),i("br"),n._v(" Tào Hùng cười nói."),i("br"),i("br"),n._v(" “Chỉ hy vọng như thế!” "),i("br"),i("br"),n._v(" Hai người vừa nói vừa đi, một lát sau một người cao lớn tửu lâu xuất hiện ở trước mắt."),i("br"),i("br"),n._v(" “Đến!” "),i("br"),i("br"),n._v(" Nhấc chân đi vào, phía sau trên tấm bảng ba chữ lớn, tỏa sáng chói lọi."),i("br"),i("br"),n._v(" Hồng Thiên Lâu!"),i("br"),i("br"),n._v("Convert by: Hoang Chau"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-40"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-42"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);