(window.webpackJsonp=window.webpackJsonp||[]).push([[1646],{2e3:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-403-sau-khong-luong-đuoc-duong-su-trung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-403-sau-khong-luong-đuoc-duong-su-trung"}},[n._v("#")]),n._v(" Chương 403: Sâu không lường được Dương sư (trung)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Trước mắt vị này có thể là vượt qua lục tinh danh sư, học sinh Trương Huyền lại liên tục đánh vỡ công hội vô số lần ghi chép, thật muốn dương danh thiên hạ, quả thực không cần bất luận cái gì chứng minh, chỉ cần nói ra là học sinh của hắn, liền đủ có thể khiến vô số thiên tài hâm mộ, ghen ghét."),t("br"),t("br"),n._v("“Dương sư chớ trách, là ta thất ngôn!”"),t("br"),t("br"),n._v("Tô sư vội vàng nói xin lỗi."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo: “Nếu như chỉ là chuyện này, các ngươi có thể đi về, Trương Huyền có nguyện ý hay không tham gia, toàn bằng cá nhân hắn quyết định, ta sẽ không hỏi nhiều! Ta chỉ phụ trách dẫn đạo, chỉ điểm, không phải bảo mẫu, chuyện gì đều muốn trông coi.”"),t("br"),t("br"),n._v("“Là chúng ta lỗ mãng...”"),t("br"),t("br"),n._v("Nghe được trong lời nói trục khách chi ý, Tô sư, Lăng sư cũng vội vàng đứng lên, chần chờ một chút, đang muốn cáo từ, cái trước cắn răng một cái: “Hôm nay có may mắn được thấy Dương sư, vừa vặn có chút trên việc tu luyện mê hoặc, muốn hỏi thăm, hy vọng có thể đạt được Dương sư chỉ điểm.”"),t("br"),t("br"),n._v("Đây chính là vượt qua lục tinh siêu cấp danh sư, cả một đời đều chưa hẳn có thể nhìn thấy một vị, thật vất vả đụng phải, không hỏi thăm một vài vấn đề, trở về khẳng định hối hận."),t("br"),t("br"),n._v("“Có gì mê hoặc?”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Trương Huyền trên mặt nhưng biểu hiện không thèm để ý chút nào."),t("br"),t("br"),n._v("Ngụy trang cấp bậc cao danh sư, sợ nhất chính là cái này, có điều, đối phương một khi mở lời, còn không thể không trả lời, bị người lên án việc nhỏ, khẳng định sẽ còn bị nghi ngờ."),t("br"),t("br"),n._v("Thấy đối phương cũng không hề có ý định cự tuyệt, Tô sư nhẹ nhàng thở ra, trong mắt vui mừng, vội vàng mở lời: “Ta tu luyện công pháp là tiểu Thuần Dương công, mỗi lần vận hành đến thứ bảy chuyển thời điểm, đều cảm thấy thân thể như là xé rách, không cách nào tiếp tục, không biết loại nào duyên cớ!”"),t("br"),t("br"),n._v("“Tiểu Thuần Dương công?” Trương Huyền hai mắt biến thành màu đen."),t("br"),t("br"),n._v("Mặc dù dung hợp Thiên Vũ vương quốc vô số tri thức, có thể môn công pháp này tên một lần đều không có nghe qua, chớ nói chi là cái gì cái gọi là lục chuyển, thất chuyển!"),t("br"),t("br"),n._v("“Tu luyện thoáng cái, để cho ta nhìn xem!”"),t("br"),t("br"),n._v("Trong lòng buồn bực, dù sao trải qua rất nhiều lần chuyện như vậy, Trương Huyền cũng không hoảng hốt, chậm rãi nâng chung trà lên, mí mắt vừa nhấc, cử chỉ trầm ổn ưu nhã."),t("br"),t("br"),n._v("“Vâng!” Tô sư gật đầu."),t("br"),t("br"),n._v("Danh sư chỉ điểm, muốn trước xem tu luyện, mới có thể đúng bệnh hốt thuốc."),t("br"),t("br"),n._v("Thân thể khẽ động, đi tới trong đại sảnh ở giữa, khoanh chân ngồi xuống, hai tay vờn quanh, nồng đậm linh khí, lập tức tụ đến."),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Không khí bị rút ra tốc độ quá nhanh, phát ra kịch liệt âm bạo."),t("br"),t("br"),n._v("“Lợi hại...”"),t("br"),t("br"),n._v("Nhìn thấy đối phương hấp thụ linh khí tốc độ, Trương Huyền lông mày nhảy một cái."),t("br"),t("br"),n._v("Không hổ là tứ tinh danh sư, quả nhiên không tầm thường."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Đối phương tu luyện, tương đương với có căn cứ, tinh thần khẽ động, một quyển sách xuất hiện tại thư viện."),t("br"),t("br"),n._v("Tiện tay lật ra, cúi đầu nhìn sang."),t("br"),t("br"),n._v("“Tô Phàm, tứ tinh sơ kỳ danh sư, Hóa Phàm cường giả, vạn quốc liên minh Danh Sư đường trưởng lão. Tu luyện công pháp: Tiểu Thuần Dương công, thiếu hụt:”"),t("br"),t("br"),n._v("Phía trên lít nha lít nhít ghi chép kỹ càng nội dung."),t("br"),t("br"),n._v("Đem trong thư viện tất cả tri thức dung nhập trong óc, hắn đã biết, võ giả cửu trọng phía trên, chính là ."),t("br"),t("br"),n._v("Võ giả cửu trọng, là đem lực lượng của phàm nhân tu luyện tới cực hạn, sau đó mới có thể hóa đi phàm trần, vào linh nhập Thánh."),t("br"),t("br"),n._v("Hóa Phàm cảnh, cùng võ giả đồng dạng, đồng dạng cửu trọng, cụ thể cấp bậc Thiên Vũ vương quốc loại tầng thứ này thư tịch, cũng không có kỹ càng giới thiệu."),t("br"),t("br"),n._v("Bất quá, có thể là Hóa Phàm cường giả, nói rõ trước mắt vị này, đã vượt qua Chí Tôn phạm trù, vô luận thân thể, tinh thần vẫn là sinh mệnh cấp độ, đều đạt đến cấp độ cực cao."),t("br"),t("br"),n._v("Thật giống như trong binh khí, quỷ binh cùng linh binh chênh lệch."),t("br"),t("br"),n._v("Không thể so sánh nổi."),t("br"),t("br"),n._v("Đem thư tịch bên trên ghi chép thiếu hụt nhìn xong, Trương Huyền trên mặt lần nữa lộ ra vẻ cổ quái."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tô sư vừa bắt đầu tu luyện, Lăng sư liền đem ánh mắt tập trung ở trước mắt vị này Dương sư trên người."),t("br"),t("br"),n._v("Lão hữu tật xấu này, hắn cũng biết, tra duyệt vô số tư liệu, thậm chí cũng thông qua đưa tin tường, hướng cấp bậc cao hơn công hội hỏi thăm qua, đáng tiếc, không có người có thể giải đáp."),t("br"),t("br"),n._v("Đã hỏi ra, vừa vặn cũng muốn nhìn xem, đối phương như thế nào quan sát, như thế nào chỉ điểm."),t("br"),t("br"),n._v("Một cái có thể chỉ điểm ra Trương Huyền loại siêu cấp thiên tài này siêu cường nhân vật, nghi tự siêu việt lục tinh siêu cấp danh sư, hiện trường quan sát, tại chỗ chỉ điểm, có thể đủ tốt hiếu học tập, đối với hắn tấn thăng, khẳng định cũng có rất lớn trợ giúp."),t("br"),t("br"),n._v("Ánh mắt tập trung, nghiêm túc học tập."),t("br"),t("br"),n._v("Bất quá, nhìn một hồi, mày nhíu lại thành u cục, trong lòng toát ra liên tiếp nghi vấn."),t("br"),t("br"),n._v("Danh sư chỉ điểm, làm cho đối phương tu luyện công pháp, võ kỹ, sau đó cẩn thận nghiên cứu, đây là rất bình thường, có thể... Trước mắt vị này, chỉ ngồi tại nguyên chỗ nhìn thoáng qua, liền nhắm mắt lại, không nhúc nhích, làm cái gì?"),t("br"),t("br"),n._v("Ngươi không phải có Minh Lý Chi Nhãn ư?"),t("br"),t("br"),n._v("Không nên dùng loại này năng lực đặc thù, quan sát trong vấn đề tu luyện, mấu chốt ư?"),t("br"),t("br"),n._v("Vì cái gì không dùng?"),t("br"),t("br"),n._v("Hơn nữa, coi như sử dụng Minh Lý Chi Nhãn, công pháp bên trên vấn đề, không giống với võ kỹ, cũng hẳn là nhìn nhiều vài lần, thậm chí hỏi thăm nội dung trong đó, mới có thể làm ra quyết đoán đi!"),t("br"),t("br"),n._v("Thờ ơ lãnh đạm, nhắm mắt theo ngủ thiếp đi đồng dạng... Đây là phương pháp gì?"),t("br"),t("br"),n._v("Lăng sư không hiểu ra sao."),t("br"),t("br"),n._v("Danh sư cho người ta chỉ điểm, cùng y sư đồng dạng, cũng cần cẩn thận quan sát, mới có thể tìm kiếm thiếu hụt, đúng bệnh hốt thuốc."),t("br"),t("br"),n._v("Làm tứ tinh danh sư, biết không dưới hơn vạn loại quan sát phương pháp, nhưng không có một cái, là tùy tiện quăng tới thoáng cái, liền nhắm mắt ngẩn người..."),t("br"),t("br"),n._v("“Sẽ không... Cũng không cách nào giải quyết chứ?”"),t("br"),t("br"),n._v("Một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("Nắm giữ Minh Lý Chi Nhãn, hắn ngược lại không hoài nghi đối phương là giả mạo cao thủ, chỉ là là bạn tốt lo lắng, sợ lần này lại là không công mà lui."),t("br"),t("br"),n._v("“Không cần luyện!”"),t("br"),t("br"),n._v("Ngay tại nghi hoặc, chỉ thấy cách đó không xa Dương sư lần nữa mở mắt, trên mặt không hề bận tâm, không vui không buồn."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Ngừng lại, Tô sư một mặt chờ mong nhìn qua."),t("br"),t("br"),n._v("“Dương sư... Có thể tìm được giải quyết phương pháp?” Lăng sư chấn động."),t("br"),t("br"),n._v("Nhìn thoáng qua, ngay sau đó nhắm mắt lại, cái gì đều không có làm... Chẳng lẽ cái này nhìn ra chỗ mấu chốt, tìm tới biện pháp giải quyết?"),t("br"),t("br"),n._v("“Phương pháp?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Hắn trên việc tu luyện không có bất cứ vấn đề gì, tiểu Thuần Dương công, cũng phù hợp thể chất!”"),t("br"),n._v(" “Cái kia...”"),t("br"),t("br"),n._v("Hai người đồng thời sửng sốt."),t("br"),t("br"),n._v("Tu luyện không có vấn đề, công pháp lại phù hợp, cái kia vì cái gì tu luyện tới thứ bảy chuyển, liền không có cách nào tiếp tục?"),t("br"),t("br"),n._v("“Ngươi muốn trùng kích thứ tám chuyển?”"),t("br"),t("br"),n._v("Không có trả lời đối phương, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Vâng! Thứ bảy chuyển đã vây khốn ta, vượt qua năm năm, từ đầu đến cuối không cách nào đột phá, tìm không thấy lối đi...”"),t("br"),t("br"),n._v("Tô sư liền vội vàng gật đầu."),t("br"),t("br"),n._v("Hắn tu luyện tiểu Thuần Dương công, là Linh cấp công pháp, từ vừa mới bắt đầu tu luyện, đến bây giờ đã không dưới năm mười năm, vô số năm nhuộm dần. Vốn cho rằng, có thể nhất cổ tác khí, đột phá đến cửu chuyển, đạt tới cảnh giới đại viên mãn, từ đó trùng kích ngũ tinh danh sư..."),t("br"),t("br"),n._v("Thế nào đều không có nghĩ đến, vây ở thất chuyển liền không có cái gì biện pháp."),t("br"),t("br"),n._v("Liên tục năm năm, đều không thể tiến bộ, hỏi thăm không biết bao nhiêu danh sư, thậm chí công hội mấy vị trưởng lão liên hợp cùng một chỗ quan sát qua, đều không có bất luận cái gì kết luận."),t("br"),t("br"),n._v("“Muốn trùng kích bát chuyển, liền nghe ta phân phó, đừng có bất kỳ kháng cự nào cùng bất luận cái gì do dự!”"),t("br"),t("br"),n._v("Chậm rãi đi tới, Trương Huyền hai tay chắp sau lưng."),t("br"),t("br"),n._v("“Tốt!” Tô sư cắn răng."),t("br"),t("br"),n._v("Đây chính là một vị dạy dỗ trời nhận danh sư cường giả, đã mở lời, đối với hắn mà nói, tuyệt đối là một lần cơ hội tốt nhất."),t("br"),t("br"),n._v("“Rất dễ, hiện tại bắt đầu tu luyện, luyện đến thứ bảy chuyển thời điểm, ta tự sẽ nghĩ biện pháp cho ngươi đột phá, có điều, trước đó, cần nhắm mắt lại, mặc kệ không hỏi, toàn tâm toàn ý vận chuyển công pháp!”"),t("br"),t("br"),n._v("Trương Huyền khoát khoát tay."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hít sâu một hơi, Tô sư khoanh chân ngồi xuống, liền muốn tu luyện."),t("br"),t("br"),n._v("“Không thể ngồi, mà muốn đứng lên, trung bình tấn!”"),t("br"),t("br"),n._v("Trương Huyền tiếp tục phân phó."),t("br"),t("br"),n._v("“Trung bình tấn tu luyện?”"),t("br"),t("br"),n._v("Sửng sốt một chút, có điều, không chần chờ bao lâu, Tô sư liền nhẹ gật đầu."),t("br"),t("br"),n._v("Tu vi đạt tới hắn loại này cấp bậc, hoàn toàn có thể tâm phân nhị dụng, đừng nói trung bình tấn tu luyện, coi như đang đánh quyền quá trình bên trong tu luyện, cũng hoàn toàn có thể làm được."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Biết yêu cầu, hít sâu một hơi, Tô sư nhắm mắt lại."),t("br"),t("br"),n._v("Cô cô cô cục cục!"),t("br"),t("br"),n._v("Trong không khí linh khí lần nữa bị hút vào trong cơ thể, tựa như tại sôi dầu bên trong gia nhập giọt nước, phát ra liên tiếp vang lên."),t("br"),t("br"),n._v("Tiểu Thuần Dương công, dương khí rất cao, lực lượng hùng hồn, cùng cấp bậc bên trong sử dụng loại công pháp này, mạnh mẽ vô song."),t("br"),t("br"),n._v("Chính vì vậy, Tô sư, Lăng sư, cùng là tứ tinh sơ kỳ, cái trước nhưng nắm giữ cao hơn địa vị."),t("br"),t("br"),n._v("Thấy đối phương nói ra bắt đầu liền bắt đầu, lập tức tiến vào cảnh giới vong ngã tu luyện, Trương Huyền hài lòng gật đầu, chắp tay sau lưng dọc theo đối phương dạo qua một vòng, nhìn về phía cách đó không xa Lăng sư."),t("br"),t("br"),n._v("“Có muốn hay không giúp hắn?”"),t("br"),t("br"),n._v("Lăng sư sững sờ: “Giúp thế nào?”"),t("br"),t("br"),n._v("Thế nào không phải ngươi còn muốn chỉ điểm ư? Thế nào còn có chuyện của ta?"),t("br"),t("br"),n._v("“Nghe ta phân phó là được!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Tốt!” Lăng sư đáp ứng."),t("br"),t("br"),n._v("Đối phương nhìn một chút, liền nhắm mắt lại, vốn là để hắn nghi hoặc, hiện tại lại để cho hắn hỗ trợ, lập tức đưa tới lòng hiếu kỳ."),t("br"),t("br"),n._v("Cũng muốn nhìn xem, đến cùng dùng phương pháp gì, làm cho đối phương đột phá thứ tám chuyển, lại phải hắn giúp thế nào bề bộn."),t("br"),t("br"),n._v("“Trước chờ lấy!”"),t("br"),t("br"),n._v("Thấy hắn đáp ứng, Trương Huyền không nói thêm lời, yên tĩnh đứng tại chỗ, không có chút nào biểu lộ."),t("br"),t("br"),n._v("Cô cô cô!"),t("br"),t("br"),n._v("Một bên Tô sư nương theo tu luyện, khí tức cả người dần dần đề cao, như rồng giống như hổ, khuấy động nhượng lại người hoảng sợ khí tức."),t("br"),t("br"),n._v("“Tứ chuyển, ngũ chuyển... Lục chuyển!”"),t("br"),t("br"),n._v("Cảm nhận được trên người đối phương khí tức, Lăng sư vẻ mặt càng ngày càng ngưng trọng."),t("br"),t("br"),n._v("Hai người hết sức quen thuộc, cũng biết tiểu Thuần Dương công mấu chốt, gặp qua hắn tu luyện, bởi vậy, đối với đối phương trong cơ thể vận chuyển mấy lần công pháp, biết đến hết sức rõ ràng."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Thứ sáu chuyển qua về sau, thứ bảy chuyển vừa bắt đầu, Tô sư xương cốt lập tức phát ra “Kẹt kẹt!” Thanh âm, giống như là thứ gì bị xé nứt, ghi nhớ lấy bắp thịt toàn thân sụp ra, máu tươi bắn ra mà ra."),t("br"),t("br"),n._v("Thời gian nháy mắt, nguyên bản nho nhã Tô sư, liền biến thành một cái như là Địa Ngục đi ra ác quỷ."),t("br"),t("br"),n._v("“Dương sư, Tô sư... Khả năng lập tức liền không kiên trì nổi...”"),t("br"),t("br"),n._v("Nhìn thấy lão hữu bộ dáng này, Lăng sư biến sắc, tràn đầy sốt ruột."),t("br"),t("br"),n._v("Cùng đối phương quen thuộc, biết cái này đã đạt đến có thể kiên trì cực hạn, lại tiếp tục, không cách nào không có đột phá là chuyện nhỏ, rất dễ dàng bản thân bị trọng thương."),t("br"),t("br"),n._v("Chính là bởi vì thân thể không chịu nổi, liên tục năm năm, lão hữu thực lực mảy may chưa tiến, lúc này mới bốn phía tìm hiểu, thậm chí nghe được Dương sư lợi hại, lập tức tới hỏi thăm."),t("br"),t("br"),n._v("“Ừm, có thể bắt đầu, đưa lỗ tai tới!”"),t("br"),t("br"),n._v("Gật gật đầu, Trương Huyền vẫy chào."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Lăng sư vội vàng đi tới."),t("br"),t("br"),n._v("“Ngươi bây giờ chỉ cần như vậy...”"),t("br"),t("br"),n._v("Thấy hắn đi tới trước mặt, Trương Huyền truyền âm, đem bí pháp của mình nói rõ chi tiết một lần."),t("br"),t("br"),n._v("“Cái này... Cái này...”"),t("br"),t("br"),n._v("Nghe xong bí pháp của hắn, Lăng sư khóe miệng giật một cái, con mắt trợn tròn, cả người cứng ngắc tại nguyên chỗ, sắp khóc."),t("br"),t("br"),n._v("Cái này mẹ nó phương pháp gì? Ngươi là muốn ta giết chết hắn chứ?"),t("br"),t("br"),n._v("“Cái này... Thật có thể làm?”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-411"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-413"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);