(window.webpackJsonp=window.webpackJsonp||[]).push([[1647],{2001:function(n,h,t){"use strict";t.r(h);var g=t(44),c=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-404-sau-khong-luong-đuoc-duong-su-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-404-sau-khong-luong-đuoc-duong-su-ha"}},[n._v("#")]),n._v(" Chương 404: Sâu không lường được Dương sư (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Có thể thử một chút!”"),t("br"),t("br"),n._v("Nói xong, Trương Huyền thần sắc lạnh nhạt ngồi xuống, lần nữa đổ đầy một chén nước trà, an tĩnh thưởng trà."),t("br"),t("br"),n._v("“Thử?” Lăng sư thổ huyết."),t("br"),t("br"),n._v("Phương pháp ngươi nói, rõ ràng là muốn giết người, vốn cho rằng có rất lớn nắm chắc, không nghĩ tới chỉ là... “Thử”!"),t("br"),t("br"),n._v("Vạn nhất không thành công, Tô sư nhưng là treo!"),t("br"),t("br"),n._v("Ta cũng muốn trên lưng tàn sát đồng liêu tội danh!"),t("br"),t("br"),n._v("“Dương sư... Nhưng còn có những biện pháp khác?” Chần chờ hồi lâu, Lăng sư nhịn không được nhìn qua."),t("br"),t("br"),n._v("Trương Huyền không thèm để ý, yên tĩnh ngồi tại nguyên chỗ, đầu cũng không chuyển."),t("br"),t("br"),n._v("Thấy bộ dáng này, Lăng sư biết, cho dù có khẳng định cũng không muốn nói, lần nữa nhìn về phía cách đó không xa Tô sư, mặt mũi tràn đầy xoắn xuýt."),t("br"),t("br"),n._v("Chuyện này là sao, vấn đề là Tô sư, muốn đột phá thứ bảy chuyển cũng là hắn, làm khó lại là bản thân..."),t("br"),t("br"),n._v("“Thế nào? Nhanh lên, ta không chịu nổi...”"),t("br"),t("br"),n._v("Không biết như thế nào cho phải, chỉ thấy mặt mũi tràn đầy dữ tợn Tô sư hô lên."),t("br"),t("br"),n._v("Thứ bảy chuyển, đã giữ vững được một hồi, lại không nghĩ biện pháp giải quyết, khẳng định lại phải trọng thương, cuối cùng đều là thất bại."),t("br"),t("br"),n._v("Dĩ vãng tu luyện, đều là như thế, tu luyện một lần, tu dưỡng chí ít một tháng."),t("br"),t("br"),n._v("Tô sư cũng rất buồn bực, gia hỏa này chuyện ra sao?"),t("br"),t("br"),n._v("Nhiều năm lão hữu, Dương sư đã nói ra giải quyết bí pháp, liền động tác nhanh lên, trễ như vậy nghi làm gì? Chẳng lẽ muốn trơ mắt nhìn ta lần nữa trùng kích thất bại?"),t("br"),t("br"),n._v("Quay về trên giường tiếp tục nằm?"),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Đang muốn tiếp tục thúc giục, chỉ thấy Lăng sư cắn răng một cái đi tới, một mặt đồng tình: “Tô sư, thật xin lỗi!”"),t("br"),t("br"),n._v("“Thật xin lỗi?”"),t("br"),t("br"),n._v("Tô sư sững sờ, dùng bí pháp giúp ta đột phá thất chuyển, đây là giúp ta, “Thật xin lỗi” có ý tứ gì?"),t("br"),t("br"),n._v("Ngay tại nghi hoặc, còn không có nghĩ rõ ràng, chỉ thấy một cái bàn chân đối với hắn dưới đũng quần, bỗng nhiên đạp tới."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Nhìn thấy cước này tốc độ, Tô sư lập tức dọa đến hồn phi phách tán, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Thật muốn bị đá trúng, không cần nghĩ, tuyệt đối bị tàn phế!"),t("br"),t("br"),n._v("Mặc dù người đã già, không thể nào dùng, nhưng cũng là trên người vật, ngươi làm cái gì vậy..."),t("br"),t("br"),n._v("“Không nên động!”"),t("br"),t("br"),n._v("Thân thể vặn chuyển, đang muốn trốn tránh, bên tai liền nghe đến Dương sư thanh âm nghiêm nghị, hơi chần chờ một chút, Lăng sư bàn chân đã đi tới trước mặt, lại trốn tránh không ra."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Tô sư vẻ mặt thoáng cái vặn vẹo đến run rẩy, mắt tối sầm lại, một cỗ đau đớn kịch liệt theo dưới hông truyền đến, để hắn kém chút không có tại chỗ ngất đi."),t("br"),t("br"),n._v("Lăng Vũ Hằng, lão tử cùng ngươi còn chưa xong..."),t("br"),t("br"),n._v("Trong lòng một tiếng gào thét, đang muốn đem gia hỏa này xé nát, chỉ thấy mắt tối sầm lại, một cái bàn tay khổng lồ, đối với đầu quay đi qua."),t("br"),t("br"),n._v("Một cước đạp trúng dưới hông, hẳn là xem bản thân không chết, lại một bàn tay rút ra."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Tô sư khóc."),t("br"),t("br"),n._v("Dương sư không phải cho ngươi thi triển bí pháp sao? Đánh ta làm gì?"),t("br"),t("br"),n._v("Một cước đạp tới, có phải hay không cảm thấy phế đi còn chưa đủ nghiền, muốn liền mạng đều lấy đi?"),t("br"),t("br"),n._v("Buồn bực sắp phát điên, muốn trốn tránh, lại phát hiện căn bản tránh không xong."),t("br"),t("br"),n._v("Hai người thực lực tương đương, coi như có thể thắng được đối phương, cũng ít nhất phải tại ngàn chiêu về sau, bây giờ bị một cước đạp sắp phế bỏ, đau một điểm lực lượng đều không có, như thế nào ngăn cản được."),t("br"),t("br"),n._v("Huống chi đối phương vừa ra tay, chân khí liền bao phủ bốn phía, tựa như lồng giam, chưa kịp tránh thoát, bàn tay của đối phương đã hung hăng rơi vào trên đầu."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Mắt tối sầm lại, một ngụm máu tươi cuồng phún mà ra."),t("br"),t("br"),n._v("Ngay sau đó, Tô sư liền cảm thấy một cỗ hung mãnh lực lượng bỗng nhiên theo đan điền sinh ra, rốt cuộc ngăn chặn không ở, rít lên một tiếng."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Khổng lồ khí lãng quét sạch ra, đem mặt đất nổ tràn đầy vết rách, phụ cận bàn ghế, toàn bộ biến thành bột mịn."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Đã vận chuyển dừng lại chân khí, phảng phất ăn thuốc đại bổ, trong chớp mắt hình thành dòng lũ, xông phá vô số ràng buộc, lần nữa vận chuyển một tuần."),t("br"),t("br"),n._v("Thứ tám chuyển!"),t("br"),t("br"),n._v("“Thành, thành công?”"),t("br"),t("br"),n._v("Cảm nhận được chân khí trong cơ thể cuồng bạo, không ngừng không nghỉ, Tô sư theo trong đau đớn tỉnh táo lại, đầu tiên là sững sờ, lập tức cuồng hỉ."),t("br"),t("br"),n._v("Nằm mơ đều không có nghĩ đến, vây lại hắn ròng rã năm năm tu luyện bình chướng, cứ như vậy đột phá..."),t("br"),t("br"),n._v("“Quả nhiên có thể thực hiện?”"),t("br"),t("br"),n._v("Vốn cho rằng đạp phế lão hữu, lại một bàn tay đem hắn sắp quất chết, hữu nghị thuyền nhỏ khẳng định đã lật, thế nào đều không có nghĩ đến, khí tức đối phương đột nhiên đại thịnh, đột phá thứ bảy chuyển ràng buộc."),t("br"),t("br"),n._v("Vội vàng quay đầu, nhìn về phía cách đó không xa Dương sư, chỉ thấy hắn tựa hồ đã sớm ngờ tới kết cục này, vẫn như cũ an tĩnh uống trà, thân thể ngay cả nhúc nhích cũng không qua."),t("br"),t("br"),n._v("“Hội trưởng, thậm chí ngũ tinh danh sư đều không giải quyết được vấn đề, một cước, một chưởng liền giải quyết...”"),t("br"),t("br"),n._v("Miệng há mở, Lăng sư thân thể run rẩy."),t("br"),t("br"),n._v("Giáo dục ra Trương Huyền lợi hại như vậy học sinh, sớm biết vị này Dương sư không đơn giản, có thể... Không nghĩ tới lợi hại như vậy."),t("br"),t("br"),n._v("Liếc qua, liền nhìn ra vấn đề, tìm tới phương pháp giải quyết, liền Minh Lý Chi Nhãn đều vô dụng, phần này thực lực, đã vượt ra khỏi tưởng tượng ra."),t("br"),t("br"),n._v("“Chỉ là... Phương pháp giải quyết, quá trò đùa đi...”"),t("br"),n._v(" Sau khi hết khiếp sợ, lại là không còn gì để nói."),t("br"),t("br"),n._v("Người khác chỉ điểm, đều là đổi công pháp, ghi rõ tu luyện lộ tuyến, phục dụng đan dược các loại, gia hỏa này ngược lại tốt, một cước một chưởng liền giải quyết vấn đề..."),t("br"),t("br"),n._v("Đơn giản là đơn giản, có thể..."),t("br"),t("br"),n._v("Vừa nghĩ tới vừa rồi một cước kia, khóe miệng liền không nhịn được run rẩy."),t("br"),t("br"),n._v("Lần này, coi như không phế, cũng ít nhất phải tu dưỡng năm đem hai năm, lại không có thể sử dụng."),t("br"),t("br"),n._v("“Đa tạ Dương sư giúp ta đột phá...”"),t("br"),t("br"),n._v("Lúc này, Tô sư rốt cục tu luyện hoàn tất, khí tức rõ ràng tăng lên một mảng lớn, hưng phấn ôm quyền."),t("br"),t("br"),n._v("Thành công đột phá thứ tám chuyển, thực lực cũng tương ứng tấn thăng, mặc dù vừa mới đột phá, còn không quá vững chắc, nhưng bước ra vây khốn hắn ròng rã năm năm cửa ải."),t("br"),t("br"),n._v("Chỉ bất quá... Đột phá là đột phá, đại giới cũng có chút quá cao!"),t("br"),t("br"),n._v("Đau đớn sắp đi không được đường, trên đầu mồ hôi lạnh ứa ra, hai chân như nhũn ra, nếu không phải chú ý đến hình ảnh, chỉ sợ sớm đã khom lưng đi xuống, không thể kiên trì được nữa."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Đặt chén trà xuống, Trương Huyền đứng dậy, lạnh nhạt nhìn qua: “Nhưng nhìn hiểu nguyên nhân?”"),t("br"),t("br"),n._v("“Còn xin Dương sư chỉ rõ!” Hai người đồng thời nhìn qua."),t("br"),t("br"),n._v("Đá một cước, đánh một chưởng liền có thể đột phá, đây là bọn hắn trước đó nằm mộng cũng nghĩ không ra."),t("br"),t("br"),n._v("Thậm chí tận mắt thấy, vẫn như cũ cảm thấy tràn đầy không thể tưởng tượng nổi, không hiểu rõ đến cùng chuyện gì xảy ra."),t("br"),t("br"),n._v("Gặp bọn họ còn không có lý giải, Trương Huyền lắc đầu, nhìn về phía Tô sư."),t("br"),t("br"),n._v("“Ngươi tu luyện chính là tiểu Thuần Dương công, thể chất cũng là dương thuộc tính, cùng công pháp xứng đôi, không có bất cứ vấn đề gì! Dựa theo đạo lý, thành công tu luyện tới thứ tám chuyển, đều không thành vấn đề, chỉ bất quá...”"),t("br"),t("br"),n._v("Ánh mắt thâm thúy, Trương Huyền lộ ra một tia tiếc hận: “Thời gian thấm thoắt, tuổi tác trôi qua, nương theo trong cơ thể sinh cơ suy yếu, thân thể dương thuộc tính đã theo không kịp! Nếu như ta không nhìn lầm, ngươi bây giờ, hẳn là đối với nữ nhân không có hứng thú quá lớn đi?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Tô sư xấu hổ."),t("br"),t("br"),n._v("Nương theo thân thể cơ năng suy yếu, niên kỷ càng lúc càng lớn, quả thực đối với nữ nhân không có trước kia kích tình."),t("br"),t("br"),n._v("Chỉ bất quá đây là bí mật của hắn, cũng là giữ mình trong sạch, bị đối phương một ngụm nói ra, lập tức xấu hổ máu me đầy mặt đỏ."),t("br"),t("br"),n._v("Nam nhân, sợ nhất liền là bị người nói không được."),t("br"),t("br"),n._v("“Liền nữ nhân đều không hứng thú, nói rõ dương khí đã suy yếu. Dương khí không sinh, tu luyện Thuần Dương công pháp, lợi hại hơn nữa, lại như thế nào có thể tự do như ý?”"),t("br"),t("br"),n._v("Không để ý tới đối phương xấu hổ, Trương Huyền tiếp tục nói: “Dưới hông, không riêng gì nam nhân tiêu chí, càng là dương khí khởi nguyên chi địa, muốn để tiểu Thuần Dương công vận chuyển gia tốc, chỉ có nơi đây. Lăng sư bạo lực ra tay, dùng lực lượng cuồng bạo trùng kích, để trong đó chất đống dương khí, trong nháy mắt tuôn ra, lực lượng đột nhiên chợt tăng không chỉ một lần.”"),t("br"),t("br"),n._v("“Đương nhiên, lực lượng bạo tăng, cũng không thể cam đoan nhất định đột phá thất chuyển, vì bảo hiểm, ta để hắn tiếp tục công kích đầu của ngươi.”"),t("br"),t("br"),n._v("“Đầu, chư dương đứng đầu vậy! Khởi nguyên chỗ bị đánh tan, dương khí tứ tán, sẽ tự nhiên mà nhưng hội tụ đến đỉnh đầu, lúc này Lăng sư lực lượng sẽ cùng hắn tiếp xúc, lập tức liền phá vỡ ràng buộc, cho ngươi thành công đột phá!”"),t("br"),t("br"),n._v("Nhẹ nhàng nở nụ cười, Trương Huyền hai tay chắp sau lưng, thân ảnh bị kéo dài, thoạt nhìn không gì sánh được cao lớn."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hai người đồng thời chấn động."),t("br"),t("br"),n._v("Mặc dù đối phương nói hời hợt, nhưng cùng là danh sư, biết trong đó độ khó."),t("br"),t("br"),n._v("Dưới hông, mặc dù là dương khí khởi nguyên chi địa, nhưng cũng là vận mệnh, có chút sai lầm, liền có thể một mệnh ô hô. Đầu lâu càng là chỗ trọng yếu nhất, dùng cái này hai nơi tiến hành đột phá..."),t("br"),t("br"),n._v("Chỉ cần suy đoán ra xuất hiện một điểm sai lầm, hoặc là ít nhìn một điểm, Tô sư cũng không phải là đột phá, mà là đã chết!"),t("br"),t("br"),n._v("Có thể đem triệu chứng nắm chắc rõ ràng như thế, cái này... Còn là người sao?"),t("br"),t("br"),n._v("“Sâu không lường được! Dương sư tuyệt đối sâu không lường được...”"),t("br"),t("br"),n._v("Nhìn nhau, trong lòng hai người đồng thời toát ra một cái ý nghĩ."),t("br"),t("br"),n._v("Có thể nhìn lên một cái, liền biết hắn vấn đề chỗ tồn tại, không có chút nào sai lầm, thậm chí liền Lăng sư công kích lực lượng đều cân nhắc đi ra, phần này năng lực, liền xem như bọn hắn, đều khó mà tưởng tượng ra!"),t("br"),t("br"),n._v("Trước đó còn tưởng rằng, cái này Dương sư nhiều nhất cùng bọn hắn cùng cấp, trình độ không sai biệt lắm, thế nào đều không có nghĩ đến, chênh lệch bao lớn, tựa như thiên địa, không thể so sánh nổi!"),t("br"),t("br"),n._v("Không hổ là lĩnh ngộ Minh Lý Chi Nhãn siêu cường danh sư, đáng sợ!"),t("br"),t("br"),n._v("“Nhanh điều chỉnh một chút đi, lần này, tổn thương cũng tương đối trọng.”"),t("br"),t("br"),n._v("Thấy hai người biểu lộ, Trương Huyền biết đối phương bị triệt để lừa dối ở, nhịn không được nhẹ nhàng thở ra, đồng thời đồng tình nhìn qua."),t("br"),t("br"),n._v("Nếu như hắn chính mình ra tay, lợi dụng Thiên Đạo chân khí kích thích huyệt vị, đồng dạng có thể để cho dương khí trác thịnh, đạt tới đột phá thất chuyển hiệu quả."),t("br"),t("br"),n._v("Nhưng này dạng làm, rất dễ dàng bị đối phương nhìn ra tu vi cùng Thiên Đạo chân khí đặc thù, được không bù mất, suy đi nghĩ lại, cũng chỉ có cái này một cái biện pháp."),t("br"),t("br"),n._v("Bất quá, cũng thực sự quá độc ác..."),t("br"),t("br"),n._v("Đừng nói đối phương, hắn nhìn ở trong mắt, đều cảm thấy dưới hông phát lạnh, một hồi nhức cả trứng."),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Nghe nói như thế, Tô sư lúc này mới thở phào nhẹ nhõm, lại không để ý tới cái khác, lúc này ngồi xuống tu luyện, chống cự bị hai lần bạo kích mang tới đau đớn."),t("br"),t("br"),n._v("“Dương sư, lần này là đột phá thất chuyển, có thể... Nếu như Tô sư, muốn lần nữa đột phá, trùng kích đệ cửu chuyển làm sao bây giờ?”"),t("br"),t("br"),n._v("Thấy lão hữu tu luyện, Lăng sư nhịn không được nhìn qua."),t("br"),t("br"),n._v("Tiểu Thuần Dương công muốn đạt tới cửu chuyển mới có thể đại viên mãn, mới có thể trùng kích cao hơn, đột phá bát chuyển đều phiền toái như vậy, mạnh nhất cửu chuyển hẳn là thế nào trùng kích?"),t("br"),t("br"),n._v("“Đơn giản, đồng dạng phương pháp này, đá thoáng cái không dùng được, vậy liền đá hai lần, hai lần không dùng được, đá mười lần... Nhiều đá một hồi, khẳng định sẽ khiến lực lượng bạo động!” Trương Huyền khoan thai mà nói."),t("br"),t("br"),n._v("“Đá mười lần? Nhiều đá một hồi...”"),t("br"),t("br"),n._v("Lăng sư lông tơ nổ lên."),t("br"),t("br"),n._v("Một bên tu luyện Tô sư thân thể nhoáng một cái, kém chút tẩu hỏa nhập ma."),t("br"),t("br"),n._v("Mẹ nó..."),t("br"),t("br"),n._v("Chúng ta đây rốt cuộc gặp dạng gì danh sư, lại cho một cái dạng gì phương pháp?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bên này hai vị danh sư sụp đổ, cách đó không xa Lâm phủ, một cái to lớn man thú thẳng tắp bay tới, “Hô!” Rơi vào giữa sân, hai nam một nữ, đi ra."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-412"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-414"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);