(window.webpackJsonp=window.webpackJsonp||[]).push([[1648],{2002:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-405-loi-hai-ton-cuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-405-loi-hai-ton-cuong"}},[n._v("#")]),n._v(" Chương 405: Lợi hại Tôn Cường")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Đây là Lâm phủ?”"),t("br"),t("br"),n._v("Dĩ vãng, Lâm phủ làm Thiên Vũ vương quốc đệ nhất gia tộc, cho dù là đêm khuya, đều dòng người cuồn cuộn, tân khách như thoi đưa."),t("br"),t("br"),n._v("Mà bây giờ, mênh mông viện lạc một mảnh hỗn độn, một cái hộ vệ, người hầu cũng không có, trống rỗng, cho người ta một loại vứt bỏ trang viên cảm giác."),t("br"),t("br"),n._v("Cùng trong trí nhớ hoàn toàn khác biệt."),t("br"),t("br"),n._v("“Liễu Trình, ta muốn giết ngươi...”"),t("br"),t("br"),n._v("Đôi mắt thanh tú trợn tròn, mái tóc đen nhánh không gió mà bay, Lâm Lung thái tử phi gầm lên giận dữ, trong sân quanh quẩn."),t("br"),t("br"),n._v("Nhận được tin tức, cái kia gọi Liễu Trình người, ngay tại đối với Lâm gia động thủ, xuất hiện loại tình huống này, không cần nghĩ, khẳng định là gia hỏa này đắc thủ!"),t("br"),t("br"),n._v("“Nhanh đi tìm xem, nhìn ta cha cùng đệ đệ ở nơi nào, nếu như bọn hắn xảy ra chuyện, ta muốn chỉnh cái Thiên Vũ vương quốc vì đó chôn cùng!”"),t("br"),t("br"),n._v("Răng ngà cắn chặt, Lâm Lung hận ý chảy xuôi, trong mắt sát cơ đại thịnh."),t("br"),t("br"),n._v("Một cấp tiểu vương quốc, rác rưởi đồng dạng tồn tại, trước đó căn bản không thèm để ý, lại dám đối với Lâm gia ra tay, quả thực tự tìm cái chết!"),t("br"),t("br"),n._v("Cái kia Liễu Trình, khiêu khích Lâm gia, phải chết!"),t("br"),t("br"),n._v("Đường đường Thiên Vũ vương thất, biết rất rõ ràng, thế mà không ngăn cản, cũng nhất định phải hủy diệt!"),t("br"),t("br"),n._v("“Vâng!” Sau lưng hai người vọt ra ngoài."),t("br"),t("br"),n._v("Đông cung thống lĩnh Lương Thanh Mệnh, thực lực Chí Tôn hậu kỳ, cùng nhau tới tuần thú sư Chu Tiến, cũng đạt tới Chí Tôn sơ kỳ!"),t("br"),t("br"),n._v("Hai vị Chí Tôn cảnh cường giả, tốc độ cực nhanh, thời gian không dài, đồng thời bay lượn mà tới."),t("br"),t("br"),n._v("“Nương nương, Lâm gia chủ cùng Lâm gia thiếu gia ở bên kia!”"),t("br"),t("br"),n._v("Nghe được cha cùng đệ đệ không chết, Lâm Lung nhẹ nhàng thở ra, vội vàng đi theo, rất mau tới đến một cái đại điện, nhìn thoáng qua, lập tức khóe mắt."),t("br"),t("br"),n._v("Hai người đích thật là không chết, nhưng cũng cùng chết không sai biệt lắm."),t("br"),t("br"),n._v("Tu vi toàn bộ bị phế, trên người cơ hồ không có một chỗ hoàn chỉnh, bị trọng thương, coi như cứu sống, cũng kém không nhiều triệt để phế đi."),t("br"),t("br"),n._v("Cái trước còn có ý biết, người sau đang hôn mê bất tỉnh, nếu không phải còn có hô hấp, thật sự cho rằng đã sớm chết."),t("br"),t("br"),n._v("“Lung nhi, muốn báo thù cho chúng ta!”"),t("br"),t("br"),n._v("Gặp nàng đi tới, còn có ý biết Lâm Nhược Thiên giống như là thấy được cứu tinh, vội vàng lao đến."),t("br"),t("br"),n._v("Lúc trước Lộ Xung là không giết hắn, có thể để Liêu điện chủ ở trên người hạ chí ít bảy, tám loại kịch độc, mỗi ngày đều tiếp nhận đau khổ kịch liệt, sống không bằng chết."),t("br"),t("br"),n._v("Hơn nữa, Lâm gia bị phá, trước kia cừu nhân nhao nhao mà tới, tra tấn nhục nhã, hận không thể tại chỗ tự sát!"),t("br"),t("br"),n._v("Nếu không phải vì chờ lấy nữ nhi, báo thù cho hắn, khẳng định đã sớm không kiên trì nổi."),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Vội vàng tiến lên, cho đối phương cho ăn một hạt chữa thương bảo mệnh đan dược, Lâm Lung lúc này mới nhìn qua."),t("br"),t("br"),n._v("“Là... Liễu Trình! Không, là Danh Sư đường nhị tinh danh sư Trương Huyền! Học sinh của hắn, chính là hai năm trước Cù gia chạy trốn tên súc sinh kia...”"),t("br"),t("br"),n._v("Ăn vào đan dược, Lâm Nhược Thiên khôi phục không ít, răng cắn chặt, đem sự tình nói một lần."),t("br"),t("br"),n._v("Đương nhiên, nói đều là như thế nào bị đối phương khi dễ, về phần vì sao mà lên, Cù gia cả nhà bị diệt, không nói tới một chữ."),t("br"),t("br"),n._v("“Trương Huyền?”"),t("br"),t("br"),n._v("Răng cắn “Khanh khách!” Rung động, Lâm Lung tròng mắt hơi híp: “Lương thống lĩnh, hiện tại liền đi Danh Sư đường, đem cái này Trương Huyền cho ta bắt tới, ta muốn lấy máu tươi của hắn, nói cho tất cả mọi người, đắc tội Lâm gia chúng ta, một con đường chết!”"),t("br"),t("br"),n._v("“Nương nương nghĩ lại, vị này Trương Huyền là nhị tinh danh sư, lại là thiên tài, ta sợ lại nhận Danh Sư đường che chở...”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Lương Thanh Mệnh cũng không trực tiếp đáp ứng."),t("br"),t("br"),n._v("Nhị tinh sơ kỳ danh sư đối với Hiên Viên vương quốc, không tính là cái gì."),t("br"),t("br"),n._v("Nhưng cũng là Danh Sư đường một thành viên, ngang nhiên chém giết, làm không cẩn thận liền sẽ gây nên bắn ngược, như thế coi như Hiên Viên vương quốc, cũng khó có thể chống chọi."),t("br"),t("br"),n._v("“Hủy ta gia tộc, hại ta cha mẹ huynh đệ, thâm cừu đại hận, coi như Danh Sư đường tìm đến lại như thế nào? Chẳng lẽ còn không thể phân rõ phải trái? Lại nói, Danh Sư đường Lạc đường chủ, là thái tử bạn tri kỉ, chỉ cần trần thuật tình huống, hắn hẳn là sẽ không nhiều lời!”"),t("br"),t("br"),n._v("Lâm Lung hai mắt đỏ thẫm."),t("br"),t("br"),n._v("Hiên Viên vương quốc Danh Sư đường đường chủ Lạc ngàn đỏ, là thái tử bạn tri kỉ, một cái vương quốc nho nhỏ danh sư mà thôi, đã giết thì đã giết, chẳng lẽ Lạc đường chủ sẽ còn bởi vì như vậy một kiện việc nhỏ, cùng bọn hắn vương thất trở mặt?"),t("br"),t("br"),n._v("“Vâng!” Thấy đối phương khăng khăng như thế, Lương Thanh Mệnh thân là cấp dưới, không cách nào phản bác, đành phải nhẹ gật đầu, đang muốn rời đi, liền nghe một thanh âm vang lên."),t("br"),t("br"),n._v("“Không vội, ta và ngươi cùng đi, ta muốn tận mắt nhìn xem gia hỏa này bị phanh thây xé xác...”"),t("br"),t("br"),n._v("Lâm Nhược Thiên khuôn mặt dữ tợn, hận ý cuồn cuộn."),t("br"),t("br"),n._v("“Tốt, chúng ta cùng một chỗ!”"),t("br"),t("br"),n._v("Thấy bộ dáng của cha, Lâm Lung gật đầu đồng ý."),t("br"),t("br"),n._v("Mấy người ngồi lên Thiểm Điện Kim Lân Thú, lăng không mà lên."),t("br"),t("br"),n._v("“Hắn bây giờ không có ở đây Danh Sư đường, tại cái kia không xa trong phủ đệ, ta đều hỏi thăm rõ ràng, hôm qua trở ra, liền lại không có đi ra!”"),t("br"),t("br"),n._v("Lâm Nhược Thiên nói."),t("br"),t("br"),n._v("Biết nữ nhi khẳng định sẽ báo thù, hai ngày này một mực phái người nghe ngóng tin tức... Vị này Trương Huyền, cũng không chạy trốn, mà là tại phủ đệ của mình bên trong."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Thiểm Điện Kim Lân Thú nhanh như điện chớp, thời gian qua một lát, liền đến đến trên tòa phủ đệ không."),t("br"),t("br"),n._v("“Lương thống lĩnh, hi vọng ngươi có thể đem hắn bắt tới, ta muốn một chút đem hắn tra tấn mà chết...”"),t("br"),t("br"),n._v("Khuôn mặt dữ tợn, vặn vẹo, Lâm Nhược Thiên trên mặt lộ ra điên cuồng."),t("br"),t("br"),n._v("Theo Lâm gia đi ra công phu, hắn đã biết trước mắt vị này trung niên nhân thực lực."),t("br"),t("br"),n._v("Chí Tôn hậu kỳ!"),t("br"),t("br"),n._v("Loại người này, dậm chân một cái, toàn bộ Thiên Vũ vương quốc đều sẽ sụp đổ, ai cũng chống lại không được!"),t("br"),t("br"),n._v("Thiên Vũ vương thất đầu kia trấn quốc man thú coi như đột phá, cũng chỉ là Chí Tôn sơ kỳ mà thôi, tại loại này cường giả trước mặt, tiện tay liền có thể bóp chết!"),t("br"),t("br"),n._v("“Yên tâm!”"),t("br"),t("br"),n._v("Gật gật đầu, Lương Thanh Mệnh thân thể nhoáng một cái, rơi vào Thiểm Điện Kim Lân Thú to lớn đầu lâu bên trên, lạnh lùng nhìn phía dưới viện lạc, Chí Tôn hậu kỳ khí tức trong nháy mắt bao phủ khắp nơi, băng hàn, lạnh lùng thanh âm vang lên."),t("br"),t("br"),n._v("“Trương Huyền, cút ra đây nhận lấy cái chết!”"),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Thanh âm vang tận mây xanh, mang theo Chí Tôn cường giả chân khí lực lượng, chấn toàn bộ vương thành đều một hồi lắc lư."),t("br"),n._v("“Cút ra đây nhận lấy cái chết?”"),t("br"),t("br"),n._v("“Nguy rồi, là Lâm gia vị kia thái tử phi đến rồi!”"),t("br"),t("br"),n._v("“Không sai, cái này hết rồi, nghe thanh âm uy thế, ít nhất là cái Chí Tôn cảnh cường giả, Trương sư... Có thể chống chọi ư?”"),t("br"),t("br"),n._v("“Hắn mặc dù không yếu, nhưng cùng Chí Tôn còn kém rất nhiều, phiền phức lớn rồi...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thanh âm vang lên, vô số người bị quấy nhiễu, tất cả đều sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Lâm gia thế lực sau lưng, đã sớm không phải bí mật gì, mấy ngày nay mặc dù không có động tĩnh, nhưng mọi người biết, chỉ là trước bão táp yên tĩnh mà thôi."),t("br"),t("br"),n._v("Lúc này, không chút kiêng kỵ thanh âm, không trung bay múa to lớn man thú, đều nói rõ, đối phương..."),t("br"),t("br"),n._v("Đến rồi!"),t("br"),t("br"),n._v("Đến báo thù!"),t("br"),t("br"),n._v("“Chí Tôn... Hậu kỳ cường giả? Nếu như không có đoán sai, hẳn là Hiên Viên vương quốc phủ thái tử hộ vệ thống lĩnh, Lương Thanh Mệnh!”"),t("br"),t("br"),n._v("Trong vương cung, Mạc Thiên Tuyết cũng nghe đến thanh âm, vội vàng đi tới tháp cao, nhìn thấy đứng tại man thú trên đầu trung niên nhân, bờ môi run lên."),t("br"),t("br"),n._v("Thân là cấp dưới vương quốc, Hiên Viên vương quốc một chút thế lực phân bố, vẫn là biết rất nhiều, có thể tản mát ra loại uy thế này, triển lộ ra loại lực lượng này, không cần nghĩ, khẳng định là vị chí tôn kia hậu kỳ thống lĩnh!"),t("br"),t("br"),n._v("“Chí Tôn hậu kỳ? Cái kia... Trương sư chẳng phải nguy hiểm?”"),t("br"),t("br"),n._v("Mạc Vũ thân thể mềm mại nhoáng một cái, một mặt lo lắng."),t("br"),t("br"),n._v("Trương Huyền mặc dù có thể nhiều lần sáng tạo kỳ tích, nhưng Chí Tôn hậu kỳ, cùng nửa bước Chí Tôn, chênh lệch không dưới vạn bên trong, cả hai không thể so sánh nổi."),t("br"),t("br"),n._v("Lâm Lung thế mà trực tiếp đem loại người này điều tới, Trương sư như thế nào ngăn cản?"),t("br"),t("br"),n._v("“Làm sao bây giờ? Ta... Cũng không có cách nào! Hơn nữa, sợ là chúng ta vương quốc cũng xong rồi...” Mạc Thiên Tuyết thân thể run rẩy."),t("br"),t("br"),n._v("Đối phương thế nhưng là Chí Tôn hậu kỳ, hắn có thể có biện pháp nào?"),t("br"),t("br"),n._v("Coi như tại trấn quốc man thú đi qua, cũng không đủ giết."),t("br"),t("br"),n._v("Căn bản không phải đối thủ."),t("br"),t("br"),n._v("“Tiểu Vũ, đem chiếc nhẫn này cầm, nắm chặt thời gian chạy trốn, có ngươi tại, coi như vương quốc tan vỡ, còn có hi vọng...” Cắn răng một cái, Mạc Thiên Tuyết theo trên tay lấy ra trữ vật giới chỉ đưa tới."),t("br"),t("br"),n._v("Mạc Vũ thiên phú cao, người cũng thông minh, là Thiên Vũ vương quốc tương lai hi vọng, không thể vẫn lạc tại nơi này."),t("br"),t("br"),n._v("Nếu không, cho dù chết, cũng thẹn với liệt tổ liệt tông."),t("br"),t("br"),n._v("“Phụ vương...”"),t("br"),t("br"),n._v("Mạc Vũ thân thể mềm mại run rẩy."),t("br"),t("br"),n._v("Mặc dù không cam lòng, nhưng cũng biết, cha quyết định là chính xác."),t("br"),t("br"),n._v("Lâm Lung lúc trước cũng bởi vì một chút chuyện nhỏ, diệt đi Cù gia cả nhà, hiện tại Lâm gia cơ hồ hủy diệt, dựa theo nàng tác phong trước sau như một, một khi giết chết Trương sư, khẳng định sẽ đối với bọn hắn Thiên Vũ vương thất bất lợi!"),t("br"),t("br"),n._v("Đem trọn cái vương thất bởi vậy hủy diệt, đều vô cùng có khả năng."),t("br"),t("br"),n._v("“Tốt, đi thôi, phụ vương đi không được... Cũng không thể đi, nếu như bọn hắn thật muốn động thủ, ta sẽ cùng vương quốc cùng tồn vong!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết ánh mắt kiên định."),t("br"),t("br"),n._v("Nữ nhi có thể rời đi, hắn không được."),t("br"),t("br"),n._v("Làm quốc vương nhiều năm như vậy, trên người đã in dấu thật sâu ấn Thiên Vũ vương quốc dấu vết, lại không cách nào rời đi."),t("br"),t("br"),n._v("“Cha...”"),t("br"),t("br"),n._v("Biết trốn không thoát kết quả, Mạc Vũ thân thể mềm mại run rẩy, đang muốn đang nói cái gì, ngay sau đó liền nghe đến một cái không nhịn được thanh âm theo phủ đệ vang lên."),t("br"),t("br"),n._v("“Làm càn! Ở đâu ra hai hàng, trong này cãi lộn? Không muốn sống, muốn gặp thiếu gia, lăn ra ngoài quỳ!”"),t("br"),t("br"),n._v("“A?”"),t("br"),t("br"),n._v("Mạc Vũ, Mạc Thiên Tuyết ngây người."),t("br"),t("br"),n._v("Lương Thanh Mệnh huyên náo như thế lớn, cơ hồ toàn bộ Thiên Vũ vương thành đều biết, tất cả quan sát tình huống, cũng tất cả đều sững sờ tại nguyên chỗ, kém chút không có thổ huyết."),t("br"),t("br"),n._v("Đây chính là Hiên Viên vương quốc tới Chí Tôn hậu kỳ cường giả, nói như vậy..."),t("br"),t("br"),n._v("Leo đến chỗ cao, đồng loạt nhìn về phía người nói chuyện, cho rằng lại là cái nào nhân vật lợi hại, xem xét phía dưới, càng là mắt tối sầm lại."),t("br"),t("br"),n._v("Chính là phủ đệ cái kia bụng tròn vo mập mạp... Tôn quản gia."),t("br"),t("br"),n._v("Mấy ngày nay, không ít người cũng cùng vị này tiếp xúc qua, cũng biết, thực lực của hắn mới Bì Cốt cảnh đỉnh phong, liền Đỉnh Lực cảnh đều không có đạt tới, yếu không thể yếu hơn nữa..."),t("br"),t("br"),n._v("Yếu như vậy... Ở đâu ra lá gan cùng khí phách?"),t("br"),t("br"),n._v("Ngươi biết trên bầu trời bay cái này rốt cuộc mạnh cỡ nào ư? Liền dám nói như vậy?"),t("br"),t("br"),n._v("Nhìn nhau, Mạc Vũ, Mạc Thiên Tuyết mỗi người run rẩy."),t("br"),t("br"),n._v("Chí Tôn hậu kỳ cường giả, đừng nói Thiên Vũ vương quốc, coi như Hiên Viên vương quốc cũng tuyệt đối được cho cao thủ, một cái nho nhỏ quản gia, Đỉnh Lực cảnh đều không có đạt tới gia hỏa, liền dám lớn lối như vậy..."),t("br"),t("br"),n._v("Móa!"),t("br"),t("br"),n._v("Vốn là khó mà điều tiết, cái này có lẽ càng xong đời."),t("br"),t("br"),n._v("Không cần nghĩ, một hồi đại đồ sát sắp bắt đầu."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Mọi người nổi điên, Lương Thanh Mệnh cũng thiếu chút theo Thiểm Điện Kim Lân Thú bên trên một đầu cắm xuống tới."),t("br"),t("br"),n._v("Thực lực của hắn, tại Hiên Viên vương quốc cũng tiếng tăm lừng lẫy, được cho siêu cấp đại nhân vật, vốn cho rằng hét lớn một tiếng, cái này tiểu vương quốc tiểu nhân vật bọn họ, sẽ toàn bộ dọa đến run lẩy bẩy, không dám nói nhảm, nằm mơ đều không có nghĩ đến..."),t("br"),t("br"),n._v("Người ta căn bản không quan tâm, còn để hắn ra ngoài quỳ xuống chờ lấy..."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Ngươi đây là tâm lớn, vẫn là đầu óc có vấn đề?"),t("br"),t("br"),n._v("“Ta đếm ba tiếng, để Trương Huyền đi ra nhận lấy cái chết, nếu không, ta liền diệt đi tòa phủ đệ này, tất cả mọi người vì hắn chôn cùng!”"),t("br"),t("br"),n._v("Da mặt lay động, cố nén tức giận, Lương Thanh Mệnh sắc mặt tái xanh."),t("br"),t("br"),n._v("(Cao trào tiến đến, cầu phiếu đề cử!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-413"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-415"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);