(window.webpackJsonp=window.webpackJsonp||[]).push([[1649],{2003:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-406-đanh-thai-tu-phi-bat-tai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-406-đanh-thai-tu-phi-bat-tai"}},[n._v("#")]),n._v(" Chương 406: Đánh thái tử phi bạt tai")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Vốn cho rằng như uy hiếp như vậy, sẽ để cho cái tên mập mạp này cùng phủ đệ người loạn cả một đoàn, dọa đến chạy trốn tứ phía, tè ra quần."),t("br"),t("br"),n._v("Lương Thanh Mệnh xem xét phía dưới, thân thể lần nữa nhoáng một cái."),t("br"),t("br"),n._v("Chỉ thấy trong phủ đệ người, tu luyện tu luyện, nấu cơm nấu cơm, giặt quần áo giặt quần áo, lắc đầu lắc đầu, đối với hắn... Không có chút nào quan tâm!"),t("br"),t("br"),n._v("Nhất là đang phía dưới cái tên mập mạp kia, đầy vẻ khinh bỉ nhìn qua, giống như là nhìn xem một kẻ ngu ngốc."),t("br"),t("br"),n._v("Mẹ nó!"),t("br"),t("br"),n._v("Đó là cái tình huống gì?"),t("br"),t("br"),n._v("Nho nhỏ nhất đẳng vương quốc nhị tinh sơ kỳ danh sư, không phải nhìn thấy bản thân dạng này Chí Tôn cường giả, dọa đến run lẩy bẩy, không dám nói nhảm ư?"),t("br"),t("br"),n._v("Danh sư là cùng cấp bậc trong mạnh nhất, có thể cấp bậc chênh lệch lớn, lợi hại hơn nữa cũng không dùng!"),t("br"),t("br"),n._v("Người trong cuộc không ra ngược lại cũng thôi, một cái liền Đỉnh Lực cảnh đều không có mập mạp, thì phách lối như vậy?"),t("br"),t("br"),n._v("Chẳng lẽ không biết ta sẽ giết người ư?"),t("br"),t("br"),n._v("Đây cũng quá không đem hắn để ở trong mắt đi!"),t("br"),t("br"),n._v("Đường đường thái tử hộ vệ thống lĩnh, Chí Tôn hậu kỳ cường giả, đi đến bất kỳ địa phương nào đều uy chấn bát phương, bốn phía bái phục, hét lớn một tiếng, có lẽ Thiên Vũ vương quốc quốc vương Mạc Thiên Tuyết đều muốn trực tiếp quỳ xuống..."),t("br"),t("br"),n._v("Nho nhỏ phủ đệ tu vi thấp mập mạp, thế mà khinh bỉ!"),t("br"),t("br"),n._v("Khinh bỉ em gái ngươi!"),t("br"),t("br"),n._v("Trong lòng càng nghĩ càng giận, lập tức nghe được thanh âm của mập mạp tiếp tục vang lên."),t("br"),t("br"),n._v("“Đếm ba tiếng? Vậy ngươi dám nhanh đếm, đừng quản ta không có khuyên ngươi, làm không cẩn thận đây là trong đời ngươi, có thể đếm được cuối cùng ba cái cân nhắc...”"),t("br"),t("br"),n._v("“Ta thao!”"),t("br"),t("br"),n._v("Lương Thanh Mệnh chỉ cảm thấy vẻ mặt biến thành màu đen, trên đầu đều muốn bốc khói."),t("br"),t("br"),n._v("Là ta muốn giết các ngươi, lại còn nói đây là chúng ta sinh cuối cùng là ba cái đếm..."),t("br"),t("br"),n._v("Ta Chí Tôn uy nghiêm đâu?"),t("br"),t("br"),n._v("Ngươi cái mập mạp chết bầm, lão tử muốn chặt ngươi..."),t("br"),t("br"),n._v("Vẻ mặt khó coi, cả người tức giận gần bạo tạc, đang muốn nổi giận, giết mấy người cho đối phương nhìn xem, để bọn hắn biết, Mã vương gia có ba con mắt, chỉ thấy đang phía dưới đại điện “Kẹt kẹt!” Một tiếng, ba bóng người đi ra."),t("br"),t("br"),n._v("Đi đầu là cái chừng ba mươi tuổi trung niên nhân, đi theo phía sau hai cái lão giả, người mặc danh sư phục sức, hẳn là Danh Sư đường người."),t("br"),t("br"),n._v("Bất quá, Thiên Vũ vương quốc Danh Sư đường, thực lực lợi hại nhất đường chủ cũng là Tông sư đỉnh phong mà thôi, còn không cần để vào mắt."),t("br"),t("br"),n._v("“Chuyện gì xảy ra? Vừa giữa trưa cãi nhau?”"),t("br"),t("br"),n._v("Đi ra trung niên nhân, cũng không ngẩng đầu lên, mày nhăn lại nhìn về phía cách đó không xa mập mạp."),t("br"),t("br"),n._v("“Hồi bẩm lão gia, gia hỏa này ở phía trên mò mẫm so tài một chút, nói muốn gặp thiếu gia, nói đùa, thiếu gia thân phận gì? Há lại mặt hàng này, muốn gặp là gặp đến?”"),t("br"),t("br"),n._v("Mập mạp nghĩa chính ngôn từ: “Ta đang định để hắn xéo đi.”"),t("br"),t("br"),n._v("“Ừm, làm tốt lắm!” Trung niên nhân nhẹ gật đầu, đối mập mạp, rất là đồng ý."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Lương Thanh Mệnh lần nữa một cái lảo đảo."),t("br"),t("br"),n._v("Một tên mập ngốc, ngược lại cũng thôi, người trung niên này rõ ràng giống như là phủ đệ chủ nhân, thế mà thì bộ dáng này... Quả thực quá vô pháp vô thiên!"),t("br"),t("br"),n._v("Vốn còn nghĩ đối phương là Danh Sư đường người, một khi động thủ, không tốt kết thúc, lúc này tức giận gần bạo tạc, cũng không tiếp tục quản không để ý."),t("br"),t("br"),n._v("“Đã tự tìm cái chết, ta liền thành toàn các ngươi...”"),t("br"),t("br"),n._v("Một tiếng nổi giận, Lương Thanh Mệnh bàn tay lớn đè ép, lực lượng hùng hậu hạ xuống từ trên trời, to lớn thủ ấn, đối trong phủ đệ mọi người, chụp lại."),t("br"),t("br"),n._v("Quỷ cấp viên mãn võ kỹ, Kim Cương đại thủ ấn!"),t("br"),t("br"),n._v("Kim Cương không phá, thủ ấn kinh phong."),t("br"),t("br"),n._v("Lăng không mà đánh, lực lượng vượt qua ba vạn đỉnh, Chí Tôn trung kỳ cường giả đụng tới, đều sẽ trong nháy mắt nghiền thành bột mịn."),t("br"),t("br"),n._v("Một chiêu đánh ra, cho rằng đối phương nhìn thấy uy lực, hẳn là sẽ hoảng sợ, sợ hãi lại không vừa rồi bộ dáng, chỉ thấy trung niên nhân một mặt lạnh nhạt nhìn qua, vừa nghiêng đầu: “Tiểu Tô, gia hỏa này giao cho ngươi, tính cả không trung bay đại điểu, một khối lấy xuống!”"),t("br"),t("br"),n._v("“Được, Dương sư!”"),t("br"),t("br"),n._v("Được xưng hô “Tiểu Tô” lão giả, tiến về phía trước một bước."),t("br"),t("br"),n._v("Cái này “Tiểu Tô” thoạt nhìn cũng không cao lớn, thậm chí thì không có khí chất gì, trọng yếu nhất chính là, hai chân kẹp chặt, vẻ mặt trắng bệch, tựa hồ có cái gì khó nói chi ẩn."),t("br"),t("br"),n._v("Tóc vẫn là rối bời, giống mới vừa bị người đánh, không có nửa phần cao thủ bộ dáng."),t("br"),t("br"),n._v("Để loại người này, đối phó bản thân?"),t("br"),t("br"),n._v("Phía dưới một đám người có phải hay không đều là điên rồi?"),t("br"),t("br"),n._v("Không chỉ hắn như vậy, một bên lặng lẽ nhìn qua vương thành rất nhiều cao thủ, thế lực, thì tất cả đều ngốc tại chỗ, gần giật."),t("br"),t("br"),n._v("Đây chính là Lâm gia chỗ dựa, Hiên Viên vương quốc thái tử phi mang tới siêu cấp cường giả, người ta muốn giết ngươi, không tốt tốt thì coi trọng thoáng cái cho chút thể diện, đây là thế nào cái ý tứ?"),t("br"),t("br"),n._v("Phái ra một cái kẹp chặt hai chân, vẻ mặt trắng bệch lão giả?"),t("br"),t("br"),n._v("Quá không đem bánh ngô xem như lương khô đi!"),t("br"),t("br"),n._v("Tô sư, Lăng sư đi tới vương thành chuyện, là Danh Sư đường cơ mật, không có người tiết lộ, tất cả mọi người không biết cái này thoạt nhìn rất bình thường gia hỏa, nhưng thật ra là cái ngưu rối tinh rối mù siêu cường nhân vật."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Mọi người ở đây từng cái con mắt choáng váng, không biết chuyện gì xảy ra thời điểm, chỉ thấy vị này “Tiểu Tô”, ngẩng đầu lên, hừ lạnh một tiếng."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Thanh âm không lớn, nhưng giống như là kim thạch nổ tung, cùng không trung to lớn trong tay đụng một cái, người sau liền ngăn cản thời gian đều không có, lập tức nổ tung."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Ngay sau đó, Lương Thanh Mệnh liền cảm thấy ngực tê rần, giống như là bị lợi kiếm đâm trúng, một ngụm máu tươi phun ra, mắt tối sầm lại, run rẩy thoáng cái, kém chút theo man thú trên đầu ngã xuống."),t("br"),t("br"),n._v("“Là... Hóa Phàm cảnh cường giả?”"),t("br"),t("br"),n._v("Đối phương cả tay đều không ra, chỉ quát lớn một câu, liền đánh nát công kích của hắn, miệng phun máu tươi, có ngốc cũng biết, cái này “Tiểu Tô” thực lực mạnh, tuyệt không phải hắn có thể chống lại."),t("br"),t("br"),n._v("Con ngươi co rụt lại, Lương Thanh Mệnh thân thể run rẩy."),t("br"),n._v("Hóa Phàm cảnh cường giả, Hiên Viên vương quốc đều cơ hồ không có, cái này nhất đẳng vương quốc từ nơi nào xuất hiện?"),t("br"),t("br"),n._v("Hơn nữa, khí tức đối phương không tiêu tan, một câu cứ như vậy lớn lực công kích... Coi như tại Hóa Phàm bên trong, có lẽ đều đạt tới cảnh giới nhất định, không phải hời hợt hạng người!"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Đem ánh mắt tập trung tới mọi người, giờ phút này thì tất cả đều mở to hai mắt nhìn, không có tại chỗ hù chết."),t("br"),t("br"),n._v("Chí Tôn hậu kỳ cường giả toàn lực đánh ra công kích, bị một tiếng uống nát, bản nhân càng là kém chút theo man thú trên đầu rơi xuống..."),t("br"),t("br"),n._v("Đến cùng chuyện gì xảy ra?"),t("br"),t("br"),n._v("“Cái này... Người danh sư này, ngươi biết?” Mạc Thiên Tuyết nhịn không được nhìn về phía nữ nhi."),t("br"),t("br"),n._v("Không biết vị này “Tiểu Tô” là ai, nhưng hắn mặc danh sư đặc hữu phục sức, vẫn có thể một chút nhìn ra."),t("br"),t("br"),n._v("Nhất thanh thanh hát, liền đánh tan Chí Tôn hậu kỳ một kích toàn lực, đây là từ nơi nào xuất hiện nhân vật cường hãn?"),t("br"),t("br"),n._v("Muốn hay không mạnh như vậy?"),t("br"),t("br"),n._v("“Cái này...” Mạc Vũ thì sửng sốt."),t("br"),t("br"),n._v("Tô sư, Lăng sư tới thời điểm, nàng cũng không trở về, cũng chưa từng thấy qua, ngay sau đó Trương Huyền, Mạc Hoằng Nhất khảo hạch, thì không nhiều đợi."),t("br"),t("br"),n._v("Bởi vậy, danh sư công hội hai vị tứ tinh danh sư xuất hiện chuyện, cũng không phải là rất rõ ràng."),t("br"),t("br"),n._v("Hơn nữa, coi như rõ ràng, thì khẳng định không thể tin được, có người dám xưng hô tứ tinh danh sư là “Tiểu Tô”... Người trung niên kia lại là cái gì dạng nhân vật đáng sợ?"),t("br"),t("br"),n._v("“Xuống đây đi!”"),t("br"),t("br"),n._v("Chấn kinh còn không có kết thúc, phía dưới gập cong “Tiểu Tô” lần nữa hừ một câu."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Như là lợi kiếm vạch phá bầu trời, bị cỗ này kình khí chấn động, không trung Thiểm Điện Kim Lân Thú còn không có kịp phản ứng, liền thân thể nhoáng một cái, thẳng tắp rơi xuống dưới."),t("br"),t("br"),n._v("Oanh!"),t("br"),t("br"),n._v("Bụi đất tung bay, mới vừa rồi còn phách lối không dứt, dự định muốn đồ diệt Trương phủ, chém giết Trương Huyền mọi người, thực sự ném xuống đất."),t("br"),t("br"),n._v("Bị ngã đến thất điên bát đảo, thật vất vả tỉnh táo lại, Lâm Lung vội vàng nhìn về phía thú trên lưng cha cùng đệ đệ, xem xét bên dưới kém chút không có tại chỗ điên mất."),t("br"),t("br"),n._v("Chỉ thấy vốn là hôn mê bất tỉnh Lâm Lang, ngã phía dưới, xương sống toàn bộ đứt gãy, hô hấp bất cứ lúc nào cũng sẽ đoạn tuyệt, khẳng định là sống không được."),t("br"),t("br"),n._v("Mới vừa ăn vào đan dược, còn có chút tinh thần Lâm Nhược Thiên, giờ phút này thì từng ngụm từng ngụm thổ huyết, vết thương cũ mới bệnh phía dưới, có thể hay không cứu sống, cũng còn khó nói."),t("br"),t("br"),n._v("Lâm Lung phát điên."),t("br"),t("br"),n._v("Chúng ta là đến báo thù..."),t("br"),t("br"),n._v("Kết quả một người đều không có giết, thậm chí liền tội khôi họa thủ Trương Huyền đều không có nhìn thấy, cha, đệ đệ thiếu chút nữa chết rồi, nàng cũng bị rơi muốn thổ huyết, đến cùng xảy ra chuyện gì?"),t("br"),t("br"),n._v("“Lương thống lĩnh, ngươi làm ăn gì, mau giết hắn...”"),t("br"),t("br"),n._v("Ngồi tại Thiểm Điện Kim Lân Thú lưng bên trên trong phòng, tình huống bên ngoài biết cũng không nhiều, chỉ nghe được đối thoại, còn tưởng rằng là Lương Thanh Mệnh cố ý nhường, Lâm Lung vẻ mặt dữ tợn, rít lên một tiếng."),t("br"),t("br"),n._v("“Giết?”"),t("br"),t("br"),n._v("Thân thể run lên, Lương thống lĩnh không có ngất đi."),t("br"),t("br"),n._v("Giết thế nào?"),t("br"),t("br"),n._v("Đối phương thế nhưng là Hóa Phàm cảnh cường giả, ngươi để cho ta giết hắn? Vẫn là để ta đi chịu chết?"),t("br"),t("br"),n._v("Thái tử như thế anh minh người, thế nào cưới ngươi dạng này một cái ngốc thiếu ngoạn ý?"),t("br"),t("br"),n._v("“Giết ta?”"),t("br"),t("br"),n._v("Tô sư nhìn qua."),t("br"),t("br"),n._v("Hắn mặc dù đột phá tu vi, vừa vặn bên trên đau đớn còn không có biến mất, vốn là bực bội, thấy thú trên lưng thoát ra nữ nhân, mới mở miệng liền muốn giết bản thân, sầm mặt lại."),t("br"),t("br"),n._v("“Không phải, không phải... Tiền bối bớt giận!”"),t("br"),t("br"),n._v("Thân thể run lên, Lương thống lĩnh liên tục khoát tay."),t("br"),t("br"),n._v("“Ngươi sợ hắn làm cái gì? Chẳng qua nhất đẳng vương quốc danh sư mà thôi, còn có thể đem ngươi thế nào?”"),t("br"),t("br"),n._v("Thấy thuộc hạ như thế bất tranh khí, trực tiếp xưng hô đối phương tiền bối, Lâm Lung càng thêm tức giận."),t("br"),t("br"),n._v("Nàng vốn là cũng là người rất sáng suốt, chỉ bất quá cha, đệ đệ chạy tới tự tay mình giết cừu nhân không được, tại chỗ rơi gần bỏ xuống, sớm đã tức đến chập mạch rồi."),t("br"),t("br"),n._v("“Xem nhẹ danh sư!” Tô sư sầm mặt lại: “Vả miệng!”"),t("br"),t("br"),n._v("Có thể trở thành tứ tinh danh sư, đương nhiên không phải cái gì người hiền lành, đối phương lời này, rõ ràng không có đem danh sư để vào mắt, thâm tình âm trầm xuống."),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Chần chờ một chút, Lương Thanh Mệnh cuối cùng vẫn là xoay người sang chỗ khác."),t("br"),t("br"),n._v("Liền xem như thái tử phi, thì hết cách rồi, trước mắt vị này chính là Hóa Phàm cảnh cường giả, chỉ cần dám không tuân thủ, chỉ sợ bọn họ tất cả mọi người hôm nay đều sẽ chết ở chỗ này."),t("br"),t("br"),n._v("“Lương thống lĩnh, ngươi muốn làm gì?”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng, Lâm Lung sững sờ, lời còn chưa nói hết, chỉ thấy cái trước thân thể nhoáng một cái, đã đi tới trước mặt, một bàn tay quất tới."),t("br"),t("br"),n._v("Ba!"),t("br"),t("br"),n._v("Không có chút nào phản kháng, Lâm Lung bị đánh một cái bổ nhào bay ra ngoài, đầu tựa vào trên mặt đất, răng mất một chỗ."),t("br"),t("br"),n._v("Nàng mặc dù là thiên tài, tiến bộ thì rất nhanh, nhận hạn chế tuổi tác, cũng chỉ Tông sư hậu kỳ mà thôi, như thế nào chống đỡ được Chí Tôn hậu kỳ cường giả công kích."),t("br"),t("br"),n._v("Ba ba ba ba!"),t("br"),t("br"),n._v("Quất một cái tát, thấy trước mắt cao thủ, không nói đình chỉ, Lương thống lĩnh không dám chần chờ, tiếp tục rút đi."),t("br"),t("br"),n._v("“Ngươi... Ngươi...”"),t("br"),t("br"),n._v("Bị liên tục quất mấy cái tát, nguyên bản thanh tú mặt, sưng như là đầu heo, Lâm Lung có ngốc thì rõ ràng sao có thể chuyện, tức giận đến thân thể mềm mại loạn chiến, lần nữa nhìn về phía Tô sư, nhìn về phía người trung niên kia Dương sư, lộ ra nồng đậm hận ý."),t("br"),t("br"),n._v("Theo Lâm gia xuất sinh, đến trở thành thái tử phi, luôn luôn thuận dòng Thuận Phong, đâu chịu nổi như thế lớn vũ nhục."),t("br"),t("br"),n._v("Loại này cừu hận, coi như dốc hết tam giang chi thủy cũng khó có thể rửa sạch."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-414"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-416"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);