(window.webpackJsonp=window.webpackJsonp||[]).push([[1650],{2004:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-407-linh-thach-đang-cap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-407-linh-thach-đang-cap"}},[n._v("#")]),n._v(" Chương 407: Linh thạch đẳng cấp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Thấy lại đánh xuống, nữ nhân này không chết cũng không sai biệt lắm, Trương Huyền lúc này mới khoát tay áo."),t("br"),t("br"),n._v("Ngay tại Tô sư đám người cảm thấy, vẫn là Dương sư khoan hồng độ lượng, nhân nghĩa vô song thời điểm, liền nghe thanh âm của hắn tiếp tục vang lên: “Tôn Cường, đi đem Lộ Xung gọi qua, đánh nữ nhân này, hắn thích hợp nhất!”"),t("br"),t("br"),n._v("“...” Tô sư, Lăng sư."),t("br"),t("br"),n._v("“...” Mạc Thiên Tuyết, Mạc Vũ."),t("br"),t("br"),n._v("Đã năm đó, Lộ Xung cả nhà là nữ nhân này phái người giết, kẻ cầm đầu tới, thế nào cũng muốn để hắn báo thù."),t("br"),t("br"),n._v("Vị này thái tử phi, nhanh như vậy có thể đi tới, trên thực tế là vượt quá Trương Huyền dự kiến."),t("br"),t("br"),n._v("Bất quá, vận khí của bọn hắn thì không tốt lắm, Tô sư, Lăng sư tại cái này, liền đã định trước cái gọi là báo thù, liền là chuyện tiếu lâm."),t("br"),t("br"),n._v("“Vâng!” Tôn Cường quay người đi ra ngoài, thời gian không dài, Lộ Xung đi tới."),t("br"),t("br"),n._v("“Sư tổ!”"),t("br"),t("br"),n._v("Lúc này Lộ Xung cũng biết người trước mắt này thân phận, ôm quyền khom người."),t("br"),t("br"),n._v("“Sư tổ, lão gia? Ta đã biết, đây là Trương Huyền vị kia một mực chưa lộ diện lão sư, Dương Huyền!”"),t("br"),t("br"),n._v("Nhìn trộm tới người, rốt cuộc minh bạch tới."),t("br"),t("br"),n._v("Truyền thuyết Trương Huyền vị lão sư này, còn thu Khương đường chủ là học sinh, thực lực sâu không lường được."),t("br"),t("br"),n._v("Vốn cho rằng là lời đồn, mới biết được, lại là thật."),t("br"),t("br"),n._v("Khó trách lúc trước Trương sư, tiện tay diệt đi Lâm gia, không chút do dự, nguyên lai bởi vậy dựa dẫm!"),t("br"),t("br"),n._v("“Ừm, nữ nhân kia liền là Lâm Lang tỷ tỷ, Lâm gia vị kia thái tử phi, giao cho ngươi xử lý!”"),t("br"),t("br"),n._v("Thấy hắn đi tới trước mặt, Trương Huyền khoát tay."),t("br"),t("br"),n._v("“Đa tạ lão sư...”"),t("br"),t("br"),n._v("Thấy sát hại cả nhà cừu nhân tại cái này, hốc mắt đỏ lên, Lộ Xung đối với Lâm Lung liền vọt tới."),t("br"),t("br"),n._v("“Ngươi muốn làm gì? Ta là Hiên Viên vương quốc thái tử phi, dám đụng đến ta, tin hay không diệt ngươi cả nhà...”"),t("br"),t("br"),n._v("Lâm Lung sợ hãi, liên tiếp lui về phía sau, không nói chuyện chưa nói xong, trên mặt liền là tê rần, “Bành!” Bay ngược mà ra."),t("br"),t("br"),n._v("Cấp bậc của nàng, mặc dù so Lộ Xung cao hơn, nhưng đã bị đánh choáng đầu hoa mắt, thực lực đại tổn, như thế nào chống đỡ được! Thời gian qua một lát, liền bị đánh lời nói đều nói không ra, trên người lại không một chỗ hoàn hảo."),t("br"),t("br"),n._v("Lương Thanh Mệnh muốn hỗ trợ, nhìn thấy Tô sư ánh mắt thỉnh thoảng liếc tới, dọa đến một cử động nhỏ cũng không dám."),t("br"),t("br"),n._v("Sớm biết liền không bồi vị này nương nương tới báo thù, ở đâu là báo thù, quả thực liền là tìm tai vạ..."),t("br"),t("br"),n._v("“Đa tạ sư tổ thành toàn!”"),t("br"),t("br"),n._v("Đánh một hồi, Lộ Xung lui về tới."),t("br"),t("br"),n._v("Cùng trước đó Lâm Nhược Thiên đám người đồng dạng, mặc dù không giết chết đối phương, nhưng cũng phế trừ tu vi, đem hắn đánh hoàn toàn thay đổi."),t("br"),t("br"),n._v("Dạng này trừng phạt, tuyệt đối so với trực tiếp giết còn khó chịu hơn."),t("br"),t("br"),n._v("“Ừm!” Lên tiếng, Trương Huyền khoát tay áo: “Được rồi, đem bọn hắn toàn bộ ném đi đi!”"),t("br"),t("br"),n._v("Hắn hiện tại là cao thủ tuyệt thế Dương sư, Tô sư không có hạ tử thủ, liền không thật nhiều nói."),t("br"),t("br"),n._v("Tình huống trước mắt, Lâm Lung trọng thương, cái này hộ vệ dọa đến lá gan đều phá, chắc hẳn không dám làm ẩu, lúc này tiếp tục đuổi tận giết tuyệt lời nói, có lẽ Tô sư, Lăng sư muốn hoài nghi."),t("br"),t("br"),n._v("Vẫn là trước ném đi quên đi."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("đọc truyện ở http://ngantruyen.com/"),t("br"),n._v("Gật gật đầu, Tô sư lăng không một trảo, chân khí hình thành một cái to lớn chưởng ấn, đem Thiểm Điện Kim Lân Thú, Lương Thanh Mệnh đám người cầm bốc lên, ném ra ngoài."),t("br"),t("br"),n._v("“Cái này... Như vậy liền xong rồi?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết, Mạc Vũ nhìn nhau, từng cái đến bây giờ cũng không dám tin tưởng."),t("br"),t("br"),n._v("Vốn cho rằng Lâm Lung tới, Thiên Vũ vương quốc đều muốn bởi vậy hủy diệt, lại khó giữ lại, thế nào đều không có nghĩ đến, Trương Huyền phủ đệ toát ra hai người, đơn giản như vậy liền giải quyết chiến đấu."),t("br"),t("br"),n._v("“Tiểu Vũ, ngươi đi thăm dò thoáng cái, hai vị này danh sư là ai? Thế nào lợi hại như vậy... Trương sư lão sư, lại nên mạnh bao nhiêu mới có thể ra lệnh cho bọn họ!”"),t("br"),t("br"),n._v("Chần chờ một chút, Mạc Thiên Tuyết phân phó."),t("br"),t("br"),n._v("Nguy cơ giải trừ, đương nhiên cũng sẽ không cần rời đi, tâm tình thật tốt phía dưới, muốn nhiều cùng những người này tiếp xúc."),t("br"),t("br"),n._v("“Tốt!” Mạc Vũ gật đầu."),t("br"),t("br"),n._v("Coi như không cần hắn nói, thì dự định cẩn thận điều tra thêm."),t("br"),t("br"),n._v("Vừa quát phía dưới, để Chí Tôn hậu kỳ công kích hóa thành hư không, lần nữa hét lớn, Chí Tôn trung kỳ man thú từ không trung ngã xuống..."),t("br"),t("br"),n._v("Vị này “Tiểu Tô” thực lực, đã siêu việt tưởng tượng ra, có lẽ ít nhất là vị tam tinh danh sư!"),t("br"),t("br"),n._v("Danh Sư đường xuất hiện như vậy một cái nhân vật cường hãn, xem như nhất tinh danh sư, thế mà không chút nào biết, cũng coi như không làm tròn bổn phận."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Về đến phòng."),t("br"),t("br"),n._v("Vừa rồi Tô sư ra tay, Trương Huyền trong lòng vẫn là rất rung động."),t("br"),t("br"),n._v("Một tiếng để Chí Tôn cường giả công kích hóa thành hư không, quả thực nghe rợn cả người."),t("br"),t("br"),n._v("Mặc dù thông qua Thiên Đạo thư viện, biết trước mắt vị này thực lực, mạnh mẽ vô song, có thể thế nào đều không có nghĩ đến, lợi hại như vậy."),t("br"),t("br"),n._v("“Dương sư...”"),t("br"),t("br"),n._v("Ngay tại cảm khái, bản thân lúc nào mới nắm giữ loại thực lực này, chỉ thấy Tô sư nhìn lại."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("“Không biết... Trương sư ở đâu? Ta có thể cùng hắn thương nghị một chút, đi danh sư thi đấu chuyện?”"),t("br"),t("br"),n._v("Đi qua điều tức, chỗ bị thương cũng khá không ít, tỉnh táo lại, Tô sư mở lời."),t("br"),t("br"),n._v("Theo vạn quốc liên minh chạy tới, chính là vì chuyện này, thật vất vả tìm tới như vậy một vị siêu cấp thiên tài, làm sao có thể từ bỏ."),t("br"),t("br"),n._v("“Hắn đang lúc bế quan tu luyện!”"),t("br"),t("br"),n._v("Trương Huyền khoát tay."),t("br"),n._v("Tô sư âm thầm gật đầu, bừng tỉnh đại ngộ."),t("br"),t("br"),n._v("Khó trách huyên náo động tĩnh như thế lớn, đều không có đi ra, nguyên lai đang bế quan tu luyện."),t("br"),t("br"),n._v("Trẻ tuổi như vậy, liền có loại thực lực này cùng năng lực, có Dương sư như vậy một vị lợi hại lão sư chỉ là một bộ phận, chỉ sợ cũng cùng bản thân cố gắng có quan hệ."),t("br"),t("br"),n._v("“Cái kia... Danh sư thi đấu chuyện...”"),t("br"),t("br"),n._v("Đối phương bế quan, không tốt kêu đi ra, Tô sư mặt mũi tràn đầy xoắn xuýt."),t("br"),t("br"),n._v("“Cùng ta nói cũng giống vậy, thân là lão sư, có thể làm của hắn chủ!” Trương Huyền nói."),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Tô sư không thể làm gì khác hơn nói: “Danh sư thi đấu, mỗi lần đều có các thế lực lớn Danh Sư đường đường chủ thương nghị mà thành, chưa có xác định quy củ, có điều, nhưng có phần thưởng phong phú. Thu hoạch được gần phía trước thứ tự, không chỉ có linh thạch làm ban thưởng, càng có đi bí cảnh tu luyện cơ hội...”"),t("br"),t("br"),n._v("Nói còn chưa dứt lời, chỉ thấy cách đó không xa Dương sư nhìn lại, hai mắt tỏa ánh sáng: “Linh thạch?”"),t("br"),t("br"),n._v("“Ây... Đúng!” Tô sư gật đầu."),t("br"),t("br"),n._v("“Đại khái có thể ban thưởng bao nhiêu?” Dương sư mặt mũi tràn đầy chờ đợi."),t("br"),t("br"),n._v("Đối với cái gì danh sư thi đấu, thật sự là hắn không có cái gì hứng thú, nhưng đối với linh thạch, nhưng có rất lớn chờ mong."),t("br"),t("br"),n._v("Hiện tại tu luyện cần linh khí càng ngày càng nhiều, dù là có Thiên Đạo Thần Công, không có linh khí, cũng cần tốn hao công phu rất lớn, nếu có đầy đủ linh thạch liền đơn giản."),t("br"),t("br"),n._v("“Cụ thể ta cũng không biết, dựa theo năm trước lệ cũ, tên thứ nhất, có thể được đến trung phẩm cấp bậc linh thạch một viên!” Tô sư chần chờ một chút, nói."),t("br"),t("br"),n._v("Năm nay tỷ thí quy củ cùng ban thưởng, còn không có xác định được, nhưng dĩ vãng đều có tham khảo, cũng có thể suy tính cái không sai biệt lắm."),t("br"),t("br"),n._v("“Linh thạch trung phẩm?” Trương Huyền nháy con mắt."),t("br"),t("br"),n._v("Thiên Vũ vương quốc cấp bậc quá thấp, trong đó thư tịch, đối linh thạch ghi chép không nhiều, cho dù có, cũng chỉ là một loại, trung phẩm... Có ý tứ gì?"),t("br"),t("br"),n._v("“Chúng ta bình thường dùng linh thạch, đều là hạ phẩm, linh khí số lượng dự trữ mặc dù rất nhiều, nhưng độ tinh thuần liền không đủ, làm tu vi đạt tới cảnh giới nhất định, còn muốn hấp thu, liền rất khó khăn.”"),t("br"),t("br"),n._v("Nghi ngờ nhìn thoáng qua, Tô sư giải thích: “Trung phẩm, so hạ phẩm cao hơn một cái cấp bậc, loại cấp bậc này linh thạch, ẩn chứa trong đó linh khí, không chỉ số lượng nhiều, cũng càng thêm tinh khiết. Thích hợp cao hơn tu vi người tu luyện. Bình thường một viên linh thạch trung phẩm, có thể hối đoái một vạn viên hạ phẩm linh thạch, hơn nữa còn là có tiền mà không mua được!”"),t("br"),t("br"),n._v("“Một vạn viên?” Trương Huyền kém chút không có đem trong tay chén trà ném đi."),t("br"),t("br"),n._v("Vì một viên hạ phẩm linh thạch, hãm hại lừa gạt, không sai biệt lắm cái gì kỹ xảo đều đã vận dụng, tham gia cái cái gọi là danh sư thi đấu, một khi thu hoạch được quán quân, có thể được đến một viên trung phẩm, cũng chính là một vạn viên hạ phẩm linh thạch?"),t("br"),t("br"),n._v("Thật hay giả?"),t("br"),t("br"),n._v("“Dương sư, cái này... Ngươi không biết?” Thấy sâu không lường được Dương sư, bộ dáng này, Tô sư cùng Lăng sư nhìn nhau, tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Đột phá Hóa Phàm, theo trong không khí hấp thu linh khí tu luyện, đã rất khó, linh thạch là thiết yếu đồ vật, cơ hồ tất cả tam tinh cực kỳ trở lên danh sư đều biết, thế nào... Lợi hại như thế Dương sư, cùng hoàn toàn không rõ đồng dạng?"),t("br"),t("br"),n._v("“Ta đương nhiên biết! Chỉ là nhớ lại một chút chuyện cũ... Cảm khái mà thôi!”"),t("br"),t("br"),n._v("Thấy kém chút lộ khuyết điểm, Trương Huyền nhịn xuống kích động, ngồi nghiêm chỉnh, một mặt tang thương."),t("br"),t("br"),n._v("Loại sự tình này giải thích càng nhiều càng giải thích không rõ, còn không bằng cao thâm mạt trắc, để bọn hắn đi phỏng đoán."),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Quả nhiên, nghe được Dương sư nhớ lại một ít chuyện cũ, Tô sư hai người thức thời không hỏi thêm nữa."),t("br"),t("br"),n._v("“Cái kia... Trương sư tham gia danh sư thi đấu chuyện...”"),t("br"),t("br"),n._v("Tô sư tiếp tục hỏi."),t("br"),t("br"),n._v("“Loại này thi đấu, là đối vãn bối lịch luyện, ta sao có thể cự tuyệt, yên tâm đi, ta sẽ để cho hắn tham gia!” Bàn tay lớn vẫy một cái, Trương Huyền nói."),t("br"),t("br"),n._v("Nói đùa, một vạn viên linh thạch ah..."),t("br"),t("br"),n._v("Loại này cơ hội tốt, thật muốn bỏ lỡ, thật sự choáng váng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Vốn nghĩ sẽ còn lọt vào cự tuyệt, không nghĩ tới nói sảng khoái như vậy, Tô sư, Lăng sư nhìn nhau, sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("Dương sư, trước đó ngươi không phải thái độ này ah, rất rõ ràng có chút phản cảm... Ta muốn hay không trở nên nhanh như vậy?"),t("br"),t("br"),n._v("Cao nhân như thế tùy tính... Cũng là say!"),t("br"),t("br"),n._v("“Vậy thì tốt, Dương sư nói như vậy, chúng ta an tâm...”"),t("br"),t("br"),n._v("Bất quá, mặc kệ nguyên nhân gì, chỉ cần đáp ứng, chẳng khác nào có chiến thắng hi vọng, Tô sư hai người đồng thời hưng phấn xiết chặt nắm đấm: “Vậy kính xin Trương sư trong vòng ba tháng, đến vạn quốc liên minh tham gia trận đấu...”"),t("br"),t("br"),n._v("“Tốt!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tô sư cổ tay khẽ đảo, lấy ra một cái lệnh bài: “Đây là tham gia thi đấu tín vật, có thể muốn phiền phức Dương sư chuyển giao cho Trương sư, đến lúc đó chỉ cần cầm trong tay cái này, liền có thể đến vạn quốc liên minh Danh Sư đường tìm ta!”"),t("br"),t("br"),n._v("Tiện tay tiếp nhận, Trương Huyền bỏ vào trữ vật giới chỉ."),t("br"),t("br"),n._v("“Đã Dương sư đáp ứng, chúng ta trước cáo từ, trở về sớm chuẩn bị, đến lúc đó sẽ ở vạn quốc liên minh cung kính chờ đợi tiền bối cùng Trương sư đến!”"),t("br"),t("br"),n._v("Tô sư ôm quyền."),t("br"),t("br"),n._v("Ngày nữa Võ Vương nước mục đích, liền là tìm vị này gọi Trương Huyền thiên tài, nhìn xem có thể hay không tham gia danh sư thi đấu, hiện tại người thấy được, so tưởng tượng còn muốn lợi hại hơn không nói, càng là đạt được nhận lời, xem như hoàn mỹ hoàn thành nhiệm vụ, có thể đi về."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ngoài phủ đệ."),t("br"),t("br"),n._v("Mạc Hoằng Nhất một mặt khẩn trương."),t("br"),t("br"),n._v("Lương thống lĩnh vừa rồi kêu thanh âm tất cả đều nghe được, hiện tại không có động tĩnh, có thể hay không đã đánh nhau?"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, muốn xông tới hỗ trợ, nhưng tưởng tượng như vậy khả năng quá mức lỗ mãng, rất dễ dàng dẫn tới người khác phản cảm, còn muốn bái sư liền không quá dễ dàng."),t("br"),t("br"),n._v("“Chỉ mong đừng làm lớn chuyện...”"),t("br"),t("br"),n._v("Nói thầm một tiếng, đang phỏng đoán bên trong đến cùng xảy ra chuyện gì, liền cảm thấy phía trên tối đen, một cái bóng đen to lớn theo sân nhỏ thẳng tắp bay ra."),t("br"),t("br"),n._v("Phù phù! Phù phù! Phù phù!"),t("br"),t("br"),n._v("Dọa đến vội vàng hướng nơi xa nhảy xuống, vừa mới đứng vững, liền thấy một cái to lớn man thú, còn có ba bóng người xuống như sủi cảo ngã tại cách đó không xa trên đường phố, từng cái đầu cắm trên mặt đất, đập ra từng cái to lớn lỗ thủng."),t("br"),t("br"),n._v("“Là Lâm gia vị kia thái tử phi?”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất ngốc tại chỗ."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-415"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-417"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);